l10n.questLookup["koKR"] = {
[1] = {"칸레타드의 임무", {"죽음의 세계로부터 다시 돌아온 걸 환영하오.","당신은 먼 옛날 한 시대에 당신 종족 사이에서 영웅으로 자랑스러운 인생을 살았소. 이젠 타락하고 쇠약해져 내 앞에 왔구려, 과거의 유물이지. 한때 전설이었던 당신을 깨우려 하니 내 앞에 서시오.","다시 한 번 당신이 잃어버린 힘과 지식을 다시 되돌려 당신을 완벽한 죽음의 기사로 탄생하게 해 주겠소."}, {"칸레타드와 다시 대화하여 특성, 무기와 탈것을 받아야 합니다."}},
[2] = {"뾰족발톱의 발톱", {"강력한 히포그리프인 뾰족발톱을 처치했습니다. 이 야수의 발톱은 당신의 승리를 나타내는 명백한 증거가 되어 줄 것입니다.","실바람 산장에 있는 세나니 썬더하트에게 이 자랑스러운 전리품을 보여주면 틀림없이 관심을 보일 것입니다."}, {"뾰족발톱의 발톱을 실바람 산장에 있는 세나니 썬더하트에게 가져가야 합니다."}},
[5] = {"굶주린 지터스", {"이 유령 마을에 몇 주 동안이나 숨어 지내다 보니 땅벌레와 잡초 외에는 아무것도 먹지 못했어요! 이젠 진짜 음식이 먹고 싶어요. 음식만 제대로라면 충분히 거래할 용의도 있다고요.","맛있는 음식을 가져다주면 후하게 사례하죠.","듣기로는 어둠골의 진홍 까마귀 선술집에 있는 주방장 그루얼이 아주 맛있는 갈색 게살 케이크를 만든다고 하던데..."}, {"주방장 그루얼과 대화해야 합니다."}},
[6] = {"개릭 패드풋 현상금", {"몇 주 동안 우리 농부들과 상인들을 괴롭혀 온 개릭 패드풋이라는 살인마가 수도원 동쪽 다리 건너편 포도밭 근처에 있는 통나무집에서 목격되었습니다. 그 악당을 처치하고 증거로 그의 가면을 가져오시면 보상해 드리겠습니다!","개릭은 주위에 악당 패거리를 거느리고 있어 접근하기 쉽지 않을 것이니 조심하시기 바랍니다."}, {"개릭 패드풋을 처치한 후 그 증거로 개릭의 가면을 북녘골 수도원 근처에 있는 부관 윌렘에게로 가져가야 합니다."}},
[7] = {"코볼트 소탕", {"당신에게 맡길 첫 임무는 쓰레기 같은 코볼트 녀석들을 처치하는 일입니다. 북쪽의 숲에 가면 코볼트들이 무리를 이루고 있는 걸 볼 수 있을 겁니다. 가서 벌레 같은 코볼트 졸개들을 보이는 대로 처치해 주십시오. 놈들의 수를 점차 줄여나가다 보면 언젠가 북녘골에서 완전히 몰아낼 날이 오겠지요."}, {"코볼트 졸개 6마리를 처치한 다음 치안대장 맥브라이드에게 가야 합니다."}},
[8] = {"도적의 거래", {"이보시오! 감당하기 힘들만큼 많은 실성한 좀비와 거미들을 상대로 싸워온 젊은이의 청 하나 들어주지 않으시겠소?","여기 이 편지를 브릴에 있는 여관주인에게 가져가야 하는데... 이름이 리니 뭐라고 하더군. 성이야 몰라도 상관없소.","어쨌든 그 여관은 세상을 헤쳐나가려는 역병의 희생자들이 모여있는 아담하고 아늑한 곳이지. 필요하면 휴식을 취하기에도 좋은 장소라오. 한번 다녀와 보시오. 돌아오면 수고비는 넉넉히 주겠소."}, {"티리스팔 숲에 있는 여관주인 리니에게 평범한 편지를 가져가야 합니다."}},
[9] = {"죽음의 땅", {"여기 이 꼴을 한번 보세요! 이 땅은 선량한 농사꾼들이 살던 곳이었는데 그 빌어먹을 도적 놈들이 농사꾼들을 모두 쫓아 버렸어요. 그러나 저까지 내쫓진 못했죠! 그런데 저기 허수아비골렘 보초들이 내 밭을 차지해 버린 것 같아요.","괜찮으시면 당신이 가서 놈들을 20마리만 처치해 주지 않으시겠어요? 놈들을 처치하고 돌아오시면 대가를 지급해 드리겠습니다. 우선 제 밭에 있는 놈들부터 처치한 후에 다른 밭에 있는 놈들을 처치하시면 될 겁니다."}, {"농부 살딘이 허수아비골렘 보초 20마리를 처치해 달라고 했습니다."}},
[10] = {"슈섕크 탈출", {"이런 끔찍한 일이! 내 수석 조수이자 전 후임측량사였던 슈섕크를 여기서 남동쪽에 있는 사막으로 보냈었소. 우리가 모래지옥 협곡이라고 부르는 곳인데... 그러니까, 그 친구가 실종되어 버리고 말았소. 마지막으로 받은 보고에 의하면 그곳에 이상한 곤충들이 수도 없이 나타났다고 하오. 그렇다면, 벌써 사막의 주요 구역이 두 군데나 점령당한 걸까?","모래지옥 협곡으로 가서 슈섕크를 찾아 주시오, 제발! 아니면 그의 측량 장비만이라도... 남은 거라고는 그것뿐일지도 모르겠소이다."}, {"후임측량사 슈섕크가 어떻게 되었는지 알아보고 가젯잔에 있는 선임측량사 피즐도저에게 데려오거나 그의 측량 장비라도 찾아서 가져가야 합니다."}},
[11] = {"갈퀴발 놀 현상금", {"이 땅에 어울리지 않는 흉포한 놀들이 엘윈 숲 경계에서 목격되었습니다. 대규모 놀 무리가 저 경비탑 남쪽의 숲을 차지했는데, 우리가 단독으로 상대하기에는 너무 버거운 숫자입니다. 또 다른 무리는 동쪽에 돌무덤 호수 근처 지역에 출몰하고 있습니다.","누구든지 놀을 처치하는 데 도움을 주는 자에게는 스톰윈드 군대에서 크게 사례할 것입니다. 단, 증거물로 놀의 화려한 완장을 제게 가져와야 합니다."}, {"병영에 있는 부관 레이너에게 놀의 화려한 완장 8개를 가져가야 합니다."}},
[12] = {"백성의 민병대", {"백성의 민병대는 오직 서부 몰락지대의 영토를 지키고 이곳의 평화를 되찾는 것을 목표로 하고 있네. 불행히도 평화는 때로 피를 담보로 하지.","우리 정찰병의 보고에 의하면 데피아즈단 덫사냥꾼들이 가까운 곳에서 파괴를 일삼고 있다고 하네. 펄브라우의 호박밭과 몰센 농장뿐만 아니라 북서쪽에 있는 장고로드 광산 근처에서도 데피아즈단 덫사냥꾼의 출몰 보고가 있었네. 우리 민병대에 지원할 생각이라면 데피아즈단 덫사냥꾼 15명과 데피아즈단 밀수업자 15명을 처치하고 돌아오게."}, {"그라이언 스타우트맨틀이 데피아즈단 강탈자 15명과 데피아즈단 약탈자 15명을 처치하고 감시의 언덕으로 돌아오라고 했습니다."}},
[13] = {"백성의 민병대", {"잔인한 데피아즈단 강탈자 무리가 황금해안 채석장, 달빛시내 마을과 알렉스턴 농장을 약탈하는 것이 목격되었네. 백성의 민병대는 절대 이런 행위를 용납할 수 없네. 즉시 그곳으로 가서 서부 몰락지대에 정의가 살아 있다는 사실을 확실히 보여 주고 돌아오게.","황금해안 채석장은 탑 서쪽의 해변 근처에 있다네. 귀관의 다음 훈련 단계는 사악한 데피아즈단 강탈자 15명과 데피아즈단 약탈자 15명을 처치하고 돌아오는 것일세."}, {"그라이언 스타우트맨틀이 데피아즈단 강탈자 15명과 데피아즈단 약탈자 15명을 처치하고 감시의 언덕으로 돌아오라고 했습니다."}},
[14] = {"백성의 민병대", {"데피아즈단 몇 명이 우리의 감시망을 교묘히 피해 다니고 있네. 내가 신뢰하는 정찰병의 보고에 따르면 데피아즈단들이 서부 몰락지대 최남쪽에 있는 농가를 약탈하고 다녔다고 하더군. 놈들은 비수 언덕에 숨어서 다음 계획을 도모하고 있는 것이 분명하네. 백성의 민병대의 이름으로 몹쓸 녀석들을 처단해 주게."}, {"그라이언 스타우트맨틀이 데피아즈단 노상강도 15명과 데피아즈단 정찰병 5명과 데피아즈단 복면강도 5명을 처치하고 감시의 언덕으로 돌아오라고 했습니다."}},
[15] = {"메아리 광산 조사", {"정찰병의 보고에 의하면 생각했던 것보다 코볼트가 훨씬 많이 출몰하고 있다고 합니다. 코볼트 잡역부 무리가 북쪽의 메아리 광산 근처에 야영지를 건설했습니다.","광산으로 가서 코볼트 놈들을 처치해 주십시오. 파악된 바로는 최소 8마리는 되는 듯합니다. 놈들을 처치하고 다른 놈들이 더 있는지 알아본 다음 제게 보고해 주십시오."}, {"코볼트 잡역부 8마리를 처치한 다음 치안대장 맥브라이드에게 돌아와 보고해야 합니다."}},
[16] = {"목마른 제라드", nil, nil},
[17] = {"울다만에서 재료 찾기", {"자홍버섯이라는 식물이 새로이 발견되었다는 얘기를 들었다오. 자홍버섯은 울다만 발굴현장에서 군생하고 너무 깊숙이 탐험하지 않아도 되는 지역 내에 긴 굴 입구에서 찾을 수 있다오. 난 그 버섯이 연금술에 사용할 잠재적인 가치가 있는지 연구하고 싶소. 그래서 당신의 도움이 필요하다오.","자홍버섯을 찾아서 나에게 12개만 가져다주시오. 버섯송이에 가까이 가면 포자에서 독이 발산되니 조심하시오.","날 위해 이 일을 맡아주시오. 돌아오면 내 당신을 격려하는 의미에서 유명한 나의 복원의 비약을 한 꾸러미 주겠소이다!"}, {"텔사마에 있는 가크 힐터치에게 자홍버섯 12개를 가져가야 합니다."}},
[18] = {"도둑 떼 소탕", {"최근에 북녘골 근방에 새로운 도둑 떼가 나타났습니다. 스스로 데피아즈단이라 칭하는 이 도둑 떼들은 주로 동쪽 다리 건너편에서 목격되고 있습니다.","놈들이 무슨 짓을 하고 다니는지 정확히 파악되지는 않지만 분명 좋지 않은 일일 겁니다! 놈들이 쓰고 다니는 복면을 가져오시면 그 보상으로 무기를 드리도록 하겠습니다."}, {"북녘골 수도원 외부에 있는 부관 윌렘에게 붉은 삼베 복면 8개를 가져가야 합니다."}},
[19] = {"타릴준", {"검은바위 길잡이와 검은바위 보초병들이 이곳과 돌망루 요새 사이에 매복하고 있네. 길잡이들의 우두머리는 타릴준이라는 오크인데 우린 이 오크를 처치하려고 하네. 그의 머리카락을 내게 가져오게나!"}, {"붉은마루 산맥에 있는 치안대장 매리스에게 타릴준을 처치한 증거로 타릴준의 머리카락을 가져가야 합니다."}},
[20] = {"검은바위 부족의 위협", {"불타는 평원에서 온 검은바위 오크들이 북쪽의 돌망루 요새를 장악했네. 오크들은 그곳을 본거지로 삼아 호숫골의 북쪽 지역을 약탈하고 마을의 주민들을 우리 안의 가축처럼 가두어 놓고 있다네.","호숫골에서 오크의 위협을 제거할 수 있게 도와 주게나. 검은바위 오크들을 처치하고 내게 그들의 마모된 도끼 10개를 증거로 가져 오게.","돌망루 요새를 장악한 오크뿐만 아니라 붉은마루 산맥 남동쪽의 고립 지대에서도 오크들의 위협에 대한 보고가 들어오고 있네. 그곳에서도 검은바위 부족을 발견하면 처치해 주게나."}, {"호숫골에 있는 치안대장 매리스에게 마모된 도끼 10개를 가져가야 합니다."}},
[21] = {"메아리 광산 전투", {"일전에 당신이 조사한 결과를 볼 때 메아리 광산에 있는 코볼트 녀석들을 없애야 할 거 같습니다. 당신이 광산으로 돌아가서 코볼트를 제거하는 일을 도와주십시오.","지체할 시간이 없습니다. 저런 상태로 코볼트들이 방치되면 방치될수록 녀석들이 북녘골 깊숙이 터전을 마련하게 될 테니 말입니다."}, {"코볼트 일꾼 8마리를 처치한 다음 북녘골 수도원에 있는 치안대장 맥브라이드에게 가야 합니다."}},
[22] = {"뿔엄니멧돼지 간 파이", {"양파 껍질은 다 벗겼고, 마늘은 잘게 저몄고, 로즈메리도 빻았고, 빵은 다 구워졌고, 딜 이파리도 잘라 두었고, 고기 국물은 끓고 있으니, 이제 뿔엄니멧돼지 간 8개만 있으면 제 자랑거리인 고기 파이를 만들 수 있답니다!"}, {"뿔엄니멧돼지 간 파이를 만들 수 있도록 살마 살딘에게 뿔엄니멧돼지 간 8개를 가져가야 합니다."}},
[23] = {"우르산고우스의 앞발", {"강력한 곰인 우르산고우스를 이대로 두고 볼 수만은 없습니다!","앞발에 작은 초승달 문장이 있는 것으로 보아 나이트 엘프들이 숲 속을 돌아다니며 적을 찾아 공격하도록 이 야수를 훈련시킨 게 틀림없습니다.","얼마나 많은 호드의 용사들이 이 야수의 공격에 당했는지 모르겠군요.","우르산고우스를 처치해 그 앞발을 실바람 산장에 있는 타르칸 대장님께 가져가야만 합니다. 대장님께서도 이렇게 무시무시한 적군의 야수를 처치했다는 것을 아시면 분명히 기뻐하실 것입니다."}, {"우르산고우스의 앞발을 실바람 산장에 있는 대장 타르칸에게 가져가야 합니다."}},
[24] = {"샤둠브라의 머리", {"숲을 바람같이 누볐던 샤둠브라가 지금 당신에게 패해 생명이 빠져나간 상태로 누워 있습니다. 이 전설적인 생명체는 나이트 엘프들에게 매우 소중한 존재입니다. 밤호랑이의 머리는 무시무시한 전리품이 될 것이며, 잿빛 골짜기에서 얼라이언스의 시대가 끝났음을 알리는 표시가 될 것입니다.","헬스크림 감시초소에 있는 대장 고가스가 이 밤호랑이의 머리를 받으면 틀림없이 기뻐할 것입니다."}, {"샤둠브라의 머리를 헬스크림 감시초소에 있는 대장 고가스에게 가져가야 합니다."}},
[25] = {"이제 그만", {"자네도 봤어야 하는데!","엘프들은 실바람 산장이 함락당하지 않으리라고 생각했었네. 철통 같은 방어진과 물의 정령 군대가 있었으니 그 생각은 거의 맞았지.","하지만 아주 맞아떨어진 건 아니었다네.","아군 마법사 중 한 명이 다른 자들을 분노의 소용돌이로 빠뜨리는 뒤틀린 정령을 소환해냈으니까 말이네.","아, 그때 놈들의 표정이란!","<크게 웃는 타르칸>","그런데 불행하게도 그 뒤틀린 정령들을 소환한 아군 마법사가 전장에서 싸우던 중 전사했네. 그러니 자네가 소환된 정령들을 좀 없애주게."}, {"실바람 산장에 있는 대장 타르칸이 미스트랄 호수에서 타이더레스와 타락한 물의 정령 12마리를 처치해 달라고 했습니다."}},
[26] = {"귀중한 교훈", {"<name>, 나는 지금까지 아주 기쁜 마음으로 그대가 발전하는 모습을 지켜봐 왔소. 확실히 그대는 소중한 자연의 수호자로 성장해 가고 있소.","우리는 세나리우스의 축복으로 여러 가지 동물의 형상을 취할 수 있게 되었지. 이제 그중 하나에 대해 자세히 배워야 할 때가 되었소. 바로 수중 세계를 자유자재로 제어할 수 있는 상이오. 이것을 배우려면 달숲에 있는 밤의 안식처으로 가서, 세나리온 의회의 일원인 덴드리트 스타블레이즈와 얘기해 보시오. 그가 자세히 가르쳐 줄 거요."}, {"달숲, 밤의 안식처에 있는 덴드리트 스타블레이즈와 대화해야 합니다."}},
[27] = {"꼭 배워야 할 교훈", {"나는 지금까지 죽 그대가 발전하는 모습을 아주 기쁜 마음으로 지켜봐 왔네. 확실히 그대는 소중한 자연의 수호자로 성장해 가고 있군.","이제 세나리우스의 축복으로 우리가 그 형상을 취할 수 있게 된 여러 가지 동물의 상 중 하나에 대해 더 자세히 배워야 할 때가 되었네. 바로 수중 형상을 통달하는 것이지. 이를 배우려면 달숲에 있는 밤의 안식처으로 가서 그곳에 있는 세나리온 의회의 일원인 덴드리트 스타블레이즈와 얘기해 보시게. 그가 더 자세히 가르쳐 줄 걸세."}, {"달숲의 밤의 안식처에 있는 덴드리트 스타블레이즈와 대화해야 합니다."}},
[28] = {"엘룬아라 호수의 시험", {"첫 번째 시험으로, 엘룬아라 호수의 깊은 곳을 뒤져 제단 구슬을 찾으십시오. 제단 구슬은 금방 그 효력이 다하니 서둘러서 레물로스의 제단으로 가져가야 합니다. 제단에서 구슬을 사용한 다음, 그곳에 있는 타자리와 얘기를 나누십시오. 그녀는 달숲에서 가장 신임받는 관리인 중 한 명입니다.","이번 임무는 제한된 시간 내에 완료해야 하는데다, 구슬을 찾는 다른 드루이드들과 경쟁을 벌이게 될 수도 있으니 각오를 단단히 하셔야 할 겁니다. 그대의 정신과 신체의 민첩성이 바로 성공의 열쇠가 될 겁니다.","그럼, 행운을 빌겠습니다, <name>."}, {"엘룬아라 호수에서 제단 구슬을 찾은 후, 달숲 북서부에 있는 레물로스의 제단으로 가져가야 합니다. 그곳에 이르면 제단 구슬을 사용하십시오.","그런 다음 제단에 있는 타자리와 대화해야 시험이 끝납니다."}},
[29] = {"엘룬아라 호수의 시험", {"첫 번째 시험으로, 엘룬아라 호수의 깊은 곳을 뒤져 제단 구슬을 찾도록 하십시오. 제단 구슬은 그 효력이 금방 다하니 서둘러서 레물로스의 제단으로 가져가십시오. 제단에서 구슬을 사용한 다음, 그곳에 있는 타자리와 얘기를 해보십시오. 달의 숲에서 가장 신뢰받는 관리인 중 한 명이죠.","이번 임무는 제한된 시간 내에 완료해야 하는데다가, 구슬을 찾는 다른 드루이드들과 경쟁을 벌이게 될 수도 있으니, 각오를 단단히 하시기 바랍니다. 당신의 정신과 신체의 민첩성이 바로 성공의 열쇠가 될 겁니다.","그럼 행운을 빌겠습니다, <name>."}, {"엘룬아라 호수에서 제단 구슬을 찾은 후 달의 숲 북서부에 있는 레물로스의 제단으로 가져가야 합니다. 그곳에 이르면 제단 구슬을 사용하십시오.","그런 다음 제단에 있는 타자리와 대화를 하면 이 시험을 통과하게 됩니다."}},
[30] = {"바다표범의 시험", {"이제 두 번째 시험으로 두 개로 쪼개진 바다표범의 펜던트를 찾아야 합니다. 하나는 물에서 미끄러지듯 움직이는 바다표범의 민첩성을, 다른 하나는 엄청난 지구력과 인내로부터 힘을 그러모으죠. 우리 드루이드는 이 두 가지 힘을 모아 바다표범으로 변신합니다.","펜던트 조각들이 있는 곳을 알아보려면 달숲 주민들에게 여쭤보세요. 두 조각을 이곳으로 가져와서 하나로 합친 다음, 완성된 바다표범의 펜던트를 덴드리트 스타블레이즈에게 가져가도록 하세요."}, {"민첩성의 바다 펜던트 반쪽과 인내의 바다 펜던트 반쪽을 찾아야 합니다. 달숲 주민들과 얘기하여 이 물건들이 있을 만한 곳에 대한 단서를 얻으십시오.","두 개의 반쪽을 찾아 바다표범의 펜던트를 만들어야 합니다. 반드시, 레물로스의 제단 근처에서만 하나로 합칠 수 있습니다.","완성된 펜던트를 달숲, 밤의 안식처에 있는 덴드리트 스타블레이즈에게 가져가야 합니다."}},
[31] = {"바다표범 변신", {"그대는 젊은 드루이드가 바다표범으로 변신하기 위해 배워야 할 교훈을 모두 얻었습니다. 이제 썬더 블러프에 있는 상급 드루이드, 투락 룬토템에게 다시 가보십시오. 그에게 이 펜던트를 보여 주면 그대가 가르침을 받을 준비가 됐다는 걸 믿어 의심치 않을 겁니다. 그에게 물과 하나가 되는 법을 배우면, 그대의 이번 훈련은 끝이 나는 것입니다.","젊은 드루이드여, 안녕히... 훗날을 기약합시다."}, {"썬더 블러프로 돌아가 투락 룬토템에게 바다표범의 펜던트를 보여 주어야 합니다."}},
[32] = {"실리시드의 전조", {"여기 보고서를 받으시오. 이 보고서는 이런 일에 대해 실제로 잘 아는 사람에게 가져가는 것이 좋겠소.","질지빈 드럼로어라는 트롤 학자를 알고 있는데 아제로스 생태계 고유의 생물들을 연구하며 많은 시간을 보내고 있소. 게다가 역사학에도 조예가 깊다오. 이 보고서를 그에게 전하면 이 위협이 정확히 무엇인지 그리고 그것을 막을 방법이 있는지 알 게 될 테지. 분명 도움을 줄 것이오. 이번 일에는 우리가 만날 수 있는 모든 이의 도움이 필요하오.","내가 마지막으로 들은 소식에 의하면 질지빈은 오그리마의 골목길 2층에 있는 집에 살고 있다고 합디다."}, {"오그리마에 있는 질지빈 드럼로어에게 보고서를 가져가야 합니다."}},
[33] = {"변경의 늑대들", {"무엇이 늑대들에게 영향을 미치는지 모르겠지만, 어쨌든 이 현상이 잠잠해지질 않는군요. 아픈 늑대는 날마다 늘어만 가고 있습니다. 야생 동물을 죽이라는 부탁 따위를 해야 한다니 정말 가슴 아프지만, 병에 걸린 새끼 늑대들을 처치하지 않으면 이제 발발한 이 질병이 유행병으로 번질 겁니다.","골짜기에 있는 병에 걸린 새끼 늑대를 처치하고, 그 증거로 감염된 늑대 모피를 가져와 주십시오."}, {"북녘골 수도원 바깥에 있는 이건 펠트스키너에게 병에 걸린 늑대 모피 8장을 가져가야 합니다."}},
[34] = {"불청객", {"제 정원이 또 짓밟히고 엉망이 되어버려서 속상해 죽겠어요. 범인이 누군지는 알고 있어요. 매리스 치안대장님이 서리먹보라고 이름 붙인 버림받은 멧돼지가 범인이라고 하셨거든요.","이제는 내년까지 나팔수선화를 다시 심을 수도 없단 말이에요. 정말 속상해요! 그 골칫거리 멧돼지 한 마리 때문에 완전히 망했어요. 제발 그 골칫거리를 없애 주세요! 녀석의 엄니를 가져오시면 기꺼이 보답해 드릴게요. 그 멧돼지는 주로 호숫골의 남서쪽 들판에서 어슬렁거리다가 간혹 이곳으로 와서 내 정원을 망가뜨린답니다."}, {"호숫골의 마티 제인로즈가 서리먹보를 처치해 달라고 했습니다. 마티 제인로즈에게 서리먹보의 엄니를 증거로 가져가야 합니다."}},
[35] = {"소문의 진상", {"멀록에 대한 소문의 진상을 확인하려면 엘윈 숲 동쪽 다리에 있는 경비병 토마스와 이야기를 나눠 보십시오. 그는 지난 몇 주간 그 근방에서 근무했었으니까 그 지역의 상황을 잘 알고 있을 겁니다.","여기 당신을 경비병 토마스에게 데려다 줄 스톰윈드 군마가 있습니다. 이 군마를 쓸 생각이라면 제게 알려주십시오."}, {"치안대장 더그한이 경비병 토마스와 대화해 달라고 했습니다."}},
[36] = {"서부정통 스튜", {"전 농장을 떠나게 되리라고는 꿈에도 생각지 못했답니다. 하지만, 밭에 작물 도둑들이 들끓으니 이제 이곳은 너무 위험해서 살 수가 없네요. 제 남편인 펄브라우가 마차를 수리하면 바로 떠날 거예요.","제 부탁을 하나만 들어주시겠어요? 제가 서부정통 스튜의 조리법을 적어 드릴 테니 저쪽 농장에 있는 살마 살딘에게 가져다주세요. 살딘 농장은 길이 갈라지는 곳 바로 너머에 있어요."}, {"베르나 펄브라우가 살마 살딘에게 서부정통 스튜 조리법을 전해 달라고 했습니다."}},
[37] = {"사라진 경비병 수색", {"며칠 전 강 주변 정찰을 위해 롤프와 말라카이를 파견했는데 아직 돌아오질 않았습니다. 이들에게 무슨 일이 일어났는지를 알아야 보고를 할 수 있는데 말입니다.","강을 따라 북쪽으로 가서 그 두 경비병을 찾아봐 주시겠습니까? 하다못해 시체라도 말입니다."}, {"경비병 토마스가 강 북쪽으로 가서 사라진 두 경비병, 롤프와 말라카이를 찾아달라고 했습니다."}},
[38] = {"서부정통 스튜 조리", {"서부정통 스튜 만드는 데 재료가 좀 필요한데 좀 도와주세요!","질깃한 독수리 고기 3개","뿔엄니멧돼지 주둥이 3개","멀록 눈알 3개","오크라 3개를 가져다주세요."}, {"살마 살딘이 질깃한 독수리 고기 3개, 뿔엄니멧돼지 주둥이 3개, 멀록 눈알 3개, 그리고 오크라 3개를 구해 달라고 했습니다."}},
[39] = {"토마스의 보고 전달", {"더그한 치안대장님께 말라카이와 롤프의 죽음을 말씀드려 주십시오. 그리고 엘윈 숲 동쪽에 있는 멀록들을 견제하기엔 현재 병력만으로는 무리라고 보고해 주시기 바랍니다.","이쪽에 충분한 병력을 배치할 만큼 상황이 여의치 않다는 것은 알지만 더그한님께서 이 지역에 희망이 되어 줄 누군가를 보내주시리라 믿습니다."}, {"황금골에 있는 치안대장 더그한에게 보고해야 합니다."}},
[40] = {"멀록의 위협", {"에... 엘윈 숲에 위험이 닥쳐오고 있어요! 멀록들이 엘윈 숲 동쪽에 있는 가... 강가를 거슬러 올라가며 무... 물고기를 겁주고 선량한 사람들을 해... 해치고 있어요!","제가 더... 더그한 치안대장님께 분명히 말씀드렸지만 그분은 노... 놀과 도둑을 더 걱정하고 계세요. 그분은 머... 멀록들이 위협이라는 걸 미... 믿지 않으세요.","부디 더... 더그한님께 동쪽으로 병력을 더 보내달라고 서... 설득해 주세요!"}, {"말더듬이 레미가 황금골에 있는 치안대장 더그한과 대화해 달라고 했습니다."}},
[45] = {"롤프의 생사 확인", {"이 지역을 자세히 조사해 보니 물갈퀴 모양의 발자국이 돌무덤 호수를 따라 동쪽으로 이어져 있는 것을 볼 수 있었습니다. 동쪽으로 멀리 떨어진 곳에는 어렴풋이 멀록 마을이 보입니다.","아마도 롤프는 저곳에서 최후를 맞은 것 같습니다."}, {"멀록 마을에서 롤프의 생사를 확인해야 합니다."}},
[46] = {"멀록 현상금", {"스톰윈드 군대에서는 엘윈 숲에 있는 멀록 잠복꾼과 멀록 채집꾼들에게 현상금을 걸었습니다. 멀록들을 처치하고 찢어진 멀록 지느러미를 경비병 토마스에게 가져가면, 스톰윈드 군대에서 충분한 보상을 해줄 것입니다.","멀록 마을은 두 군데가 있는데, 하나는 여기서 북쪽에 있는 돌무덤 호수에 있고, 나머지 하나는 남쪽에 시냇물이 갈라지는 곳에서 볼 수 있을 겁니다."}, {"엘윈 숲 다리 동쪽에 있는 경비병 토마스에게 찢어진 멀록 지느러미 8개를 가져가야 합니다."}},
[47] = {"금가루 거래", {"이 지역의 코... 코볼트들은 가끔 금가루를 가... 가지고 다녀요. 제겐 정말 유용한 것들인데 말이죠. 제게 금가루를 잔뜩 가져오면 마을에서 가... 가장 높은 값을 쳐 드리겠습니다!","코... 코볼트는 남쪽에 개... 개미굴 광산이나 부... 북동쪽에 벼...벽옥맥 광산 근처에서 차... 찾을 수 있습니다."}, {"황금골에 있는 말더듬이 레미에게 금가루 10개를 가져가야 합니다. 금가루는 엘윈 숲에 있는 코볼트로부터 얻을 수 있습니다."}},
[48] = {"달콤한 황색 맥주", {"남쪽 땅 그곳을 휘감는 덩굴","그 아래 깊은 곳에 흐르는 샘물","빛의 성스러운 딸을 닮은 그 순수","내게로 가져다주오 신성한 샘물을"}, {"그림부즈 썬더브루에게 신성한 샘물을 가져가야 합니다."}},
[49] = {"달콤한 황색 맥주", {"황색은 내 평생 사랑의 색조","하늘에 물드는 마지막 황혼을 닮았네","내 사랑이 열매 맺도록 가져다주오","보리와 호밀 그리고 옥수수 부대를"}, {"그림부즈 썬더브루에게 보리, 호밀, 옥수수 부대를 가져가야 합니다."}},
[50] = {"달콤한 황색 맥주", {"녹이고 녹여, 섞고 또 섞어","아무도 모를 우리만의 비법","튼튼한 금속으로 증류기를 만드세","진은을 구해 주오, 이 노래를 마칠 수 있도록"}, {"그림부즈 썬더브루에게 진은 주괴를 가져가야 합니다."}},
[51] = {"달콤한 황색 맥주", {"아직은 여과 안 된 신선한 증류주","경작을 기다리는 농토와 같네","숯 한 묶음을 만들어야지","이번에 필요한 건 플라타너스 가지 하나"}, {"그림부즈 썬더브루에게 플라타너스 가지 하나를 가져가야 합니다."}},
[52] = {"변경 방어", {"황금골에서 멀어질수록 야생동물들은 점점 더 난폭해지고 동쪽계곡 벌목지는 끊임없이 이어지는 늑대와 곰들의 공격으로 골치를 앓고 있습니다!","이곳에서 당신 도움을 좀 받았으면 좋겠습니다."}, {"굶주린 늑대나 회색 숲늑대 8마리와 새끼 숲곰 5마리를 사냥한 다음 엘윈 숲 동쪽 다리에 있는 경비병 토마스에게 가야 합니다."}},
[53] = {"달콤한 황색 맥주", {"감미로운 액체는 아기 눈물처럼 맑고","여러 해가 지나면 깊고 아름다운 황색이 되네","하지만 이 술에 흠뻑 취하기 전에","참나무 숯으로 맥주통을 만들어야지"}, {"그림부즈 썬더브루에게 참나무 숯 한 묶음을 가져가야 합니다."}},
[54] = {"황금골로의 전갈", {"당신은 <class>|1으로서;로서; 북녘골의 안전을 위해 애써 왔으니, 이번에는 엘윈 숲 주위의 방어를 맡기도록 하겠습니다.이 임무를 맡겠다면 제가 준비한 문서를 황금골의 치안대장 더그한에게 전해 주십시오. 남쪽 도로를 따라가다가 방벽을 지나 계속 가다 보면 황금골에 도착할 겁니다."}, {"엘윈 숲의 황금골에 있는 치안대장 더그한에게 치안대장 맥브라이드의 문서를 가져가야 합니다."}},
[55] = {"모벤트 펠", {"모벤트 펠은 까마귀 언덕 묘지가 내려다보이는 동쪽의 언덕 위에 있는 자신의 집에 숨어 있습니다. 이제 이 땅에서 그의 시대는 곧 끝나게 될 겁니다...","모벤트 파멸의 불꽃을 사용하여 그에게 대항하십시오. 불꽃은 그를 보호하고 있는 마법을 무력하게 할 겁니다.","그를 처치하여 우리를 사악한 손길로부터 구해 주시기 바랍니다. 내 복수를 해 주시고 그늘숲의 영웅이 되십시오!"}, {"모벤트 파멸의 불꽃을 사용하여 모벤트 펠을 처치하고 스벤의 야영지에 있는 스벤에게 모벤트 파멸의 불꽃을 돌려줘야 합니다."}},
[56] = {"어둠의 순찰대", {"어둠의 순찰대의 사령관으로서 어둠골의 주민을 보호하는 것은 내 본연의 임무요. 스톰윈드에서 이곳에 배치했던 경비병들을 철수했기 때문에 이젠 자체 병력만으로 싸워야 하는 상황이오.","당신의 도움이 절실히 필요하오! 어둠골 정남 쪽에 있는 고요의 정원 묘지로 들어가 해골 전사 8마리, 해골 마법사 6마리를 처치해 준다면 어둠의 순찰대에 큰 도움이 될 것이오. 이 언데드 녀석들이 다시는 어둠골을 위협하지 못하도록 해야 하오!"}, {"어둠골의 사령관 알시아 이본로크가 해골 전사 8마리와 해골 마법사 6마리를 처치해 달라고 했습니다."}},
[57] = {"어둠의 순찰대", {"숲에 언데드가 출몰한다는 신고가 끊이지 않고 있소! 자신 있다면 까마귀 언덕 묘지로 가서 저 지독한 해골 악령과 해골 병사를 각각 15마리씩 처치해 주시오. 보고에 따르면 어둠골 바로 남쪽의 고요의 정원 묘지와 그 주변에서도 출몰한다고 하오. 이 언데드 녀석들을 그늘숲에서 몰아내야만 하오!"}, {"어둠골의 사령관 알시아 이본로크가 해골 악령 15마리와 해골 병사 15마리를 처치해 달라고 했습니다."}},
[58] = {"어둠의 순찰대", {"까마귀 언덕의 상황은 여전히 나아지지 않고 있소. 마을의 안전을 책임질 순찰대원이 부족한 상황이니 이제 어둠골의 안녕은 당신 손에 달려있소. 다시 한번 까마귀 언덕으로 가서 묘지 동쪽에 있는 역병 걸린 시체 20마리를 처치해 주시오!"}, {"어둠골의 사령관 알시아 이본로크가 까마귀 언덕 묘지 동쪽에 있는 역병 걸린 시체 20마리를 처치해 달라고 했습니다."}},
[59] = {"천 갑옷과 가죽 갑옷", {"당신의 통찰력과 용기를 위해 내가 당신 갑옷에 붙일 좋은 문장을 하나 드리겠습니다. 이걸 동쪽계곡 벌목지에 있는 사라 팀버레인에게 가져다주면 갑옷 한 벌을 만들어 줄 겁니다. 갑옷을 받으면 엘윈 숲을 지키는 일에 사용하시길 바랍니다.","동쪽계곡 벌목지는 다리를 건너 동쪽에 있습니다."}, {"사라 팀버레인에게 스톰윈드 갑옷 문장을 줘야 합니다."}},
[60] = {"코볼트 양초", {"안녕하십니까. 잠깐 시간 좀 있으신가요?","저희 형제는 스톰윈드에서 연금술 가게를 운영하고 있는데 큰 양초를 모아 그 밀랍을 얻으려고 이곳에 왔습니다. 절 좀 도와주실 수 있습니까?","큰 양초는 코볼트들에게 얻을 수 있는데 듣자하니 녀석들이 엘윈 숲의 광산에 출몰한다고 하더군요. 남쪽의 개미굴 광산과 동쪽의 벽옥맥 광산에 말이죠. 이 두 곳 중 한 곳에 가서 양초를 구해다 주시면 보상을 해 드리도록 하겠습니다."}, {"황금골에 있는 윌리엄 페슬에게 큰 양초 8개를 가져가야 합니다."}},
[61] = {"스톰윈드로의 배달", {"우리 형 모건이 스톰윈드에서 양초가 배달되기를 기다리고 있는데 제가 거기까지 갈 시간이 없답니다. 제 대신 이 짐을 운반해 주시면 형이 충분히 사례해 드릴 겁니다.","지금까지 모은 양초를 다 포장했으니 이걸 가지고 스톰윈드 상업 지구에 있는 우리 상점인 페슬의 약제상에서 형 모건을 찾으시기 바랍니다."}, {"스톰윈드 상업 지구에 있는 모건 페슬에게 윌리엄의 뱃짐을 가져가야 합니다."}},
[62] = {"개미굴 광산", {"북녘골에 있는 광산에만 문제가 있는 게 아닙니다! 엘윈의 개미굴 광산도 코볼트의 소굴이 되어 버렸다는 보고를 받았습니다.","광산을 정찰하여 보고 내용이 사실인지 확인한 후 제게 돌아오십시오. 개미굴 광산은 황금골에서 남쪽 방향, 스톤필드 농장과 매클루어 포도밭 사이에 있습니다."}, {"개미굴 광산을 조사하고 황금골에 있는 치안대장 더그한에게 가야 합니다."}},
[63] = {"물의 부름", {"공동묘지 서쪽, 은빛소나무 숲의 해변을 따라가다 보면 역병 때문에 황폐해진 제단이 있을 거예요. 그곳 정령들은 이제 우리 종족과 말도 섞지 않는답니다. 타락한 하급 물의 현신을 해치우고 연못을 정화하는 것이 당신의 임무예요.","그 정령에게 얻은 현신의 팔보호구와 브린이 만들어준 물병에 남아 있는 순수한 물을 가져가서 화로 위에 올려놓으세요. 그러면 하급 물의 현신을 소환해 정화할 수 있을 거예요.","임무를 완수하면 그 증거를 가져오세요. 그러면 답례로 토템을 드리죠."}, {"은빛소나무 숲에서 타락한 하급 물의 현신을 물리치고 영원의 샘 제단 위에 타락한 현신의 팔보호구와 얼마 남지 않은 순수한 물을 함께 올려놓아야 합니다."}},
[64] = {"두고 온 회중시계", {"어떻게 이런 일이! 내 아내 베르나가 밭에서 시끄러운 소리가 난다며 나를 깨웠습니다. 그래서 밖에 나가봤더니 밭에 불량배 놈들이 떼지어 있는 겁니다. 아내와 함께 서둘러 도망쳐 나오느라 내 회중시계도 챙기지 못했어요. 그 시계는 우리 결혼식 날 장인 어른께서 결혼 선물로 주신 거란 말입니다. 놈들이 그걸 가져갈 거라고 생각하니 도저히 참을 수가 없군요. 회중시계는 우리 농가의 옷장 속에 두고 나왔어요. 서쪽에 있는 호박밭에 찾아가시면 우리 집을 쉽게 찾을 수 있을 겁니다. 시계를 가져다준다면 정말 고맙겠습니다!"}, {"농부 펄브라우가 서쪽의 호박밭에 있는 그의 농가로 가서 옷장 안에 있는 회중시계를 가져다 달라고 했습니다."}},
[65] = {"데피아즈 결사단", {"자칭 데피아즈 결사단이라는 비열한 패거리가 서부 몰락지대에서 선량한 사람들을 내쫓고 있네. 귀관이 이 흉악한 조직에 잠입하여 놈들의 우두머리가 누구이며 어디에 숨어 있는지 알아내 주게. 호숫골의 선술집에 가면 와일리라는 건달이 있는데, 내게 진 빚이 있으니 자네를 도와줄 것일세.","엘윈 숲 동쪽의 붉은마루 산맥에 있는 호숫골로 가서 귀관이 할 수 있는 일을 찾아보도록 하게."}, {"그라이언 스타우트맨틀이 호숫골의 와일리와 대화해 달라고 했습니다."}},
[66] = {"스탈반의 전설", {"내 손녀 알리사가 큰 위험에 처해 있다는 끔찍하고 불길한 기운을 느꼈네. 그래서 카드로 점을 쳐 보았더니 죽음이 보였어.","난 오랫동안 어두운 최면 상태에 빠져 있다가 이 끔찍한 수수께끼의 단서를 얻을 수 있었다네. 이름이 떠올랐어, 스탈반이라는 이름이 말이야.","마을회관에서 서기관을 찾아서 이 스탈반이라는 인물에 대해 좀 더 알아보도록 하게. 우리 모두의 운명이 위태로워."}, {"어둠골 마을회관에서 서기관 달트리를 찾아야 합니다."}},
[67] = {"스탈반의 전설", {"스탈반이요? 흠, 마을 기록을 한번 찾아보죠.","스탈반. . .스탈반. . .어디 보자, 아! 여기 있군요. 스탈반 미스트맨틀이라는 자에 대한 기록이 있습니다. 여기 마지막으로 기록된 주소는 달빛시내 학교로군요. 그런데 이런, 너무 오래된 기록이네요.","제 부탁 하나만 들어주시겠습니까? 혹시 달빛시내 마을에 가게 되시거든 이 사람에 대한 새로운 정보를 제게 좀 알려 주세요. 기록을 완전하게 정리하고 싶거든요."}, {"달빛시내 학교로 가서 스탈반에 대한 최신 정보를 어둠골에 있는 서기관 달트리에게 전해야 합니다."}},
[68] = {"스탈반의 전설", {"낡은 트렁크 안에서 먼지투성이의 부치지 않은 편지를 발견했습니다."}, {"어둠골에 있는 서기관 달트리에게 먼지투성이의 부치지 않은 편지를 가져가야 합니다."}},
[69] = {"스탈반의 전설", {"아니, 이런! 틀림없이 처음에 이걸 빠뜨렸었나 봅니다. 기록에 의하면 스탈반의 첫 번째 주소 바로 밑에 일부분이 지워진 다른 주소가 있어요. 그는 황금골에 있는 사자무리 여관으로 향하고 있었나 봅니다. 그곳을 한번 조사해 보세요."}, {"사자무리 여관으로 가서 스탈반에 대해 아는 사람이 있는지 확인해야 합니다."}},
[70] = {"스탈반의 전설", {"아! 스탈반이라는 이름이 이제야 생각나는군요.","오래전 폭풍이 몰아치던 날 밤에 전령이 와서는 하룻밤 묵을 곳을 찾았었지요. 그런데 자정이 거의 다 되었을 때쯤 그 사람이 비명을 지르며 계단을 뛰어 내려왔답니다. 얼굴은 두려움에 하얗게 질려 있었고 말입니다. 그리고 잠옷을 입은 채 폭우 속으로 사라졌어요.","위층 상자 안에 편지도 그대로 둔 채로 말이에요. 그 뒤로 편지를 찾으러 오지도 않았고. 그 편지들 중 하나는 스탈반이라는 사람이 스톰윈드의 대운하로 보내는 것이었어요. 뭐, 원하신다면 편지를 가져가셔도 됩니다."}, {"상자에서 스탈반의 배달되지 않은 편지를 입수해 스톰윈드의 대운하에 있는 누군가에게 전해야 합니다."}},
[71] = {"토마스에게 보고", {"발견한 두 목걸이를 다리에서 근무하고 있는 경비병 토마스에게 가져다주고 부하 경비병들이 살해되었음을 알려야 합니다."}, {"엘윈 숲 동쪽 다리에 있는 경비병 토마스에게 롤프와 말라카이의 목걸이를 가져가야 합니다."}},
[72] = {"스탈반의 전설", {"내 아버지는 내가 이 일을 맡기 훨씬 전에 토지 관리인이셨소. 아버지는 대학살이 있은 후 그 피비린내 나는 걸 다 치워야 했단 말이오. 하지만 그건 문제가 아니지.","플린트리지 신용조합의 마지막 자금이 바닥나 버렸었지. 이제는 가문의 마지막 재산까지 경매에 내놓아야 할 판이었소. 괘씸한 세금 도둑들 때문이지. 더 알고 싶은 게 있다면 저 뒤에 상자에 들어 있는 쓸모 없는 것들이나 마음껏 살펴보시오. 뭐, 뭔가 알아내게 될지도 모를 일이니..."}, {"뒤에 있는 짐 꾸러미에서 단서를 찾아봐야 합니다."}},
[74] = {"스탈반의 전설", {"상자 안에는 케케묵은 가족 유품들, 가족 초상화 하나, 사냥 트로피 몇 개, 낡은 책 몇 권 등 여러 가지 물건이 있습니다. 상자의 맨 아래쪽에 있는 도자기 항아리 밑에서 찢어진 일지 페이지를 발견합니다."}, {"발견한 찢어진 일지 페이지에 다음 단서가 들어 있습니다."}},
[75] = {"스탈반의 전설", {"내가 이곳으로 왔을 때 위층에 있는 상자 안에 양피지 문서 꾸러미가 있었다네. 처음 도착했을 때 그 문서들을 한번 보긴 했지만 눈이 어두워진 후로는 보려고 한 적이 없지.","이 장님에 가까운 늙은이에겐 힘든 일이니, 직접 위층의 상자에서 스탈반이라는 자에 대해 더 알아보는 데 도움이 될 만한 것이 있나 찾아보게나. 젊은이에게 도움이 될 만한 색 바랜 일지 페이지가 있을 걸세. 그걸 가져오면 내가 할 수 있는 한 도와 주도록 하겠네."}, {"치안대장 해거드가 위층의 상자에서 색 바랜 일지 페이지를 가져와 달라고 부탁했습니다."}},
[76] = {"벽옥맥 광산", {"당신 덕분에 개미굴 광산에 코볼트들이 우글거린다는 사실을 확인했으니, 이번에는 좀 더 멀리 있는 벽옥맥 광산을 조사해 봐야겠습니다.","벽옥맥 광산을 탐색하여 코볼트가 있는지 확인해 주십시오. 벽옥맥 광산에 가려면 일단 길을 따라 동쪽으로 가다가 아조라의 탑이 나오면 거기서 북쪽으로 가시면 됩니다. 그러면 구릉에 있는 광산을 발견할 수 있습니다."}, {"벽옥맥 광산을 탐색한 다음 황금골에 있는 치안대장 더그한에게 보고해야 합니다."}},
[77] = {"까다로운 임무", {"내게 드란 드로퍼스가 찾는 리플의 원래 포장 재료가 있소. 말톤 아버지는 맘에 안 들지만 그에게 줄 리플을 포장하는 걸 도와주겠소. 단 리플을 두 병 더 가져와 준다면 말이오. 굼벵이 바위굴 근처의 버려진 호드 기지에 가면 상자가 있는데 그 속에 리플이 들어 있소.","아주 힘든 임무가 될 거요. 폐허에는 지독한 수액괴물들이 들러붙어 있거든. 소문을 듣자 하니 우두머리 수액괴물이 지배한다고 하오. 그거야 어쨌든 리플을 구해오면 포장을 해주리다."}, {"굼벵이 바위굴의 버려진 호드 기지 근처에서 상자에 든 동부 내륙지 벌꿀 리플 10병을 회수해야 합니다."}},
[78] = {"스탈반의 전설", {"자넬 도와줄 만한 사람을 알고 있네. 내가 스톰윈드 경비병을 지휘하던 시절에 우리는 어둠골에 있는 진홍 까마귀 선술집에서 술을 마시곤 했지. 선술집주인 스미츠는 그 지역에서 일어나는 일이라면 모르는 게 없었다네. 그에게 이 일지를 보여주고 뭔가 알고 있는지 물어보게나."}, {"그늘숲의 어둠골에 있는 선술집주인 스미츠에게 색 바랜 일지 페이지를 가져가야 합니다."}},
[79] = {"스탈반의 전설", {"난 지난 몇 년 동안 이 스탈반이라는 인물의 이야기를 추적해 다녔지요. 몇 년 전 귀족들이 학살당했을 당시에도 나는 헤거드 씨와 함께 조사를 나갔답니다. 그때 이 낡은 보고서들을 찾았지만 이 필체가 숲 속의 그 미치광이의 것인 줄은 전혀 몰랐군요. 당신이 찾아낸 증거들이 그의 유죄를 입증한 셈입니다. 이걸 즉시 이본로크 사령관께 가져가 당신이 발견한 사실들을 알려 드리세요."}, {"사령관 알시아 이본로크에게 진흙투성이의 일지 페이지를 가져가야 합니다."}},
[80] = {"스탈반의 전설", {"난 오랫동안 그 스탈반이란 녀석을 주시해 왔소. 만약 이 일지를 녀석이 작성했다면 이로써 녀석의 죄가 분명히 입증되는 것이오.","지금까지 마을 등기부를 관리해 오고 있는 서기관 달트리는 필체 감정 전문가의 수준이라고 할 수 있소. 그에게 이 일지를 보여 주고 이 필체가 등록된 서명의 필체와 일치하는지 확인해 보도록 하시오."}, {"서기관 달트리에게 피투성이의 일지 페이지를 보여 주어야 합니다."}},
[81] = {"리플 배달", {"드란에게 가져갈 리플을 술통에 안전하게 잘 담았는지, 또 그 사람이 부탁했던 병도 몇 개 따로 챙겨 뒀는지 한번 더 확인했소. 드란을 좋아해서 한 일은 아니오. 소중한 리플이 흔들리면 맛이 없어지니까... 감사는 접어 두시오.","자, 오그리마의 고물상에 있는 드란 드로퍼스에게 이 술통을 가져다주시오. 그리고 부탁이니, 내 대신 말톤에게 안부 좀 전해 주시오. 좋은 술을 준비해두었으니 술친구가 생각나면 언제든 주저 말고 찾아오라고 말이오."}, {"오그리마에 있는 드란 드로퍼스에게 술통을 가져가야 합니다."}},
[82] = {"맹독의 둥지 조사", {"그럼, 그간 들어온 보고에 대해서 얘기해 주겠소. 녀석들이 한때 맹독의 둥지가 있던 물웅덩이에서 물을 모조리 빨아먹고 있다고 하오. 또... 다른 건 아무것도 아는 게 없소.","그러니... 먼저 녀석들에 대해 좀 더 자세히 파악하는 것이 급선무요! 맹독의 둥지로 가서 그 생물들의 신체 기관을 좀 갖다주시오. 센티파르 기관 5개를 구해 연금술사 페슬저그에게 조사를 부탁하시오. 그 친구라면 분명 녀석들의 정체를 알아낼 수 있을 거요!"}, {"맹독의 둥지에서 센티파르 곤충 기관 5개를 구해 가젯잔에 있는 연금술사 페슬저그에게 가져가야 합니다."}},
[83] = {"질 좋은 리넨 복면", {"엘윈에 있는 패거리들은 질 좋은 리넨을 재료로 제가 만들 수 있는 복면을 씁니다.","리넨 조각을 가져오시면 그걸 사용해서 멋진 걸 만들어 드릴게요.","엘윈 숲 도처에서 제멋대로 날뛰는 범죄자들을 찾을 수 있을 거예요."}, {"동쪽계곡 벌목지에 있는 사라 팀버레인에게 리넨 조각 6개를 가져가야 합니다."}},
[84] = {"빌리에게 돌아가기", {"여기 있어요. 이 파이를 빌리에게 가져다주세요. 빌리 그 녀석, 이 파이를 먹고 목이라도 막혀 고생이나 했으면 좋겠네요!"}, {"매클루어 포도밭에 있는 빌리 매클루어에게 돼지 삼겹살 파이를 가져가야 합니다."}},
[85] = {"잃어버린 목걸이", {"얼마 전 목걸이를 잃어버렸는데, 아무래도 내 생각엔 빌리 매클루어라는 꼬마가 가져간 것 같아요. 그 꼬마는 주로 동쪽에 있는 매클루어 포도밭에서 놀곤 한답니다.","내 목걸이를 좀 되찾아 주세요. 그러면 이 늙은이의 가슴이 따뜻해질 겁니다."}, {"빌리 매클루어와 대화해야 합니다."}},
[86] = {"빌리를 위한 파이", {"파이를 하나 가져다주시면 누가 목걸이를 가지고 있는지 알려 드릴 수 있을 거 같은데... 알고 계신지 모르겠지만 저쪽 농장에 있는 버니스 아줌마가 기막힌 돼지 삼겹살 파이를 만들거든요.","우리 농장 주위에 돌아다니는 멧돼지를 잡아서 부드러운 멧돼지 고기를 몇 개 가져다주시고 파이가 왜 필요한지 얘기하시면 분명히 하나 만들어 주실 거예요."}, {"스톤필드 농장에 있는 보모 버니스 스톤필드에게 부드러운 멧돼지 고기 4개를 가져가야 합니다."}},
[87] = {"황금니 코볼트", {"개미굴 광산 근처에서 놀다가 아줌마의 목걸이를 떨어뜨린 것 같아요. 아...아니, 그러니까 아줌마의 목걸이를 봤어요. 목걸이가 왜 거기 있는지는 묻지 마세요. 제가 가져간 게 아니에요!","어쨌든 마지막으로 몸집이 큰 황금니 코볼트가 목걸이를 주워서 광산 위에 있는 자기 소굴로 도망치는 걸 봤어요. 그 코볼트를 찾으면 목걸이도 찾을 수 있을 거예요. 진짜예요, 맹세할게요!"}, {"버니스의 목걸이를 스톤필드 농장에 있는 보모 버니스 스톤필드에게 가져가야 합니다."}},
[88] = {"공주를 없애라!", {"브랙웰 집안에는 마을 가축 대회에서 입상한 공주라는 이름의 돼지가 있는데, 몸집도 거대한 이 암퇘지가 이곳으로 와 내 채소를 먹어 버리니 얼마나 속상한지 모른답니다!","지금 공주가 여기 와서 우리 채소를 먹어 치우고 있어요! 공주를 없애고 증거물로 암퇘지의 목걸이를 가지고 오면 사례하지요.","공주는 우리 수박밭 한복판에서 찾을 수 있을 겁니다."}, {"공주를 해치운 후 스톤필드 농장에 있는 스톤필드 부인에게 놋쇠 목걸이를 가져가야 합니다."}},
[89] = {"영원고요 다리", {"마지막으로 오크의 침략이 있었을 때 우리는 모든 철을 녹여 총알, 검, 갑옷을 만들어야만 했네. 스톰윈드에 자재를 새로 보내 달라는 전갈을 보냈지만 붉은마루 놀 무리가 중간에 운반차를 가로채 호숫골 뒤쪽의 언덕으로 도망쳐 버렸지.","그래서 이제는 다리를 재건할 수 있는 자재가 부족하네. 내게 철제 파이크 5개와 철제 리벳 5개를 가져다준다면 확실히 보상해 주도록 하지.","자, 어서 출발하게나!"}, {"호숫골에 있는 현장감독 오슬로에게 철제 파이크 5개와 철제 리벳 5개를 가져가야 합니다."}},
[90] = {"늑대 양념 케밥", {"늑대 양념 케밥은 이곳에서 가장 인기 있는 요리 중 하나지요! 손님께서 원하시면 요리해 드릴 수도 있지만 우선 재료가 있어야겠지요? 늑대 살코기 10점과 스톰윈드 향초를 구해 오셨으면 합니다. 이 마을의 숲엔 늑대가 많이 살고 있으니 살코기는 쉽게 구할 수 있을 겁니다. 아, 향초는 스톰윈드 대운하 향기로운 화원에 있는 펠리시아 검프를 찾아가 보세요."}, {"늑대 살코기 10개와 스톰윈드 향초를 모아 어둠골의 주방장 그루얼에게 가야 합니다."}},
[91] = {"솔로몬의 법", {"이곳 붉은마루 산맥 동쪽 언덕엔 스톰윈드의 적이 우글거리고 있습니다. 한 사악한 녀석이 비전 마술을 사용하여 마을 사람들을 불안과 공포에 떨게 하고 있지요. 이 사악한 일을 벌이는 장본인은 바로 모건스라는 자입니다.","현재 모건스는 잿빛가죽 놀을 다스리며 자신의 명령을 따르게 하고 있습니다. 호숫골의 법에 따르면 왕국의 죄인을 도우면 사형에 처하도록 되어 있습니다.","이 사악한 놀들에게 호숫골의 이름으로 처벌을 내려야만 합니다!","그들을 처치하고 잿빛가죽 펜던트 10개를 가져다주시면 보상해 드리도록 하겠습니다."}, {"호숫골 마을회관에 있는 집행관 코나처에게 잿빛가죽 펜던트 10개를 가져가야 합니다."}},
[92] = {"붉은마루산 굴라시 스튜", {"손님께서 주문하신 붉은마루산 굴라시 스튜를 요리해 드릴 수 있다면 좋겠지만 문제가 좀 있어요. 최근에 오크들이 날뛰는 동안 스톰윈드 전체 부대에 식량을 공급하느라 재료 창고가 바닥이 났답니다. 혹시 재료를 구해다 주신다면 정성껏 요리해 드리죠.","질긴 대머리수리 고기 5개, 큰 뿔엄니멧돼지 주둥이 5개, 파삭파삭한 거미 고기 5개를 가져다주세요."}, {"호숫골의 주방장 브리아나가 질긴 대머리수리 고기 5개, 큰 뿔엄니멧돼지 주둥이 5개, 파삭파삭한 거미 고기 5개를 가져다 달라고 했습니다."}},
[93] = {"갈색 게살 케이크", {"작은 비밀을 하나 알려 드릴까요? 갈색 게살 케이크는 사실 거미 다리로 만든 거랍니다! 좀 메스껍다는 건 알지만 톡 쏘는 맛이 훌륭하고 씹는 맛도 일품이거든요! 끈끈한 거미 다리를 가져다주시면 케이크를 몇 개 구워 드리도록 하죠.","북쪽에 있는 언덕과 강 사이에 서식하는 그물독거미가 최고의 재료라고 합니다."}, {"끈끈한 거미 다리 6개를 모아 어둠골에 있는 주방장 그루얼에게 가져가야 합니다."}},
[94] = {"감시의 눈동자", {"내 경쟁자인 모건스라는 강력한 악의 마법사가 붉은마루 산맥의 일갈라의 탑에 살고 있다네. 난 아조라의 눈으로 그자를 몰래 감시할 수 있는 방법을 찾아내어 이제 멀리서도 그를 볼 수가 있게 되었네.","그러나 그의 생각을 연구하려면... 도움이 좀 더 필요하다네.","나를 도와 줄 생각이 있다면 내 아조라의 조각상을 일갈라의 탑으로 가져가 탑 근처의 오래된 사자상 위에 올려놓아 주게나.","그게 끝나면 아조라의 힘은 모건스의 차후 계획을 예견해 줄 수 있을 걸세."}, {"아조라의 조각상을 붉은마루 산맥에 있는 일갈라의 탑 근처의 오래된 사자상으로 가져가야 합니다."}},
[95] = {"스벤의 복수", {"난 남동쪽에 농장을 하나 운영했었습니다. 그런데 내가 없는 사이에 저승바람 고개에서 흑기사들이 농장으로 내려와 우리 가족을 모조리 학살해 버렸습니다!","집으로 돌아왔을 때 헛간 근처에서 유령 같이 창백한 얼굴을 한 자가 뭔가를 허겁지겁 묻고 있는 걸 봤습니다. 그자는 내가 다가가기 전에 도망쳐 버렸고, 나도 흑기사의 뒤를 추적해야했기에 그자가 무얼 숨긴 건지 아직까지 모르고 있습니다.","그자가 뭘 묻었는지 알아봐 주면 정말 고맙겠습니다. 숨긴 곳의 위치는 내 헛간 근처의 오래된 그루터기 뒤입니다."}, {"요르겐 농장으로 가서 오래된 그루터기 뒤에 묻힌 물건을 찾아야 합니다."}},
[96] = {"물의 부름", {"이 물의 결정을 받게. 그리고 저들에게 생명을 주게.","생명을 주고 깨닫게. 물은 곧 생명이요... 타락은 생명의 끝이라는 것을!","자, 가서 물의 힘으로 생명을 보호하게."}, {"불모의 땅에 있는 이슬렌 워터시어에게 물의 결정을 가져가야 합니다."}},
[97] = {"스탈반의 전설", {"필체가 일치한다는 사실을 즉시 이본로크 사령관께 전해 주십시오! 사령관님의 의혹이 사실로 밝혀졌다고요!"}, {"사령관 이본로크 경에게 필체가 일치한다는 소식을 보고해야 합니다."}},
[98] = {"스탈반의 전설", {"스탈반 미스트맨틀은 타락한 삶을 살았소. 그자의 손에 선량한 사람들이 희생됐고, 그자가 수없이 많은 범죄를 저질렀다는 건 의심의 여지도 없지. 지금 이 미치광이가 어둠골을 위협하고 있지만 또 어떤 추악한 계획을 세우고 있는지는 아무도 모르오. <class>. 마을 바로 북쪽에 있는 그의 오두막으로 가서 스탈반을 없애버리도록 하시오!","그리고 임무를 완수한 후에는 에바 부인에게 가서 미스트맨틀 가문의 반지를 보여 주도록 하시오. 결국 그녀의 예지력 덕분에 이런 소름끼치는 사실을 밝혀낼 수 있었던 거니까 말이오. 그리고 그녀 덕분에 어둠골도 더 안전해졌소."}, {"스탈반 미스트맨틀을 처치하고 에바 부인에게 미스트맨틀 가문의 반지를 보여 줘야 합니다."}},
[99] = {"아루갈의 계략", {"아루갈의 마법에 대한 흑막을 알아갈수록 그 불쌍한 바보가 경멸스럽군. 그자의 치료법이라는 것에 대한 조사가 거의 끝나 가고 있소.","장작나무 마을에서 벌어지고 있는 이상한 일들의 원인이 된 마법만 밝혀내면 모든 걸 알 수 있을 것이오. 그곳 농부들은 낮 동안은 인간의 모습을 하고 있지만 해가 지면 달분노 일족 늑대인간으로 변한다오.","아루갈이 주민들에게 채워 놓은 마법의 족쇄에서 힘을 뽑아내고자 하오. 마법걸린 장작나무 족쇄 6개를 내게 가져오시오."}, {"공동묘지에 있는 달라 돈위버에게 장작나무 족쇄 6개를 가져가야 합니다."}},
[100] = {"물의 부름", {"주문을 다 외우자 눈부신 빛이 뿜어져 나옵니다. 물방울이 불꽃에 닿는 소리가 희미해지고 주위의 타락한 정령들이 죽어가면서 온몸의 감각이 마비되는 것 같습니다."}, {"은빛소나무 숲에 있는 하급 물의 현신과 대화하십시오."}},
[101] = {"시련의 토템", {"서서히 숲에 불길한 조짐이 나타나고 있으니 이제 자신을 방어해야 할 때가 온 게야. 바람이 곧 그대가 처하게 될 크나큰 위험에 대해 알려주고 있네.","고귀한 <class>|1이여;여;, 자신을 보호하고자 한다면 구울 송곳니 10개와 해골 손가락 10개, 거미독 약병 5개를 내게 가져오게. 내 마법의 힘으로 그대를 해하려는 자들을 막아 줄 시련의 토템을 만들어 주도록 하지."}, {"어둠골의 에바 부인에게 구울 송곳니 10개, 해골 손가락 10개와 거미독 약병 5개를 가져가야 합니다."}},
[102] = {"서부 몰락지대 순찰", {"서부 몰락지대의 평원을 가로질러 사악한 바람이 불어오고 있소. 스톰윈드는 내 고향 서부 몰락지대를 버렸지만 난 이곳에 남기로 결정한 주민들에게 절대 등을 돌리지 않을 것이오. 그저 농사밖에 몰랐던 우리가 이제 자신을 지키지 않으면 안 되는 시기요.","당신이 우리를 돕겠다면 서부 몰락지대의 초원을 순찰하는 임무를 맡기도록 하지. 죽음의 폐광 도적 떼와 야합한 고약한 놀 무리를 처치해 주시오. 놀의 앞발 8개를 가져오면 당신의 용기에 합당한 보상을 하도록 하겠소."}, {"감시의 언덕에 있는 정찰대장 다누빈에게 놀의 앞발 8개를 가져가야 합니다."}},
[103] = {"불꽃을 지키는 자", {"등대지기의 가족이 죽던 밤은 정말 끔찍했지. 내 눈앞에서 늙은 거먹눈 멀록이 공격을 주도하는데 나는 아무것도 하지 못하고 보고만 있었소. 후회하고 있지만 이미 엎질러진 물은 되돌릴 수 없으니 어쩌겠소. 지금은 대해를 항해 중인 선원들의 생명이 걱정이오. 배가 해안선의 거대한 암초에 너무 가까이 접근했소. 그리고 등대의 불꽃을 지키는 사람이 없으니 이제는 내가 그 책임을 져야 하오.","내가 횃불을 밝힐 수 있도록 허수아비 괴물들을 처치하고 기름 주머니 5개를 구해다 주시오."}, {"서부 몰락지대 등대에 있는 선장 그레이슨에게 기름 주머니 5개를 가져가야 합니다."}},
[104] = {"해안의 위협", {"암초에 배가 침몰해 내 목숨을 잃었을 때는 죽은 후에도 이렇게 산 자들과 함께 지낼 수 있으리라고는 상상도 못했소. 내가 죽던 그날 밤 늙은 거먹눈 멀록이 등대지기의 가족들을 위협하여 쫓아내는 바람에 등대가 꺼져 있었지. 그들은 나중에 다시 돌아와 횃불을 밝혔지만 늙은 거먹눈 멀록이 약한 멀록들을 선동해 다시 등대를 습격하고 말았소. 등대지기의 가족들은 내 눈앞에서 힘없이 죽어갔지.","해변에서 늙은 거먹눈 멀록을 보거든 처치해 버리고 놈의 비늘 하나를 내게 가져다주시면 내 보상하도록 하겠소."}, {"서부 몰락지대 등대에 있는 선장 그레이슨에게 늙은 거먹눈 멀록의 비늘을 가져가야 합니다."}},
[105] = {"리치의 최후", {"이제 안돌할을 공격해 그곳을 지배하고 있는 리치를 몰아낼 때가 왔네!","도시의 폐허 안쪽에 지배권을 쥐고 있는 소환사 아라즈라는 리치가 있네. 그는 도시 중심부의 폐허 주위에 있는 수많은 스컬지의 보호를 받고 있고, 그에게 직접적으로 결속되어 있는 개인 수행원들은 위협적인 존재들이네. 그와 맞서려면 많은 동료가 필요할게야.","리치를 처치하고 그 증거로 아라즈의 성물함 조각을 가져오도록 하게!"}, {"서부 역병지대의 보루에 있는 고위집행관 데링턴에게 아라즈의 성물함 조각을 가져가야 합니다."}},
[106] = {"젊은 연인들", {"아, 어떡하면 좋죠? 내 마음은 이미 토미 조 스톤필드에게 빼앗겨 버렸는데... 우리 집안은 토미 집안이랑 서로 원수지간이랍니다. 그래서 그의 멋진 얼굴을 보고 싶어도 만날 수가 없어요!","제발, 이 편지를 토미 조에게 전해 주세요. 토미 조는 보통 스톤필드 농장의 서쪽에 있는 강가에 있답니다. 여기서 정서쪽으로 가시면 돼요."}, {"토미 조 스톤필드에게 메이벨의 연애 편지를 가져가야 합니다."}},
[107] = {"윌리엄에게 보내는 쪽지", {"오, 그래. 윌리엄 페슬이라면 틀림없이 이 두 젊은 연인을 만나게 해 줄 수 있는 물약을 가지고 있을 게야!","자, 이 쪽지를 윌리엄에게 전달해 주게나. 윌리엄은 황금골의 사자무리 여관에 머물고 있다네."}, {"윌리엄 페슬에게 스톤필드 할머니의 쪽지를 전달해야 합니다."}},
[109] = {"그라이언 스타우트맨틀에게 보고", {"그동안 전투 경험을 많이 쌓은 것 같군. 아직 그라이언 스타우트맨틀 경께 보고하지 않았다면 어서 그분께 가 보도록 하시오. 그라이언 스타우트맨틀 경께서는 서부 몰락지대의 안전을 위해 결성된 백성의 민병대의 지휘관이시오. 당신이라면 분명히 그분께 도움이 될 것이외다. 서부 몰락지대의 중심부 도로를 약간 벗어난 곳에 위치한 감시의 언덕으로 가면 그분을 만나 뵐 수 있을 것이오."}, {"그라이언 스타우트맨틀과 대화해야 합니다. 서부 몰락지대의 중심부에서 길을 약간 벗어난 곳에 있는 감시의 언덕 돌탑으로 가면 그를 만날 수 있습니다."}},
[110] = {"곤충 기관 분석", {"당분간 나는 슈섕크의 업무를 정리하고 또... 그의 측량 장비를 살펴보려고 하오. 어째선지 그 곤충들로 인해 더 피해를 보게 될 것 같군.","연금술사 페슬저그에게 가서 내가 보냈던 곤충 기관을 조사했나 알아봐 주시오. 결과물이 준비됐으면 내게 가져와 주시오."}, {"가젯잔에 가서 연금술사 페슬저그를 만나 곤충 기관의 분석이 끝났는지 알아보아야 합니다."}},
[111] = {"할머니와 대화", {"부탁입니다. 이 문제를 우리 할머니와 의논해 주세요. 내가 메이벨을 만나게 해 줄 방법을 아는 분은 우리 할머니밖에 없을 겁니다.","할머니는 여기에서 동쪽에 있는 우리 집 안에 계십니다."}, {"스톤필드 할머니와 대화해야 합니다."}},
[112] = {"해초 수집", {"내가 메이벨에게 투명주를 만들어 주지요. 그러면 매클루어 포도밭을 빠져나와 토미 조를 만나러 갈 수 있을 겁니다. 하지만 이 술을 만들려면 투명한 해초가 필요하답니다.","원래 해초는 큰 바다에서 자라지만... 때로는 수정 호수에 있는 멀록이 수집해 두기도 하지요. 수정 호수는 바로 황금골의 동쪽에 있습니다."}, {"투명한 해초 잎 4개를 황금골의 윌리엄 페슬에게 가져가야 합니다."}},
[113] = {"곤충 기관 분석", {"즉시 이 보고서를 선임측량사 피즐도저에게 보여주어야 합니다. 사실... 글을 읽을 줄 아는 자라면 누구에게든 보여주어야 해요. 그만큼 도움의 손길이 절실한 중대사입니다. 이 심각한 위협은 그대로 방치할 경우 타나리스와 가젯잔뿐만 아니라 칼림도어 전체를 집어삼킬 거예요. 그게 다 뭐야, 아예 아제로스를 통째로 휩쓸어 버릴 수도 있어요. 그 세력은 사그라질 기미가 전혀 보이지 않으니까...","피즐도저에게 이 보고서의 사본을 만들어 달라고 하세요. 그래서 모두에게 알려야 해요. 이 위협에 대해서는 모두 알아야만 합니다!"}, {"가젯잔에 있는 선임측량사 피즐도저에게 곤충 분석 보고서를 가져가야 합니다. 연금술사 페슬저그가 요청한 대로 피즐도저로부터 보고서의 사본을 꼭 받아야 합니다."}},
[114] = {"탈출", {"이 투명주를 메이벨에게 가져다주십시오. 토미 조를 만나러 가는 데 걸리는 시간 동안은 약효가 지속될 겁니다."}, {"메이벨 매클루어에게 투명주를 가져가야 합니다."}},
[115] = {"어둠의 마법", {"검은바위 오크들이 붉은마루 산맥 공격을 지원하기 위해 흑마술사를 끌어들이고 있네. 이 흑마술사들은 어둠의 힘이 담긴 암흑의 수정구를 사용하고 있지. 이 수정구는 붉은마루 산맥의 경비병들에게 강력한 공격을 퍼붓고 있기 때문에 일차적으로 제거해야 할 물건일세.","검은바위 흑마술사를 쓰러뜨리고 암흑의 수정구를 찾아오면 내가 파괴하도록 하겠네. 이 세상에 그런 수정구는 없는 게 더 낫지 않겠나!"}, {"호숫골에 있는 치안대장 매리스에게 암흑의 수정구 3개를 가져가야 합니다."}},
[116] = {"떨어져 가는 술", {"휴, 술창고에 술이 다 떨어져 가고 있어요. 장사를 할 수 없을 지경이라 큰일이에요. 마지막으로 술을 들여온 지 무척 오래되었는데 사방에서 오크의 침략 때문에 난리가 나서 보급을 받을 수가 없답니다.","혹시 좀 도와 주실 수 있나요?","서부 몰락지대 언덕의 그림부즈 썬더브루에게 썬더브루 맥주통과 스톰윈드에서 멀롯 술통, 어둠골에서 밀주병, 황금골에서 향기로운 럼주 주머니를 가져다주세요. 제 부탁을 들어주시면 꼭 보답하겠습니다."}, {"호숫골의 바텐더 다니엘스가 썬더브루 맥주통과 멀롯 술통, 밀주병, 향기로운 럼주 주머니를 가져다 달라고 했습니다."}},
[117] = {"썬더브루 맥주", {"서두르게 친구여, 어서 서둘러","맛 좋은 맥주를","스튜같지 않고 진한 맥주로 만들려면","홉 열매를 넣어서 맥주를 만드세"}, {"특별한 맥주를 완성할 수 있도록 그림부즈 썬더브루에게 홉 열매 5개를 가져가야 합니다."}},
[118] = {"편자의 가격", {"스톰윈드 경비병들의 장비를 마련하느라 철을 너무 많이 써 버려서 마구간에 있는 말들의 발에 편자를 댈 철이 부족합니다!","이 쪽지를 황금골에 있는 대장장이 아르거스에게 전달해 주세요. 현재 겪고 있는 문제와 편자를 좀 공급해 달라는 부탁을 적어 놓았습니다."}, {"엘윈 숲의 황금골에 있는 대장장이 아르거스에게 베르너의 쪽지를 전달해야 합니다."}},
[119] = {"베르너에게 돌아가기", {"좋습니다, 내 그에게 편자 50개를 주도록 하지요...","자, 여기 상자에 넣어 포장했으니 가지고 가면 됩니다! 베르너에게 거래하게 되어 기쁘다는 말과 함께 꼭 상자에 붙여 놓은 쪽지를 읽으라고 전해주세요!"}, {"붉은마루 산맥의 호숫골에 있는 베르너 오스구드에게 편자 상자를 전해야 합니다."}},
[120] = {"스톰윈드로의 전갈", {"우리는 매우 어려운 시기를 겪고 있소, <class>|1이여;여;. 마을이 지속적인 공격을 받고 있어 증원군이 없으면 우리는 패배를 면치 못할 거요. 지금 맡기는 이 전갈은 그 무엇보다도 중요하오. 이 보고서를 즉시 스톰윈드의 장군 마커스 조나단 님께 전달해 주시오. 전달한 후에 좋건, 나쁘건 바로 소식을 가지고 돌아와 주시오.","자, 어서 서두르시오!"}, {"집정관 솔로몬이 스톰윈드에 있는 장군 마커스 조나단에게 보고서를 전달해 달라고 했습니다. 또한 전달한 후에 즉시 가야 합니다."}},
[121] = {"스톰윈드로의 전갈", {"내가 전하의 답변을 기다리는 동안 자네는 이 편지를 집정관 솔로몬에게 전달하게.","자, 그럼 해산하도록!"}, {"호숫골의 집정관 솔로몬에게 장군 마커스 조나단의 답장을 전달해야 합니다."}},
[122] = {"얇은 새끼용 비늘", {"황금골의 아르거스가 보내 온 편자에 대한 비용을 지급하려면 새끼 검은용을 처치하고 얇은 새끼용 비늘을 구해야 합니다. 얇은 새끼용 비늘을 6개 구해다 주시면 몇 개는 아르거스에게 지급하는데 사용하고... 나머지는 유용한 걸 만들어 드릴 수 있을 겁니다.","새끼 검은용은 주로 호숫골 남쪽에서 날아다니지만 여기저기 방랑하는 것을 좋아하는 습성이 있습니다. 항상 주위를 살피다가 발견하는 즉시 사냥하는 게 좋을 겁니다."}, {"얇은 새끼용 비늘을 6개 모아 붉은마루 산맥에 있는 베르너 오스구드에게 가져가야 합니다."}},
[123] = {"징세관", {"이것은 단지 \"징세관\"이라고만 명시되어 있는 누군가가 엘윈 숲에 있는 광산에서 운송되어 오는 황금을 받는 날짜와 시간을 적어놓은 일정표입니다.","이 일정표에 의하면 징세관은 엘윈 숲 동쪽의 브랙웰 호박밭 근처에 거주하는 것으로 보입니다.","중요한 정보이므로 동쪽계곡 벌목지에 있는 치안대장 맥크리에게 보고해야 합니다."}, {"엘윈 숲, 동쪽계곡 벌목지의 치안대장 맥크리에게 징세관의 수금 일정표를 가져가야 합니다."}},
[124] = {"놀의 울음소리", {"오크의 공격만 해도 정신이 없는데, 이제는 놀 투사와 놀 비술사까지 소중한 마구간 북쪽의 산마루를 어슬렁거리면서 낙오된 말들을 훔쳐가고 있습니다.","당신이 저 놀들을 해치워 준다면 내 말들도 고마워할 겁니다."}, {"붉은마루 투사 10마리와 붉은마루 비술사 8마리를 처치한 다음 베르너 오스구드에게 가야 합니다."}},
[125] = {"잃어버린 연장통", {"나 좀 도와 주게나. 마을이 공격받고 있어서 보급품을 얻기가 쉽지 않네. 내 연장통이 황금골에서 마차로 배달되었는데 지금은 다리가 끊어져 버려서 연장통을 배에 실어 운반하던 중 오크들의 투석기에 맞아 침몰해 버렸다네. 후, 연장통이 내 희망과 함께 호수 밑바닥으로 가라앉은 것이지.","내 연장통을 찾아 준다면 충분히 보상해 주겠네."}, {"호숫골의 현장감독 오슬로가 영원고요 호수의 밑바닥에서 그의 연장통을 찾아다 달라고 했습니다."}},
[126] = {"언덕에서 들려 오는 울음소리", {"아직도 놀들이 저곳에 있습니다. 호숫골 위의 언덕에서 놀들이 사냥하는 울음소리가 들려온단 말입니다. 특히 그들의 우두머리인 요울러의 울음소리가 무척 크게 들립니다.","요울러를 죽이고 그의 무리를 쫓아 주세요! 그리고 요울러의 앞발을 증거로 가져다주십시오.","요울러가 우두머리가 된 데는 분명 이유가 있을 겁니다. 그냥 단순한 싸움꾼은 아닐 테니 조심하시기 바랍니다."}, {"호숫골에 있는 베르너 오스구드에게 요울러의 앞발을 가져가야 합니다."}},
[127] = {"물고기 매매", {"영원고요 호수는 점박이 개복치로 유명하지요. 항상 수요가 넘치는데... 공급이 부족하답니다!","점박이 개복치 10마리를 가지고 오면 좋은 값을 쳐 드리죠. 직접 낚기가 어려우면 멀록에게 빼앗으면 될 겁니다!"}, {"호숫골에 있는 항구주임 바렌에게 점박이 개복치 10마리를 가져가야 합니다."}},
[128] = {"검은바위 부족 현상금", {"국왕 폐하의 영토를 침범한 저 괘씸한 검은바위 부족은 반드시 쓰러뜨려야만 합니다. 보고에 의하면 검은바위 부족 각 소대의 지휘관을 용사라고 부른답니다. 북쪽 야영지로 가면 이들을 찾을 수 있을 겁니다. 불타는 평원으로 가는 길의 바로 서쪽에 있지요.","집정관 솔로몬께서는 이 사악한 짐승들을 쓰러뜨리기를 바라고 계십니다. 검은바위 용사 15명을 처치하고 이곳으로 돌아와 주십시오."}, {"검은바위 용사 15명을 처치하면 호숫골의 경비병 하우가 보상을 해줄 겁니다."}},
[129] = {"공짜 점심", {"제 부탁 하나 들어주시겠어요? 경비병 파커의 점심을 준비해 놓았는데 지금 순찰 중이에요. 파커는 키가 크고 강한 스톰윈드 경비병이니까 자기 몸을 지킬 수 있지만, 저같이 힘없는 마을 사람이 나가기엔 밖은 너무 위험하답니다.","그러니까 이 점심을 저 대신 배달해 주고 오시면 점심 한 끼를 공짜로 대접할게요! 파커는 그늘숲으로 가는 도로를 따라 순찰하고 있을 겁니다."}, {"어둠골로 가는 도로를 순찰하고 있는 경비병 파커의 도시락을 가져가야 합니다."}},
[130] = {"약초채집가 방문", {"달시한테 돌아가기 전에 제 대신 달시에게 꽃을 좀 사다 주시겠습니까? 호숫골에 돌아가서 마을의 서쪽 끝으로 가면 꽃가게가 있답니다.","그런데 말이죠... 꽃을 살 때 꽃가게 아가씨 마티에게는 제가 달시한테 보내는 꽃이라는 걸 절대로 말하면 안 됩니다..."}, {"붉은마루 산맥 약초채집가, 마티 제인로즈와 대화해야 합니다."}},
[131] = {"나팔수선화 배달", {"자, 꽃다발이 완성됐어요. 특별히 제가 제일 좋아하는 나팔수선화로 만들었답니다!"}, {"달시에게 나팔수선화 꽃다발을 주어야 합니다."}},
[132] = {"데피아즈 결사단", {"지금부터 하는 얘기는 내 목숨을 걸고 하는 거니 잘 들어요. 데피아즈단은 지금 뭔가 큰일을 꾸미고 있어요. 최근 들은 바로는 그들이 놀, 코볼트, 심지어 고블린과도 손을 잡고 있다더군요.","이 쪽지를 스타우트맨틀에게 전해 주세요. 내가 알고 있는 거라고는 거기 적힌 게 전부에요."}, {"서부 몰락지대에 있는 그라이언 스타우트맨틀에게 와일리의 쪽지를 전달해야 합니다."}},
[133] = {"구울 허수아비", {"마을에서 떨어진 외딴곳에 살고 있어 보호를 받을 수 없는데도 구울이나 언데드에게 잡아먹히지 않은 게 신기하지 않나! 사실 어제만 해도 뼈만 남은 시체 무리가 내 집 벽을 치는 소리가 들렸다네.","구울을 퇴치할 수 있는 허수아비 같은 걸 만들어야겠어. 허수아비를 만들려면 구울 갈비뼈가 필요하네. 구울 갈비뼈는 굶주린 시체나 뼈만 남은 시체, 걸신들린 시체, 썩어버린 시체, 역병 걸린 시체 녀석들에게서 구할 수 있다네.","날 위해 구울 갈비뼈를 구해다 준다면 사례를 하도록 하지."}, {"구울 갈비뼈 7개를 모아 통나무집에 있는 애버크롬비에게 가져가야 합니다."}},
[134] = {"오우거 도적 떼", {"몇 주 전 집에서 멀리 떨어진 곳에서 향초를 뜯고 있었는데 오우거 무리가 날 공격했네! 혼비백산해서 도망치느라 귀중한 도구와 향초가 든 상자를 두고 왔지 뭔가.","놈들은 날 멀리 쫓아낸 후에 그늘숲 남쪽의 오우거 언덕으로 사라졌으니 상자는 오우거 언덕 근처에 있을 걸세.","내 상자를 제발 좀 찾아 주게. 내겐 너무 소중한 거야."}, {"애버크롬비에게 애버크롬비의 상자를 가져가야 합니다."}},
[135] = {"데피아즈 결사단", {"와일리가 석공 길드에 대해서도 썼는데, 이게 무슨 의미일까? 뭐, 실수로 잘못 적은 것일 수도 있겠지만. 데피아즈단이 석공 길드와 관계가 있다고? 이에 대해 확실히 알 만한 사람은 스톰윈드 SI:7의 단장인 마티아스 쇼밖에 없네. 그에게 와일리의 쪽지를 보여 주고 아는 게 있는지 알아보게나. 쇼를 찾는 게 어렵다면, 구 시가지의 병영에 가보도록 하게나."}, {"스톰윈드에 있는 마티아스 쇼에게 와일리의 쪽지를 가져가야 합니다."}},
[136] = {"선장 샌더스의 숨겨진 보물", {"누군가 이 글을 읽는다면 이 늙은 샌더스 선장이 바다 속에 수장되었다는 뜻이오. 그러니 이제 내 보물은 당신 것이지만 내 보물을 찾으려면 단서를 발견해야 하오.","먼저 내 사물함을 찾아야 하오. 아마 지금쯤 서부 몰락지대 서쪽 해안에 있는 난파선 근처 모래 속에 반쯤 묻혀 있을 것이오. 거기에 있는 많은 난파선 중에서 해변에 녹슨 닻을 내리고 있는 난파선을 발견하면 내 사물함을 찾을 수 있을 것이오. 거기서 다음 단서를 찾도록 하시오."}, {"선장 샌더스의 사물함에서 다음 단서를 찾아야 합니다."}},
[138] = {"샌더스 선장의 숨겨진 보물", {"보물의 단서에는 다음과 같이 쓰여 있습니다. \"제법이군 그래, 친구! 이제 정동쪽을 향해 갈 차례요. 동쪽 절벽 위의 길을 따라 동쪽으로 가시오. 길가에서 낡은 굴뚝의 잔해를 발견하면 다음 단서가 들어 있는 낡은 통을 발견할 것이오.\""}, {"다음 단서를 얻기 위해 굴뚝 잔해 근처에서 낡은 통을 찾아야 합니다."}},
[139] = {"샌더스 선장의 숨겨진 보물", {"발견한 통을 살피던 중에 또 다른 양피지 문서를 발견했습니다. 여기에는 다음과 같이 적혀 있습니다. \"이제 여기에서 북쪽으로 가야 하오. 해안 절벽의 외딴 풍차 옆에 있는 빈 단지가 보일 때까지 곧장 가도록 하시오. 단지를 잘 살펴보면 단서를 발견할 수 있을 것이오.\""}, {"다음 단서를 찾기 위해 풍차 옆에 있는 빈 단지를 살펴봐야 합니다."}},
[140] = {"샌더 선장의 숨겨진 보물", {"그가 적어 놓은 대로, 낡은 단지 안 깊은 곳에 샌더의 보물에 대한 또 다른 단서가 들어 있습니다. 잉크를 쏟은 자국이 있고 종이에서는 위스키 냄새가 나지만 다음과 같은 내용을 읽을 수 있습니다. \"이제 내 위스키 단지를 찾았으니 보물도 거의 다 찾았다고 할 수 있소! 서쪽으로 간 다음 해변으로 내려가시오. 바다에 이르면 계속 앞으로 가시오! 보물 상자가 있는 섬이 보일 때까지 서쪽으로 계속 헤엄쳐 가시오.\""}, {"보물을 얻기 위해 샌더 선장의 보물 상자를 찾아 열어야 합니다."}},
[141] = {"데피아즈 결사단", {"석공 길드는 에드윈 밴클리프라는 자가 운영하고 있네. 밴클리프는 1차 대전쟁 중 오크족이 파괴해 버린 스톰윈드의 재건을 책임졌던 인물이지. 재건 사업이 완료된 후, 밴클리프와 그의 동료는 자신들에 대한 왕의 대우에 강한 불만을 가지고 있었네. 이 사실만으로도 어느 정도 짐작이 가지.","자세한 내용은 이 보고서에 썼으니 즉시 서부 몰락지대의 그라이언 스타우트맨틀 경께 전하게!"}, {"서부 몰락지대의 그라이언 스타우트맨틀에게 쇼의 보고서를 가져가야 합니다."}},
[142] = {"데피아즈 결사단", {"데피아즈단의 은신처가 어디인지 알아내야 하네. 우리 정찰병의 보고에 따르면 달빛시내 마을, 황금해안 채석장, 그리고 장고로드 광산 사이의 길에서 데피아즈단 전령이 지나가는 것을 목격했다고 하네. 귀관이 가서 놈을 좀 잡아 주게나. 놈이 저항할 경우 처치한 다음 놈이 전하려던 전갈을 내게 가져오게."}, {"서부 몰락지대에서 데피아즈단 전령을 추적하여 그가 전하려던 전갈을 스타우트맨틀에게 전해야 합니다."}},
[143] = {"서부 몰락지대로의 전갈", {"지금쯤이면 보급품이 도착하고도 남았을 텐데 스톰윈드 군대에 무슨 일이 일어난 것이 틀림없소. 어쨌든 우리에겐 더는 지체할 시간이 없소. 그냥 이대로 아무런 도움도 받지 못하고 호숫골의 주민들이 죽는 것을 지켜보고 있을 수는 없소이다. 듣자하니 서부 몰락지대에서 백성의 민병대를 편성하고 있다고 하더군.","자, 이 청원서를 민병대의 지휘관인 그라이언 스타우트맨틀 경께 전달해 주시오. 왕의 증원군이 도착할 때까지 스타우트맨틀 경의 병사들이 어느 정도 도움을 줄 수 있을 거요."}, {"집정관 솔로몬이 서부 몰락지대에 있는 그라이언 스타우트맨틀에게 자신이 직접 작성한 탄원서를 전달해 달라고 했습니다."}},
[144] = {"서부 몰락지대로의 전갈", {"집정관 솔로몬의 청원서를 읽고 있으려니 마음이 무척 아프군. 하지만, 민병대는 자기 마을을 지키기 위해 소집된 것이라 서부 몰락지대가 아닌 다른 곳으로 파병까지 할 수는 없네. 애초에 민병대의 도움을 바란다는 것 자체가 무리한 요구지. 하기야 스톰윈드가 우리를 이렇게 버리지 않았다면 아예 민병대를 조직할 필요도 없었을 테지만 말일세.","이 답장을 붉은마루 산맥의 솔로몬 집정관님께 전달하도록 하게. 그리고 상황이 이렇게 되어 매우 유감이라고도 전해 주게나."}, {"그라이언 스타우트맨틀의 답장을 가지고 집정관 솔로몬에게 가야 합니다."}},
[145] = {"어둠골로의 전갈", {"염치없으나 다시 당신에게 도움을 청해야겠소. 이본로크 경에게 그늘숲의 정예 병력인 어둠의 순찰대를 파병하여 호숫골의 방어를 도와 달라는 탄원서를 작성하였소. 무엇보다도 이 서신을 그에게 빨리 전달하는 것이 중요하오. 한순간이라도 긴장의 끈을 놓지 마시오. 이본로크 경은 그늘숲의 중심에 있는 어둠골이라는 도시의 시장이오. 절대 길을 벗어나지 않도록 주의하시오."}, {"집정관 솔로몬이 어둠골에 있는 이본로크 경에게 탄원서를 전달해 달라고 했습니다."}},
[146] = {"다크샤이어로의 전갈", {"집정관 솔로몬과 개인적 친분이 없었다면 아마 그 사람을 미친 자로 생각했을 것일세. 스톰윈드가 도와 주지 않고 있으니 어둠의 순찰대를 레이크샤이어로 파병해 달라고? 어둠의 순찰대는 이 땅에 사악한 마법이 서서히 번지기 시작했을 때 스톰윈드가 우리를 버렸기에 조직된 것이야. 어둠의 순찰대를 파병한다면 순찰대원이 숲 가장자리에 도달하기도 전에 다크샤이어는 악의 구렁텅이에 빠질 걸세.","이 답장을 레이크샤이어에 있는 집정관 솔로몬에게 전달해 주게나."}, {"이본로크 경의 답장을 가지고 집정관 솔로몬에게 돌아가야 합니다."}},
[147] = {"징세관 수사", {"소위 말하는 징세관이라는 자가 우리 금광에서 황금을 가져가는 거라면, 왕국의 재산을 훔치는 행위가 됩니다! 정의의 이름으로 징세관을 처단하고 당신이 준 수금 일정표에 적혀 있는 반지를 가져오십시오. 징세관이 누구를 위해서 일하는지 밝히는 데 도움이 될 수도 있으니까요.","이 수금 일정표에 의하면 징세관은 브랙웰 호박밭에 숨어 있다고 하니 그곳에서 찾아보도록 하십시오."}, {"징세관을 찾아서 처치한 다음 징세관의 반지를 가지고 치안대장 맥크리에게 가야 합니다."}},
[148] = {"어둠골로부터의 보급품", {"오, 이 불쌍한 늙은이는 이제 어쩌란 말인가? 어둠골에서 보급품을 가져와야 하는데 마을은 너무 멀고 난 기력이 없으니, 아마 절대 마을까지 가지 못할 걸세.","자네가 어둠골로 가서 유령의 머리 가닥을 좀 가져다주겠나? 어둠골 광장에 있는 에바 부인의 집으로 가면 얻을 수 있을 걸세."}, {"에바 부인과 대화해야 합니다."}},
[149] = {"유령의 머리 가닥", {"어둠골 남쪽 언덕에 있는 오래된 농가에는 불쌍하게 죽어 영혼이 된 눈먼 메리가 나타나곤 한다네.","자, 여기 이 유령의 빗을 가지고 가서 눈먼 메리에게 머리카락을 좀 빗어 보라고 해보게. 그런 다음 빗을 내게 다시 가져오면 머리 가닥을 만들 유령 머리카락을 얻을 수 있다네.","아, 요즘 농가 주변을 어슬렁거리는 사악한 녀석들을 조심하게나."}, {"눈먼 메리에게 유령의 빗을 가져가야 합니다."}},
[150] = {"침입자 멀록", {"요즘 멀록이 여간 골칫거리가 아니랍니다. 이 대규모의 물고기 인간 집단이 영원고요 호수 기슭에 정착하여 물고기를 떼거리로 잡아내고 있어요! 저들이 호수에 있는 모든 물고기를 다 잡아먹기 전에 쫓아내야 합니다!","멀록을 사냥해 주십시오! 멀록 지느러미를 8개 가져다주면 보상해 드리도록 하죠."}, {"호숫골에 있는 항구주임 바렌에게 멀록 지느러미 8개를 가져가야 합니다."}},
[151] = {"늙은말 블랑쉬", {"늙은말 블랑쉬! 우리를 위해 일하느라 너무 지쳐버린 불쌍한 짐승이죠. 우리가 농장을 떠나오기 전까지는 제가 길렀지만 이제는 마차가 부서져서 정말 기르기가 힘겹군요. 그래서 말인데 귀리를 좀 구해서 불쌍한 블랑쉬에게 가져다주시면 정말 고맙겠어요.","아마 서부 몰락지대의 모든 농장 주위에서 귀리 부대를 발견할 수 있을 거예요. 저 끔찍한 기계들만 어떻게 할 수 있다면 말이죠. 이곳의 남서쪽에 여러 개의 농장이 있으니 그곳들을 찾아보세요."}, {"서부 몰락지대의 베르나 펄브라우가 귀리 한 줌 8개를 가져다 달라고 했습니다."}},
[152] = {"남아 있는 위험", {"그대도 아마 위험천만한 대해에서 희생되어 해안에 침몰한 난파선을 본 적이 있을 것이오. 선원들이 이 해변에 안전하게 정박할 수 있도록 서부 몰락지대의 해안에서 위험을 제거해야 하오. 바로 멀록이 문제인데...","멀록 바다사냥꾼 7마리, 전사 7마리, 점쟁이 7마리와 길잡이 7마리를 처치해 준다면 내 반드시 보상하도록 하겠소."}, {"멀록 바다사냥꾼 7마리, 멀록 전사 7마리, 멀록 점쟁이 7마리, 멀록 길잡이 7마리를 처치하고 서부 몰락지대 등대에 있는 선장 그레이슨에게 가야 합니다."}},
[153] = {"붉은 가죽 복면", {"데피아즈단 전방부대가 계속 이동 중에 있습니다. 나는 오랫동안 놈들의 움직임을 추적해 오면서, 이 패거리들이 붉은 가죽 복면을 쓰고 있기 때문에 복면을 쓴 자들만 뒤쫓으면 추적이 가능하다는 사실을 알아냈습니다.","내게 그 붉은 가죽 복면 15개를 가져오면 반드시 보상을 받을 수 있도록 해 드리겠습니다."}, {"감시의 언덕의 정찰병 갈리안에게 붉은 가죽 복면 15개를 가져가야 합니다."}},
[154] = {"유령의 빗 돌려주기", {"싫어!!!!!","자, 이 빗을 가지고 어서 가버려요!","난 괴물일 뿐이야. 누구도 다시는 날 아름답다고 생각하지 않을 거야!"}, {"다크샤이어의 에바 부인에게 유령의 머리빗을 다시 가져가야 합니다."}},
[155] = {"데피아즈 결사단", {"그래, 스타우트맨틀이 나를 보호한답시고 당신같이 허약한 <race>|1을;를; 보냈단 말이지? 쳇, 당신 잘하는 게 좋을 거요. 보아하니 친구들도 좀 더 부르는 게 좋을 거 같군.","우리 거래에 대해서는 잘 알고 있겠지? 당신은 나를 지켜 주고, 나는 데피아즈단의 은신처로 당신을 안내하고. 데피아즈단이 내 목을 노리고 있으니 내게서 잠시라도 한눈팔면 안 되오, 명심하라고! 그들이 내가 당신과 같이 있는 걸 본다면 날 죽이려 들거요.","당신과 친구들이 준비되면 알려 주시오."}, {"데피아즈 결사단의 비밀 은신처까지 데피아즈단 변절자를 호위해야 합니다. 데피아즈단의 변절자가 밴클리프와 그의 동료가 숨어 있는 곳까지 안내한 이후에 그라이언 스타우트맨틀에게 돌아가 그 위치를 알려야 합니다."}},
[156] = {"썩은 꽃 모으기", {"제게 썩은 꽃을 가져다주시면 좀비 독주를 한 병 만들어 드리겠습니다.","썩은 꽃은 낯선 곳에서 자란답니다. 뭐, 손님께서 소문대로 용감하시다면 해골 병사와 해골 악령의 두개골 속에 있는 걸 구해올 수 있겠지만... 어휴, 전 무서워서 감히 쳐다볼 수도 없었습니다요!","까마귀 언덕 묘지에 가시면 그 기분 나쁜 해골들이 우글대고 있을 겁니다. 놈들을 처치하면 시든 꽃을 얻을 수 있을 겁니다. 그리해 주시면 어둠골 마을에서도 고마워할 겁니다."}, {"썩은 꽃 8송이를 모아 어둠골의 선술집주인 스미츠에게 가져가야 합니다."}},
[157] = {"머리 가닥 전달", {"자, 여기 머리 가닥일세. 이 머리 가닥은 사악한 영혼들을 묶어 버릴 수도 있고, 생명이 소실된 물체에 소생의 기운을 불어넣고 봉할 수도 있어서 아주 쓸모가 많지."}, {"까마귀 언덕 묘지 북쪽의 통나무집에 있는 애버크롬비에게 유령의 머리 가닥을 가져가야 합니다."}},
[158] = {"좀비 독주", {"이제 특별한 술 한 병이 필요한데... 바로 좀비 독주라는 술이지. 어둠골의 진홍 까마귀 선술집주인 스미츠가 좀 가지고 있을 걸세.","서두르게나. 밤 공기가 점점 차가워지고 있어. 난 그 술로 몸을 녹이지 않으면 안 된다네!"}, {"선술집주인 스미츠와 대화해야 합니다."}},
[159] = {"좀비 독주 배달", {"자, 여기 좀비 독주입니다...","...그리고 노파심에서 드리는 말입니다만 이 술 조심하세요. 워낙 강해서 죽은 자들을 되살릴 수도 있을 정도니까 말입니다."}, {"통나무집에 있는 애버크롬비에게 좀비 독주를 가져가야 합니다."}},
[160] = {"시장에게 보내는 편지", {"이 편지를 다크샤이어의 시장인 엘로 이본로크에게 전하게.","그와는 오랜 친구 사이지... 아, 그리고 이 편지를 전하면 자네가 날 위해 해준 일에 대해 나만큼 이나 기뻐할 거야. 하하하하!"}, {"다크샤이어의 시장 엘로 이본로크 경에게 엘로에게 보내는 편지를 전해야 합니다."}},
[161] = {"어둠의 전조", {"내 느낌이 맞는다면 이건 일종의 폭약 가루인 것 같군. 무엇인지 확실히 알아내려면 폭파 전문가에게 분석을 맡겨야 하네.","이 일을 할 수 있는 사람은 한 명뿐이네. 애실런 스톤스머크라는 폭파전문가인데, 동쪽에 있는 모그로쉬 소굴에 있는 오우거들의 동태를 파악하는 임무를 맡았었지. 모그로쉬 소굴 쪽으로 가서 그를 찾아 이 가루의 성분이 무엇인지 자문을 좀 구해보게나."}, {"선임기술자 힌더바이르가 이상한 향기의 가루를 저습지의 모그로쉬 소굴에 있는 폭파 전문가 애실런 스톤스머크에게 전달해 달라고 했습니다."}},
[162] = {"실리시드의 출현", {"이 보고서는 이런 일에 대해 잘 아는 사람에게 가져다주는 게 좋겠소.","그라시나 스피릿마이트라는 나이트 엘프 학자를 알고 있는데 그녀는 아제로스 생태계의 생물들을 연구하며 많은 시간을 보내고 있소. 게다가 역사학에도 해박하지. 이 위협이 정확히 무엇인지 알아내고 또 이걸 막을 방법을 알아내려면 그녀에게 이 보고서를 가져가 도움을 청하시오.","내가 마지막으로 들은 소식으로는, 다르나서스에 있는 달의 신전에 산다고 합디다."}, {"다르나서스에 있는 그라시나 스피릿마이트에게 곤충 분석 보고서를 가져가야 합니다."}},
[163] = {"까마귀 언덕", {"까마귀 언덕에서 뭔가 심상치 않은 일이 벌어지고 있습니다. 칼로는 그곳 건물에서 뭔가가 출몰하고 있다고 믿고 있답니다. 며칠 전엔 늦은 밤 창문에서 불빛을 보았다고 하고 이틀 연속으로 그림자가 움직이는 걸 목격했다고 하더군요.","그늘숲의 안전을 걱정하는 분이라면, 까마귀 언덕에서 출몰하는 것이 어떤 괴물인지 확인해 주시기 바랍니다."}, {"까마귀 언덕에 출몰하는 것이 무엇인지 확인해야 합니다."}},
[164] = {"스벤에게 보급품 전달", {"스벤을 알고 있나요? 그는 정의를 위해 일하고 있는 사람이죠! 그와 그의 수행원이 그늘숲 북서쪽 변방에 야영지를 설치하고 지내고 있답니다.","그런데 안타깝게도 워낙 소수의 인원이다 보니 보급품을 가지러 마을에 사람을 보낼 여력이 없답니다. 그에게 보급품을 전해주시겠어요?"}, {"스벤에게 보급품을 가져가야 합니다."}},
[165] = {"은둔자", {"종종 필요한 물건을 사러 마을에 오는 노인이 있었는데 한동안 보지 못해서 무슨 일이 있는 건 아닌지 걱정이에요. 내 기억이 맞다면 그 노인은 까마귀 언덕 공동 묘지가 내려다보이는 통나무집에 살고 있을 거예요. 가서 노인이 무사한지 확인해 주세요."}, {"까마귀 언덕 묘지 근처 통나무집에 있는 노인이 잘 있는지 확인해야 합니다."}},
[166] = {"데피아즈 결사단", {"귀관이 완수해야 할 일이 하나 더 남아있네. 백성의 민병대의 이름으로 에드윈 밴클리프를 처단해야만 하네. 누군가에게 사형을 선고해야 한다는 것이 무척 가슴 아프지만, 서부 몰락지대 주민의 안녕이라는 더 큰 대의를 위해서 밴클리프는 영원히 잠재울 수밖에 없네. 놈을 처치한 후 놈이 신분을 숨기기 위해 사용하던 가면을 가져오게."}, {"에드윈 밴클리프를 처치하고 그 증거로 그의 가면을 그라이언 스타우트맨틀에게 가져가야 합니다."}},
[167] = {"형제여...", {"우리는 서부 몰락지대에 달빛시내 마을 헛간 밑에 숨겨진 거대한 광산 깊은 곳에 있었지. 그 고약한 데피아즈단 약탈자 놈들이 어디에서 왔는지 도무지 알 길이 없었소. 그건 그렇고, 우리 형제는 갱도가 무너져 광산에 갇히게 되었소. 나는 가까스로 탈출했지만 다른 사람들은... 그 후로 다른 사람들 소식을 들을 수 없었소.","보아하니 당신 운이 좋은 자인 것 같은데... 우리 형을 찾아봐 준다면 정말 고맙겠소이다. 우리 형은 항상 탐험가 연맹 배지를 지니고 다녔소. 형을 찾을 순 없어도 그 배지라도 있다면 조금이나마 위안이 될 것 같소."}, {"스톰윈드에 있는 빌더 시슬네틀에게 시슬네틀의 배지를 가져가야 합니다."}},
[168] = {"기억을 더듬어...", {"갱도가 무너지던 그 악몽 같던 날, 내 친구들이 많이 죽었소이다. 여행 중에 혹시 달빛시내 마을의 갱도를 지나게 된다면 그들을 확인할 수 있는 소지품이 있는지 살펴봐 주시겠소? 그리고 광부 조합의 명함을 하나라도 찾게 된다면, 그들의 가족에게 그 끔찍한 사고를 해명할 수 있도록 내게 가져다주시기 바라오."}, {"스톰윈드에 있는 빌더 시슬네틀에게 광부 조합의 명함 4장을 가져가야 합니다."}},
[169] = {"가스일조그 현상 수배", {"현상 수배를 내린다! 검은바위 부족의 우두머리인 가스일조그가 돌망루 요새를 공격하고 호숫골의 마을회관을 습격하고 있다.","가스일조그는 매우 위험한 인물로서 보고에 의하면, 최근 정복한 요새에 머물면서 다음 공격을 준비하고 있다고 한다. 무슨 수를 써서라도 검은바위 오크의 침략을 막아야 한다. 가스일조그를 처치하고 그 증거를 집정관 솔로몬에게 가져오는 용감한 자에게는 엄청난 보상이 주어질 것이다."}, {"가스일조그를 처치하고 그의 머리카락을 호숫골에 있는 집정관 솔로몬에게 가져가 보상을 받아야 합니다."}},
[170] = {"새로운 위협", {"우리를 좀 도와주실 수 있으신가요? 지난번 트로그가 습격한 이후로 경비대의 인원이 부족해서 말입니다. 우릴 도울 생각이 있는 사람이라면 누구든 대환영입니다.","요즘엔 망할 놈의 트로그들이 사방팔방에서 나타나고 있다던데 여기 눈마루 골짜기도 예외는 아닙니다. 트로그는 주로 남동쪽 언덕 근처나 얼어붙은 호수 근방에 출몰하고 있습니다.","그뿐만이 아닙니다. 며칠 전엔 서쪽에 있는 야영지까지 습격했다고 하더군요... 지금 우리만으론 역부족이니 트로그놈들을 좀 처치해 주시겠습니까?"}, {"발리르 프로스트해머가 바위턱트로그 6마리와 힘센 바위턱트로그 6마리를 처치해 달라고 했습니다."}},
[171] = {"얼라이언스 보육원", {"저한테 이렇게 잘해주시다니... 어린이 주간을 이렇게 즐겁게 보내리라고는 기대도 못 했는데... 감사해요.","우리가 본 많은 것들을 다른 친구들도 볼 수 있었으면 좋겠어요. 혹시 크래글 워블톱 아저씨를 다시 찾아가서 장난감 칼 선반을 하나 살 생각 없으세요? 아마 아직도 상업 지구 바깥 대운하에서 장난감이 가득 담긴 수레를 끌고 다니실 거 같은데. 장난감 칼 선반을 사서 가져가면, 보육원의 다른 친구들도 뭔가 즐거운 일을 할 수 있잖아요!","그러고 나면, 보육원으로 돌아갈 시간이 되겠죠."}, {"크래글 워블톱에게서 장난감 칼 선반을 구입한 다음 스톰윈드 보육원으로 아이를 데려가 대성당 광장에 있는 보육원 원장님 나이팅게일과 대화해야 합니다."}},
[172] = {"어린이 주간", {"여기 보육원 일을 돕고 싶으시다고요? 어린이 주간 동안 저희를 도와주신다면 너무 감사할 거예요. 당신에게 신의 축복이 있기를!","이 호루라기를 받으세요. 이것은 짧은 시간, 그러니까 어린이 주간에만 사용할 수 있어요. 호루라기를 한 번 불면 당신이 돌볼 아이와 얘기를 나눌 수 있고 한 번 더 불면 그 아이를 보낼 수 있죠.","부디 이 호루라기를 불어 당신이 돌볼 아이를 만나보세요! 우리 아이가 당신의 온정을 기다리고 있으니까요."}, {"오크 고아 호루라기를 사용해 어린이 주간에 돌볼 아이와 대화해야 합니다."}},
[173] = {"숲 속의 늑대인간", {"그늘숲에 피할 수 없는 암흑이 드리워지고 있는 것 같습니다. 대장 카레빈님의 임무는 이단과 악의 세력을 몰아내는 것입니다. 우리의 노력으로 어둠골 주민들이 안전하게 지낼 수 있는 것이랍니다.","우리 임무에 동참할 수 있습니까?","나도 예전에 당신과 같은 생각을 했었습니다. 어둠의 순찰대의 그 알량한 자기도취에 환멸을 느꼈었기에 대장 카레빈 님과 함께하기로 했습니다. 우리 임무에 동참할 생각이라면 말만 앞세워서는 안 될 겁니다.","밝은나무 숲이라고? 하! 말도 안 돼, 그런 곳을 밝다고 부르다니! 아무튼 그곳과 오염된 과수원에 있는 파멸어둠 흑마술사를 상대로 당신 실력을 한번 증명해 보시기 바랍니다."}, {"어둠골의 칼로를 위해 파멸어둠 흑마술사 6마리를 처치해야 합니다."}},
[174] = {"별 관측", {"나는 나의 노움 친구 코그가 별을 관측하는 장치를 만드는 작업을 도와 주기로 했다오. 이 장치가 완성되면 숲 속에서 일어나고 있는 심상치 않은 변화를 설명해 줄 진실을 알아낼 수 있을 것이오.","코그의 설계도에 따르면 숙련된 기술자가 만든 청동관이 첫 번째 재료라고 하오. 내게 청동관을 하나 가져다주시오."}, {"어둠골의 빅토리 프리즘안트라스가 청동관을 가져다 달라고 했습니다."}},
[175] = {"별 관측", {"코그의 설계도에 따르면 이번엔 빛을 반사하는 물건이 필요하오. 아, 늘 거울을 가지고 다니는 것으로 유명한 메리라는 여인이 생각나는구려. 당신이 메리를 찾아 이번 작업에 쓸 반사면을 빌려 줄 수 있는지 알아봐 주시겠소? 예전엔 그녀가 그늘숲 남쪽에 살았던 것으로 기억하오만..."}, {"메리를 찾아 빅토리의 작업에 사용할 반사면을 빌려 와야 합니다."}},
[176] = {"들창코 현상 수배", {"들창코 현상 수배:","엘윈 남서쪽 숲에서 돌아다니는 거대한 놀 들창코를 포획하려는 시도가 모두 수포로 돌아갔다.","이에 스톰윈드 군대는 이 놀의 목에 상당한 현상금을 걸고 수배하는 바이다. 현상금을 받으려는 현상금 사냥꾼은 숲을 탐험하여 야수를 처리해야 한다."}, {"\"들창코 사태\"를 처리하고 치안대장 더그한에게 가야 합니다."}},
[177] = {"별 관측", {"그 사건이... 일어나기 전까지, 그 거울은 나의 가장 귀중한 보물 중 하나였죠. 하지만 그 일이 있은 후 거울을 헛간 뒤에 버렸어요. 그곳을 어슬렁거리던 광기 어린 구울이 있었는데, 아마 그가 그 거울을 주워 갔을 거예요. 소문을 듣자하니 그 악마 같은 구울은 고요의 정원 묘지에 산다더군요."}, {"광기 어린 구울에게서 메리의 거울을 되찾아 어둠골에 빅토리 프리즘안트라스에게 가져가야 합니다."}},
[178] = {"테오크리투스의 역공", {"<허공에서 들리는 목소리...>","놀라지 말게. 나는 엘윈 숲에 있는 아조라의 탑의 고위 마법사, 테오크리투스일세. 자네가 지닌 그 펜던트는 잿빛가죽 놀과 그들의 수장인 모건스와 교신하는 수단이지.","수개월 동안 연구한 끝에 나도 그 펜던트를 통해 교신하는 방법을 알아냈지. 자네가 내 목소리를 들을 수 있다면 내 주문이 성공한 것일 게야.","그 펜던트를 내게 가지고 오면 보상해 주겠네."}, {"엘윈 숲의 아조라의 탑에 있는 마법사 테오크리투스에게 잿빛가죽 놀의 빛바랜 펜던트를 가져가야 합니다."}},
[179] = {"드워프 장갑", {"만나서 반갑네. 흠, 그런데 자세히 보니 손이 좀 추워 보이는군. 따뜻하게 해줄 게 필요하겠어.","어디 보자. 그래, 따뜻한 장갑이 좋겠군. 내가 이런 일은 그냥 못 지나치는 성미라서 말이야. 자네 두 손에 꼭 맞는 장갑을 만들어 주겠네. 단 한 가지 조건이 있어.","늑대 고기를 좀 구해다 주겠나? 그러면 동상 따윈 절대 걸리지 않을 따뜻한 장갑을 만들어 줌세. 어떤가?"}, {"스텐 스타우트암이 질긴 늑대 고기 8조각을 구해 달라고 했습니다."}},
[180] = {"부대장 붉은송곳니 현상 수배", {"현상 수배: 부대장 붉은송곳니","부대장 붉은송곳니는 마을과 왕국의 적인 모건스를 위해 일하고 있으며 잿빛가죽 놀의 우두머리로 알려져 있다. 집정관 솔로몬의 명에 따라 스톰윈드를 배반한 놀의 목에 현상금을 거는 바이다. 현상금을 원하는 자는 붉은송곳니의 앞발을 집정관에게 증거로 제출해야 한다."}, {"부대장 붉은송곳니를 처치한 후 그의 앞발을 가지고 호숫골의 집정관 솔로몬에게 가야 합니다."}},
[181] = {"별 관측", {"이제 한 가지 재료만 더 있으면 장치가 완성되는군. 코그의 설계도에 따르면 마지막 재료로 렌즈 같은 것이 필요하다고 쓰여있소. 장치에 사용할 만큼 큰 렌즈를 하나 알고 있긴 한데, 손에 넣기가 쉽지 않을 것이오.","그늘숲 남쪽에 있는 언덕에 짜르크불이라는 오우거가 살고 있다오. 짜르크불이 쓰고 있는 외눈안경을 렌즈로 사용하면 될 거 같은데... 내게 좀 가져다주지 않겠소?"}, {"그늘숲 남쪽의 오우거 언덕에서 짜르크불을 찾아 오우거의 외눈안경을 어둠골에 있는 빅토리에게 가져가야 합니다."}},
[182] = {"트롤의 위협", {"트롤들이 말썽을 부리기 시작했어요. 아, 평소보다 심각하다는 말이죠. 예전에도 녀석들이 전투 부대를 모집하는 모습을 보긴 했지만, 이번에는 조금... 달라요.","어쨌건 지금 가장 중요한 일은 바로 앤빌마를 보호하는 거지요. 우리가 트로그에게 신경 쓰고 있는 동안 트롤들이 쳐들어오는 일이 있어서는 안 돼요. 가서 어린 트롤을 몇 녀석 처치해 주세요. 우리가 여전히 경계를 늦추고 있지 않다는 사실을 알려줘야 해요."}, {"어린 서리갈기 트롤 10마리를 처치해야 합니다."}},
[183] = {"멧돼지 사냥", {"멧돼지 사냥처럼 재미있는 일도 없을 거야, 안 그런가?","하지만 이곳 눈마루 골짜기에는 멧돼지가 너무 많아서 사냥하는 재미가 떨어진단 말이야. 굳이 성질을 돋우지 않아도 무턱대고 달려들어서 요즘 같아선 그냥 외출하기도 힘들 정도라네.","그래서 말인데 자네가 멧돼지들을 좀 처리해 줬으면 하네. 내 꼭 사례함세."}, {"탈린 킨아이가 작은 멧돼지 12마리를 처치해 달라고 했습니다."}},
[184] = {"펄브라우의 땅문서", {"이 땅문서는 서부 몰락지대의 어느 농지에 대한 등기 권리증입니다. 이 땅문서에는 시어도어 펄브라우와 그의 아내, 베르나의 서명이 있습니다. 그리고 뒷면에는 다음과 같은 내용이 급히 휘갈겨 쓰여 있습니다.","\"우리는 펄브라우를 위협해 놈에게서 땅문서를 받아 냈다. 이곳 땅 중 일부를 손에 넣으려면 이 방법이 가장 편리할 거라 생각됐다.","펄브라우 일가는 이제 우리에게 문제를 일으키지 않을 것이다. 마지막으로 그들을 본 게 서부 몰락지대를 떠나는 길에 마차가 고장 나 꼼짝 못하는 모습이었다.\"","펄브라우 일가는 이 땅문서를 되찾고 싶어할 것입니다."}, {"펄브라우의 땅문서를 서부 몰락지대의 얀센 농장으로 가져가야 합니다."}},
[185] = {"호랑이 사냥하기", {"제가 아이였을 때 아버지는 제게 호랑이 사냥 기술을 가르치셨죠.","어린 고양잇과 동물은 추적하거나 사냥하기가 훨씬 쉽답니다. 그러니 쉬운 사냥감부터 시작해보도록 하죠. 야영지 근처에서는 새끼 호랑이를 종종 볼 수 있어요.","호랑이를 추적해서 몇 마리 사냥할 수 있을지 스스로 한번 시험해 보도록 하세요. 남쪽에 있는 야영지 근처에서 사냥을 시작하시면 돼요."}, {"새끼 가시덤불 호랑이 10마리를 처치해야 합니다."}},
[186] = {"호랑이 추적하기", {"어린 호랑이와 어미 호랑이는 완전히 다른 존재입니다. 가시덤불의 어미 호랑이를 사냥하여 당신의 능력을 증명해 보세요.","어미 호랑이는 강 동쪽에 있는 쿠르젠 주둔지의 남쪽에 주로 서식합니다."}, {"가시덤불 호랑이 10마리를 처치해야 합니다."}},
[187] = {"호랑이 사냥의 고수", {"덩치 큰 가시덤불 호랑이는 호랑이 중에서도 가장 힘센 녀석들입니다. 수많은 <race> <class>|1이;가; 이들의 칼처럼 날카로운 이빨과 커다란 발톱에 목숨을 잃었죠.","당신이 놈들을 처치했다는 걸 알면 아제크도 기뻐할 겁니다. 가장 큰 호랑이 무리는 남쪽에 있는 가시덤불 곶과의 지역 경계선인 세계의 분리 근처에 있습니다."}, {"덩치 큰 가시덤불 호랑이 5마리를 처치해야 합니다."}},
[188] = {"호랑이 사냥의 달인", {"너무나도 강력하고 거대해 다 큰 남성도 왜소해 보이게 만든다는 전설의 암호랑이가 있습니다. 네싱워리 원정대의 사냥꾼들이 단 한 번도 사냥하지 못한 녀석이죠. 많은 이들이 이 호랑이의 날카로운 발톱에 희생됐고, 지금은 이 호랑이가 머무는 그롬골 주둔지 북쪽의 언덕 아래에서 썩어가고 있습니다.","이 야수의 이름은 신달이라고 합니다.","이제 지금, 당신은 그 어떤 <race>도 성공하지 못한 위업을 이루고자 합니다. 바로 신달을 사냥하고, 그 증거로 놈의 앞발을 획득하는 일입니다."}, {"신달을 처치하고 그 앞발을 가져가야 합니다."}},
[189] = {"붉은머리 부족의 귀", {"괘씸한 트롤들 같으니! 저 트롤놈들이 계속 정글에서 우리 대원들을 해치고 있어요! 이미 놈들과의 전투로 상당수의 훌륭한 경비병들을 잃은데다가 희생자가 계속 늘어나고 있단 말이에요! 이걸 레빌가즈 남작님께 어떻게 설명해야 할지 모르겠어요.","단호한 조치를 취해야 하는데... 방법을 생각해 봐, 케복... 오늘 빈둥거리기나 하라고 일을 맡긴 건 아니잖아... 아! 당신은... <name>|1이;가; 아닌가요? 날 좀 도와주지 않을래요? 보상금이 꽤 많은 일이에요!","내게 붉은머리 트롤의 귀 15개를 가져오면 두둑이 보상해 드리죠."}, {"붉은머리의 귀 15개를 모아 무법항에 있는 케복에게 가져가야 합니다."}},
[190] = {"퓨마 사냥하기", {"헤멧 2세의 창단 아래 결성한 이 일류 원정대에 참가하려면 당신이 퓨마를 사냥할 만한 실력이 있는지 증명해 보이셔야 합니다. 우선 쉬운 것부터 시작할 테니 너무 걱정하지는 마십시오. 일단 새끼 퓨마 사냥에 집중하십시오. 나중에 사냥꾼으로서의 경험을 쌓게 되면, 반드시 더 위험한 사냥감을 찾게 될 테니까요.","일단 동쪽으로 가보는 걸로 사냥을 시작하십시오. 퓨마 사냥의 달인이 되면 돌아오시고요."}, {"새끼 퓨마 10마리를 처치해야 합니다."}},
[191] = {"퓨마 추적하기", {"가장 어린 퓨마들은 손쉽게 쓰러뜨릴 수 있었습니다. 하지만 퓨마가 조금 더 자라면 더 강하고 빨라지므로 사냥하기 어려워집니다.","퓨마의 흔적이 서쪽으로 나 있습니다."}, {"퓨마 10마리를 처치해야 합니다."}},
[192] = {"퓨마 사냥의 고수", {"이제 어려운 과제가 기다리고 있습니다. 퓨마는 나이를 먹으면 먹을수록 야생에 더 적응합니다. 주위 환경에 맞게 몸을 바꾸고 발견하기 힘들게 변하기도 하죠. 이 비열한 퓨마는 \"그림자퓨마\"라 불립니다. 어둠 속에서 날카로운 이빨을 드러내며 기다리다 갑자기 번쩍하고 튀어나오는 이들의 습성에서 따온 이름입니다.","만약 퓨마 사냥꾼으로서 자신을 증명하겠다면, 그림자퓨마 사냥이 유일한 방법일 겁니다. 이들은 강 건너 남동쪽을 돌아다닙니다."}, {"그림자퓨마 5마리를 처치해야 합니다."}},
[193] = {"퓨마 사냥의 달인", {"당신은 자신이 퓨마 사냥의 고수라는 걸 어느 정도 증명했습니다. 하지만 아직 당신의 손아귀를 교묘하게 벗어난 퓨마가 있습니다.","바그테라라는 이름을 가진 이 무적의 퓨마는 자신에 대한 노래가 불릴 정도인데, 이 거대한 야수와 마주하고 나면 당신도 그 이유를 알게 될 겁니다.","얼가딘 경에게 바그테라의 송곳니를 가져가면 진정한 퓨마 사냥의 달인으로 인정받을 수 있을 것입니다."}, {"바그테라의 송곳니를 획득해야 합니다."}},
[194] = {"랩터 사냥하기", {"자신이 최고의 사냥 기술을 가졌다고 생각하십니까? 흠, 금방 시험해 볼 수 있죠. 정글로 가서 가시덤불랩터 10마리를 처치하십시오. 당신이 얼마나 대단한 사냥꾼인지 지켜보겠습니다.","놈들을 어디서 찾을 수 있는지는 가르쳐 드리지 않겠습니다. 놈들을 찾아내는 게 이 일의 절반에 해당되니까요.","뭐, 좋아요. 조금만 가르쳐 드리죠. 서쪽으로 가십시오."}, {"가시덤불랩터 10마리를 처치해야 합니다."}},
[195] = {"랩터 추적하기", {"다음 단계로 넘어갈 때입니다. 정글로 가서 저 지독한 채찍꼬리랩터 10마리를 처치하십시오. 전에 상대했던 랩터보다는 훨씬 더 강한 녀석들일 겁니다.","채찍꼬리랩터는 여기서 남동쪽에 있는 칼아이 폐허 근처에 주로 서식합니다."}, {"채찍꼬리랩터 10마리를 처치해야 합니다."}},
[196] = {"랩터 사냥의 고수", {"강 건너 남쪽에는 줄무늬정글랩터라는, 좀처럼 정체를 드러내지 않는 독특한 종류의 랩터가 있습니다. 지금껏 당신이 사냥을 시도했던 놈들보다 훨씬 더 추적하거나 사냥하기 어려운 놈들입니다.","줄무늬정글랩터를 처치한다면 두고두고 자랑할 좋은 이야깃거리가 될 겁니다."}, {"줄무늬정글랩터 10마리를 처치해야 합니다."}},
[197] = {"랩터 사냥의 달인", {"네싱워리 원정대로 복귀하기 전에 모두가 불가능하다 믿는 일에 도전하십시오. 바로 그 어떤 사냥꾼도 처치하지 못한 랩터를 사냥하는 겁니다. 그 괴물의 이름은 테디스라 하는데, 북부 가시덤불의 남동쪽 지역을 공포로 몰아넣고 있는 녀석입니다.","테디스를 처치하고 놈의 발톱을 드워프 사냥꾼에게 가져간다면 당신이 평범한 사냥꾼이 아니라는 걸 증명하는 셈이 될 겁니다."}, {"테디스를 처치해야 합니다."}},
[198] = {"병사 토르센을 위한 보급품", {"흠, 사실 당신에게 부탁할 일이 있긴 한데요. 저 북쪽에 있는 부관 도렌 님과 그의 부하들이 우리가 보내 줄 정기 보급품을 기다리고 있어요.","트롤 녀석들의 공격 때문에 아직 보내지 못했는데 누군가 맡아 줄 이가 필요해요. 도렌 님의 야영지는 길을 따라 북서쪽으로 그늘숲에서 가시덤불 골짜기로 들어오는 입구에 있어요. 보급품을 병사 토르센에게 전해주세요. 만약 토르센이 야영지에 없다면 거기서 남쪽을 정찰하고 있을 거예요. 당신이 보급품을 가져다주면 그가 보급품을 관리할 거예요.","실패할 경우에는 보급품을 모두 변상해야 한다는 사실을 잊지마세요."}, {"병사 토르센에게 고블린의 잡다한 보급품을 전달해야 합니다."}},
[199] = {"어둠의 전조", {"이상한 향기가 나는 가루의 견본을 얻었습니다."}, {"선임기술자 힌더바이르에게 돌아가 발견한 단서를 보여 주어야 합니다."}},
[200] = {"사서 헤로드", {"쿠르젠이 토르센의 비밀에 대해 알고 있다면 분명 우리 중에 첩자가 있는 거요. 반드시 이 비밀이 어떻게 새어 나갔는지 밝혀내야 하오!","쿠르젠의 야영지 내에는 사서 헤로드라는 자가 토르센의 연락책이었소. 헤로드는 쿠르젠의 병참장교로서 쿠르젠 주둔지의 모든 운영 상황에 대해 알고 있소. 당신이 헤로드의 쪽지나 기록을 가져온다면 토르센이 발각된 경위를 알아낼 수 있을 거요.","우리가 쿠르젠의 부대에 속해 있을 당시 그 사서의 사무실은 야영지 내의 유일한 2층짜리 건물의 맨 위층에 있었소. 헤로드의 기록은 아마 아직 거기에 있을 것이오."}, {"사서 헤로드의 기록을 찾아야 합니다."}},
[201] = {"야영지 조사", {"투자개발회사의 채광 기지가 있는 나즈페리티 호수에서 강기슭을 따라 올라가면 한 사냥꾼 무리가 설치한 야영지가 있다는 보고를 받았어요. 보통은 이런 일에 신경 쓰지 않지만, 놀랍게도 그 사냥꾼들 중에는 헤멧 네싱워리 2세도 끼어 있다 하더군요. 그자는 주의해야 할 인물이에요. 그자는 여기저기 연결되어 있는 자인데다가, 당신도 아주 안전할 수만은 없을 거예요.","당신이 그자들의 야영지를 조사해서 무슨 일을 벌이고 있는지 좀 알아봐 줄래요?"}, {"사냥꾼 야영지를 찾은 다음 무법항에 있는 크라젝에게 그 위치를 보고해야 합니다."}},
[202] = {"대령 쿠르젠", {"당신은 매우 놀라운 능력을 자주 선보이는군. 아마 당신의 능력은 쿠르젠과 비교해도 손색이 없을 것이오. 당신이 쿠르젠과 직접 겨뤄서 이긴다면 우리 반란군에겐 구세주와 같은 존재가 될 것이오!","그는 보급창 동굴 깊은 곳에 숨어서 부하들을 지휘하고 있소. 그 동굴로 들어가 호위병들을 물리치고 쿠르젠과 지휘부를 직접 상대해야 하오이다.","부탁하오. 당신은 나의 유일한 희망이며, 우리의 운명이 당신 손에 달려 있소."}, {"쿠르젠 정예병 6명, 쿠르젠 부대장 4명을 처치하고 그 증거로 쿠르젠의 머리카락을 반란군 야영지에 있는 부관 도렌에게 가져가야 합니다."}},
[203] = {"제 2의 반란", {"우린 대령 쿠르젠과의 전투에서 밀리고 있습니다. 그는 뛰어난 전술가일 뿐 아니라 수적으로도 우리보다 열 배는 우세합니다! 살아남으려면 그의 위장 전술을 역으로 이용해야 합니다.","동쪽에 있는 그의 야영지로 가서 부하들을 처치하시기 바랍니다. 그리고 쿠르젠의 지원군이 오기 전에 빠져나와야 합니다."}, {"쿠르젠 밀림전사 15명을 처치해야 합니다.","반란군 야영지에 있는 하사관 요화에게 돌아가십시오."}},
[204] = {"나쁜 약", {"쿠르젠군의 의료 지원을 무력화하면 그들을 물리칠 기회를 얻을지도 모릅니다. 그는 주위에 수많은 약사를 두고는 밀림 치료제로 자신의 병사들을 치료하고 독고사리 즙으로 우리 병사들을 독살하고 있습니다.","상처 입은 우리 병사들을 치료하려면 그 밀림 치료제가 필요합니다. 또 직접 치료법을 개발하려면 연구에 필요한 독고사리 즙 견본도 있어야 함은 물론입니다. 밀림 치료제는 그의 약사들이나 밀림전사들이 가지고 있고, 독고사리 즙은 야영지의 보급품 상자 중 어딘가에 들어 있을 것이니 쿠르젠 주둔지에 가서 그 두 가지를 가져오십시오."}, {"반란군 야영지에 있는 하사관 요화에게 밀림 치료제 7개와 독고사리 즙 1개를 가져가야 합니다."}},
[205] = {"트롤의 마법", {"<형제/자매>께서 입수한 전설들을 연구할 동안 사악한 트롤의 마법 도구를 모아 주셨으면 합니다. 남쪽 먼 곳에 두 번째 트롤 부족인 백골가루 부족의 땅으로 가십시오. 그리고 그들 부족의 의술사들과 비술사들에게서 백골가루 우상을 구해서 가져오도록 하시기 바랍니다."}, {"반란군 야영지에 있는 수사 니메츠에게 백골가루 우상 4개를 가져가야 합니다."}},
[206] = {"마이조스", {"쿠르젠이 죽었을지 모르지만 그를 악의 세력으로 몰아넣은 자는 아직 살아 있습니다! 이번 연구를 통해 몇 가지 알아낸 것이 있는데 가시덤불 골짜기 동쪽 산 내의 오우거 소굴에 마이조스라는 오우거 마법사가 살고 있습니다. 마이조스는 정신을 지배하는 마법을 사용한다고 알려져 있는데, 나는 그 오우거가 쿠르젠을 타락시켰다고 확신하고 있습니다.","마이조스를 살려두면 다른 이들에게까지 악한 영향력을 행사할 테고, 제2의 쿠르젠이 나타날 수도 있을 겁니다.","그러니 녀석을 처치하고 녀석이 다른 이를 타락시키는 데 사용하는 마음의 눈을 가져오십시오."}, {"반란군 야영지에 있는 수사 니메츠에게 마음의 눈을 가져가야 합니다."}},
[207] = {"쿠르젠의 비밀", {"대령 쿠르젠은 한때 훌륭하고 고귀한 존재였습니다. 그랬던 그가 왜 타락해 버렸는지 반드시 알아내야 합니다! 어쩌면 트롤의 마법에 조종당하고 있는지도 모르겠습니다. 그걸 확인하려면 그들의 전설에 대해 연구해 볼 필요가 있습이다.","가시덤불 골짜기의 트롤 폐허에는 서판이 네 개 숨겨져 있습니다. 그 서판에 쓰인 내용을 확인해야 하니 서판의 내용을 필사하여 가져오시기 바랍니다.","첫 번째 서판은 발랄 폐허에 있고, 두 번째는 썩은내 산호초에 그리고 세 번째와 네 번째 서판은 줄쿤다의 폐허에 있습니다."}, {"4개의 서판을 찾아 거기에 기록된 전설을 수사 니메츠에게 가져가야 합니다."}},
[208] = {"나 이런 사냥꾼이야!", {"당신은 우리 동료로서 손색없는 사냥꾼입니다. 로데론의 전장에서 깊은 정글에 이르기까지, 그 어느 곳에서도 당신만큼 무기를 잘 다루는 이를 본 적이 없습니다.","결국은 그 사나운 신달과 잡기 힘든 바그테라, 그리고 종잡을 수 없는 테디스까지 처치하셨네요. 하지만 그 무엇보다 야수왕 뱅갈라시의 머리를 베어야 진정한 목표를 달성했다고 할 수 있습니다. 저도 그 하얀 호랑이 때문에 이곳에 왔지만, 녀석은 겁도 없이 남쪽에서 저를 도발하고 있어요. 야수왕 뱅갈라시를 처치한다면 당신의 사냥 솜씨가 그 누구보다 뛰어나다는 것을 입증하는 셈이 될 겁니다."}, {"거대한 백색 호랑이인 야수왕 뱅갈라시를 처치하고 그 머리를 얻어야 합니다."}},
[209] = {"백골가루 엄니", {"남해의 섬에 수출할 호랑이 송곳니를 충분히 공급할 수 있을 만큼 호랑이를 사냥하는 것은 어렵기도 하고 비용도 만만치 않아요. 다행히 우리는 평범한 뿔 모양의 물건으로 감쪽같이 모조품을 만드는 기술을 개발했죠.","우리가 발견한 가장 비슷한 재료는 놀랍게도 백골가루 트롤의 엄니였어요.","이 일에 대해 왈가왈부하지 마세요. 사는 사람들은 아예 이 사실을 모르는 게 약이니까요. 일단 산 후에는 그저 잘 쓸 수만 있으면 되는 것 아니겠어요? 이번 달 수출 물량을 맞출 수 있도록 백골가루 트롤의 엄니를 많이 가져다주세요!"}, {"백골가루 엄니 18개를 모아 케복에게 가져가야 합니다."}},
[210] = {"크라젝의 요리 냄비", {"이 야영지에서 요리는 거의 내 몫이랍니다. 무법항의 고블린 상인에게서 요리 냄비 기계를 구입하기 전까지 요리는 상당한 중노동이었지요. 정말 놀라운 냄비랍니다! 시간도 엄청나게 절약됐었지요... 그런데 그만 망가뜨리고 말았습니다.","당신이 나 대신 이 냄비를 고블린에게 가져가 수리해 주시지 않겠습니까? 그의 이름은 크라젝입니다."}, {"고블린 크라젝에게 크라젝의 고물 냄비를 가져가야 합니다."}},
[211] = {"리치의 최후", {"이제 안돌할을 공격해 그곳을 지배하는 리치를 몰아낼 때가 왔소!","도시의 폐허 안쪽에 권력을 잡고 있는 소환사 아라즈라는 리치가 있소. 도시 가운데 유적들 주위에는 리치를 경호하는 스컬지들이 깔려 있고, 그의 직속 수행원들은 정말 위협적인 존재들이라오. 친구들을 많이 데리고 가야 그와 대등하게 맞설 기회를 잡을 수 있을 거요.","<name>, 가서 리치를 처치하고 그 증거로 아라즈의 성물함 조각을 가져오도록 하시오!"}, {"서부 역병지대의 서리바람 야영지에 있는 사령관 아쉬람 발러피스트에게 아라즈의 성물함 조각을 가져가야 합니다."}},
[212] = {"식은 음식", {"저의 가장 유명한 요리 중 하나는 라임 드레싱을 곁들인 얼린 바실리스크 요리입니다. 그런데 문제는... 바실리스크 고기는 신선하게 유지하는 것이 불가능한데 오늘 저녁 왕의 만찬에 이 요리를 올려야 한다는 겁니다!","그러니까 얼음눈바실리스크를 죽이고 그 즉시 얼린 바실리스크 다리를 빨리 가져다주시면 왕의 만찬으로 보답하겠습니다!","얼음눈바실리스크는 북서쪽 가시덤불 골짜기의 해변 모래 언덕을 따라 돌아다닌다고 합니다. 그러니까 여행을 좀 해야 할 겁니다. 서두르세요!"}, {"얼음눈바실리스크를 처치하고 얼린 바실리스크 다리를 얻은 다음 고독한 은둔자 선술집에 있는 앵거스 스턴에게 가져가야 합니다."}},
[213] = {"적대적 탈취", {"흠... 겔리즈가 무역왕들이 임명한 거물들을 힘으로 밀어붙였다는 나쁜 소식이에요. 이보다 나쁜 소식이 어디 있겠어요? 결국 놈이 일을 내고 말았다고요! 놈은 이제 가장 악랄한 해적이 되려 하고 있으니!","레빌가즈 님은 이 일을 직접 처리하려 하시지 않고 나더러 문제를 해결하라고 지시하셨어요. 내 방식대로 해결하라니... 내 방식이라면 도둑질밖에 없는데... 나즈페리티 호수 근처에 있는 투자개발회사 지질학자들이 광산에서 발견되고 있는 이상한 파란 수정에 큰 관심을 보이고 있다고 하더군요. 그 지질학자들에게서 수정 견본을 구해다 주세요. 뭐, 어떻게 구하든 그 방법은 알아서 하세요."}, {"연마된 수정 8개를 모아 무법항에 있는 케복에게 가져가야 합니다."}},
[214] = {"붉은 비단 복면", {"이미 알려진 바대로 붉은 복면은 데피아즈단의 상징입니다. 얼마 전 복면의 재질이 놈들의 계급을 나타낸다는 사실을 새로이 알아냈습니다.","자, 이 사실을 알려드렸으니 데피아즈단원 고위 계급자들을 최대한 많이 처치해 주시기 바랍니다. 틀림없이 밴클리프의 비밀 은신처에 가면 최고위 계급 단원들을 찾을 수 있을 겁니다. 놈들을 처치하고 증거물로 붉은 비단 복면 10개를 가져다주시면 보상해 드리도록 하겠습니다. 행운을 빕니다."}, {"감시의 언덕 탑의 정찰병 리엘이 붉은 비단 복면 10개를 가져다 달라고 했습니다."}},
[215] = {"정글의 비밀", {"날 구해 줘서 고맙습니다. 좀 의아해 보이는 모양인데 설명해 드리도록 하지요.","도렌 부관님께서 내게 쿠르젠의 부대에 잠입하여 놈들을 염탐하고 반란군에게 꼭 필요한 정보를 가져오라고 명하셨습니다. 그래서 쿠르젠 주둔지에 침투해 들어갈 기회를 엿보다가 지금이 기회다 싶어 실행에 옮기고 있던 참이었습니다. 그런데 내가 또 잘못 생각했던 모양입니다. 다시 한번 구해 주어 고맙습니다.","이 임무는 일급비밀이니 누구에게도 말해선 안 됩니다. 하지만 도렌 부관님을 만나 보면 설명해 주실 겁니다."}, {"반란군 야영지에 있는 부관 도렌과 대화해야 합니다."}},
[216] = {"엉겅퀴 마을 침공", {"괘씸한 엉겅퀴 펄볼그들이 우리 앞길을 막고 있소이다! 그러니까 서쪽에 있는 엉겅퀴 마을이 토막나무 주둔지로 가는 길을 가로막고 있소. 아스트라나르 마을을 우회하는 경로이므로 그 길을 잃으면 얼라이언스에 확장로를 넘겨주는 꼴이 되지.","그런 일은 절대 있을 수도, 있어서도 안 되는 일이오!","엉겅퀴 마을로 가서 펄볼그들의 머릿수를 줄이시오. 이 임무를 마치면 충분한 보상이 주어질 것이오. 하지만 호드에 대한 당신의 의무를 다하는 것이 무엇보다 중요하오!"}, {"헬스크림 감시초소에 있는 카랑 아마카르가 엉겅퀴 마을에서 엉겅퀴 펄볼그 15마리를 처치해 달라고 했습니다."}},
[217] = {"결정적인 공격", {"제 계획이 성공했어요. 모단 호수에 있는 트로그들은 그로머그라는 우두머리의 명령을 따르는 것 같아요. 그렇지만 이 녀석 옆에는 내셔와 브롤러라는 우수한 경호원 둘이 항상 붙어 다녀서 매우 철저하게 경호를 하고 있더군요.","가능하다면 동굴 안에 있는 그로머그와 두 경비병의 암살 임무를 맡아 주세요. 어서 가세요! 저는 이것들을 풀어내고 곧 당신을 다시 만날게요."}, {"트로그의 우두머리인 그로머그와 그의 경호원인 내셔와 브롤러를 처치하고 텔사마에 있는 경비대장 루겔퍼스에게 보고해야 합니다."}},
[218] = {"얼음과 불", {"소렌이 당신에게 정찰 임무를 맡겨 정말 다행이군요. 덕분에 좋은 정보를 알게 되었어요.","하지만 우리가 할 일은 늘어났네요. 혼란에 빠져 지금은 정신을 차리지 못하고 있지만, 트롤들은 여전히 눈마루 골짜기에서 우리 드워프들을 쫓아내고 싶어 해요. 그리고 그 불붙은 악마 한 마리는 우리 골짜기에 쌓인 눈을 모두 녹여버릴 태세지요.","자, 지금 당신 도움이 필요해요. 남동쪽 동굴에 살고 있는 트롤 지도자와 하인들, 그리고 불의 정령을 모두 처치해 주세요."}, {"냉혈한 그리크니르, 서리갈기 검사 6명, 서리갈기 수습마법사 3명, 난폭한 불의 정령을 처치해야 합니다."}},
[219] = {"교전 중 실종", {"드디어 얼라이언스 동지를 만나게 되었군!","제발... 나 좀 도와 주게...","사악한 오크들이 우리 연대를 제압하여 대부분의 병사들이 목숨을 잃었네. 난 머리에 큰 충격을 입고 그 자리에서 의식을 잃고 말았지. 깨어나니 이 동굴에 갇혀 있더군. 아직 상처가 심해 탈출할 수가 없다네. 제발... 날 좀 도와주게.","어서 마을로 되돌아가 치료를 받아야 해... 제발... 자네가 유일한 희망일세..."}, {"하사관 키샨이 붉은마루 산맥으로 돌아갈 수 있도록 보호해야 합니다."}},
[220] = {"물의 부름", {"이슬렌이 한 가지 임무를 더 준 다음 토템을 상으로 줄 거예요. 그게 어떤 임무든 내가 주는 물병이 꼭 필요할 거예요.","이제 그녀를 찾아가세요. 우린 언젠가 다시 만나게 될 거예요."}, {"불모의 땅에 있는 이슬렌 워터시어에게 가득 찬 순수한 물을 가져가야 합니다."}},
[221] = {"숲 속의 늑대인간", {"숲 속에서 파멸어둠 흑마술사와 함께 돌아다니는 큰 늑대인간을 보았겠지요? 우리가 확인한 바로는 늑대인간 무리 중 다수가 이 암흑길잡이들인 것 같습니다.","놈들이 마을 남서쪽의 오염된 과수원을 점령해 버렸다는 사실도 확인했습니다. 이 늑대인간 무리는 지난번에 당신이 상대한 놈들보다 좀 더 강할 겁니다.","조심하세요."}, {"어둠골의 칼로를 위해 파멸어둠 암흑길잡이 12마리를 처치해야 합니다."}},
[222] = {"숲 속의 늑대인간", {"지금까지의 당신의 임무 수행 과정으로 볼 때, 숲에 출몰하고 있는 늑대인간들 중 가장 사나운 놈을 상대해도 될 것 같군요.","이곳에 새로 나타난 늑대인간 중에는 송곳니투사와 타락전사가 가장 위험한 것으로 밝혀졌습니다. 놈들은 동굴 근처나 남쪽의 광산 안에 살고 있습니다.","먼 곳에서도 놈들이 피우는 모닥불 불빛을 볼 수 있을 겁니다."}, {"어둠골의 칼로를 위해 파멸어둠 송곳니투사 8마리와 파멸어둠 타락전사 8마리를 처치해야 합니다."}},
[223] = {"숲 속의 늑대인간", {"여기 있습니다. 이 편지를 대장 카레빈께 가져가십시오.","<재빨리 색 바랜 양피지 문서를 건네주는 칼로>","당신 같은 사람이 몇 명만 더 있으면 수적으로 어둠의 순찰대를 능가할 수 있을 겁니다! 그러면 아마 지금 몇 안 되는 인원으로 힘겹게 수행하고 있는 이 임무를 수월하게 완수할 수 있을 것입니다."}, {"조나단 카레빈에게 칼로의 쪽지를 가져가야 합니다."}},
[224] = {"왕국 수호", {"폐하의 이름으로 수많은 병사들이 최전방에서 치열한 전투를 벌이고 있소. 게다가 트로그놈들이 틈만 보이면 굴을 파고 쏟아져 나오는 상황이오. 트로그는 이미 아이언포지의 안녕을 위협하는 심각한 골칫거리가 되었소이다.","트로그의 위협을 막으려면 당신과 같은 용감한 모험가의 도움이 필요하다오. 가루바위 트로그 6마리와 가루바위 정찰병 6마리를 처치하고 돌아와 보고해 주시오. 트로그들은 남쪽에 산으로 들어가는 길 주변에서 찾을 수 있을 것이오."}, {"남쪽 경비탑의 산악경비대 코블플린트가 가루바위 트로그와 가루바위 정찰병을 각각 6마리씩 처치해 달라고 했습니다."}},
[225] = {"오래된 무덤", {"이 오래된 무덤의 비석에는 다음과 같은 글이 새겨져 있습니다.","모건 래디모어","<좀 더 자세히 살펴보면 무덤 위의 흙이 최근 파헤쳐졌으며 상당한 양의 흙이 옮겨졌다는 사실을 확인할 수 있습니다...>"}, {"어둠골의 시라 본인디와 대화해야 합니다."}},
[226] = {"늑대의 위협", {"스벤과 저는 동쪽에 있는 강령술사들의 공격으로 하루하루가 위험의 연속이랍니다. 게다가 이곳에는 우리 둘밖에 없기 때문에 먹고살려면 직접 사냥을 해야 하는 형편이지요. 그런데 짐승의 고기나 물고기를 가지고 야영지로 돌아올 때마다 굶주린 그늘숲늑대와 광포한 그늘숲늑대가 숲에서 튀어나와 마치 고기를 내 놓으라는 듯 위협한답니다. 어쩌겠어요, 살려면 고기를 내줄 수밖에요. 이곳에서 지내는 게 이렇게 위험하답니다.","저희를 위해 늑대를 몇 마리 없애 준다면 이곳에서 훨씬 편하게 생활할 수 있을 겁니다. 늑대들은 주로 이곳의 북쪽과 동쪽 강 근처에 자주 출몰합니다."}, {"굶주린 그늘숲늑대 12마리와 광포한 그늘숲늑대 8마리를 처치한 다음 그늘숲의 서쪽 변경의 스벤의 야영지에 있는 라스에게 가야 합니다."}},
[227] = {"모건 래디모어", {"<책장 선반에서 가죽 표지의 책을 찾아내며>","더 자세히 알고 싶다면 알시아님께 물어보십시오. 최근 그의... 문제를... 처리한 게 알시아님이니 말입니다."}, {"다크샤이어의 사령관 알시아 이본로크와 대화해야 합니다."}},
[228] = {"모르라딤", {"우리 정찰병의 보고에 따르면 모르라딤은 묘지 옆의 낯설고 굽은 길을 따라 그늘숲 여기저기를 돌아다니고 있다고 하오. 우린 그를 언덕 위의 집을 지나서 묻었는데, 당신 그 집이 어디 있는지 알고 있소?","행운을 빌어 주는 것밖에는 내가 도울 수 있는 일이 없는 것 같군."}, {"모르라딤을 처치한 다음 그의 해골을 어둠골의 사령관 알시아 이본로크에게 가져가야 합니다."}},
[229] = {"살아 남은 딸", {"전에 이 얘기도 했어야 했는데... 흠, 어떻게 얘기해야 하나...","모건 래디모어는 자기 가족이 모두 죽었다고 생각했었는지 모르지만, 실은 사라 래디모어라고 그의 딸이 지금 순찰대원으로 일하고 있소. 그녀는 항상 아버지의 죽음...과 관련된 일들 때문에... 많은 어려움을 겪어 왔지. 그러니 당신이 가서 이 소식을 알려 주는 게 좋을 것 같소."}, {"다크샤이어의 순찰대원 사라 래디모어와 대화해야 합니다."}},
[230] = {"스벤의 야영지", {"흙무더기를 파 보니 흙으로 뒤덮인 작은 책을 발견했습니다.","책에 제목은 없지만 스벤이 알고 싶어하는 정보가 들어 있습니다."}, {"스벤에게 스벤 농장의 책을 가져가야 합니다."}},
[231] = {"딸의 사랑", {"여기 이걸 가져다가 아버지의 무덤에 놓아 주세요. 제가 무사하다는 것과 지금까지 있었던 일들에 대해서 아무도 아버지를 원망하지 않는다는 걸 아시도록 말이에요.","<눈에 눈물이 고인 채로 손가락에서 반지를 빼내 손에 건네주며>","그리고... 감사합니다."}, {"사라 래디모어의 반지를 모건 래디모어의 무덤으로 가져가야 합니다."}},
[232] = {"연금술사 진게의 심부름", {"왕립 연금술 학회에서 당신의 도움을 필요로 하고 있습니다. 내 계획대로라면 우리 둘 다 득을 볼 수 있을 것이니 잘 들으세요.","옆방에 있는 연금술사 키버는 좀... 정신이 이상...할진 몰라도 그의 연구는 똑똑한 연구자의 손에 맡기면 좋은 결과를 얻을 수 있을 겁니다. 다행히도 내가 바로 그런 연구자랍니다. 이 구입주문서를 푸른달 골동품점의 주인, 알렉산드로 루카에게 가져다주세요. 그러면 그가 분석 도구를 하나 줄 것이니 그걸 받으면 내게 가져오세요. 그러면 그때 당신의 임무를 더 자세히 설명해 주도록 하겠습니다."}, {"언더시티에 있는 알렉산드로 루카에게 진게의 구입주문서를 가져가야 합니다."}},
[233] = {"눈마루 골짜기 편지 배달", {"흠... 이 부탁을 들어줄지는 모르겠지만. 시간이 없어서 오늘 들어온 편지 뭉치를 배달 못했네.","어디 보자, 받는 사람이 탈린 킨아이 앞으로 되어 있군. 탈린 킨아이는 여기서 서쪽 길을 따라 내려가다 보면 나오는 얼어붙은 호수 옆에서 야영을 하고 있을 걸세.","나 대신 편지를 좀 배달해주겠나?"}, {"눈마루 골짜기에 있는 탈린 킨아이에게 편지 뭉치를 전달해야 합니다."}},
[234] = {"눈마루 골짜기 편지 배달", {"내 기억이 맞는다면 그렐린의 야영지는 여기서 남동쪽 방향으로 가면 있을 걸세. 아마 이 편지를 몹시도 기다리고 있을 테니 어서 가보게나."}, {"눈마루 골짜기에 있는 그렐린 화이트비어드에게 편지를 전달해야 합니다."}},
[235] = {"잿빛 골짜기 사냥 대회", {"젊은 모험가들이여, 내 말을 잘 들으시오! 잿빛 골짜기의 야생이 당신을 기다리고 있소!","우리 호드는 불모의 땅 북쪽에 튼튼한 기지를 세웠소. 토막나무 주둔지와 조람 해안 주둔지는 호드의 이름을 빛내려고 고군분투하고 있소! 자신을 증명하고 싶은 이는 모두 그곳으로 가서 길을 찾으시오. 불모의 땅 바로 북쪽에 있는 토막나무 주둔지의 세나니 썬더하트가 잿빛 골짜기 사냥 대회에 참가할 모험가를 모집하고 있다오!"}, {"잿빛 골짜기 토막나무 주둔지에 있는 세나니 썬더하트와 대화해야 합니다."}},
[237] = {"왕국 수호", {"산악경비대 코블플린트가 당신에 대해 입에 침이 마르도록 칭찬하더군, <class> 친구. 그럼 당신을 믿고 아주 중대한 임무를 맡기도록 하겠소. 드워프 형제들이 얼라이언스 최전선으로부터 돌아올 때까지 침략자 트로그들을 계속 밀어붙여야 하오.","남쪽 언덕으로 가서 가루바위 망치잡이 10마리와 가루바위 점쟁이 10마리를 처치해 주시오. 그러면 얼마간의 시간을 벌 수 있을 테니까 말이오. 임무를 완수하면 내게 돌아와 알려 주시오."}, {"남쪽 경비탑의 산악경비대 그래블고가 가루바위 망치잡이와 가루바위 점쟁이를 각각 10마리씩 처치한 후 그에게 돌아와 보고해 달라고 했습니다."}},
[238] = {"연금술사 진게의 심부름", {"자, 분석 도구가 준비되었소이다. 분명 연금술사 진게님께서는 최대한 빨리 이걸 받고 싶어하실 것이오. 지체하지 말고 어서 가져다 드리고 이 도구의 비용은 학회의 계산서에 첨부하겠다고 전해 주시오. 아, 내 안부도 함께 전해 주구려.","자, 거기 그냥 그렇게 서 있지 말고 어서 가시오. 심부름꾼의 삶이란 항상 움직여 다녀야 하는 것 아니겠소?"}, {"언더시티에 있는 연금술사 진게에게 휴대용 분석 도구를 가져가야 합니다."}},
[239] = {"서쪽시내 주둔지 지원", {"서쪽 경계 지역의 주둔지에서 놀과 도적의 활동이 늘고 있다는 전갈을 보내왔습니다. 주둔지에서는 스톰윈드 군대를 보내 달라고 요청해 왔지만 우리도 병력이 부족한 형편이란 말입니다!","당신이 그들을 도와 준다면 고맙겠습니다. 서쪽시내 주둔지로 가서 부관 레이너를 만나 무엇이 필요한지 알아봐 주십시오.","주둔지로 가려면 서쪽 길을 따라 내려가면 됩니다. 작은 개울의 다리를 건넌 다음 오른쪽으로 가십시오."}, {"서쪽시내 주둔지로 가서 부관 레이너와 대화해야 합니다."}},
[240] = {"지터스에게 돌아가기", {"자, 그루얼식 게살 케이크가 완성되었습니다!"}, {"갈색 게살 케이크를 가지고 지터스에게 가야 합니다."}},
[243] = {"사막으로", {"자, 이제 당신은 타나리스 사막으로 나가서 그곳에 사는 바실리스크, 전갈, 그리고 하이에나의 근육 조직 일관성을 시험해 봐야 합니다. 조직 견본을 구해서 가져간 분석 도구를 이용해 보세요. 견본 시험은 여러 번 실패하게 될 겁니다. 각각의 짐승들에게서 얻은 쓸만한 견본이 9개씩 필요합니다.","그리고 이 개조된 도구를 사용하려면 가젯잔 고블린의 동력원이 필요하니까 선임기술자 빌지위즐에게 얘기해서 도구에 사용할 동력원을 하나 얻도록 하세요.","반드시 성공한 후 내게 돌아오세요."}, {"분석 도구에 사용할 동력원을 얻기 위해 가젯잔에 있는 선임기술자 빌지위즐과 대화해야 합니다."}},
[244] = {"놀 침략자", {"안녕하십니까. 전 이 도로를 순찰하는 임무를 맡고 있습니다. 아직까진 별 탈 없었는데 며칠 전 북쪽과 동쪽에서 놀 야영지를 목격했습니다.","놀이 병력을 소집하고 있다는 것을 호숫골에 알려야 합니다! 호숫골의 펠던님께 이 사실을 보고해 주시기 바랍니다. 대의원님께서 정찰병 임금 정도는 지급해 주실 테니 걱정하지 마시고요.","펠턴님은 주로 호숫골 다리의 남쪽 지역을 순찰하고 계십니다."}, {"붉은마루 산맥의 호숫골 남쪽 다리에 있는 부관 펠던에게 보고해야 합니다."}},
[245] = {"거미의 위협", {"안녕하십니까, <class>|1이여;여;. 서쪽 변방에 들끓는 거미들을 본 적 있습니까? 우리만으로 다리 여덟 달린 그 괴물들을 처치하기에는 버겁지만 그렇다고 해서 어둠골에서 멀리 떨어진 이곳까지 지원군을 보내기에는 사령관님께서 지휘하시는 병력이 턱없이 부족합니다.","이런 부탁을 해서 미안하지만, 놈들을 좀 처치해 주지 않으시겠습니까? 큰 보상을 약속할 수는 없지만, 그 고약한 벌레들을 처리해 준다면 사례는 분명히 밝히도록 하겠습니다.","아, 그리고 한 가지 충고하지요. 녀석들의 독액을 조심하시기 바랍니다."}, {"새끼 그물독거미 15마리를 처치한 후 그늘숲에 있는 순찰대원 도즈에게 보고해야 합니다."}},
[246] = {"탐색전", {"현재 따로 병력을 편성할 수 없는데다가 그 놀들을 그대로 둘 수도 없는 노릇이니 큰일이군.","붉은마루 산맥 남쪽에 있는 놀들의 정보를 수집해 주시오. 정보를 수집하는 동안 눈에 띄는 놈들을 처치하고 가능하면 숫자를 줄여서 적의 병력이 어느 정도이고 얼마나 강한지 보고해 주시오. 붉은마루 산맥 남쪽 길로 쭉 가면 야영 중인 놀들을 찾을 수 있을 것이오.","스톰윈드 군대를 위해 이 임무를 수행해 주면 보상하도록 하겠소."}, {"붉은마루 싸움꾼 10마리, 붉은마루 밀렵꾼 6마리를 처치하고 호숫골에 있는 부관 펠던에게 돌아가 보고해야 합니다."}},
[247] = {"사냥 대성공", nil, nil},
[248] = {"자세한 조사", {"아조라의 조각상을 통해 일갈라의 탑를 지켜보니 모건스가 돌망루 요새의 오크들과 싸우고 있더군. 싸움의 이유를 모르겠네만 자네가 이 비밀을 밝히는 걸 도와 주리라 믿네.","다른 아조라의 조각상을 가지고 돌망루 요새 옆에 있는 감시탑의 꼭대기로 가게나.","그곳에서 적합한 장소를 찾아 그 위에 조각상을 설치하면 된다네. 상자나 단지처럼 조각상이 쉽게 눈에 띄지 않는 장소라면 충분하네."}, {"붉은마루 산맥의 돌망루 요새 옆에 있는 감시탑에 올라가야 합니다.","그곳에서 아조라의 조각상을 넣어 두기에 적합한 용기를 찾아야 합니다."}},
[249] = {"모건스", {"아조라의 조각상을 통해 좀 더 감시한 결과 드디어 모건스의 계획을 알아냈네.","이미 발견했을지도 모르지만... 그는 엘룬의 낫을 찾고 있네! 모건스가 엘룬의 낫을 손에 넣었다면 붉은마루 산맥과 스톰윈드 전역에 어두운 힘이 뻗치게 될 걸세.","지금 당장 모건스를 막아야 하네만 불행히도 나는 그와 싸울 준비가 안 되었네. 그러니 자네가 일갈라의 탑으로 들어가 모건스를 상대해 주게나.","그를 쓰러뜨리고 모건스가 지니고 있는 그림자 펜던트를 가져다주게. 그러면 자네 손으로 스톰윈드 왕국을 구하게 될 걸세."}, {"모건스를 처치하십시오.","엘윈 숲의 아조라의 탑에 있는 테오크리투스에게 그림자 펜던트를 가져가야 합니다."}},
[250] = {"어둠의 전조", {"우리 댐의 안전을 걱정해 주는 이가 있다니 얼마나 반가운지 모르겠네. 처음엔 탄돌 교각이 파괴되더니 다음엔 던 모드르가 약탈당하고... 검은무쇠 드워프의 다음 목표는 분명 댐일 거야. 불행히도 내 상관인 감독관님을 포함해서 방어 병력 대부분이 얼라이언스 최전선에 배치되어 있는 상황이네.","그래서 자네의 도움이 절실히 필요해. 며칠 전 검은무쇠 공병들이 댐의 동쪽 사면을 굴착하는 것을 보았네. 그 지역을 정찰하고 뭔가 수상한 것이 있으면 가져다주게."}, {"댐의 동쪽 사면 근처를 정찰하고 검은무쇠 드워프의 음모를 밝힐만한 단서를 찾아 선임기술자 힌더바이르에게 가져가야 합니다."}},
[251] = {"에이버크롬비의 편지 번역", {"자네가 가져온 이 편지를 시라 본인디에게 가져가게. 그는 고대 언어와 예술에 능통한 친구지. 이 마을에서 이걸 번역할 수 있는 사람이 있다면 그 뿐일 걸세."}, {"시라 본인디에게 엘로에게 보내는 편지를 가져가야 합니다."}},
[252] = {"엘로 이본로크 경에게 편지 전달", {"오, 이런! 이 편지에 쓰인 내용이 사실이라면 에이버크롬비가 뭔가 대단히 사악한 짓을 저지른 게 틀림없습니다.","이 번역된 편지를 서둘러 시장님께 전해주시기 바랍니다. 벌써 우리 마을에 불길한 기운이 찾아들고 있는 것 같으니 서두르세요!"}, {"엘로 이본로크 경에게 번역된 편지를 가져가야 합니다."}},
[253] = {"장의사의 신부", {"아이들 사이에서 전해지는 이야기에는 장의사라는 인물이 등장하네. 그는 원래는 착한 연금술사였지만 아내 엘리자가 죽자 미쳐 버렸지. 그는 아내를 부활시키기 위해 흑마법을 사용하여 자기 심장을 죽은 아내의 몸에 넣었다네.","엘리자가 되살아나긴 했지만... 장의사는 저주를 받아 인육에 굶주려 하는 그녀를 다시 묻을 수밖에 없었다네.","그의 심장은 곧 그의 힘이지. 장의사의 아내 무덤에서 심장을 찾아 박동이 멎기 전에 내게 가져오게. 그러면 그의 힘을 빼앗을 수 있을 걸세."}, {"엘리자의 무덤을 찾아 그녀의 몸에서 장의사의 심장을 찾아 엘로 이본로크 경에게 가져가야 합니다."}},
[254] = {"무덤 파기", nil, nil},
[255] = {nil, {"야생 동물을 사냥하는 것도 잠시 뒤면 옛일이 될 것이오. 나는 더 큰 사냥감을 노리고 있소... 야생 동물보다도 더 큰 것 말이오. 정확히는 오우거가 되겠군.","이곳에 있는 드워프들은 북동쪽에 있는 모그로쉬 소굴에 대해 염려하고 있지만, 그 일을 감당하기에는 인력이 충분치 않다오. 만약 당신이 오우거 사냥에 동참하고 싶다면 마음 놓고 그 오우거들을 찾아 없애 주시오. 일을 마치고 나면 내게 알려 주시오 그러면 내가 집정관과 협의했던 보상을 나누어주겠소."}, {"모그로쉬 야영장에 있는 켈트 토마신이 모그로쉬 오우거 4마리, 모그로쉬 투사 4마리와 모그로쉬 집행자 4마리를 처치하기 위해 당신을 고용했습니다."}},
[256] = {"현상 수배: 초크술", {"텔사마 집정관의 명령:","오우거 우두머리인 초크술이 텔사마를 비롯해 돌다지 댐과 발굴 지역 공격을 지휘하고 있다고 한다. 모든 수단과 방법을 동원하여 그를 처치하라.","집정관에게 초크술을 처치한 증거를 가져오는 이에게는 포상금이 지급될 것이다.","초크술이 마지막으로 목격된 곳은 모단 호수 북동 지역 오우거 야영지였다."}, {"초크술을 처치하고 그 증거로 그의 머리카락을 텔사마의 집정관 블런트노즈에게 가져가야 합니다."}},
[257] = {"사냥꾼의 허세", {"당신이 이 용감한 다릴만큼 뛰어나다고 생각하나요? 어림없는 소리! 뭐, 그래도 도전은 언제나 환영이랍니다. 자, 그럼 내기 하나 할까요? 결국 당신이 능력 부족이라는 사실만 확인하게 되겠지만 말이죠. 실패하더라도 너무 상심하진 말아요.","이곳 모단 호수 지역엔 대머리수리 떼가 둥지를 틀고 있죠. 그걸 몇 마리 잡아와 보시는 게 어떨까요? 당신이 성공한다면 제 활이나 총을 드리죠.","<거만하게 쳐다보는 다릴>","뭐, 당신이 손해 볼 건 없겠죠?"}, {"산악대머리수리 8마리를 처치한 후 원정순찰대 오두막에 있는 풋내기 다릴에게 가야 합니다."}},
[258] = {"사냥꾼의 도전", {"또? 보아하니 첫 번째 도전을 통과했다고 신이 난 모양이군요. 하지만 그건 자랑삼아 떠벌릴 일도 못됩니다.","그럼 이번엔 산돼지 사냥을 한번 맡겨보도록 하겠습니다. 눈마루 골짜기 멧돼지와는 차원이 다를 테니 조심하는 게 좋을 겁니다.","실패하더라도 너무 상심할 필요는 없습니다. 행여나 성공해 돌아오면 입고 있는 셔츠라도 벗어 드릴 수 있습니다.","제 몸에 난 이 상처에 대해 얘기한 적 있던가요? 2년 전이었는데..."}, {"덩치 큰 산돼지 10마리를 처치한 후 원정순찰대 오두막에 있는 풋내기 다릴에게 가야 합니다."}},
[261] = {"붉은십자군의 길", {"<class>|1이여;여;, 할 얘기가 있으니 이리 가까이 오십시오. 흠... 솔직히 얘기하도록 하겠습니다. 지금 우리는 우리 주민을 타락시키고 우리의 땅을 오염시키는 악의 무리, 스컬지와 전쟁 중입니다. 스컬지가 우리를 괴멸시키고 이 세상을 암흑으로 끌고 가기 전에 막아야 합니다. 우리, 붉은십자군은 몸과 마음을 다해 스컬지와 싸울 것을 맹세했습니다.","<형제/자매>도 우리와 함께하겠다면 용기를 내어 충의를 증명해 보여주십시오. 잊혀진 땅 남쪽에 있는 뼈의 골짜기에서 언데드 약탈자들을 처치해 주시면 됩니다.","이를 수행하면 붉은십자군에서 당신을 기꺼이 받아들일 것입니다."}, {"언데드 약탈자 30마리를 처치한 후 나이젤의 야영지에 있는 수사 안톤에게 가야 합니다."}},
[262] = {"의문의 남자", {"우리 가족이 몰살당한 날 밤, 그림자 속에서 서성대던 그자가 누구인지 알아내야겠습니다!","제발, 한 번 더 당신 실력을 발휘해서 그자를 찾아주십시오. 그가 무엇을 숨기고 있었는지 알아내야 합니다!","당신이 아는 것 말고는 나도 별다른 단서가 없으니 내 농장에서 발견한 책을 가져가십시오. 마을 사람 중에서 이 책의 주인이 누구인지 아는 사람이 있을 수도 있을 겁니다."}, {"어둠골의 마을 주민들에게 스벤의 농장에서 발견한 책을 보여 줘야 합니다."}},
[263] = {"왕국 수호", {"많은 산악경비원들이 당신을 매우 실력 있는 친구라고 칭찬하더군. 여긴 당신과 같은 용감한 <race>|1이;가; 절실히 필요하다오. 예비 사단은 전방으로 배치되어 가고 여긴 우리뿐이라오. 그러니 트로그를 상대로 한 싸움은 나아질 기미도 보이지 않을 수밖에... 하지만 이제 자네 같은 유능한 <class>|1이;가; 왔으니 실력을 보여 주시오.","가서 가루바위 주술사 8마리와 가루바위 뼈다귀싸움꾼 8마리를 처치해주시오. 남쪽에 있는 산의 동굴 주변에서 그들을 찾을 수 있을 것이오. 어디, 명성이 사실인지 한번 보도록 하겠소. 트로그 사냥꾼."}, {"남쪽 경비탑의 산악경비대 월뱅이 가루바위 주술사 8명과 가루바위 뼈다귀싸움꾼 8명을 처치해 달라고 했습니다."}},
[264] = {"영원한 이별", {"나쁜 사람 같으니! 그놈의 십자군 때문에 날 두고 가버리다니... \"언데드의 위협에 맞서는 데 있어 가장 중요한 건 바로 신성한 정의!\"이기 때문이라고?","그럼, 아이들은 어떻게 되는 거지?! 또 나는? 매일 밤 울면서 기다려 왔는데... 언제나 나보단 그 몹쓸 임무가 먼저라니!","도대체 그놈의 \"정의\"가 우리에게 뭘 가져다주는지 한번 지켜보겠어! 그이는 죽었고 나는 그이가 막으려던 그 악몽을 견디며 살아야 하다니!","이 쓰레기 같은 물건들을 공동묘지에 있는 그이 무덤에 가져다줘 버리세요. 이제 내겐 이 물건들도, 그이도 다 필요 없으니까요!"}, {"은빛소나무 숲에 있는 유리브의 무덤에 클라리스의 펜던트를 가져다 놓아야 합니다."}},
[265] = {"계속되는 조사", {"자, 이 책을 도로 가져가게. 그대가 꼭 그 사람을 찾게 되길 빌어 줌세. 다른 마을 주민에게 물어보면 또 다른 단서를 얻을지도 몰라.","그리고 한 가지 더... 이 책을 쓴 사람은 다크샤이어 외부 사람이지만 책 자체는 이곳에서 구입한 것이야."}, {"다른 마을 주민에게서 의문의 남자에 대한 정보를 알아봐야 합니다."}},
[266] = {"여관에서의 조사", {"자, 책 여기 있습니다. 그리고 조사가 잘 이뤄지길 바랍니다.","여관에 가서 더 알아보는 건 어떻습니까? 당신이 찾는 사람이 이 마을에서 지냈다면 누군가 여관에서 그를 보았을 가능성이 있을 테니까 말입니다."}, {"여관에서 의문의 남자에 대한 정보를 얻으십시오."}},
[267] = {"트로그의 위협", {"당신도 지금 트로그가 드워프의 땅에 위협이 되고 있다는 것을 알고 있겠지요? 현재 아이언포지 예비 사단이 얼라이언스 최전선에 배치된 상황이라 이 지역을 방어할 병력이 부족한 상태입니다. 제가 지휘하는 연대는 이 마을을 지키는 것이 임무라, 언덕에 숨어있는 저 괘씸한 트로그놈들을 처리하고 싶어도 섣불리 나설 수 없는 노릇이지요.","저 괘씸한 트로그놈들을 처리해야겠는데... 당신이 이번 임무를 맡아서 트로그놈들에게 뜨거운 맛을 보여주시겠습니까? 그리고 증거물로 트로그의 단단한 이빨 8개를 가지고 오십시오."}, {"남쪽 경비탑에 있는 경비대장 루겔퍼스에게 트로그의 단단한 이빨 8개를 가져가야 합니다."}},
[268] = {"스벤에게 돌아가기", {"당신이 발견한 건 반밖에 못 쓴 겁니다. 스벤의 농장에서 도망칠 때 두고 올 수밖에 없어서...","자, 이 완성된 일지를 스벤에게 가져다주시기 바랍니다. 그의 농장에서 일어난 일을 전부 다 기록해 놓았습니다.","이 일지가 지난 몇 주 동안 나와 함께한 유일한 친구였지만, 이 책을 포기해야만 분노에 눈먼 스벤과 마주치는 것을 피할 수 있다면 두말할 필요 없이 내놓도록 하죠!"}, {"완성된 지터스의 일지를 가지고 스벤에게 가야 합니다."}},
[269] = {"지혜의 추구", {"스톰윈드로 가서 빛의 대성당에 있는 파딩 주교님과 대화해 보시기 바랍니다. 그분께 지터스의 일지를 보여 주고 모벤트 펠을 상대하겠다고 얘기하십시오.","스톰윈드는 어둠골을 저버렸지만 주교께서는 인도적이고 매우 지혜로운 분이시니 당신이 우리를 구할 수 있도록 도와 주실 겁니다.","당신의 임무는 위험천만하지만 서둘러 주시기 바랍니다. 시간이 지날수록 그늘숲의 그림자는 점점 커져 가고 있습니다."}, {"스톰윈드에 있는 주교 파딩에게 완성된 지터스의 일지를 보여 줘야 합니다."}},
[270] = {"저주받은 함대", {"몇 해 전 쿨 티라스의 제3함대가 드워프 왕국 아이언포지의 북쪽에 넓게 펼쳐진 저습지의 해안가에서 침몰했소이다. 그중 한 척의 배에 라이트포지 철이 실려 있었소.","이 금속은 우리에게 매우 중요하오. 라이트포지 철로 만든 물건은 거룩한 진실의 힘을 발휘하기 때문이오이다. <형제/자매>께서 모벤트 펠을 상대할 생각이라면 그런 무기가 필요할 것이오.","메네실 항구로 가서 나의 드워프 동료인 글로린 스틸브라우와 얘기해 보시오. 그 잃어버린 금속을 찾을 수 있다면 그가 위치를 알고 있을 것이오."}, {"저습지에 있는 메네실 항구로 가서 글로린 스틸브라우와 대화해야 합니다."}},
[271] = {"바이린의 복수", {"거만한 다릴의 콧대를 꺾을 수 있는 일이 있는데 혹시 관심 있으세요? 인정하긴 싫지만 다릴이 잘난 체가 심하긴 해도 기술은 뛰어나잖아요.","1년 전 큰 곰 한 마리가 원정순찰대 오두막의 주요 표적이 된 적이 있었지만 어떤 사냥꾼도 그 괴물을 잡지는 못했죠. 물론 다릴도 최선을 다했지만 얻은 거라곤 눈 위의 그 흉한 상처뿐이었어요.","그 곰을 잡으면 다릴도 할 말을 잃을 거예요.","큰곰 숯검댕이라는 그 곰은 텔사마 북쪽에 있는 굴 안에 있는 곰 소굴 근처에 있어요."}, {"큰곰 숯검댕이를 처치하고 그 결과를 원정순찰대 오두막에 있는 풋내기 다릴에게 보여 줘야 합니다."}},
[272] = {"바다표범의 시험", {"이제 두 번째 시험으로 반으로 쪼개진 바다표범의 펜던트 두 개를 찾아야 합니다. 하나는 물에서 미끄러지듯 움직이는 바다표범의 민첩성을 그러모으고, 다른 하나는 바다표범의 엄청난 지구력과 인내를 그러모으죠. 드루이드는 이 두 가지 특징을 모두 모아 바다표범으로 변신합니다.","펜던트 조각들의 위치를 알아보려면 달숲 주민들과 대화를 나눠보세요. 두 조각을 이곳으로 가져와서 하나로 합친 다음, 완성된 펜던트를 덴드리트 스타블레이즈에게 가져가면 된답니다."}, {"민첩성의 바다 펜던트 반쪽과 인내의 바다 펜던트 반쪽을 찾아야 합니다. 달숲 주민들에게서 이 물건들이 있을 만한 위치에 대한 단서를 얻으십시오.","두 개의 반쪽을 찾아 바다표범의 펜던트를 만들어야 합니다. 반드시 레물로스의 제단 근처로 와야만 하나로 합칠 수 있습니다.","완성된 바다표범의 펜던트를 달숲, 밤의 안식처에 있는 덴드리트 스타블레이즈에게 가져가야 합니다."}},
[273] = {"물자 공급 재개", {"그리 늦은 건 아니니 지금이라도 훌다르를 찾아보시오. 무거운 짐을 짊어지고 가는 데다 미란과 사옌만큼 힘이 센 것도 아니라 멀리 가지는 못했을 거요.","그들은 늘 다니던 길로 가고 있소. 마을의 남쪽으로 가서 모단 호수의 굽은 길을 따라가 보시오.","이미 목적지에 도착하지만 않았다면 쉽게 찾을 수 있을 거요."}, {"훌다르와 대화해야 합니다."}},
[274] = {"어둠의 전조", {"자, 너무 늦기 전에 힌더바이르에게 이 혼합물을 가져가시오. 검은무쇠 놈들은 일을 늦출 생각이 없으니, 우리도 빨리 움직여야 하오."}, {"애실런 스톤스머크가 모단 호수에 있는 선임기술자 힌더바이르에게 중화 혼합물을 전해 달라고 했습니다."}},
[275] = {"대지의 상처", {"이유는 모르겠지만 저습지 전체가 곪아 신음하고 열병을 옮기는 수렁이끼괴물의 수가 늘어나고 있다. 이 땅이 더는 열병으로 신음하지 않도록 하려면 반드시 놈들을 몰아내어 다시 늪으로 돌려보내야 하느니라.","수렁이끼괴물들은 저습지의 수로 한복판을 돌아다니고 있다."}, {"수렁이끼괴물 8마리를 처치한 다음 저습지에 있는 신록수호자 레시엘에게 가야 합니다."}},
[276] = {"파괴의 손길", {"이끼가죽 놀들은 저습지에서 오랫동안 평화롭게 지내왔지만 요즘 그 수가 너무 늘어나고 있다. 이들은 늪의 덤불과 꽃들을 짓밟고 그 고약한 도끼로 수없이 많은 나무를 땔감으로 베어내고 있다.","그 놀은 더 이상 이 땅과 조화를 이루지 못하고 있으니 저습지를 지키려면 놀의 숫자를 줄여야만 할 것이다.","이끼가죽 놀 15마리와 이끼가죽 싸움꾼 10마리를 처치하라. 남쪽에 있는 알가즈라는 드워프 관문 근처에 놈들이 있을 것이다. 임무를 완수하고 내게 돌아오너라."}, {"이끼가죽 놀 15마리와 이끼가죽 싸움꾼 10마리를 처치한 다음 저습지에 있는 신록수호자 레시엘에게 가야 합니다."}},
[277] = {"화재 예방", {"이끼가죽 놀들이 저습지의 나무를 베어 불을 지피는 데 사용하고 있다. 이제 더 이상 두고 볼 수만은 없는 일!","이끼가죽 놀들이 불을 붙이는 데 사용하는 조잡한 부싯돌을 빼앗아 오라. 이끼가죽 놀과 이끼가죽 싸움꾼을 제외한 모든 이끼가죽 일족이 부싯돌을 지니고 있다."}, {"신록수호자 레시엘에게 조잡한 부싯돌 9개를 가져가야 합니다."}},
[278] = {"어둠의 전조", {"중화제를 만드는 데 필요한 재료들을 즉시 모아 주게. 늑대거미 독액은 근처에 있는 거미에게서 얻을 수 있고, 악어 눈물도 모단 호수에서 찾을 수 있지... 하지만 모그로쉬 수정은 찾기가 매우 어려울 거야. 모단 호수 북동쪽 광산에서 오우거들이 모그로쉬 수정을 캐기는 하지만 아주 귀한 물건이라서 말이야.","검은무쇠 드워프의 공격을 막으려면 반드시 이 재료들이 필요하네. 모두 모으면 나에게 돌아와 주게. 그러면 혼합하여 중화제를 만들도록 하지."}, {"모그로쉬 소굴에 있는 애실런 스톤스머크가 늑대거미 독액, 모그로쉬 수정, 광포어 피를 모아 달라고 했습니다."}},
[279] = {"멀록과 고블러", {"멀록들이 깊은 물속에서 기어 나와 해안가에 자신들의 소굴을 짓고 있습니다. 그뿐 아니라 어부와 상인들을 괴롭히고 있으니, 놈들을 반드시 막아야 합니다.","이 멀록들 중 고블러란 녀석이 푸른아가미 멀록들과 함께 숨어들어와 지나가는 상인들을 괴롭히고는, 멀록 소굴 근처에 있는 은신처로 도망가 버리곤 합니다. 당신이 이 위험으로부터 우리 상인들을 보호해 준다면 적당한 보수를 지급할 생각입니다.","고블러란 놈은 북쪽의 강어귀를 배회하는 것으로 알려져 있습니다. 놈을 찾아서 그 무리를 모두 처치한 후에 그놈의 머리를 내게 가져다주시기 바랍니다."}, {"푸른아가미 멀록 12마리와 고블러를 처치하고 그 머리를 모아야 합니다.","메네실 항구의 칼 보란에게 고블러의 머리를 가져가야 합니다."}},
[280] = {"어둠의 전조", {"오늘 검은무쇠단 폭도들이 큰 통을 가지고 댐 쪽으로 헤엄쳐 가는 걸 목격했네. 이른 아침이라 그들을 막을 산악경비원은 아무도 없었어! 놈들이 댐 밑바닥에 시포리움을 설치한 건 아닌지 걱정되는군. 만약 그랬다면 곧 폭발할지도 몰라. 이제 모아온 재료들을 혼합해 중화제를 만들어야겠어.","자, 다 됐군.","너무 늦기 전에 이 중화제를 놈들이 가져다 놓은 통 안의 것과 섞어야 하네. 서둘러야 해!"}, {"선임기술자 힌더바이르가 댐 밑바닥으로 헤엄쳐 가서 화약통에 중화제를 넣어 섞어 달라고 부탁했습니다."}},
[281] = {"뱃짐 되찾기", {"얼마 전에 상선 눈먼 공주호가 멀록의 습격을 받았습니다. 다행히 배는 무사했지만 놈들이 이 배에 실려 있던 짐을 강탈해 가 버렸습니다. 그 뱃짐 중에는 귀중한 테레나스 메네실의 조각상도 있습니다. 그 조각상을 좀 되찾아 주십시오.","해안가의 멀록 소굴에서 상자나 통 등 잃어버린 짐으로 보이는 것들을 찾아보고, 그 조각상을 찾으면 내게 가져오시기 바랍니다."}, {"해안가의 멀록 소굴에서 상자와 통을 찾아야 합니다."}},
[282] = {"그렐린의 보고서", {"원래는 수습생을 시켜 세니르 형에게 보고서를 전달하려 했는데 좀 더 믿음직한 사람이 좋을 것 같아서 말입니다. 당신한테 부탁해도 될까요?","흠, 카라노스로 가려면 앤빌마 동쪽에 있는 터널을 가로질러 가야 합니다... 터널 입구에 가면 산악경비대원인 탈로스가 있을 테니 그와 얘기를 나눠 보십시오. 아마 지금쯤 터널 안엔 트로그들이 잔뜩 진을 치고 있을 겁니다.","앤빌마로 가다가 동쪽 방향의 길을 따라 가면 터널이 나올 겁니다. 탈로스는 그 입구 근처에 있을 겁니다."}, {"산악경비대 탈로스와 얘기를 나누십시오."}},
[283] = {"어둠의 전조", {"통에서 작은 소리가 납니다."}, {"중화제의 효과가 나타나는 것 같습니다. 선임기술자 힌더바이르에게 돌아가 이 기쁜 소식을 알려 주십시오."}},
[284] = {"계속되는 수색", {"멀록의 소굴을 계속 뒤져 나가지만 메네실 조각상의 흔적은 보이지 않습니다.","북쪽과 북동쪽에 있는 근처의 멀록 소굴 두 곳 중 한 곳에 단서가 있을지도 모릅니다."}, {"메네실 조각상을 찾아야 합니다."}},
[285] = {"다른 소굴 수색", {"이 멀록 소굴을 샅샅이 조사했지만 아무것도 발견하지 못했습니다.","하지만 다음에 찾게 될 단서는 매우 유용할 것이라고 확신합니다.","북쪽에 있는 소굴 중 하나에는 반드시 메네실 조각상이 있을 것입니다!"}, {"멀록 소굴에서 메네실 조각상을 계속 찾아봐야 합니다."}},
[286] = {"조각상 돌려주기", {"흙 속에서 통을 꺼내 열자 깨끗한 지푸라기에 쌓여 있는 은빛 조각상이 나옵니다. 조각상은 해머를 높이 들고 있는 긴 머리의 젊은 성기사의 모습입니다.","이것은 메네실 조각상이 분명합니다! 이제 메네실 항구의 칼 보란에게 돌아가야 합니다."}, {"칼 보란에게 메네실 조각상을 가져가야 합니다."}},
[287] = {"서리갈기 소굴", {"내가 아직 보고서를 완성하지 못한 상황이라서 말인데 자네 도움이 좀 필요하다네. 트롤들이 숨어 있는 곳을 발견했지만 너무 많아서 들어갈 수가 있어야지. 자네가 동굴로 들어가서 트롤을 찾아 몇 놈 처치한 다음에 이곳으로 돌아와 주게.","우선 카라노스 북쪽 외곽 길을 따라 다리가 있는 곳까지 가게. 그리고 거기서 서쪽으로 얼어붙은 강을 따라 얼음 호수가 나올 때까지 계속 가게. 그러면 서쪽 강둑에 브루날 마을이 보일 거야. 서리갈기 소굴은 그 마을 남서쪽에 있네."}, {"서리갈기 소굴을 찾아 서리갈기 인간사냥꾼 5마리를 처치한 후 카라노스에 있는 세니르 화이트비어드에게 돌아와야 합니다."}},
[288] = {"제 3함대", {"난 쿨 티라스 제 3함대의 주력함이었던 파도의 여왕호의 일등항해사였지. 마지막 항해에서 붉은용들의 공격을 받아 전멸하고 말았던 비운의 함대 말이야. 함대를 공격한 그 용들은 오크 놈들이 조종하는 거였어!","용이 뿜어내는 불기운을 버틸 재간이 없었던 함대는 결국 불탄 채로 난파되어 이 해안으로 떠내려 오게 된 거지. 전 함대를 통틀어 내가 유일한 생존자야.","아, 그 생각을 하니 또 머리가 지끈거리는군! 마음을 달래고 얘기를 계속하려면 술을 한 잔 더 걸쳐야겠어."}, {"일등항해사 피츠시몬즈에게 벌꿀술을 한 병 사 줘야 합니다."}},
[289] = {"저주받은 선원", {"<고무되어 이야기를 계속하는 피츠시몬즈>","내가 바로 북쪽 해안에 난파된 그 쿨 티라스 제 3함대의 유일한 생존자란 말이야, 내가! 그런데 문제는 함대 대원 중에 아직 숨 쉬고 있는 건 나뿐이지만, 이곳에 남아 있는 건 나만이 아니라는 거지! 우리 배들 중 한 척에는 값지고 무시무시한 보물이 숨겨져 있어서, 거기서 뿜어 나오는 기분 나쁜 기운이 우리 동료의 영혼들을 저 썩어가는 선체에 가둬두고 있어.","당신이 우리 동료 몇 명의 영혼을 자유롭게 해 준다면 그 저주를 풀 수 있을 거야."}, {"저주받은 선원 13명, 저주받은 해병 5명, 그리고 일등항해사 스넬리그를 처치해야 합니다. 메네실 항구에 있는 일등항해사 피츠시몬즈에게 스넬리그의 연초상자를 가져가야 합니다."}},
[290] = {"저주 풀기", {"내 동료의 저주를 풀고 그들을 모두 자유롭게 하려면 바로 그... 그것... 우리 배에 실었던 그걸 찾아내서 정화해야 해! 그게 무엇인지는 모르지만 할린도르 선장이 애지중지하던 금고에 안전하게 보관되어 있었어.","난파된 용감무쌍호에 떠도는 우리 늙은 선장이 계속 꿈에 나타나고 있어. 그에게서 열쇠를 찾아내 용감무쌍호의 금고를 좀 열어 줘.","그리고 그 저주받은 물건을 꺼내서 마을에 있는 사제, 글로린 스틸브라우에게 가져다줬으면 해."}, {"할린도르 선장을 처치한 후 용감무쌍호의 금고 열쇠를 구해야 합니다.","그 열쇠를 사용하여 용감무쌍호의 잠긴 금고를 열어야 합니다."}},
[291] = {"보고", {"내 보고서를 원로원의원 바린 레드스톤 님께 전해주게. 매우 까다로운 분이니 화나게 해서는 안 될 거야. 아이언포지에 마그니 폐하가 궁정 회의를 하셨던 왕좌 옆에 계실 걸세.","자네, 아이언포지로 가는 길은 알고 있나? 카라노스를 나가서 북쪽 길을 따라 다리를 건넌 다음, 길가에 큰 깃발이 있는 곳에서 동쪽 길을 따라 산허리로 올라가면 된다네.","참, 그분께 자네가 날 돕는다는 사실은 비밀로 해주겠나? 내가 하는 일도 없이 놀고만 있다고 생각하시면 안 되니까 말이야, 알았지?"}, {"아이언포지에 있는 원로원의원 바린 레드스톤에게 세니르의 보고서를 전달해야 합니다."}},
[292] = {"팔레스의 눈", {"금고를 열자 기분 나쁜 빛을 발하는 더러워진 수정구가 들어 있습니다. 피츠시몬즈가 말하던 보물, 팔레스의 저주받은 눈입니다.","팔레스의 저주받은 눈을 메네실 항구에 있는 드워프 사제 글로린 스틸브로우에게 가져가십시오. 그리고 그에게 팔레스의 눈에 걸려 있는 저주를 풀어 달라고 부탁해야 합니다."}, {"글로린 스틸브로우에게 팔레스의 저주받은 눈을 가져가야 합니다."}},
[293] = {"팔레스의 눈 정화", {"미안하지만 난 팔레스의 눈에 걸려 있는 저주를 풀 수 없네. 이건 인간 사제가 만든 물건이니까 스톰윈드로 가서 도움을 줄 만한 사람을 찾아야 해. 대주교 베네딕투스와 한번 얘기해 보게. 그는 스톰윈드에 있는 빛의 대성당의 대표자일세. 그가 자넬 도울 수 있을 거야.","나도 자넬 위해 기도하겠네."}, {"대주교 베네딕투스에게 팔레스의 저주받은 눈을 가져가야 합니다. 베네딕투스는 스톰윈드의 빛의 대성당에 있습니다."}},
[294] = {"오머의 복수", {"상황이 매우 좋지 않소. 우리가 이 뼈들을 발견했을 때는 이미 냄새를 맡은 랩터들이 모여들고 있었소. 이 고약한 짐승들은 내 동료를 죽이고, 난 메린과 불쌍한 발굴조사단장님과 함께 이곳에 갇히게 되었소.","저습지에서 저 고약한 랩터들을 몰아낼 수 있도록 도와 주시오. 저 절벽 서쪽에 줄무늬랩터와 긴송곳니 줄무늬랩터를 각각 10마리씩 처치해 주시오.","그것으로 놈들에 대한 내 복수가 시작될 거요."}, {"웰가르의 발굴현장에 있는 오머 아이언브레이드가 긴송곳니 줄무늬랩터 10마리와 줄무늬랩터 10마리를 처치해 달라고 했습니다."}},
[295] = {"오머의 복수", {"자, 이제 저 끔찍한 랩터들에게 수많은 피를 흘리게 한 대가가 얼마나 큰 것인지 깨닫도록 해야 할 때요. 바로 저 아래쪽으로 가면 갈퀴발톱 줄무늬랩터와 무쇠턱 줄무늬랩터들이 무리 지어 있소. 이 고약한 짐승들을 각각 10마리씩 처치해 주시오!"}, {"웰가르의 발굴현장에 있는 오머 아이언브레이드가 갈퀴발톱 줄무늬랩터 10마리, 무쇠턱 줄무늬랩터 10마리를 처치해 달라고 했습니다."}},
[296] = {"오머의 복수", {"당신이 없는 동안 저 짐승들 중 한 놈이 다른 놈들과 다르다는 사실을 발견했소. 이놈은 다른 놈들보다 크고 훨씬 더 사나웠소이다. 아무래도 놈이 무리를 이끌면서 발굴 현장을 어지럽히고 다니는 우두머리인 것 같소.","이번이 내가 부탁하는 마지막 임무가 될 거요. 살투스란 녀석은 죄가 막중하니 정의의 이름으로 처치해 주시오! 내게 놈이 죽었다는 증거로 놈의 발톱 하나를 가져다주시오."}, {"웰가르의 발굴현장에 있는 오머 아이언브레이드가 살투스를 처치한 후 발톱을 가져다 달라고 했습니다."}},
[297] = {"조각상 모으기", {"최근, 그러니까 트로그가 이 지역에 나타나기 직전에 조각된 이상한 돌 조각상을 대량으로 발견했었지. 그런데 우리가 그 조각상들을 발견하자마자 트로그놈들이 우리를 쫓아오는 바람에 황급히 유적지를 빠져나와야 했소. 조각상들을 살펴 볼 겨를도 없었소. 그 조각상들이 트로그에게 영향을 미쳐 놈들이 더 광폭해지는 것 같소!","조각상들이 트로그에 어떤 영향을 미치는지 연구할 수 있도록 돌 조각상 8개를 내게 가져다주시오. 조각상이 트로그놈들 손에 들어가지 못하게 막아야 하오. 이 지역을 배회하고 있는 트로그들에게 조각상을 빼앗을 수 있을 거요."}, {"아이언밴드의 발굴현장에 있는 마그마 펠휴에게 돌 조각상 8개를 가져가야 합니다."}},
[298] = {"발굴 상황 보고서", {"텔사마에 발굴 상황에 대한 보고를 해야 하오. 화약 지원 요청도 해야 하고 말이오. 보고서는 여기 있소. 이걸 텔사마의 연락 담당인 예른 혼헬름에게 갖다주시겠소?","매우 중대한 일이오. 힘든 일은 아니니 꼭 좀 부탁하겠소!"}, {"텔사마의 예른 혼헬름에게 아이언밴드의 상황 보고서를 가져가야 합니다."}},
[299] = {"드러난 과거", {"저 랩터들의 침략이 있기 직전에 고아즈석이라는 큰 석판을 발견했다네. 석판의 내용은 모두 4부분으로 나뉘어 있네. 그 내용을 보면 \"신성한 계획\"과 \"운명의 예언\"에 대해 말하고 있다네.","시간이 별로 없네. 내가 직접 가서 저 짐승들을 상대하기엔 너무 늙었지만 자네는 강하고 젊지 않은가.","발굴현장을 정리하고 사라진 4개의 석판 조각을 찾아 주게나. 그 석판 조각들은 각각 아도스, 모드르, 골름, 네루라고 한다네. 고대 유물이나 흙이 팬 곳에서 찾아보면 될 거야. 의문을 풀 수 있도록 내게 그 석판 조각들을 가져다주게!"}, {"발굴조사단장 웰가르가 발굴현장을 정리하고 사라진 아도스 조각, 모드르 조각, 골름 조각, 네루 조각을 가져다 달라고 했습니다."}},
[301] = {"아이언포지에 보고", {"아이언밴드에게 화약을 더 보내려면 승인을 받아야 하오. 그러려면 그 보고서를 아이언포지 탐험가 연맹 본부로 가져가야 하오.","여기 보고서를 받으시오. 이걸 발굴조사단장 스톰파이크에게 주면 처리해 줄 거요. 그 사람도 아이언밴드에서 발굴 작업이 지연되는 걸 원치 않을 테니까...","스톰파이크는 탐험가 총회에 가면 만날 수 있소."}, {"발굴조사단장 스톰파이크에게 아이언밴드의 상황 보고서를 가져가야 합니다."}},
[302] = {"아이언밴드에게 화약 지원", {"그래도 아이언밴드가 화약이 필요하다고 하면 보내 줘야겠지. 이미 전령을 통해 텔사마에 있는 예른 혼헬름에게 승인서를 보냈으니 그가 아이언밴드에게 보낼 화약과 다른 물품들을 준비해 줄 게요.","그리고 한 가지 부탁이 더 있는데, 화약이 제대로 전달되는지 잘 감시해 주시오. 지난번에 보낸 화약은 아이언밴드에게 전달되지 않았으니 철저히 감시해 주길 바라오."}, {"텔사마에 있는 예른 혼헬름과 대화해야 합니다."}},
[303] = {"검은무쇠 부족과의 전투", {"나라 안팎으로 전쟁이 번지고 있소. 강력한 드워프 병사들이 먼 타향에서 얼라이언스의 이름으로 죽어가는 동안, 정작 우리 조국은 검은무쇠 드워프에게 짓밟히고 있으니... 탄돌 교각에서 우리 병사들이 희생되는 바람에 던 모드르가 그리 허무하게 함락되고 만 거요.","당신도 우리를 도와 병력을 소집해 놈들을 저지해 주었으면 하오. 검은무쇠 드워프 10명과 채굴꾼, 투사, 폭파병 각각 5명씩을 처치해 준다면 예비 병력이 도착해 놈들을 몰아낼 때까지 버틸 수 있을 거요.","어서 서두르시오!"}, {"던 모드르의 모틀리 가마슨이 검은무쇠 드워프 10명, 검은무쇠 채굴꾼 5명, 검은무쇠 투사 5명, 검은무쇠 폭파병 5명을 처치해 달라고 했습니다."}},
[304] = {"위험한 임무", {"던 모드르 전투에서 수많은 병사가 목숨을 잃었다네. 내 친동생도 탄돌 교각의 서쪽 다리가 무너질 때 전사하고 말았지. 그렇네, 검은무쇠 드워프는 악마 같은 녀석들이야. 로고가 수집한 정보에 따르면 저습지의 이 흉악한 무리의 우두머리가 불한당 발가라스라는 흑마법사라고 하네. 녀석은 위험한 전투에 부하들만 내보내고 자기는 남동쪽에 있는 공포철로 언덕의 야영지에 숨는 비겁한 겁쟁이지.","그 비겁하고 쓰레기 같은 녀석을 찾아내 처치하고 그 증거를 가져오면 내 보상하지."}, {"불한당 발가라스를 처치하고 그의 귀걸이를 던 모드르 외곽에 있는 결의의 롱브레이드에게 가져가야 합니다."}},
[305] = {"발굴단을 찾아서", {"오! 당신이 바로 롱브레이드 님이 보낸다던 정찰병이로군! 내가 얼마나 걱정했는지 모를 거요.","웰가르 발굴단의 일원인 내 아내 메린은 지난달 내내 나에게 매주 편지를 보내 줬었소. 그런데 마지막으로 편지를 받은 지 2주가 다 지났소. 아직까지 연락이 없는 걸 보니 무슨 일이 있는 게 분명하오!","롱브레이드 님께서 근간에 발굴단에서 연락이 없으면 정찰병을 보내 확인해 보시겠다고 하셨다오. 아마 그 정찰병이 당신인가 보구려. 동쪽의 발굴현장에서 메린을 찾아 주시오!"}, {"타렐 록위버가 발굴현장으로 가서 메린 록위버를 찾아 달라고 했습니다."}},
[306] = {"발굴단을 찾아서", {"우리는 굉장히 희귀한 유물을 발굴하고 있었는데, 이 거대한 뼈들이 나오기 시작한 거예요. 정말 끔찍했어요!","그런데 우리가 무슨 일이 일어나고 있는지 알아채기도 전에 줄무늬랩터들이 발굴현장을 공격해 왔어요. 그래서 많은 사람이 목숨을 잃었죠.","우리의 용감한 오머가 발굴조사단장님과 저의 목숨을 구해 줬어요. 오머가 엄호해 준 덕분에 안전한 곳으로 도망칠 수 있었죠. 이젠 여기 갇힌 신세이고 우리가 힘들게 발굴한 것들도 다 버려져 있답니다.","자, 이 편지를 남편 타렐에게 전해 주세요. 편지에 이곳 상황을 자세히 설명해 놨어요."}, {"저습지의 메네실 항구에 있는 타렐 록위버에게 메린의 편지를 가져가야 합니다."}},
[307] = {"더러운 손", {"여기서 동쪽에 있는 은여울 광산은 오래전 폐광이 되었소. 광부 조합에서 그곳을 저장고로 사용했는데, 지금은 코볼트들이 들어와 살고 있소. 드워프의 소유지와 훌륭한 도구들이 추악한 괴물들의 손에 들어간 거요!","곧 우리 전사들이 녀석들을 소탕할 계획이지만, 광부 조합에서는 누군가가 광부의 도구를 먼저 찾아 주길 바라고 있소. 아마 부대를 동원해야 할지도 모르는 힘든 임무가 될 거요.","광부의 도구는 동굴 곳곳에 있는 광부 조합 상자에 들어 있다오. 행운을 빌겠소."}, {"은여울 광산에서 광부의 도구 6개를 모아야 합니다.","그런 다음 산악경비대 스톰파이크에게 돌아가십시오."}},
[308] = {"맥주를 좋아하는 야벤", nil, nil},
[309] = {"화약을 지켜라", {"화약을 아이언밴드에게로 운반해야 하는데 이 짐마차가 꿈쩍도 하지 않고 있네. 짐도 무겁고, 매우 위험할 게야. 게다가 사옌이 검은무쇠 놈들과 한패가 되었으니 놈들이 언제 습격할지도 모르고...","그러니 자네가 이 짐을 지키도록 도와주었으면 좋겠네. 나와 자네가 짐마차를 지키는 동안 미란이 야영장에 화약통을 내릴 수 있을 거야."}, {"훌다르와 아이언밴드 짐마차를 보호하고 발굴조사단장 아이언밴드에게 이 사실을 알려야 합니다."}},
[310] = {"숙명의 경쟁자", {"썬더브루의 술이 훌륭하다는 건 저도 인정합니다. 하지만, 자기네들만 좋은 술을 만드는 건 아니라는 걸 가르쳐 주고 싶군요! 그래서 말인데 도움이 좀 필요합니다.","자, 여기 발리브루 스칼더 맥주통을 가지고 카라노스에 있는 썬더브루 양조장 지하실로 숨어들어가세요. 그리고 이걸 썬더 에일 맥주통 중 하나와 바꿔 놓으세요. 어디, 그 집 단골들이 누구 맥주를 더 좋아하는지 한번 지켜보고 싶군요.","혹시 누군가 그 맥주통을 지키고 있다면 그자의 주의를 다른 곳으로 돌려야 할 겁니다."}, {"카라노스에 있는 썬더브루 양조장의 지하실로 가서 썬더 에일 맥주통을 발리브루 스칼더 맥주통으로 바꿔 놓아야 합니다."}},
[311] = {"말레스에게 돌아가기", {"말레스가 준 스칼더 맥주통을 카라노스 양조장 지하실에 놓고 썬더 에일 맥주통 하나를 챙겼습니다. 바꿔치기에 성공한 증거가 될 것 같습니다."}, {"브루날 마을에 있는 말레스 발리브루에게 썬더 에일 맥주통을 가져가야 합니다."}},
[312] = {"툰드라 맥그란의 고기 상자", {"난 추운 겨울을 대비해서 한 달치 분량의 말린 고기를 소금에 절여 상자에 넣고 잠가 두었네. 곰들이 가져가지 못하게 말이야! 그런데 사슴 사냥을 나간 사이 늙은 얼음수염이라는 짐승이 그 상자를 훔쳐갔어. 상자는 토륨으로 만들어져 있어서 쉽게 부술 수는 없을 테지만 그 안의 고기를 되찾지 못하면 영락없이 굶어야 할 판이야.","그래서 말인데, 숨어서 몰래 왔다갔다하는 건 자네가 나보다 더 잘할 것 같으니 내 상자 좀 찾아 주게나. 내 이 느려 터진 두 다리로는 동굴 안을 재빠르게 들락날락할 수 없을 것 같아."}, {"늙은 얼음수염의 동굴에 있는 잃어버린 고기 상자에서 툰드라 맥그란의 말린 고기를 되찾아 와야 합니다."}},
[313] = {"소환 명령", {"대격변으로 수많은 트로그들이 지상으로 몰려나왔고, 놈들은 서리갈기 트롤을 압박해 몰아내려고 하고 있네. 공교롭게도 이 서리갈기 트롤은 바로 우리 문전에 정착을 결심하고 여러 가지 문제들을 발생시키고 있네.","이곳 서쪽의 은빛 동굴에 사는 웬디고들을 감시하려고 산악경비대 던스탄과 그의 부하들을 그곳에 배치했는데 이제 그들을 다시 카라노스로 불러들여야겠네. 그들을 찾아 노움이 제공한 원격 조종 관측 로봇을 설치하고 다시 카라노스로 돌아오라고 전해주게."}, {"대장 타란의 명령을 은빛 동굴에 있는 산악경비대 던스탄, 르윈, 발그럼에게 전해야 합니다."}},
[314] = {"가축 보호", {"한밤중에 짐승 울음소리가 들려서 잠을 설쳤는데, 아침에 일어나자마자 우리로 가봤더니 아니나 다를까 산양 두 마리가 없어졌지 뭐예요. 그동안 계속해서 바가쉬라고 하는 비열한 짐승이 우리 가축을 해쳐 왔답니다. 지금은 아이언포지의 병력이 부족한 상태라 바가쉬를 처치해 줄 사람이 없어요.","당신은 그 짐승을 찾아내서 처치할 만큼 용감해 보이네요. 바가쉬의 송곳니를 가지고 오면 보상해 드리죠. 놈은 이 목장의 바로 위쪽에 숨어 있어요. 아주 위험한 짐승이니 조심하셔야 해요."}, {"바가쉬의 송곳니를 획득해야 합니다."}},
[315] = {"최고급 스타우트 맥주", {"해야 할 일이 하나 있네. 바로 최고급 스타우트 맥주를 만드는 것이지. 우리 집안은 대대로 양조업을 해왔기 때문에 자신 있다네. 확실한 제조법만 찾으면 되는데...","서리갈기 트롤들이 북서쪽에 있는 아지랑이 마루에서 아지랑이풀이라는 식물을 재배하고 있다고 들었네. 기이한 부족 의식에 사용하기 위해서지. 우리 드워프한테는 별로 쓸모 있는 식물이 아니지만 독특한 맛이 나거든... 그걸로 술을 한번 담가 보려고 한다네.","아지랑이풀을 얻는 게 쉬운 일은 아니지만 트롤 마을에 있는 바구니에서 살짝 가져와 준다면 비싼 값에 쳐주겠네."}, {"카라노스에 있는 레욜트 발리브루에게 아지랑이풀 7개를 가져가야 합니다."}},
[317] = {"증기 전차 공급품 비축", {"난 이번에 증기 전차 부대에서 맡은 임무를 곧 시작할 계획이네. 시간이 오래 걸리는 임무라 증기 전차에 한 달치 정도의 보급품을 비축해 둬야 하지. 스틸그릴이 내 탱크를 손보고 있는 동안 날 위해 사냥을 좀 해 주겠나?","깔고 잘 수 있는 것과 우리의 주식인 멧돼지 고기가 필요하다네. 가죽은 멧돼지, 표범, 곰 등을 사냥해서 구하면 되고, 고기는 물론 멧돼지에게서 얻으면 되지... 은빛 동굴 남쪽의 눈 덮인 벌판에 가면 사냥감이 많이 있을 거야."}, {"멧돼지 고기 4개와 두꺼운 곰 모피 2개를 모아서 스틸그릴의 정비소에 있는 조종사 벨로우피즈에게 전달해야 합니다."}},
[318] = {"에버샤인", {"레욜트 발리브루라는 드워프가 다양한 종류의 술을 만들고 있는데 맛있는 술도 있지만 개중에는 형편없는 것도 있다네. 그리고 가끔 아주 독한 술을 만드는 데, 체력이 웬만한 드워프도 한두 잔만 마시면 뻗어 버릴 정도지.","레욜트는 카라노스의 서쪽에 있는 브루날 마을에 살고 있네. 그가 만드는 에버샤인이라고 하는 술이 필요한데... 추운 날 바깥에서 돌아다닐 때 몸을 녹이는 데 그만이지.","게다가 워낙 독한 술이라서 증기 전차 엔진에 넣어도 대단한 화력을 얻을 수 있거든. 그 방법으로 위급한 상황에서 빠져나간 게 한두 번이 아니라네."}, {"브루날 마을에 있는 레욜트 발리브루에게서 에버샤인 술통을 얻어야 합니다."}},
[319] = {"에버샤인 얻기", {"에버샤인이 한 통 있기는 한데... 내 부탁을 먼저 들어줬으면 하네. 브루날 마을 근처의 눈 덮인 벌판을 배회하는 짐승들을 좀 없애 줬으면 하는데... 가끔 마을까지 넘봐서 말이야.","물론 그 곰이나 표범, 멧돼지 따위가 무서워서 그러는 건 절대로 아닐세. 우리 드워프가 그깟 짐승들을 무서워할 리가 있겠나? 하지만 자네가 그 짐승들을 좀 처리해 준다면 우리는 그만큼 술이나 다른 물건들을 만드는 일에 투자할 시간을 벌 수 있다네."}, {"얼음발톱흰곰 6마리, 덩치 큰 멧돼지 8마리, 눈표범 8마리를 사냥한 다음 브루날 마을에 있는 레욜트 발리브루에게 가야 합니다."}},
[320] = {"벨로우피즈에게 돌아가기", {"여기 에버샤인 술통을 받게. 짐승들을 사냥해 준 것 다시 한 번 고맙게 생각하네."}, {"스틸그릴의 정비소에 있는 조종사 벨로우피즈에게 에버샤인 술통을 가져가야 합니다."}},
[321] = {"라이트포지 철", {"해안에 버려진 난파선들은 지난 대전쟁 중에 붉은용의 공격으로 불에 타 가라앉은 쿨 티라스의 제 3함대의 잔해이네.","그 함대는 로데론 소속이었지만 엘프 군함인 창공의 물수리호와 함께 항해하고 있었지. 이 배는 라이트포지 철을 싣고 제 3함대의 호위를 받던 중이었는데, 호위를 하던 제3함대에 쏟아진 용들의 공격에 피해를 입어 침몰하고 말았지.","옛날 이야기는 이쯤 하도록 하고, 난파된 창공의 물수리호는 메네실 항구 남쪽에 있네. 그 배에서 라이트포지 철을 찾으면 내게 가져다주게."}, {"저습지에 있는 난파된 창공의 물수리호를 찾아야 합니다."}},
[322] = {"신성한 무기", {"스톰윈드에 있는 드워프 지구에 라이트포지 철을 잘 다루는 그리만트 엘모어라는 드워프 대장장이가 있네.","자네가 찾은 주괴를 제련하여 상자에 넣어 두었으니 그걸 그리만트에게 가져가 자네 얘길 해 보게나. 이런 일은 기꺼이 도와줄 친구니까 자네를 위해 무기를 만들어 줄 것이야. 모벤트 펠도 무서워할 만큼의 강력한 무기를!"}, {"스톰윈드에 있는 그리만트 엘모어에게 라이트포지 주괴가 든 상자를 가져가야 합니다."}},
[323] = {"영웅의 진가", {"모벤트 펠은 강령술사로 흑기사들과 연합한 자입니다. 당신에게 모벤트 펠을 처치해 달라고 부탁하고 싶지만, 당신 실력이 어느 정도인지도 모르면서 그 악당에게 헛되이 목숨을 잃게 할 수는 없습니다.","당신의 용기는 이미 인정합니다만 정말 그 강령술사를 상대하고 싶다면 그의 부하들을 먼저 쓰러뜨리고 실력을 증명해 보이기 바랍니다."}, {"해골 약탈자 15마리, 해골 치료사 3마리, 해골 문지기 3마리를 처치한 다음 스벤에게 가야 합니다."}},
[324] = {"사라진 주괴", {"물에 젖은 상자에 라이트포지 주괴가 하나 있는 것으로 보아 멀록들이 나머지 주괴를 훔쳐 간 것이 분명합니다. 난파된 창공의 물수리호 부근에서 멀록 약탈꾼들을 처치하고 사라진 라이트포지 철을 찾아야 합니다."}, {"라이트포지 주괴 5개를 모은 다음 저습지에 있는 글로린 스틸브로우에게 돌아가야 합니다."}},
[325] = {"무기 완성", {"당신이 말했던 악마에게 대항할 무기가 여기 있소. 자, 이제 모든 준비가 끝났군그래. 행운을 비오. 그리고 조만간 승리의 소식을 기다리도록 하지!"}, {"모벤트 파멸의 불꽃을 그늘숲에 있는 스벤에게 가져가야 합니다."}},
[328] = {"숨겨진 열쇠", {"헤로드의 장부 묶음 기록들에 병사 토르센에 대한 언급은 없지만 작은 놋쇠 열쇠가 숨겨져 있습니다.","그리고 그의 쪽지를 더 조사한 결과 여러 문서에 \"비밀 금고\"가 언급되어 있는 것을 발견했습니다. 금고는 쿠르젠 주둔지 뒤편에 있는 보급창 동굴 어딘가에 있어 보입니다.","이 비밀 금고를 찾으면 발견한 작은 놋쇠 열쇠를 사용해 열 수 있습니다. 그 안에서 토르센이 발각된 원인을 밝혀 줄 무언가를 찾을 수도 있을 것입니다."}, {"작은 놋쇠 열쇠로 사서 헤로드의 금고를 열어야 합니다."}},
[329] = {"드러나는 첩자의 정체", {"금고의 귀중한 물건들 중에는 진흙투성이의 휘갈겨 쓴 쪽지가 있습니다. 알아보기 힘들긴 하지만 병사 토르센의 여행 계획인 것 같습니다. 거기에는 정찰 경로, 접촉할 대상, 그리고 반란군의 임무를 완수하기 위한 시간 계획 등이 적혀 있습니다. 계획서에는 '-M'이라는 한 글자로 된 서명이 있습니다.","이 쪽지를 작성한 것이 누구든지 간에 토르센과 매우 가까운 사이었음이 분명합니다. 또한 그자가 바로 반란군 야영지 내의 배반자일 것입니다."}, {"반란군 야영지에 있는 부관 도렌에게 진흙투성이 쪽지를 가져가야 합니다."}},
[330] = {"정찰 일정", {"당신이 찾아 준 쪽지에 \"-M\"이라고 서명이 되어 있는데... 누구를 말하는 건지 모르겠소.","하지만... 쪽지에 따르면 이자가 지난주에 토르센과 함께 정찰을 했다는 것을 알 수 있군그래. 그러니 지난주에 토르센과 함께 정찰 임무를 수행한 자가 배반자일 거요!","하사관 세스먼에게 가 보시오. 그가 정찰 일정을 관리하니 토르센과 함께 임무를 수행한 자가 누군지 말해 줄 수 있을 거요."}, {"하사관 세스먼과 대화해야 합니다."}},
[331] = {"도렌에게 보고하기", {"도렌 부관님께서 이 정찰 정보를 보고하라고 하셨나요?"}, {"부관 도렌과 대화해야 합니다."}},
[332] = {"포도주 가게 광고", {"스톰윈드 시민들에게 제일 유명한 포도주 가게에 대해서 물어보신 적이 있으시다면 우리 갈리나 집안에서 스톰윈드 최고의 포도주를 판다는 것을 아실 겁니다. 위치도 멀지 않답니다. 도시의 아름다운 수로를 따라 상업 지구에 있답니다.","자, 이 광고물을 받으세요. 가게에 있는 제 여동생 수제타에게 이 광고물을 가져가시면 유명한 달라란 누아 포도주 한 병을 무료로 드릴 겁니다. 절대 후회하시지 않으실 거예요!"}, {"갈리나 포도주 상점으로 가서 수제타 갈리나에게 포도주 상품권을 주고 포도주 한 병을 무료로 얻어야 합니다."}},
[333] = {"할란의 새 공급품", {"최근 들어 사업이 무척 잘 되고 있어 기분이 좋아요. 마치 춥고 거친 계절을 맞이하는 것처럼 사람들이 갑옷과 튼튼한 옷을 구매하고 있는 것 같습니다.","그런데 그거야 나중 일이고... 지금은 팔아야 할 옷감의 재고가 떨어져 가고 있다는 게 걱정입니다. 원단 공급자에게서 물건을 또 받아야 하는 실정이에요.","이 주문서를 대운하 의류점에 있는 레마 슈나이더에게 가져다주면 정말 고맙겠습니다."}, {"대운하 의류점으로 가서 레마 슈나이더에게 할란 바글리가 보내는 옷감 주문서를 전해야 합니다."}},
[334] = {"투르만의 준비물", {"제 아들, 투르만은 마법사 지구에 있는 라슨의 옷가게의 수습생이랍니다. 그런데 오늘 너무 서두르다가 그만 가위와 바늘을 놓고 가 버렸어요. 물론 당신처럼 위대한 분은 이보다 훨씬 중요한 일들이 있겠지만 바느질 도구 없이는 투르만이 수습생 일을 할 수가 없답니다!","제발 제 아들에게 바느질 도구를 좀 가져다주시겠어요? 라슨의 옷가게는 마법사 지구에 있는 두 개의 옷 가게 중 하나인데 그중에서 좀 더 깊숙한 곳, 마법사의 탑 근처에 있는 가게예요."}, {"스톰윈드 마법사 지구에 있는 라슨의 옷가게로 가서 투르만 슈나이더에게 바느질 도구를 줘야 합니다."}},
[335] = {"귀족을 위한 술", {"위쇼크 경이 귀족 평의회에서 다른 사람들을 선동하는 모양이야. 그 친구, 이 작은 선술집에 대한 소문을 들은 후부터 전면적인 조사를 펼쳐야 한다고 설득하고 다니고 있지. 하지만 내게도 계획이 있소. 위쇼크 경에게 다른 곳에 신경 쓸 일을 만들어 주면 되는 거 아니겠소? 그런데 당신 도움이 좀 필요할 것 같군.","어둠골 변두리에 있는 미스트맨틀 장원의 스탈반의 정원에서 자라는 꽃이 있소. 작은 흰색 꽃인데, 사람들이 틸로아의 눈물이라고 부른다오. 또 저습지의 돌다지 댐 아래 멀리 있는 폭포의 맨 밑바닥에서만 찾을 수 있는 사향쥐 뿌리도 필요하니 좀 가져다주시오."}, {"검은발톱 자데스가 어둠골에서 틸로아의 눈물과 저습지에서 사향쥐 뿌리를 가져다 달라고 했습니다."}},
[336] = {"귀족을 위한 술", {"위쇼크 경도 모든 사람과 마찬가지로 약점이 있소. 술을 무척 좋아한단 말이지. 그러고 보면 그 작자의 위선도 참 대단해. 낮에는 이 선술집의 문을 닫게 하려고 노력하면서 밤에는 이곳 위층에서 술을 퍼마신단 말이야. 게다가 계산은 절대 안 하지. 자, 어디 보자. 내가 특별히 준비한 술, 검은발톱 스타우트 맥주를 맘에 들어 할까? 이거 꽤나 독한 술이라오.","이 술을 위쇼크 경에게 가져다주고 얼마나 좋아하는지 살펴보시오. 그 늙은 바보 녀석은 주로 왕국 민원실 근처에서 정처없이 서성거린다오."}, {"왕국 민원실에 있는 보울즈 K. 위쇼크 경에게 검은발톱 스타우트 맥주를 주어야 합니다."}},
[337] = {"오래된 역사책", {"이 책의 표지는 심하게 긁히고 진흙으로 덮여 있지만 책 등에 '스톰윈드의 역사'라는 단어가 적혀 있는 것은 볼 수 있습니다. 책을 열면 첫 페이지에 스톰윈드 도서관의 인장이 명확하게 찍혀 있습니다.","누군가 이 책을 빌린 후에 반납하지 않은 것 같습니다."}, {"스톰윈드 도서관에 스톰윈드의 역사 책을 가져가야 합니다."}},
[338] = {"가시덤불 골짜기의 푸른 언덕", {"네싱워리 주인님께서 이 사실을 알게 된다면 무척 화내실 거예요!","그분은 저를 믿고 그분의 소설 가시덤불 골짜기의 푸른 언덕의 원고 교정을 맡기셨답니다. 교정 작업은 순조롭게 진행되고 있었는데 야영지에 엄청난 돌풍이 불어 원고가 낱장으로 여기저기로 흩어지고 말았어요!","대부분의 원고를 되찾았지만 아직도 많은 페이지들이 정글에 흩어져 있어요. 네 개의 장을 각각 완성할 수 있도록 흩어진 페이지들을 모아 주세요.","네 개의 장을 모두 모아 제게 가져 오시면 순서에 맞게 정리하는 것을 도와 드리도록 하죠."}, {"가시덤불 골짜기의 푸른 언덕 원고의 잃어버린 페이지들을 모아야 합니다. 네 개의 장이 모두 완성되면 바닐 스톤팟에게 가져가십시오."}},
[339] = {"제1장", {"1장은 상당히 매력적이랍니다. 1장에서 네싱워리 주인님은 이야기의 배경을 설정하고 그의 사냥 동료에 대해 묘사하고 있습니다. 물론 저도 포함해서요!","하지만 지금 그게 중요한 게 아닙니다. 아직 찾지 못한 쪽을 한번 확인해 봅시다. 음... 1, 4, 6, 8 쪽이 없군요. 그렇다면 아직 정글 어딘가에 흩어져 있는 것이 틀림없습니다!"}, {"바닐 스톤팟이 네싱워리의 소설 가시덤불 골짜기의 푸른 언덕의 1장을 완성할 수 있도록 1, 4, 6, 8 쪽을 가져가야 합니다."}},
[340] = {"제2장", {"2장은 퓨마와 호랑이 사냥에 관한 흥미진진한 이야기로 가득하답니다. 이 장의 쪽들을 바람에 날려 버리고 고작 자책밖에 할 수 없다니...!","10, 11, 14, 16 쪽만 되찾으면 2장의 원고를 모두 복구할 수 있을 것 같습니다."}, {"바닐 스톤팟이 네싱워리의 소설 가시덤불 골짜기의 푸른 언덕의 2장을 완성할 수 있도록 10, 11, 14, 16 쪽을 가져가야 합니다."}},
[341] = {"제3장", {"3장은 제가 가장 좋아하는 부분입니다. 3장에서는 얼가딘 경과 루아크 양이 드워프 라이플 총으로 첫 번째 사냥을 했던 이야기가 실려 있답니다. 3장의 마지막은 정말 손에 땀을 쥐게 하지요!","3장에서는 아직 18, 20, 21, 24 쪽이 빠져 있습니다. 저 밖의 어딘가에 있을 텐데..."}, {"바닐 스톤팟이 네싱워리의 소설 가시덤불 골짜기의 푸른 언덕의 3장을 완성할 수 있도록 18, 20, 21, 24 쪽을 가져가야 합니다."}},
[342] = {"제4장", {"아, 대미를 장식하는 4장! 정말 멋진 결말이었답니다.","이 장의 원고는 단 세 쪽이 빠져 있습니다. 25, 26, 27 쪽만 찾으면 4장의 원고를 모두 되찾게 됩니다."}, {"바닐 스톤팟이 네싱워리의 소설 가시덤불 골짜기의 푸른 언덕의 4장을 완성할 수 있도록 25, 26, 27 쪽을 가져가야 합니다."}},
[343] = {"불굴의 의지에 대한 연설", {"이번 총회에서 불굴의 의지에 대한 연설을 할 예정인데, '철강의 중요성'이라는 야금술에 대한 책에서 몇 구절을 인용했으면 하오. 이 책의 사본이 스톰윈드 왕궁 내의 왕실 도서관에 있을 텐데 가져다줄 수 있겠소? 그렇다면 신의 은총을 받을 것이오!","왕실 도서관은 매우 넓지만 그곳의 수석 사서인 밀턴 시프는 각각의 책 선반을 샅샅이 꿰고 있으니까 그에게 얘기하면 책을 찾아 줄 것이오."}, {"스톰윈드 왕궁에 있는 왕실 도서관으로 가서 밀턴 시프와 대화해야 합니다. 그는 수사 크리스토프가 연설에 필요로 하는 야금술에 대한 책을 찾아 줄 수 있습니다."}},
[344] = {"수사 팩스턴", {"철강의 중요성이라고요? 알고 말고요. 몇십 년 전에 드워프 광부인 마굴프 블라곤이 쓴 꽤 흥미로운 책이죠. 저자는 대부분의 삶을 카즈 모단의 깊은 산맥에서 광석을 캐면서 보냈다죠. 대단한 친구 아닙니까!","참, 어디까지 얘기했죠? 그렇지, 철강의 중요성! 어떡하죠, 그 책은 지금 북녘골 수도원에 있는 것 같네요. 수도원 도서관의 관리자인 팩스턴님이 그 책의 사본을 만들기 위해 가져가셨거든요.","그런데 그게 벌써 몇 달 전의 일인데... 왜 이리 오래 걸리는지 모르겠네요."}, {"북녘골 수도원에 있는 수사 팩스턴과 대화해야 합니다."}},
[345] = {"잉크 공급", {"철강의 중요성의 사본을 완성하려고 했습니다만 보시다시피 전 제가 필사하는 책에 저만의 잉크를 사용한답니다. 제가 직접 만든 잉크인데 퍼지거나 스며들지 않고 매우 빨리 마르거든요. 단점이 있다면 정말 너무 빨리 마른다는 것입니다! 사본을 다 만들기 전에 마지막 잉크가 다 떨어졌는데, 아직 잉크를 더 만들 기회가 없었답니다!","잉크를 만들려면 붉은마루 산맥의 레스밴 광석이 필요합니다. 그 광석은 주로 호숫골에 있는 현장감독 오슬로우님이 공급해 주셨었습니다."}, {"호숫골에 있는 현장감독 오슬로우와 대화하여 레스밴 철광석을 달라고 부탁해야 합니다."}},
[346] = {"수사 크리스포트에게로", {"자, 책의 사본을 다 완성했습니다. 이게 '철광의 중요성' 원본이니 받으세요. 인내심을 가지고 기다려 줘서 정말 고맙습니다. 아참, 밀턴 씨에게는 시간이 오래 걸려서 미안하다고 전해 주십시오."}, {"대성당 광장에 있는 수사 크리스토프에게 돌아가서 '철강의 중요성'을 전달해야 합니다."}},
[347] = {"레스밴 광석", {"레스밴 광석은 이 광석을 처음 발견한 바트 레스밴의 성을 따서 이름을 붙인 걸세. 용해할 경우 철처럼 단단하지는 못하지만 어둡고 탁한 색을 내기 때문에 잉크로 사용하는 서기들이 종종 있지. 이곳 북쪽에 있는 레스밴 동굴로 가면 찾을 수 있을 걸세.","평소에는 비축해 놓은 것이 몇 개 있었지만 최근에는 오크의 공격에 대비하느라 채취할 시간이 없었지. 게다가... 지금은 레스밴 동굴에 붉은마루 놀도 살고 있고 붉은마루 노역꾼들이 그 광석을 캐고 있다네.","그러니까 필요하다면 직접 가서 가져오는 수밖에 없다네."}, {"레스밴 광석 5개를 모아서 북녘골 도서관에 있는 수사 팩스턴에게 가져가야 합니다."}},
[348] = {"가시덤불 골짜기의 열병", {"아... 아파... 너... 너무 아파...","가시덤불 골짜기의 열병에 걸리고 말았어요... 지... 지독한... 바이러스...","유... 유일한 치료야... 약은... 모... 모... 흉포한 모크의 심장을 머... 먹는 건데... 그... 사납고... 자... 잡기도 힘든... 하얀 짐승...","의술사 운바그와만이... 유일하게 부... 불러낼 수 있어요...","남쪽의 곶에 있는 숨겨진 동굴에서 운바그와를 좀 찾아... 주... 주세요.","빨리요... 나... 난 죽어 가고... 있어요..."}, {"의술사 운바그와를 찾아 흉포한 모크를 불러내도록 해야 합니다. 핀 피즈라켓에게 모크의 심장을 가져가야 합니다."}},
[349] = {"가시덤불 골짜기의 열병", nil, nil},
[350] = {"오랜 친구 찾기", {"그 마르존 녀석이 데피아즈 단원과 접촉하고 있었을 테니 라스코발과 연관이 있을 걸세...","밴클리프와 스레드가 죽었으니 라스코발을 법대로 처리하기가 어렵게 됐어. 그들의 증언 없이는 자네 증거도 아무 소용이 없으니까 말이야. 더군다나 라스코발은 귀족이고 고위 인사와 관계도 두텁지. 법 위에 사는 녀석이야.","다른 데서 도움을 받아야 할 것 같아. 정말 내키진 않지만 우리를 도와줄 만한 자가 있네. 이 도시의 상업 지구에 있는 트리아스를 찾아보게."}, {"스톰윈드에 있는 엘링 트리아스와 대화해야 합니다."}},
[351] = {"OOX-17/TN 수색", {"금속으로 만들어진 이상한 알처럼 생긴 장치를 찾았습니다. 손잡이 하나를 만지작거리다 보니 장치가 열리면서 노움 로봇 장치가 들어있는 것이 보입니다! 그 속에서 누군가의 목소리가 잡음에 섞여서 들립니다.","\"제 이름은 오글소프... 제 유도 로봇이 불시착했는데 찾아주면 사례를 할게요! 이 탐지기를 로봇에게 가져가세요. 다행이네요, 방금 로봇의 현재 위치를 산출해 보았는데 타나리스에 있는 모래지옥 협곡 부근에 추락한 것 같아요!\""}, {"오글소프의 유도 로봇에게 조난 탐지기를 가져가야 합니다."}},
[353] = {"스톰파이크에게 소포 배달", {"스톰파이크 씨는 존경받는 드워프 일원이며 전투 감각이 뛰어나고 무기를 고르는 안목도 대단한 것으로 유명하다오. 이렇다 보니 아이언포지에서 그링거 스톰파이크 씨가 내게 무기를 만들어 달라고 주문한 것도 전혀 놀랄 일이 아니지.","무기는 다 만들었는데... 스톰파이크 씨가 저 멀리 모단 호수에 있어서 말이오. 혹시 북쪽으로 여행할 계획이 있으면 이 물건을 나 대신 배달해 주지 않겠소?","스톰파이크 씨에게 받은 마지막 전갈에 의하면 모단 호수의 북쪽 경비탑에서 근무하고 있다고 하오."}, {"모단 호수에 있는 산악경비대 스톰파이크에게 스톰파이크의 소포를 전달해야 합니다."}},
[354] = {"가문의 몰살", {"아가만드 가문은 티리스팔 숲에서 가장 부유한 가문이었지. 역병이 닥치지 전에는 나도 그들의 제분소에서 일했었고 말이야.","스컬지가 처음 공격해 왔을 때 아가만드 가문은 가택을 요새화하고 피고용인들을 설득해서 함께 방어하게 했다네. 그러나 적어도 우리는 충직한 바보들이었어...","그런데 아가만드 가문이 우리를 언데드로 만들어 버렸지뭔가! 그리고 그들은 이제 스컬지의 앞잡이가 되었다네!","역병에 넘어간 아가만드 가문을 처단하여 포세이큰에 봉사하게. 그들의 유골을 내게 가져와 주게나."}, {"브릴에 있는 콜만 파딩에게 그레고르의 유골, 니사의 유해, 투르만의 유골을 가져가야 합니다."}},
[355] = {"세브렌과의 대화", {"<class>님께서 아가만드 가문의 유골을 가져다준 덕분에 이제 복수의 욕망은 채워졌습니다. 하지만 아가만드 밀농장은 저뿐만 아니라 모든 포세이큰들에게 위협이 되고 있습니다.","아가만드 가문을 상대로 이룬 <class>님의 공적을 집정관 세브렌님께 말씀드렸더니 만나 보고 싶어하십니다. 그분께 가 보시기 바랍니다."}, {"브릴에 있는 집정관 세브렌과 대화해야 합니다."}},
[356] = {"후방 경비", {"역병지대로부터 티리스팔을 방어하는 보루 수비대의 경계 태세가 철통 같긴 하지만, 간혹 스컬지가 몰래 경계를 통과하기도 하오.","정면의 적만 상대하면 뒤에서 공격을 당하거나 수비대에게 보급로가 차단당할 수 있다는 문제가 있어.","그래서 말인데 보루 방어를 좀 도와주지 않겠소? 동쪽으로 순찰을 나가 눈에 띄는 스컬지를 모두 처치해 주시오. 특히 동쪽에 있는 발니르 농장이 이 침략자들의 본거지가 되어 버린 것 같으니 그곳에 있는 스컬지들을 처치해 주시오."}, {"피투성이 좀비 8명과 떠도는 영혼 8명을 처치한 다음 초소로 돌아가 리니아에게 보고해야 합니다."}},
[357] = {"마술사의 정체", {"북쪽 청명 호수에 섬이 있는데 거기에 마술사가 한 명 살고 있지. 그가 자유의지를 가진 포세이큰이기는 하지만 다른 언데드들은 모두 스컬지 노예라고 여기는 데다, 그에게 접근하는 자는 누구든 졸개들을 시켜 공격한다네.","그가 강령술에 능통하다면 우리에게 큰 도움이 될 것이며 포세이큰의 일원이기에 어둠의 여왕님께 충성해야 마땅하네.","그의 정체와 능력을 알아내려면 그의 마법책이 필요해. 그의 은거지로 숨어들어 마법책을 훔친 다음 내게 가져다주게."}, {"언더시티에 있는 베소르 아이스샤드에게 마술사의 마법책을 가져가야 합니다."}},
[358] = {"묘지 도굴꾼", {"북쪽 가렌의 흉가의 남서쪽엔 묘지가 하나 있는데, 그 묘지는 역병이 처음 퍼졌을 때 안타깝게 죽은 수많은 티리스팔 주민들을 매장하기 위해 만들어진 곳일세.","이 묘지의 시체들은 지금까지 언데드가 되는 신세를 면해 왔는데 최근 스컬지가 썩은가죽 놀들을 보내 시체를 모아 그들의 군대로 만들려 하고 있어. 절대 이런 일이 일어나서는 안 되지!","자네의 임무는 두 가지네. 가렌의 흉가와 묘지에서 썩은가죽 놀들을 처치하고 그들에게 생명을 불어넣는 불변의 영액을 빼앗아 돌아오게나."}, {"썩은가죽 도굴꾼 8마리와 썩은가죽 싸움꾼 5마리를 처치해야 합니다.","이들에게서 불변의 영액 8개를 모은 후 브릴에 있는 집정관 세브렌에게 가져가야 합니다."}},
[359] = {"포세이큰의 의무", {"동쪽에 있는 죽음경비병 초소로부터 최근 상황에 대한 보고를 받아야 하네.","스컬지 병력이 보루를 빠져나와 티리스팔로 들어왔는지가 내 관심거리일세. 역병지대 변두리에 대한 경계를 유지하지 않으면 스컬지가 이 지역에 근거지를 구축할지도 몰라!","길을 따라 남쪽으로 이동한 후 세 갈래 길에서 동쪽 길로 가면 초소가 나오네. 죽음경비병 리니아와 이야기를 나눠보면 필요한 정보를 들을 수 있을 거야."}, {"죽음경비병 리니아와 대화해야 합니다."}},
[360] = {"집정관에게 돌아가기", {"이제 내가 알려준 정보들을 집정관 세브렌님께 보고해 주시오. 세브렌님께서 가능한 빨리 보고를 받길 원하실 것이니 최대한 서두르시오.","돌아가는 길에 혹시 스컬지가 눈에 띄거든 없애 버리도록 하시오. 우리 땅에서 그들이 활개치는 걸 볼 순 없으니까 말이오."}, {"브릴에 있는 집정관 세브렌에게 돌아가야 합니다."}},
[361] = {"배달되지 않은 편지", {"아가만드가의 장남인 그레고르 아가만드의 장남이 쓴 편지입니다. 수신자가 이베트 파딩으로 되어 있지만 아직 배달되지 못한 것이 분명해 보입니다."}, {"이베트 파딩을 찾아서 투르만 아가만드가 쓴 편지를 전해줘야 합니다."}},
[362] = {"제분소의 유령", {"데블린 아가만드는 아가만드 가문의 두 아들 중 막내였는데 형과 그렇게 다를 수 없었네. 형인 투르만은 키도 크고 상냥했지만 데블린은 약골에다 입은 매우 거칠었지.","그래서 아가만드 제분소에 역병이 퍼졌을 때 데블린이 제일 먼저 죽었다는 소식을 들었지만 인과응보라는 생각에 별로 놀라지도 않았다네. 아직도 아가만드 제분소로 가는 도로 근처에서는 그의 광기 어린 중얼거림이 들린다고 하더군.","나는 지금 아가만드 가문의 유골을 모으는 중인데 불운한 데블린의 유골이 탐나서 말이야. 그를 찾아 처치한 후에 유골을 내게 가져오게나."}, {"데블린을 처치한 후 그 유골을 브릴에 있는 콜만 파딩에게 가져가야 합니다."}},
[363] = {"각성의 시간", {"제때 깨어나서 반갑구려. 다른 이들과 함께 불 속에 던져 넣으려던 참이었는데 회복했군","나는 죽음의 종소리 마을의 무덤을 관리하고 있는 모르도라고 하오. 이제 당신은 더는 리치 왕의 노예가 아닌 포세이큰의 일원으로 다시 태어났소.","언덕 아래쪽으로 내려가다 보면 성당이 나올 거요. 거기에 있는 어둠의 사제 사비스 님과 얘기해 보시오. 그분이 포세이큰의 일원이 된 자로서 알아야 할 얘기를 자세히 들려주실 것이오.","이 무덤을 벗어나려거든, 계단을 올라가 지상으로 나가시오. 나가서 길을 따라 내려가면 사비스 님이 계신 성당을 찾을 수 있소."}, {"어둠의 사제 사비스와 대화해야 합니다."}},
[364] = {"넋 나간 좀비", {"우리 포세이큰은 지금 리치 왕의 스컬지 군대와 전쟁 중이라네. 놈들은 마법으로 소환된 언데드, 북쪽의 사악한 짐승들, 그리고 고통받는 유령들로 이루어진 군대야.","이 넋 나간 좀비들이 우리 마을 동쪽 지역을 침범했네. 반드시 놈들을 말살시켜야 해. 설사 그들이 우리의 옛 형제, 자매들이었을지 몰라도 이젠 리치 왕의 노예일 뿐일세. 가차없이 처치하도록 해."}, {"어둠의사제 사비스가 넋 나간 좀비 8명과 흉측한 좀비 8명을 처치해 달라고 부탁했습니다."}},
[365] = {"오염된 농장", {"이건 또 뭔가? 아, 풋내기 <class>|1이로군;로군;! 어둠의 여왕님께 능력을 증명해 보이고 싶다면 포세이큰의 방식부터 배우는 게 좋을 거야.","서쪽에 농장이 하나 있네. 인간들이 마치 썩은 시체 위에 곰팡이처럼 그곳을 오염시키고 있지. 게다가 주변에 있는 탑은 붉은십자군 녀석들이 순찰하고 있어.","저 오합지졸들에게 한 수 가르쳐 주고 저들이 소중하게 여기는 호박을 10개 훔쳐 오게. 10개를 다 모으면 브릴에 있는 연금술사 요한에게 가 보라고."}, {"죽음의 종소리 마을 북쪽에 있는 농장에서 티리스팔 호박을 10개 훔친 다음 브릴에 있는 연금술사 요한에게 가져가야 합니다."}},
[366] = {"마법책 반납", {"오래전 군터와 나, 그리고 지금은 스컬지 강령술사가 된 까마귀발톱 쑤울은 달라란에 있는 마법사 학회인 키린 토에서 함께 공부하는 사이였네.","군터와 나는 모두 역병에 휘말렸었는데, 난 그가 죽었거나 리치 왕 수하의 귀족이 되었겠거니 생각하고 있었지. 그가 자유로운 몸이라는 사실을 알게 되어 기쁘군.","그를 반드시 우리 편으로 끌어들여야 하네. 이 마법책을 그 친구가 보면 나 역시 리치 왕으로부터 자유로운 몸이라는 걸 그에게 보여 주도록 책에 주문을 걸어 놓았네. 그가 마법책을 본 후엔 우리에게 동참했으면 좋겠군."}, {"군터의 은거지로 가서 군터의 마법책을 돌려줘야 합니다."}},
[367] = {"새로운 역병", {"실바나스 여왕님께서 왕립 연금술 학회 총회를 소집하셨소. 어둠의 여왕님께서는 우리의 지식과 새로 발견된 마법을 결합하면, 아서스를 파멸로 이끌 방법을 찾을 수 있을 거라 믿고 계시오. 그래서 우리에게 아제로스에 알려진 그 어떤 병보다도 치명적인 새로운 역병을 만들어내라고 명령하셨지. 이 새로운 역병은 아서스의 스컬지 군대를 파멸로 이끌게 될 것이오.","연구해 보니 새로운 역병을 만드는 데 짐승의 피가 핵심적인 요소가 될 수도 있을 것 같더군. 내 이론을 시험해 볼 수 있도록 암흑사냥개 피 5병을 가져다주시오."}, {"브릴에 있는 연금술사 요한이 암흑사냥개 피 5병을 가져다 달라고 했습니다."}},
[368] = {"새로운 역병", {"당신이 재료를 모아 오는 동안 몇 가지 실험을 해 봤는데 이 새로운 역병을 퍼뜨리기 위해선 몇 가지 재료가 더 필요하다는 걸 알게 됐소. 운 나쁘게 실험용으로 발탁된 녀석들 몇 명을 독살하는 건 쉬운 일이지만 온 세상에 역병을 퍼뜨리는 건 좀 더 복잡하단 말이지.","북쪽이나 서쪽에 있는 해안으로 가면 멀록들이 있을 것이니 그들을 처치하고 썩은지느러미멀록 비늘 5개를 가져다주시오."}, {"브릴에 있는 연금술사 요한이 티리스팔 숲에 있는 썩은지느러미멀록 비늘 5개를 가져다 달라고 부탁했습니다."}},
[369] = {"새로운 역병", {"당신이 비늘을 모아 오는 동안 오래된 서적에서 고대의 역병이 수천 명의 무고한 희생자를 냈다는 사실을 발견했지. 그 역병의 치명적인 병원체가 검은그물거미의 독액에 들어 있다고 하는군.","이 실험을 끝낼 수 있도록 흉포한 검은그물거미의 독을 가지고 오시오. 독액의 병원체가 내가 새로 만든 조제약과 잘 어울리는지 봐야겠소. 소문에 의하면 그 거미는 티리스팔 숲 동부 지역에 있다고 하오."}, {"브릴에 있는 연금술사 요한이 흉포한 검은그물거미의 독 4개를 가져다 달라고 했습니다."}},
[370] = {"붉은십자군과의 전쟁", {"집행관 아렌이 보내 온 전략 문서에는 이 땅에서 인간을 내쫓는 데 필요한 모든 정보가 들어 있네. 이 정보에 따르면 붉은십자군이 페린 대장과 그의 부대를 브릴 남서쪽의 폐허가 된 탑에 배치한 걸로 되어 있군.","페린과 붉은십자군 광신도 3명, 선교사 3명을 처리한 후 보고하도록 하게."}, {"브릴에 있는 집행관 자이건드가 대장 페린, 붉은십자군 광신도 3명, 붉은십자군 선교사 3명을 처치해 달라고 했습니다."}},
[371] = {"붉은십자군과의 전쟁", {"우린 여전히 붉은십자군과 전쟁 중이고 적들은 더 강해지고 있다네. 집행관 아렌에게서 받은 보고에 의하면 붉은십자군이 대장 바콘의 지휘 아래 티리스팔 남동부 발니르 농장 근처에 있는 폐허가 된 탑에서 공격해 오고 있다고 하네.","가서 바콘과 붉은십자군 수사를 5명 처치하면 틀림없이 붉은십자군이 크게 흔들릴 것이야!"}, {"브릴에 있는 집행관 자이건드가 대장 바콘과 붉은십자군 수사 5명을 처치하라고 명령했습니다."}},
[372] = {"붉은십자군과의 전쟁", {"붉은십자군이 파올의 안식처를 지나 티리스팔 북쪽, 그러니까 여기서 북동쪽에 있는 폐허가 된 탑에서 공격해 오고 있네. 정보에 의하면 대장 멜라케라는 무자비한 사령관이 이 사악한 부대를 지휘하고 있다고 하는군.","이 임무 역시 자네가 좀 맡아줬으면 하네. 어둠의 여왕 폐하의 이름으로 멜라케와 그의 두 경호원을 처치해 주게."}, {"브릴 마을에 있는 집행관 자이건드가 대장 멜라케와 붉은십자군 경호원 2명을 처치해 달라고 했습니다."}},
[373] = {"부치지 않은 편지", {"에드윈 밴클리프의 몸을 수색하던 도중 다른 물건들과 함께 부치지 않은 편지 한 통을 발견합니다. 편지의 수신인은 대성당 광장, 시청 내 도시계획 사무소에 있는 스톰윈드의 도시 건축가 바로스 알렉스턴으로 되어 있습니다.","편지는 최근에 작성되어 봉인된 것으로 보입니다."}, {"스톰윈드에 있는 도시 건축가 바로스 알렉스턴에게 편지를 전달해야 합니다."}},
[374] = {"죽음의 증거", {"집행관 자이건드 님께서 자네가 붉은십자군들을 소탕 중이라고 하시더군. 티리스팔 숲은 붉은십자군 천지야.","어둠의 여왕님의 부관이신 바리마트라스 님께서는 아제로스의 쓰레기인 인간 종족을 모두 쓸어 버리고 싶어하시네. 특히 이곳 티리스팔 숲을 더럽히고 있는 붉은십자군들을 말이야.","어서 나가서 그들을 처단해 주게. 바리마트라스 님과 어둠의 여왕님을 향한 충성의 증거로 붉은십자군 반지 10개를 가져 오게."}, {"브릴에 있는 죽음경비병 버제스에게 붉은십자군 반지 10개를 가져가야 합니다."}},
[375] = {"죽음의 한기", {"흐... 너무 추워요. 제 혈관 속에는 끔찍한 역병이 마치 얼음 뱀처럼 기어다니고 있답니다. 곧 저도 곧 이성을 잃게 되겠죠. 비록 저주받은 운명이라 해도 제가 어둠의 여왕님을 섬기는 것을 막을 수는 없어요. 저는 부름에 따라 실바나스 님의 강력한 언데드 군대를 위해 시체 운반 가방을 수선해 왔어요.","이젠 너무 추워 손이 떨릴 지경이에요. 그늘박쥐를 잡아 그늘박쥐 모피 5개를 구하고 상인에게서 굵은 실타래 몇 개를 사 오면 담요를 기워서 덮을 수 있을 텐데 말이에요. 제가 맡은 임무를 계속 수행할 수 있도록 좀 도와 주세요."}, {"브릴에 있는 그레첸 데드마에게 그늘박쥐 모피 5개와 굵은 실타래 1개를 가져가야 합니다."}},
[376] = {"저주받은 존재", {"제가 맡은 일은 부상당한 전사들을 돌보거나, 갑옷과 의복을 짓거나, 어둠의사제 사비스님이 필요로 하시는 모든 일을 돕는 거랍니다.","보아하니 당신도 그분을 도와... 좀비들을 처치하게 될 것 같군요. 제 생각엔 틀림없이 당신을 보호해줄 방어 장비가 필요할 테니 도와 드리도록 하죠. 그런데 재료가 되는 짐승 앞발과 날개가 다 떨어져서 말이에요. 좀 구해다 주시면 갑옷을 만들어 드릴게요. 남쪽으로 가면 늑대와 박쥐들이 많이 있을 거예요."}, {"수련사제 엘레스가 승냥이 앞발 6개와 그늘박쥐 날개 6개를 구해다 달라고 부탁했습니다."}},
[377] = {"죄와 벌", {"귀족 평의회는 이본로크 경의 판결대로 덱스트렌 워드를 처형하지 않고 스톰윈드에 수감하도록 했으니, 그늘숲 주민을 멸시하는 것도 모자라 이제 어둠골까지 무시하는 처사가 아니면 무엇이겠소?","덱스트렌 워드는 공동 묘지의 시체를 모벤트 펠에게 팔아 넘긴 죄로 잡혔으니 죽어 마땅한 중죄인이 아니오? 하지만 스톰윈드에서는 아직 놈이 자기네 죄수라고 억지를 부리고 있소.","스톰윈드 지하감옥에 있는 덱스트렌 워드를 암살한 다음 놈의 장갑을 내게 가져 오시오. 그러면 내 당신에게 보상하리다."}, {"어둠골의 원로 밀스타이프가 덱스트렌 워드를 처치한 후 그의 장갑을 가져다 달라고 했습니다."}},
[378] = {"격노", {"마그니 폐하에 대한 당신의 충성심은 이미 잘 알고 있소. 그리고 당신도 나만큼이나 저 검은무쇠 드워프 쓰레기들을 증오하는 것 같더군.","내가 직접 완수하고 싶은 임무가 있지만, 롱브레이드 님 곁을 지켜야 하는 처지라 당신이 대신 좀 맡아 주었으면 하오. 로고가 수집한 정보에 의하면 캄 딥퓨리가 탄돌 교각 공격을 획책한 주범임이 밝혀졌소. 놈의 음모로 롱브레이드 님께서는 가족을 잃었지.","딥퓨리는 정치적인 이유로 스톰윈드 지하감옥에 수감돼 있소. 롱브레이드 님을 위해 그를 처치해 주시오!"}, {"던 모드르에 있는 모틀리 가마슨이 캄 딥퓨리의 머리카락을 가져다 달라고 했습니다."}},
[379] = {"갈증 해소", {"동력원이 필요하다면야 내가 도와줄 수 있지. 내게 필요한 게 뭔지 아나? 바로 마실 거야! 내게 사막유랑단 물주머니 5개를 가져오면 당신에게 4711-FTZ를 주지.","어디... 그 사막유랑단 물주머니들을 어디서 찾을 수 있을 것 같나? 동쪽에 있는 사막유랑단 유목민 한 명에게 가서 하나 달라고 해 보시지. 그리고 그 결과를 내게 알려달라고."}, {"가젯잔에 있는 선임기술자 빌지위즐에게 사막유랑단 물주머니 5개를 가져가야 합니다."}},
[380] = {"검은그물 거미굴", {"우리의 가장 큰 난관 중 하나는 생존에 필요한 천연자원을 얻는 것일세. 얼라이언스의 세력이 막강했던 시절조차 금이 부족했었는데 지금 같은 시기에야 오죽하겠나.","북서쪽에 거미들이 득실대는 금광이 있네. 광산에 거미들이 기어다니는 통이라 금을 채굴할 수가 없다네. 게다가 임무를 맡길 만한 인원도 부족한 상황이라 채굴량이 적은 상황이야.","자네가 가서 도와줄 만한 일이 있는지 봐주게."}, {"집행관 아렌이 새끼 검은그물거미 8마리와 검은그물거미 5마리를 처치해 달라고 했습니다."}},
[381] = {"붉은십자군", {"자네가 도와준 덕분에 광산 쪽 일은 잘 진행되고 있는 것 같군. 수고해 줘서 정말 고맙네. 이젠 좀 다른 문제에 신경을 써야 하네.","정찰병 보고에 따르면 붉은십자군 파견대가 죽음의 종소리 마을의 남동쪽에 야영지를 설치하고 있다고 하네. 붉은십자군은 우릴 위협하고 있는 인간들의 조직인데, 이들은 스컬지든 포세이큰이든 모든 언데드를 파멸시킬 때까지 멈추지 않을 거야. 우리가 당하기 전에 먼저 공격해야 하네!","조심하게, 사악한 신앙 때문인진 몰라도 저들은 아주 위험해."}, {"붉은십자군 순례자와 붉은십자군 수습마법사에게서 붉은십자군 완장 12개를 구해, 집행관 아렌에게 가져가야 합니다."}},
[382] = {"붉은십자군 전령", {"브릴에 계신 내 상관께서 보내신 소식에 따르면 붉은십자군 중 한 명이 붉은십자군 수도원에서 이곳 죽음의 종소리 마을로 파견됐다고 하네. 정보에 따르면 그 전령이 중요한 정보를 가지고 있다고 하는데 우리가 아는 건 여기까지야.","이 일은 자네가 적임자야. 그 전령을 찾아내 지니고 있는 정보를 내게 가져오도록 하게."}, {"붉은십자군 야영지에 있는 전령 메븐 코갈을 처치한 다음 입수한 정보를 집행관 아렌에게 가져가야 합니다."}},
[383] = {"중요한 정보", {"이 정보를 브릴에 있는 내 상관이신 자이건드 집행관님께 보내야 하네. 이 정도 정보라면 붉은십자군에 결정적인 타격을 줄 수 있을 거야. 이곳 죽음의 종소리 마을에서도 자네의 도움이 필요하긴 하지만 말이야... 자이건드 님께 이 정보를 전달할 자도 필요하고, 그분이 자네한테 더 적합한 임무를 맡겨 주실 거라 믿네.","죽음의 종소리 마을을 나가 북쪽으로 길을 따라가게. 티리스팔로 접어들면 곧 교차로가 나오는데 거기서 동쪽으로 돌아 계속 동쪽 길을 따라가도록 해. 길을 따라가다 보면 버려진 장원을 지나가게 될 거야."}, {"브릴에 있는 집행관 자이건드에게 붉은십자군 문서를 전달해야 합니다."}},
[384] = {"맥주로 양념한 멧돼지 갈비", {"우리 가게 손님들이 가장 많이 찾는 건 맥주로 양념한 멧돼지 갈비지! 그런데 내게 멧돼지 고기를 대주던 이쪽 사냥꾼 친구가 얼라이언스 전방에서 벌어지는 전투를 돕기 위해 왕국 군대에 차출되었다오.","당신이 좀 도와주겠소? 멧돼지 갈비 조금과 선술집에서 랩소디 몰트 한 잔을 사오면 우리 가문에서 대대로 내려오는 맥주로 양념한 멧돼지 갈비 조리법을 알려 주도록 하지. 시식용으로 갈비 요리도 해 주고말고! 잊지 마시오, 맛의 비밀은 몰트에 있다오!"}, {"카라노스에 있는 라그나르 썬더브루가 부드러운 멧돼지 갈비 4개와 랩소디 몰트 한 잔을 구해다 달라고 했습니다."}},
[385] = {"악어 사냥", {"많은 사냥꾼들이 악어를 사냥하기 위해 이곳 모단 호수로 모여든다네. 악어 껍질은 옷이나 갑옷 등에 쓰이고 그 고기도 아주 잘 팔리거든.","우리도 악어를 거래하긴 하지만 많은 양은 아니라네. 악어가 워낙 사나운데다가 요즘에는 호수 안의 섬 곳곳에 틀어박혀 지내서 말이야. 그렇다고 포기는 말게. 이 짐승과 싸워 보는 건 좋은 경험이 된다네.","어때, 한번 해 보겠나?"}, {"원정순찰대 오두막에 있는 마렉 아이언하트에게 악어 고기 5개와 악어 껍질 6장을 가져가야 합니다."}},
[386] = {"사필귀정", {"흉악범 타고르는 가스일조그 밑에서 일하는 최고의 사형집행인이었습니다. 추악한 오크라는 것은 둘째치고 아주 잔인하기 짝이 없는 자였지요. 그의 변덕에 따라 왕국을 지키기 위해 용감하게 싸운 용사들이 고통을 받아야 했답니다. 지금은 스톰윈드 지하감옥에 갇혀 있으며, 사형 선고를 받은 상태입니다. 하지만 뭔가 일이 잘못 돌아가고 있습니다. 귀족 하나가 그자의 처형을 연기했답니다. 분명히 뭔가 사악한 뒷거래가 있는 게 분명합니다.","이 참에 흉악범 타고르의 숨통을 끊어 주시기 바랍니다. 지하감옥으로 가서 농간이 펼쳐지기 전에 그를 없애 버려야 합니다."}, {"흉악범 타고르를 처치한 후 그 증거로 그의 머리카락을 호숫골에 있는 경비병 베르턴에게 가져가야 합니다."}},
[387] = {"폭동 진압", {"폭동이 일어났네! 이를 진압해야 하니 제발 날 좀 도와 주게. 지하감옥이 죄수들 손에 넘어갔다는 소문이 바깥으로 퍼지기라도 하면 난 분명히 해고되고 말걸세! 생각만 해도 끔찍한 일이야. 하지만 지금 중요한 건 우선 죄수들의 항복을 받아내 스톰윈드를 안전하게 보호하는 것이네.","폭동에 대한 대가는 죽음뿐! 이 일탈자들의 일부를 처단해 주게! 그러면 이곳을 다시 통제할 수가 있을걸세."}, {"스톰윈드에 있는 교도소장 델워터가 지하감옥에서 복역수 10명, 무기징역수 8명, 반역자 8명을 처치해 달라고 했습니다."}},
[388] = {"피의 색", {"도저히 이 비통한 마음을 억누를 길이 없어요. 내 손자 맥은 정말 좋은 아이였는데... 나이도 열네 살밖에 되지 않았어요. 군대가 전방으로 출병한 후로 맥은 도시를 지키고자 무기를 들었죠. 그리고 데피아즈단이 갇혀 있는 지하감옥에서 경비병으로 복무하게 되었답니다. 거기서 몇몇 죄수가 양모 담요를 찢어 패거리의 상징인 복면을 만드는 것을 질책했다는 이유만으로 결국 등에 칼을 맞고 말았어요.","난 복수를 원합니다. 그들이 그토록 소중히 여기는 양모 복면을 10개 가져다주세요. 그 복면은 피처럼 붉은색이랍니다."}, {"스톰윈드에 있는 니코바 라스콜이 붉은 양모 복면을 10개를 모아 달라고 했습니다."}},
[389] = {"바질 스레드", {"밴클리프와 난 석공 길드의 회원이었습니다. 우리의 주요 계획은 전쟁 후의 스톰윈드를 재건하는 것이었지요.","그런데 맡은 일을 완수한 후 사기를 당하고 말았습니다. 귀족들이 작업에 대한 정당한 대가를 지급하지 않으려고 한 것이지요. 이후 국가에서 석공 길드의 몇몇 수석 건축가에게 일을 맡기려 했으나 밴클리프는 석공 길드에 대한 신의를 지키기 위해 이를 거절했습니다. 그 후 그는 한바탕 소동을 일으킨 후에 복수를 맹세하며 도시를 떠났지요.","밴클리프의 부관인 바질 스레드가 그의 자세한 계획을 알고 있을 겁니다. 바질 스레드는 현재 지하감옥에 죄수로 감금되어 있습니다."}, {"스톰윈드 지하감옥에 있는 교도소장 델워터와 대화해야 합니다."}},
[391] = {"감옥 폭동", {"한 가지는 분명히 밝히지. 난 자넬 믿지 않아. 하지만 상황이 이렇다 보니 말이야, 자네 하나 때문에 일이 더 악화될 리는 없겠지만...","자, 그러면 이렇게 하세. 저리 내려가서 스레드와 얘기를 하게. 그리고 정말 자네가 진정 우리 편이라면 그를 처치하고 머리카락을 내게 가져오게.","만에 하나 자네가 스레드와 한패라면 돌아오지 않겠지만... 대신 나중에 만나면 나머지 친구들과 자네는 저 세상 구경을 할 테니 명심하게."}, {"바질 스레드를 처치하고 그 증거로 그의 머리카락을 감옥에 있는 교도소장 델워터에게 가져가야 합니다."}},
[392] = {"수상한 면회인", {"스레드가 지하감옥에 들어온 후 처음 몇 년간은 아무도 면회를 오지 않았네. 그래서 난 결사단에서 스레드가 더는 쓸모없게 되었다고 생각했지.","그런데 몇 달 전부터 상황이 완전히 바뀌었네. 1주일에 한두 번 정도 정기적으로 면회인이 찾아오기 시작한 것이지. 면회인은 이상한 느낌을 주는 조용한 성격의 남자였네. 심증은 있었지만 서류는 이상이 없었네.","그자의 면회증에는 매릭이라는 이름이 적혀 있었고 인상착의는 여기 적힌 것과 같네. 아무튼 그자가 더 이상 스레드에게 면회올 일은 없겠군."}, {"스톰윈드에 있는 바로스 알렉스턴에게 스레드의 방문객에 대한 봉인된 인상서를 전해야 합니다."}},
[393] = {"과거의 그림자", {"이제 더 알려드릴 단서가 없습니다. 그러나 도움이 될 만한 사람을 한 명 알지요. 더구나 그 사람은 내게 빚을 진 일이 하나 있어서... 흠, 지금은 그런 얘기를 할 때가 아니군요. 어쨌든 구 시가지에 있는 병영에서 SI:7의 단장 마티아스 쇼를 찾아가 보십시오. 쇼는 도시에서 일어나는 대부분의 비밀스럽고 음흉한 일에 관련되어 있답니다.","쇼가 모르면 아무도 모르는 겁니다."}, {"스톰윈드에 있는 단장 마티아스 쇼에게 스레드의 방문객에 대한 봉인된 인상서를 전해야 합니다."}},
[394] = {"잔인한 자의 최후", {"구 시가지로 가서 쇼 단장을 찾으시기 바랍니다. 가서 당신이 라스코발의 위협을 제거한 사실과 그가 꾸몄던 음모를 알아냈다는 소식을 전하도록 하십시오. 마티아스는 스톰윈드 시민 중에 영웅이 또 있다는 사실을 알면 자랑스러워할 겁니다.","그 친군 틀림없이 당신을 왕자님이나 다른 귀족과 만나게 해줄 것입니다. 단 술은 너무 많이 마시지 마세요. 귀족들은 이 도시를 자기들 마음대로 통치할 수 있다고 생각하지만 귀족들이 어찌하지 못하는 일들도 있는 것이지요."}, {"스톰윈드에 있는 마티아스 쇼와 대화해야 합니다."}},
[395] = {"데피아즈단의 종말", {"아, 자네가 떠나 있는 동안 알렉스턴이 왔다 갔다는 걸 깜빡했군. 자네와 얘기를 하고 싶다고 하더군. 분명히 데피아즈단의 사태에 대해서 귀족들께 보고하려고 하는 것일게야.","한 가지 조언을 하도록 하지. 누군가 내게 묻거든 모든 것을 자세히 설명해 주도록 하겠네. 물론 자네가 라스코발 경을 처치했다는 것만 빼고 말이야. 날 믿게나. 이런 일에는 이골이 난 사람이라네.","그리고 자네... 트리아스에 대한 얘기는... 아무에게도 하지 말게나."}, {"스톰윈드에 있는 바로스 알렉스턴과 대화하십시오."}},
[396] = {"국왕 알현", {"국왕께 보낼 보고서를 준비했습니다. 지금까지 당신이 발견한 것들을 고려해 볼 때 당신이 이 보고서를 전할 적임자라고 생각합니다. 걱정하지 마십시오, 국왕께서도 분명히 스톰윈드의 백성으로서 의무를 다한 당신을 칭찬하실 테니까 말입니다.","알현 일정이 곧 잡혀 있으니 서두르는 게 좋겠습니다. 스톰윈드 왕궁까지는 약간 머니 말입니다."}, {"스톰윈드 왕궁으로 가서 국왕 바리안 린에게 올릴 알렉스턴의 데피아즈단에 대한 보고서를 전달해야 합니다."}},
[397] = {"수고의 대가", {"위쇼크 경이 술을 맛있게 먹었다고 하는군, 흐흐.","그동안 수고 많았소. 당신 덕분에 스톰윈드의 흑마법사들이 방해받지 않고 계속 하던 일을 할 수 있게 되었소. 수고한 대가로 특별한 선물을 준비해 두었지. 내 하인, 즈기에게 얘기해서 선물을 받아 가도록 하시오."}, {"검은발톱 자데스의 하인인 즈기와 대화하여 보상을 받으십시오."}},
[398] = {"구더기눈 현상 수배", {"구더기눈 현상 수배!","저 사악한 짐승인 놀 종족의 구더기눈은 우리에게 저지른 범죄의 대가로 처단 받아 마땅하다. 이 비열한 악당은 리치 왕의 군대에 사용할 시체들을 훔치고 있다. 그가 마지막으로 목격된 곳은 브릴 북쪽의 가렌의 흉가 근처이다.","브릴 마을에서는 구더기눈을 처치하는 자에게 현상금을 지급할 것이다. 이 악당을 처치한 자는 그의 앞발을 집행관 자이건드에게 제출하면 된다.","자비는 약자를 위한 것일 뿐임을 명심하라."}, {"구더기눈을 처치한 후 브릴에 있는 집행관 자이건드에게 돌아가 구더기눈의 앞발을 증거로 제출하고 보상을 받아야 합니다."}},
[399] = {"초라한 시작", {"서부 몰락지대의 농장에서 일하던 어린 시절이 까마득한 옛날일 같군요. 흔히들 과거로 돌아갈 수 없다고 하지 않습니까? 정말 맞는 말이더군요. 특히 내 경우에는 더욱 그렇답니다. 우리 가족의 고향은 다 타버리고 방랑자들 소굴이 되어 버렸답니다.","전에 아버지와 그 당시 두고온 물건들에 대해 이야기한 적이 있었는데, 아버지가 사주신 첫 번째 선물인 나침반도 그중 하나였지요. 아버지는 그것들을 가지고 올 수 없어서 안타깝다고 말씀하시면서 농장 어딘가에 숨겨져 있을 거라고도 하셨습니다.","감시의 언덕 서쪽으로 가면 알렉스턴 농장이 있는데, 혹시 그곳으로 가서 내 나침반을 찾아서 가져다줄 수 있겠습니까?"}, {"서부 몰락지대에 있는 바로스 알렉스턴의 집으로 가서 나침반을 찾은 다음 스톰윈드의 대성당 광장에 있는 바로스 알렉스턴에게 가져가야 합니다."}},
[400] = {"스틸그릴이 주문한 공구", {"벨딘 스틸그릴이라는 친구가 있는데 드워프 최고의 증기 전차 기술자라고 할 수 있지. 그 친구는 정비소를 하나 운영하고 있는데 공구를 워낙 험하게 다루어서 내 속을 좀 썩인다네. 만능 스패너를 보내주는 족족 망가뜨려서 말이야.","그 친구가 최근 주문한 공구를 이제 겨우 다 만들었네. 이 물건을 그에게 배달해 주면 분명히 사례를 할 거야.","스틸그릴의 정비소는 카라노스의 북동쪽에 있네. 노련한 증기 전차 조종사들이 자주 출입하는 곳이니까 그곳에 가면 다른 일을 구할 수도 있을 거야."}, {"벨딘 스틸그릴에게 스틸그릴의 공구를 전달해야 합니다."}},
[401] = {"시라의 번역 기다리기", {"편지를 거의 다 번역했습니다. 잠시 후에 다시 찾아오면 끝나 있을 겁니다."}, {"잠시 기다렸다가 시라 본인디와 다시 대화해야 합니다."}},
[403] = {"썬더브루 맥주통", nil, nil},
[404] = {"더러운 임무", {"스컬지가 티리스팔 숲에 침투하여 브릴 서쪽 지역에 있는 오래된 다리 근처를 완전히 오염시켰네.","그곳으로 가서 썩어 문드러진 망자와 심하게 훼손된 시체들을 눈에 띄는 대로 처치한 다음 내게 썩은 발톱을 가져오게. 그리하면 죽음의경비대에서 보상이 있을 것이네."}, {"브릴에 있는 죽음의경비병 딜링거에게 썩은 발톱 7개를 가져가야 합니다."}},
[405] = {"회개한 마술사", {"남쪽 언더시티에 계신 강력한 고위 마법사 베소르 아이스샤드 님께서 스컬지를 상대할 수 있는 실력 있는 요원을 보내 달라고 명하셨네. 그래서 자네를 보내려고 하는데 말이야.","이 명령서를 베소르 님께 드리면 임무를 내리실 걸세. 정확한 내용은 나도 모르지만 대충 듣기로는 마음이 흔들리고 있는 마술사를 포섭하는 것과 관련된 일이라고 하니 위험한 일이 될 거야.","단단히 준비하도록 하고 절대로 실패해서는 안 된다는 것을 명심하게. 베소르 님을 찾으려면 언더시티의 마법 지구로 가보도록 하게."}, {"언더시티에 있는 베소르 아이스샤드에게 세브렌의 명령서를 보여 줘야 합니다."}},
[407] = {"오염된 농장", {"당신이 가져온 호박들은 겉으론 그냥 호박이지. 하지만 북쪽에서 전진해 오는 아서스의 진군을 막아내고, 남쪽에서 점점 늘어나고 있는 인간의 수를 줄이려면 우리 언데드의 잠재된 능력을 모두 발휘해야 하오.","약간의 재주만 발휘하면 평범한 호박을 어둠의 여왕님의 위대한 계획에 큰 도움이 되는 재료로 만들 수 있소. 방금 이 호박에 내 최신 처방을 했으니 이제 꽤 쓸만한 물건이 될 것이오.","아직 붉은십자군 광신도 중 한 명이 포로로 잡혀서 교수대끝 선술집 지하실에 갇혀 있을 거요. 이 호박을 그 얼간이에게 가져다주시오."}, {"사로잡힌 붉은십자군 광신도에게 줄무늬 호박을 가져가야 합니다. 교수대끝 선술집 지하실에 갇혀 있습니다."}},
[408] = {"아가만드가 납골당", {"자네도 알다시피 아가만드 제분소 지역이 스컬지의 본거지가 되었네. 이들의 주요 거점은 그 지역 지휘관인 대장 다르골의 본부가 있는 아가만드가 납골당이야. 그는 강령술을 통해 아가만드 가문의 조상을 언데드로 만들어 우리를 공격하려고 계획하고 있네. 절대로 그런 일이 있어서는 안 돼. 자네가 다르골의 계획을 막아 주기 바라네.","아가만드가 납골당으로 가서 언데드로 되살아난 아가만드 조상을 해치우고 다르골의 해골을 내게 가져오게."}, {"울부짖는 아가만드 망령 8마리와 썩어가는 아가만드 시체 8마리를 처치해야 합니다.","대장 다르골을 처치하고 그의 해골을 브릴에 있는 집정관 세브렌에게 가져가야 합니다."}},
[409] = {"동맹의 증거", {"자네가 리치 왕의 부하가 아니라고 주장하고 있는데다, 베소르가 이 책에 불어넣은 마법이 그 말이 사실임을 증명해 주는군. 하지만 자네가 스컬지의 적이라는 것을 증명해야 믿을 수 있을 것 같군.","이 섬 남쪽 끝에 또 다른 조그만 섬이 있는데 그곳에 오래된 피투성이의 제단이 있네. 강력한 스컬지 요원, 릴리스 네파라가 사용하는 것이지.","여기 있는 상자에서 부름의 양초를 가지고 가서 제단 위에 이 양초를 올려놓고 불을 붙이면 릴리스가 깨어날 걸세. 릴리스를 처치하면 자네가 리치 왕의 졸개가 아니라는 것을 믿어 주지."}, {"부름의 양초를 획득해야 합니다.","릴리스 네파라를 소환하여 처치해야 합니다.","그 후에 섬에 있는 군터에게 돌아가도록 하십시오."}},
[410] = {"잠든 망령", nil, nil},
[411] = {"회개한 마술사의 귀환", {"이 지옥의 보석을 내 오랜 친구인 베소르에게 가져다주게. 이것으로 내가 그와 교신할 수 있게 될 걸세. 아.. 그에게 할 말이 무척 많군."}, {"언더시티에 있는 베소르 아이스샤드에게 지옥의 보석을 가져가야 합니다."}},
[412] = {"작전명 재결합", {"놈리건으로 되돌아가서 방사능에 오염된 노움들을 치유할 방법을 계속 찾아봐야 해요.","그렇지만 카라노스에 있을 수밖에 없었답니다. 제 고향인 이 마을을 서리갈기 부족의 공격에 맞서 지키고 싶었거든요. 그래서 증기 전차들을 유지보수하는 일을 해 왔던 거예요.","무슨 이유인지는 모르겠지만, 서리갈기 청소부들이 수리를 마치는 데 필요한 자이로 기어를 수집하는 걸 좋아하는 것 같아요. 놈들은 마을 남동쪽에 있는 서리갈기 싸움터를 돌아다니고 있답니다. 그 기어들을 되찾는 걸 도와주시겠어요?"}, {"라즐 스프리스프로켓에게 자이로 기어 8개를 가져가야 합니다."}},
[413] = {"아지랑이 스타우트 맥주", {"산악경비대에 근무 중인 우리 형 발리브루한테 내가 만든 새 아지랑이 스타우트 맥주 맛을 보여줬으면 하는데... 형은 모단 호수의 경계 부근에 있는 두 개의 탑 중 남쪽 경비탑에서 근무하고 있을 거야.","형은 발리브루 가문 드워프들처럼 양조업을 하지는 않지만 술을 무척이나 좋아하니 분명 아주 기뻐할 걸세.","이 아지랑이 스타우트 맥주를 나 대신 좀 갖다 주겠나?"}, {"산악경비대 발리브루에게 아지랑이 스타우트 맥주통을 가져가야 합니다."}},
[414] = {"카드렐에게 맥주 배달하기", {"내 동생이 만든 아지랑이 스타우트 맥주, 좀 마셔보니 이거 정말 물건이군! 동료 산악경비원에게도 맛보여 줬으면 좋겠소. 그 친구 이름은 카드렐인데, 주로 모단 호수에서 텔사마를 통과하는 도로를 순찰하고 있소.","텔사마로 가려면 동굴을 따라 남동쪽으로 간 다음 길이 갈라지는 곳에서 북동쪽으로 가시오.","산악경비대 카드렐에게 이 아지랑이 스타우트 맥주통을 전해주고 맛이 어떤지 한번 물어봐 주시오!"}, {"산악경비대 카드렐에게 아지랑이 스타우트 맥주통을 가져가야 합니다."}},
[415] = {"레욜트의 새 술", {"브루날 마을의 레욜트 발리브루가 자네를 찾고 있다네. 요즘 만들고 있는 새로운 술에 대해 이야기하고 싶은 모양이더군. 뭐라더라... 자네가 도와 줄 수 있을 거라던데 말이야.","브루날 마을로 다시 돌아가 그의 이야기를 들어 보겠나? 아마 공짜 술은 배불리 얻어 먹을 수 있을 걸세."}, {"레욜트 발리브루와 대화하십시오."}},
[416] = {"코볼트 사냥", {"이곳 전지역에서 정말 심각한 문제가 일어나고 있소. 동굴쥐 코볼트들이 언덕으로 이사를 와서는 함부로 소굴을 만들어서 우리 땅을 더럽히고 있단 말이지!","그 코볼트들을 제거했으면 하오. 코볼트를 사냥하고 귀걸이를 모아오면 보상금을 드리겠소.","동굴쥐 코볼트들은 동쪽에 있는 은여울 광산 주변에 득실거린다오."}, {"알가즈 주둔지에 있는 산악경비대 스톰파이크에게 동굴쥐 귀걸이 8개를 가져가야 합니다."}},
[417] = {"조종사의 복수", {"힐델브의 일지에는 썩은발톱의 공격을 받았다고 적혀 있습니다. 힐델브는 그 짐승을 물리치려다 심한 상처를 입은 것 같습니다.","시체의 상태를 보니 썩은발톱이 나중에 다시 돌아와 그를 죽인 것 같습니다.","일지에 그의 마지막 유언이 남아 있습니다.","썩은발톱에게 복수를!"}, {"썩은발톱을 처치해야 합니다.","그러고 나서 놈의 발톱과 힐델브의 일지를 조종사 해머풋에게 가져다주어야 합니다."}},
[418] = {"텔사마 선지 소시지", {"이곳 텔사마에서는 식당이 항상 손님들로 북적대지요. 아이들도 들락날락 거리고, 밤에는 힘든 하루 일과를 마친 채굴꾼들로 붐비고 말이에요. 우리 집은 선지 소시지가 유명하거든요. 당신은 아직 먹어 본 적 없지요?","아마 그렇겠죠. 그런데 이곳에서는 열심히 일한 사람들만이 식사할 수 있어요. 당신이 아무리 멋진 <class>|1이라고;라고; 해서 예외가 될 수는 없죠.","곰 엉덩이살만 가져오세요. 나머지 재료들은 다 준비되어 있거든요. 제가 말한 재료만 가져오면 나머지는 이 비드라에게 맡겨 주세요!"}, {"텔사마에 있는 비드라 하스스토브가 곰 엉덩잇살 8개를 가져다 달라고 했습니다."}},
[419] = {"실종된 조종사", {"내 친구이자 동료 증기 전차 조종사 모리 힐델브가 언덕에서 실종됐네. 우리 둘은 좋은 화약을 만드는 데 필요한 희귀한 광석을 찾아 나섰는데, 힐델브가 전차를 가파른 언덕으로 몰고 가다가 그만 고장이 나 버렸지.","그래도 힐델브는 이 산에 그 광석이 분명히 있다면서, 내게 전차를 지키라고 하고선 광석을 찾으러 갔다네.","그리고 며칠이 지난뒤 그 언덕에서 짐승의 울음소리 같은 것이 들려왔었네. 모리가 강한 친구긴 하지만, 그래도 걱정이 되어서 말이야.","제발, 그를 찾아 주게."}, {"남부 관문 전초기지에 있는 조종사 해머풋이 조종사 힐델브를 찾아달라고 했습니다."}},
[420] = {"그렐린의 보고서", {"터널 안에 있는 사나운 트로그들은 누구든 가리지 않고 공격합니다. 하지만 당신 정도라면 큰 걱정은 없을 것 같습니다.","세니르씨를 만나려면 카라노스로 가야 합니다. 터널을 빠져나가서 길을 따라가다 보면 카라노스에 도착하게 될 테니 거기서 세니르를 찾아 보십시오."}, {"그렐린의 보고서를 카라노스에 있는 세니르 화이트비어드에게 전달해야 합니다."}},
[421] = {"실력 검증", {"실바나스 여왕님께서는 바리마트라스 님께 남쪽에 있는 인간과 드워프의 땅을 점령하라고 명하셨소. 그런데 지금은 그림자송곳니 성채의 저주받은 짐승이 되어 버린 그 멍청한 달라란의 협잡꾼 아루갈, 그 바보가 마법을 앞뒤 가지리 않고 남발하여 전략적으로 중요한 은빛소나무 숲의 성채를 난장판으로 만들고 말았소이다.","전투 경험이 많은 노련한 베테랑이 그곳에 가서 아루갈이 저질러 놓은 짓을 정리해야 하는데 말이지... 달분노 백발전사 5마리를 처치하여 당신의 능력을 한번 증명해 보여 주시오. 그 비열한 짐승들은 저 아래 언덕 밑의 길가에 종종 나타나곤 하오."}, {"공동묘지에 있는 달라 돈위버가 달분노 백발전사 5마리를 처치해 달라고 했습니다."}},
[422] = {"아루갈의 계략", {"아루갈이 애들 장난 같은 마법 능력으로 어떻게 해서 키린 토에서 그리 인정을 받게 되었는지는 나로서도 알 길이 없군.","우선은 아루갈이 사용한 마법이 정확히 무엇인지 알아내어 이를 풀고 어둠의 여왕님을 위해 은빛소나무 숲을 지켜내는 것이 중요하오.","아루갈이 제일 처음 기거한 곳은 저 아래 다리 바로 북쪽에 있는 밀농장이오. 죽음추적자 중 한 명이 마법책을 발견했지만 손에 넣지는 못했다는 보고가 들어왔소. 그 마법책들 중에서 아루갈의 치료법이라는 주문서를 찾아오시오."}, {"공동묘지에 있는 달라 돈위버를 위해 아루갈의 치료법을 구해야 합니다."}},
[423] = {"아루갈의 계략", {"아루갈의 치료법을 조사해 보니, 역시 내 불길한 예감이 딱 들어맞았더군. 역시 그자는 긴박한 시기에 키린 토를 대표할만한 인물도 아니거니와 달라란에서 큰일을 할만한 인물도 전혀 아니었소. 바보 같으니라고!","아루갈은 마법이 걸린 물건들을 이용하여 자신의 약한 마법을 강화했던 것이오. 우선 조사해봐야 할 물건들이 있소. 어서 가서 달분노 광신도와 달분노 검은영혼들을 처치하고 그들의 마법의 족쇄를 충분히 모아 오시오. 그 역겨운 짐승들은 북쪽과 동쪽 지역에서 목격되었소."}, {"공동묘지에 있는 달라 돈위버에게 광신도의 족쇄 6개와 검은영혼의 족쇄 3개를 가져가야 합니다."}},
[424] = {"아루갈의 계략", {"아루갈이 사용한 마법의 흑막을 캐내는 게 생각했던 것보다 시간이 오래 걸릴 것 같소.","그동안 우리 죽음추적자들이 발견한 작은 문제를 좀 해결해 주겠소? 정보에 의하면 아루갈이 남동쪽 언덕에 있는 심원 광산에 그의 마법을 퍼뜨린 것 같소. 그 광산은 바리마트라스 님의 군대에 매우 유용한 자원이 될 것이라 증명되었소.","가서 타락한 은빛갈기 그림슨이라는 광산 우두머리를 처치해 주시오. 그를 처치하면 광산은 우리 손에 들어올 것이오."}, {"은빛갈기 그림슨을 처치하고 그의 해골을 공동묘지에 있는 달라 돈위버에게 가져가야 합니다."}},
[425] = {"포악한 이바르", {"제 오빠 퀸과 저에게 은빛소나무 숲을 정찰하는 임무가 주어졌었답니다. 그리고 하드렉님께 보고하기로 되어 있었고요. 그런데 농장을 가로질러 이동하던 중에 포악한 이바르로 불리는 구울에게 공격당해 아직 본격적인 정찰을 시작하지도 못했답니다.","이바르에게서 도망치긴 했지만 오빠가 심각한 부상을 입고 말았어요. 가져다주신 물약 덕분에 오빠를 치료할 수 있었지만 오빠가 완전히 회복되기 전에 이바르가 다시 올지도 모릅니다.","오빠가 안전하게 회복될 수 있도록 당신이 이바르를 처치해 주세요. 이바르는 저쪽 헛간에서 공격 기회를 엿보고 있어요."}, {"포악한 이바르를 처치하고 이바르의 해골을 이바르 호박밭에 있는 레인 요릭에게 가져가야 합니다."}},
[426] = {"제분소 습격", {"스컬지가 아가만드 제분소에 기지를 구축하려고 하네. 기지를 구축하고 나면 병력을 정비하여 티리스팔의 다른 지역을 공격하려 할 거야.","제분소에 있는 이 스컬지들을 처치하라는 명령이 내려졌네. 제분소로 가서 덜그럭거리는 해골병사와 금이간 해골병사를 처치하여 톱니모양 늑골을 모으고, 검은눈 해골마법사를 처치하여 저주받은 해골을 모아오게.","제분소로 가려면 서쪽 길을 따라가다가 다리를 건넌 후에는 세 갈래 길에서 북쪽 길을 택해 계속 북쪽으로 가게. 아가만드 농장의 풍차가 보이면 전투를 준비해야 할 거야."}, {"톱니모양 늑골 5개와 저주받은 해골 3개를 모은 다음 브릴에 있는 죽음경비병 딜링거에게 가야 합니다."}},
[427] = {"붉은십자군과의 전쟁", {"집행관 아렌이 보내준 문서 덕분에 이제 티리스팔 숲에 포진한 붉은십자군의 정확한 위치를 알게 되었네. 때문에 저 야비한 붉은십자군과의 전투에서 훨씬 유리해지겠군.","하지만 우리 죽음경비병들에게 더 큰 걱정거리가 있네. 바로 리치 왕의 군대가 매일 밤 급속도로 늘어나고 있다는 거야. 그래서 붉은십자군 정도는 자네처럼 배포 있는 <class>|1이;가; 처리해 줬으면 좋겠는데...","어둠의 여왕 폐하를 섬길 만한 자격이 있는지 증명해 보여주지 않겠나? 솔리덴 농장을 지나 서쪽의 탑으로 가서 붉은십자군 전사 10명을 처치해 주게."}, {"브릴에 있는 집행관 자이건드가 붉은십자군 전사 10명을 처치해 달라고 했습니다."}},
[428] = {"실종된 죽음추적자", {"몇 주 전 우리 죽음추적자 중 레인 요릭과 퀸 요릭이라는 두 병사를 은빛소나무 숲 북부 지역으로 정찰 보냈는데 아직 소식이 없어 걱정일세.","이 둘이 스컬지에게 희생된 건 아닌지 확인할 필요가 있으니 이들을 찾아 주게.","그들의 첫 번째 목표는 은빛소나무 숲 북쪽에 있는 농장을 정찰하는 것이었으니 그곳을 먼저 수색하는 게 좋을 거야."}, {"죽음추적자 퀸 요릭과 레인 요릭을 찾아야 합니다."}},
[429] = {"늑대의 심장", {"제 오빠 퀸이 포악한 이바르라는 구울에게 심각한 부상을 입었어요. 적절한 마법 치료 없이는 가망이 없을 것 같아서 걱정이에요. 전 연금술사는 아니지만 그들이 검은늑대의 심장으로 치료 물약을 만들 수 있다는 건 알고 있답니다.","이 짐승들의 심장을 모아서 남쪽의 공동묘지에 있는 연금술사 렌퍼럴에게 가져다주시고 치유 물약이 완성되면 여기로 다시 가져와 주세요. 검은늑대들은 이 근처나 동쪽의 말덴의 과수원에 많이 있을 거예요."}, {"변색된 검은늑대 심장 6개를 모아 공동묘지에 있는 연금술사 렌퍼럴에게 가져가야 합니다."}},
[430] = {"퀸에게 돌아가기", {"자, 퀸 요릭을 치료할 물약이네.","그럼 난 계속해야 할 중요한 일이 있어서 이만 실례하겠네."}, {"공동묘지 북쪽의 이바르 호박밭에 있는 퀸 요릭에게 퀸의 물약을 가져가야 합니다."}},
[431] = {"부름의 양초", nil, nil},
[432] = {"저주받을 트로그", {"빌어먹을 트로그들, 제 작업장을 난장판으로 만들어 버렸습니다! 저것 보십시오, 화약통을 또 하나 가져가고 있잖습니까! 당장 내려가서 모조리 해치워 버렸으면 좋겠군요! 전부 다 말입니다!","으... 참아야지. 이렇게 스트레스받고 화내면 건강에 안 좋아... 그런데 이제 남은 게 뭐가 있지? 젠장, 아무것도 없네!","제 손으로 다 없애버렸으면 좋겠지만 이제 저도 늙었습니다! 당신이 채석장으로 내려가서 저 더러운 것들을 해치워 주십시오! 보상은 꼭 해 드리겠습니다! 눈에 보이는 대로 없애버리십시오!"}, {"골볼라 채석장과 광산에서 바위턱 망치잡이 8마리와 바위턱 뼈다귀싸움꾼 8마리를 처치해야 합니다."}},
[433] = {"공무 수행", {"원로원의 의원이자 탐험가 연맹의 회원으로서 채석장의 상태에 대해 의문스러운 점이 있습니다. 그건 바로 트로그들이 난폭해졌다는 점이지요.","골치덩이 트로그들이 뚜렷한 이유도 없이 이 골볼라 채석장을 난장판으로 만들고 있습니다. 광부들이 땅을 파내려가고 있는데 트로그들이 수도 없이 쏟아져 나와 장비를 파괴하고 인부들을 잡아갔다는군요.","잡혀간 인부들을 그곳에서 구해내야만 합니다. 마법사들에게 초과근무를 명령해 이 복귀의 룬에 마법을 불어넣도록 했습니다. 이 룬을 갇혀 있는 인부들에게 가져다주면 지상으로 안전하게 돌아올 수 있을 것입니다."}, {"복귀의 룬을 골볼라 채석장에 있는 갇혀 있는 광부 7명에게 나눠줘야 합니다."}},
[434] = {"공격!", {"첩보로봇을 변장시키고 나면 안으로 들어가서 라스코발에게 말을 걸게 될 거요. 첩보로봇은 경비병들을 문제없이 통과할 수 있을 거야.","당신이 내게 줄 재료를 구해오는 동안 마르존에게 거짓 정보도 좀 흘려놓고 라스코발과 다시 만날 때가 되었다고 알려놓았지.","라스코발은 그의 왕실근위병을 물러가게 하고 이 정원에서 마르존을 기다릴 것이오. 데피아즈단에 대해 얘기하는 기미가 보이거든 행동을 개시하라고. 경비병들이 돌아오기 전에 두 명 다 처치해야 하오!","일이 끝나면 트리아스에게 얘기하라고."}, {"그레골 라스코발 경과 소리없는검객 마르존이 대화를 나눌때까지 정원에서 기다려야 합니다. 대화를 들은 후, 이들을 모두 처치하고 스톰윈드에 있는 엘링 트리아스에게 가야 합니다."}},
[435] = {"엘런드 경호", {"밖에 있는 늑대들은 떠날 생각을 하지 않고 날 사납게 노려보고 있소. 내가 저 문으로 한 발짝이라도 내딛으면 놈들이 덤벼들 거요. 놈들은 너무 굶주린 나머지 나를 맛있는 먹잇감으로 보고 있는 것이 분명하오.","동료인 레인 요릭에게 보고서를 가져다줘야 하는데, 그러려면 아무래도 누군가 날 좀 보호해 줘야 할 것 같소.","당신이 날 좀 도와주겠소? 늑대들이 너무 많아서 혼자서는 힘들지도 모르니 친구들이 더 있다면 도움이 될 것이오."}, {"레인 요릭이 있는 곳까지 늑대들을 물리치고 엘런드를 호위해야 합니다."}},
[436] = {"아이언밴드의 발굴현장", {"발굴조사단장 아이언밴드가 호수 동쪽의 고대 유적지에서 발굴 작업을 지휘하고 있소. 그런데 최근 작업 진행 속도가 더뎌지고 있소. 지금까지 우리가 제공해 준 보급품의 양을 고려하면 좀 이상한 일인데...","아이언밴드는 결과를 중시하는 완고하고 정직한 드워프라서... 혹시 인부들이 그에게 반발하고 있는 건 아닌지 걱정이 되는군.","아이언밴드의 발굴현장으로 가서 마그마 펠휴와 이야기해 보시오. 현장의 세부사항을 관리하니까 작업 속도가 느린 이유를 알고 있을 거요. 발굴현장으로 가려면 호수 남쪽 끝을 돌아 동쪽으로 가면 되오."}, {"탐험가 펠휴와 대화해야 합니다."}},
[437] = {"죽음의 농장", {"은빛소나무 숲의 초기 정찰 보고에 따르면 썩은가죽 일족이 북쪽의 오래된 농장인 죽음의 농장을 본거지로 삼고 있다고 하네. 그들에게 산발적인 공격을 시도했지만, 마법을 사용하여 우리 군을 궤멸시키는 밴시 나이트래쉬 때문에 번번이 실패하고 말았네.","나이트래쉬는 반드시 없애야 해. 죽음의 농장으로 가서 썩은가죽 일족을 공격하게. 나이트래쉬가 나타날 때까지 썩은가죽 일족을 처치하고 그녀가 나타나면 같이 처치하도록 하게.","이제 그녀의 정수를 한낱 먼지로 만들어 버릴 때가 온 게야. 그 증거로 내게 그 정수를 가져오게."}, {"죽음의 농장에서 썩은가죽 놀들을 처치하십시오. 나이트래쉬가 나타나면 그녀를 처치하십시오.","공동묘지에 있는 고위집행관 하드렉에게 나이트래쉬의 정수를 가져가야 합니다."}},
[438] = {"오래된 나루터", {"우리는 오래된 나루터에서 새로운 스컬지의 소굴을 발견했네. 그 나루터는 공동묘지 동쪽에 있는데 썩은가죽 일족과 다른 언데드들의 경계가 삼엄하다고 하는군.","스컬지가 그곳에 자리 잡은 이유가 분명히 있을 거야. 그 오래된 나루터가 있는 곳으로 가서 스컬지가 그곳에 주둔하고 있는 이유를 알아내도록 하게."}, {"오래된 나루터로 가야 합니다.","그곳에 스컬지가 주둔한 이유를 찾아내십시오."}},
[439] = {"썩은가죽일족의 단서", {"시체들 사이에서 손 하나가 밖으로 나와 있습니다. 그 손은 여성의 것으로 역병으로 인한 반점이 있지만 비교적 생기 있어 보입니다.","그리고 손가락에 반지가 끼워져 있습니다. 반지를 빼내 살펴보니 다음과 같은 문구가 새겨져 있습니다.","\"델리아에게\""}, {"공동묘지에 있는 고위집행관 하드렉에게 델리아의 반지를 가져가야 합니다."}},
[440] = {"문자가 새겨진 반지", {"남편인지 연인인지는 모르지만, 델리아에게 반지를 준 이를 찾고 싶다면 브릴에 있는 집정관 세브렌과 얘기해 보게. 아마 최근의 사망 기록이 있을 테니 운이 좋으면 델리아가 누군지 밝혀낼 수 있을지도 모르네.","별로 기대하지는 않지만... 그녀에게 반지를 준 게 누구든 이미 죽었을 걸세. 죽지 않았더라도 더 이상 델리아에게 신경 쓰지 않을 거고 말이야."}, {"브릴에 있는 집정관 세브렌에게 델리아의 반지를 가져가야 합니다."}},
[441] = {"랠리와 언더시티", {"델리아에게는 지금 언더시티에 살고 있는 랠리라는 남편이 있었네. 브릴에 살았던 델리아가 이 반지의 주인이 맞다면 랠리가 알고 있을 거야.","랠리는 언더시티 상업 지구 쪽에 있다네."}, {"랠리에게 델리아의 반지를 가져가야 합니다."}},
[442] = {"펜리스 섬 공격", {"이제 은빛소나무 숲에 있는 스컬지의 본거지인 펜리스 섬을 공격할 때가 되었네.","다시 펜리스 섬으로 돌아가 까마귀발톱 쑤울을 처치해야 하네. 그리고 그의 머리카락을 가져오게! 이번 임무는 매우 중요한 만큼 위험도 커. 반드시 성공해야 하네."}, {"공동묘지에 있는 고위집행관 하드렉에게 쑤울의 머리카락을 가져가야 합니다."}},
[443] = {"썩은가죽 영액", {"펜리스 섬을 공격하기 전에 썩은가죽 놀들을 조종하고 있는 배후 인물이 누구인지 알아내야 하네. 우리 연금술사들은 그 놀들의 근원이 놈들의 몸에서 흘러나오고 있는 이상한 액체와 관련 있다고 믿고 있네.","오래된 나루터 동쪽에 있는 펜리스 섬으로 가서 놀들로부터 썩은가죽 영액을 모아 오게. 그리고 그걸 공동묘지 외곽에 있는 연금술사 렌퍼럴에게 가져가도록 하게."}, {"공동묘지에 있는 연금술사 렌퍼럴에게 썩은가죽 영액 8병을 가져가야 합니다."}},
[444] = {"썩은가죽일족의 기원", {"자네가 가져온 영액을 변형시켜 기본 구성 요소를 분리해 보았네. 연구 결과는 아직 확실하지 않지만, 이 영액은 흑마법사와 강령술사가 마법을 사용하여 만들어진 것 같네.","내가 영액에 대한 연구를 계속할 동안 자네는 언더시티에 있는 베소르 아이스샤드님께 이 표본을 좀 전해 주게. 베소르님은 연금술사는 아니지만 마법에 대한 그분의 지식이 썩은가죽일족의 비밀을 알아내는 데 도움이 될 것이야.","자, 어서 떠나게. 베소르님께서는 언더시티의 마법 지구에 계실 걸세."}, {"언더시티에 있는 베소르 아이스샤드에게 영액 견본을 가져가야 합니다."}},
[445] = {"은빛소나무 숲으로의 배달", {"지금쯤이면 당신도 확실히 알겠지만 실바나스 여왕님을 모시며 일하는 우리에게는 시간이 무엇보다 중요하오.","왕립 연금술 학회의 회원으로서 내가 알아낸 지식을 동료와 교환해야 하는 것이 내 의무요. 그래야만 언젠가 어둠의 여왕님이 그토록 원하시는 새로운 역병을 만들어낼 수 있을 테니까 말이오.","은빛소나무 숲의 공동묘지에 있는 연금술사 렌퍼럴에게 이 연구 결과를 전달해 주시오. 공동묘지로 가려면 브릴에서 길을 따라 남쪽으로 가다가 언더시티가 나오면 거기서 남서쪽으로 가면 되오."}, {"은빛소나무 숲에 있는 연금술사 렌퍼럴에게 요한의 연구 결과를 전달해야 합니다."}},
[446] = {"까마귀발톱 쑤울", {"이 영액은 까마귀발톱 쑤울이라는 내 옛 동료가 만든 거라네. 대전쟁 이전에 그는 젊고 전도유망한 마법사였지만 리치 왕이 권력을 장악하자 형제들을 배신하고 스컬지와 손을 잡았지.","그가 아직 살아있다는 걸 몰랐는데다가 썩은가죽 일족의 수장으로 우릴 공격하고 있던 자가 그자라니!","난 그의 마법 주문에 대해 많이 알고 있으니 그 마법에 대항할 물약을 만드는 걸 도울 수 있네. 이 두루마리를 연금술사 렌퍼럴에게 다시 가져가게. 그 안에 물약을 만들기 위해 필요한 주문이 적혀 있네."}, {"은빛소나무 숲 공동묘지에 있는 연금술사 렌퍼럴에게 베소르의 두루마리를 가져가야 합니다."}},
[447] = {"죽음의 비법", {"아서스의 군대는 병력면에서 압도적인 우위를 차지하고 있네. 하지만 새로운 역병을 사용한다면 스컬지 군대와 인간의 영토를 모두 초토화할 수 있지. 내 연구 결과에 따르면 거미의 피와 불곰 심장에서 뽑아낸 독을 혼합하면 치명적인 비약을 만들 수 있다네.","북서쪽에 위치한 암흑 땅거미굴의 거미와 은빛소나무 숲 전역에 돌아다니는 곰에게서 표본을 모아오도록 하게. 그리고 그 재료들을 언더시티에 있는 왕립 연금술 학회의 수석 연금술사 파라넬님께 전해 주게."}, {"불곰 심장 6개와 땅거미 점액 6개를 모아 언더시티에 있는 수석 연금술사 파라넬에게 가져가야 합니다."}},
[448] = {"하드렉에게 보고", {"펜리스 섬을 공격하기 전에 고위집행관 하드렉님께 보고하게. 그분이 다른 정보나 필요한 장비를 주실지도 모르네."}, {"공동묘지에 있는 고위집행관 하드렉에게 보고해야 합니다."}},
[449] = {"죽음의추적자의 보고", {"아직 쿠인 오빠의 상처가 완치되지는 않았지만, 최대한 빨리 고위집행관 하드렉님께 보고를 드려야 해요. 포세이큰이 은빛소나무 숲을 장악하기 위해서 매우 중요한 정보에요."}, {"공동묘지에 있는 고위집행관 하드렉에게 죽음의추적자 보고서를 가져가야 합니다."}},
[450] = {"죽음의 비법", {"왕립 연금술 학회를 위해 자네가 해 줘야할 일이 더 있네.","아루갈이 숲에 그 비열한 마법을 걸었을 때 장래가 촉망되는 우리의 베랄드라는 약제사도 저주에 걸렸다네. 그 저주로 인해 베랄드는 이제 아루갈의 꼭두각시가 되었으니, 미안한 일이지만 우리에겐 더 이상 이용가치가 없는 인물이야. 하지만 말이지 그의 연구 실적은 가치가 매우 높다네. 그는 수세기 전 로다미어 호수의 수중 생물들을 몰살시킨 고대의 역병에 대해 연구하곤 했었지.","장작나무 마을 여관에 있는 베랄드의 연구실에서 베랄드의 일지를 찾아 연금술사 렌퍼럴에게 가져가게."}, {"장작나무 마을에서 베랄드의 일지를 구해 공동묘지에 있는 연금술사 렌퍼럴에게 가져가야 합니다."}},
[451] = {"죽음의 비법", {"베랄드의 일지에 따르면 호수덤불괴물과 호수늪지괴물의 이끼가 새로운 역병 제조에 유용할지도 모르겠군. 이 짐승들은 동쪽의 로다미어 호수에 있는 섬에 살고 있지.","또한 그는 일지에 썩은지느러미 멀록에게서 발견되는 매우 희귀한 굳어진 종양에 대해서도 언급하고 있네. 그 종양은 너무 희귀해서 베랄드도 하나밖에 입수하지 못한 듯해.","이끼 표본과 함께 그 희귀하고 단단한 종양을 수집해서 언더시티에 있는 수석 연금술사 파라넬님께 가져가도록 하게."}, {"호수늪지이끼 6개, 호수덤불이끼 6개, 그리고 굳어진 종양 1개를 언더시티에 있는 수석 연금술사 파라넬에게 가져가야 합니다."}},
[452] = {"장작나무 마을 매복", {"오, 제때에 와줬군요!","곧 장작나무 원로들이 돌아올 거예요. 아루갈이 성채에 머무르는 데는 다른 이유가 있답니다. 그는 원로들을 꼭두각시로 만들어 장작나무 마을 주민들을 마음대로 조종하면서 부하로 삼고 있어요. 장작나무 마을 원로들만 없다면, 아루갈의 부하 노릇을 하고 있는 마을 주민들도 모두 풀려나게 될 거예요.","하지만 혼자서 모든 원로들을 상대할 수는 없으니 당신 도움이 필요해요. 나와 함께 싸워 주실 수 있나요? 우리 함께 실바나스 여왕님의 이름으로 원로들을 처단하자고요."}, {"죽음추적자 페를레이아를 도와 장작나무 마을 원로원들을 처치해야 합니다."}},
[453] = {"의문의 남자 찾기", {"행운이 있기를... 그 사람을 꼭 찾으시기 바랍니다. 그 친구가 살아 있어야 할 텐데...","어둠골의 안전한 울타리를 벗어났으니 야생 짐승들의 먹이가 되었을 수도 있거든요."}, {"의문의 남자를 찾아야 합니다. 지금까지 얻은 단서는 다음과 같습니다.","어둠골에서 태어난 사람이 아닙니다.","신경질적이고 신경과민인 사람입니다.","어둠골을 떠나 서쪽으로 갔습니다."}},
[454] = {"훌다르와 미란", {"이제까지는 셋이 왔다갔다하면서 화약통을 운반했는데... 이제는 일을 나눠서, 미란이 운반하고 나는 여기서 다른 통들을 지켜야 하겠군.","그런데 미란 혼자 운반하는 게 걱정돼서 말이야. 미란이 준비를 마치고 출발하게 되면 그를 경호해주지 않겠나?"}, {"미란과 대화하십시오."}},
[455] = {"알가즈의 위기", {"용아귀 오크들이 저습지를 차지한 것도 모자라, 이제 던 알가즈에까지 손을 뻗어오고 있소! 이곳에 있는 놈들의 거점을 제거하지 않으면 저습지에 있는 우리의 항만 시설과 요새인, 메네실 항구에서 모단 호수에 이르는 길목을 차단당하게 되오.","던 알가즈를 가로질러 저습지 너머로 이동하시오. 그리고 가는 길에 오크 녀석들도 좀 처치해 주시오. 그런 다음 메네실 항구에 있는 발스탁 아이언조에게 보고하도록 하시오.","메네실은 던 알가즈를 지나 길을 따라 북쪽으로 간 다음 갈림길에서 서쪽으로 가면 되오.","우린 당신만 믿겠으니 우릴 실망시키지 마시오."}, {"던 알가즈를 가로질러 가야 합니다.","용아귀 정찰병 8명과 용아귀 그런트 6명을 처치해야 합니다.","메네실 항구에 있는 발스탁 아이언조에게 보고하십시오."}},
[456] = {"자연의 균형", {"어서 오십시오. 저는 관리인 일사레인이라고 합니다. 그늘 협곡에서 자연의 균형을 유지하는 일을 맡고 있지요.","올해는 유난히 봄비가 많이 내려서 많은 짐승이 피해를 입었습니다. 하지만 어떤 짐승에게는 오히려 좋은 일이 되기도 했지요. 특히 엉겅퀴멧돼지와 밤호랑이의 수가 눈에 띄게 늘었는데 우리에겐 골칫거리가 아닐 수 없습니다. 그늘 협곡에는 짐승을 먹일 식량이 한정되어 있기 때문에 생태계의 균형을 유지하는 것은 매우 중요한 일입니다.","어서 가십시오, 젊은 <class>|1이여;여;. 가서 멧돼지와 밤호랑이의 수를 줄여 자연의 균형을 지켜주십시오."}, {"새끼 밤호랑이 3마리와 새끼 엉겅퀴멧돼지 3마리를 처치해야 합니다."}},
[457] = {"자연의 대가", {"그늘 협곡에 사는 어린 짐승들의 수를 줄였으니 일단 출발은 순조롭다고 할 수 있습니다. 하지만 아직도 그대가 해야 할 일이 있습니다.","이 문제를 해결하지 않는다면 숲의 자원이 금세 고갈돼 버릴 겁니다. 이 땅을 공유하고 있는 모든 생명을 위해 짐승 사냥은 반드시 시행해야만 하는 필요악입니다. 자연의 균형을 위해 북동쪽으로 더 나아가 병든 밤호랑이와 엉겅퀴멧돼지를 처치해주십시오."}, {"병든 밤호랑이 6마리와 엉겅퀴멧돼지 6마리를 처치해야 합니다."}},
[458] = {"숲의 수호자", {"그대를 이곳으로 이끌어 주신 엘룬께 감사를! 숲에 사는 정령들의 속삭임을 통해 이상한 소식을 들었소.","신비한 숲의 수호자 타린드렐라가 그늘 협곡으로 다시 돌아왔다고 하오. 텔드랏실의 축복 전부터는 숲에서 이 드리아드의 존재를 느끼지 못했었소. 그녀가 이 땅에 돌아왔다면 필시 무언가 문제가 있는 것이 분명하오.","타린드렐라를 찾아 그녀가 우리 숲에 온 이유를 알아내시오. 파수꾼의 보고에 의하면 알드랏실 남서쪽에서 그녀를 보았다고 하오."}, {"타린드렐라라고 알려진 드리아드를 찾아야 합니다."}},
[459] = {"악랄한 도둑", {"나쁜 그렐놈들! 놈들이 또 우리 식량 창고를 털어갔어요. 서쪽에 있는 그렐 야영지에 훔쳐간 식량들이 여기저기 흩어져 있을 거예요. 도와주실 수 있다면 그곳으로 가서 식량을 되찾아와 주시겠어요?"}, {"델리라의 잃어버린 콩, 델리라의 잃어버린 물, 델리라의 잃어버린 자루를 회수해야 합니다."}},
[460] = {"분리된 몸과 머리", {"당신은 썩은가죽일족은 아닌 것 같으니 다행이군. 난 완전히 희망을 잃었소.","난 쑤울의 수석 부관이자 경호원이었던 알라리크라고 하오. 쑤울은 끔찍한 역병에서 나를 보호하기 위해 마법을 사용했는데 그가 사용한 주문 때문에 난 살지도 죽지도 못하게 되어 버렸소. 쑤울이 망할 놈의 썩은가죽 놀을 만들어낸 후에 나를 배신한 거야!","제발 나를 내 몸이 있는 곳으로 데려가 주시오. 놀들이 펜리스 요새 근처에 있는 마구간 앞에 내 몸을 묻었을 거요."}, {"알라리크의 머리를 그의 무덤으로 데려가야 합니다."}},
[461] = {"비밀 벽감", {"성채 안 어딘가에 쑤울과 그의 오랜 동료가 소중한 물건을 숨겨 놓는 비밀 벽감이 있다고 알고 있소. 거기에는 당신에게 유용한 것도 있을 것이오. 나를 그곳으로 데려가 주시오!","그 벽감은 성채의 남서쪽 탑에 있는 먼지투성이 선반 뒤에 숨겨져 있소. 장화는 신경 쓰지 말고 내 몸이나 챙겨주시오. 하도 작아서 신을 수가 있어야지..."}, {"알라리크를 비밀 벽감으로 데려가야 합니다."}},
[463] = {"신록수호자", {"신록수호자에 대해 들어봤나? 혹시 그 짐승을 찾고 있는 건가? 흠, 그렇다면 당신 정신 나간게야. 뭐, 목숨 아까운 줄 모르는 바보를 구태여 내가 뜯어말릴 필요는 없겠지.","그 짐승을 찾고 있다면 던 모드르 쪽으로 갈라지는 길 동쪽에 있는 습지 근처에 산다고 하니까 그쪽으로 가 보라고. 악어 사이에 숨어 있다고 하는 친구도 있었지... 그보다 더할 수도 있지!","그리고 참, 돈은 여기 놓고 가라고. 거기 가 봤자 필요 없을 테니까 말이야. 그 늙은 녹색 짐승이 당신을 두 동강 내서 씹어 먹을 때 금화에 그 이빨 자국이 남게 되는 걸 바라지 않겠지?"}, {"신록수호자를 찾아야 합니다."}},
[464] = {"전쟁 깃발", {"용아귀 오크는 자신들이 부리던 붉은용을 통제할 수 없게 되면서 2차 대전쟁이 종결될 즈음에 거의 섬멸됐었네. 용들은 옛 주인에게 불만이 많았었는지 무서운 기세로 용아귀 부족을 공격했지. 얼마 남지 않은 생존자들은 그림 바톨을 탈출하여 웰가르의 발굴현장 동쪽에 있는 산으로 피신했다네.","그런데 그들이 힘을 길러 지금 메네실 북동쪽에 진을 치고 있네.","자네가 놈들의 주둔지를 공격해 주게. 그리고 임무 완수의 증거로 놈들의 전쟁 깃발을 모아 내게 가져오게나."}, {"메네실 항구에 있는 경비대장 스타우트피스트에게 용아귀 전쟁 깃발 8개를 가져가야 합니다."}},
[465] = {"용아귀 투석기", {"자네가 용아귀 부족과 싸우는 동안 놈들의 계획에 대한 정보를 알아냈네.","네크로쉬가 투석기를 만들어 메네실을 포위 공격하려 한다는 걸세! 던 알가즈도 교전 중이기 때문에 우린 사실상 고립되어 지원병도 기대할 수 없는 상황일세.","자네가 놈들의 투석기를 제거해 주어야겠네. 투석기는 용아귀 부족의 주둔지에 있네.","자, 이 부싯깃을 가져가도록 하게. 투석기가 있는 곳까지 접근한 다음 이 부싯깃으로 불을 붙이게. 그런 다음 네크로쉬를 찾아 반드시 처치해야 하네. 네크로쉬의 약점을 잘 찾아서 노리게."}, {"용아귀 투석기를 파괴해야 합니다."}},
[466] = {"발연 광석을 찾아서", {"저습지의 알가즈 관문 북서쪽에 있는 동굴에서 발연 광석이라는 희귀한 광석이 발견되었다고 들었네. 발연 광석은 휘발성 물질이라 자칫 잘못 사용하면 폭발 사고를 당할 수 있지만, 제대로 용해한다면 엄청난 화력을 자랑하는 포탄을 만들 수 있을 걸세!","내게 발연 광석을 가져다주면 그 광석으로 실험을 할 용감한 사람을 찾아보도록 하지.","아, 광맥에서 광석을 추출하려면 채광 기술이 있어야 한다네. 자네가 기술을 익히지 않았다면 채광 기술을 익힌 동료를 데려가도록 하게나."}, {"던 모로에 있는 조종사 스톤기어에게 발연 광석 6개를 가져가야 합니다."}},
[467] = {"스톤기어의 광석", {"조종사 스톤기어는 여느 조종사들과 마찬가지로 자신의 증기 전차에 대한 애착이 무척 강하다오. 항상 전차의 화력을 올릴 수 있는 새로운 방법을 찾으려고 열심이지.","지금 그는 어떤 희귀한 광석에 대한 소문을 듣고는 자신을 위해 그 광석을 찾아다 줄 이를 구하고 있소. 생각이 있다면 던 모로에 있는 스틸그릴의 정비소로 가서 스톤기어를 만나 그가 뭘 원하는지 알아보도록 하시오."}, {"조종사 스톤기어와 대화해야 합니다."}},
[468] = {"산악경비대 록가르에게 보고", {"산악경비대 록가르가 던 알가즈와 저습지에서 임무를 수행해 줄 지원자를 찾고 있다네.","그 질퍽거리는 북쪽 지역에서 임무를 수행할 의향이 있다면 던 알가즈로 가서 록가르와 한번 얘기해 보게. 길을 따라 북쪽으로 가다 보면 경비탑이 나오는데 던 알가즈는 그 북쪽 경비탑 너머에 있네."}, {"산악경비대 록가르와 대화해야 합니다."}},
[469] = {"가죽 배달", {"안녕하시오!","메네실 쪽으로 가는 것 같아 보이지는 않는군... 여유가 있다면 메네실에 한번 들러 보시기 바라오. 필요한 물건을 구입하는 건 물론이고 맥주 한 잔 하면서 쉬어가기에도 더할 나위 없이 좋은 곳이지.","메네실 쪽으로 갈 거라면 무두장이 제임스 할로란에게 이 악어 껍질 한 묶음을 가져다주지 않겠소?","내가 직접 가고 싶지만 요즘 우리 집사람 심기가 상당히 불편한 것 같아서 얼굴을 마주치기가 좀 그렇소이다."}, {"메네실 항구의 무두장이 제임스 할로란에게 악어 껍질 한 묶음을 전달해야 합니다."}},
[470] = {"가방 찾기", {"전 매주 아이언비어드의 고분에 방문하는데 한번은 흉측하고 물투성이 진흙괴물로부터 공격을 받았어요! 전 너무 무서워서 제정신이 아니었답니다.","수액괴물 녀석 중 한 놈에게 가방을 던져 봤지만 가방이 바로 빨려 들어가서 아무 소용이 없었어요! 다행히도 그때를 틈타 도망갈 수 있었죠.","그런데 그 가방에는 제게 꼭 필요한 물건들이 들어 있었어요. 수액괴물들은 전부 똑같이 생겨서 어느 놈에게 던졌는지도 알 수가 없네요! 아이언비어드의 고분은 던 모드르 근처, 이곳에서 북동쪽에 있답니다. 제 가방을 찾아주시면 정말 감사하겠어요!"}, {"아이언비어드의 고분에 돌아다니는 수액괴물 중 하나가 시다의 가방을 갖고 있습니다. 그 가방을 구해 메네실 항구의 시다에게 가져가야 합니다."}},
[471] = {"수습생의 임무", {"이 지역에는 악명 높은 습지악어가 살고 있습니다. 그 녀석의 껍질을 구해 오라고 수습생을 보냈는데 돌아오지 않기에 찾으러 나가 봤더니... 정말 끔찍한 광경이었습니다.","의사 선생님은 그가 잘 견뎌낼 거라고 했지만, 어쨌든 저는 지금 당장 껍질이 필요합니다!","놈들에 대한 복수라고 생각하고 이 짐승을 몇 마리 더 잡아 주시겠습니까? 해안 북쪽으로 멀록 소굴 너머에 가면 거대한 습지악어를 찾을 수 있을 겁니다."}, {"거대한 악어 껍질 6장을 모아 메네실 항구의 제임스 할로란에게 가져가야 합니다."}},
[472] = {"던 모드르의 함락", {"던... 모드르가 검은무쇠 드워프의 손에 들어가고 말았소!","우리 부대는 거의 전멸했고, 나... 난 심각한 부상을 입었지. 검은무쇠 악당들은 우리가 탄돌 교각의 매복지에서 부대를 재편성하기도 전에 공격해 왔소.","우리 지휘관 롱브레이드 님께서는 퇴각 나팔을 불 수밖에 없었소. 우리가 마을에서 후퇴할 때 빗나간 도끼가 내 등에 꽂히고 난 정신을 잃었소.","깨어나 보니 이곳 메네실에 와 있었지만 동료 걱정으로 마음이 편치 않다오. 아직 난 동료가 무사하다는 희망이 있소. 롱브레이드 님께서 아직 살아 계실지도 모르니, 던 모드르 근처에서 그분을 좀 찾아봐 주시오!"}, {"던 모드르 마을 근처에서 롱브레이드를 찾아야 합니다."}},
[473] = {"경비대장 스타우트피스트에게 보고", {"용아귀 오크는 알가즈뿐 아니라 다른 지역까지 공격하고 있소. 그래서 스타우트피스트 경비대장님께서 전투에 참가해 오크와 싸울 지원자들을 모집하라고 하셨소. 생각이 있다면 선장님을 만나보시오. 스타우트피스트 경비대장님은 요새 안에 계시오."}, {"경비대장 스타우트피스트와 대화해야 합니다."}},
[474] = {"네크로쉬 처치", {"투석기를 불태웠으니 이제 족장 네크로쉬를 찾아 처치해야 합니다."}, {"족장 네크로쉬를 처치해야 합니다.","그런 다음 그 증거로 경비대장 스타우트피스트에게 네크로쉬의 머리카락을 가져가야 합니다."}},
[475] = {"혼란의 바람", {"숲에 혼란의 바람이 불고 있소.","게롤라스 탈베드렌은 위대한 감시자의 역할을 맡아 바네실 지하굴에서 갈퀴발톱의 드루이드를 지킨다오. 게롤라스는 수면을 보호하도록 선택된 자로서, 이세라와 드루이드 사이에 맺은 협정이 유지되도록 드루이드를 안전하게 지킬 임무가 있소.","그런데 최근 게롤라스에게서 연락이 늦어져 초조해지고 있소. 동쪽 별바람 마을로 가서 게롤라스의 보고서를 가지고 오시오. 우리 동족들이 무사하다는 사실을 확인하고 나면 나도 안심할 수 있소."}, {"별바람 마을에서 게롤라스 탈베드렌을 찾아야 합니다."}},
[476] = {"나무옹이 일족의 타락", {"나무옹이 일족이 다시 타락했소!","매복했던 나무옹이 일족이 바네실 지하굴을 향해 가는 나를 공격하고는 별바람 마을을 약탈하러 갔소. 숲에 있는 무언가 때문에 족장인 투사 우르살은 타락해버리고서는 악마의 이끼에 깃든 사악한 힘을 이용해 나무옹이 일족을 광란의 상태로 몰아 넣었소.","나는 부상이 심해 아스리다스에게 이 암울한 소식을 직접 전하지 못하오. 그러니 이 임무를 당신에게 맡겨야겠소, 젊은 <class>|1이여;여;. 미쳐버린 나무옹이 일족이 바네실 지하굴에 아직 도착하지 않았기를 바라는 수밖에 없소!"}, {"돌라나르에 있는 아스리다스 베어맨틀에게 가야 합니다."}},
[477] = {"마법사의 상자", {"자네도 알고 있다시피 호박색 농장에서는 대부분 달라란의 마법사들의 지원을 통해 아직 인간의 저항이 계속되고 있네.","그들의 계획이 무엇인지는 모르겠지만, 호박색 농장처럼 고립된 마을에 관심을 보이는 것을 보면 그곳에 뭔가 중요한 것이 있는 것이 틀림없어.","놈들은 언덕마루에서 마차를 이용해 상자들을 실어 나르고 있네. 상자들의 도착지는 장작나무 마을 북쪽에 있는 작은 야영지인 것 같은데, 자네가 상자의 내용물을 입수해 내게 가져와 주게."}, {"달라란 마술사의 상자 중 하나의 내용물을 찾아 와야 합니다. 장작나무 마을은 남쪽에서 찾을 수 있습니다."}},
[478] = {"지도와 룬문자 펜던트", {"상자에서 재빨리 펜던트를 꺼내고 뚜껑을 닫습니다."}, {"공동묘지에 있는 어둠의사제 알리스터에게 룬문자 펜던트를 가져가야 합니다."}},
[479] = {"호박색 농장 조사", {"달라는 그 마법사들이 사용하는 마법의 원천을 찾고 있네. 그리고 지금 우리는 모든 방법을 동원하여 놈들의 계획을 지연시켜야 해.","분명히 마법사와 수호병들이 펜던트를 운반하고 있을 걸세.","그 펜던트를 빼앗아 와야 하네. 길을 따라 남쪽으로 간 다음 길이 갈라지는 곳에서 호박색 농장으로 이어지는 동쪽으로 가게."}, {"공동묘지의 어둠의 사제 알리스터가 달라란 펜던트 8개를 가져와 달라고 했습니다."}},
[480] = {"대마법사의 지팡이", {"엄청난 마력을 가진 대마법사가 초원 에너지 원천의 부활을 감독하고 있다네. 놈들이 이미 상당한 진척을 이루었으니, 우리에게 남은 길은 그를 처치하여 놈들의 계획을 막는 방법뿐이네. 다른 마법사와 보초병들이 그를 호위하고 있을 테니 어려운 임무가 될 거야.","서둘러 그를 저지하도록 하게. 놈을 처치한 후에 그의 지팡이를 찾아서 내게 가져와야 하네. 시간이 없으니 어서 가게나."}, {"달라란 대마법사 아태릭을 처치하고 그의 지팡이를 공동묘지에 있는 어둠의 사제 알리스터에게 가져가야 합니다."}},
[481] = {"달라의 분석", {"저기 있는 달라 던위버에게 펜던트를 가져가 보게. 그가 마술사들의 계획을 밝혀낼 수 있을지도 모르네."}, {"공동묘지에 있는 달라 던위버에게 룬문자 펜던트를 가져가야 합니다."}},
[482] = {"달라란의 의도", {"그렇지! 내가 이걸 어떻게 지금까지 까맣게 잊고 있었지?","앰버밀에는 잠자고 있는 초원 에너지의 원천이 있기 때문에 키린 토가 일찌감치 주목하고 있던 지역 중 하나요. 마법사들은 에너지의 중심을 부활시켜 뭔가 더 큰 목적에 그 에너지를 사용하려는 게 틀림없소. 이건 절대 그냥 넘길 수 있는 문제가 아니오.","그 지역의 에너지를 활성화하는데는 엄청난 양의 마법이 필요할 테니 마술사들에게서 펜던트를 빼앗으면 놈들의 계획을 지연시킬 수 있을 것이오. 어서 이 정보를 알리스터에게 전하시오. 그럼 그가 조치를 취할 것이오."}, {"공동묘지에 있는 어둠의사제 알리스터와 대화해야 합니다."}},
[483] = {"깨움의 의식을 위한 재료", {"나무옹이 침략자들이 서쪽 언덕에 자리 잡은 바네실 지하굴을 약탈하는 것이 확인되었소.","우리가 손쓰지 않으면 그 안에 잠든 드루이드들은 자신들의 운명을 의식하지도 못한 채 영원히 에메랄드의 꿈속에 갇힐 거요. 섬세한 수면의 의식은 깨움의 의식을 위한 재료 없이는 해제될 수 없소.","바네실 지하굴로 가서 까마귀 발톱 부적과 검은 깃털, 천상의 사파이어, 그리고 안식의 룬을 찾아오시오. 드루이드들은 이 물건들을 신성한 상자에 보관하고 있소. 그것들을 내게 가져오면 드루이드들을 깨우는 의식을 준비하도록 하겠소."}, {"깨움의 의식을 위한 재료를 구해야 합니다."}},
[484] = {"새끼 악어 가죽", {"저는 무두장이 제임스 할로란이라고 합니다. 제가 가공한 악어 껍질은 인기가 많아 여기저기서 주문이 들어오고 있답니다. 제 제품들은 배에 실려 전 세계로 배달되기도 하지요.","아이나르를 만나셨죠? 아이나르도 저한테 악어 껍질을 납품하는 사냥꾼 중 하나랍니다.","그런데 요즘 새끼 악어의 부드러운 껍질이 좀 부족합니다. 그러니 새끼 악어 껍질을 구해 주신다면 값을 후하게 쳐 드리겠습니다. 마을 바로 바깥쪽에 있는 습지에 가면 새끼 악어를 찾을 수 있을 겁니다."}, {"메네실 항구의 제임스 할로란이 새끼 악어 껍질 4장을 가져와 달라고 했습니다."}},
[485] = {"OOX-09/HL 수색", {"금속으로 만들어진 이상한 알 모양의 장치를 찾았습니다. 손잡이 하나를 만지작거리다 보니 작동이 시작되면서 노움 로봇 장치로 변합니다! 로봇 속에서 소음과 함께 목소리가 들려옵니다.","\"나는 오글소프 오브노티쿠스라고 하는데, 불시착한 내 유도 로봇을 찾아주면 사례를 하겠소. 이 탐지기를 로봇이 있는 곳으로 가져가시오!\"","\"방금 로봇의 현재 위치를 산출해 봤는데 굼벵이 바위굴 근처, 동부 내륙지의 강이 시작되는 곳에 추락한 것 같소!\""}, {"굼벵이 바위굴 근처, 동부 내륙지의 강이 시작되는 곳에 추락한 오글소프의 유도 로봇에게 조난 탐지기를 가져가야 합니다."}},
[486] = {"투사 우르살", {"이제 갈퀴발톱의 드루이드들을 구할 때가 되었소. 지금 실패하면 그들은 영원히 에메랄드의 꿈에서 깨어나지 못하오.","나는 재료를 준비한 다음 의식을 거행해 그들을 깨우겠소. 하지만 의식의 효과가 나타나려면 이 끔찍한 상황을 불러온 그 저주받은 우르살을 반드시 처치해야 하오.","우리 동족의 소명을 방해한 것은 우르살이니 이제 그자가 대가를 치러야 드루이드들이 자유로워질 수 있소. 길을 따라 북서쪽으로 가다가 바위 동굴로 이어지는 북쪽 길을 찾아보시오. 우르살의 소굴이 그곳에 있소"}, {"투사 우르살을 처치해야 합니다."}},
[487] = {"다르나서스로 가는 길", {"다르나서스로 가는 길은 반드시 안전하게 지켜야 합니다. 그런데 타락한 나무옹이 펄볼그들이 끊임없이 여행자들을 공격하고 있습니다.","날마다 중요한 소식과 거래가 이 길을 거쳐 다르나서스로 오갑니다. 그러니 우리는 이 야만스러운 도둑놈들의 위협을 그냥 보고만 있을 수는 없습니다.","신성한 숲의 이름으로 무기를 들어주십시오, <class>님. 놈들의 소굴은 북쪽으로 향하는 이 길에 있습니다. 나무옹이 복병을 처치한 다음 제게 알려주십시오."}, {"나무옹이 복병 8마리를 처치해야 합니다."}},
[488] = {"젠의 부탁", {"일거리를 찾나 보군. 알겠네. 항상 일을 처리해줄 풋내기 <class>|1이;가; 필요한 나같은 이를 만나다니 운이 좋았군.","이봐, <name>. 내가 행복하게 해 줄 수도 있고, 꿈속에서나 가질 만한 물건을 줄 수도 있는데, 그러려면 우선 물건을 몇 가지 구해와야 하네. 숲에서 하는 일엔 특별한... 재료가 종종 필요하거든.","밤호랑이 송곳니, 스트리기드 올빼미 깃털, 그리고 나무그물거미의 거미줄을 가져오라고.","이 일은 우리 둘만 아는 걸로 하지, <race> 친구."}, {"밤호랑이 송곳니 2개, 스트리기드 올빼미 깃털 2개, 나무그물거미의 거미줄 2개를 구해야 합니다."}},
[489] = {"신뢰 회복!", {"그대가 젠 파울후프를 도왔다는 소식이 숲의 원로회에 전해졌어요. 사티로스는 숲을 해치는 족속이죠. 그대는 <race>|1이니까;니까; 자연이 창조한 생명체를 죽여 숲을 모독할 만큼 바보는 아니겠죠.","텔드랏실의 친구로 남고 싶다면 원로들의 신뢰를 되찾아야 해요. 파울후프에게 본때를 보여 준다면 신뢰를 얻을 수 있어요.","악의 열매는 나무에서 떨어지는 오염된 씨앗인데 열매에서 녹색 연기가 피어오르죠.","파울후프에게 열매를 좀 가져다주세요. 아마도 그걸 몸에 해롭지 않은 간식거리로 생각할 거예요."}, {"악의 열매 3개를 모아야 합니다."}},
[491] = {"베소르에게 전할 마법봉", {"쑤울은 아주 오래전에 그와 두 명의 동료가 지니고 있던 수습생 마법봉을 결합하여 마법봉을 만들었다고 말했소. 이 마법봉이 그들에겐 우정의 상징이었던 거였지만 쑤울은 그 마법봉을 하찮게 여기면서도 다른 친구들이 소유하는 것을 원치 않았지. 그래서 그는 마법봉을 훔쳐 바로 이곳 펜리스 요새에 숨겼소.","자, 이 마법봉을 쑤울의 옛 친구 중 한 명인 베소르 아이스샤드에게 가져다주시오. 아마 베소르는 마법봉을 원하고 있을 것이오."}, {"언더시티의 마법 지구에 있는 베소르 아이스샤드에게 결합된 마법봉을 가져가야 합니다."}},
[492] = {"새로운 역병", {"경비병들 말로는 멍청한 드워프 산악경비대원 한 녀석이 잡혀 있다고 하더군. 죽음의경비병들은 포로의 처분이 결정될 때까지 갤로우 선술집 지하실에 가둬 두지.","내가 특별히 준비한 이 음료를 산악경비대 포로가 얼마나 맛있게 마시는지 구경해 보지 않겠소? 이 음료로 말하자면... 음, 어둠의 여왕께서 아제로스를 위해 어떤 일을 계획하고 계신지 암시하는 것이지."}, {"산악경비대 포로에게 요한의 특별조제약을 전해줘야 합니다."}},
[493] = {"언덕마루 구릉지로의 여정", {"왕립 연금술 학회는 어둠의 여왕님의 명령에 따라 새로운 역병을 개발하고 있네. 부단히 연구한 결과 현재 상당한 진척을 이루었네만, 학회 회원들이 정보를 공유한다면 훨씬 더 빨리 개발에 성공할 수 있을 것이야.","그러니 자네가 최근 내 연구 결과를 타렌 제분소에 살고 있는 연금술사 라이던에게 전해 주게나. 타렌 제분소는 언덕마루 구릉지에 자리 잡고 있는 작은 마을이니 아마 긴 여행이 될 걸세. 아래로 내려가 길을 따라 남쪽 방향의 길을 따라 계속 가게. 가다가 표지판을 잘 살펴보고!"}, {"언덕마루 구릉지의 타렌 제분소에 있는 연금술사 라이던에게 렐퍼럴의 연구 결과를 전달해야 합니다."}},
[494] = {"공격 개시", {"<class>님, 잘 들으세요.","나는 언덕마루 마을을 조사하기 위해 이곳에 파견되었습니다. 당신이 지금 즉시 타렌 제분소의 고위집행관 다살리아님께 내 보고를 전해 주어야겠습니다. 고위 집행관님께 죽음추적자 레시가 다음과 같은 전갈을 전했다고 하면 됩니다.","\"서쪽에서 갈까마귀 울음소리가 손짓하고 있다.\"","표지판을 잘 살펴보면서 동쪽으로 길을 따라가시기 바랍니다. 자, 어서 서두르세요! 매우 시급한 사안이니 지금 즉시 타렌 제분소로 떠나야 합니다."}, {"타렌 제분소로 가서 고위집행관 다살리아에게 죽음추적자 레시의 전갈을 전해야 합니다."}},
[495] = {"의지의 왕관", {"무그톨과 그의 오우거 부대가 실바나스 여왕님을 위해 의지의 왕관을 찾는 임무를 띠고 알터랙으로 떠났었지. 그런데 공교롭게 그 바보 같은 자들이 왕관을 찾아오는 대신에 소유욕에 불타오르고 말았어. 오우거들은 값져 보이는 것만 보면 사족을 못 쓰는 모양이더군.","녀석의 바보 같은 행동이 결국 큰 문제를 일으키고 말았지. 의지의 왕관을 가지려는 욕심 때문에 자신의 직분을 잊고 감히 실바나스 여왕님께 대항했던 것이야.","어둠의 여왕님께서는 그자를 여왕님께 반하는 자들에게 본보기로 삼으려 하고 계시다. 언덕마루의 타렌 제분소로 가서 나의 부관인 멜리사라에게 이 사태를 전하라."}, {"언덕마루 구릉지의 타렌 제분소에 있는 멜리사라에게 보고해야 합니다."}},
[496] = {"괴로움의 비약", {"아, 타렌 제분소의 무료한 하루가 또 지나가고 있군. 깨끗한 공기는 내 기분을 망쳐 버린다오.","이제 곧 이곳의 인간들에게 역병을 퍼뜨리면 좀 나아질 거야. 빠르면 빠를수록 좋은데 말이지. 내 조제약에 사용할 만한 독극물에 대해 심도 있는 연구를 계속해 왔지만, 실제로 그 성분들을 모두 모을 만한 시간이 없소.","날 도와줄 생각이라면 회색곰 혀 10개와 아주 귀해서 구하기 어려운 이끼거미 수액을 좀 구해주지 않겠소? 회색곰과 숲이끼거미는 타렌 제분소 외곽에서 찾을 수 있소."}, {"타렌 제분소에 있는 연금술사 라이던이 회색곰 혀 10개와 이끼거미 수액을 구해다 달라고 했습니다."}},
[498] = {"포로 구출", {"나와 오크 전사 몇 명이 대족장님의 명에 따라 던홀드 요새에서 물건을 찾는 임무를 수행했었소. 그 후 비밀결사대에 대한 조사를 벌이기 위해 이곳까지 파견되었지.","우리는 던홀드 요새를 수색하러 갔다가 많은 수의 인간들에게 습격을 당하고 말았소. 우리는 사력을 다해 싸웠지만 그들의 수가 너무 많아 패하고 말았지. 인간들은 우리 중 몇 명을 포로로 잡았지만 나는 간신히 탈출했소.","여기서 남동쪽으로 강 건너에 던홀드 요새가 있소. 그곳에 드럴과 토그타르가 잡혀 있으니 가서 꼭 그들을 구출해 주시오."}, {"타렌 제분소에 있는 크루스크가 던홀드 요새에서 드럴과 토그타르를 구출해 달라고 했습니다."}},
[499] = {"괴로움의 비약", {"이제 이 재료들을 혼합하기만 하면 내가 괴로움의 비약이라 이름 붙인 물질이 곧 완성될 것이오.","자, 보라고! 완성되었어! 이 끔찍한 구릉지에서 무료한 날들을 달래기엔 세상에 작은 고통을 선사하는 것만큼이나 좋은 일도 없지.","그럼, 우리 독약의 효과를 한번 확인해 볼까? 어둠의 여왕님께서 인간과 스컬지를 위해 계획해 온 독약을 내 조수 개구리 움피에게 먹이도록 하시오."}, {"개구리 움피에게 괴로움의 비약을 먹여야 합니다."}},
[500] = {"산사태 일족 현상금", {"산사태 오우거들이 폐허가 된 알터랙 도시 근처의 알터랙 산맥에 오우거 소굴을 만들었다네. 그리고 우리 정찰병에 따르면 놈들이 알터랙 폐허까지 점령했다고 하는군.","놈들이 그곳에 진을 치도록 내버려 둘 수는 없네. 일단 그곳을 완전히 차지했다고 생각하면, 우리 진영을 공격하는데 전초 기지가 될 수 있는 구릉지까지 넘볼 것이야!","북쪽의 알터랙 산맥으로 가서 오우거들을 처치해 주게나. 그리고 놈들이 가지고 있는 더러운 손가락 뼈를 가져오면 후하게 보상해 주도록 하겠네."}, {"알터랙 산맥에 있는 산사태 오우거에게서 더러운 손가락 뼈 9개를 모은 후 남녘해안에 있는 치안대장 레드패스에게 가져가야 합니다."}},
[501] = {"고통의 비약", {"오, 언덕마루에는 꽃이 만발하고 공기는 청명하고 상쾌하지. 하지만 난 이런 환경 속에서는 견딜 수가 없다니까. 지저분한 창으로 밖을 내다보면서 우리가 만든 새로운 역병이 이 세상에 합당한 죽음과 질병을 퍼뜨리게 될 날을 간절히 바란다오.","내가 하루종일 자리에 앉아 생각하기를 좋아하는 만큼 어둠의 여왕님께서 부여하신 임무를 완수하고 싶은 욕구도 커지지.","나를 좀 도와주겠소? 내 연구 결과, 산사자 피로 가장 효과적인 죽음의 혼합물을 만들 수 있다는 결론에 도달했소. 내게 산사자 피를 좀 가져다준다면 우린 새로운 재미를 찾을 수 있을 것이오."}, {"타렌 제분소에 있는 연금술사 라이던에게 산사자 피 10병을 가져가야 합니다."}},
[502] = {"고통의 비약", {"아, 이 구릉지에서 신선한 피 냄새보다 좋은 향기는 없지!","이제 재료들을 조금씩 섞기만 하면 정말 재미있는 일이 시작되는 것이오! 이 피로 만든 나의 멋진 작품을 고통의 비약이라 부르도록 하겠소. 내 계산이 정확하다면 이 혼합물은 실바나스 여왕님을 위해 매우 유용하게 사용될 것이오. 하지만 먼저 시험을 해 봐야지.","이 비약을 남서쪽에 있는 언덕마루 농장의 북쪽 집앞에 가져가도록 하시오. 과연 농부 레이의 애견 스탠리가 내 작품을 좋아하는지 어디 한번 보자고."}, {"스탠리에게 고통의 비약을 먹여야 합니다."}},
[503] = {"골디르", {"당신이 입수한 문서에 따르면 골디르 님은 아마 북쪽의 알터랙 산맥에 있는 대규모의 비밀결사대 기지에 잡혀 있을 거요. 그분은 너무 오랫동안 포로로 잡혀 계셨으니 반드시 구해 내야 하오."}, {"골디르를 구출하여 타렌 제분소에 있는 크루스크에게 가야 합니다."}},
[504] = {"산사태 전사", {"이제 산사태 오우거에 대해서는 자신이 있을 터이니, 놈들의 코를 납작하게 눌러 버릴 차례일세.","알터랙 폐허로 가서 산사태 전사들을 찾게나. 자네가 놈들을 많이 처치해야만 그 짐승 같은 놈들이 얼라이언스의 지역을 넘보지 못하게 될 걸세."}, {"산사태 전사 10마리를 처치한 다음 남녘해안에 있는 치안대장 레드패스에게 가야 합니다."}},
[505] = {"비밀결사대 암살자", {"나는 얼마 전 암살된 전임 집정관의 후임으로 임명된 남녘해안의 새 집정관일세. 암살자들을 찾아내진 못했지만, 조사한 내용에 따르면 그들은 비밀결사대에서 고용한 자들이 거의 확실하네. 비밀결사대는 지금은 멸망한 알터랙 왕국의 귀족들이 이끄는 도둑 떼라네.","지금까지 발견한 단서에 따르면 비밀결사대들은 호드 마을인 타렌 제분소의 북쪽과 서쪽, 소페라의 절벽과 코란의 비수에 있는 야영지에 숨어 있는 것 같네. 그곳으로 가서 눈에 띄는 비밀결사대 일당을 처치해 주게."}, {"비밀결사대 노상강도 12명과 비밀결사대 도둑 8명을 처치한 다음 남녘해안에 있는 집정관 헨리 말레브에게 가야 합니다."}},
[506] = {"블랙무어의 유산", {"며칠 전, 비밀결사대의 우두머리가 날 찾아왔었네. 앨리덴 페레놀드란 자인데, 자기가 이 땅의 주인이라고 하더군. 그자는 자신에게 저항하는 오크들을 노예로 삼을 계획이라고 털어놓았네. 그의 아버지이자 스승인 저주스러운 블랙무어가 계획했던 것처럼 말이야.","더 중요한 사실은 그가 엘리사라는 이름의 가녀린 부인을 함께 데려왔다는 점이야. 그녀의 목에 대족장님께서 원하시는 펜던트가 걸려 있었네.","자, 이제 난 여기서 볼일을 끝낸 후에 내갈 길을 가야겠으니 어서 이 소식을 전하도록 하게."}, {"타렌 밀농장에 있는 크루스크에게 골디르가 수집한 정보를 전해야 합니다."}},
[507] = {"앨리덴 페레놀드 경", {"반드시 앨리덴 페레놀드를 처치하여 더 이상 악행을 일삼지 못하도록 해야 하오. 그의 영지는 여기서 북쪽 방향에 있는 로다미어 호수 근처에 있소. 임무를 완수한 후 대족장님께서 원하시는 펜던트를 찾아 이곳으로 돌아오시오."}, {"앨리덴 페레놀드 경을 처치하고 그의 부인 엘리사에게 타레사의 펜던트에 대해서 물어봐야 합니다."}},
[508] = {"타레사의 선물", {"펜던트 말인가요? 그거라면 기꺼이 드리지요... 자, 가져가세요! 그에 대한 추억은 더 이상 간직하고 싶지 않으니까... 다만 제발 아무 소란도 일으키지 말고 가 주세요..."}, {"언덕마루 구릉지의 타렌 제분소에 있는 크루스크에게 타레사의 펜던트를 가져가야 합니다."}},
[509] = {"죽음의 비약", {"이제 곧 새로운 비약이 완성될 것이오. 나 같으면 저 불쌍한 인간과 스컬지들이 끝까지 싸우도록 내버려두겠는데... 안 그래도 놈들은 서로 싸울 기세니까 말이오.","아아, 하지만 우리 어둠의 여왕께서는 저들의 멸망을 좀 더 앞당기길 원하시니, 이 암울한 시기에 약간의 죽음과 역병을 퍼뜨린다고 해도 문제 될 건 전혀 없지 않겠소?","우리 계획은 이러하오. 당신이 타렌 제분소의 남쪽, 강과 남녘해안의 동쪽에 있는 네산더 농장으로 가서 진흙주둥이꽃 6송이를 모아오면 새 비약을 만들도록 하겠소."}, {"타렌 제분소에 있는 연금술사 라이던에게 진흙주둥이꽃 6개를 가져가야 합니다."}},
[510] = {"불길한 예감이 드는 계획서", {"이 문서들 중에 보급품 요청 목록이 있습니다. 그 목록에는 식량, 무기, 의류 등 평범한 물품 외에도 가면, 기름, 독 등 특이한 물품이 포함되어 있습니다.","하지만 가장 중요한 점은 보급품 요청이 상당히 긴급하다는 사실입니다. 이 물품은 '남녘해안 공격 일급 작전'이라 이름 지어진 작전을 시작하기 위해 곧 사용될 예정입니다."}, {"남녘해안에 있는 집정관 말레브에게 불길한 예감이 드는 계획서를 가져가야 합니다."}},
[511] = {"암호화된 편지", {"탁자 위에 있는 편지 중 하나는 암호로 쓰여 있어 내용을 알 수 없습니다."}, {"남녘해안에 있는 현자 딥스에게 복잡하게 암호화된 편지를 가져가야 합니다."}},
[512] = {"지휘부 처치", {"비밀결사대의 지휘부가 남녘해안 함락에 혈안이 되어 걱정이네. 놈들이 계획을 실행에 옮기기 전에 놈들의 지휘부에 결정타를 안겨야 하네.","북쪽 알터랙 산맥에 있는 스트란브래드의 폐허로 가 보게. 비밀결사대가 그 지역을 본거지로 삼고 있는 것 같네.","그리고 예전 알터랙의 귀족들이 이 살인 집단을 이끌고 있다는 사실도 이미 밝혀졌다네. 지휘부는 아마 일반 비밀결사대 일당과 똑같은 모습을 하고 숨어 있겠지만, 놈들이 낀 인장 반지로 구별할 수 있을 걸세.","그 인장 반지들을 내게 가져다주게."}, {"남녘해안에 있는 집정관 말레브에게 알터랙 인장 반지 7개를 가져가야 합니다."}},
[513] = {"죽음의 비약", {"아무 낙 없이 또 하루가 지나간다고 여기던 차에 당신이 내게 멋진 진흙주둥이꽃을 가져다주었군.","자, 그럼 먼저 꽃송이들을 잘 섞어 질 좋은 합성물을 만든 다음에... 언더시티에 계신 수석 연금술사 파라넬님의 부패 콜로이드 용액을 섞기만 하면 아주 재미있는 일이 일어날 것이오.","자, 이 진흙주둥이 합성물을 즉시 수석 연금술사 파라넬님께 전하도록 하시오."}, {"언더시티에 있는 수석 연금술사 파라넬에게 진흙주둥이 합성물을 가져가야 합니다."}},
[514] = {"스톰파이크에게 보내는 편지", {"아이언포지에서 일하고 있는 내 동료가 아마 이 편지를 해독할 수 있을 거요. 아이언포지에 있는 탐험가의 전당에 있는 도서관으로 가서 발굴조사단장 스톰파이크를 만나 보시오. 그에게 내가 보냈다고 말하고 편지를 보여 주시오. 검은바위 첨탑의 북부에서 이 편지를 읽을 수 있는 이가 있다면 그건 틀림없이 그 친구밖에 없을 거요."}, {"아이언포지에 있는 발굴조사단장 스톰파이크에게 복잡하게 암호화된 편지를 가져가야 합니다."}},
[515] = {"죽음의 비약", {"진흙주둥이 합성물이라... 정말 훌륭한 발상이군! 왜 진작 이 생각을 못했을까? 내 부패 콜로이드를 이 악마의 혼합물에 섞어야겠네.","이 진흙주둥이 혼합물의 독성을 활성화하려면 라이던에게 강렬한 트롤의 피 비약, 그리고 언덕마루 남쪽 해안에서 구할 수 있는 비수가시 일족의 비늘과 너덜지느러미 눈이 필요할 걸세. 자네가 이 실험 재료만 모두 구해다 주면 그가 알아서 할 거야. 이거, 하루빨리 그의 실험 결과를 듣고 싶군!"}, {"타렌 제분소에 있는 연금술사 라이던에게 진흙주둥이 혼합물, 강렬한 트롤의 피 비약, 비수가시 비늘 5개, 그리고 너덜지느러미 눈 5개를 가져가야 합니다."}},
[516] = {"베렌의 동굴", {"리치 왕의 언데드 무리가 우리를 공격하기 위해 숨어서 준비하고 있다는 보고가 들어왔네. 이 정보를 무기로 우리가 먼저 공격하여 허를 찌르면 놈들의 공격을 미리 방지할 수 있을 걸세.","하지만 안타깝게도 내 정보에도 허점이 있어. 놈들은 베렌의 동굴이라는 곳에 숨어 있다고 하네. 정확한 위치는 알 수 없지만, 달라란 마술사들 본거지 근처의 언덕에 있는 동굴 중 한 곳에 숨어 있을 가능성이 크지.","자네의 기지를 발휘해서 놈들을 찾아서 처치해 줄 걸로 믿네."}, {"베렌의 동굴에서 까마귀발톱 노역꾼 6명과 까마귀발톱 수호병 6명을 처치하고 공동묘지에 있는 어둠의 사제 알리스터에게 가야 합니다."}},
[517] = {"죽음의 비약", {"드디어 이 새로운 비약을 시험해 볼 준비가 됐소. 하지만 하찮은 개구리나 개에게 이 강하고 훌륭한 역병을 낭비할 필요는 없겠지? 그보다 실바나스 여왕님의 이름으로 진정한 실험의 재미를 맛보도록 하지.","던 가록으로 가시오. 저 고약한 드워프들의 병영이 어디 있는지는 알고 있겠지? 놈들의 신디거 스타우트 맥주통을 훔쳐다 주시오. 그러면 나와 함께 신나는 죽음의 향연을 구경할 수 있을 것이오."}, {"타렌 제분소에 있는 연금술사 라이던에게 신디거 스타우트 맥주통을 가져가야 합니다."}},
[518] = {"의지의 왕관", {"무그톨과 그의 산사태 오우거들이 알터랙 폐허를 짓밟아 왔습니다. 하지만 오우거의 수를 줄이지 않고는 무그톨을 단죄하기 힘들 거예요.","그러니까 먼저 놈들의 전력을 약화시킨 후에 작전을 진행해야 합니다.","알았나요? 자, 어서 가보도록 하세요."}, {"타렌 제분소에 있는 멜리사라를 위해 산사태 싸움꾼 10마리를 처치해야 합니다."}},
[519] = {"의지의 왕관", {"타르그, 무크라케, 그리고 글롬무스는 무그톨의 부관입니다. 무엇 때문인지는 모르지만, 이 오우거들은 나름대로 무그톨에게 충성을 다하고 있습니다.","이 부관들을 모두 처치한 후 그 증거를 가지고 돌아오세요. 그런 다음 남는 일은 무그톨을 제거하고 그의 왕관을 빼앗은 다음 나머지를 모두 없애는 것뿐이겠지요."}, {"타르그, 무크라케, 글롬무스를 처치하고 그 증거로 그들의 머리카락을 타렌 제분소에 있는 멜리사라에게 가져가야 합니다."}},
[520] = {"의지의 왕관", {"무그톨의 부관들을 처치했으니 이제 무그톨을 처치하고 의지의 왕관을 가져오는 일만 남았군요. 틀림없이 쉽게 왕관을 내놓지는 않을 겁니다. 그 일당이 사라졌으니 아마 무그톨도 당신이 올 거라고 예상하고 있을 겁니다.","무그톨을 찾아서 처치하세요. 그런 다음 그의 머리카락과 의지의 왕관을 가져오세요. 산사태 일족의 저항도 이것으로 종지부를 찍게 될 겁니다."}, {"무그톨을 처치하고 그 증거로 그의 머리카락과 의지의 왕관을 타렌 제분소에 있는 멜리사라에게 가져가야 합니다."}},
[521] = {"의지의 왕관", {"이제 의지의 왕관을 언더시티에 계신 샬린드라님께 가져가기만 하면 되는군요. 그러면 실바나스 여왕님께 왕관을 전해 드릴 겁니다. 경고하겠는데 무그톨과 같은 꼴을 당하고 싶지 않다면, 사사로운 목적으로 왕관을 이용할 생각은 안 하는 게 좋을 겁니다."}, {"언더시티에 있는 샬린드라에게 의지의 왕관을 가져가야 합니다."}},
[522] = {"청부업자의 계약서", {"집정관 헨리 말레브 암살 계약서입니다. 이 암살 계약서는 독특한 인장 반지가 찍힌 붉은 밀랍으로 봉인되어 있습니다."}, {"남녘해안에 있는 집정관 말레브에게 청부업자의 계약서를 가져가야 합니다."}},
[523] = {"바르두스 남작 심판", {"자네가 발견한 계약서는 한때 알터랙 왕국의 귀족이었으나 잔인한 짓을 일삼는 냉혈한으로 알려진 바르두스 남작이 서명한 것이네. 바르두스 남작 같은 자가 배후에 있다는 건 이상할 것도 없는 일이지.","자네가 그자를 찾아서 정의의 이름으로 심판을 해 주게나. 그자는 스트란브래드의 폐허나 그 북쪽의 계곡에 있는 것으로 파악되는 비밀결사대 소굴에 있을 걸세."}, {"바르두스 남작을 처치하고 그 증거로 그의 머리카락을 남녘해안에 있는 집정관 말레브에게 가져가야 합니다."}},
[524] = {"죽음의 비약", {"옆 건물에 죽음의경비병들이 사로잡힌 농부들을 인질로 감금해 놓고 있소. 고위집행관 다살리아님께서는 그 포로들 가운데 스톰윈드의 군사 전략가 중 하나가 숨어 있다는 것을 알아채셨지. 그런데 그 군사 전략가 녀석이 어젯밤 역병지대에서 죽은 채로 발견된 모양이오. 항상 날 배려해 주시는 다살리아님께서 이 포로들을 내 손에 맡기셨소.","그러니 이제 그 농부 녀석들에게 죽음의 파티를 선사할 때요. 옛 영광이 다시 찾아온 것 같지 않소?","이 오염된 맥주통을 녀석들이 수감된 방의 양탄자 위에 가져다 놓으시오. 모두 이 역병 맥주를 맛보기를!"}, {"오염된 맥주통을 사로잡힌 농부들이 있는 방의 양탄자 위에 가져다 놓아야 합니다."}},
[525] = {"깊어지는 의문", {"편지를 해독했지만 무슨 뜻인지는 잘 모르겠군. 이 편지를 알터랙 구릉지대에 있는 도적들에게서 발견했다고 했소? 정말 이상해...","이걸 남녘해안의 집정관께 가져가시오. 비록 이 편지 내용이 모호하기는 하지만 남녘해안이 알터랙에서 가장 가까운 마을이니, 혹시 위험한 일이 발생할 조짐이 있는 거라면 남녘해안의 집정관이 알고 있어야 할 테니까 말이오."}, {"남녘해안에 있는 집정관 말레브에게 해독된 편지를 가져가야 합니다."}},
[526] = {"라이트포지 주괴", {"그 상자에서 발견한 라이트포지 주괴 하나만으로는 쓸만한 물건은 만들 수 없네.","그러니 그곳으로 다시 가서 좀 더 찾아 와야 할 거야. 아마 그 난파선 주위에 있는 멀록들이 라이트포지 주괴를 가지고 있을 걸세."}, {"라이트포지 주괴 5개를 모은 다음 저습지에 있는 글로린 스틸브로우에게 돌아가야 합니다."}},
[527] = {"언덕마루 전투", {"나와 말을 할 때는 항상 정신 바짝 차리게, <class>.","나는 실바나스 님의 직속 부관이네. 우리는 연금술사들이 새로운 역병을 개발할 수 있을 때까지 인간들의 침입을 저지해야 하는 임무를 띠고 있지.","정보에 따르면 언덕마루 마을의 방어가 취약한 것 같더군. 자네의 첫 임무는 놈들의 식량 공급 및 기반 시설을 차단하는 것이야.","서쪽으로 가서 언덕마루 농장 북쪽에 있는 농장들을 공격하고, 토지 소유주인 농부 레이와 농부 게츠도 처치하네."}, {"언덕마루 농장 일꾼 6명, 언덕마루 농부 6명, 그리고 농부 레이와 농부 게츠를 처치한 다음 타렌 제분소에 있는 다살리아에게 보고해야 합니다."}},
[528] = {"언덕마루 전투", {"북쪽 농장이 공격당했다는 소식으로 인간 마을 주민들이 공포에 떨고 있다더군. 아주 훌륭해.","죽음추적자의 보고에 따르면 소작농들이 언덕마루 남쪽 농장으로 피난하고 있다는군. 바보 같은 놈들이 과수원으로 숨으면 안전할 거라 생각하는 모양이야.","가서 놈들이 얼마나 어리석은지 일깨워 주고 오라고. 언덕마루 농장으로 돌아가서 숨어 있는 농부들을 찾아내도록 하게. 그리고 우리 어둠의 여왕님의 이름으로 놈들을 처단하도록.","이상!"}, {"언덕마루 소작농 15명을 처치한 다음 타렌 제분소에 있는 다살리아에게 돌아와 보고해야 합니다."}},
[529] = {"언덕마루 전투", {"예상치 못한 상황이 발생했네. 언덕마루의 인간들이 저항을 꾀하고 있는 모양이야. 마을의 대장장이가 언덕마루 주민들에게 무기를 공급하고 있는 것 같으니 모든 무기 공급선을 차단해야 하네.","자네의 임무는 대장장이 베링턴을 처치하는 것이야. 놈의 수습생들도 함께 처치하고 그곳에 있는 동안 철괴 상자를 가지고 오는 것도 잊지 말도록 하게. 그 보급품은 우리가 언덕마루를 점령한 후 남녘해안 공격을 감행할 때 유용하게 사용할 수 있을 거야."}, {"대장장이 베링턴과 언덕마루 수습대장장이 4명을 처치해야 합니다.","철괴 상자를 찾은 다음 타렌 제분소에 있는 다살리아에게 돌아와 보고하십시오."}},
[530] = {"랠리의 복수", {"한때 나의 좋은 친구였던 발드레드가 내 아내를 살해했소. 복수에 대한 욕망이 가득하여 이제 더 슬퍼할 수가 없다오. 당신이 그 저주받을 발드레드를 처치하면 그 증거를 가져오시오. 내 그러면 보상해 주겠소.","내가 마지막으로 들은 바에 따르면 그는 은빛소나무 숲 남부의 그레이메인 성벽에서 길니아스 왕국으로 달아나려 하고 있다고 했었소."}, {"발드레드 모레이를 처치해야 합니다.","그 증거로 언더시티에 있는 랠리 앤드리언에게 발드레드의 장갑을 가져가야 합니다."}},
[531] = {"바이린의 복수", {"그런 물건은 순찰자의 오두막에 가지고 오지 마시오... 불결해, 오금이 저릴 정도로... 재수 없고... 역겹다고!","제발 가지고 나가요. 그런 끔찍한 건 아무도 보고 싶어하지 않을 테니까. 진짜!"}, {"순찰자의 오두막에 있는 바이린 스위프트윈드에게 늙은곰 수티의 머리를 가져가야 합니다."}},
[532] = {"언덕마루 전투", {"언덕마루 마을이 혼란에 빠졌으니 이제 곧 함락될 것이야. 하지만 구릉지에서 쓰레기 같은 인간들이 영원히 제거될 때까지 공격의 고삐를 늦춰서는 안 되네.","인간들에게 압승을 거두긴 했지만 이들이 지휘부를 중심으로 재집결하여 저항을 계속하고 있어. 이들을 지휘하고 있는 집정관 번사이드와 마을 의회의 원로를 제거해야 하네. 또한 그들이 하늘같이 떠받드는 성명문인 언덕마루 선언문도 파괴해 버리고, 언덕마루 마을 기록부를 빼내 오게."}, {"집정관 번사이드와 언덕마루 원로 4명을 처치해야 합니다. 언덕마루 선언문을 파괴하고 언덕마루 마을 기록부를 빼내 와야 합니다. 그런 다음 타렌 제분소에 있는 다살리아에게 돌아와 보고하십시오."}},
[533] = {"침투", {"마침내 골디르 님이 잡혀 계신 곳에 정보와 증거가 입수되기 시작했소. 인간들이 그분을 여기저기로 끌고 다니고 있지만, 수색 범위를 좁혀 여기서 바로 북쪽에 있는 대규모의 비밀결사대 야영지까지 압축되었소.","그곳으로 가서 정보를 수집해 주시오."}, {"타렌 제분소에 있는 크루스크를 위해 골디르가 어디에 있는지 정보를 얻어야 합니다."}},
[535] = {"발릭", {"으... 부상으로 다 죽어가는 마당에 적까지 만나는군.","어쨌든 당신은 날 죽이겠지만, 그 전에 당신네 종족이 마시는 남녘해안 스타우트 맥주를 좀 가져다준다면 당신이 원하는 정보를 주겠소."}, {"비밀결사대 야영지에 있는 발릭에게 남녘해안의 스타우트 맥주 한 잔을 가져가야 합니다."}},
[536] = {"해안의 위협", {"스톰윈드 근위병에서 남녘해안 같은 촌구석으로 전출되다니... 밤낮으로 항구의 비린내가 진동하는 이런 곳으로 말이오.","저 늙은 대장 레드패스에 대해서는 말도 꺼내지 마시오. 나더러 멀록을 상대하라니. 하! 기가 차서...","난 서부 해안에 있는 저 거대한 변종 물고기 주머니 속에 제 발로 걸어 들어갈 생각은 없단 말이오. 그래서 말인데, 당신이 나 대신 가서 놈들을 좀 처치해 준다면 정말 고맙겠소."}, {"남녘해안에 있는 부관 파렌 오리넬이 너덜지느러미 바다사냥꾼 10마리와 너덜지느러미 점쟁이 10마리를 처치해 달라고 했습니다."}},
[537] = {"어둠의 의회", {"자네가 가져온 편지는 아르거스 재림회 회원인 나가즈라는 자가 작성한 것이라네. 나도 아르거스 재림회가 어떤 조직인지는 모르지만, 비밀결사대와 연합했다면 위험한 조직이 틀림없네. 자네가 편지에 언급된 나가즈와 아르거스 재림회 어둠마법사를 찾아서 처치해 주게나.","비밀결사대의 세력이 가장 강한 지역인 고원을 수색해 보게. 스트란브래드 북쪽으로 가면 되네.","운이 따라 준다면, 거기서 아르거스 재림회의 사절을 찾을 수 있을 게야."}, {"아르거스 재림회 어둠마법사 4명을 처치해야 합니다.","그리고 나가즈를 처치한 증거로 그의 머리카락을 남녘해안에 있는 집정관 헨리 말레브에게 가져가야 합니다."}},
[538] = {"남녘해안", {"혹시 긴 여행도 마다하지 않으신다면 제가 스톰윈드 도서관의 장서를 다시 보충하기 위한 일을 부탁 드렸으면 합니다.","대전쟁 이후 멸망한 옛 알터랙 왕국에는 방대한 양의 장서가 있었답니다. 그 도시가 멸망해 버린 것도 비극이지만 더 큰 비극은 그 방대한 지식이 모두 소실되었다는 겁니다! 우리는 최대한 그 지식을 복원해 내야 합니다.","남녘해안은 알터랙의 폐허에서 가장 가까운 마을이랍니다. 그곳으로 가서 현자 딥스를 만나 보십시오. 그가 알터랙 폐허로 가는 길과 거기서 어떤 책을 찾아야 할지를 알려 줄 겁니다."}, {"남녘해안에 있는 현자 딥스와 대화해야 합니다."}},
[539] = {"언덕마루 전투", {"언덕마루 마을 기록부를 보면 청금석 광산이 얼라이언스 손에 넘어간 것으로 되어 있군. 게다가 그 광산은 얼라이언스의 무기 생산에 필요한 철 광석의 주공급원이지.","언덕마루 마을에 결정적인 타격을 주긴 했지만, 여전히 얼라이언스는 청금석 광산을 철저히 방어하고 있는 실정이야.","청금석 광산은 언덕마루 남쪽에 있네. 광산으로 가서 본즈라는 인간 현장감독을 처치하도록 하고 그가 지휘하는 광부들도 함께 처치해야 해. 이번 작전으로 얼라이언스에 커다란 타격을 줄 수 있을 걸세."}, {"현장감독 본즈와 언덕마루 광부 10명을 처치한 다음 타렌 제분소에 있는 다살리아에게 돌아와 보고해야 합니다."}},
[540] = {"지식 보존", {"저 짐승 같은 오우거들이 지금 알터랙 폐허를 점령하고 있으니 아직 거기 남아 있는 귀중한 책들을 어떻게 할지 정말 걱정이오. 당신이 구할 수 있는 대로 그 책들을 되찾아 줬으면 하오!","알터랙 폐허로 가서 오우거 놈들이 약탈해 간 책들을 찾아봐 주시오. 가능한 많은 책들을 찾아서 내게 가져오되, 반드시 찾아야 할 책이 한 권 있소. 바로 '그리렉의 팔'이라는 책이지. 거기에는 내가 꼭 알아야 할 고대 트롤의 지식이 수록돼 있는데, 세월의 흔적에도 손상되지 않은 사본이 알터랙의 폐허에서 마지막으로 발견되었소."}, {"되찾은 학술서 5권과 그리렉의 팔이 들어 있는 낡은 가죽 책을 남녘해안에 있는 현자 딥스에게 가져가야 합니다."}},
[541] = {"언덕마루 전투", {"드워프가 인간을 돕고 있다고? 괘씸한 꼬마 짐승들 같으니!","얼라이언스라는 놈들의 전력을 한번 시험해 볼 필요가 있겠군. 우리의 보복에도 그 조그마한 녀석들이 계속 인간을 돕는지 한번 지켜보자고. 이 지역 내에 있는 드워프 놈들의 본거지는 이미 파악되었네.","녀석들은 언덕마루 남동부에 있는 소라딘의 성벽 주위의 던 가록이라는 지역에 병영을 설치했지. 이번 전투에서 자네의 마지막 임무는 던 가록을 포위 공격하는 것이야. 놈들을 지휘하고 있는 부대장 아이언힐을 반드시 처치하도록 하게."}, {"던 가록으로 가서 던 가록 산악경비대 8명, 던 가록 소총병 4명, 던 가록 사제 2명과 부대장 아이언힐을 처치한 다음 타렌 제분소에 있는 다살리아에게 돌아와 보고해야 합니다."}},
[542] = {"밀턴에게 돌아가기", {"자네가 찾아다 준 이 그리렉의 팔을 연구하는 데는 시간이 좀 걸릴 것이오. 그동안 나머지 책들을 밀턴에게 전해 주지 않겠소? 틀림없이 그는 나머지 책들을 다시 스톰윈드 장서에 넣길 고대하고 있을 거요.","여기, 당신이 알터랙 폐허에서 모아 온 책들을 포장하고 그 목록을 작성해 두었소. 이 책들을 스톰윈드에 있는 밀턴 시프에게 다시 가져다주면 매우 고마워할 것이오."}, {"스톰윈드에 있는 밀턴 시프에게 알터랙의 학술서를 가져가야 합니다."}},
[543] = {"페레놀드 티아라", {"오, 자네로군. 내 자네에게 맡길 일이 하나 있어 기다리고 있던 참일세.","자네 페레놀드 티아라라고 들어봤는가? 페레놀드 티아라는 알터랙 왕국이 폐허가 되기 전에 알터랙을 다스렸던 페레놀드 귀족 가문의 오래된 가보일세. 아이언포지의 보석세공 장인이 만들었다고 전해지며 최고의 에메랄드가 박혀 있지.","알터랙이 파괴될 때 티아라도 사라졌지만 최근에 오우거 마법사, 욕심쟁이 그렐볼그가 발견했고 현재 또 다른 보물을 찾아 알터랙 폐허를 방황하고 있다고 하는데 말이야.","자네가 우리를 위해 그 티아라를 찾아 왔으면 하네."}, {"페레놀드 티아라를 회수해야 합니다."}},
[544] = {"감옥 습격", {"난 원래 연구 목적으로 타렌 제분소에 왔지만 우선 당면 위기의 해결이 급선무요. 반드시 이 문제를 해결해야 하니 잘 듣도록 하시오.","당신도 알다시피 몇 달 전 포세이큰 네 명이 우릴 배신하고 언더시티를 빠져나갔소이다. 놈들은 우리를 배신한 것뿐 아니라 어둠의 여왕님께서 지니신 뭔가를 훔쳐 가기까지 했지. 이 지독한 배반자들이 비밀 금고에 침입해 연금술사들의 연구에 쓰이는 4개의 유물을 훔쳐 간 것이오. 실바나스 님을 위해 그 유물들은 반드시 되찾아야 하오.","그 도둑들은 언더시티를 탈출해 달라란으로 도망갔는데, 마법사들이 그들을 로다미어 포로수용소에 격리 수용해 두었다고 하오."}, {"배반자들을 찾아내 유물을 되찾은 다음 타렌 제분소에 있는 학자 워딘 보이드글레어에게 가야 합니다."}},
[545] = {"달라란 순찰", {"달라란의 마법사들이 시가지 중심부에 거대한 마법의 반구를 만들었소. 이 난세에 자신을 보호하고자 만들었다고 하는 이도 있고, 그저 연구와 구상에 몰두하기 위한 것이라 말하는 이도 있지.","나야 뭐 이유에는 관심도 없지만... 내 관심은 반구가 아니라 그 폐허가 된 도시 외곽에 귀중한 마법의 보물이 숨겨져 있을지도 모른다는 사실이야.","하지만 마법사들과 그들의 하수인인 노예 바위 정령들이 그 지역을 순찰하고 있다오. 그러니 당신이 그들을 처치해서 놈들의 숫자를 줄이고 내게 다시 돌아와 주시오."}, {"달라란 소환사 6명과 노예 바위 정령 12마리를 처치한 다음 타렌 제분소에 있는 학자 워딘 보이드글레어에게 가야 합니다."}},
[546] = {"죽음의 전리품", {"당신이 다살리아 님을 도와 언덕마루에 있는 인간과 전쟁을 벌였다고 들었습니다. 정말 부럽군요...","인간을 처치하고, 마을을 약탈하고, 또 죄 없는 주민을 공포에 떨게 하고... 당신이 그런 재미를 보는 동안 나는 이곳 타렌 제분소에 처박혀 경비나 서고 있었단 말입니다.","아마 당신은 나처럼 늙은 죽음경비병을 불쌍히 여겨 주겠지요? 보다시피 난 인간의 해골을 모으고 있습니다. 당신은 앞으로도 오랫동안 인간들과 싸우게 될 테지요. 그 긴 전투 기간 동안 해골 30개를 모아 준다면 합당한 대가를 지급하도록 하겠습니다."}, {"타렌 제분소에 있는 죽음경비병 삼사가 언덕마루 주민의 해골 30개를 가져다 달라고 했습니다."}},
[547] = {"험버트의 검", {"고위집행관 다살리아 님께서 던 가록을 정찰하라고 나를 보내셨었지요. 물론 위험한 곳이라 너무 가까이는 가지 말라고 하셨죠. 난 놈들의 가시거리 밖에서 최대한 근접한 곳에 야영지를 설치했습니다. 그리고 아침이 밝자 물을 끓이려고 땔감을 구하러 야영지를 떠났었습니다. 야영지에 돌아와 보니 제 장비들이 보이지 않았습니다.","아무래도 던 가록에서 온 드워프 정찰병 녀석들이 지나가다 제 야영지를 발견하고 가져간 것 같습니다. 다른 건 몰라도 제 검을 꼭 찾아야 합니다! 제 검을 되찾아 주신다면 정말 고맙겠습니다. 던 가록엔 드워프들이 워낙 많아서... 제 검을 가져간 녀석을 찾기가 꽤 어렵긴 하겠지만요."}, {"던 가록에서 죽음경비병 험버트의 검을 되찾아 타렌 제분소로 돌아와 험버트에게 검을 돌려줘야 합니다."}},
[549] = {"비밀결사대원 현상 수배", {"실바나스 여왕의 권한으로 비밀결사대라는 조직의 모든 대원을 처치할 것을 명한다. 더불어 이미 구금 또는 수감 중인 해당자는 즉결 처형될 것이다. 이들은 현재 타렌 제분소 남동쪽에 있는 던홀드 요새의 폐허에 집결해 있는 것으로 파악되고 있다.","위의 범죄자를 처치한 증거를 고위집행관 다살리아에게 가져오는 이에게는 후한 보상금이 지급될 것이다."}, {"비밀결사대 도적 10명과 비밀결사대 경비병 10명을 처치해야 합니다. 그런 다음 타렌 제분소에 있는 고위집행관 다살리아에게 돌아가 보상금을 받으십시오."}},
[550] = {"언덕마루 전투", {"언덕마루 전투에서 보여준 자네의 용기와 불굴의 의지에 대해 추천서를 작성해 두었네. 내가 자네의 영웅적인 활약상에 대한 찬사를 아끼지 않았으니 지휘부에서도 자네를 인정해 줄 거야.","자, 이 봉인된 추천서를 언더시티에 있는 브라고르 블러드피스트 님께 가져가도록 하게. 자네가 자랑스럽군!"}, {"언더시티에 있는 브라고르 블러드피스트에게 다살리아의 봉인된 추천서를 가져가야 합니다."}},
[551] = {"마법의 양피지", {"이 양피지는 마법이 걸려 있어 판독이 불가능합니다. 그러나 양피지의 맨 위에 있는 하나의 단어는 읽을 수 있습니다.","나가즈"}, {"언덕마루 구릉지의 남녘해안에 있는 현자 딥스에게 마법의 양피지를 가져가야 합니다."}},
[552] = {"헬쿨라의 복수", {"헬쿨라님께서 마술사로의 변신을 거의 마쳤을 때 인간 폭도 일당이 언덕에서 의식을 행하는 장소를 발견하고 그분을 잔인하게 죽여버렸습니다. 그것도 모자라 그분의 시체를 남녘해안 묘지에 묻어 버렸지 뭡니까.","그분과 같은 위대한 마법사님께 그따위 짓을 하다니 절대로 용서할 수 없습니다. 그분의 수습생으로서 남녘해안 마을에 반드시 복수하고 말겠어요!","복수를 위해서는 먼저 헬쿨라님의 마법봉이 필요해요. 그런데 지금은 의식을 행하는 장소를 설인이 점령해 버렸답니다. 틀림없이 놈들 중 하나가 마법봉을 가지고 있을 테니 가져다주세요."}, {"설인에게서 헬쿨라의 마법봉을 되찾아 타렌 제분소에 있는 수련사 사이반드에게 가져가야 합니다."}},
[553] = {"헬쿨라의 복수", {"자, 헬쿨라의 마법봉이 필요할 테니 받으세요.","헬쿨라님은 그의 가장 강력한 주문에 사용할 의식용 불꽃 3개를 만드셨습니다. 그중 아젤의 불꽃과 베라즈의 불꽃은 구릉지 동굴에 있고 우젤의 불꽃은 그보다 위, 산에서 북쪽으로 더 들어간 곳에 위치한 동굴에 있죠.","헬쿨라의 마법봉에 이 세 불꽃 모두로부터의 힘을 불어넣어야 해요. 모든 의식이 끝나면 마법봉을 남녘해안에 있는 헬쿨라의 무덤으로 가져가야 해요. 무덤은 남쪽에 있는 남녘해안에 있는데 그곳은 경계가 삼엄한 인간 마을이에요. 무슨 일이 있어도 반드시 복수를 해야 해요!"}, {"헬쿨라의 마법봉에 아젤의 불꽃, 베라즈의 불꽃, 우젤의 불꽃의 마력을 불어넣어야 합니다.","힘을 불어넣은 마법봉을 남녘해안에 있는 헬쿨라의 무덤으로 가져가야 합니다."}},
[554] = {"스톰파이크의 판독", {"암호 해독이나 판독이라면 발굴조사단장 스톰파이크만한 전문가도 없을 것이오. 당신이 발견한 양피지를 가져가 보시기 바라오. 운이 좋으면 스톰파이크가 해독할 수도 있을 테니 말이오. 스톰파이크는 지금 아이언포지에 있는 발굴조사 본부에서 연구 중이오.","그를 만나면 내가 보냈다고 하시오. 최근에 내가 그에게 일거리를 많이 보내 주었다오."}, {"아이언포지에 있는 발굴조사단장 스톰파이크에게 마법의 양피지를 가져가야 합니다."}},
[555] = {"독특한 거북이 비스크", {"남녘해안 하면 빼놓을 수 없는 것이 바로 스톰윈드 북부 지역에서 최고를 자랑하는 요리랍니다! 저만의 비법으로 만드는 거북이 비스크도 그중 하나라 할 수 있지요. 오로지 제 요리를 맛보기 위해 멀리 어둠골에서 이곳까지 오는 이들도 있을 정도랍니다.","그런데 요즘은 통 거북이 비스크를 만들 수가 없었어요. 전엔 달라란을 지나 로다미어 호수까지 가서 직접 무쇠턱거북을 사냥하곤 했는데, 지금은 너무 위험해서 원 갈 수가 있어야지요. 북쪽 지역에 있는 무쇠턱거북의 거북이 고기를 구해다 주시면 금방 거북이 비스크를 만들어 드리도록 하죠."}, {"남녘해안에 있는 주방장 예센에게 거북이 고기 10개를 가져가야 합니다."}},
[556] = {"징표의 돌", {"달라란 마법사들은 정령을 다루는 데 탁월한 재주가 있습니다. 그 정령들은 다른 차원에서 온 강력한 존재들입니다. 마법사들은 징표의 돌을 사용하여 정령들을 조정하고 있습니다. 학자 보이드글레어 님께서 아마 이 돌들을 좀 연구하고 싶어하실 겁니다.","언덕마루 서쪽에 달라란 마법사의 땅으로 들어가서 징표의 돌을 모은 다음 내게 가져오십시오.","그 지역 내에 있는 달라란 주민에게서도 돌을 얻을 수 있겠지만, 달라란보다는 안전한 로다미어 포로수용소로 가는 게 좋을 겁니다."}, {"타렌 제분소에 있는 관리인 벨바릴에게 닳아해진 징표의 돌 10개를 가져가야 합니다."}},
[557] = {"결속의 팔보호구", {"보이드글레어 님께서는 지금 당신이 수집한 징표의 돌을 조사하느라 바쁘실 겁니다. 하지만 곧 대지 결속의 팔보호구도 필요하실 겁니다. 팔보호구는 정령들이 마법사에게 충성을 바치도록 하기 위해 정령의 손목에 채우는 물건입니다.","달라란의 폐허에서 정령들을 찾아 그들의 대지 결속의 팔보호구를 모아 오도록 하십시오. 아마 보이드글레어 님께서 매우 기뻐하실 겁니다."}, {"타렌 제분소에 있는 관리인 벨바릴에게 대지 결속의 팔보호구 4개를 가져가야 합니다."}},
[558] = {"제이나의 사인", {"사람들이 테라모어에 계시는 제이나 프라우드모어님은 얼라이언스 최고의 영웅 중 한 분이라던데요. 저도 이 다음에 커서 얼라이언스의 영웅이 되고 싶어요!","제이나님을 아세요? 저, 부탁 한 가지만 들어주실래요? 제이나님의 사인 좀 받아주실 수 있으세요? 제가 제이나님께 음... 무슨 말씀을 드려야 할지도 모르겠고 저 같은 꼬맹이한테 시간을 내줄 만큼 한가하진 않으실 테니까요... 하지만 <형이라면/누나라면> 들어주실 거예요! <형/누나>도 그분 같은 모험가잖아요! <형이라면/누나라면> 금방 사인을 해 주실 거예요!","들어주실거죠?"}, {"돌보고 있는 고아를 위해 테라모어로 가서 여군주 제이나 프라우드모어의 사인을 받아야 합니다."}},
[559] = {"파렌의 증거물", {"이건 정말 말도 안 되지 않소? 레드패스가 지난번 우리가 처치한 멀록만으로는 만족하지 않고 있다니 말이야. 오, 그렇다고 겸손해 할 필요는 없소. 당신의 활약은 정말 대단했으니까... 레드패스가 이번에는 멀록을 처치한 증거로 차갑고 끈적끈적한 멀록 머리를 가져와서 승인을 받으라고 하고 있다오. 당신이 좀 도와주시오."}, {"남녘해안에 있는 부관 파렌 오리넬에게 서부 해안에서 멀록을 처치하고 멀록 머리 10개를 모아야 합니다."}},
[560] = {"파렌의 증거물", {"난 도저히 이 멀록 머리를 못 가져가겠소. 어차피 이미 이 머리들에 손을 댄 김에 당신이 내 대신 이것들을 레드패스에게 좀 전해 주지 않겠소?"}, {"사우스쇼어에 있는 치안대장 레드패스에게 멀록 머리가 든 자루를 전달해야 합니다."}},
[561] = {"파렌의 증거물", {"부관 파렌 오리넬에게 이번에는 동부 해안에 출몰하고 있는 나가를 처리하는데 전념하라고 전해 주게나. 그리고 성과가 있을 때는 직접 와서 보고하라고 하게."}, {"사우스쇼어에 있는 부관 파렌 오리넬과 대화해야 합니다."}},
[562] = {"아, 스톰윈드!", {"스톰윈드 근위병 시절 내 스승이었던 분이 얼라이언스 총사령관께서 나가의 위협 때문에 염려하고 계시다는 전갈을 보내오셨소. 스승님이 들으신 바로는 이와 관련해서 능력을 발휘한 이에게는 진급이나 작게는 원하는 곳으로 보직을 변경할 기회를 준다고 하오.","여기서 한 가지 작은 문제가 있긴 한데... 바로 내가 나가를 감당할 수 없다는 점이오. 술집에서 주정뱅이가 벌이는 소동을 진압하거나 싸움꾼 거지를 체포하는 것쯤이야 자신 있지만 나가는 내게 버거운 상대요. 당신이 좀 도와주시오!"}, {"남녘해안에 있는 부관 파렌 오리넬을 위해 비수가시 바다사냥꾼 10마리와 비수가시 세이렌 10마리를 처치해야 합니다."}},
[563] = {"전출", {"경비대의 신임 대장인 사무엘슨 상사님은 내가 스톰윈드에 배치되어 있었을 때, 내 스승이셨으니까 보고서를 제출하는 건 문제없을 거요. 이 보고서는 그분이 나를 다시 도시로 불러들일 수 있는 충분한 사유가 될 거요. 그분께 내 보고서를 가져다주시오. 단, 저 늙은 레드패스가 눈치채지 못하게 해야 하오."}, {"스톰윈드 왕궁에 있는 상사 사무엘슨에게 파렌의 보고서를 전달해야 합니다."}},
[564] = {"크나큰 위협", {"여기 이 아름다운 말들을 잃는 것보다 더 괴로운 일은 없습니다요. 사실 지금까지 저 성가신 언데드 무리와 이 근처를 배회하는 불량배들이나 만고에 도움이 안 되는 비밀결사대 도적에게도 말을 꽤 많이 잃었답니다.","하지만 조상님을 걸고 맹세컨대, 우리 말들에게 저 알터랙의 산사자보다 더 큰 위협이 되는 건 없을 겁니다. 녀석들은 매번 언덕에서 내려와 저를 미치게 한다니까요!","스스로 영웅이라 생각하신다면 가서 녀석들을 어떻게 좀 해 주십쇼."}, {"남녘해안의 대런 말뷰가 산사자 8마리와 덩치 큰 산사자 10마리를 처치해 달라고 했습니다."}},
[565] = {"바르톨로의 설인 모피 망토", {"나, 위대한 바르톨로가 얼라이언스 내에서 명성이 자자한, 세계적인 설인 모피 망토를 만들어 주겠소!","내 장담하는데, 온 세상을 돌아다녀도 내가 만든 망토만한 건 없을 거요. 오, 뭐라고 했소, 말쑥한 <race> 양반? \"바르톨로, 당신 것보다 나은 망토는 어디에도 없소!\"라고 했소? 아, 그럼, 그렇고말고. 그런 말은 워낙 많이 들었다오.","내게 양모 두루마리 하나, 솜씨 좋은 가죽세공사가 만든 산악인의 망토 한 벌, 가는 실타래, 그리고 설인 모피를 가져다주시오. 필요한 재료들을 다 구해 오면 멋진 망토를 만들어 드리리다!"}, {"남녘해안에 있는 바르톨로 진세티에게 양모 두루마리 1개, 산악인의 망토, 가는 실타래, 그리고 설인 모피 10개를 가져가야 합니다."}},
[566] = {"바르두스 남작 현상 수배", {"고위집행관 다살리아의 명에 따라, 바르두스 남작이라는 인간을 포세이큰에 대한 범죄 혐의로 지명 수배한다. 바르두스는 비밀결사대라는 조직의 고위 간부로 알려져 있다.","그는 타렌 제분소 북쪽에 있는 알터랙 산맥의 고원에서 마지막으로 목격되었다.","위 범죄자를 처치할 것을 명한다. 바르두스 남작을 제거한 증거를 고위집행관 다살리아에게 가져오는 자에게 보상금이 지급될 것이다."}, {"바르두스 남작을 처치하고 그 증거로 그의 머리카락을 타렌 제분소에 있는 고위집행관 다살리아에게 가져가야 합니다."}},
[567] = {"위험 인물 수배!", {"위험 인물 수배!","언덕마루에 있는 다음 인간들은 위험 인물로 간주되므로 고위집행관 다살리아의 이름으로 현상금을 거는 바이다.","서기관 호레이스 화이트스티드: 죽음추적자 토마 살해 혐의로 수배함.","민간인 윌키스: 연금술사 엘리 살해 혐의로 수배함.","광부 하켓: 죽음추적자 프라이 살해 혐의로 수배함.","농부 칼라바: 언더시티에서 지급되는 보급품 약탈 혐의로 수배함.","이상의 죄인들은 모두 잠적 중이므로 찾기 힘들 수 있을 것이다. 이들을 처치하고 신고하는 자에게는 보상금을 지급할 것이다."}, {"타렌 제분소의 고위집행관 다살리아가 서기관 호레이스 화이트스티드, 민간인 윌키스, 광부 하켓, 농부 칼라바에 현상금을 걸었습니다."}},
[568] = {"그롬골 방어", {"트롬카! 그롬골에 온 것을 환영하네. 제때에 잘 도착했군. 나는 위대하신 대족장님 휘하의 주둔지 사령관으로서 이곳 정글에서 전 호드의 안전을 책임지는 영광스러운 임무를 맡고 있다네. 그런데 이곳에 서식하는 짐승 중 일부가 스토나드까지의 보급망의 안전을 지키는 우리의 임무를 방해하고 있어.","자네가 그롬골 외곽에 있는 랩터의 수를 좀 줄이도록 하게. 놈들의 수를 최대한 줄인 후에는 내게 돌아와 다른 임무를 맡도록."}, {"그롬골 주둔지의 사령관 아그로고쉬가 채찍꼬리랩터 15마리를 처치해 달라고 했습니다."}},
[569] = {"그롬골 방어", {"처음 이 정글에 도착했을 때 우리는 이곳 토착 종족들과 싸울 생각이 전혀 없었네. 스토나드에 보급품을 공급하는 임무가 최우선이었으니 말이지. 하지만 복잡한 문제가 발생하였다네. 이곳의 오우거 무리가 그롬골 남동쪽의 미즈자 폐허를 점령하고 말았으며, 수차례 경고를 했음에도 놈들은 우리 보급 짐마차에 매복 공격을 해 오고 있다네.","호드의 명예로운 일원인 자네에게 이 비협조적인 오우거들을 처리하는 임무를 맡기도록 하지. 미즈자 폐허로 가서 놈들을 그 지역에서 몰아내도록 하게. 적어도 놈들에게 확실한 경고는 될 것이야."}, {"그롬골 주둔지에 있는 사령관 아그로고쉬가 모쉬오그 투사 10명과 모쉬오그 의술사 5명을 처치해 달라고 했습니다."}},
[570] = {"모크타르딘의 마법", {"호드는 지금이라도 정글 마법의 힘을 깨닫고 이용해야 하오. 지금까지 내 마법 의식은 상당한 효과를 보아 왔지. 이제 난 아주 강력한 마법을 사용할 수 있게 되었소. 용감한 <class>|1이여;여;, 당신을 위해 아주 특별한 물건을 만들어 주도록 하지.","내게 그림자퓨마 발톱과 암호랑이의 송곳니를 가져오시오. 아무 송곳니나 사용할 수 있는 게 아니고, 반드시 오염되지 않은 순수한 상태의 송곳니여야 하오.","이 짐승은 보통 정글 여기저기를 어슬렁거리지만 가까운 곳에서도 찾을 수 있을 테니 남동쪽의 미즈자 폐허 강 건너편으로 가 보시오."}, {"그롬골 주둔지에 있는 예언자 모크타르딘이 그림자퓨마 발톱 8개와 온전한 암호랑이의 송곳니를 가져다 달라고 했습니다."}},
[571] = {"모크타르딘의 마법", {"자, 다음 재료는 상당히 구하기 어려운 물건이야. 이 깃털, 발톱, 순수한 송곳니를 제대로 한데 묶으려면 마법의 밧줄이 필요하오.","마법의 밧줄로 사용할 수 있는 건 늙은 고릴라의 힘줄밖에 없지. 덩치 큰 고릴라의 힘줄은 덩치 큰 안개계곡고릴라에게서만 얻을 수 있는데, 놈들은 무법항 동쪽 지역으로 먹을 것을 찾아다닌다고들 하오.","반드시 완벽한 상태의 힘줄을 가져와야 해. 내가 필요로 하는 완벽한 상태의 힘줄을 얻으려면 꽤 여러 마리를 처치해야 할 수도 있겠지만, 그만한 가치는 충분히 되는 물건이야."}, {"그롬골에 있는 예언자 모크타르딘에게 덩치 큰 고릴라의 힘줄을 가져가야 합니다."}},
[572] = {"모크타르딘의 마법", {"강력한 마법 물건을 만들기 위해서는 골짜기의 저 고양잇과 동물들에게서 모아 온 재료들 외에도 아직 필요한 물건이 많소. 이 정글 장신구는 내가 주술적인 의식을 통해 결합한 것이지. 호드와 대족장님을 위한 임무에 큰 도움이 될 것이오.","자, 우선 내게 줄무늬정글랩터 깃털을 좀 모아 줘야겠소. 깃털은 보통 놈들의 완장에 사용되지. 대형 투기장에서 멀지 않은, 그롬골 바로 남쪽 지역에 가면 랩터들을 발견할 수 있을 것이오."}, {"그롬골 주둔지에 있는 예언자 모크타르딘에게 줄무늬정글랩터 깃털 10개를 가져가야 합니다."}},
[573] = {"모크타르딘의 마법", {"이제 마지막 재료 한 가지만 구하면 마법을 완성할 수 있소.","남쪽에 서부 해안 쪽 절벽 높은 곳에 신성한 샘이 있소. 이 샘은 종교학자와 신비주의 학자들 사이에서 뜨거운 논란의 대상이 되고 있소이다. 아제로스의 모든 종족들은 그 샘물이 자기 일족을 위해 신이 주신 신령한 선물이라고 주장하지.","마지막으로 부탁할 일은 바로 그 신성한 샘물을 가져다 달라는 것이오. 나가 탐험가도 그 샘물을 찾아 나섰다고 하니 조심하는 게 좋을 거요. 샘물을 가져오려면 아마 놈들과 부딪쳐야 할 거요."}, {"그롬골에 있는 예언자 모크타르딘이 나가 탐험가 10명을 처치하고 신성한 샘물을 가져다 달라고 했습니다."}},
[574] = {"특수 부대", {"쿠르젠의 정규군 수를 많이 줄였으니 이제 고도로 훈련된 그의 특수 부대를 상대해야 합니다. 놈들은 이 밀림에서 우리 반란군을 학살하지 않을 때는, 쿠르젠 주둔지 뒤편에 있는 보급창인 동굴 깊숙한 곳에서 지내고 있습니다.","개인적으로 쿠르젠의 병사들을 많이 알고 있지만 그들은 노련하고, 쿠르젠에게 매우 충성스러우며, 또 잔인하니 이번 임무는 쉽지 않을 겁니다.","그들을 처치한 후에 제 상관이신 도렌 부관님께 보고하도록 하십시오. 당신에게 직접 보고를 듣고 싶어하실 겁니다."}, {"쿠르젠 특전사 10명과 쿠르젠 심리학자 6명을 처치해야 합니다.","반란군 야영지에 있는 부관 도렌에게 보고하십시오."}},
[575] = {"수요와 공급", {"모두들 저에게 \"예나 지금이나 남해는 물론 전 세계에서 가장 뛰어난 가죽세공꾼\"이라 말하죠. 나! 드리즐릭을 말이에요! 가장 뛰어난 가죽... 에헴, 흠. 내 가죽 제품은 아주 훌륭한 것으로 전문가들에게도 그 명성이 대단하죠.","방금 전에도 땜장이 조합의 조합장이신 리들복스 님께서 즐겨 신는 엑셀시어류 장화 12짝을 주문하셨다니까요. 사실, 그 신발은 저희 집에서 제일 잘 팔리는 신발입죠.","장화의 기본 형태를 잡으려면 우선 가시덤불 골짜기 북부 지역의 강가에서 악어 껍질을 구해와야 하는데 좀 구해다 주시겠어요?"}, {"무법항에 있는 드리즐릭에게 큰 민물악어 껍질 2장을 가져가야 합니다."}},
[576] = {"잃어버린 눈", {"잠깐, 멈추시오! 거기 누구요?","흑, 난 특수한 마법 눈이 없으면 아무것도 할 수 없다오. 애초에 전투에서 눈을 잃은 것부터가 불행의 시작이었지! 하지만 어찌하다 보니 운 좋게 메네실에 정박하게 되었다오. 그런데, 어떤 떠돌이 마법사가 마법으로 특수한 유리 눈을 만들어 줘서 다시 앞을 보게 되었지.","그런데 지난밤 밖으로 나가던 길에 붉은해적단 놈들이 나를 덮쳐 내 눈을 가지고 갔지 뭐요! 그 녀석들, 아무래도 무법항 쪽으로 가는 것 같았는데...","이 늙은 뱃사람을 위해 눈을 되찾아 주지 않으시겠소?"}, {"무법항에 있는 애꾸눈 디지가 붉은해적단에게서 그의 눈을 찾아달라고 했습니다."}},
[577] = {"무쇠턱악어 껍질", {"자, 이제 장화의 기본적인 형태는 잘 잡혔는데, 이 껍질만으로는 안감을 만들기에 충분하지 않네요.","\"그럼 어떻게 해야 하죠, 드리즐릭?\" 이렇게 물으시려고 했다고요? 그렇다면 걱정 마세요! 이제 이 드리즐릭이 앞으로 해야 할 일을 알려 드릴 테니까요.","안감은 보이는 부분이 아니니까, 질이 떨어지더라도 나즈페리티 호수 부근에 사는 악어의 두껍고 싼 껍질을 사용하면 충분히 장화를 완성할 수 있어요."}, {"무법항에 있는 드리즐릭에게 무쇠턱악어 껍질 5장을 가져가야 합니다."}},
[578] = {"해일의 돌", {"그 섬에 정말로 고블린 마법사와 물의 정령들이 출몰한다면 해일의 돌을 내 손에 넣을 수 있을지도 모르겠군!","자네가 이 유령의 섬을 찾아서 무슨 일이 일어나고 있는지 알아봐 주게. 아, 그리고 해일의 돌에 대해 더 알고 싶다면, 이 증서를 가지고 스톰윈드 도서관으로 가보라고. 그러면 내가 처음으로 그 전설을 접했던 책을 한 권 찾아 줄 거야.","가즈반이 정말로 그 돌을 찾을 수 있었던가...?"}, {"무법항에 있는 남작 레빌가즈를 위해 유령의 섬을 찾아야 합니다."}},
[579] = {"스톰윈드 도서관", nil, nil},
[580] = {"위스키 슬림의 잃어버린 술병", {"우린 옛 동부 내륙지가 바라다보이는 전망대 절벽 근처에서 특별 배급된 술로 잔치를 벌이고 있었지. 바다는 곧 우릴 삼켜 버릴 것처럼 사나웠고 말이야.","이 슬림님은 술에 대해서라면 일가견이 있지. 선장에게는 우리가 스톰윈드로 가던 중에 한 사나포선으로부터 빼앗은 퍼플리버보스 포트 포도주 한 상자가 있었는데, 나 같은 애주가가 그런 술을 몰라볼 리가 있겠소?","동부 내륙지로부터 폭풍이 몰아칠 때 난 술을 마시느라 정신없었지. 그런데 큰 파도가 밀려와 술병이 모두 배 밖으로 빠져버렸지 뭐요. 당신이 나를 도와 그 술병들을 찾아 주지 않는다면, 선장은 아마 닻을 끌어올리는 도르래로 나를 치려고 할 거야!"}, {"무법항에 있는 위스키 슬림이 잃어버린 퍼플리버보스 포트 포도주를 찾아 달라고 했습니다."}},
[581] = {"예니쿠 수색", {"비극적이야. 우리 족장님의 막내 아드님인 예니쿠의 일은 정말 비극적이오. 우리 일족의 관습에 따라 예니쿠는 줄구룹의 구루바시 트롤에게 보내졌지. 하지만 우리 검은창 부족이 오크 스랄 님께 편입되면서 불쌍한 예니쿠가 잊혀지고 말았소. 난 족장의 아드님인 예니쿠를 찾기 위해 저 멀리 오그리마에서 가시덤불 골짜기로 파견되었소.","내 생각에 예니쿠는 붉은머리 트롤에게 잡혀 있는 게 분명하오. 날 도와줄 생각이라면 붉은머리 놈들을 처치해 주시오. 북쪽으로 가면 놈들을 찾을 수 있을 테니 놈들의 엄니를 모아 내게 가져다주시오."}, {"그롬골 주둔지에 있는 님보야에게 붉은머리의 엄니 9개를 가져가야 합니다."}},
[582] = {"인간사냥꾼", {"우린 반드시 예니쿠의 생사를 확인해야 하오.","붉은머리 인간사냥꾼들을 처치하고 놈들이 가지고 있는 쭈그러든 해골을 가능한 많이 가져와야 하오. 그래야 그중에 예니쿠가 있는지 없는지 확인할 수 있으니까 말이오.","만약 붉은머리 놈들이 예니쿠를 죽였다면 놈들의 우두머리인 간줄라의 영혼에 600년의 저주가 내려질 것이오!","자, 어서 가시오. 북쪽으로 해안을 따라 줄다이아 폐허와 줄쿤다의 폐허로 가면 인간사냥꾼들이 많이 있을 거요."}, {"그롬골 주둔지에 있는 님보야에게 쭈그러든 해골 20개를 가져가야 합니다."}},
[583] = {"정글 탐험", {"가시덤불에 온 걸 환영합니다!","눈치챘는지 모르겠지만, 저기 계신 드워프께서 바로 얼라이언스의 전쟁 영웅이자 사냥의 대가이신 그 유명한 헤멧 네싱워리 님의 아드님이십니다. 저분의 아버님 되시는 분의 성품을 닮아, 네싱워리 2세 님은 자신의 야영지에 낯선 자가 들어오는 걸 별로 달가워하지 않지만 <class>, 당신이라면 지금까지의 활약상을 보아하니 괜찮을 것 같군요.","가서 그분과 대화를 나눠 보시기 바랍니다. 아버님을 쏙 빼닮으신 분이니 사냥에 대해 조언을 해 주실 수 있을 겁니다."}, {"헤멧 네싱워리 2세와 대화해야 합니다."}},
[584] = {"붉은머리 부족의 머리카락", {"다행히 뱀신의 가호로 그중에 예니쿠는 없었소. 하지만 아직 그를 찾을 단서가 전혀 없으니...","반드시 진실을 밝혀내야 하오. 혹시 붉은머리 부족의 족장과 그의 의술사만 진실을 알고 있다면 그야말로 곤란한 일이 아닐 수 없군. 검은창 부족의 적인 그들이 순순히 털어놓지는 않을 테니, 우선 놈들을 죽인 후 강제로 알아낼 수밖에 없소!","간줄라와 공포의 네즐뤼크를 처치하고 그들의 머리카락을 가져와 이 가마솥 안에 넣도록 하시오. 놈들은 줄쿤다의 폐허 깊숙한 곳 어딘가에 있을 거요. 그 머리카락이 있으면 놈들의 자백을 받아낼 수 있소."}, {"그롬골 주둔지에 있는 끓고 있는 가마솥에 간줄라와 네즐뤼크의 머리카락을 가져가야 합니다."}},
[585] = {"네즐뤼크와의 대화", {"<가마솥에서 들려오는 네즐뤼크의 목소리>","사실이다. 나는 검은창 부족의 아이가 끌려간 곳을 알고 있다. 비록 네가 나를 이기고 내 영혼을 묶어 놓긴 했지만 대가 없이 입을 열지는 않을 것이다!","우리 붉은머리 부족은 백골가루 부족과 전쟁을 치르고 있다. 내게서 원하는 정보를 얻으려면 백골가루 부족에게서 놈들의 전리품 해골 3개를 훔쳐 여기 우리가 있는 곳으로 가져와라. 그러면 난 황천에서도 함께할 노예를 얻을 수 있으니... 첫 번째는 발리아마에 있는 놈들의 전리품 더미에서 가져와야 하고, 다른 하나는 지아타자이, 그리고 나머지 하나는 줄맘웨에서 가져온 전리품이어야 한다."}, {"그롬골 주둔지에 있는 끓고 있는 가마솥으로 발리아마 전리품 해골과 지아타자이 전리품 해골, 줄맘웨 전리품 해골을 가져가야 합니다."}},
[586] = {"간줄라와의 대화", {"<가마솥에서 들려오는 간줄라의 목소리>","너와 너의 조상까지도 저주할 것이야!!","피와 복수만이 내 분노를 달랠 수 있을 것이다. 좋아, 나를 달래고 싶다면 내 말대로 따라야 한다.","동쪽에 백골가루 부족이 있는 곳 한복판으로 가서 그중 가장 사나운 놈들을 처치하라. 살아남을 수 있다면 놈들의 족장인 학살자 아나텍과 싸워라. 하하하! 할 수만 있다면 놈의 망가진 갑옷이라도 가져와 보시지!","아마 놈은 너를 썩은 고기를 먹는 짐승의 밥으로 던져주려 할 것이다."}, {"백골가루 사냥꾼 8명, 백골가루 인간사냥꾼 6명, 그리고 백골가루 광전사 4명을 처치해야 합니다.","그롬골 주둔지에 있는 끓고 있는 가마솥으로 망가진 아나텍의 갑옷을 가져가야 합니다."}},
[587] = {"코담배를 찾아서", {"아... 오늘따라 바닷바람이 새롭군. 특별히 좋은 건 없을지 몰라도 뱃사람의 삶이 바로 나의 삶이지.","난 항구에 정박할 때마다 좋은 술 한 병과 질 좋은 코담배를 찾곤 하지. 별로 좋지 않은 습관이긴 하지만, 해적이라면 해적다운 뭔가 있어야 하지 않나, 안 그래?","그런데 마을의 약초 장수는 떠나 버렸고, 코담배는 이미 다 팔려 물건이 없다는 사실을 알았을 때 내가 얼마나 슬펐겠나? 그 괘씸한 붉은해적단 약탈자 놈들이 약초 장수의 물건을 강탈해 갔다고 하더군.","당신처럼 용감한 <class>|1이라면;라면; 놈들이 강탈해 간 물건 중 일부를 가져올 수 있을 것 같은데, 어떤가?"}, {"무법항에 있는 디그가 코담배 15개를 가져다 달라고 했습니다."}},
[588] = {"예니쿠의 생사 확인", {"<가마솥에서 들려오는 네즐뤼크의 목소리>","나와 우리 족장의 분노를 달래 주었으니 진실을 알려 주겠다.","예니쿠라는 애송이는 자신의 일족에게조차 잊힌 자이지. 추방자 잔질이라는 의술사가 그를 데려갔지. 이제 잔질이 그의 몸과 영혼을 조종하고 있으니 아주 강력한 마법이 아니면 그를 구할 수 없을 것이다.","<class> 따위는 절대 사용할 수 없는 마법이지.","너의 동맹군인 검은창부족이라면 예니쿠를 구할 수 있을지도 모르겠군. 가능성은 거의 없어 보이지만 말이야."}, {"킨윌레이와 대화해야 합니다."}},
[589] = {"고동치는 푸른 수정", {"가시덤불 골짜기 심장부의 깊은 곳에 보물이 숨겨져 있다는 전설이 전해 오고 있소. 우린 그걸 마음의 눈이라 부르는데, 아주 강력한 힘을 지니고 있소. 당신이 마음의 눈을 찾아와 준다면 예니쿠를 어둠에서 구해낼 수 있을 거요.","난 정글의 영혼들에게 마음의 눈에 대해 물어보도록 하겠소. 그동안 당신은 남동쪽의 수정 광산으로 가서 무쇠턱바실리스크들에게서 고동치는 푸른 수정을 구해 오도록 하시오. 그 수정은 매우 희귀하지만 마음의 눈을 우리 의지대로 다루려면 그 수정의 마법이 꼭 필요하오."}, {"그롬골 주둔지에 있는 킨윌레이에게 고동치는 푸른 수정 3개를 가져가야 합니다."}},
[590] = {"도적의 거래", {"뭐야?","뭐냐고?!","돈을 달라고? 난 당신에게 빚진 게 없다고.","편지? 무슨 편지? 오호, 덤벼 보시겠다? 좋아, 친구. 한판 붙어보자고! 내가 빚을 졌다니..."}, {"데스넬에 있는 캘빈 몬태그와 싸워서 이겨야 합니다."}},
[591] = {"마음의 눈", {"마음의 눈은 동쪽에 있는 모쉬오그 오우거 소굴 안 깊은 곳에 있는 마이조스라는 오우거 마법사가 가지고 있소. 검은창 부족이 가시덤불 골짜기에 살던 시절부터 내가 알고 있던 자이지. 그 오우거는 마법 능력이 강하며 아주 사악한 자라오. 게다가 마음의 눈을 가지고 있다면 지금쯤 마법이 더 강력해졌을 거요.","당신이 마이조스를 상대하고 그에게서 마음의 눈을 빼앗아 와야 하오. 위험한 임무지만 그것이 예니쿠를 구할 수 있는 유일한 희망이오."}, {"그롬골 주둔지에 있는 킨윌레이에게 마음의 눈을 가져가야 합니다."}},
[592] = {"예니쿠 구출", {"마음의 눈을 영혼의 보석으로 바꾸어 놓았는데 이렇게 한 데에는 두 가지 목적이 있소. 우선 영혼의 보석을 사용해서 예니쿠를 기절시킨 다음 그의 머리 위에 보석을 놓으면 그의 영혼을 가둘 수 있지.","그런 다음 영혼의 보석을 님보야에게 가져가시오.","예니쿠는 여기서 좀 떨어져 있는 가시덤불 곶의 수정 해안을 따라가면 나오는 아보라즈의 폐허에 추방자 잔질과 함께 있소."}, {"님보야에게 충만한 영혼의 보석을 가져가야 합니다."}},
[593] = {"영혼의 보석 채우기", nil, nil},
[594] = {"쪽지가 든 병", {"상당히 좋은 필체의 쪽지에는 다음과 같이 적혀 있습니다.","- 누구든 저를 도와 주실 수 있는 분께-","전 지금 도움이 절실히 필요합니다. 거대한 하늘갈기 고릴라가 남해에 있는 한 섬에 절 잡아 두고 있습니다.","밤하늘의 별도 저에겐 낯설고 정글의 숲에 가려 잘 보이지 않지만, 아주 맑은 날에는 북서쪽에 거친 해안이 보입니다. 그리고 남서쪽에는 거대한 배로 보이는 것이 있습니다.","제 구세주가 되어 주실 분께 간절히 부탁 드립니다. 부디 절 도와주십시오."}, {"쪽지가 든 병의 장본인을 찾아야 합니다."}},
[595] = {"붉은해적단", {"보아하니 일자리를 찾고 있나 보군, 친구. 왜 육지를 좋아하는 <race> <class>|1이;가; 검은바다 해적단을 돕겠다고 하는지는 모르겠지만, 원한다면 말리지는 않겠어.","우리 배가 이곳 무법항에 정박한 지 얼마나 됐다고 우리 적인 고약한 저 붉은해적단 놈들이 가시덤불 골짜기 남쪽 지역에 모여 있다는 소식이 들리고 있네.","동료는 모두 술 마시고 여자를 찾아다니느라 정신이 없어! 그래서 지금 정찰병이 필요해.","폭풍 해안을 따라 무법항 북쪽에 있는 작은 만을 조사해 주라고."}, {"일등항해사 크래즈가 무법항 북쪽의 작은 만을 조사해 달라고 했습니다."}},
[596] = {"피묻은 뼈목걸이", {"난 님보야의 요청으로 그를 돕기 위해 그롬골로 파견되었소. 하지만... 여기까지 오게 된 내 나름의 목적도 있소이다.","바로 우리의 적인 붉은머리 부족 때문이지. 적의 전리품을 모으면 그들에 대항할 힘을 얻을 수 있소. 당신이 우리 일족을 도울 생각이라면 우리의 적을 처치해 주시오.","북쪽에 살고 있는 붉은머리 트롤을 처치하고 그들에게서 피묻은 뼈목걸이를 가져오시오. 가능한 많이 모아 오면 충분한 사례를 하도록 하겠소."}, {"그롬골 주둔지에 있는 킨윌레이에게 피묻은 뼈목걸이 25개를 가져가야 합니다."}},
[597] = {"붉은해적단", {"지도 상의 무법항에는 정확하게 현재 서 있는 위치에 빨간색으로 \"X\" 표시가 되어 있습니다.","휘갈겨 쓰인 글씨의 내용은 다음과 같습니다.","\"모두 현재 위치에 야영지를 확보하고 소리가 새어 나가지 않도록 하라. 남부 전투 사령부에서 공격 신호가 있을 때까지 기다려라. 접근하는 자는 모두 없애도록.\""}, {"무법항에 있는 일등항해사 크래즈에게 조사 결과를 보고해야 합니다."}},
[598] = {"부서진 뼈목걸이", {"붉은머리 부족의 전리품을 모아 왔으니 이제 다른 임무를 주도록 하지. 우리는 지난 몇 년 동안 남동쪽에 사는 백골가루 부족과 전쟁을 치러 왔소. 우리 검은창 부족은 더 이상 가시덤불 골짜기에 살지 않지만 백골가루 부족과의 충돌은 계속되고 있는 상황이오.","그러니 당신이 놈들을 처치하고 놈들의 부서진 뼈목걸이를 내게 가져다주시오. 우리의 전쟁을 도와준다면 당신과 검은창 부족의 관계는 더욱 돈독해질 것이오."}, {"그롬골 주둔지에 있는 킨윌레이에게 부서진 뼈목걸이 25개를 가져가야 합니다."}},
[599] = {"붉은해적단", {"이런 제길! 그래, 붉은해적단 놈들이 무법항을 공격할 모종의 계획을 꾸미고 있다고? 흥! 그 악당 놈들 참 대담하군그래. 무법항은 영원히 우리 검은바다 해적단의 영역인 것을!","이거 생각보다 문제가 심각한 것 같군. 이 사실을 즉시 함장 시혼님께 보고하도록 해 주라고! 저 늙은 바다의 개들이 마을 어딘가에서 기웃거리고 있다니..."}, {"함장 시혼에게 붉은해적단의 공격 계획을 보고해야 합니다."}},
[600] = {"투자개발회사 채광", {"투자개발회사는 가시덤불 골짜기 전역에서 일련의 사업을 벌이고 있죠. 덕분에 열심히 일하는 나 같은 고블린은 정직하게 돈을 벌지도 못한다고요! 당신이 날 좀 꼭 도와줘야겠어요!","투자개발회사는 북쪽의 수정 광산 근처에서 채광하고 있어요. 그들은 바실리스크 때문에 광산에 들어가지 못하고 있지만 주변 언덕에서 여전히 노래하는 수정을 캐내고 있답니다.","그들에게서 수정을 빼앗고, 그들이 정글의 주인이 아니란 걸 깨닫게 해 줘요. 그리고... 험... 그 수정은... 내게 증거로 가져오세요."}, {"무법항에 있는 크랭크 피즐버브에게 노래하는 파란 수정 10개를 가져가야 합니다."}},
[601] = {"물의 정령", {"해일의 돌을 발견했다고 전해지는 가즈반이라는 고블린 마법사가 물의 정령들을 불러낸 것이 분명해. 자네도 알고 있겠지만, 해일의 돌은 그걸 소유하는 자의 존재를 점차 소멸시키거나 사라지게 하는 저주를 내린다네.","달라란의 마법사에게 물의 정령의 족쇄에 남은 마법을 분석하게 하면 그 저주를 풀 방법을 찾을 수 있을 것 같군그래. 자네가 다시 그 섬으로 가서 그 족쇄를 좀 찾아오라고."}, {"무법항에 있는 남작 레빌가즈가 물의정령 족쇄 6개를 모아 달라고 했습니다."}},
[602] = {"마법 분석", {"들리는 바에 따르면, 달라란의 마법사들이 도시를 재건해내서는 노스렌드로 옮겨갔다더군. 예전에 도시가 있던 자리엔 커다란 구덩이만 남기고 말일세. 하지만 거기에 여전히 머무르는 마법사도 있다네. 그중 누군가 시간을 내서 족쇄를 봐줄 게야. 내가 마법사에 대해 잘 아는 건 아니지만, 어쨌든 그들이 절대 마법적인 수수께끼를 거부하지 못한단 건 안다네.","물의정령 족쇄가 든 가방을 가지고 가서 그들이 해일의 돌에 걸린 저주를 풀 수 있는 실마리를 찾을 수 있을지 알아봐 주게. 대마법사 앤자이럼 룬위버를 찾아가라고. 전에 내가 그와 거래했었고 그의 지식은 무척 방대하니 도움이 될 걸세."}, {"달라란 구덩이에 있는 대마법사 앤자이럼 룬위버에게 물의정령 족쇄가 든 가방을 가져가야 합니다."}},
[603] = {"앤자이럼의 열쇠", {"해일의 돌을 소유하고 있는 마법사는 그 돌이 가진 힘을 일종의 제단 같은 곳에 집중되도록 증폭시켜 놓았소. 당신의 힘으로 돌을 깨뜨려 버릴 수 있을 것이오.","내가 아는 바로는 그걸 가능하게 할 수 있는 물건은 고대 트롤 유물 중의 하나인 고대의 단검뿐이오. 그런데 말이오... 내가 가시덤불 골짜기에서 연구하던 시절에 그 단검을 발견했네만 그걸 내 고집 센 딸, 케이틀린에게 줘 버렸소. 그리고 그 애는 해적이 되기 위해 무법항으로 도망쳐 버렸고 말이지."}, {"무법항에 있는 케이틀린과 대화해야 합니다."}},
[604] = {"붉은해적단", {"크래즈가 검은바다 해적단을 위한 자네의 노고를 높이 사더군. 내가 믿고 우리 함대에게 매우 중요한 임무를 맡기도록 하겠네.","붉은해적단이 거친 해안에 설치한 소위 남부 사령부에 잠입해 놈들의 야영지를 급습하게. 그 쓰레기 같은 놈들이 무슨 일을 꾸미고 있는지 파악하기 위해 놈들의 해도와 명령서를 가지고 오게."}, {"무법항에 있는 함장 시혼이 붉은해적단 칼잡이 10명을 처치하고 붉은해적단 해도와 붉은해적단 명령서를 가지고 와 달라고 했습니다."}},
[605] = {"노래하는 수정 조각", {"노래하는 수정은 가시덤불 골짜기에서만 나는데다가 어떤 이들에겐 대단히 귀중한 물건이기도 하죠. 나도 수정을 팔 수는 있지만 저 지독한 투자개발회사가 독점하기 때문에 나처럼 선량한 중개상들은 수정을 구경하기도 힘들죠.","그래서 당신을 고용할까 하는데 말이에요. 가시덤불 골짜기에 사는 바실리스크들은 그 수정을 먹고 살죠. 때문에 놈들의 가죽은 매우 단단하고, 또 가끔 놈들 가죽에서 질 좋은 수정을 얻을 수도 있답니다.","아무 바실리스크나 사냥하면 되지만 북쪽으로, 줄쿤다의 폐허 남쪽 해안가를 따라 서식하는 놈들이 그래도 사냥하기 쉬울 겁니다."}, {"크랭크 피즐버브에게 노래하는 수정 조각 10개를 가져가야 합니다."}},
[606] = {"겁쟁이", {"내가 정말 참을 수 없는 건 노름 빚을 안 갚는 자들이야. 지금도 내게 빚진 녀석들이 부지기수라니까! 당신이 대신 빚을 좀 받아다 줄 수 있나?","첫 번째는 부두 옆에 사는 \"겁쟁이\" 필립이라는 놈이지. 녀석은 지난주 카드 게임 이후로 내게 빚을 많이 졌단 말이야! 이제 갚을 때가 됐고. 놈에게서 돈을 받으려면 겁을 좀 주면 될 거야.","마을을 나가서 덩치 큰 안개계곡고릴라를 잡은 다음에 고릴라의 내장을 좀 꺼내서 그 겁쟁이에게 가져가라고. 그 겁쟁이한테는 그게 짐승의 내장이 아니라 우리 동족의 내장이라고 믿게 해야 한다고. 더 끔찍해 보이도록 말이야."}, {"무법항에 있는 겁쟁이 필립에게 안개계곡고릴라 내장 5개를 가져가야 합니다."}},
[607] = {"맥킨리에게 돌아가기", {"자, 여기 이 주머니를 맥킨리에게 갖다줘요. 그리고 그에게 돈은 갚을 생각이었다고 말해 주세요!","술이 취해 길을 잃어서... 뭐, 말 안 해도 알잖아요, 어떻게 된 건지... 그도 아마 이해할 거예요. 그러니 그렇게 겁줄 필요 없다고요!"}, {"'바다늑대' 맥킨리에게 겁쟁이 필립의 빚진 돈을 가져가야 합니다."}},
[608] = {"붉은해적단", {"제길, 건방진 피랠론 녀석의 뻔뻔스러움이라니! 녀석은 절대로 전 함대는커녕 배 한 척도 우리 손아귀를 빠져나가 수정 호수를 지날 수 없을 게야.","붉은해적단 놈들은 반드시 경솔한 행동에 대한 대가를 치르게 될 거야. 이제 놈들의 공격 계획을 파악했으니 눈치채기 전에 우리가 먼저 역습할 수 있어. 하지만 모험심이 강하고 용감한 부대의 병사들만으로도 충분한 임무에 전 함대가 움직일 필요는 없지 않은가? 가장 노련한 동료를 이끌고 피랠론의 상륙 지점으로 가서 붉은해적단의 지휘부를 암살하도록 하게. 그러면 곧 그들의 함대도 침몰하고 말 거야."}, {"무법항에 있는 함장 시혼이 선장 스틸워터, 선장 킬홀과 함장 피랠론을 암살해 달라고 했습니다."}},
[609] = {"빚 돌려받기", {"몇몇한테 돈을 받아내긴 했지만 아직도 받을 돈이 많아.","이제 \"절름발이\" 모리 윌킨스, \"까마귀\" 존존, 그리고 \"왕손가락\" 처키에게서 돈을 받아 내야 하지.","이 치사한 놈들은 몇 달이나 돈을 안 갚고 있는데, 난 돈을 안 갚으려고 마을에서 도망친 줄 알았지 뭐요! 그런데 전에 놈들이 저주를 받아 마법에 걸려 정글 폐허 주위를 배회하고 있다는 말을 들었소. 뭐 놈들이 어찌 되었건 그건 내 알 바 아니고 난 그저 내 돈만 받아내면 되니까.","놈들은 이곳에서 북동쪽에 있는 아보라즈의 폐허와 주부왈의 폐허에 있소. 그들을 찾아서 돈을 받아 내 주시오."}, {"무법항에 있는 \"바다늑대\" 맥킨리에게 마우리의 의족, 존존의 황금 망원경과 처키의 큰 반지를 가져가야 합니다."}},
[610] = {"\"꽃미남\" 던컨", {"아버지가 당신을 보냈다고요? 그 괴팍하신 어른께서 이미 날 포기했다고 생각했는데...","내가 도망쳐 온 건 정말 아버지 탓이 아니에요. 그저 아버지가 기대하는 대로 백발이 성성한 그 진부한 마법사들과 어울려 곰팡이 슨 책이나 반복해 읽으면서 지루한 삶을 살기 싫었을 뿐이었다고요!","내 단검이 필요하다면 붉은해적단의 일원인 \"꽃미남\" 던컨에게서 되찾아 와야 할 거예요. 녀석의 약점은 양 어깨 사이니 제대로 한방 먹여주면 아마 단검을 포기할 거예요."}, {"\"꽃미남\" 던컨에게서 케이틀린의 단검을 구해야 합니다."}},
[611] = {"해일의 저주", {"그래서 지금 일종의 제단 같은 것을 찾는단 말인가요? 흠, 난 아버지처럼 트롤 폐허에 대한 전문가는 아니지만, 당신이 찾고 있는 그 섬 근처에 있는 가라앉은 트롤 도시의 폐허에 대해 들은 적이 있어요. 그곳부터 한번 찾아보세요.","내 검을 되찾아 줬으니 한번 빌려 주도록 하죠. 하지만 용무가 끝나면 꼭 돌려줘야 해요!"}, {"케이틀린의 검으로 해일의 제단을 파괴한 다음 가즈반을 처치하고 해일의 돌과 케이틀린의 검을 남작 레빌가즈에게 가져가야 합니다."}},
[613] = {"마우리의 의족 열기", {"이런 젠장! 그 느림보 마우리 녀석이 의족에 자물쇠를 채워 놓았는데 열 수가 없어!. 내가 녀석에게서 받아 내야 할 게 이 의족 안에 있단 말이지!","이 자물쇠를 열 수 있는 열쇠를 찾아야 하오. 마우리는 북동쪽에 있는 오우거 소굴에서 모쉬오그 오우거들과 노름을 했었다고 하던데 말이야. 녀석이 아직 살아 있을 때 들은 적이 있는데, 마지막으로 오우거 소굴에 갔을 때 돈을 너무 많이 따서 놈들에게 쫓겼다고 했었어. 그 오우거 놈들이 그 열쇠를 가지고 있을지도 모른다는 거지.","놈들에게 열쇠가 있다면 많은 수를 처치해야 우리가 찾는 열쇠를 얻을 수 있을 거요."}, {"무법항에 있는 \"바다늑대\" 맥킨리에게 마우리의 열쇠를 가져가야 합니다."}},
[614] = {"선장의 상자", {"난 여기에서 칼림도어의 톱니항까지 배로 항해하곤 했다네. 그래도 꽤 유명한 선장이었다고. 그런데...","저주받은 바다 거인들이 내 인생을 망쳐 놓고 말았어! 거인들이 내 배를 박살 내고 부하들을 모두 죽였지. 난 겨우 구명정에 몸을 맡기고 이 무법항까지 흘러 왔다네. 그 거인들 중 고를라쉬라는 놈이 내 상자를 훔쳐 가면서 그걸 코담뱃갑으로 쓸 거라더군. 뻔뻔한 놈 같으니!","들리는 소문에 의하면 고를라쉬는 이곳에서 동쪽에 있는 해안을 어슬렁거린다고 하네. 놈을 찾아서 내 상자를 되찾아 주게! 그렇게 해 준다면 정말 고맙겠군그래."}, {"무법항에 있는 허클베리 스모츠에게 스모츠의 상자를 가져가야 합니다."}},
[615] = {"선장의 커틀라스", {"그 거인들에게 내 첫 번째 배를 잃은 후, 난 다른 배를 샀고 스모츠의 복수라는 이름을 붙였지. 그리고 선원을 모으고 식량을 싣고는 그 악당 놈들을 찾아 떠났다네. 결국 놈들을 찾긴 했지만 또다시 당하고 말았지. 놈은 스모츠의 복수호를 박살내고 내 두 번째 선원들을 모두 죽였다네. 그리고 난 또다시 구명정에 의지하는 신세가 되었지.","이번에는 네골라쉬라는 거인 놈이 내 커틀라스를 훔쳐가 버렸네. 네골라쉬를 찾아 내 커틀라스를 되찾아 주게나.","스프로거와 얘기해 보게. 저 친구는 스모츠의 복수호의 요리사였는데 마지막 공격에서 살아남았으니까 그가 거인 찾는 걸 도와줄 것이야."}, {"스프로거와 대화해야 합니다."}},
[616] = {"유령의 섬", {"레빌가즈 남작님께서 내게 무법항과 가시덤불 골짜기에 떠도는 이상한 소문을 파악하여 그 진상을 캐내라고 당부하셨어요. 보통 난 유령 이야기는 잘 믿지 않지만, 선술집에서 어떤 선원들이 해안 근처에 있는 유령의 섬에 대해 이야기하는 것을 들었어요. 물의 정령들과 미쳐 날뛰는 고블린 유령이 있다고 말이에요.","레빌가즈 님을 만나면 이 이야기를 좀 전해 주겠어요?"}, {"무법항에 있는 남작 레빌가즈와 대화해야 합니다."}},
[617] = {"아키리스 묶음", {"일거릴 찾고 있소? 작은 일이라도 괜찮다면 내가 일거릴 줄 수도 있는데...","내 친구 중에 그로이라고 있는데, 나가를 죽여서 얻는 아키리스 갈대를 팔아 큰 재미를 보고 있다고 하던데. 그게 일종의 비싼 바다 갈대라고 하더군. 무엇에 쓰이는 건지는 모르겠지만 그에게 그 갈대 묶음을 가져가면 값을 후하게 쳐주고 있소.","내게 그 갈대 묶음 10개를 가지고 오면 우리도 돈을 좀 만져볼 수 있을 거요. 나가를 마지막으로 본 건 여기서 북쪽에 있는 해변가였소."}, {"무법항에 있는 선원 블로즈에게 아키리스 갈대 묶음 10개를 가져가야 합니다."}},
[618] = {"네골라쉬와의 대적", {"네골라쉬는 늘 굶주려 있는 거인이에요.","그 거인들이 우리 배를 파괴했을 때, 네골라쉬는 곧장 제 주방으로 쳐들어 왔었죠. 녀석은 다른 거인처럼 선원들로 배를 채우려 하지 않았어요. 네골라쉬는 와인과... 내가 만든 바베큐를 원하고 있었어요. 그건 제 특별 요리인 데다 재료를 구하려면 황야의 땅까지 가야 하는데 말이죠!","그 거인을 유인하려면 와인과 대머리수리 날개가 많이 필요할 거예요. 여기에서 해안을 따라 남동쪽에 있는 낡은 구명정에 음식을 실어 놓으세요. 네골라쉬가 그 음식 냄새를 맡으면 반드시 나타날 거예요!"}, {"선장 스모츠의 구명정에 대머리수리 날개 구이 10개와 밀림포도나무 와인 5병을 가져가야 합니다.","네골라쉬를 처치하고 가시덤불 골짜기에 있는 선장 스모츠에게 스모츠의 커틀라스를 가져가십시오."}},
[619] = {"네골라쉬 유인", nil, nil},
[620] = {"이니셜이 새겨진 머리띠", {"이 멋진 머리띠에는 가장자리를 따라 \"HS\" 라는 머리글자가 수놓아져 있습니다."}, {"이니셜이 새겨진 머리띠를 주인에게 돌려줘야 합니다."}},
[621] = {"잔질의 비법", {"추방자 잔질은 무법항 북동쪽의 주부왈의 폐허와 아보라즈의 폐허에서 그의 부하들과 함께 살고 있어요. 자기 일족에게 추방당했죠. 왜냐고요? 그야... 잔질이 다른 이의 마음을 조종하는 능력이 있어서 일족의 지도자들이 그를 두려워하기 때문이죠.","그는 정글 식물의 혼합물을 사용해 다른 이의 의지는 약화시키고 육체적인 힘은 강하게 만들고 있어요. 잔질의 혼합물은 다른 이를 조종하고자 하는 자들에게 무척 유용할 거예요.","그의 혼합물 견본을 가져오면, 혼합물을 유용하게 사용할 방법을 찾아보죠."}, {"무법항에 있는 크랭크 피즐버브에게 잔질의 혼합물 12개를 가져가야 합니다."}},
[622] = {"하사관 칼레브에게 돌아가기", {"당신이 혈석을 구하러 간 동안 냄비 수리를 끝냈어요. 그리고 증기 피리도 부착해 놓았고, 여기 보세요! 바닥에는 다리뼈도 자를 수 있는 신형 칼까지 달아 놓았어요!","보시다시피, 내 오래된 냄비는 이제 완벽한 요리 기계가 됐어요. 냄비 주인도 만족할 거예요."}, {"반란군 야영지에 있는 하사관 칼레브에게 크라젝의 수리된 냄비를 가져가야 합니다."}},
[623] = {"아키리스 묶음", {"당신이 없는 동안 그로이로부터 전갈이 왔소. 지난번에 보내준 아키리스 갈대에 상당히 만족했다고 하더군. 그리고 아키리스 갈대를 급하게 더 찾는 고객이 있다고 하오.","그런데 나는 앞으로 일주일 동안은 테라모어 섬으로 갈 계획이 없는데 말이야. 괜찮다면 당신이 나 대신 아키리스 갈대를 가져다주면 좋을 것 같은데... 어떻소, 구미가 당기지 않소?","당신 먼지진흙 습지대가 어딘지 알고 있지 않소? 나도 그곳에 가면 조심하려고 하지만 당신이 테라모어 섬의 친구들의 일원이 아니라면 그곳은 위험할 수 있소."}, {"테라모어 섬에 있는 선원 그로이에게 아키리스 갈대 한 묶음을 가져가야 합니다."}},
[624] = {"코르텔로의 수수께끼", {"많은 이들이 오가는 화려한 다리가 있지","저승바람 고개 동쪽, 붉은마루 산맥 남쪽","그 밑에서 무언가가 당신을 기다리고 있지","어떤 이는 암시라고, 어떤 이는 단서라 하는 그것","그것은 영원토록 그곳에 있지","슬픔의 늪에서 시간을 견디며"}, {"수수께끼를 풀어야 합니다."}},
[625] = {"코르텔로의 수수께끼", {"칼림도어의 먼 땅 당신을 기다리는 그곳","위험이 희망과 운명을 꺾어 버리는 곳","불모의 땅 동쪽 지독한 날씨","어둡고 위험한 먼지진흙 습지대","그러나 거기엔 기꺼이 시험에 도전하려는 자들을 위한 단서가 있네","남서쪽 동굴에서 누군가 찾아 줄 날을 기다린다네"}, {"수수께끼를 풀어야 합니다."}},
[626] = {"코르텔로의 수수께끼", {"위대한 혼란의 땅 로데론으로!","용감한 병사들이 죽음에 맞서 싸우는 곳","그 거대한 혼란을 뚫고 당신의 길을 찾으라","거기서 이 수수께끼의 결말을 알게 되리","동부 내륙지의 해안을 따라, 거대한 폭포의 밑바닥","물밑에서 보물이 당신을 부르고 있다!"}, {"수수께끼를 풀어야 합니다."}},
[627] = {"크라젝의 부탁", {"아라시 고원 북쪽 먼 곳에 있는 마른수염 골짜기에 가면 산 속 동굴이 하나 있어요. 그 동굴에는 혈석이라는 희귀한 광석이 있죠. 그 동굴에 있는 혈석은 일등품은 아니지만, 품질이 좋지 않은 혈석도 그걸 어떻게 사용하는지 아는 이에게는 귀하게 사용될 수 있어요.","나한테 그게 좀 필요해요. 그러니 내가 당신이 가져온 고물 냄비를 수리하고 있을 동안 혈석을 좀 가져다주세요. 충분한 양을 가져다준다면 가격을 맞춰 드리도록 하죠.","아, 그리고 그 광석 채광 방법을 모른다면 채광해 줄 자를 찾아야 할 거예요!"}, {"무법항에 있는 크라젝에게 하급 혈석 광물 4개를 가져가야 합니다."}},
[628] = {"엑셀시어", {"안감을 완성했으니 이제 겉감만 만들면 됩니다요. 겉감에는 최고의 덩치 큰 악어 껍질만 사용하죠. 악어 껍질이 더 튼튼하면서도 질이 좋기 때문에 저만의 특별한 디자인인 드리즐릭의 엑셀시어류 제품을 만들기에 안성맞춤이에요.","완벽한 껍질을 찾기가 어렵긴 하지만 덩치 큰 바다악어 껍질이라면 제격이죠. 녀석들은 다른 악어를 지켜야 할 경우에만 밖으로 나오기 때문에 바다악어를 몇 마리 처리해야 놈을 볼 수 있을 거예요."}, {"무법항에 있는 드리즐릭에게 덩치 큰 악어 껍질을 가져가야 합니다."}},
[629] = {"썩은내 산호초", {"오래전, 큰 지진으로 오래된 트롤의 도시가 북서쪽 폭풍 해안 앞바다에 잠기고 말았지. 지금은 멀록들이 도시의 폐허 속에 살면서 접근하는 이들을 공격하고 있기에 우린 그곳을 썩은내 산호초라고 부른다오.","그 폐허 속에는 트롤 전설 속의 영웅 그리렉에 대한 옛 이야기가 적혀 있는 오래된 서판이 있소. 그 이야기는 우리 검은창 부족에게 신성한 것이오. 비록 서판 자체는 너무 커서 옮길 수 없지만, 그 서판 조각 하나쯤은 반드시 우리의 새로운 고향인 오그리마에 모셨으면 하오. 서판 조각을 내게 가져다줄 수 있겠소?"}, {"그롬골 주둔지에 있는 킨윌레이에게 서판 조각을 가져가야 합니다."}},
[630] = {"쪽지가 든 병", {"당신과 저를 구하기 위해 오신 동료를 믿겠습니다. 저는 거대한 밀림의 왕 무클라에게 잡혀 있답니다. 무클라가 이 섬의 짐승들에게 명령을 내려 제가 이 사슬을 끊고 탈출하지 못하도록 잡아두게 하고 있어요. 당신이 그 고릴라를 처치하고 이 족쇄의 열쇠를 찾아 주신다면 그 은혜 정말 잊지 않겠습니다.","섬의 남동쪽 가장자리에서 무클라의 목소리가 자주 들리곤 해요. 쉬운 상대가 아니니 제발 조심하세요."}, {"밀림의 왕 무클라를 처치하고 공주 푸바에게 열쇠를 가져가 그녀를 구해야 합니다."}},
[631] = {"탄돌 교각", {"언덕에서 끔찍한 폭발이 일어났소. 정말 끔찍한 폭발이었다오!","그 폭발로 탄돌 교각을 방어하던 유능한 병사들을 잃고 말았소. 롱브레이드 님께서는 곧바로 검은무쇠 드워프에 대한 복수를 선언하셨소.","우리는 수세에 몰리고 있고 지원군은 어디에도 보이지 않는 상황이오. 며칠 전에 롱브레이드 님께서는 동쪽 다리로 러스트로크를 정찰 보내 다리를 탈환할 수 있을지 알아보도록 하셨소. 던 모드르가 포위된 지금 우리는 그를 찾으러 보낼 만한 병력이 없는 상태요.","그러니 당신이 그를 좀 찾아 주시오."}, {"라그 가마슨이 러스트로크를 찾아 달라고 했습니다."}},
[632] = {"탄돌 교각", {"가여운 러스트로크는 검은무쇠 드워프의 매복지까지 들어갔던 것 같습니다. 꽉 쥐고 있는 그의 주먹 옆에서 양피지 문서가 발견되었습니다.","이 슬픈 소식을 라그 가마슨에게 보고하고 발견한 양피지 문서를 보여 줘야 합니다."}, {"저습지의 던 모르드에 있는 라그 가마슨에게 돌아가서 러스트로크의 시체에서 발견한 양피지 문서를 보여 줘야 합니다."}},
[633] = {"탄돌 교각", {"어떻게 이런 일이! 가엾은 러스트로크. 이건 있을 수 없는 비극이오.","그의 죽음이 헛되게 해서는 안 되오. 당신이 발견한 이 양피지 문서에 따르면 캄 딥퓨리가 탄돌 교각 건너편 아라시 고원에 비밀리에 설치해 놓은 폭약을 감추기 위해 위장 공격을 한 것이라고 되어 있군.","만약 그 폭약으로 다리가 파괴되면 북쪽으로의 주요 보급로를 차단당하게 되오. 그 전에 우리가 폭약을 찾아내 반드시 파괴해야 하오! 임무를 완수하면 여기로 돌아오시오. 당신이 우리의 유일한 희망이오."}, {"탄돌 교각 건너편에 있는 폭발물을 찾아서 파괴해야 합니다."}},
[634] = {"얼라이언스에 지원 요청", {"작은 부탁 하나만 더 해도 되겠소, <class> 친구? 우리는 아이언포지로부터 예비 사단을 기다리고 있소. 그런데 로고의 말에 의하면 북쪽 아라시 고원의 임시 주둔지에 얼라이언스 야영지가 있다고 하오.","그곳으로 가서 부대장 나이얼스 님을 만나보시오. 그리고 그녀에게 우리가 던 모드르와 탄돌 교각을 탈환하도록 지원 병력을 보내줄 수 있는지 물어봐 주시오."}, {"아라시 고원의 임시 주둔지에 있는 부대장 나이얼스를 찾아야 합니다."}},
[635] = {"산 속의 수정", {"수정을 손에 쥐자 고동치기 시작합니다.","마치 도움이 필요한 누군가가 자신의 의사를 전달하려 애쓰는 것처럼 수정에서는 간절한 애원이 느껴집니다.","마음속에 아라시 고원 북쪽 산악 지역과 그곳의 땅속에서 튀어나온 빛나는 수정에 대한 환영이 보입니다.","그곳으로 끌려가는 듯한 느낌이 듭니다."}, {"환영에서 보았던 수정을 찾아야 합니다."}},
[636] = {"대지의 전설", {"이 책은 정령 공주 미즈라엘과 그녀가 아라시 고원 아래 깊은 곳에 갇힌 데 대한 일화가 쓰여 있습니다.","이 책은 또한 비밀의 수정에 대해서도 언급하고 있습니다. 미즈라엘의 결정이라 불리는 이 수정들은 공주의 감옥으로 통하는 문이라고 합니다.","만약 이 미즈라엘의 결정이라는 것을 찾으면 미즈라엘과 대화를 해야 할 것 같습니다..."}, {"미즈라엘의 결정을 찾아야 합니다."}},
[637] = {"설리 발루의 편지", {"편지 봉투 안에는 공들여 쓰인 편지 몇 장이 들어 있는데, 물에 젖은데다 전쟁의 모진 상처들로 인해 다 낡아 버려서 편지의 몇 장이 손에 쥐자마자 녹아 없어져 버립니다.","편지는 아이언포지의 사라 발루 부인 앞으로 보내진 것이며 \"사랑하는 사라에게...\"로 시작하고 있습니다."}, {"설리 발루의 편지를 사라 발루에게 전해야 합니다."}},
[638] = {"트롤베인", {"토라스 트롤베인도 죽었고 스트롬가드도 폐허가 되었으니, 볼진께서 이제 인간의 손에서 전설의 검인 트롤칼라를 빼앗아 앞으로 있을 줄구룹의 트롤과의 전투에 사용할 수 있도록 하라고 지시하셨소.","검 탈취 작전을 감독하기 위해 노련한 사냥꾼 중 하나인 젠구가 아라시 고원 북동부 지역의 해머폴로 파견되었으니 그를 만나보도록 하시오."}, {"아라시 고원의 해머폴로 가서 젠구와 대화해야 합니다."}},
[639] = {"스트롬의 인장", {"고대에 트롤들은 아라토리안 제국에 대항하여 싸웠네. 그 중에는 스트롬의 트롤베인이라는 별명으로 유명한 이그네우스 경이 가장 큰 호적수였지. 그자는 고대어로 트롤 사냥꾼이라는 뜻을 가진 트롤칼라라는 검을 들고 전투에 나섰지.","하지만 지금의 트롤베인의 후예들의 힘은 약해졌고 그들의 성채는 폐허가 되었네.","트롤베인은 지금 스트롬가드의 생존자 몇 명이 나눠 가지고 있는 여러 개의 인장으로 보호되어 있는데 이 인장들을 빼앗아야 하네.","첫 번째 인장은 비밀결사대가 가지고 있는데, 스트롬가드에 있는 녀석들 중 한 명에게 있을 것이야."}, {"해머폴에 있는 젠구에게 스트롬의 인장을 가져가야 합니다."}},
[640] = {"깨진 인장", {"토라스 트롤베인의 무덤은 세 개의 인장으로 보호되어 있네. 자네가 비밀결사대로부터 빼앗아 온 것은 이 중 첫 번째이고 나머지 두 개는 철통같이 수비하고 있는 스트롬가드 민병대가 가지고 있으니 구하기가 더 어려울 걸세.","세 개의 인장 중 한 개인 소라딘의 인장은 토라스의 아들인 갈렌이 열한 개의 조각으로 나누어 스트롬가드 수호병들에게 나누어 주었지. 그 조각을 찾아내 우리 트롤 주술사인 토건에게 가져다주면 그가 조각을 결합하여 완전한 인장을 만들 걸세."}, {"스트롬가드의 수호병들에게서 인장 조각 11개를 빼앗아 해머폴에 있는 토건에게 가져가야 합니다."}},
[641] = {"소라딘의 인장", {"흠잡을 데 없이 완벽하게 인장을 완성했네. 이제 이 인장을 사용하여 트롤베인의 무덤에 있는 봉인을 깨뜨릴 수 있다네. 젠구에게 가 보게. 분명히 다른 임무가 또 있을 거야."}, {"해머폴에 있는 젠구에게 소라딘의 인장을 가져가야 합니다."}},
[642] = {"함정에 빠진 공주", {"제 이름은 미즈라엘입니다. 전 대지의 공주인데 거인들이 나를 잡아서 아라시 고원 땅속 깊이 가두어 놓았답니다. 이 수정 결정이 제가 지상 세계와 교신할 수 있는 유일한 수단이에요.","제발 절 도와주세요. 동쪽의 마른수염 골짜기에 있는 동굴에 사는, 거인과 한편인 마른수염 코볼트들에게 이것과 같은 결정이 있어요. 그 결정을 작동시키려면 코볼트들에게서 미즈라엘의 결정 파편을 빼앗아야 합니다.","제발 부탁해요. 절 도와주세요!"}, {"미즈라엘의 결정 파편을 12개 모아서 마른수염 골짜기에 있는 오색 결정에 가져가야 합니다."}},
[643] = {"아라소르의 인장", {"아라시 고원 전체는 말할 것도 없고, 스트롬가드 조차도 완벽히 자신의 휘하에 두지 못하고 있는 인간족의 왕자가 자신의 졸개 기병을 데리고 온 지역을 돌아다니고 있네. 도대체 그 이유를 알 수가 없단 말이야. 하기야 인간의 머릿속에 뭐가 들어 있는지 누가 알겠나?","이유야 어쨌든 중요한 건 그가 기병 대장인 부관 밸러콜에게 인장을 맡겼다는 것이야. 부관 밸러콜을 찾아서 처치하고 인장을 내게 가지고 와주게."}, {"아라소르의 인장을 찾은 후 해머폴에 있는 젠구에게 돌아가야 합니다."}},
[644] = {"트롤베인의 인장", {"토라스 트롤베인의 무덤을 여는 데 필요한 마지막 인장은 그의 아들이 가지고 있네. 트롤베인의 아들은 스트롬가드의 깊은 곳, 수호대가 장악하고 있는 지역의 작은 예배당에 숨어 있어. 갈렌 왕자 주위는 경호가 삼엄한 데다가 본인도 막강한 전사이니 이번 임무가 가장 어려울 걸세."}, {"왕자 갈렌 트롤베인을 처치하고 해머폴에 있는 젠구에게 트롤베인의 인장을 가져가야 합니다."}},
[645] = {"트롤칼라", {"이건 뭐지? 인장 네 개를 한 곳에 모았더니 하나의 인장으로 결합되었네. 분명 이 인장을 사용하면 트롤베인의 무덤에 설치된 봉인을 깨뜨릴 수 있을 거야.","트롤베인의 유골은 스트롬가드 내부 성소 뒤편에 있는 지하 납골당에 묻혀 있네. 가서 트롤칼라를 되찾아 오게."}, {"스트롬가드에 있는 트롤베인의 무덤에서 트롤칼라를 가지고 와야 합니다."}},
[646] = {"트롤칼라", {"빛이 서서히 사라지며 돌로 된 칼집 안에 들어 있는 트롤칼라를 집었습니다."}, {"해머폴에 있는 젠구에게 트롤칼라를 가져가야 합니다."}},
[647] = {"맥크릴의 밀주", {"어젯밤 오래 묵은 밀주를 좀 마셨지. 보초를 서는 동안 술에 취해 버린 걸 알면 롱브레이드가 날 죽이려 들 거요.","아, 술 좀 마셨더니 지독히 괴롭군. 머리 위로 전차 부대가 하나 요란하게 지나가는 것 같아.","오, 이런! 시간이 이렇게나 됐군! 남녘해안에 있는 양조업자 빌저에게 밀주를 보내 밀린 외상값을 갚기로 했는데...","갚기로 한 시간이 15분밖에 안 남았잖아! 부탁이니 이걸 좀 전해 주시오. 서둘러야 하오!"}, {"남녘해안에 있는 양조업자 빌저에게 맥크릴의 밀주를 가져가야 합니다."}},
[648] = {"OOX-17/TN 구출", {"로봇에서 노움의 목소리가 다시 흘러나옵니다.","\"OOX-17/TN을 넓고 안전한 장소로 옮겨가야겠어요. 긴 이륙 절차를 시작할 수 있도록 말이에요. 은폐 기능이 탑재되어 있긴 하지만 다시 작동하려면 저도 시간이 좀 필요해요. 그 로봇을 현재 위치에서, 그러니까 시간의 동굴 근처로 호위해주세요! 그곳이라면 이륙하기에 충분할 거예요!\"","\"그 녀석을 무사히 호위한 다음에 무법항으로 와서 절 찾으세요! 이상, 오글소프 오브노티쿠스였습니다!\""}, {"OOX-17/TN을 안전하게 호위하고 나서 무법항에 있는 오글소프 오브노티쿠스에게 가서 보고해야 합니다."}},
[649] = {"리플 회수", {"당신이 뭘 좀 찾아와 줬으면 하는데. 당신도 나처럼 리플 애호가라면 이 일이 맘에 쏙 들 거요! 리플이라니까!","술 말이야 술! 이런 바보하고는...","버려진 호드 전초기지에 녀석들이 장사를 할 때부터 숨겨두었던 동부 내륙지 벌꿀 리플이 있소이다. 그런 좋은 리플은 이제 더 이상 찾지도 못하지! 말톤의 멍청한 친구 녀석 중 하나가 어디 있는지 안다니까 가서 포장된 리플을 한 상자 가져다주시오. 아, 내 소장품으로 간직하게 두 개 더 가져오고! 말톤에게 얘기하면 자세히 알려줄 거요."}, {"오그리마에 있는 말톤 드로퍼스와 대화해야 합니다."}},
[650] = {"리플 회수", {"당신이 찾아갈 엘프의 이름은 길버라딘 선체이서라고 해요. 냄새만 맡고도 무슨 술인지 다 알아맞힐 정도의 애주가죠! 리플 포장도 할 줄 아니까 그와 대화해 보세요. 포장할 병은 모두 여섯 개예요. 두 개는 아버지께 드릴 거고요. 그자가 수고비를 얼마나 달라고 할지는 모르겠지만... 좀 요구하긴 할 거예요. 리플을 다 포장하고 아버지 드릴 두 병을 챙기면 여기로 돌아오세요!","길버라딘의 야영지는 동부 내륙지의 어떤 하이 엘프 오두막 근처에 있는데 그곳 하이 엘프들이 조용한 성격은 아니랍니다. 제 말을 믿으세요."}, {"동부 내륙지의 야영지에 있는 길버라딘 선체이서와 대화해야 합니다."}},
[651] = {"마법진의 돌", {"아라시 고원 전역에는 4개의 봉인석이 흩어져 있습니다. 이 돌들이 저를 땅속에 묶어 놓고 있는 거랍니다.","4개의 돌을 모두 빼내려면 가장 강한 내부 봉인석을 빼내야 한답니다. 그러기 위해서는 다른 세 개의 돌에서 열쇠를 구해야 합니다.","이 돌들은 수호자가 지키고 있는데, 당신이 머리를 쓰거나 운이 좋다면 수호자를 내쫓을 수 있을 거예요."}, {"서부 봉인석, 동부 봉인석, 외부 봉인석에서 각각 불타는 열쇠, 용마루장식 열쇠, 천둥 열쇠를 구해야 합니다.","세 개의 열쇠를 내부 봉인석으로 가져가야 합니다."}},
[652] = {"쐐기돌 깨기", {"봉인석을 열어서 몸이 훨씬 자유로워졌지만 아직도 족쇄가 채워져 있답니다. 저 원형 모양의 돌 사이에 있는 게 쐐기돌입니다. 열쇠는 저를 가둬 놓은 자 중 한 명인 포즈루크가 가지고 있어요.","거인 포즈루크에게서 저들이 질서의 마법봉이라고 부르는 열쇠를 빼앗은 후 쐐기돌을 열어야 합니다.","지금까지 절 많이 도와 주신 것 알고 있습니다. 이제 조금만 더 도와주시면... 제 감사의 마음이 온천하에 퍼지게 될 겁니다!"}, {"포즈루크를 찾아 처치해야 합니다. 아라시 고원에 있는 쐐기돌에 질서의 마법봉을 가져가야 합니다."}},
[653] = {"미즈라엘의 친구들", {"<미즈라엘의 말>","자유다! 이제 자유야! 저를 사로잡은 자들에게서 벗어나 이제서야 힘을 모을 수 있게 됐답니다. 혹시라도 그들과 다시 마주치면 절 힘으로 장악하여 다시 가둬 놓을 겁니다.","하지만 때가 되면 제가 직접 거인들과 싸울 것이고, 저를 가둬 놓은 대가를 치르게 할 겁니다!","당신은 이제부터 제 친구입니다. 때가 되면 저를 다시 한 번 도와 주시기 바랍니다. 제가 준비되면 지상으로 소환해 주시길 부탁드립니다.","아이언포지의 쓸쓸한 뒷골목에 있는 게릭 본그립과 얘기를 나눠 보시기 바랍니다. 그는 날 소환하는 방법을 알고 있습니다."}, {"아이언포지의 쓸쓸한 뒷골목에 있는 게릭 본그립과 대화해야 합니다."}},
[654] = {"타나리스 현장 견본 수집", {"동력원을 가동하자 휴대용 분석 도구가 빛을 발하며 진동하기 시작하는 것을 보니 계획한 대로 작동하는 것 같습니다.","선임 기술자 빌지위즐에 의하면 타나리스 사막에서 필요한 자료를 수집하기 위한 시간이 두 시간 남았습니다. 두 시간 안에 다 수집하지 못하면 모든 자료를 잃게 되어 선임기술자 빌지위즐에게서 또 다른 동력원을 구해야 합니다.","임무를 마치면 그에게 돌아가야 합니다."}, {"타나리스 사막에 사는 바실리스크, 하이에나. 전갈에게서 쓸만한 바실리스크 견본, 쓸만한 하이에나 견본, 쓸만한 전갈 견본을 각 8개씩 구해야 합니다. 동력원이 다 떨어지기 전에 가젯잔에 있는 선임기술자 빌지위즐에게 분석 도구를 가져가야 합니다."}},
[655] = {"해머폴", {"마치 어제와도 같이 선명하군... 이 끔찍한 곳에 갇혀서 수많은 해를 절망 속에서 지내왔지. 그러던 중 마침내, 마침내! 대족장님께서 나타나셨네. 검은 갑옷을 입고 워해머를 높이 든... 둠해머님이 말이야.","그러나... 너무나 끔찍한 일이 일어나고 말았네... 비겁한 녀석의 단 한 번의 일격에 가장 위대한 영웅께서 쓰러지시다니... 그 비겁한 놈이 말을 타고 달려와 창으로 뒤에서 둠해머님의 등을 찔러 버리고 말았지... 난 무릎을 꿇고 말았네. 내 눈앞에서 희망이 사라지는 것을 보았어... 도대체 무슨 희망이 있었겠나? 무슨 희망이..."}, {"해머폴에 있는 토건과 대화해야 합니다."}},
[656] = {"공주 소환", {"미즈라엘이 힘을 되찾기 전에 처치해야 해. 그렇지 않으면 힘을 회복한 후에 자기를 가둬 놓았던 자들을 해치러 갈 거야! 어떤 녀석들은 미즈라엘을 통제할 수 있다고 생각하는데, 그건 다 착각이라고. 그게 가능하다면 애당초 왜 거인들이 그녀를 묶어 놓았겠어??","말도 안 되지, 안 그래?","미즈라엘을 무찌르려면 그녀와 처음 얘기를 나눈 미즈라엘의 결정에서 이 두루마리로 그녀를 소환해야 해. 그녀를 무찌르고 기괴한 족쇄를 주은 다음 미즈라엘의 결정에 봉인해 놔.","그러면 다시 갇히게 될 거야."}, {"미즈라엘의 결정으로 가서 그녀를 소환한 후에 처치해야 합니다.","기괴한 족쇄를 주워서 다시 미즈라엘의 결정에 봉인해야 합니다."}},
[657] = {"새로운 역병의 징조?", {"<서류를 읽기 시작하는 쿠에>","이건 꽤 흥미롭군요. 연금술사 조렐이 이곳 고셰크 농장에서 치명적인 실험을 하고 있었던 것 같아요. 힐스브래드하고 어떤 관계가 있는지는 확실하지 않지만 핀이 이걸 그대로 내버려 두는 걸 원치는 않을 거예요.","당신이 가 있는 동안 오크 몇 마리와 싸우다가 상처를 좀 입어서 이 몸으로는 키넬로리를 도와 주기 어려워요. 가능하다면 당신이 키넬로리를 보호해 주지 않겠어요? 그러면 그녀가 농장으로 들어가 조렐이 아직 타렌 밀농장에 보내지 않은 물건들을 훔쳐 올 거예요."}, {"키넬로리와 대화해야 합니다."}},
[658] = {"새로운 역병의 징조?", {"그러니까 핀이 우리가 잘못되지 않았는지 걱정돼서 당신을 보냈다고요? 하기야 놀라운 일도 아니죠. 핀은 워낙 걱정이 많은 성격이니까요.","우리는 며칠 간 농장을 지켜보고 있었는데, 얼마 전에 키넬로리가 저기 남쪽에 있는 작은 집에서 급사가 빠져나가는 것을 봤다고 하더군요.","키넬로리가 평소에 말이 많긴 하지만 위험한 지역에서는 매우 정확한 감각을 자랑한답니다.","참, 혹시 당신도 실력 있는 수색자인가요? 우리 생각에는 급사가 타렌 제분소로 전갈을 가지고 간 것 같아요. 그 서류를 우리 손에 넣을 수 있으면 언덕마루에서 일어나는 일에 대한 단서를 얻을 수 있을지도 몰라요."}, {"포세이큰 급사를 찾아서 봉인된 서류를 고셰크 농장 근처에 있는 쿠에에게 가져가야 합니다."}},
[659] = {"새로운 역병의 징조?", {"만나서 반갑소. 난 이 지역 안에서 일어나는 이상한 일들을 조사하는 모험가 집단을 이끌고 있소이다.","언덕마루에 사는 내 사촌은 최근에 기르고 있던 개, 스탠리를 죽여 버릴 수밖에 없었소. 이상하게 개의 몸집이 점점 거대해지더니 근처에 오는 모든 이들을 사납게 공격했기 때문이라오.","내 생각에는 언데드가 이 일에 관련되어 있는 것 같아 이를 증명할 증거를 찾고 있소.","내 동료 중 두 명, 쿠에와 키넬로리가 아라시 고원에 있는 고셰크 농장으로 갔는데 며칠 동안 아무 연락이 없다오. 그들을 찾아봐 주겠소? 점점 걱정이 되기 시작해서 말이오."}, {"아라시 고원에 있는 고셰크 농장 근처에서 쿠에와 키넬로리를 찾아야 합니다."}},
[660] = {"새로운 역병의 징조?", {"좋아요. 난 언제나 준비되어 있답니다.","그럼 시작할까요?"}, {"키넬로리가 쿠에에게 돌아갈 때까지 보호해야 합니다."}},
[661] = {"새로운 역병의 징조?", {"키넬로리의 현재 모습이 지난번에 선술집에서 하룻밤 묵고 나서의 모습보단 낫군요. 여기까지 정말 잘 호위해 주셨네요.","그녀가 농장에서 가져온 물건을 핀이 보면 이곳 아라시에서 대충 어떤 일이 벌어지고 있는지 짐작할 수 있을 거예요. 보복 공격이 없는지 확인하기 위해 우리는 이곳에 좀 더 남아 있을게요. 먼저 남녘해안으로 가서 핀에게 얘기해 주겠어요?"}, {"언덕마루 구릉지의 남녘해안에 있는 핀 오델릭을 찾아야 합니다."}},
[662] = {"심해의 인양 작업", {"여어, 안녕! 오브린 선장님께서 배를 후미로 이동하라고 지시하셨지. 우리는 여기에서 분명히 보물을 찾을 수 있을 거로 생각했다고. 선구자호는 무사히 정박했는데 처녀의 망상호와 은빛소나무의 영혼호가 육지에 접근하자 갑자기 조수가 낮아지기 시작했지. 태어나서 여태껏 그때처럼 이상한 일은 처음 봤다니까!","두 배는... 마치 암초 위의 사원 같은 곳에 좌초되었고 순식간에 침몰했지. 비극 그 자체였어.","우린 이곳을 떠나기 전에 그 두 배의 항해일지와 해도를 되찾아야 해. 정보가 남의 손에 들어가서는 안 돼!"}, {"일등항해사 닐즐릭스가 처녀의 망상호와 은빛소나무의 영혼호에서 해도와 항해일지를 찾아 달라고 했습니다."}},
[663] = {"육지다!", {"롤로 눈에 작은 <race>|1이;가; 보인다! 롤로 항상 망 보고 있다!","팔디르의 만에 온 걸 환영한다. 오브린 선장이 여기 몇 시간만 있으면 된다고 했다. 보물을 모아서 무법항으로 가져가기만 하면 된다고 했다.","하지만 롤로 생각하기에는 시간이 더 오래 걸릴 것 같다. 우리 함대에서 배 두 개 잃었다. 불쌍한 은빛소나무의 영혼호와 처녀의 망상호... 지금은 저 바다 밑에 자고 있을 거다!","여기서 쉴 거면 오브린 선장과 얘기해 보는 게 좋을 거다."}, {"아라시 고원의 팔디르의 만에 있는 선장 오브린과 대화해야 합니다."}},
[664] = {"비통한 마음", {"아, 정말로 끔찍한 삶이로군. 난 지금 여기에 불가에 앉아 맑은 공기로 숨 쉬고 있을 게 아니라 진작에 물속에 빠져 죽었어야 해.","선원들이 아름다운 처녀의 망상호를 지키기 위해 그렇게 노력했건만... 그들에게 명복을, 그들의 영혼에 평온이 있기를.","오브린은 해안에 안전하게 다가갈 수 있었지만 우린 운이 나빴어. 바다가 우리를 위에서 덮쳤지, 정말일세.","분명히 수면 위에 있었는데 갑자기 위에서 물이 우리를 덮쳤지 그리고 그 짐승들도! 그 짐승들이 생존자를 모두 죽여 버렸다네. 흉측한 나가 놈들 말일세. 난 정신을 잃었는데 깨어나 보니 이곳에 이렇게 살아 있더군.","제발, 그 짐승들을 좀 죽여 주게."}, {"팔디르의 만에 있는 선장 스틸구트가 비수가시 침략자 10마리와 비수가시 여마법사 3마리를 처치해 달라고 했습니다."}},
[665] = {"가라앉은 보물", {"자, 이제 우리도 어엿한 검은바다 해적단이니까 오브린 선장이 잃어버린 엘프의 보물을 찾도록 도와 주어야 한다오.","아무런 도움없이 어두운 바다 속에서 보석을 찾는다는 건 거의 불가능한 일이지. 드랙슬게이지가 고글을 몇 개 만들어 놓았다오. 언덕 바로 위의 동굴 속에 있는 마법의 돌에서 힘을 끌어와 고글에 불어넣어야 한다더군.","그런데 그 동굴에는 저주가 걸려 있다오! 가까이 다가가면 공격을 받을 것이야. 그러니까 당신은 내가 고글에 마법의 돌의 힘을 채울 수 있도록 보호해 주면 된다오."}, {"학자 피즐솔프가 동굴에 들어갔다 나올 수 있도록 호위해야 합니다."}},
[666] = {"가라앉은 보물", {"에, 보물이 해저에 가라앉은지 하도 오래돼서 보석이 전부 단단한 돌로 굳어져 버렸소. 하지만 이 고글에 담긴 힘 덕분에 보석을 쉽게 찾아낼 수 있을 거라오.","이 키 작은 노움의 재간이 정말 뛰어나지 않소?","자, 그러니까 보물찾기 고글을 빌려가서 오브린 선장을 위해 잃어버린 보물을 찾아오시오.","내가 직접 아래로 헤엄쳐 갈 수도 있지만... 음... 그러니까, 안전한 육지에서 과학적으로 중요한 일이 자주 발생하니까... 그러니까... 난 여기 자리를 지켜야 한단 말이지."}, {"팔디르의 만에 있는 발명가 드랙슬게이지가 엘프의 보석 10개를 주워 오고 이 일을 끝낸 후 보물찾기 고글을 돌려 달라고 했습니다."}},
[667] = {"나가의 공격", {"큰일이군. 비수가시일족 녀석들이 공격을 하려고 모이는 모양이야.","나가는 전투에서 잔악하기로 유명한 녀석들이라 내가 죽을 때까지 계속해서 공격할 것이오.","내가 내 선원들보다 내 목숨을 아끼는 건 아니지만 나만큼 바다에 대해 잘 아는 자가 없으니 내가 없으면 어차피 선원들도 무법항까지 돌아가지 못할 것이야. 그러니 날 꼭 지켜 줘야해.","나가 녀석들은 바다에서 접근해 올 것이오. 나와 함께 이 위에 남아 녀석들의 공격을 막아주시오. 배 위에 있는 대포를 이용해서 녀석들을 몰아내시오.","준비는 됐소?"}, {"나가의 공격으로부터 셰익스 오브린을 보호해야 합니다."}},
[668] = {"가라앉은 보물", {"오브린 선장이 오래 기다리지 않도록 하라고. 보석을 발견하는 즉시 가져오라고 했단 말이지. 결국은 그것 때문에 우리가 여기 있는 것이지!","그리고 몇 주 동안 해적들과 어울리면서 피즐솔프와 내가 얻은 교훈은 이들의 보물을 오래 가지고 있으면 신상에 절대 이롭지 못하다는 거야.","자, 이 보석들을 오브린 선장에게 가져가라고."}, {"선장 오브린에게 엘프의 보석을 가져가야 합니다."}},
[669] = {"가라앉은 보물", {"시혼 함장님께 이 사실을 즉시 보고해야 하오.","그런데 당신도 보다시피 이곳을 빠져나가기가 상당히 곤란하단 말이지. 조수가 낮을 뿐만 아니라, 일단 몰아내기는 했지만 빌어먹을 짐승들이 우리를 계속 노리고 있소.","당신이 무법항에 계신 시혼 함장님께 보물을 발견했다는 사실과 현재 계속 끌어올리는 중이라고 보고해 주면 검은바다 해적단에게 정말 큰 도움을 주는 것이야. 증거로 이 표본을 가져가도록 하시오."}, {"가시덤불 골짜기의 무법항에 있는 시혼 함장에게 엘프의 보석 표본을 가져가야 합니다."}},
[670] = {"가라앉은 보물", {"말해보게, <name>. 자네는 모험을 꽤 즐기는 것 같은데. 보급품을 새로 장만할 때까지는 내 함대가 무법항을 벗어날 수 없다네.","그런데 이곳저곳에서 심각한 문제들이 발생하고 있어서 아라시 해안에 갇혀 있는 셰익스 오브린에게 극비 문서를 보내야 하는데 말이야.","이 편지를 자네가 훔쳐 보지 않고 안전하게 배달해 줄 것을 믿어도 되겠지?"}, {"아라시 고원의 팔디르의 만에 있는 셰익스 오브린에게 시혼의 봉인된 편지를 전달해야 합니다."}},
[671] = {"사악한 마법", {"지난 밤 뭔가 이상한 기운을 느꼈네. 내 피를 들끓게 하는 기운을... 의심 가는 데가 있긴 하네. 나를 불안하게 하는 기운의 근원을 찾아 보았더니 서쪽의 북부습곡 장원에서 악마를 소환한 흔적이 있더군. 좀 더 가까이 다가가자 피가 끓기 시작했고 분노가 치밀어 오르기 시작했네.","악마를 소환하는데 사용한 혈석 아뮬렛을 바닥에서 주웠지만 내가 발견한 아뮬렛은 이미 그 힘이 고갈되어 있었네. 조사를 계속하려면 다른 아뮬렛이 필요한데, 감히 그곳에 다시 돌아가지는 못하겠군. 자네가 몇 개를 가져다주겠나?"}, {"해머폴에 있는 토건에게 혈석 아뮬렛 10개를 가져가야 합니다."}},
[672] = {"되살아나는 영혼", {"우린 그에게 칼림도어의 오그리마라는 우리의 강력한 도시에 대해 이야기해 주며, 진정시키고 위로하려고 노력했지만 아무것도 그의 마음을 움직이지 못했네.","둠해머님의 갑옷과 워해머를 이어받은 우리의 젊은 대족장 스랄님에 대한 이야기도... 하지만 어쩌면... 그래, 그러면 될지도 몰라! 우리가 새로 발견한 주술을 보여 주면 다시 희망을 가지게 될지도 모르겠군. 한 번 시도해 볼 만하군그래!","그를 위해 작은 물건을 하나 만들 수 있을 텐데... 그래! 랩터 눈을 몇 개 가져다주게. 그럼 내가 뭔가 할 수 있을 것 같아."}, {"줄무늬 고원랩터와 외뿔 고원랩터에게서 고원 랩터 눈 10개를 구해 해머폴에 있는 토건에게 가져가야 합니다."}},
[673] = {"사악한 마법", {"스트롬가드의 성벽 내부에 아주 강력한 흑마법사가 있다네. 간혹 이 흑마법사는 신비한 마법의 유물을 사용하여 끔찍한 악마를 대량으로 소환하곤 했지. 우린 그가 가지고 있는 힘의 근원을 파괴해야 하네.","흑마법사를 찾아서 처치하도록 해. 그에게서 발견한 마법 물건을 가지고 오면 내가 직접 파괴하여 더 이상 이 땅을 더럽히는 데 사용하지 못하도록 하지."}, {"마레즈 코울의 더럽혀진 혈석 수정구를 빼앗아 해머폴에 있는 토건에게 가져가야 합니다."}},
[674] = {"되살아나는 영혼", {"자, 이 아뮬렛을 고르멀에게 가져다주고 랩터의 힘이 한때 그의 몸 속에 타오르던 용사의 열정을 되살릴 수 있는지 보라고."}, {"고르멀에게 랩터 발톱 아뮬렛을 전달해야 합니다."}},
[675] = {"되살아나는 영혼", {"<고르멀이 아뮬렛을 땅에 버리며>","토건이 자네를 보냈나? 그렇지? 흥, 그가 내게 왜 이런 자비를 베푸는지 도저히 이해할 수 없군. 전투에서 대족장님과 함께 죽지 못했다는 것 자체가 이미 신의 자비가 나를 버린 거야. 너무나 잔인한 운명이지..."}, {"토건과 대화해야 합니다."}},
[676] = {"망치 주둔지 함락 위기", {"전부 약해 빠진 놈들뿐이니... 이봐, 뭘 쳐다보는 거요? 당신은 스스로 뭔가 특별하다고 생각하는 모양이군. 그럼 한번 중명해 보시지 그러시오?","아라시의 망치 주둔지 기지가 공격받고 있소. 서둘러 지원군을 보내지 않으면 곧 붕괴하고 말 거요.","하지만 그 전에 우선 당신의 능력을 시험해 보는 게 좋을 거요. 아라시 고원의 스트롬가드 북동쪽에 있는 오우거 고분에 돌주먹 일족이 살고 있소. 그곳의 오우거들과 집행자들을 처치한 후 망치 주둔지에 있는 드럼 펠에게 보고하도록 하시오."}, {"돌주먹 오우거 8마리와 돌주먹 집행자 10마리를 처치한 다음 아라시 고원의 망치 주둔지로 가서 드럼 펠을 찾아야 합니다."}},
[677] = {"전장으로", {"아라시의 마른나무껍질 트롤과 돌주먹 오우거가 우리를 이 땅에서 몰아내려 안간힘을 쓰고 있소. 하지만 이 세상 어느 누구도 우리를 몰아낼 수는 없지. 우린 그들에게 이곳이 우리의 땅이기도 하다는 것을 보여 줄 것이오.","그 트롤들은 우리에게 등을 돌려버렸소. 그리고 오우거들은 우리를 먹잇감 정도로 생각하고 있는데다가 우리의 가죽을 벗겨 침구로 사용하려 하고 있소. 이런 일은 절대 일어나서는 안 되지.","우선 우리는 남쪽에 있는 마른나무껍질 트롤을 공격할 계획이오. 당신도 녀석들을 닥치는 대로 처치하고 녀석들의 야영지가 피로 물들 때까진 돌아오지 마시오."}, {"마른나무껍질 도끼투척병 10명과 인간사냥꾼 10명, 의술사 8명을 처치하고 망치 주둔지 전초기지에 있는 드럼 펠에게 가야 합니다."}},
[678] = {"전장으로", {"당신의 전투 실력은 탁월하군. 그러면 이번에는 더 강한 녀석들을 한 번 상대해 보겠소?","돌주먹 일족의 소굴은 이곳의 남서쪽, 드워프 다리 방향에 있소. 그리고 그곳에 있는 돌주먹 싸움꾼과 사제는 아주 강력한 상대들이오. 지금까지 우리에게는 그랬었지.","이들을 최대한 많이 쓰러뜨리고 돌아오시오. 살아서 돌아오든가 죽어서 귀신이 되어 돌아오든가... 어떻게 되든 전장에서 벌인 당신의 활약은 칭송을 받을 거요."}, {"돌주먹 투사 10마리와 돌주먹 사제 4마리를 처치한 다음 망치 주둔지 전초기지에 있는 드럼 펠에게 가야 합니다."}},
[679] = {"전장으로", {"이제 그 짐승들의 우두머리를 해치울 때가 왔소. 지금까지 보여 준 모습을 보면 이 마지막 임무를 수행하는 데는 당신이 적격인 것 같군. 침략자의 우두머리는 인간의 연약함을 상징하는 스트롬가드에 자신들의 소굴을 만들었소.","돌주먹 우두머리와 그들의 부하인 주술사를 찾아서 죽이시오. 그들이 우리를 그 땅에서 쫓아낸 자들이오."}, {"돌주먹 주술사 7마리와 돌주먹 우두머리 3마리를 처치한 다음 망치 주둔지 전초기지에 있는 드럼 펠에게 가야 합니다."}},
[680] = {"거대한 위협", {"수고하셨어요. 내 남편이 당신에게 준 임무를 아주 잘 완수해냈더군요. 하지만 그이는 왜 망치 주둔지이 공격 받고 있는지는 잘 모르고 있어요.","스트롬가드에 있는 무그톨의 부하, 오르칼라는 내 남편을 매우 증오하고 있습니다. 몇 년 전부터 내 남편을 없앨 궁리만 하고 있을 정도니까요.","점을 쳐 본 결과 오르칼라가 내 남편을 함정에 빠뜨릴 계획인 모양이더군요. 그래서 난 남편이 오르칼라의 행방을 알 수 없도록 오르칼라에게 마법을 걸어 두었습니다.","내 남편은 오우거만 보이면 무조건 쳐들어갈 게 뻔하니까요. 당신이 그 짐승을 대신 없애 주었으면 합니다."}, {"오르칼라를 처치하고 그 증거로 그의 머리카락을 망치 주둔지 전초기지에 있는 코린 펠에게 가져가야 합니다."}},
[681] = {"북부습곡 장원", {"우리는 스트롬가드에서 전쟁 중입니다만 요즘 전황이 좋지 않습니다. 오우거, 비밀결사대 그리고 우리 이렇게 세 세력이 한때 거대했던 우리의 도시를 나눠 가지고 있으며 서로 간에 피 튀기는 싸움을 벌이고 있습니다.","하지만 적들은 병력이 점점 많아지는 반면 우리는 점점 줄어들고 있으니...","남녘해안 마을의 도움을 받아야 하는데, 비밀결사대가 중간 지역을 장악하고 있기 때문에 보급품을 받기가 어렵습니다.","북부습곡 장원에 있는 비밀결사대 기지로 가 그들을 해치워 주시기 바랍니다. 그러면 우리의 보급 부대가 좀 더 안전하게 통과할 수 있을 겁니다."}, {"비밀결사대 강도 10명과 비밀결사대 용병 6명을 처치해야 합니다.","임시 주둔지에 있는 부대장 나이얼스에게 돌아가십시오."}},
[682] = {"스트롬가드 휘장", {"스트롬가드의 비밀결사대가 그곳에 파견된 우리의 수비대와 전투를 벌이고 있습니다. 비밀결사대 지도자들은 부하들을 자극하려고 우리 병사들의 목에 현상금을 걸었더군요. 듣자하니 수비대를 죽이고 스트롬가드 휘장을 가져오면 금화를 준다고 합니다.","우린 그 휘장을 되찾아 왔으면 합니다. 저 살인자들이 쓰러진 우리의 용맹한 병사들을 전리품처럼 여기는 꼴을 보고 있을 수는 없습니다!","스트롬가드에서 비밀결사대가 장악한 지역으로 들어가 그들을 없애고 휘장을 되찾아 와 주시기 바랍니다."}, {"임시 주둔지에 있는 부대장 나이얼스에게 스트롬가드 휘장 7개를 가져가야 합니다."}},
[683] = {"사라 발루의 탄원서", {"제 작은 부탁을 들어 주시겠어요?","이 탄원서를 아이언포지의 위대한 마그니 브론즈비어드 국왕님께 전해 주세요. 당신처럼 얼라이언스를 위해 위대한 업적을 쌓은 분께 이렇게 사소한 부탁을 드리는 것이 송구스럽긴 하지만...","지금 제가 이렇게 눈물 젖은 모습으로 국왕님을 알현하면 실수라도 하게 될 것 같아서요. 이해해 주시리라 믿습니다."}, {"아이언포지에 있는 마그니 브론즈비어드 국왕에게 사라 발루의 탄원서를 전달해야 합니다."}},
[684] = {"마레즈 코울 현상 수배!", {"비밀결사대의 고문이자 상급 첩자인 마레즈 코울에게 현상금을 거는 바이다. 그녀가 마지막으로 목격된 곳은 스트롬가드에서 비밀결사대가 점령한 지역이다.","현상금은 부대장 나이얼스에게서 받을 수 있다.","경고: 마레즈는 악마와 거래하는 것으로 알려진 강하고 교활한 흑마법사이다. 그러므로 그녀를 쫓을 때는 매우 신중하고 조심하도록!"}, {"마레즈를 처치하고 그 증거로 그녀의 머리카락을 임시 주둔지에 있는 부대장 나이얼스에게 가져가야 합니다."}},
[685] = {"오토와 팰컨크레스트 현상 수배!", {"스트롬가드 민병대는 팰컨크레스트와 그의 경호원인 오토에게 현상금을 거는 바이다. 팰컨크레스트는 아라시 고원에서 비밀결사대의 전투를 지휘하고 있으므로 그를 죽이면 적에게 큰 혼란을 일으킬 수 있을 것이다.","그의 경호원인 오토는 전략적으로는 중요한 인물이 아니지만 매우 강력한 상대로서 이미 십여 명의 아군 수비병을 살해했다.","현상금은 부대장 나이얼스에게서 받을 수 있다."}, {"오토와 팰컨크레스트를 처치하고 그 증거로 머리카락을 임시 주둔지에 있는 부대장 나이얼스에게 가져가야 합니다."}},
[686] = {"왕의 하사품", {"발루 부인과 같은 드워프 평민들이 더 큰 행복과 안전을 위해 전쟁을 불사할 수밖에 없는 이유를 이해해 주기를 바라지는 않았네. 하지만 그녀의 남편을 포함해 얼라이언스의 이름으로 싸우다 목숨을 잃은 모든 드워프를 생각하면 짐의 마음이 무겁기 그지 없군.","그녀의 남편을 뒤틀린 황천에서 다시 데려올 수는 없지만 그대신 하사품을 내리겠네. 대석공 마블스텐으로 하여금 설리 발루의 기념비를 세우도록 명하겠네.","당장 그에게 내 말을 전하도록, <class>. 어서 가서 짐의 명을 수행하게."}, {"마그니 브론즈비어드 국왕이 대석공 마블스텐과 대화하라고 명령했습니다."}},
[687] = {"실성한 텔두린", {"텔두린은 황혼의 망치단의 일원이었네. 그런데 그가 실성한 후 우리의 가르침을 무시해 버렸지. 그는 우리한테서 도망치면서 미즈라엘의 두루마리를 훔쳐 갔네! 우린 그를 잡지 못했지만, 소문에 따르면 황야의 땅에서 실성한 채로 알 수 없는 말을 지껄이며 숨어 있다고 하네.","공주를 다시 모셔오려면 반드시 텔두린을 찾아 그 두루마리를 찾아야 하네."}, {"실성한 텔두린을 찾아야 합니다."}},
[688] = {"미즈라엘의 친구들", {"<미즈라엘의 말.>","자유다! 이제 자유야! 저를 사로잡은 자들에게서 벗어나 이제서야 힘을 모을 수 있게 됐답니다. 혹시라도 그들과 다시 마주치면 저를 힘으로 장악하여 다시 가둬 놓을 겁니다.","하지만 때가 되면 제가 직접 거인들과 싸울 것이고, 저를 가둬 놓은 대가를 치르게 할 겁니다!","당신은 이제부터 제 친구입니다. 때가 되면 저를 다시 한 번 도와 주기 바랍니다. 제가 준비되면 지상으로 소환해 주시길 부탁드립니다.","해머폴에 있는 자루크와 얘기를 나눠 보시기 바랍니다. 그는 날 소환하는 방법을 알고 있답니다."}, {"해머폴에 있는 자루크와 대화해야 합니다."}},
[689] = {"왕의 하사품", {"그러니까 우리의 위대하신 국왕께서 설리 발루라는 병사를 위해 기념비를 세웠으면 하신단 말이오? 그 불쌍한 젊은이가 탄돌 교각 참사에서 죽었단 말이지?","도대체 이 동부 왕국에는 언제쯤 평화가 찾아올는지...","휴, 왕께서 내리신 명령이니 우선 다른 작업은 뒤로 미뤄야겠군. 후세에까지 길이 남을 진정한 영웅의 기념비를 만들려면 최상급 돌이 필요하오. 언덕마루의 다로우 언덕으로 가서 알터랙 화강암을 좀 가져 오시오."}, {"아이언포지의 대석공 마블스텐이 알터랙 화강암 5개를 가져다 달라고 했습니다."}},
[690] = {"말린의 요청", {"안녕하시오. 보아하니 모험가의 분위기가 느껴지는군. 도움이 필요한 일이 있는데 한번 들어 보겠소?","얼마 전에 아라시 고원의 스트롬가드 요새를 떠나신 나의 첫 스승이자 대마법사이신 트렐레인 님께서 두고 오신 몇몇 물건을 찾는 걸 도와 달라고 하셨소.","그 도시는 함락되었고 스승께서는 아라소르의 탑을 방어하려 했으나 성공하지 못했다오. 이미 아라시 고원에 사람을 보냈으나 그들은 도움을 더 요청해 오고 있소. 어떻소, 관심이 있소?"}, {"아라시 고원의 임시 주둔지에서 스쿼토를 찾아야 합니다."}},
[691] = {"마법 재료 구하기", {"대마법사이신 말린 님께서 스쿼토와 저에게 마법 재료를 구해 오라고 부탁하셨답니다. 그런데 그 물건들을 어떻게 구해야 할지 고민 중이었는데 당신이 여기에 왔으니 문제가 해결된 것 같군요.","필요한 물건은 마른나무껍질 엄니, 마른나무껍질 약초 주머니, 어둠사냥꾼 나이프입니다.","제 대신 구해다 주시겠습니까? 마른나무껍질 부족은 주로 아라시 고원의 남동쪽에 산답니다."}, {"임시 주둔지에 있는 수습마법사 크리텐에게 마른나무껍질 엄니 10개, 마른나무껍질 약초 주머니 4개, 어둠사냥꾼 나이프 1개를 가져가야 합니다."}},
[692] = {"잃어버린 조각", {"미즈라엘의 두루마리... 미즈라엘의 두루마리?! 설마 그걸 원하는 건 아니겠지! 그 두루마리의 마법을 사용하면 미즈라엘을 지상 세계로 끌어낼 수 있단 말이야. 그러면 모두 다 곤란해진다고!","어쩌면... 벌써 늦은 걸지도 몰라. 벌써 자유의 몸이 되어 힘을 모으고 있는 중일지도 몰라. 그렇다면 그 두루마리가 필요하지!","...하지만 이미 파괴되어 버렸는데 어쩌지? 광포한 바위 정령들이 훔쳐가서 갈기갈기 찢어 버렸어.","하지만 조각을 모두 모아 올 수 있다면 다시 하나로 만들 수 있을지도..."}, {"실성한 텔두린에게 찢어진 두루마리 조각, 구겨진 두루마리 조각, 그을린 두루마리 조각을 가져가야 합니다."}},
[693] = {"마법 지팡이 회수", {"어흠...흠. 저기...일전에는 무뚝뚝하게 굴어서 미안하오. 기분 나쁘게 하려고 했던 건 아니니 이해해 주시오.","트렐레인의 첫 번째 물건이 어디로 갔는지 알아냈다오. 돌주먹 소굴에 있는 코르그레쉬가 가지고 있더군. 지금은 자신의 종족을 통솔하기 위해 얼음 마법을 사용하고 있는데, 머지않아 마법 지팡이의 모든 능력을 사용하는 방법을 알아낼 것이오.","돌주먹 소굴은 이곳의 남쪽과 탄돌 교각의 동쪽에 있소. 이 오우거를 찾아가 마법 지팡이를 회수해 오시오.","당신이 가 있는 동안 난 스트롬가드 요새에 대해 어떤 조치를 취할 수 있는지 알아보도록 하겠소."}, {"트렐레인의 마법지팡이를 찾아 임시 주둔지에 있는 스쿼토에게 가져가야 합니다."}},
[694] = {"트렐레인의 방어 장치", {"푹 쉬었소?","자, 다음 단계는 이렇소. 스트롬가드에 있는 아라소르의 탑 주위에는 오우거가 사방에 깔려 있소. 오우거를 상대로 살아서 물건을 가지고 오려면 탑의 방어 기능을 작동시켜야 하오. 그러려면 돌주먹 주술사가 좋아하는 보석이 좀 필요하오. 돌주먹 주술사는 탑에서 이 보석을 가지고 나오지만 실제로 소지하고 있는 시간은 길지 않다오.","보석을 하나 구해서 크리텐에게 가져다주시오. 그러면 그가 마법 주문을 걸어서 열쇠처럼 작동하게 해 줄 것이오."}, {"푸른색 마노를 찾아서 임시 주둔지에 있는 수습마법사 크리텐에게 가져가야 합니다."}},
[695] = {"수습생의 마법", {"이걸 받으십시오. 탑의 토대 근처에 있는 기둥 중 한 곳에 대고 보석을 누르면 탑의 방어 기능이 작동하게 됩니다. 탑에 들어가서 트렐레인의 유품을 찾을 준비가 되면 사용하시기 바랍니다.","행운을 빕니다. 당신이 야영지로 돌아오기를 기다리고 있겠습니다. 참, 떠나기 전에 스쿼토와 얘기를 나눠 보십시오. 나머지 자세한 내용을 알려 줄 겁니다."}, {"스쿼토와 대화해야 합니다."}},
[696] = {"탑 공격", {"당신이 찾아야 할 물건은 트렐레인의 부적, 트렐레인의 수정구, 그리고 트렐레인의 잿불 마노라오.","세 가지 모두 탑 안의 상자에 들어 있는데, 상자를 여는 데는 별문제가 없을 테고... 조심해야 할 건 오우거들이오.","탑의 방어 기능으로는 오우거를 직접 처리할 수 있는 게 없기 때문에 무기를 쓸 준비를 하고 있어야 할 것이오. 당신이 물건을 훔치러 왔다는 걸 오우거들이 알면 분명히 공격해 올 것이오.","조심하고 당신에게 행운이 있기를 바라오."}, {"트렐레인의 부적, 트렐레인의 수정구, 트렐레인의 잿불 마노를 찾아 임시 주둔지에 있는 스쿼토에게 가져가야 합니다."}},
[697] = {"멀린의 의뢰", {"이 편지를 스톰윈드에 계시는 멀린님께 전해 드리고 크리텐과 내가 곧 돌아갈 것이라고 전해 주시오. 멀린님께서 이곳에서 우리를 도와 준 것에 대해서 분명히 큰 보상을 해 주실 것이오.","안전한 여행이 되길..."}, {"스톰윈드의 마법사 지구에 있는 대마법사 멀린에게 보내는 봉인된 편지를 전달해 주어야 합니다."}},
[698] = {"보급품 보충", {"난 동쪽 해변 근처에 있는 우리 전초기지에 보내줄 보급품을 모으는 일을 하고 있소. 톡카르도 할 수 있는데까지 하고 있지만 보초병 일에 병사들 요리까지 해야 하니 남는 시간이 많지 않소. 병사들이 톱니악어 고기를 요리해 먹는 걸 좋아하는데 아직 추가 물량을 보내지 못하고 있소. 그들을 좀 도와주지 않겠소?","새끼 녀석들은 1-2년을 더 있어야 완전히 성장한 톱니악어가 되거든. 새끼 톱니악어는 다 자란 놈들에 비해 맛이 떨어지니 잡지 마시오."}, {"슬픔의 늪에 있는 톡카르에게 신선한 톱니악어 고기 8개를 가져가야 합니다."}},
[699] = {"보급품 보충", {"우리는 스토나드로 다가오는 모든 위협을 방어하거나, 적의 규모가 대규모일 경우에 아군에게 경고하는 임무를 띠고 이곳에 배치되었네. 해안에서 멀록의 위협이 끊이지 않고 있다 보니 우린 쉴 틈도 거의 없지. 그래서 말인데, 괜찮다면 자네의 도움을 좀 받았으면 하네.","무쇠턱 톱니악어의 발톱을 잘 다듬으면 좋은 무기를 만들 수 있지. 내게 그 발톱을 몇 개 가져다주면 신선한 고기와 좋은 친구에 대한 답례로 우리 무기 중 하나를 주겠네."}, {"슬픔의 늪에 있는 톡카르에게 무쇠턱 톱니악어 발톱 6개를 가져가야 합니다."}},
[700] = {"왕의 하사품", {"아, 이렇게 질 좋은 암석들이 나오는 로데론이 폐허로 변해 버리다니 안타까운 일이야.","하지만 기념비는 아주 잘 만들어졌소! 왕께서 아주 기뻐하실 것이오.","자, 그럼 어서 가서 기념비가 완성되었다고 전해 드리도록 하시오."}, {"마그니 브론즈비어드 국왕에게 보고해야 합니다."}},
[701] = {"랩터의 기민함", {"재료가 몇 개 더 있으면 내가 고르멀에게 랩터의 기민함을 불어넣어 주는 물건을 만들어 줄 수 있네! 랩터의 힘이 몸속에 흘러 들어가면 고르멀이 우리의 새로운 힘을 알게 되고 잠들어 있는 용사의 기운을 회복할 걸세!","이 물건과 주술을 완성하기 위해서는 남서쪽에 있는 랩터 심장이 필요하네."}, {"식인 고원랩터에게서 랩터 심장 12개를 얻어 토건에게 가져가야 합니다."}},
[702] = {"랩터의 기민함", {"고르멀에게 이 수정구를 가져다주어 랩터의 기민함을 사용하게 해 보게. 그럼 분명히 그의 영혼을 고무시키고 새로운 삶의 목표를 찾을 수 있게 해 줄 걸세. 그가 정상으로 돌아오면 그에게 호드의 현재 상황을 설명해 줄 수 있을 테고, 그러면 이 끊임없는 전투에서 노련한 전사의 도움을 받을 수 있게 될 것이야."}, {"고르멀에게 토건의 수정구를 전달해야 합니다."}},
[703] = {"대머리수리 날개 구이", {"오, 물론이야. 대머리수리 날개 구이는 내가 제일 좋아하는 요리라고! 음, 풍부한 소스와 감칠맛이 나는 고기! 그리고 그 냄새는 멀리 떨어진 곳에서도 당장 달려올 정도라니까!","대머리수리 날개만 빼고 다른 건 준비 다 됐는데...","당신도 맛을 보고 싶다면 대머리수리 날개를 구해달라고. 요리하기에 충분한 양을 구해다 준다면 당신에게 아예 조리법을 전수해 줄 수도 있다고!"}, {"리글퍼즈에게 대머리수리 날개 4개를 가져가야 합니다."}},
[704] = {"아그몬드의 운명", {"아그몬드에게서 받은 마지막 보고가 마음에 걸리는군. 이제는 그가 죽어서 더욱 그렇소! 그의 보고서에 의하면 인부들이 울다만 외곽의 발굴현장에서 '고대의 단지'를 발견했다고 되어있소. 그 현장에서 찾은 단지가 트로그 녀석들을 자극한 게 아닌가 걱정이군. 내가 여기서 찾은 조각상처럼 말이오.","조사를 하려면 단지가 필요한데 울다만 발굴현장으로 가서 돌조각 단지를 모아 내게 돌아오시오."}, {"모단 호수에 있는 발굴조사단장 아이언밴드에게 돌조각 단지 4개를 가져가야 합니다."}},
[705] = {"진주잡이", {"황야의 땅은 난폭한 포식자들과 썩은 고기를 먹는 지독한 짐승들이 가득 찬 위험한 곳이지. 나같이 키 작은 고블린에게는... 으... 무서운 곳이 아닐 수 없다니까. 내가 여기서 살아남으려면 절대로 방심해선 안 된다고!","그나마 섬광탄 제조법을 알고 있기에, 그걸 만들어 야생동물을 쫓는 데 사용하고 있지. 근데 재료가 하나 거의 다 떨어져 가서 말이야. 바로 부셔서 만든 푸른 진주 가루라오. 푸른 진주를 좀 구해다 주면 당신이 수고한 만큼 값지게 쓰도록 하지.","아, 그리고 방수가 잘 되는 질 좋은 장화를 신고 가는 게 좋을 거요. 내가 필요한 푸른 진주는 썩은내 산호초에 있는 조개에서 나니까 말이지. 그래, 바로 가시덤불 골짜기에 있는 그 썩은내 산호초 말이야!"}, {"황야의 땅에 있는 리글퍼즈에게 푸른 진주 9개를 가져가야 합니다."}},
[706] = {"이글거리는 불꽃의 부적", {"내가 페라노어 스틸토님의 수습생으로 있을 때 무기에 불의 마법을 불어넣는 특수한 기술을 배웠지. 마지막으로 들은 소식에 의하면 그분이 저 위 로데론에서 사냥꾼 무리와 함께 대장간에서 일하고 계시다는 거요.","자세히 설명해 줄 순 없지만 마법을 불어넣는 과정에는 희귀할 뿐만 아니라, 신중을 기해야만 잡을 수 있는 검은 새끼 비룡의 심장에서 얻은 피가 필요하오이다. 아이언포지에서 연락이 오길 기다리는 동안 할 일도 별로 없으니, 레슬로 협곡에서 검은 새끼 비룡의 심장을 구해 내게 가져다주면 무기에 불의 마법을 불어넣는 기술을 보여 주리다!"}, {"시그런 아이언휴가 검은 비룡의 심장을 구해 달라고 했습니다."}},
[707] = {"아이언밴드의 호출", {"모단 호수의 발굴현장에 있는 발굴조사단장 아이언밴드로부터 보고를 받았소. 다른 발굴현장에 대한 방문 조사를 해야 한다는군.","내가 아는 정보는 이 정도뿐이니 잠시 머리도 식힐 겸 여행을 해 볼 생각이라면 가서 아이언밴드와 이야기해 보시오."}, {"모단 호수에 있는 아이언밴드의 발굴현장에서 발굴조사단장 아이언밴드와 대화해야 합니다."}},
[708] = {"블랙박스", {"이 검은 금속 상자는 마치 산성 물질에 담가 두었던 것처럼 심하게 부식되어 있습니다. 오랫동안 거대한 대머리수리의 위장 속에 있었던 것이 분명합니다.","상자를 뒤집자 바닥에 도장이 찍혀 있으며 그 도장 아래에 다음과 같이 적혀 있습니다.","'전차 #19 - 아이언포지 증기 전차 부대'"}, {"녹슨 블랙박스를 아이언포지에 있는 전투의 전당으로 가져가야 합니다."}},
[709] = {"멸망의 해결책", {"오, 이런, 이런! 멸망이 다가오고 있어. 내 말을 명심해! 멸망이란 말이야!","아냐, 말할 수 없어... 그들의 이름을 입 밖에 내면 놈들이 들을 수도 있단 말이야! 하지만 우리가 놈들에 대해 더 알아낸다면... 그래, 놈들에 대해 더 알아낸다면 그들을 막을 수 있을지도 몰라! 아마도...","울다만으로 가. 악! 내 입으로 말하고 말다니! 반드시... 반드시... 류네의 서판을 찾아야 해. 그래, 그걸 찾아! 그리고 다시는 내가 그 이름을 말하게 하지 말아 줘!","그 서판은 그곳 깊은 곳에 있는 고대의 상자 안에 있어. 거기에 숨겨져 있다고... 드워프들이 만든 그 어떤 것보다도 오래된 그 상자 속에 말이야."}, {"실성한 텔두린에게 류네의 서판을 가져가야 합니다."}},
[710] = {"바위의 정령 연구", {"난 최근에 맡은 정령 봉인의 족쇄 연구를 위해 이곳 황야의 땅에 머물게 되었어요.","이 연구가 완료되면 마법사가 더욱 강력한 정령을 불러내 그 놀라운 능력을 십분 활용할 수 있게 되죠. 하지만 그 전에 먼저 내가 조립한 여러 장치를 사용하여 이번에 만든 최신 공격 주문을 시험해 보기 위해서는 작은 돌조각들을 모아야 해요.","돌조각은 서쪽에 있는 하급 바위 정령들에게서 쉽게 얻을 수 있을 거예요. 날 위해 돌조각을 좀 구해다 주지 않겠어요?"}, {"황야의 땅에 있는 로트윌 베리아투스에게 작은 돌조각 10개를 가져가야 합니다."}},
[711] = {"바위의 정령 연구", {"당신이 보여준 실력이 내 수습생 모두를 합친 것보다도 낫군요.","다음으로 부탁할 일은 큰 돌조각을 구해와야 하는 좀 더 힘든 일이에요. 충분히 주의한다면 바위 정령에게서 돌조각을 얻을 수 있을 거예요. 아무래도 바위 정령은 몸이 크고 강하기 때문에 내 실험에 사용할 만큼 큰 돌조각이 있을 가능성이 커요. 이곳에서 서쪽으로 더 가거나 산기슭을 따라 남동쪽으로 가면 바위 정령들을 찾을 수 있을 거예요.","당신이 가져온 작은 돌조각을 시험해 보려면 시간이 좀 걸릴 테니 서두르진 않아도 돼요."}, {"황야의 땅에 있는 로트윌 베리아투스에게 큰 돌조각 3개를 가져가야 합니다."}},
[712] = {"바위의 정령 연구", {"이곳 황야의 땅에 있는 정령에게서 필요한 것이 한 가지 더 있어요. 상급 바위 정령들에게서 바위 결속의 팔보호구를 구해다 주었으면 하거든요. 내 계산에 따르면 그 팔보호구는 내가 만든 족쇄의 힘을 측정하기에 충분해요.","내가 수정점을 쳐 보니 거대한 바위 정령은 여기서 남서쪽에 있는 것으로 나왔어요. 아마 내 기억으론 오우거 근처에 있었던 것 같단 말이죠.","여분까지 해서 팔보호구를 5개만 가져다주세요. 내 조수 세르보가 내 연구 재료와 결과물을 어디 뒀는지 곧잘 잊어버리니... 만일을 대비해서 말이에요."}, {"황야의 땅에 있는 로트윌 베리아투스에게 바위 결속의 팔보호구 5개를 가져가야 합니다."}},
[713] = {"최고의 냉각제", {"냉기 기름에 대해 가르쳐 줄까요? 엔진이나 기어의 가열을 식히는 데는 냉기 기름 만한 게 없어요.","그 냉각 능력은 놀라울 정도고 윤활유로서의 수명은 그 어떤 것도 따라오지 못하죠. 하지만 이게 전부가 아니에요. 마법이 걸린 물건에 마법 능력을 오래 유지시켜주는 기능도 있다니까요.","내가 소환해낸 정령이 우리 모두를 공격하지 못하게 제어할 수 있을 정도의 족쇄 마법 능력을 높이기 위해서는 냉기 기름 한 병이 필요해요. 알터랙에 있는 내 고블린 친구, 브로킨이 냉기 기름 만드는 법을 가르쳐 줄 거예요."}, {"황야의 땅에 있는 로트윌 베리아투스에게 냉기 기름을 가져가야 합니다."}},
[714] = {"태엽...장치?", {"지금까지 정말 잘해 주었어요.","<실험으로 정신없어 보이는 로트윌>","우리가 카라노스에서 만났을 때 일자리가 필요하거든 언제든지 말만 하라고요.","이런! 얼른 이걸 쥐고 있어 주세요. 네, 그렇게... 고마워요.","이제 작지만 결정적인 재료 한 가지만 더 있으면 되는데... 태엽장치 말이에요. 당신이 태엽장치만 구해다 주면 이 실험을 끝낼 수 있을 거예요."}, {"황야의 땅에 있는 로트윌 베리아투스에게 태엽장치를 가져가야 합니다."}},
[715] = {"액체 돌", {"로트윌 선생님을 위해 당신이 구해 온 재료들은 나무랄 데 없는 것 같군요. 단도직입적으로 당신은 로트윌 선생님께서 신성하게 여기시는 과학에 상당한 조예가 있는 듯하니, 저도 좀 도와주시지 않으시겠어요?","로트윌 선생님께서 정령들을 불러내면 곧 투명 물약과 치유 물약 두 가지가 필요하게 될 거예요. 물약을 구해다 주시면 답례로 로트윌 선생님 밑에 있으면서 개발한 이 물약 조제법을 알려 드리지요.","이 조제법은 선생님께서 이곳 황야의 땅에서 하고 계신 연구 중 일부에 기초한 거랍니다."}, {"황야의 땅에 있는 루시앙 토셀렌치에게 치유 물약과 하급 투명 물약을 가져가야 합니다."}},
[716] = {"돌로 만든 보호구", {"로트윌 선생님이 얼마나 인색한지는 잘 알고 있어요. 그러니 제가 대신 도움될 만한 물건을 만들어 드리지요. 아마 선생님의 새로운 발명으로 발생할 끔찍한 일로부터도 보호받을 수 있을 거예요.","당신이 선생님을 위해 모아온 여분의 돌들을 살펴봤는데 당신이 사용할 만한 괜찮은 팔보호구를 만들 수 있을 것 같더군요. 물론 마법도 걸 수 있죠.","이제 대장장이가 만든 청동 무늬 팔보호구만 있으면 당신에게 줄 팔보호구를 완성할 수 있어요. 제게 청동 무늬 팔보호구를 좀 가져다주세요."}, {"황야의 땅에 있는 루시앙 토슬렌치에게 청동 무늬 팔보호구를 가져가야 합니다."}},
[717] = {"대지의 진동", {"블랙래쉬와 헤마투스를 계속 레슬로 협곡에 봉인할 수 있을 거라고는 누구도 보장할 수 없는 상황일세. 그 검은용 두 마리는 그곳에 본거지를 마련했고 오우거들은 봉인까지 훔쳤으니 더욱 강력한 조치를 취해야 할 때야.","비룡의 감옥을 유지하고 있는 마법석 세 개는 대지의 봉인을 사용해야만 가져올 수 있네. 그리고 그 마법석들을 사용하면 그들을 가두고 있는 봉인을 깨뜨릴 수 있다네.","그들의 꿈이 영원히 헛된 망상으로 끝나게 해 주게."}, {"대지의 봉인을 사용하여 자수정, 오팔, 다이아몬드의 기둥을 작동시켜 마법석을 획득해야 합니다.","마법석들을 대지의 봉인에 놓아 블랙래쉬와 헤마투스의 봉인을 해제해야 합니다.","풀려 난 블랙래쉬와 헤마투스를 처치하고 가렉에게 블랙래쉬의 굴레, 헤마투스의 굴레와 대지의 봉인을 가져가야 합니다."}},
[718] = {"신기루", {"우린 울다만 발굴현장을 빠져나오느라 정신이 없어 보급품 마차와 상자 상당수를 길에 두고 올 수밖에 없었소. 그런데 코쉬 야영지에서 온 고약한 오우거들이 시체에 모여드는 독수리들처럼 버려진 우리 물건들에 손을 대지 뭐요. 고약한 것들...","어쨌든, 녀석들이 우리의 무기와 식료품이 실려 있던 마차를 가져가서... 부끄럽게도 먹을 걸 전혀 구하지 못했다오.","그래서 지금 몹시 난처한 처지인데 당신이 좀 도와주겠소?"}, {"시그런 아이언휴를 위해 보급품 상자를 되찾아야 합니다."}},
[719] = {"행운의 곡괭이", {"안녕하시오, <class>. 여기는 검은무쇠 드워프들이 사방에 깔렸으니 조심해야 하오. 아마도 어둠괴철로 드워프 같았소이다.","좀 전에도 내가 작업 중인 발굴현장을 공격해 난 겨우 목숨을 건졌지만 내 상관인 해머토를 비롯해 거기 있던 거의 모든 이들이 목숨을 잃었소.","발굴현장은 여기에서 바로 북쪽에 있는데, 우리 보급품들, 특히 내 행운의 곡괭이를 되찾아올 방법을 궁리하는 중이었소.","당신이 나를 좀 도와주시오. 저 몹쓸 드워프 중 한 명에게 내 곡괭이가 있을 거요! 녀석들 중 누구든 가지고 있을 테니 좀 찾아다 주시겠소?"}, {"라이돌의 행운의 곡괭이를 찾아 해머토의 발굴현장의 남쪽에 있는 발굴조사단장 라이돌에게 가져가야 합니다."}},
[720] = {"희망의 전조", {"<지도에는 분필로 휘갈겨 쓴 쪽지가 적혀 있습니다.>","\"라이돌, 놈들은 나를 울다만으로 데려가고 있네... 도와 주게.","-해머토\""}, {"발굴조사단장 라이돌을 찾아 해머토 그레즈가 살아 있다는 사실을 알려야 합니다."}},
[721] = {"희망의 전조", {"당신이 그를 좀 찾아 주지 않겠소? 울다만에 있는 해머토를 좀 찾아 주시오.","비열한 어둠괴철로 드워프들이 대규모 발굴 작업에 대해 알아내려고 그를 인질로 잡은 거라면 아이언포지 전체가 큰 위험에 빠질 수도 있소.","저 드워프 녀석들은 오래전부터 더 강한 골렘을 만들어내려고 새로운 마법을 손에 넣으려 했다오. 해머토도 그들과 같은 일을 하고 있었지만, 해머토는 그들을 막으려 한 것이지.","녀석들은 아마 그가 아는 사실을 알아낼 때까지 그를 죽이지 않을 거요. 그를 구하려면 서두르시오!"}, {"울다만에서 해머토 그레즈를 찾아야 합니다."}},
[722] = {"비밀의 아뮬렛", {"나를 위해... <콜록>...이곳 울다만까지 와 주다니... 정말 용감하군...","이... 늙은 해머토를 도울 생각이라면... <콜록>... 내 아뮬렛을... 좀... 찾아 주시게...","내 아뮬렛을 딥섀도... 마그레간 딥섀도가 가져갔지. 그는 이 근처 어딘가에 있을 게야... 틀림없이 이 폐허 어딘가에... 그를 막지 못하면 아뮬렛이 놈들을... <콜록>... 두루마리가 있는 길로 안내하게 될 것이네. 이제 모든 건 자네에게 달려 있네. 그러니 제발...아뮬렛을 찾아 주게나.","그리고 그 두루마리는 검은무쇠 드워프들이 보다 강력한 골렘을 만드는 데 꼭 필요한 것일 게야. 확실해. 뼛속까지 느껴지는 걸 보면..."}, {"해머토의 아뮬렛을 찾아 울다만에 있는 해머토에게 가져가야 합니다."}},
[723] = {"돈독한 믿음", {"<상처 부위를 감싸며 안정을 되찾은 후 다시 이야기를 시작하는 해머토>","라이돌에게 가게... 그리고 그에게 내 아뮬렛을 가져다주게나. <콜록 콜록> 그러면 그 친구는... <콜록> 해야 할 일을 알 수 있을 거야. 내가 우리의 근원을 찾고 있었다는걸 그는 알고 있고 <콜록> 어둠괴철로단 드워프 녀석들을 막는것 또한 나의 다른 임무였다는 것도 알고 있을걸세. 그들이 그 두루마리를 찾게 해서는 안 될 것이야...<콜록>"}, {"황야의 땅에 있는 발굴조사단장 라이돌에게 해머토의 아뮬렛을 가져가야 합니다."}},
[724] = {"돈독한 믿음", {"좋소, 좋아... 그렇다면 우리가 해야 할 일을 알고 있는 게 누굴까?","그렇지! 역사가 카르닉이 있었지!","그는 아이언포지에 있소. 그라면 우리를 도울 수 있을 거요. 그와 해머토는 울다만에서 사라진 유물을 찾기 위해 함께 일했었지.","아마 어둠괴철로 드워프 녀석들도 그 두 명이 실제로 말한 것보다 더 많이 알고 있다는 사실을 눈치챘을 거요.","해머토에게 일어난 일을 카르닉에게 설명하고 해머토의 아뮬렛을 전해 주시오. 그러면 그가 다음에 해야 할 일을 알려 줄 거요."}, {"아이언포지에 있는 역사가 카르닉에게 해머토의 아뮬렛을 가져가야 합니다."}},
[725] = {"위협의 경고", {"우선 최대한 빨리 조언자 벨그룸님을 찾아 가시게. 그리고 그분께 자네가 본 바를 모두 얘기해 준 다음 앞으로 어떻게 해야 할지 물어보아야 할 것이야. 이 쪽지를 보여 주면 그분이 이번 사건에 대해 보다 잘 알 수 있을 걸세.","그동안 나는 해머토의 영혼과 얘기해 보겠네. 그가 가진 정보가 황야의 땅에서 벌이고 있는 어둠괴철로단 드워프의 음모를 막는 데 도움이 될 거야. 일이 잘 되기를 빌어 볼 수밖에..."}, {"조언자 벨그룸을 찾아 봉인된 쪽지를 전해야 합니다."}},
[726] = {"위협의 경고", {"<쪽지를 끝까지 읽는 조언자 벨그룸, 잠시 눈이 휘둥그레지며>","이런...악마들의 세상이 도래했군!","역사학자 카르닉에게 돌아가 왕의 이름으로 이 정보에 따라 신속하고 단호하게 조치를 취하라고 전하시오. 그에게 모든 수단과 방법을 동원해 황야의 땅에서 어둠괴철로단 드워프들의 활동을 저지하라고 전해야 한다고 하시오."}, {"역사학자 카르닉과 대화해야 합니다."}},
[727] = {"야그인의 법전을 찾아서", {"이 서판은 많은 것을 말해 주지. 하지만 아직 부족해! 너무 부족해!","아주 오래전에 야그인이 쓴 법전이 있어. 날... 잡아두었던 자들... 그자들이 그 법전을 여러 장소에 보관해 두고 있어. 그리고 학자들이 연구를 하지. 그 법전에는 방대한 지식이 담겨 있어. 우리를 보호해줄 지식 말이야!","아이언포지의 게릭 본그립을 찾아가. 그는 아이언포지의 쓸쓸한 뒷골목에 있는 상점에 있어. 이 인장을 보여주면 당신이 같은 편이라고 생각할 거야. 야그인의 법전을 달라고 해서... 내게 가져와 줘!"}, {"아이언포지의 쓸쓸한 뒷골목에 있는 게릭 본그립에게 해머의 인장을 가져가야 합니다."}},
[728] = {"야그인의 법전을 찾아서", {"이 서판은 많은 것을 말해 주지. 하지만 아직 부족해! 너무 부족해!","아주 오래전에 야그인이 쓴 법전이 있어. 날... 잡아두었던 자들... 그 자들이 그 법전을 여러 장소에 보관해 두고 있어. 그리고 학자들이 연구를 하지. 그 법전에는 방대한 지식이 담겨 있어. 우리를 보호해줄 지식 말이야!","로데론의 언더시티에 있는 관리인 벨두거를 찾아가. 이 인장을 보여주면 당신이 같은 편이라고 생각할 거야. 야그인의 법전을 달라고 해서... 내게 가져와 줘!"}, {"언더시티에 있는 관리인 벨두거에게 해머의 인장을 가져가야 합니다."}},
[729] = {"정신없는 발굴조사단장", {"수석고고학자 그레이위스커 님께서 절 위대한 발굴조사단장 렘트래블 님 밑에서 일하게 해 주셨으니 이 얼마나 영광인지 모릅니다. 아이언포지의 학교에서는 렘트래블 님의 위대한 발견에 대해 모르는 이가 없죠.","하지만 이 저명한 발굴조사단장께서는 주위 상황에는... 음... 그다지 신경 쓰지 않는 것 같아요.","우리는 위대한 고대 문화의 흔적을 찾아냈어요. 그런데 땅에서 끔찍한 골렘들이 나와서 현장을 덮치고 말았죠. 렘트래블 님께서는 이 사실을 모르고 계신 것 같아요. 전 도움을 요청하러 아우버다인으로 달려왔답니다.","남쪽으로 가서 렘트래블 님께서 무사한지 좀 봐 주세요."}, {"남쪽으로 가서 발굴조사단장 렘트래블의 상태를 확인해야 합니다."}},
[730] = {"어둠해안에 생긴 문제", {"이 세계의 위대한 고고학적 불가사의에 관심을 가진 <class>|1을;를; 만나게 되어 정말 기쁘오.","얼라이언스 친구들은 우리 일을 단순한 취미 정도로 치부하는 경우가 종종 있지. 그러나 최근 카즈 모단에서 발견된 것들은 어떤 강력한 세력이 로데론에서 칼림도어에 이르는 아제로스 전역을 위협하고 있음을 증명하고 있소. 얼마나 많은 이들이 이걸 깨닫지 못하고 있는지...","지금 가장 큰 근심은 어둠해안으로 보낸 우리 대원들이오. 벌써 몇 주 동안이나 소식이 끊겼소. 아우버다인으로 가서 그들의 소재에 대한 단서를 찾아봐 주겠소?"}, {"아우버다인으로 가서 드워프 발굴단의 흔적을 찾아야 합니다."}},
[731] = {"정신없는 발굴조사단장", {"그러니까 호올리... 아, 맞아. 당신은 호올리가 아니지. 호올리는 어디 있는 게요?","뭐, 그게 중요한 게 아니고... 털이개를 찾아야 하는데... 아니, 신비로운 화석 말이야! 그 화석을 반드시 찾아야 하오. 다르나서스에 있는 탐험가 연맹에서 내 작업의 증거를 보고 싶어할 테니 말이오.","준비가 되면 내게 말씀하시오. 같이 털이개를... 아니, 화석을 찾아봅시다. 연맹이 만족할 만한 보고를 제출해야 하니까 말이야.","준비가 됐소?"}, {"신비로운 화석을 찾는 동안 발굴조사단장 렘트래블을 경호한 후 아우버다인에 있는 고고학자 호올리에게 가야 합니다."}},
[732] = {"대지의 진동", {"엘룬께 영광을. 그대가 시그런을 많이 도와 줬다고 들었기에 나도 도움을 좀 청할까 하네. 우리 스승님인 키린 토의 크라서스 님께서 레슬로 협곡에서 발생한 문제를 해결하라고 나를 이곳에 보내셨지.","이곳에서 내가 맡은 임무에 대해서 자세하게 얘기해 줄 수는 없지만, 매우 중요한 임무라는 것만은 확실히 말할 수 있다네.","보프 야영지 근처에서 가끔 보이는 떠돌이 오우거 무리가 대지의 봉인이라는 물건을 훔쳤는데 그걸 되찾아 와야 해. 그대가 날 위해 그걸 찾아와 준다면 자세한 얘기를 해 주겠네."}, {"가렉을 위해 대지의 봉인을 되찾아야 합니다."}},
[733] = {"자재 모으기", {"내가 방어 도구나 기타 다른 필수 장비들을 제작할 자재를 찾아오지 못하면 이곳 상황이 안 좋아질 수도 있소. 이 지역의 오우거들은 금속 조각들을 방어구로 사용하여 단단히 무장하고 있는 것 같소. 그러고 보니 황야의 땅에 증기 전차가 있다는 이야기가 헛소문이 아닐지도 모르겠군.","아무튼, 발굴조사단장은 이 작은 야영지의 상황을 걱정하고 있소. 그래서 가능한 빨리 작업을 시작해야 할 것 같은데...","오우거의 주 야영지는 서쪽으로 멀리 떨어져 있는 먼지목도리 소굴에 있으니 금속 조각을 좀 가져다주시오."}, {"시그런에게 금속 조각 7개를 가져가야 합니다."}},
[734] = {"불길한 예감", {"훌륭해, 정말 훌륭해...","<다시 족쇄 만들기에 몰두하기 전에 잠시 올려다보는 로트윌>","루시앙에게 가 봐요. 그리고 5번 기어가 필요하다고 말하고 그걸 내게 좀 가져다줄래요?"}, {"황야의 땅에 있는 루시앙 토셀렌치와 대화해야 합니다."}},
[735] = {"별, 손, 그리고 심장", {"야그인의 법전? 드워프 역사의 새로운 장이라도 쓸 생각인가? 아니, 그건 내가 물어볼 질문이 아니로군.","내게 여분이 한 권 있으니 자네를 도와 주도록 하지. 서로 도와 가며 사는 게 도리 아닌가? 그래야 발전이 있는 법이지.","하지만 그전에 내가 원하는 3개의 강력한 물건들을 가져와야 하네. 확인 후 법전을 주도록 하지.","쉿! 지금 누가 엿듣고 있을지도 모르니 물건들의 이름을 소리 내어 말하지는 않겠어. 대신 여기 적어 주지."}, {"아이언포지의 쓸쓸한 뒷골목에 있는 게릭 본그립에게 본그립의 노트에 적힌 물건들을 가져가야 합니다."}},
[736] = {"별, 손, 그리고 심장", {"야그인의 법전이 필요하다고? 그렇다면 당신 계획은 또 다른 무리를 모으는 것인가? 당연히 그럴 테지... 물론, 도와주도록 하겠소. 하지만 당신도 날 도와줘야겠소.","내가 요구하는 세 가지 강력한 물건을 가져오면 법전을 주도록 하지. 이 물건들의 이름은 입에 올리지는 못하니 노트에 적어 주도록 하겠소.","이 목록에 적힌 이름의 물건들을 가져올 수 있을 정도라면 당신은 정말 우리 포세이큰에 새로운 역사를 기록할 만한 자격이 있는 것이오."}, {"언더시티에 있는 벨두거에게 벨두거의 노트에 적힌 물건들을 가져가야 합니다."}},
[737] = {"금지된 지식", {"자, 받게. 이 법전을 소중히 다루어야 하네. 또한 적의 손에 넘어가지 않도록 하길 바라네.","이 법전에는 많은 진실이 담겨 있지...","...저절로 타버릴 수도 있는 진실 말이야."}, {"텔두린에게 야그인의 법전을 가져가야 합니다."}},
[738] = {"아그몬드 찾기", {"발굴조사단장 아그몬드는 여기서 남서쪽에 있는 황야의 땅에서 발굴현장을 지휘하고 있었소. 있었다고 말한 건... 그게, 몇 주째 그에게서 아무 소식도 듣지 못해서요. 난 그의 보고를 받아야 하니 아그몬드를 찾아주시오.","들은 바로는 아그몬드는 황야의 땅의 고통의 요새를 지나 최남단에 있는 조그마한 발굴현장에 마지막으로 있었다고 하오. 우선 거기서부터 그를 찾아보시오."}, {"아그몬드를 찾아야 합니다."}},
[739] = {"멀달록", {"아그몬드의 자취가 서쪽에서 이어지고 있습니다. 바위와 모래에 피가 묻어 있는 것으로 보아 이 드워프는 죽기 전에 얼마간의 거리를 기어와서 이곳에서 죽은 것으로 보입니다.","자세히 살펴보니 황야의 땅 사암 조각에 \"멀달록\"이라는 단어가 휘갈겨 쓰여 있습니다. 아마도 아그몬드가 죽기 직전에 쓴 것으로 보입니다.","이 단어가 쓰인 바로 옆에는 죽은 아그몬드의 손이 있습니다."}, {"아그몬드를 살해한 멀달록을 처치해야 합니다.","바위동굴 뼈다귀싸움꾼 12마리를 처치해야 합니다.","그 후 모단 호수에 있는 발굴조사단장 아이언밴드에게 보고하십시오."}},
[741] = {"정신없는 발굴조사단장", {"발굴조사단장께서 신비로운 화석을 다르나서스에 있는 탐험가 연맹으로 보내시길 원하신단 말인가요? 하지만 저 혼자 이 어둠해안을 떠날 수는 없답니다.","자, 이 신비로운 화석을 다르나서스에 있는 수석고고학자 그레이위스커 님께 전해주세요."}, {"다르나서스에 있는 수석고고학자 그레이위스커에게 신비로운 화석을 가져가야 합니다."}},
[742] = {"잿빛 골짜기 사냥 대회", {"젊은 모험가들이여, 잘 들으십시오! 잿빛 골짜기의 야생이 당신을 기다리고 있습니다!","호드는 불모의 땅 북쪽에 튼튼한 전초기지를 세웠습니다. 토막나무 주둔지와 조람 해안에 있는 주둔지에서는 호드의 이름을 빛내려 노력하고 있습니다! 자신을 증명하고 싶은 이는 그곳에서 안내를 받으시기 바랍니다. 불모의 땅 바로 위에 있는 토막나무 주둔지의 세나니 썬더하트가 잿빛 골짜기 사냥에 참가할 모험가를 찾고 있으니 참고하시기 바랍니다!"}, {"잿빛 골짜기의 토막나무 주둔지에 있는 세나니 썬더하트와 대화해야 합니다."}},
[743] = {"성난바람 하피의 위협", {"내 이야기를 들어줄 시간을 내줘서 고맙네. 나는 루울이라고 하네. 전사지만 가르치는 일도 겸하고 있지.","<형제/자매>도 이제 멀고어 밖으로 모험을 떠날 준비가 되었는지 자신을 시험해 봐야 할 시기가 된 것 같군.","자신의 힘을 시험해 보고 싶다면, 남동쪽에 있는 성난바람 하피부터 시작해 보도록 하게. 그들은 길에서 떨어져 있는 산자락 주변에 서식하고 있네.","이 짐승은 이곳 멀고어에서 우리의 천적 중 하나이며 물론 <형제/자매>의 능력을 판단할 수 있는 좋은 기준이 될 걸세."}, {"블러드후프 마을에 있는 루울 이글탈론에게 성난바람 하피의 갈퀴발톱 8개를 가져가야 합니다."}},
[744] = {"의식을 위한 준비", {"나에게는 형님이 한 분 계시다오. 그 형님께서 곧 블러드후프 족장님을 만나는 의식을 치르게 되는데, 형님의 의식을 위해 머리 장식을 만들어 드리려 하오.","내가 이 가죽끈을 다듬는 동안 당신에게 부탁할 일이 있는데... 내가 충분한 양의 깃털을 모아 올 시간이 없으니, 날 위해 깃털을 좀 모아 주겠소?","썬더 블러프 멀리 북쪽과 북서쪽에 사는 하피들에게서 적당한 크기의 깃털을 얻을 수 있을 것이오. 하늘색 깃털과 청동색 깃털이 각각 6개씩만 있으면 원하는 모양의 머리 장식을 완성할 수 있을 것 같소."}, {"하늘색 깃털 6개와 청동색 깃털 6개를 모아야 합니다."}},
[745] = {"대지의 공유", {"이 땅은 많은 반목과 대립으로 상처받고 있네. 나와 달리 자네는 그런 끔찍한 일들을 많이 목격하지 않기를 바라네. 그럼에도 자네에게 부탁하고 싶은 일이 하나 있지. 아주 작은 위협이라도 빨리 손을 쓰지 않으면 얼마나 끔찍한 결과를 가져올 수 있는지 이번 일을 통해 알게 될 걸세.","회색갈기 놀들이 블러드후프의 남쪽과 서쪽에 있는 바위굴에 정착했네. 그들은 대화로 해결하려는 우리의 노력을 무시하고 평화롭게 초원을 뛰노는 멀고어의 야생동물들을 무참히 학살하고 있어.","이제 대화로는 문제를 해결할 수 없을 듯하네."}, {"회색갈기 무두장이 10마리와 회색갈기 가죽장이 8마리, 회색갈기 밀렵꾼 5마리를 처치한 다음 블러드후프 마을에 있는 바인 블러드후프에게 가야 합니다."}},
[746] = {"드워프 발굴지", {"북서쪽에 드워프들이 발굴지를 차리고 있네. 그놈들은 대지에 비밀이 숨겨져 있다고 생각하지. 그게 사실이긴 하지만, 대지를 파헤치고 더럽힌다고 비밀을 알아낼 수 있는 건 아니라네.","드워프들의 발굴 도구를 모아서 박살 내고, 부러진 곡괭이들을 내게 가져오게.","가열로 부근에서 놈들의 곡괭이를 부술 수 있을 걸세. 썬더 블러프에 가열로가 하나 있긴 하지만 어떤 가열로라도 상관없다네. 그러니 드워프 야영지에 있는 가열로를 사용해도 될 거야."}, {"발굴조사단장의 곡괭이 5개를 모아야 합니다.","가열로에서 발굴조사단장의 곡괭이를 부러진 곡괭이로 만들어야 합니다.","블러드후프 마을에 있는 바인 블러드후프에게 부러진 곡괭이 5개를 가져가야 합니다."}},
[747] = {"사냥의 시작", {"당신은 전도유망한 풍모를 지니고 있으며, 우리 부족에서 스스로 가치를 증명하게 될 것이오. 아마 머지않아 위대한 도시 썬더 블러프에서 당신이 환영받을 날이 올 거요. 하지만 그 전에 먼저 우리 아버지이신 호크윈드 족장님께 당신의 능력을 증명해 보여야 하오.","이곳 붉은구름 고원의 타우렌들은 자신의 훌륭한 사냥 실력에 대해 자부심을 가지고 있소. 우리 타우렌은 필요에 의해 사냥을 하기도 하고, 실력을 겨루기 위해 사냥을 하기도 하지. 그런데 지금 마을에 고기는 거의 바닥났고 옷을 만들 깃털도 필요한 상황이오. 근처에 있는 초원타조를 사냥해 마을에 필요한 물자를 보충하여 당신의 능력을 증명해 보시오."}, {"나라체 야영지에 있는 그룰 호크윈드가 초원타조 깃털 7개와 초원타조 고기 7덩이를 가져다 달라고 했습니다."}},
[748] = {"오염된 우물", {"고블린과 놈들의 부하들이 우리의 신성한 우물들을 점령하고 더럽히고 있소! 이건 절대 용납할 수 없는 일이오.","우물을 정화하려면 먼저 내가 정화 토템을 만들어야 하오. 그대가 그 토템을 우물로 가져가 정화의 의식을 수행해 주시오.","우선 윈터후프 우물에 사용할 정화 토템을 만들어야 하는데, 그러려면 대지의 도움을 받아야 하오. 토템에 필요한 재료는 남서쪽에 서식하는 초원늑대의 앞발과 다자란 초원타조의 발톱이오. 앞발과 발톱을 구해 내게 돌아오시오, <name>."}, {"블러드후프에 있는 멀 썬더혼에게 초원늑대 앞발 6개와 초원타조 갈퀴발톱 4개를 가져가야 합니다."}},
[749] = {"습격당한 짐마차 행렬", {"며칠 전, 투자개발회사의 짐마차가 경호를 받으며 황소바위 호수의 북쪽 가장자리를 따라 이동하는 것을 발견했습니다. 우린 이전 전투에서 병사 둘을 잃었기 때문에 재빨리 짐마차를 공격해 파괴했지요.","우린 짐마차의 잔해에 불을 지르곤 놈들의 보급품 상자를 확인하지 못하고, 상자들이 모두 불타 버릴 거라고 생각했습니다. 하지만 투자개발회사의 구조대가 불에 탄 마차 근처에서 목격된 걸 보니 우리 생각이 틀렸던 모양입니다.","짐마차가 있는 곳으로 가서 상자의 내용물을 좀 확인해 주십시오."}, {"모린 클라우드스토커가 습격당한 짐마차 행렬에 실려 있는 보급품 상자의 내용물을 확인해 달라고 했습니다."}},
[750] = {"계속되는 사냥", {"사냥에 능한 타우렌이라면 단순히 과시하고자 사냥을 하는 것은 아님을 알고 있을 것이오. 평야에 서식하는 짐승들은 우리에게 곧 생존 수단이기도 하기 때문이지. 혹시 당신이 산지퓨마 모피를 구해올 수 있다면 마을의 장로들에게 깊은 인상을 심어줄 수 있을 거요. 산지퓨마들은 남쪽 언덕으로 가면 찾을 수 있소.","지금 우리에겐 아이들의 옷을 만들고 천막을 수리할 가죽이 필요하오."}, {"나라체 야영지의 그룰 호크윈드가 산지퓨마 모피 10필을 가져다 달라고 했습니다."}},
[751] = {"습격당한 짐마차 행렬", {"뚜껑을 열자 잘 분류된 광석과 광물, 그리고 투자개발회사가 멀고어에서 채굴 작업을 진행 중임을 나타내는 물건들이 빼곡히 들어 있습니다. 광석과 광물 위에서 단단히 묶여 있는 종이 뭉치를 꺼냅니다.","동쪽의 작은 언덕에서 작업하는 소리가 희미하게 들리고 큰불에서 나는 연기가 보입니다."}, {"블러드후프 마을 근처에 있는 모린 클라우드스토커에게 투자개발회사의 문서를 가져가야 합니다."}},
[752] = {"작은 부탁", {"우리 부족원들은 조화로운 삶을 공유하며 살아가고 있지. 삶도 같이하고 일도 함께하면서 말일세.","부족이 서로 조화롭게 어울려 살아가기 위해서는 그에 따른 책임이 따른다네. 그런 의미에서 그대에게 작은 부탁 하나 하지.","우리 어머니께서 오늘 아침 나라체 남동쪽에 있는 우물로 물을 길러 떠나셨지. 벌써 출발하신 지 꽤 되었다네. 내가 여기서 부족 일을 돌보는 동안 자네가 어머니를 찾아 무슨 일이 생기신 건 아닌지 좀 알아봐 주겠나?"}, {"족장 호크윈드가 나라체 야영지 남동쪽에 있는 우물 근처에서 그의 어머니를 찾아봐 달라고 했습니다."}},
[753] = {"작은 부탁", {"내 소싯적에는 여행도 많이 다니곤 했지만 이젠 몸이 예전 같지 않구먼. 그래도 아직은 부족을 위해 내 소임을 다할 정도는 된다네. 나이 든 노파이니 때론 예전보다 시간이 좀 더 걸리는 편이긴 하지만 말이야.","자네는 의욕이 넘치는 <class> 같군. 그 젊은 혈기로 부탁 하나만 들어주지 않겠나? 우물에 있는 물병을 내 아들, 나라체 야영지의 족장에게 좀 가져다주게나.","보잘것없는 부탁이지만 잘해내 준다면 마을 장로들의 인정을 받을 수 있다는 사실을 잊지 말게."}, {"우물에 있는 물병을 집어서 우물에서 북서쪽에 있는 나라체 야영지의 족장 호크윈드에게 가져가야 합니다."}},
[754] = {"윈터후프 정화", {"윈터후프 우물을 정화할 토템이 완성되었소. 이제 이 토템을 우물로 가져가 정화의 의식을 수행하도록 하시오. 윈터후프 우물은 남동쪽에 있는데 괘씸한 고블린 녀석들이 지키고 있소.","쉽지 않은 일이겠지만 우물을 다시 사용하려면 반드시 해야 할 일이오.","행운이 함께하기를 비오."}, {"윈터후프 우물에서 윈터후프 정화 토템을 사용한 후 멀 썬더혼에게 가야 합니다."}},
[755] = {"대지모신의 의식", {"나라체의 타우렌들을 위해 작은 일도 마다하지 않는 마음과 배우고자 하는 열의는 아주 고귀한 성품일세. 그대라면 언젠가 썬더 블러프에서도 위대한 <class>|1으로서;로서; 이름을 떨칠 수 있을 거라 믿네.","그 전에 먼저 세 가지로 이루어진 대지모신의 의식을 수행해야 하네.","첫 번째 시험은 힘의 의식일세. 예언자 그레이텅을 찾아가 족장 호크윈드가 보냈다고 전하게.","나라체 야영지 남쪽으로 곧장 가면 언덕에 숨겨진 그의 거처를 찾을 수 있을 걸세."}, {"나라체 야영지 정남쪽에 있는 언덕에 사는 예언자 그레이텅을 찾아가야 합니다."}},
[756] = {"썬더혼 토템", {"썬더혼 정화 토템을 만들기 위해서는 멀고어 평야에 살고 있는 포식 동물의 발톱을 모아 와야 하오. 긴발톱 초원늑대와 평원퓨마를 잡아 발톱을 구해 돌아오시오.","긴발톱 초원늑대와 평원퓨마는 이곳의 동쪽과 서쪽에서 발견할 수 있을 거요."}, {"멀 썬더혼에게 긴발톱 초원늑대 발톱 6개와 평원퓨마 발톱 6개를 가져가야 합니다."}},
[757] = {"힘의 의식", {"대지모신의 의식은 젊은 타우렌이 썬더 블러프에서 인정을 받기 위해 반드시 거쳐야 할 과정이요.","우선 그대는 이번 힘의 의식을 통과해야만 하오. 이 시험을 통해 그대는 부족의 적을 처치하여 자신의 용맹함을 증명해야 하오.","동쪽에 있는 칼날가시 협곡의 뾰족털 일족이 우리 부족의 땅을 침범하고 있소. 그들은 우리 사냥꾼들을 매복하여 공격하고 어둠을 틈타 마을을 약탈하고 있소.","이 악당들을 처치하여 그대의 용맹함을 보이고, 그 증거로 그들의 허리띠를 나라체 야영지의 족장님께 가져가도록 하시오."}, {"칼날가시 협곡에 있는 뾰족털 일족을 처치하고 나라체 야영지에 있는 족장 호크윈드에게 뾰족털 허리띠 12개를 가져가야 합니다."}},
[758] = {"썬더혼 정화", {"토템이 준비되었소. 다시 한번 말하지만, 정화의 의식을 수행하는 건 <형제/자매>의 몫이오.","마을 북쪽에 있는 썬더혼 우물로 가서 썬더혼 정화 토템을 사용하여 의식을 수행하시오. 의식을 마친 후에는 내게 돌아와야 하오.","정령들의 가호가 함께하기를..."}, {"썬더혼 우물에서 썬더혼 정화 토템을 사용한 다음 멀 썬더혼에게 가야 합니다."}},
[759] = {"와일드메인 토템", {"이제 남은 우물 하나, 와일드메인 부족의 이름을 딴 우물을 정화하는 일만 남았소. 이 우물을 깨끗이 정화하려면 이 땅의 난폭한 포식자인 우두머리 초원늑대 이빨이 필요하오. 북쪽에서 우두머리 초원늑대를 찾아 사냥해 돌아오시오.","우두머리 초원늑대들은 종종 위대한 도시 썬더 블러프의 기슭을 돌아다니곤 하오."}, {"블러드후프 마을에 있는 멀 썬더혼에게 우두머리 초원늑대 이빨 8개를 가져가야 합니다."}},
[760] = {"와일드메인 정화", {"윈터후프와 썬더혼 우물을 정화한 <형제/자매>의 공적은 이미 많은 이들에게 회자되고 있소. 와일드메인 토템이 만들어졌으니 이제 마지막 임무를 수행할 차례요.","와일드메인 우물은 썬더 블러프 북쪽에 있소. 그곳에서 의식을 수행하고, 그림토템의 독에 오염된 땅을 정화하시오! 다시 한번 깨끗한 물이 흘러나오게 하시오!"}, {"와일드메인 우물에서 와일드메인 정화 토템을 사용해야 합니다."}},
[761] = {"검독수리 사냥", {"검독수리는 교활한 새라 녀석들을 찾아 사냥한다는 건 쉬운 일이 아니지. 때문에 검독수리 깃털을 수집하는 것은 사냥꾼에게 유능함과 결단력을 상징하오.","이 일을 해볼 생각이 있다면 평야로 가서 검독수리들을 사냥해 보시오. 내게 검독수리 깃털을 가져오면 <형제/자매>의 명예가 높아질 거요.","검독수리들은 멀고어 어디에나 서식하고 있소. 하지만 눈을 크게 뜨고 살펴야만 찾을 수 있으며, 발이 빨라야 녀석들을 잡을 수 있다는 것을 명심하시오."}, {"블러드후프 마을에 있는 하큰 윈드토템에게 검독수리 깃털 8개를 가져가야 합니다."}},
[762] = {"악의 사자", {"라그나로스는 울다만에서 사라진 유물을 찾고 있는 어둠괴철로 드워프들을 보호하기 위해 사자 인페르누스를 고통의 요새로 보냈지.","이미 우리 종족에게 무서운 참사가 일어났지만 그 위력은 우리의 예상보다는 미비한 것이었네. 하지만 놈들이 얼마나 많은 양의 유물을 찾아 아이언포지 공격에 사용할지 생각하기조차 싫군.","우리의 목표는 간단하네, <class>. 황야의 땅으로 돌아가 사자 인페르누스를 처치하게. 위협이 제거되면 승리의 증거를 가지고 조언자 벨그룸 님께 돌아가면 되네.","이제 모든 건 자네 손에 달렸어."}, {"고통의 요새에 있는 사자 인페르누스를 처치하고 아이언포지에 있는 조언자 벨그룸에게 그의 죽음에 대한 증거를 가져가야 합니다."}},
[763] = {"대지모신의 의식", {"대지모신의 의식을 계속 수행하려면 두 가지 시험을 더 통과해야 하네. 그대의 경험을 넓힐 때가 된 것이야.","블러드후프 마을로 가서 케른 블러드후프님의 아들이자 족장인, 바인 블러드후프를 찾도록 하게. 거기서 모험을 계속하다 보면 썬더 블러프 장로들의 신임을 얻을 수 있을 걸세.","이 토템을 바인에게 가져가도록 하게. 그 친구가 내 목각품이라는 것을 알아보고 그대를 도와줄 걸세.","서둘러 나라체 야영지 외각의 길을 따라가도록 하게. 한눈팔면 길을 잃을 수도 있으니 조심하게."}, {"블러드후프 마을에 있는 바인 블러드후프에게 호크윈드의 토템을 가져가야 합니다. 나라체 야영지 외곽의 길을 따라가십시오."}},
[764] = {"투자개발회사", {"이제 멀고어에서 투자개발회사 직원들과 장비들이 점점 더 많이 목격되고 있는 이유를 알 것 같군요. 저 고블린 녀석들... 투자개발회사가 이익을 위해 사업을 너무 급하게 확장하고 있습니다. 놈들은 자신들이 고블린을 대표해 대단한 일을 한다고 말하고 다니지만 어림없는 소리죠. 저들의 목적이 무엇인지는 충분히 짐작하고도 남습니다.","놈들은 탐욕스러운 난쟁이들에 불과합니다. 그들의 사업에는 안 된 일이지만, 우리 타우렌은 놈들이 우리 땅에서 작업을 벌이는 걸 두고 볼 수 없습니다. 습격당한 짐마차 행렬의 동쪽에 있는 놈들의 광산으로 가서 우리의 뜻을 전하십시오."}, {"블러드후프 마을에 있는 모린 클라우드스토커를 위해 투자개발회사 노역꾼 14명과 투자개발회사 현장감독 6명을 처치해야 합니다."}},
[765] = {"감독관 피즈스프로켓", {"당신이 짐마차에서 가져온 문서에 따르면 투자개발회사는 이 땅에서 우리를 몰아내고 우리 땅을 마음껏 약탈하고자 많은 계획을 세웠군요.","목표한 바를 이루려고 수단과 방법을 가리지 않는 그들을 보면 혐오감에 몸이 오싹해지기까지 합니다. 배후에서 그 계획을 세운 지도자를 처치하십시오. 바로 감독관 피즈스프로켓이라는 자인데, 습격당한 짐마차 행렬 동쪽에 있는 투자개발회사 광산으로 가면 찾을 수 있을 겁니다. 뭔가 유용한 정보를 얻을지도 모르니, 제게 그자의 서류철을 가져다주십시오."}, {"감독관 피즈스프로켓을 처치하고 블러드후프 마을의 모린 클라우드스토커에게 피즈스프로켓의 서류철을 가져가야 합니다."}},
[766] = {"마즈라나체", {"우리 할아버지께서는 내게 마즈라나체라는 타조 녀석과 싸웠던 얘기를 해 주시곤 했지. 할아버지는 마즈라나체의 악마 같은 붉은 눈과 면도날처럼 날카로운 발톱, 독을 뿜는 이빨에 대해 얘기해 주셨어.","그런데 마치 그게 예언이었던 것처럼 평야를 여행하다 우연히 놈과 마주쳤고 놈에게 어깨를 물리고 말았네. 놈은 할아버지께서 말씀하신 그대로 정말 끔찍한 놈이었지. 놈에게 물린 상처의 감염을 막으려면 몇 가지 재료가 필요한데 좀처럼 얻기 힘든 것들이지. 늑대 심장, 퓨마의 다리뼈, 초원타조 비늘, 그리고 검독수리 내장이 필요하다네.","시간이 부족하니 서둘러 주게."}, {"블러드후프 마을에 있는 마우르 레인콜러에게 초원늑대 심장, 평원퓨마 다리뼈, 초원타조 비늘과 검독수리 내장을 가져가야 합니다."}},
[767] = {"예언의 의식", {"대지모신의 의식 중 하나인 예언의 의식은 썬더 블러프 장로들의 신임을 얻는 데 도움이 될 걸세.","우리 타우렌들은 대지가 우리의 가장 신성한 부양자라는 사실을 배워 왔지. 의식에서 예언을 직접 얻으려면 마을의 정신적 지도자인 잘만 투문을 만나야 할 게야."}, {"블러드후프 마을에 있는 잘만 투문과 대화해야 합니다."}},
[768] = {"가죽 수집", {"어서 오시오, 젊은이. 당신 눈빛을 보니 평야로 돌아가 사냥하고픈 욕망이 가득하군. 당신이 부족의 명예를 빛내게 되기를 진심으로 바라오!","나는 썬더 블러프 마을 주민들을 위해 짐승 가죽으로 평상복과 갑옷을 만들고 있소.","당신도 짐승을 사냥하거든 가죽을 내게 가져다주시오. 그러면 당신을 위해 무언가 만들어 주도록 하겠소."}, {"썬더 블러프에 있는 베렌 톨스트라이더에게 얇은 가죽 12개를 가져가야 합니다."}},
[769] = {"코도 가죽 가방", {"멀고어에 사는 코도는 힘이 세고 용맹해서 우리에겐 동경의 대상이지. 혹 당신이 가죽세공을 익혔고, 코도 가죽 가방 만드는 기술을 익히고 싶다면 거래에 필요한 물품을 좀 구해다 주시오.","그렇게 해준다면 내가 방법을 알려 주겠소."}, {"썬더 블러프에 있는 베렌 톨스트라이더에게 얇은 가죽 4개와 굵은 실타래 4개를 가져가야 합니다."}},
[770] = {"악마의 흔적이 남은 가죽망토", {"해진 유령울음늑대의 가죽에는 아직도 불타는 군단과의 전투에서 입은 끔찍한 상처가 남아 있습니다.","해넘이 기지에 유령울음늑대의 죽음에 대해 아는 이가 있을지도 모릅니다..."}, {"유령울음늑대에 대해 아는 이를 찾아야 합니다."}},
[771] = {"예언의 의식", {"예언의 의식을 행하려면 현자의 물을 만드는 데 필요한 재료들을 모아야 하네.","우선 멀고어의 우물 주위에서 볼 수 있는 우물돌이 필요하지. 또 우리 땅에서 자라는 거대한 나무 밑 여기저기 떨어져 있는 대지열매를 주워 우물돌과 결합해야 하네.","이 재료들은 마법 성분이 있어 부족의 불로 끓여 마시면 미래를 예견할 수 있을 것이야. 그 예견을 따르면 이 신성한 임무의 다음 단계에 다다르게 될 것이네."}, {"우물돌 2개와 대지열매 2개를 모아 블러드후프 마을에 있는 잘만 투문에게 가져가야 합니다."}},
[772] = {"예언의 의식", {"자, 현자의 물을 받게.","준비가 되면 부족의 불 근처에서 그 물을 마시게나. 현자의 물을 마신 후 조금 있으면 불 앞에 미래의 예언이 나타날 것이야.","바로 그때, 그것을 운명으로 따를 것인지는 그대에게 달려 있네."}, {"블러드후프 마을에 있는 부족의 불 앞에서 현자의 물을 마시고 나타나는 예언을 따라가야 합니다."}},
[773] = {"지혜의 의식", {"썬더 블러프의 장로들에게 인정을 받으려면, 다음으로 지혜의 의식을 완수해야만 하네.","이제 예언의 의식을 잘 통과했으니 붉은 바위 언덕에 잠들어 계시는 선조님들의 가호를 받을 자격은 있지만, 예언의 물을 마신 자만이 그 가호를 받을 수 있다네.","썬더 블러프 동쪽에 있는 붉은 바위 언덕으로 가서 고대의 영혼을 찾도록 하게."}, {"썬더 블러프 동쪽에 있는 붉은 바위 언덕으로 가서 고대의 영혼과 대화해야 합니다."}},
[775] = {"썬더 블러프로의 여정", {"이제 우리의 위대한 도시에서 그대는 존경과 찬양을 받게 될 것이로다. 대지모신의 의식을 완수할 만한 능력이 있는 <race>|1이라면;라면; 그 영광을 얻을 자격이 있음이 틀림없도다.","그대에게 마지막 임무를 부여하노니, 썬더 블러프의 가장 높은 벼랑 꼭대기에 기거하는 고귀한 지도자 케른 블러드후프를 찾도록 하라."}, {"썬더 블러프에 있는 케른 블러드후프와 대화해야 합니다."}},
[776] = {"대지모신의 의식", {"그대는 대지모신의 의식을 충실히 수행하여 썬더 블러프에서 당당한 지위를 얻었네.","그러나 애써 얻은 지위를 유지하려면, 형제들에게 그대의 능력을 계속 증명해 보여야만 하네.","그대도 알다시피 우리는 사냥꾼이지 않은가, <name>. 저 아래 멀고어에 알라치아라는 강력한 코도가 살지. 알라치아를 추적해 처치하고 알라치아의 뿔을 내게 가져와 그대의 능력을 보여주게나."}, {"썬더 블러프에 있는 케른 블러드후프가 알라치아의 뿔을 가져다 달라고 했습니다."}},
[777] = {"불길한 예감", {"자, 여기 있습니다.","전 이 덮개 뒤에 몸을 숨길 거예요.","이리 와서 여기 숨으세요."}, {"로트윌 베리아투스에게 5번 기어를 가져가야 합니다."}},
[778] = {"불길한 예감", {"하하! 드디어 다 됐군! 족쇄가 완성되었단 말이야!","루시앙! 세르보! 준비해!","연금술, 공학, 그리고 불가사의의 완벽한 결정체를 직접 볼 준비가 됐나요?","이제 강력한 정령을 불러낼 거예요. 오늘은 우리 모두 새로운 걸음을 내딛는 뜻 깊은 날이 될 거라고요."}, {"팜레터 수호정령을 물리치고 로트윌 베리아투스에게 로트윌의 정령 봉인 족쇄를 되찾아 가야 합니다."}},
[779] = {"대지의 봉인", nil, nil},
[780] = {"전투멧돼지", {"동쪽에 있는 칼날가시 협곡의 전투멧돼지들이 우리 부족의 사냥터를 침범하고 있소. 그 전투멧돼지들은 우리와 전쟁 중인 뾰족털 가시멧돼지 놈들이 조련시킨 것이오.","이 괘씸한 짐승들을 처치하고 우리 아이들을 위해 스튜를 만들 수 있도록 주둥이와 갈빗살을 몇 점 가져다주시오."}, {"나라체 야영지에 있는 그룰 호크윈드가 전투멧돼지를 처치하고 전투멧돼지 주둥이 8개와 전투멧돼지 갈빗살 8점을 가져다 달라고 했습니다."}},
[781] = {"나라체 야영지에 대한 공격", {"지도를 꼼꼼히 살펴보니 뾰족털 대족장이 나라체 야영지에 대한 대대적인 포위 공격을 계획하고 있다는 사실을 알아냅니다.","이는 분명히 족장 호크윈드에게 유용한 정보가 될 것입니다!"}, {"나라체 야영지에 있는 족장 호크윈드에게 뾰족털일족의 작전도를 가져가야 합니다."}},
[782] = {"깨어진 동맹", {"2차 대전쟁 때 호드가 용 데스윙과 동맹을 맺었던 경험을 통해, 우리는 데스윙의 부관인 블랙래쉬와 헤마투스가 저 먼 동쪽 레슬로 협곡에 갇혀 있었다는 사실을 익히 알고 있소. 우리는 반드시 타락한 옛 동지를 처단하여 호드라는 이름의 명예를 회복해야 하오!","우리는 토그룬과 그의 오우거 용병 부대를 고용해 비룡의 감옥을 여는 열쇠인 대지의 봉인을 찾고 있었소.","그런데 토그룬이 우리를 배신하고 봉인을 가져가 버렸으니 보프 야영지로 가서 봉인을 되찾아 오시오!"}, {"우두머리 토그룬을 처치하고 카르가스에 있는 고른에게 대지의 봉인을 가져가야 합니다."}},
[783] = {"내부의 위협", {"젊은 <class>|1이여;여;. 데스윙과 황혼의 군대가 아제로스에 총공격을 감행해 왔습니다. 이에 대응해 국왕 폐하는 동원 가능한 모든 얼라이언스 시민들의 힘을 불러모으기로 하셨습니다.","넓은 이 땅 아래 지켜야 할 사람들은 너무 많고, 너무나 많은 위협이 엄습해오는 가운데, 스톰윈드 경비병들은 이곳의 평화를 지켜내야 한다는 강한 압박감을 느끼고 있습니다. 그래서 현재는 고향과 자신의 동맹국을 지키려는 뜻이 있는 자라면 누구든지 지원자로 받아들이고 있습니다.","이러한 부름에 응하고자 이곳에 오셨다면 제 상관이신 치안대장 맥브라이드 님을 만나 보시기 바랍니다. 그분은 수도원 밖에 계십니다."}, {"치안대장 맥브라이드와 대화해야 합니다."}},
[784] = {"배신자 처단", {"프라우드무어 제독이 이끄는 쿨 티라스의 인간들이 몇 년 전 아키몬드를 물리치기 위해 듀로타를 선공했네. 제이나 프라우드무어는 대족장님과 맺은 불가침 조약도 깨고 말이지.","하지만 사악한 인간들의 침략 행위는 저지당했고 타이라가드 요새도 함락됐어. 그런데 최근 살아남은 프라우드무어 함장의 병력들이 부대장 베네딕트의 지휘 아래 타이라가드 요새를 탈환하기 위해 우리 땅을 넘보고 있네. 이자들은 외교 조약을 완전히 무시하고 있지.","남쪽의 타이라가드 요새로 가서 침략자 인간 무리를 처단해주게나."}, {"쿨 티라스 선원 10명과 쿨 티라스 해병 8명, 그리고 부대장 베네딕트를 처치한 다음 칼바위 언덕에 있는 가르톡에게 가야 합니다."}},
[785] = {"전략적 동맹", {"스랄 님은 지혜롭고 훌륭한 지도자시지. 뛰어난 외교적 수완만큼이나 전장에서 집념도 대단하시고 말일세.","이제 우리에게 있어 검은창 트롤은 연합군으로서 대단히 중요한 힘이 돼주고 있지. 우리는 충성과 명예를 담아 그들과 보호 협정을 맺었네.","그래서 대족장님께서는 센진 마을에 있는 검은창 연합군에게 직접적으로 가해지는 위협들을 모두 제거하라는 명령을 내리셨네. 검은창 트롤의 가장 믿음직한 정찰병인 라르 프라울터스크가 서쪽 측면 지역을 엄중히 감시하고 있네.","라르를 찾아 도움을 주도록."}, {"센진 마을 서쪽 측면 지역에서 라르 프라울터스크를 찾아야 합니다."}},
[786] = {"콜카르 침략 저지", {"목소리를 낮춰. 콜카르 켄타우로스들이 콜카르 바윗골 서쪽 능선을 넘어 공격해 오고 있어.","어젯밤 놈들이 습격하는 동안 나는 놈들의 마을로 숨어들어 이 더러운 짐승들이 듀로타의 트롤과 오크에 대한 삼중 공격을 계획하고 있다는 사실을 알아냈지.","반드시 놈들의 계획을 막아야 해. 당신이 병사들을 소집해 콜카르 바윗골에 잠입하여 놈들의 공격 계획서를 없애 주지 않겠나?","내가 마지막으로 확인한 바로는 네 명의 우두머리가 있었고 그중 세 명이 세 개의 공격 계획서를 각각 하나씩 가지고 있었어."}, {"센진 마을 외곽에 있는 라르 프라울터스크가 콜카르 바윗골에 있는 3개의 공격 계획서를 없애 달라고 했습니다."}},
[787] = {"호드 신병", {"트롬카! 난 스랄 님으로부터 신병 훈련 임무를 받은 아이트리그라 하네.","지금의 호드는 예전과 많이 달라졌네. 한 때 권력에 굶주린 불타는 군단과 결탁한 앞잡이들이 세력을 불렸다네. 나는 환멸을 느껴 호드를 떠났던 적이 있네. 그 후 인간 무리에 포로로 잡혔었는데 대족장님께서 나를 구해 주셨다네. 나는 대족장님의 뜻에 공감하여 다시 돌아왔다네. 그분께서는 주술사들의 지도하에 악마의 세력에서 벗어난 자유로운 호드를 꿈꾸셨지.","동굴 막사에 있는 고르넥이 자네에게 앞으로의 훈련 내용을 자세히 알려줄 걸세."}, {"동굴 막사에 있는 고르넥에게 보고해야 합니다."}},
[788] = {"몸 풀기", {"우선 당신의 굳어진 몸을 풀어줄 만한 일부터 시작하도록 하지. 사실 곧바로 불모의 땅으로 보내 코도를 잡아오라고 하고 싶지만 그러다 당신이 죽어버리기라도 하면 곤란한 일이지. 아무래도 살아 있는 것이 죽는 것보단 우리한테 도움이 될 테니 말이야.","우선은 점박이멧돼지가 당신에게 적합한 상대일 걸세. 북쪽과 북동쪽으로 가면 농장에 멧돼지가 많을 테니 한 번 잡아 보게."}, {"점박이멧돼지 8마리를 처치한 다음 동굴 막사에 있는 고르넥에게 가야 합니다."}},
[789] = {"전갈의 독침", {"고참 전사나 서툰 풋내기나 할 것 없이 모두 무릎을 꿇게 만드는 것이 바로 전갈의 독침이지. 당신의 용기를 증명해 보이고 싶다면 북서쪽에 있는 전갈들을 처치하고 그 증거로 일꾼전갈 꼬리 8개를 가져오게.","그 꼬리에서 독액을 추출하면 전갈 독침의 해독약을 만들 수 있네. 부상병을 치료하려면 많은 양의 해독약을 미리 준비해 놓고 있어야 하지. 하지만 당신과 같이 강한 자라면 해독약 같은 건 필요 없을 거라 믿지만..."}, {"동굴 막사에 있는 고르넥을 위해 일꾼전갈 꼬리 8개를 모아야 합니다."}},
[790] = {"사르코스", {"오, <class> 친구를 만나다니! 여기서 저 혼자 아무도 모르게 죽게 되는 줄만 알았는데... 아까 골짜기에서 전갈을 사냥하던 중에 유난히 사악해 보이는 녀석을 하나 발견했습니다. 그래서 놈에게 뛰어들어 녀석의 발톱을 힘껏 내리쳤지만 곧 내 다리 가까이로 기어오더군요.","그놈의 독침에 대해서는 무방비 상태였던 터라, 녀석이 순식간에 다가와 가슴을 찌르는 바람에 피를 많이 흘리고 말았습니다. 제발 절 위해 그 전갈 녀석을 처치해 주십시오! 그래서 반드시 제 잃어버린 명예를 되찾아 주었으면 합니다. 여기에서 남쪽에 있는 언덕에서 녀석과 싸웠었습니다."}, {"사르코스를 처치하고 그의 발톱을 하나주아에게 가져가야 합니다."}},
[791] = {"보급품 조달", {"세월은 내 전투 능력을 앗아가고 기력도 쇠해 이제 나는 다른 일을 맡고 있다네.","바로 이 고지에서 침략자가 없는지 감시하는 일이지. 이곳에서 우리의 세력이 커짐에 따라 내가 전쟁 나팔을 부는 횟수도 나날이 줄고 있네.","그래서 우리 땅을 지키는 젊은이들을 돕기 위한 장비를 만들며 무료함을 달래고 있지.","내 자네를 위해 소지품을 보관할 수 있는 가방을 만들어 주도록 하지. 그러려면 약간의 천 조각이 필요하니 좀 가지고 오게. 천 조각은 인간과 켄타우로스들이 즐겨 사용하니, 그들에게 구할 수 있을 거야."}, {"칼바위 언덕 감시탑에 있는 펄 스콘브라우가 캔버스 천조각 8개를 가져다 달라고 했습니다."}},
[792] = {"악마의 하수인", {"여기 시험의 골짜기에서 많은 걸 배울 수 있을 거라 믿어요. 난 당신을 지도하기 위해 이곳으로 파견되었습니다.","얼마 전 이곳에서 점점 커가고 있는 악의 세력을 발견했어요. 자칭 불타는 칼날단이라는 일족이 시험의 골짜기에 소굴을 마련했더군요. 놈들은 북동쪽 동굴 안에 숨어서 악마의 하수인을 내보내 대혼란을 야기하려 하고 있어요.","첫 임무로 놈들의 하수인을 처치하도록 하세요. 언젠가는 불타는 칼날단을 완전히 소탕하러 가야겠지만... 지금은 될 수 있는 한 많이 처치하고 돌아오세요. 살아남을 수 있다면 말이죠."}, {"악마의 하수인 8마리를 처치해야 합니다.","임무를 완수한 뒤 동굴 막사 바깥에 있는 주리타 파게이즈에게 가야 합니다."}},
[793] = {"깨어진 동맹", {"대지의 봉인은 레슬로 협곡에 있는 다이아몬드 기둥, 오팔 기둥, 자수정 기둥에 보관된 세 개의 마법석에 의해 유지되고 있소. 대지의 봉인을 해제하려면 대지의 봉인을 사용하여 기둥 안에서 작동되고 있는 마법석을 제거해야만 하오.","검은 비룡 블랙래쉬와 헤마투스는 만만한 상대가 아니니 조심하시오. 놈들을 처치하고 놈들이 차고 있는 굴레를 내게 가져다주시오.","레슬로 협곡으로 가려면 황야의 땅의 동쪽 먼 곳으로 여행해야 하오. 협곡은 헤아릴 수 없이 많은 비룡의 서식지이니 조심하도록 하시오."}, {"대지의 봉인을 사용하여 다이아몬드, 오팔, 자수정의 기둥을 작동시켜 마법석을 획득해야 합니다.","마법석을 대지의 봉인에 놓아 블랙래쉬와 헤마투스의 봉인을 풀어줘야 합니다.","블랙래쉬와 헤마투스를 처치하고 고른에게 블랙래쉬의 굴레, 헤마투스의 굴레와 대지의 봉인을 가져가야 합니다."}},
[794] = {"불타는 칼날단 메달", {"내가 점을 쳐본 바로는 불타는 칼날단 소굴 깊숙한 곳에 강한 힘을 지닌 무언가가 숨겨져 있는 게 보여요. 짐승들과 흑마법이 그것을 지키고 있지요.","불타는 칼날단 메달이라 불리는 것인데, 당신의 다음 임무는 소굴 안에서 그 물건을 찾아 가져오는 거예요.","하지만 그 물건을 지닌 녀석은 이전에 당신이 상대했던 하수인들보다 훨씬 강할 테니 조심하도록 하세요.","북쪽으로 가서 동굴을 찾으면 동굴 안 북쪽과 서쪽에서 그 무리를 찾을 수 있을 거예요."}, {"동굴 막사 외부에 있는 주리타 파게이즈에게 불타는 칼날단 메달을 가져가야 합니다."}},
[795] = {"대지의 봉인", nil, nil},
[804] = {"사르코스", {"당신이 나를 위해 해준 용감한 행동에 크게 감명 받았소. 내가 이렇게 쉽게 죽을 수는 없지 않겠소! 반드시 이겨 낼 것이오!","하지만 다른 이의 도움 없이는 동굴 막사로 돌아갈 수 없다는 게 문제구료. 동굴 막사로 돌아가 고르넥에게 내 상황을 좀 전해 주시오. 아마 그는 날 도와 줄 수 있을 거요."}, {"동굴 막사에 있는 고르넥에게 하나주아의 현재 상황을 전해야 합니다."}},
[805] = {"센진 마을에 보고", {"이곳 시험의 골짜기에서 불타는 칼날단을 상대로 한 시험은 이것으로 끝났어요. 하지만 당신이 발견한 사실들을 보고해 줬으면 좋겠는데...","트롤들의 마을인 센진 마을로 가서 장로 가드린을 찾도록 하세요. 센진 마을은 시험의 골짜기 밖으로 나가 동쪽으로 가다 보면 나오는 갈림길에서 오른쪽에 있습니다.","가드린에게 불타는 칼날단이 시험의 골짜기까지 넘보고 있다고 전하세요. 녀석들이 벌써 센진 마을에까지 이른 것은 아닌지 그에게 알아보세요.","가세요, <name>. 서둘러야 합니다. 불타는 칼날단 소굴의 악이 더 큰 위협을 예고하는 건 아닌지 걱정이군요."}, {"센진 마을에 있는 가드린 장로와 대화해야 합니다."}},
[806] = {"다크스톰", {"불타는 칼날단이 듀로타에까지 발을 들여 놓게 할 수는 없네. 악의 세력이 퍼지기 전에 밟아 줘야 해!","내 알아본 소식에 따르면 피즐 다크스톰이라는 고블린 흑마법사가 북서쪽 천둥 마루에 야영지를 설치했네. 거기에서 피즐 다크스톰은 그 숭배자들과 함께 혼란과 재앙을 불러 일으키고 있어.","피즐 다크스톰을 찾아 처치하고 놈의 발톱을 가져오게."}, {"칼바위 언덕에 있는 오그닐 소울스카에게 피즐의 발톱을 가져가야 합니다."}},
[808] = {"민시나의 해골", {"꿈에서 나를 부르는 우리 형 민시나의 목소리를 들었네.","형은 동쪽에 있는 메아리 섬의 흑마법사, 잘라제인에게 잡혀가서 죽고 말았지.","하지만 형은 죽어서도 자유로울 수 없었어. 잘라제인은 형의 영혼에 마법을 걸어 민시나의 해골에 가두어 놓았네. 꿈속에서 난 민시나의 해골을 보았네. 메아리 섬 일대에서 가장 큰 섬에 있는 힘의 마법진에 다른 해골들과 함께 있었어. 해골이 거기에 있는 한, 형의 가혹한 운명은 끝나지 않을 거야.","제발 그 마법진을 찾아 민시나의 해골을 내게 가져다주게.","부디 형을 자유롭게 해 줘!"}, {"메아리 섬에 있는 힘의 마법진에서 민시나의 해골을 찾아와야 합니다.","센진 마을에 있는 장로 가드린에게 민시나의 해골을 가져가야 합니다."}},
[809] = {"아크젤로스", {"불타는 군단이 패한 최후의 대전쟁 이후, 나는 오크 사회 내에서 마력이 불러온 타락과 부정을 연구해 왔네. 자네가 가져온 이글거리는 목걸이를 통해 그동안 걱정해왔던 것이 사실이었음을 확인했지.","이 목걸이는 악마의 씨앗이라고 하는 마력의 물건을 중심으로 결성된 불타는 칼날단 이교도 집단의 것이야. 그 씨앗은 반드시 파괴되어야만 하네! 불모의 땅, 공포안개 봉우리 꼭대기에 있지.","여기서 서쪽으로 가면 불모의 땅 경계 부근에 천리길 경비초소가 보일 거야. 그곳에서 내 조수인 아크젤로스를 만나 보게. 앞으로 당신이 할 일을 알려줄 게야."}, {"불모의 땅에 있는 아크젤로스와 대화해야 합니다."}},
[812] = {"해독약 구하기", {"마침 잘 왔군. 자네에게 급한 부탁이 있네.","난 이 근처에서 맹독전갈 몇 마리와 싸우고 있었는데, 부주의로 그만 한 놈에게 깊이 찔리고 말았어. 당신에게 얘기하고 있는 지금도 몸 속에 독이 퍼져 나가는 게 느껴져. 이런 속도라면 아마 한 시간도 채 견디지 못할 것 같아. 그래서 도움이 필요해.","오그리마에 있는 코간에게 가면 해독약을 만들어 줄 거야.","제발 코간을 빨리 찾으라고. 서둘러 줘. 난 오래 버틸 수 없을 것 같아. 코간은 어둠의 틈이라는 곳에서 찾을 수 있을 거야."}, {"오그리마에서 코간을 찾아서 해독약을 받은 다음, 불모의 땅과 인접한 듀로타 변두리에 있는 리나그에게 맹독전갈 해독약을 가져가야 합니다."}},
[813] = {"해독약 재료 구하기", {"맹독전갈은 듀로타에 서식하는 가장 위험한 전갈 중 하나지. 해독약 없이 놈들을 사냥하러 나간 것 자체가 리나그의 잘못이지만, 지금 그를 탓해 봤자 아무 소용없는 일이고...","오그리마 입구 근처에서 맹독전갈 독주머니 몇 개를 구해오면 내가 해독약을 만들어 주도록 하지.","서둘러 움직이라고. 호드의 일원이 도움을 필요로 하고 있으니."}, {"오그리마에 있는 코간에게 맹독전갈 독주머니 4개를 가져가야 합니다."}},
[815] = {"알 요리", {"이런! 아침 식사 준비를 깜빡 잊을 뻔했군.","어서 가서 붉은발톱랩터 알을 몇 개 구해 오게. 내일 식사 준비를 하려면 적어도 3개는 필요하단 말이야.","붉은발톱랩터는 안 그래도 사납지만, 특히 알을 보호하기 위해서라면 죽음을 불사하기도 하지. 그리고 알을 땅속 깊이 숨겨 두기도 한다네. 하지만 센진 마을에서 남동쪽에 있는 메아리 섬으로 가면 곳곳에서 놈들을 쉽게 찾을 수 있을 거야."}, {"칼바위 언덕에 있는 요리사 토르카에게 붉은발톱랩터 알 3개를 가져가야 합니다."}},
[816] = {"크론의 흔적", {"제발, 제발 저를 도와 주세요. 내 아들 크론이 며칠 전 사냥을 나가서 아직 돌아오지 않고 있어요. 크론은 불모의 땅 서쪽의 성난남녘 강으로 악어 사냥을 떠났어요. 제발 아무 일도 없어야 할 텐데...","강한 아이지만 겁도 없고 고집도 세서 늘 문제랍니다.","악어는 힘도 세고 포악하다고 그렇게도 주의를 줬건만... 원래 거친 아이라서 제 말은 잘 듣지 않는다니까요.","그 아이의 흔적이라도 찾아다 주세요. 성난남녘 강 강둑에 있는 악어를 다 죽여서라도..."}, {"칼바위 언덕의 북서쪽 농장에 있는 미샤 톨크렌에게 크론의 흔적을 가져가야 합니다."}},
[817] = {"유용한 사냥감", {"우리가 사용하는 가죽의 상당수는 듀로타 호랑이에게서 얻고 있소. 모포, 갑옷, 천막... 정말 쓰임새가 다양하지. 그게 바로 우리가 이 짐승을 사냥함과 동시에 보호하는 이유라오.","말하자면, 지금이 놈들의 머릿수를 좀 추려내야 할 시기라는 거요. 주민의 수가 늘면서 비축해둔 가죽의 양보다 수요가 많아지기 시작했기 때문이지. 좀 더 사냥해 질 좋은 가죽을 준비해 놓는 수밖에 없소.","당신이 듀로타 호랑이 가죽 4필을 가져다주면 보상을 해 주도록 하지. 호랑이는 남쪽에 있는 섬에서 찾을 수 있소."}, {"센진 마을에 있는 벨린 팡에게 듀로타 호랑이 가죽 4필을 가져가야 합니다."}},
[818] = {"나약한 정신", {"눈이 많이 나빠지긴 했지만 완전히 안 보이는 건 아니야. 그래도 예전과 달리 먼 곳을 보려면 연금술을 이용해 마법의 힘을 자주 빌려야 하지. 그래도 수습생은 받아들이지 않았네. 수습생이 되기를 자청한 이들 중에 그만한 자격이 되는 트롤이나 오크는 없었거든.","물론 자네도 나라면 충분한 자격이 있다고 생각하겠지.","그래서 말인데 내가 몇 가지 필요한 게 있다네. 자네가 좀 구해다 줄 텐가?","온전한 마크루라의 눈과 붉은톱니게 점액이 필요하다네. 마크루라나 붉은톱니게는 바닷가에서 찾을 수 있을 거야. 그럼 또 보게 되길..."}, {"센진 마을에 있는 장로 보르날에게 온전한 마크루라의 눈 4개와 붉은톱니게 점액 8병을 가져가야 합니다."}},
[819] = {"Chen's Empty Keg", {"\"Chen Stormstout-"}, {"Find someone who knows about Chen's Empty Keg."}},
[821] = {"첸의 빈 맥주통", {"첸의 이름을 딴 이 걸작품을 한번 맛보겠소? 스톰스타우트 맥주는 독한 술이오. 내 스승께서 첸의 양조 방법을 배워 내게 전수하셨지. 이 술을 만들려면 당신이 몇 가지를 준비해야 하는데, 그 정도 수고쯤은 할 만한 가치가 충분히 있는 술이오.","사바나 사자 엄니 5개, 초원타조 신장 5개, 그리고 천둥도마뱀의 뿔 1개를 구해다 주시오. 불모의 땅 전역에 거주하는 동물들에게서 이 재료들을 찾을 수 있을 거요."}, {"톱니항에 있는 양조사 드론에게 사바나 사자 엄니 5개, 초원타조 신장 5개와 천둥도마뱀의 뿔 1개를 가져가야 합니다."}},
[822] = {"첸의 빈 맥주통", {"스톰스타우트 맥주 맛 어떻소? 정말 일품이지 않소이까?","첸의 또 다른 양조법도 배운 적이 있는데, 구미가 당기지 않소? 만드는 데 그리 오래 걸리지도 않고 당신의 여행길에 아마 도움이 될 거요.","폭풍도마뱀의 번개 분출선 5개, 거대한 천둥매의 천둥매 타액 분비선 1개, 그리고 저 불모의 땅 코도 간 1개를 가져다주시오.","아까도 말했지만, 정말 훌륭한 맥주가 나올 거요. 내가 전에 얘기했던 그 트로그 에일 맥주라오."}, {"톱니항에 있는 양조사 드론에게 번개 분출선 5개, 천둥매 타액 분비선 1개, 코도 간 1개를 가져가야 합니다."}},
[823] = {"오그닐에게 보고", {"우리 센진 마을에도 나름대로 문제가 있다네. 자네의 도움에 감사하네. 하지만 자네가 가져온 불타는 칼날단 소식은 모두에게 나쁜 소식이 될 거야.","칼바위 언덕에 주둔하는 오그닐 소울스카라는 오크가 있는데 불타는 칼날단과 같은 악의 무리로부터 듀로타를 지키는 임무를 맡고 있네! 오그닐에게 가서 시험의 골짜기에서 온 소식과 함께 우리가 곤경에 처했다고 얘기해주게. 아마 소식을 기다리고 있을 거야.","칼바위 언덕은 북쪽에 있네."}, {"칼바위 언덕에 있는 오그닐 소울스카와 대화해야 합니다."}},
[824] = {"대지 고리회 제네우", {"대지 고리회는 자연의 원소를 연구, 보존하는 데 헌신하는 주술사 단체라네. 그들의 기억에서 사라진 전설과 설화, 정신 나간 고대 신 이야기만 해도 아마 대부분의 학자가 평생 배우는 것보다 많을 걸세.","자네는 운이 좋은 편이군. 내가 이곳 잿빛 골짜기에 사는 대지 고리회 일원을 하나 알고 있으니까 말일세. 조람 해안에 있는 조람가르 전초기지로 가서 제네우 생크리에게 자네가 찾은 걸 보여 주게. 그 구슬의 중요성을 알아낼 자는 제네우밖에 없을 테니까."}, {"검은심연 나락에 있는 제네우 생크리에게 더럽혀진 물구슬을 가져가야 합니다."}},
[825] = {"난파선", {"우리의 유능한 정찰병의 보고에 의하면 프라우드무어 함대의 잔해가 타이라가드 요새 바로 동쪽 듀로타 해안에 남아 있다고 하네.","인간과 동맹을 맺고 있는 노움이라 알려진 작고 비열한 종족이 기계에 대한 뛰어난 지식을 보유하고 있다는 것은 너무나 유명하지. 적을 알고 나를 알면 백전백승이라 하지 않나. 적을 잘 파악해서 그들이 보유한 신기술을 역으로 활용할 수만 있다면 크게 도움이 될 거야.","난파선으로 가서 얼라이언스가 사용하는 도구를 찾아오도록."}, {"칼바위 언덕의 가르톡이 해안에 있는 난파선에서 노움 공구 3개를 찾아 달라고 했습니다."}},
[826] = {"잘라제인", {"의술사 잘라제인은 동쪽의 메아리 섬에 살고 있네. 그 섬들은 바로 우리의 고향이지.","거기에서 그는 육지로 트롤들을 보내 우리 주민들에게 마법을 걸어 노예로 삼기 위해 납치해 가고 있네.","놈을 막아야 하네.","잘라제인과 이젠 그의 앞잡이가 된 검은창 트롤들을 처치하게. 그리고 악의 지배가 끝났음을 확인할 수 있도록 그 증거를 가져오게."}, {"잘라제인을 처치해야 합니다.","부두교 트롤 8명과 사술에 걸린 트롤 8명을 처치해야 합니다.","가드린에게 잘라제인의 머리카락을 가져가야 합니다."}},
[827] = {"해골 바위굴", {"불타는 칼날단은 오그리마 동쪽에 있는 해골 바위굴이라는 동굴에서 출몰하고 있소. 놈들은 그 동굴 안에서 고약한 의식을 거행하며, 이글거리는 목걸이로 자신들의 살을 태우고 있지.","내 생각으론 놈들이 착용한 이글거리는 목걸이가 마력의 열쇠인 것 같소. 하지만 그 이글거리는 목걸이를 수집해서 직접 연구해 보기 전까지는 단정할 수 없겠지.","해골 바위굴로 가서 숭배자들을 처치하고 이글거리는 목걸이를 모아 오시오. 그러면 놈들의 비밀을 밝혀내는 데 큰 도움이 될 거요."}, {"해골 바위굴에 있는 숭배자들을 처치하고 이글거리는 목걸이를 모아야 합니다.","야영지에 있는 마르고즈에게 수집한 목걸이를 가져가야 합니다."}},
[828] = {"마르고즈", {"우리 주술사 마르고즈가 불타는 칼날단의 음모에 대한 새로운 사실을 하나 알고 있네. 그가 오그리마 외곽의 산 속에 있는 해골 바위굴에 대해 알려 주었지. 그곳에는 수많은 불타는 칼날단 이교도들이 숨어 있다고 하네.","해골 바위굴로 가기 전에 마르고즈를 만나 얘기해 보도록 하게. 그는 현명한 자이니까 분명 도움이 될만한 조언을 얻을 수 있을 거야.","지금 북쪽에 있는 모래바람 협곡과 해안가 사이에서 야영을 하고 있네.","그의 조언을 따르게, 하지만 그가 뭐라고 하든 자네가 그 숭배자 놈들을 처치해야 한다는 사실은 변함이 없네!"}, {"마르고즈와 대화해야 합니다."}},
[829] = {"네루 파이어블레이드", {"당신이 가져온 이글거리는 목걸이는 강력한 악마의 도구요. 안타깝게도 이글거리는 목걸이의 비밀을 알아내는 건 주술사로서 내 능력 밖이라오. 흑마법사에게 이글거리는 목걸이를 조사해 달라고 해야 할 것 같소.","이 목걸이를 오그리마에 있는 네루 파이어블레이드에게 가져가시오. 그는 흑마법사로서 상당한 실력을 갖고 있는데, 자신의 힘으로 악마를 상대하겠다고 초자연적인 현상에 대한 연구 결과를 내놓기도 한다는군. 이게 사실이든 아니든 듀로타에서 벌어지는 마력의 의식에 대항하기 위해서는 반드시 그의 도움이 필요하오.","네루는 오그리마에 있는 어둠의 틈에서 찾을 수 있을 거요."}, {"오그리마에 있는 네루 파이어블레이드에게 목걸이 견본을 가져가야 합니다."}},
[830] = {"함장의 명령서", {"낡고 빛 바랜 봉투를 열자 공문서처럼 보이는 문서가 나옵니다. 그 안에는 프라우드무어 함장의 인장이 찍혀 있습니다.","중요한 문서인 것 같아 보입니다. 칼바위 언덕에 있는 사령관 가르톡에게 유용한 정보가 될 것 같습니다."}, {"칼바위 언덕에 있는 가르톡에게 함장 프라우드무어의 명령서를 가져가야 합니다."}},
[831] = {"제독의 명령서", {"인간들은 믿을 수 없어. 그 나약한 족속들이 언젠가 우릴 배신할 걸 알면서도 함께 싸워왔지.","프라우드무어 제독은 죽어서도 속임수를 쓰는군. 비열한 인간들이 그가 죽기 전 그의 계획을 완성했어.","죽은 후에도 그의 영향력은 베네딕트와 함께 살아 있는 것 같네. 그러니 프라우드무어의 병사들이 우리 해안을 당장에라도 공격하지 않으리라고 누가 장담할 수 있겠나?","서둘러 이 명령서를 오그리마에 있는 볼진께 가져가야 하네! 그분은 스랄의 요새에서 찾을 수 있을 걸세."}, {"오그리마에 있는 스랄의 요새로 가서 제독 프라우드무어의 명령서를 볼진에게 전달해야 합니다."}},
[832] = {"이글거리는 그림자", {"<펜던트 모양의 눈에서 목소리가 흘러 나오며...>","가즈우즈?","가즈우즈... 어서 보고해!","네가 해골 바위굴에서 뭔가 발견했단 소릴 들었는데. 각오해야 할 거야!","가즈우즈, 어서 대답해! 아니면 네루 파이어블레이드에게 자네가 여기 있다고 일러 버리겠어. 그럼 아마 그가 자네를 무자비하게 공격할 걸.","네루에게는 함부로 맞서지 않는 게 좋을 거야.","가즈우즈, 듣고 있는 거야?"}, {"이 눈을 네루 파이어블레이드에게 가져가야 합니다."}},
[833] = {"신성한 묘지", {"가장 용감한 타우렌만이 우리의 신성한 묘지인 붉은 바위 언덕에 잠들 수 있네. 썬더 블러프를 건설하고 수호하는데 기여한 위대한 전사들과, 부족 및 지도자들의 더 큰 대의를 위해 자신의 생명을 바친 자들에게 주어지는 대단한 영광이지.","하지만 우리의 신성한 땅에 악한 위험이 드리워지는 것 같네. 뾰족털 약탈자 무리가 이 묘지를 황폐하게 하고 있어. 한창때였다면 모르겠지만, 난 이미 놈들을 몰아내기에는 너무 늙어 버렸네.","반드시 놈들을 처단해야 하네."}, {"붉은 바위 언덕에 있는 뾰족털 약탈자 8명을 처치해야 합니다."}},
[834] = {"사막의 바람", {"나는 가즈로 님의 부하 레즐락이라고 해. 두목께서 오크를 도우라고 이곳 듀로타로 날 보내셨지. 지금까지 모든 일이 순조롭게 풀리고 있었다고. 짐마차를 제외하면 말이야... 짐마차를 안전하게 관리하는 일은 정말 어렵다고! 그것 때문에 좀 골치가 아파.","무사히 도착하리라 믿었던 지난번 짐마차가... 어디 보자, 그게 어디였더라... 칼끝바람 협곡? 아무튼 그곳 먼지바람 하피들 손에 넘어가고 말았어.","보급품을 찾아야 한다고! 거대한 협곡을 따라 남쪽으로 가면 서쪽 암벽 사이로 계곡이 보일 거야."}, {"보급품 부대 5개를 모아야 합니다."}},
[835] = {"보급로 확보", {"가즈로 님이 언젠가 이런 말씀을 하신 적이 있지. \"레즐락, 인생을 살면서 마음에 깊이 새겨 두어야 할 사실이 한 가지 있어. 무슨 일이든 제대로 하려면 직접 해야 한다는 거지.\"","짐마차를 모두 잃는 사태가 생기면 안 되니, 내가 나서서 문제를 해결해야겠어. 하피가 우리 보급로에 들끓고 있다고. 그 하피들을 처치한다면 안전하게 짐마차를 수송할 수 있지 않겠어?","가즈로 님은 이런 말씀도 하셨지. \"돈만 내면 안 되는 일이 없지.\"라고 말이야. 생각 있어, <name>? 좋아... 동쪽 협곡에 있는 놈들부터 시작해 보라고."}, {"듀로타 하피를 종류에 상관없이 12마리 처치해야 합니다."}},
[836] = {"OOX-09/HL 구출 작전", {"노움의 목소리가 다시 로봇에서 흘러나옵니다.","\"긴 활주로로 사용할 수 있을 만한 넓고 안전한 장소로 OOX-09/HL을 옮겨가야겠어요. 은폐 기능이 탑재되어 있지만 다시 작동할 수 있도록 하려면 저도 시간이 좀 필요해요. 로봇을 현재 있는 곳에서, 그러니까, 시야를 가리는 큰 바위가 있는 곳으로 호위해 주세요. 그곳이라면 이륙하기에 충분할 거예요!\"","\"해안까지 무사히 호위해 간 후에 무법항으로 와서 절 찾으세요! 오글소프 오브노티쿠스, 이상!\""}, {"OOX-09/HL을 안전하게 호위하고 무법항에 있는 오글소프 오브노티쿠스에게 가서 보고해야 합니다."}},
[837] = {"영토 침범", {"우리가 이 땅에 도착했을 때 서슬갈기 가시멧돼지들이 대부분을 점령하고 있었지. 우리 입장에서 놈들은 눈엣가시 같은 존재였어. 각고의 노력 끝에 대부분 몰아냈지만, 아직 일부 지역에 살아남은 것들이 완강히 저항하고 있네.","이런 상황이 너무 오래 지속되고 있는 것도 문제고, 또 전략적인 측면으로 봤을 때도 더 이상 서슬갈기 일족이 우리 땅에 남아 있는 걸 용납할 수 없지. 여기에서 서쪽으로 가면 놈들의 야영지가 있네. 가시덤불이 보이는 곳에서 놈들을 찾을 수 있을 걸세. 지금은 듀로타에 있는 놈들만 상대해야 하지만 나중엔 칼림도어 전지역에서 놈들을 섬멸해야 할 걸세."}, {"칼바위 언덕의 가르톡을 위해 서슬갈기 가시멧돼지 4 마리와 서슬갈기 정찰병 4 마리, 서슬갈기 사막길잡이 4 마리, 서슬갈기 전투호위병 4 마리를 처치해야 합니다."}},
[838] = {"스칼로맨스", {"카엘 다로우의 섬에는 스컬지의 요새 역할을 하는 학교, 스칼로맨스가 있다네. 우리의 적들은 그 요새 안에 머무르면서 자신들의 힘을 강화할 연구를 진행하고 있지. 그곳에는 문 하나가 길을 막고 있는데, 아무리 들어가려고 애를 써도 올바른 열쇠가 없으면 들어갈 수 없다네.","스칼로맨스에 대해서라면 연금술사 디더스가 아주 잘 알고 있는데, 어떻게 하면 열쇠를 구할 수 있는지도 알고 있지. 자네는 주어진 임무를 아주 잘 수행하는 자라는 것을 내게 입증했으니 자네에게 이 임무를 맡기기로 함세. 연금술사 디더스와 이 일에 대해 얘기해 보게."}, {"서부 역병지대의 보루에 있는 연금술사 디더스와 대화하십시오."}},
[840] = {"호드의 징병", {"흠, 꽤 강해 보이는 친구로군.","내 둘도 없는 친구인 카르갈이 불모의 땅에서 임무를 수행할 신병을 모집 중이오. 호드를 위해 정의로운 일을 하고 싶지 않소? 지금이 좋은 기회라오.","이 징병 추천서를 카르갈에게 가져가서 그의 서명을 받을 수 있을지 알아보시오."}, {"불모의 땅 전초기지에 있는 카르갈 배틀스카에게 징병 추천서를 전달해야 합니다."}},
[841] = {"추가 동력원 필요?", {"내가 한번 맞혀 보지. 어디... 당신이 하려던 그 무슨 세계 종말적인 사악한 일을 시간 안에 마치지 못한 거 아닌가? 아니지, 그게 아니라, 아마... 모래 속에 떨어뜨려 버렸겠지! 뭐, 이유야 어쨌든 당신에게 팔 여분의 동력원이 있긴 한데, 이번에는 열 개. 그렇지, 사막유랑단 물주머니 10개가 필요하다고!","요즘은 모두 사정이 좋지 않은데다 동력원이 나무에서 자라는 것도 아니라서 말이야. 흠, 하지만 정말로 나무에서 자란다면 어떨까? 아예 동력원 나무를 만들어 버릴까..."}, {"가젯잔에 있는 선임기술자 빌지위즐에게 사막유랑단 물주머니 10개를 가져가 다른 동력원과 맞바꿔야 합니다."}},
[842] = {"크로스로드의 징병", {"내가 서명한 이 징병 추천서를 크로스로드로 가져가도록 하게.","저주스러운 셀그라 다크쏜이 그곳 책임자지. 그 악랄한 주술사는 아마 자네에게 임무를 맡기기 전에 혼을 쏙 빼놓을 거야. 스랄께서는 그녀에게 뭔가 특별한 것이 있다고 믿고 있네만, 글쎄... 난 잘 모르겠어.","뭐, 판단은 자네 몫이니까..."}, {"카르갈의 전초 기지로부터 서쪽 길을 따라가십시오.","T자형 갈림길에서 왼쪽 길을 따라 남쪽으로 가십시오.","크로스로드의 교차로에서 셀그라 다크쏜을 찾아야 합니다."}},
[843] = {"갠의 땅 회복", {"저는 지금 슬픔에 빠져 홀로 이 길을 걷고 있습니다.","바엘 모단의 드워프들이 우리의 땅을 망치고 있습니다. 기계들이 굉음을 내며 언덕을 갈라 버리고 끔찍한 폭발 소리와 함께 깊은 구멍이 곳곳에 나고 있지요. 이 땅은 우리 부족의 옛 고향이자 다양한 자원의 산지였는데 이제 저 드워프들의 파괴에 의해 엉망이 되고 있습니다.","외교적인 노력도 실패하고 말았으니 이제 단호한 행동을 취할 때입니다.","바엘 모단 발굴지에서 드워프 놈들을 몰아내고 그들의 우두머리인 발굴조사단장 카즈고름의 보고서를 가져다주십시오."}, {"갠 스톤스파이어가 바엘던 발굴인부 15명과 바엘던 현장감독 5명을 처치해 달라고 했습니다.","그리고 카즈고름의 보고서를 갠 스톤스파이어에게 가져가야 합니다."}},
[844] = {"초원타조의 위협", {"초원타조에 대해 좀 아나? 쓸모없는 동물들이지. 날지도 못하는 데다가 냄새까지 심하고 말이야. 또 머릿속의 뇌는 내 주먹만 해서 그런지 움직이는 짐마차를 피하기에는 너무 멍청하지. 그놈들의 단단한 머리 덕분에 차축만 더 부러졌다네... 가끔 나는 가시멧돼지놈들이 기습 공격 전에 일부러 초원타조 무리에 겁을 줘서 우리 마차가 다니는 길로 몰아넣는 게 아닌가 하는 생각도 드네.","나를 자랑스럽게 해 주게, <class>. 마차가 지나가는 길을 확보할 수 있도록 이 지저분한 새들을 쓸어버려 주게나. 그리고 증거로 내게 부리를 가져오게."}, {"초원타조 부리 7개를 수집해야 합니다."}},
[845] = {"얼룩말", {"호드의 일원이 되고 싶다면 우선 자네의 능력을 내게 보여줘야 하네. 일단 얼룩말을 사냥하는 것으로 시작하지. 불모의 땅에서 그렇게 강력한 야수는 아니지만, 그놈들이 뒷다리로 발길질할 때 물러나지 않으면 후회할 걸세.","북쪽이나 남쪽으로 가서 얼룩말들을 해치우고 발굽을 가져오게. 그러면 좀 더 강한 놈들을 알려주지."}, {"굶주린 얼룩말을 처치하여 얼룩말 발굽 4개를 모아야 합니다."}},
[846] = {"갠의 복수", {"지금도 저들이 폭약과 시끄러운 기계를 이용해 땅을 파헤치고 있다는 것은 명백한 사실입니다. 곧 새로 부임할 현장감독이 잔인한 파괴를 계속하겠지...","저는 저 괘씸한 드워프 놈들에게도 폭발의 맛을 제대로 한번 보여주고 싶군요. 그러려면 니트로글리세린과 나무 펄프, 질산나트륨을 혼합하여 폭발물을 만들어야 하지요. 요새에 있는 소총병, 병사, 그리고 장교들에게 이 재료가 있다는군요.","이 물건들을 모아 제게 가져오십시오. 그러면 드워프들에게 잊지 못할 선물을 안겨 주겠습니다!"}, {"갠 스톤스파이어가 니트로글리세린 6병, 나무 펄프 6묶음, 질산나트륨 표본 6개를 가져다 달라고 했습니다."}},
[847] = {"랩터의 기민함", {"이... 이 힘은... 이게 토건이 내게 보여 주고 싶어했던 주술사의 힘인가? 정말... 정말 흥미롭군. 토건에게 가서 내가 크게 감동했다고 전해 주게. 물론 지금 당장 이야기일 뿐이지만. 그가 무슨 이야기를 하는지 들어 보겠네."}, {"토건과 대화해야 합니다."}},
[848] = {"버섯 포자", {"불모의 땅에 있는 오아시스엔 신비한 비밀이 숨겨져 있네. 물속에서 생명의 힘이 흘러나와 이 물을 섭취하는 식물과 짐승들에게 생기를 불어넣어 주고 있지.","뿐만 아니라 종 자체를 변화시키는 일도 있네.","북서쪽에 있는 잊혀진 웅덩이 근처에서 자라는 버섯이 있는데, 우리 로데론의 연금술사들은 이 버섯 포자에 매우 유용한 성분이 들어 있는 것으로 보고 있다네.","내게 이 포자를 가져다주면 정말로 고맙겠네."}, {"잊혀진 웅덩이에 있는 버섯에서 버섯 포자 5개를 수집해야 합니다."}},
[849] = {"갠의 복수", {"카즈고름의 보고서를 보니, 폭발을 일으키는 건 별로 어렵지 않겠습니다.","흠... 이제 완성됐군요.","그대에게 특별한 임무를 맡기겠습니다. 이 폭탄을 가지고 가서 바엘 모단에 있는 비행기를 폭파시켜 버리십시오.","그 비행기는 그곳 기반 시설의 핵심일 뿐 아니라 전쟁 무기로도 사용되고 있습니다. 하지만 더욱 중요한 것은 그것이 드워프들의 힘의 상징이자 우리 타우렌의 명예를 더럽힌 기계라는 점이지요.","이 명예로운 임무를 완수해 준다면 그대에게 충분히 보답하겠습니다."}, {"바엘 모단에 있는 비행기를 파괴하고 갠 스톤스파이어에게 가야 합니다."}},
[850] = {"콜카르 지도자", {"켄타우로스는 수년간 타우렌들을 괴롭혀 왔습니다. 최근에는 불모의 땅에 위협이 느껴질 정도로 콜카르 켄타우로스의 수가 늘어났지요. 예전에는 오합지졸처럼 돌아다녔는데 지금은 이들을 하나로 통합한 지도자가 생긴 것 같습니다.","이곳에 있는 우리 세력을 지키려면 켄타우로스 지도자를 없애야 합니다.","잊혀진 웅덩이의 서쪽으로 가서 바라크 코도베인을 처치하고 지도자의 상징인 머리띠를 가져오십시오."}, {"바라크 코도베인을 처치하고 그의 머리띠를 입수해야 합니다."}},
[851] = {"광신자 베로그", {"광신자 베로그를 찾기는 어렵겠지만, 당신이 수집해온 정보로 판단해 보면 그의 동료인 위네스는 항상 이곳 서쪽의 지휘 천막에 있는 것으로 확인됐으니 만약 그녀를 처치한다면 베로그가 달려나올 것입니다.","렉타르 님께서 정찰 목적으로 우리를 이곳으로 보내셨지만, 만약 할 수만 있다면 켄타우로스의 명령 체계를 혼란에 빠뜨릴 기회를 찾아보라고도 하셨습니다. 그러니 베로그를 유인해 처치하십시오!"}, {"위네스를 처치해 광신자 베로그를 유인한 뒤, 베로그를 처치하고 그의 머리띠를 수집해야 합니다."}},
[852] = {"헤즈룰 블러드마크", {"헤즈룰 블러드마크는 불모의 땅에 있는 콜카르 켄타우로스의 지도자라네. 사납고 잔인한데다가 교활하기 이를 데 없는 녀석으로 유명하지. 그를 처치하면 콜카르족을 혼란에 빠뜨려 분열시킬 수 있을 테니까 녀석들이 우리를 위협하는 일도 많이 줄어들 거야.","그래서 말인데, 헤즈룰 블러드마크를 처치하고 증거로 그의 머리띠를 내게 가져다주게.","헤즈룰은 여기서 남쪽에 있는 푸른 오아시스에서 일족을 거느리고 있다네."}, {"헤즈룰 블러드마크를 처치하고 그의 머리띠를 수집해야 합니다."}},
[853] = {nil, {"자네가 준 포자를 유액으로 정제했네. 이제 이걸 우리의 동료인 연금술사 자마에게 보내야 해. 가져다주면 가지고 있는 연금술 물품 중에서 무언가를 줄 거야.","자마는 썬더 블러프의 주술사들이 모여 있는 곳의 아래쪽에 있는 예언의 웅덩이라는 동굴에 있네. 이 동굴은 깊숙이 숨겨져 있지만 정기의 봉우리로 올라가는 길에서는 쉽게 찾을 수 있을 걸세.","그런데 이 유액은 효력이 오래가지 않아. 반드시 그전에 봉인을 풀어 처리해야 하네. 그러니 발길을 재촉해야 할거야."}, {"제한 시간 내에 썬더 블러프에 있는 연금술사 자마에게 정제된 포자를 가져가야 합니다."}},
[854] = {"십자로로의 여정", {"그대가 이곳에 오리란 걸 알고 있었네, 젊은 <class>|1이여;여;. 이젠 더 이상 젊기만 한 숙맥은 아니니 말일세.","이제 그대가 보다 넓은 곳으로 떠나야 할 시간이 온 것 같군. 그대가 지금까지와는 다른 큰일을 할 운명임을 느낄 수 있다네.","그대가 탐험해야 할 땅은 십자로라네. 오크족과 우리의 교류가 이루어지는 곳이자 그들이 우리의 도움을 필요로 하는 곳이지. 길을 따라 동쪽으로 가다가 북쪽으로 방향을 바꾸어 계속해서 가다 보면 십자로에 닿을 것이네. 그곳에서 대지모신께서 축복을 내리신 톨크를 찾아가게."}, {"불모의 땅에 있는 톨크와 대화해야 합니다."}},
[855] = {"켄타우로스의 팔보호구", {"켄타우로스의 지도 체계는 다른 켄타우로스들을 통솔하는 수뇌부 전사들이 주를 이루고 있네. 아주 충직하고 노련한 놈들이지. 놈들의 숫자를 줄인다면 콜카르 일족은 자신들의 포악함을 제어할 힘을 잃게 될걸세. 그래서 현상금이 걸린 이번 임무를 자네에게 맡기려고 하네.","남쪽의 푸른 오아시스 주변에서 콜카르 약탈자와 폭풍술사를 처치해 그들의 팔보호구를 모아오게. 팔보호구를 충분히 구해오면 그에 합당한 보상을 해주겠네."}, {"콜카르 약탈자와 폭풍술사를 처치해 켄타우로스의 팔보호구 10개를 수집해야 합니다."}},
[857] = {"달의 눈물", {"그 힘은 내 것이었어! 바로 내 손 안에 들어와 있어야 한단 말이지. 그걸 되찾아야 해. 되찾아야 한다고!","저 더러운 굴착자들이 그걸 발견했지... 얼마나 아름다웠는지 몰라. 달의 눈물은 바엘 모단에서 우리가 처음으로 이루어낸 위대한 발견이지.","그건 내 것이었어야 했어. 내 것이어야 했다고!","트윈브레이드 장군은 달의 눈물이 지닌 힘을 탐내어 요새 깊숙한 곳에 숨겨 버렸어. 그 저주 받을 자가 그랬다고! 바보 같은!","괘씸한 트윈브레이드가 나를 쫓아내 버렸어. 다른 자들도 그걸 노리고 있어. 그들 역시 막아야 해!","이제 그걸 되찾아야 해. 되찾아야 한다고!"}, {"추방자 피글리가 달의 눈물을 되찾아 달라고 했습니다."}},
[858] = {"시동", {"설마 스퍼터밸브가 보낸 건가? 난 이 안에 꼼짝없이 갇혔다고. 처음엔 이 벌목기를 타고선 좋아라 했지... 시동축을 풀려면 열쇠가 필요하다는 것도 모르고 말이야. 다른 벌목기 조종사가 내게 무슨 문제가 있느냐고 물어봤는데, 그만 당황해서... 그냥 기계에 이상이 좀 있는 것 같다고 말했지 뭐야. 그때 열쇠를 잃어버렸다고 해야 했는데...","빨리 여길 벗어나야 해! 이 건물 관제탑 위층에 있는 관리자 러그위즐이 벌목기 열쇠를 가지고 있을 거야.","여기서 도망갈 수 있게 어서 나 좀 도와달라고!"}, {"관리자 러그위즐에게서 시동 열쇠를 얻어야 합니다."}},
[860] = {"셀그라 다크쏜", {"사냥꾼의 길을 가기 원한다면 그 여정은 불모의 땅으로 이어질 것이오. 그곳에 사는 야수들은 집념이 강하고 아주 사납지. 그곳에서 사냥하면 많은 것을 배우게 될 것이고 그대의 영혼도 성장하게 될 거요.","불모의 땅에서의 첫 번째 안내자가 될 셀그라 다크쏜과 얘기해 보시오.","셀그라는 십자로에 있소이다. 그녀에게 가려면 블러드후프 마을에서 동쪽으로 가서 불모의 땅에 들어서면 길이 갈라지는 곳에서 북쪽으로 가시오. 불모의 땅은 준비되지 않았거나 부주의한 이들에게는 아주 위험한 곳이니까 가는 길에 조심하도록 하시오."}, {"십자로에 있는 셀그라 다크쏜과 대화해야 합니다."}},
[861] = {"사냥꾼의 길", {"당신은 모험에 굶주려 있는 것 같군요. 한눈에 알 수 있습니다. 제게도 당신처럼 모험을 갈망하던 시절이 있었지요...","모험과 사냥 말입니다. 우리 타우렌에게 사냥은 최고의 영예이지요.","진정 사냥꾼의 길을 가고 싶다면 당신의 능력과 결의를 보여주십시오. 멀고어에 사는 굶주린 평원퓨마 발톱을 제게 가져오십시오. 사납고 교활한 짐승이라, 사냥꾼의 길을 가려는 젊은 <class>에게는 더없이 좋은 사냥감이지요."}, {"굶주린 평원퓨마 발톱 4개를 모아야 합니다."}},
[862] = {"들쥐 고기 스튜", {"당신 같은 <class>|1은;는; 이곳 불모의 땅에서 배고픔으로 고생하는 경우가 많지. 나? 나도 하루종일 배가 고프다오.","당신이 좋아할 만한 얘기를 하나 들려줄까? 내 당신을 위해 맛있는 스튜를 만들어 주겠소. 들쥐 몇 마리만 있으면 되지.","그런데 아, 글쎄, 바엘 모단의 그 추악한 드워프 놈들이 모두의 일용할 양식인 들쥐를 죄다 먹어치우고 있는 모양이오. 자, 남쪽의 바엘 모단으로 가서 들쥐를 잡아 그 고기를 가져다주시오.","들쥐 사냥을 해보지 않은 자는 감히 호드의 노련한 모험가라고 할 수 없지, 암!"}, {"십자로 동쪽에 있는 그럽에게 들쥐 고기 8개를 가져가야 합니다."}},
[863] = {"탈출", {"톱니항으로 가는 동안 사용법을 죄다 알아낼 수 있을 거야... 뭐, 그리 어렵진 않을 것 같은데... 여기에 단추 몇 개랑 지렛대 한두 개를... 흠, 어쨌든 떠날 준비는 다 된 거지?"}, {"진흙 늪을 빠져나가는 동안 위즐크랭크와 훔쳐낸 고블린 벌목기를 보호해야 합니다."}},
[864] = {"연금술사 진게에게 돌아가기", {"\"자, 됐소. 동력원은 안전하게 제거됐고 안에 든 자료는 그대로 남아 있소. 그럼, 잘 가라고. 그 사악한 과학 실험이 잘 풀렸다면 좋겠군.\"","빌지위즐이 등을 돌리고 가젯잔 수자원개발회사의 일에 집중합니다. 봉인된 분석 도구를 잘 챙겨서 언더시티로 되돌아갈 준비를 합니다. 그곳에서 연금술사 진게가 당신이 봉인된 분석 도구를 가져오기를 기다리고 있습니다."}, {"언더시티에 있는 연금술사 진게에게 봉인된 분석 도구를 가져가야 합니다."}},
[865] = {"랩터 뿔", {"이곳 불모의 땅 랩터들은 다른 곳에 사는 랩터보다 특출나게 영리하지. 놈들의 그 영리함은 전부 그 뿔에서 나오는 게 틀림없다고. 정말 그렇다면 그 랩터의 뿔을 갈아 가루로 만들어 포도주에 타면 영리해지는 음료수가 만들어질지도 몰라. 그걸 팔면 큰돈을 벌 수 있을 테고 말이지!","바로 이 부분에서 자네 도움이 필요해. 이 주변에서 가장 똘똘한 랩터의 뿔이 필요하거든. 톱니항 북쪽 절벽 위에 있는 왕 죽음발톱이라는 랩터는 나머지 영리한 랩터 무리를 거느리고 있어. 그 녀석의 뿔을 내게 가져다줘! 그런데 저번에 보냈던 다른 친구들처럼 걸레 조각이 돼버리면 안 돼!"}, {"왕 죽음발톱의 뿔을 획득해야 합니다."}},
[866] = {"뿌리 표본", {"이곳 불모의 땅의 향초는 다른 땅에 것과는 달리 좀 독특하지. 대부분의 특성이 비슷하긴 하지만... 이곳에서 자라는 향초는 조금 달라. 오아시스의 물이 이곳 식물들을 약간 변질시켜서 그런 것 같아. 아주 약간이긴 하지만...","만약 그 내용물을 추출할 수 있다면 그 차이에 대해 연구해 보고 싶은데.","약초채집에 자신이 있다면 불모의 땅에서 발견되는 향초의 뿌리 표본을 좀 모아 줘. 표본을 가져다주면 다른 지역에서 나는 향초와 비교해 볼 수 있을 테니까."}, {"불모의 땅 북부 주변에서 뿌리 표본 8개를 수집해야 합니다."}},
[867] = {"하피의 습격", {"<class> 친구, 여기 온 김에 조그만 문제 하나를 처리하는 것 좀 도와주면 좋겠군. 빌어먹을 하피들이 아주 오래전부터 우리의 짐마차 보급품을 습격해 왔다네. 이제 그 마녀 같은 것들의 숨통을 조여 없애버려야 할 때일세.","일단 하급 지휘관들을 먼저 처치한 후에 상급 지휘관들을 해치우는 게 가장 효과적인 공격이 될 것이네.","북서쪽에 있는 칼날바람 하피를 사냥해서 갈퀴발톱을 6개 가져오도록 하게나. 그 정도면 우리 일의 시작치곤 괜찮을 걸세."}, {"칼날바람 하피를 처치해 칼날바람 하피의 갈퀴발톱 6개를 모아야 합니다."}},
[868] = {"알 사냥", {"난 이 땅에서 일어나고 있는 일을 조사해서 상사에게 보고해야 하는 임무를 띠고 오그리마에서 이곳 십자로로 파견되었소.","그 임무 중 하나는 거인의 들판에 출몰하는 곤충같이 생긴 어떤 생물을 조사하는 것이오. 그런데 정보가 거의 없단 말이지... 그 어떤 생물보다도 높은 지능을 지닌 듯하다는 것밖에... 앞으로 더 자세히 조사해봐야 하오.","이 발굴용 갈퀴를 가지고 놈들이 살고 있는 언덕에서 알을 좀 모아 주시오. 놈들에게 발각되면 공격을 받을 수 있으니 들키지 않도록 조심하시오."}, {"십자로에 있는 코란에게 실리시드의 알 12개와 발굴용 갈퀴를 가져가야 합니다."}},
[869] = {"도둑 추적", {"잔해 주변에 어마어마한 크기의 휘어진 발톱으로 찍힌 발자국이 가득합니다. 아무래도 랩터의 발자국 같습니다.","직감적으로 생각해 보았을 때 랩터를 탄 자들이 짐마차를 습격했을 것 같지만 그렇게 생각하기엔 랩터 발자국이 깊지 않고, 랩터가 아닌 것들의 발자국도 보이지 않습니다.","발자국은 이곳에서 북동쪽으로 가시덩굴 언덕의 동쪽까지 나 있습니다. 이 수수께끼의 답은 아마 그곳에 있을 것입니다."}, {"발자국이 가시덩굴 언덕의 동쪽으로 이어져 있습니다. 그 발자국을 따라가서 무엇을 찾을 수 있는지 알아내야 합니다."}},
[870] = {"잊혀진 웅덩이", {"땅이 흔들리는 것을 느꼈소? 무언가가 이 땅을 둘로 갈라놓았소. 그렇지만 우리 드루이드는 또 다른 흔들림도 느꼈다오.","예전의 메말랐던 죽음의 땅에서 생명이 솟구치고 있소. 십자로 주변의 오아시스가 갑자기 활발하게 활동하는 것이라오. 우리 드루이드들은 어떠한 힘이 대지로부터 솟아나오는 것을 어렴풋이 느낄 수 있었지만, 그 근원이 어디인지 모르겠소. 불모의 땅을 산산조각낸 재앙과 관련이 있는지도 모르겠군.","이 일의 원인을 알아내야만 하오. 십자로의 북서쪽에 있는 잊혀진 웅덩이로 가시오. 그곳 물속에서 힘의 근원을 찾아낸 후 이곳으로 돌아오시오."}, {"십자로 북서쪽에 있는 잊혀진 웅덩이 바닥을 수색해야 합니다."}},
[871] = {"서슬갈기 방어 작전", {"<name>, 모든 가시멧돼지는 우리의 적이네. 특히 그중 몇 녀석이 큰 골칫거리가 되고 있지.","요즘 서슬갈기 일족이 오그리마에서 이곳으로 지나가는 우리 보급 마차를 계속 공격하고 있다네. 현재 정찰병을 보내 놈들의 지휘관이 누구인지 조사하는 중인데, 그동안 자네가 이 성가신 멧돼지 놈들을 손봐준다면 우리에게 많은 도움이 될 거야.","먼저 그놈들이 강 근처에서 우리 야영지를 공격했던 곳인 이곳 남쪽에서부터 놈들을 찾아 처치해주기 바라네."}, {"서슬갈기 약탈자 8마리, 서슬갈기 사냥꾼 3마리를 처치해야 합니다."}},
[872] = {"서슬갈기 공격 작전", {"서슬갈기 멧돼지 놈들이 듀로타 경계 지역에서 미쳐 날뛰고 있단 말이군? 이 경비초소에서는 절대로 그런 일이 생기게 할 순 없지! 옛말에 최선의 공격은 최선의 방어라는 말이 있네.","우리 정찰병 중 한 명이 짐마차를 마지막으로 습격했던 멧돼지를 추적했으니 어디를 공격해야 하는지는 확실하다네. 가시멧돼지 놈들 중 크리니그 스날스나웃이라는 녀석이 강을 따라 벌어지는 공격을 지휘하고 있네. 그 녀석을 처치해 주게. 놈들을 혼쭐내 다시는 이곳을 공격할 엄두도 내지 못하게 해 달란 말이네.","마구간 남쪽의 강을 따라 이동해 놈들이 들끓는 동굴을 찾아보게나."}, {"서슬갈기 가시마술사 5명과 서슬갈기 파수병 5명, 크리니그 스날스나웃을 처치해야 합니다. 그 후 증거로 크리니그 스날스나웃의 엄니를 가져가야 합니다."}},
[873] = {"이샤 아와크", {"이 해안의 지배자인 거대한 이샤 아와크는 자존심이 강하고 엄청난 힘을 자랑하지요. 그동안 놈은 해변에 살고 있는 많은 인간들을 해쳤기 때문에 모두 두려워하고 있죠.","하지만 난 놈이 두렵지 않아요. 오히려 놈이 이곳에 있다는 사실에 감사하고 있죠. 도전할 만한 가치가 있는 훌륭한 사냥감이니까요.","자, 준비가 됐다면 저 바다를 헤엄쳐 가서 이샤 아와크를 찾도록 하세요.","놈의 영혼이 담긴 심장이 뛰는 소리를 들으면 당신의 운명을 알 수 있을 거예요."}, {"마렌 스카이시어에게 이샤 아와크의 심장을 가져가야 합니다."}},
[874] = {"마렌 스카이시어", {"지금껏 불모의 땅의 짐승들을 사냥한 것을 보면 자네는 기백이 상당한 친구일세. 하지만 진정한 <race>|1은;는; 항상 어떠한 곳에서도 사냥감을 쫓을 수 있는 준비가 되어 있어야 하네. 험한 산이라도, 깊은 늪도 문제가 되어서는 안 되지. 심지어 바다까지도 쫓아가야 해.","내 누이인 마렌을 찾아가게. 그녀는 해안을 따라 물에 사는 짐승들을 사냥하고 있지. 누이가 녀석들을 어떻게 사냥하는지 방법을 알려줄 거야.","그런데 누이를 찾아가는 동안 조심해야 할 거야. 불모의 땅의 해안은 인간들이 장악하고 있으니까 말이야."}, {"마렌 스카이시어와 대화해야 합니다."}},
[875] = {"하피 부대장", {"하피의 공격을 멈추려면 놈들의 명령 체계를 파괴해버리는 수밖에 없네. 두려움을 모르는 놈들의 부대장인 칼날바람 암살자가 하피를 지휘하고 있네. 부대장을 처치해버리면 나머지도 따라서 목숨을 끊을 걸세.","메마른 언덕으로 가서 칼날바람 암살자를 처치해 버리게. 아주 고통스러운 죽음을 선사해줘야 한다는 걸 잊지 말게나. 호드에게 시비를 거는 게 얼마나 멍청한 짓인지 알게 해줘야 하니까."}, {"북부 불모의 땅의 메마른 언덕에서 칼날바람 암살자 6마리를 처치해야 합니다."}},
[876] = {"붉은깃털 세레나", {"처치한 하피들이 그들의 대장인 붉은깃털 세레나에 대한 이야기했습니다. 다른 하피들이 호드에게 극도의 적대감을 드러내는 것으로 보아 세레나의 원한도 깊을 것입니다. 당신은 붉은깃털 세레나를 쫓아 이 문제를 완전히 해결하기로 마음먹습니다."}, {"붉은깃털 세레나를 처치하고 그 증거로 그녀의 머리카락을 수집해야 합니다."}},
[877] = {"죽은 오아시스", {"남쪽에서 일어났던 일들이 이곳에서도 일어날 수 있는 건가? 그대가 잊혀진 웅덩이에서 발견한 것으로 판단해 보았을 때 이 오아시스가 아무래도 단서인 것 같군. 이제 실험을 시작해봐야겠소.","자, 이곳 정상에서 가져온 씨앗을 받으시오. 말라 비틀어져 이미 죽어버린 씨앗이지... 이걸 우리 아래에 있는 죽은 오아시스로 가지고 가보시오. 그곳에도 균열이 있다면 씨앗을 그 안에 심고 어떻게 되는지 관찰한 뒤에 발견한 사항을 십자로에 있는 통가에게 보고하시오."}, {"말라버린 씨앗을 사용해 죽은 오아시스에 있는 균열을 조사해야 합니다."}},
[878] = {"부족 간의 전쟁", {"적을 처치해주게. <킁> 이 뻐드렁니와 서슬갈기 일족을 도와 뾰족털 일족을 공격해줘. 두 부족 모두 신앙심이 아주 깊어서 땅의 힘을 부릴 수 있지. <킁킁> 땅은 언제나 우리에게 큰 힘을 주지. 특히 전쟁에서!","이 뻐드렁니를 도와 주면 톨크와 <킁> 호드의 고민이 뭔지 알 수 있을걸! 북쪽으로 가서 뾰족털 일족의 정신적 지도자들을 처치해. 그때 우리 서슬갈기 일족은 이 불모의 땅을 지배할 수 있네."}, {"뾰족털 일족의 풍수사 6명, 가시마술사 12명, 흙점쟁이 12명을 처치해야 합니다. 임무를 완수한 후 불모의 땅, 타우라조 야영지에 있는 뻐드렁니에게 돌아가십시오."}},
[879] = {"내부의 배반자", {"일족 세 명이 감옥에서 탈출해 약탈을 일삼고 있어. <킁킁> 잔인하고 교활하기 짝이 없는 녀석들... 처치할 수만 있다면 십자로뿐만 아니라 불모의 땅 전체가 평화로워질 거야... <킁> 최소한 우리 서슬갈기 일족이라도 말이지. <킁킁>","자네가 처리해야 할 놈들은 나크와 쿠즈, 로크 일행이야. 불모의 땅에서 남쪽으로 멀리 떨어진 곳에서 찾을 수 있을 거야. 한 놈은 마법사이고 다른 놈은 사냥꾼, 그리고 우두머리 로크는, <킁킁> 전사야. <race>, 그들에게 쓴맛을 보여주게. 마지막 눈을 감으며 우리 일족에게 저지른 끔찍한 짓을 후회하도록..."}, {"나크와 쿠즈, 로크를 처단하여 그들의 해골을 불모의 땅의 타우라조 야영지에 있는 뻐드렁니에게 가져가야 합니다."}},
[880] = {"변화된 생물", {"오아시스와 당신이 발견한 이상한 힘에 의해 영향을 받은 식물뿐 아니라 동물을 연구하는 것도 아주 중요한 일이 될 것이오. 우리 아래에 있는 죽은 오아시스는 무쇠턱거북 종의 발원지라오. 최근에는 그 개체 수가 폭발적으로 증가했소. 게다가 이곳에 올라오기 전 고통스러운 경험을 통해 얻은 교훈에 의하면, 예전에는 온순했던 거북이들이 점점 공격적으로 변하고 있소.","<붕대를 감은 손을 들어보이는 제릭>","아무래도 오아시스의 영향으로 어떤 변화가 일어난 것 같소. 연구가 더 필요할 것 같으니 무쇠턱거북의 등껍질을 수집해서 십자로에 있는 통가에게 가져가시오."}, {"죽은 오아시스에서 변형된 무쇠턱거북 등껍질 6개를 수집해야 합니다."}},
[881] = {"에체야키", {"하얀 안개 또는 타우렌어로 '에체야키'라고 불리는 놈은 사바나 사자 무리의 왕이라네. 먹잇감에 몰래 접근해 사냥하는 모습이 마치 대지 위에 옅게 깔린 하얀 안개와도 같아서 그런 이름이 붙었지. 녀석은 먹잇감이 두려움이나 고통을 느낄 새도 없이 단숨에 처치해 버린다네.","이곳 타우렌들은 에체야키가 자비이자 죽음 그 자체라고도 하지.","이제 에체야키를 사냥하러 가는 길을 알려 주겠네. 녀석의 소굴은 이곳의 북서쪽에 있는 작은 산의 북쪽 면, 커다란 코도의 뼈 근처에 있네.","어서 떠나도록 하게. 그 소굴에 가까이 가서 이 뿔피리를 불면 녀석이 나타날 것이네."}, {"에체야키의 소굴에서 뿔피리를 불어 에체야키를 불러내 처치 또는 제압해야 합니다."}},
[882] = {"이샤무헤일", {"강철이빨 이샤무헤일은 불모의 땅에서 가장 사나운 해비늘랩터일세. 놈이 사냥을 하는 이유는 먹기 위한 것도 재미를 위해서도 아니지. 순수한 사냥에 대한 열정 때문이야. 그것이 놈의 본성이기 때문에 다른 생물들을 죽이는 거야.","이샤무헤일을 쫓다 보면 자네도 놈의 본성을 알게 될 걸세.","어서 사냥을 떠나게. 그가 가장 좋아하는 먹이인 얼룩말을 처치하고, 그 시체를 톱니항 북서쪽에 있는 고목으로 가져가게. 이샤무헤일은 시체의 냄새를 맡고 나타날 거야.","그가 나타나면 마음의 준비를 하도록 하게."}, {"타우라조 야영지에 있는 조른 스카이시어에게 이샤무헤일의 송곳니를 가져가야 합니다."}},
[883] = {"라코타마니", {"라코타마니의 발굽은 위대한 코도의 힘을 상징합니다."}, {"타우라조 야영지에 있는 조른 스카이시어에게 라코타마니의 발굽을 가져가야 합니다."}},
[884] = {"오와탄카", {"오와탄카의 꼬리가시에 손을 대자 불꽃이 일어납니다."}, {"타우라조 야영지에 있는 조른 스카이시어에게 오와탄카의 꼬리가시를 가져가야 합니다."}},
[885] = {"워시트 포우니", {"워시트 포우니의 깃털에 손을 대자 고통이 느껴집니다."}, {"타우라조 야영지에 있는 조른 스카이시어에게 워시트 포우니의 깃털을 가져가야 합니다."}},
[886] = {"불모의 땅 오아시스", {"썬더 블러프의 드루이드들이 멀고어 동쪽, 불모의 땅으로 흘러들어오는 이상한 기운을 느끼고 있네.","그 힘의 근원을 찾기 위해 통가 룬토템을 보냈지만 그에게 도움이 필요한 것 같네.","불모의 땅에 있는 통가를 찾아가게. 그는 아마 십자로에 있을 걸세. 그곳으로 가려면 블러드후프 마을에서 바깥으로 이어지는 동쪽 길을 따라가다가, 불모의 땅에 들어선 후 갈림길이 나오면 북쪽 길로 계속 가게. 그러다 보면 십자로에 다다르게 될 거네.","불모의 땅은 험난한 지역이니 길을 벗어나지 않도록 조심해야 하네."}, {"십자로에 있는 통가 룬토템과 대화해야 합니다."}},
[887] = {"남쪽바다 해적단", {"가즈로 님께서 톱니항의 해적 문제를 해결하도록 도와준다면 승진시켜 주시겠다고 약속하셨어. 문제는 이놈들이 이곳 남쪽에 있는 격투의 섬에 정박해 그 섬을 점령해버렸다는 거야. 해적 놈들은 단순히 물건을 훔치려는 게 아니야! 우리에게 복수하려고 한다고! 심지어 어느 날 밤에는 우리 전투용 범선을 공격하기도 했다니까.","이 상황을 해결하기 위한 계획이 따로 있기는 하지만, 가즈로 님께서는 성격이 워낙 급하셔서 말이야. 자네가 격투의 섬으로 가서 그놈들을 좀 처치해주지 않겠어?"}, {"격투의 섬에서 남쪽바다 자객 또는 남쪽바다 선원 8명을 처치해야 합니다."}},
[888] = {nil, {"내 물건을 모두 다 되찾고 싶지만 불가능하다는 걸 잘 알고 있네. 그래도 몇 가지 물건은 반드시 찾아야만 해!","보다시피 내 관측소는 이제 가동할 준비가 거의 다 되었는데, 작은 렌즈 두 개가 더 필요하네. 하나는 듀로타에서 온 짐마차에 실려 도착했지만 다른 하나는 배에 실려 오기로 했는데 도착할 때가 훨씬 지났지. 그리고 무법항의 드리즐릭이 보낸 장화가 든 뱃짐도 아직 못 받았어.","해적 야영지로 가서 내 물건들을 좀 찾아봐 주게."}, {"톱니항에 있는 가즈로를 위해 장화가 든 뱃짐과 망원경 렌즈를 회수해야 합니다."}},
[889] = {"바람의 정기", nil, nil},
[890] = {"사라진 뱃짐", {"무법항에서 보낸 마지막 뱃짐을 목이 빠져라 기다리고 있는데 아직도 안 왔어! 해적이 중간에서 가로챈 것이 분명해. 하지만 혹시 내가 모르니, 부두로 내려가 물건이 창고로 들어간 건 아닌지 디지위그에게 물어봐 주게.","여기 내 장부를 줄 테니, 디지위그에게 보여주고 내 재고 기록과 그의 기록표를 대조해 보라고 전해 줘."}, {"부두주임 디지위그에게 가즈로의 장부를 가져가야 합니다."}},
[891] = {"선장의 복수", {"지금 우리 함대의 배 한 척이 테라모어에서 온 저 미치광이 인간들에게 또 나포되었습니다!","놈들의 신임 부제독은 이곳 해안에 잠시 머무르고 있는데, 이자는 기분 내키는 대로 무고한 배들을 나포하거나 공격하는군요.","무역 해안을 따라 남쪽에 있는 부제독의 야영지로 가서 그의 부하들을 처치하고 증거로 그놈들의 휘장을 가지고 오십시오. 고위 간부인 부관 파이어와 벅랜드도 함께 처치해 주시면 좋겠군요. 놈들이 제 함대를 파괴했던 것과 동등한 복수를 해주십시오!"}, {"테라모어 해병과 갑판원을 처치해 테라모어 휘장 10개를 모으고 부관 파이어와 벅랜드를 처치해야 합니다."}},
[892] = {"사라진 뱃짐", {"내 기록표를 꼼꼼히 확인해 봤는데 실수로 다른 곳에 보낸 짐은 없네. 모두 정확해! 가즈로에게 가서 안됐지만 물건이 중간에 사라진 것 같다고 전하게. 해적들이 물건을 가로채는 일이 종종 있거든.","나도 더 이상 어떻게 해 줄 수가 없군. 참... 지하광산에서 그 앞으로 온 물건이 있으니까 짬이 날 때 여기로 와서 가져가라고 전해 줘."}, {"톱니항에 있는 가즈로에게 가즈로의 장부를 돌려주어야 합니다."}},
[893] = {"최고의 무기", {"거인의 들판 너머 남쪽 지역에 사는 서슬갈기 가시멧돼지 중에는 솜씨 좋은 대장장이가 없는 것으로 아는데, 최근 들어 꽤 괜찮은 무기들을 생산하기 시작했단 말이야. 가능하다면 그들의 무기를 몇 가지 살펴보고 그 기술을 배웠으면 좋겠소.","호드에 충성을 바치고 싶다면 그들의 무기를 몇 개 구하여 내게 가져오시오. 서슬갈기 정찰꾼에게서 단검을, 점쟁이에게서 마법봉을, 그리고 광전사에게서는 방패를 구해오시오. 뭐, 그 정도면 충분할 것 같소!"}, {"불모의 땅, 타우라조 야영지에 있는 테터낵 스틸포지에게 서슬갈기 기습용 단검, 불타는 마법봉, 전투용 방패를 가져가야 합니다."}},
[894] = {"사모플랜지", {"투자개발회사는 여기서 서쪽에, 그러니까 진흙 늪에 있는 기름 발굴현장 바로 옆에 작은 연구 시설을 가지고 있어. 정확히 어떤 일을 하고 있는지는 잘 모르겠지만 사모플랜지라는 걸 가지고 실험을 하고 있다는 것만 알아냈지.","그런데 대체 사모플랜지라는 게 뭔지 모르겠단 말이야. 누군가 구해다 주기만 해준다면 한번 조사해 보고 싶은데...","제어장치 조작설명서를 한 부 구했으니까 자네가 한번 읽어보면 사모플랜지를 어떻게 분리해내야 하는지 알 수 있을 거야."}, {"투자개발회사 연구 시설의 제어장치에 가야 합니다."}},
[895] = {"가비 선장 현상 수배", {"현상 수배!","여신의 단검호의 선장, 가비","공해 상에서의 해적 행위, 금전 갈취 및 납치, 방화, 노예 학대 등 기타 혐의로 수배함. 최근 이 지역 바다에 도착해 남쪽바다 해적단을 이끌고 있으며, 격투의 섬을 공격하던 것이 마지막으로 목격되었음. 선장 가비를 처치했음을 알 수 있는 증거를 가져올 것!","-가즈로"}, {"가비 선장의 머리카락을 톱니항에 있는 가즈로에게 가져가야 합니다."}},
[896] = {"광부의 행운", {"톱니항처럼 분주한 항구의 부두주임을 맡다 보니 항상 난 정보의 흐름을 죄다 파악하고 있지. 나는 이곳과 무법항 간의 모든 상품 거래와 돈의 흐름에 정통하다네.","최근 수집한 정보에 의하면 진흙늪 북동쪽에 있는 투자개발회사의 돌무더기 광산에서 한 광부가 자네의 주먹 크기만한 에메랄드를 발견했다더군. 내가 그런 물건에 관심을 보일 만한 구매자를 좀 알고 있는데... 자네가 그 에메랄드를 찾아오면 그걸 팔아 이익을 50 대 50으로 나누어 가지는게 어떻겠나?"}, {"톱니항에 있는 부두주임 디지위그가 투자개발회사 감독이나 투자개발회사 집행자로부터 고양이눈 에메랄드를 가져다 달라고 부탁했습니다."}},
[897] = {"여왕 실리시드", {"벌레같이 생긴 이 이상한 생명체는 자연계의 생물이 아닌 것으로 보이며 불모의 땅의 생태계에 속하지 않습니다."}, {"조른 스카이시어에게 여왕 실리시드의 머리를 가져다주어야 합니다."}},
[898] = {"요새 탈출", {"아! 드디어 날 구하러 왔군!","저 수많은 경비병들을 따돌릴 수 있는 자는 없을 줄 알았소. 테라모어에서 온 저 광신도들은 정말 미치광이들이오. 우리 배를 침몰시키고 유일한 생존자인 나를 이곳에 가뒀지. 나를 얼라이언스를 위협하는 적으로 취급하면서 말이오.","톱니항과 무법항 간에 밀주를 실어 나르는 배의 선원일 뿐인 내가 얼라이언스에 위협이 된다고? 말도 안 되는 소리...","쓸데 없는 말을 너무 많이 한 것 같군. 톱니항으로 돌아가도록 도와주시오. 준비가 되면 함께 빠져나갑시다."}, {"길사레즈 파이어바우를 톱니항에 있는 브라이트썬 선장에게 데려가야 합니다."}},
[899] = {"타오르는 증오", {"그들은 내 아내를 죽였고, 집과 땅을 불태웠네. 그 비극의 밤 이후로 나는 아침부터 해가 수평선에 닿을 때까지 이곳을 돌아다니며 놈들을 찾아 헤매고 있지. 지금까지 서슬갈기 일족들을 만나는 족족 처치했지만, 그래도 성이 차지 않는군.","자네가 나를 좀 도와주게.","놈들을 최대한 많이 처치해주게. 그리고 그 엄니를 가지고 돌아와 나와 함께 놈들의 멸망을 기뻐하세나."}, {"가시멧돼지 엄니 30개를 수집해야 합니다."}},
[900] = {"사모플랜지", {"현재 세 개의 제어 밸브가 열려 있습니다. 제어판 눈금 여러 개를 살펴본 결과 제어 밸브를 모두 잠가야만 기계를 끌 수 있을 것 같습니다."}, {"연료 제어 밸브, 압력 조절 밸브, 주제어 밸브를 잠근 다음 제어장치를 다시 작동시켜야 합니다."}},
[901] = {"사모플랜지", {"기계가 완전히 꺼졌으므로 제어판을 연 다음 사모플랜지를 꺼낼 수 있습니다. 이제 다른 것은 필요 없고 제어판을 열 수 있는 열쇠만 구하면 됩니다."}, {"땜장이 스니글즈에게서 제어장치 열쇠를 구해야 합니다."}},
[902] = {"사모플랜지", {"제어장치의 덮개를 열자 전선과 이상한 관, 기타 기계 부품들이 복잡하게 얽혀 있습니다. 하지만 대부분 실제로는 별다른 용도가 없는 것 같습니다. 부품들을 헤집고 제어장치의 안으로 팔을 깊숙이 넣자 사모플랜지가 있습니다. 당신을 그것을 세게 잡아당겨 고정 장치에서 빼냅니다."}, {"사모플랜지를 북부 불모의 땅의 노즐팟의 전초기지에 있는 스퍼터밸브에게 가져가야 합니다."}},
[903] = {"암사자 사냥", {"정말로 자네의 용기를 과시하고 싶다면 진짜 무서운 놈들을 알려주지. 불모의 땅을 어슬렁거리는 사바나 암사자들이 좋겠군. 교활하면서도 강인하고, 지능도 높은데다가 빠르고 치명적인 공격을 자랑하는 놈들이지. 최근 마을 외곽에서 이놈들이 우리 정찰대를 괴롭히고 있네. 특히 북동쪽 지역에서 말이야.","사냥하러 나가서 자칫 방심하면 그놈들의 먹이가 될 수도 있으니까 놈들을 상대할 때는 조심하도록 하게나."}, {"사바나 암사자를 사냥해 암사자 발톱 5개를 수집해야 합니다."}},
[905] = {"랩터 둥지로", {"<name>, 사냥이 점점 흥미로워지고 있군. 이번엔 사냥감의 소굴로 찾아 들어가 처치해야 한다네.","남쪽으로 가는 길 바로 밖의 언덕 사이에 랩터들이 만들어 놓은 소굴이 있네. 그곳에 있는 해비늘랩터들을 처치한 뒤 그들의 깃털을 뽑게나. 그런 다음 깃털을 죽은 오아시스 근처에 있는 해비늘랩터 둥지에 올려놓아 랩터 무리에게 자네가 놈들을 무서워하지 않는다는 걸 보여주게!","조심해야 하네. 랩터들은 무리를 지어서 사냥하기 때문에 주의하지 않는다면 놈들에게 둘러싸이게 될 거야."}, {"해비늘랩터를 처치해 깃털을 모은 후 해비늘랩터 둥지 3곳에 깃털을 올려놓아야 합니다."}},
[906] = {"내부의 배반자", {"<race>, 로크의 해골을 크로스로드에 있는 톨크에게 가지고 가게. <킁킁> 그러면 아무 말 안 해도, 우리 일족에게 어떤 일이 일어났는지 대강 알아차릴 거야. 뻐드렁니가 이런 일을 했다는 건 믿지 않겠지만... <킁> 자네가 이렇게 큰 일을 도운 걸 알면 보답을 할 거야. <킁킁>","뻐드렁니가 이 안에서 살아 있는 한 아감마간님께서 자네에게 능력을 주시길 기도하겠네. 다시 만날 때까지 안녕..."}, {"불모의 땅, 크로스로드에 있는 톨크에게 로크의 해골을 가져가야 합니다."}},
[907] = {"분노한 천둥도마뱀", {"자, 이젠 천둥도마뱀을 상대할 차례야. 몸집이 하도 커서 놈들이 평원을 한발한발 걸을 때면 지축이 흔들리는 소리가 나곤 하지. 놈들을 상대할 땐 조심해야 하네. 한 발짝이라도 잘못 디뎠다간 밟혀버리는 수가 있거든.","천둥도마뱀들은 불모의 땅 남쪽에 있다네. 폭풍주둥이도마뱀, 폭풍도마뱀, 그리고 천둥뿔도마뱀들이 모두 천둥도마뱀과라고 알려져 있네. 놈들을 처치하고 내게 돌아오게."}, {"천둥도마뱀 피 3개를 모아 타우라조 야영지에 있는 조른 스카이시어에게 가져가야 합니다."}},
[908] = {"폐허 사이로", {"황혼의 망치단은 검은심연 나락에 있는 달의 제단 폐허로 이동했소. 그들로 인해 그곳의 정령들도 우리에게 반기를 들고 있소. 아무것도 하지 않으면 이 지역이 영원히 그들의 손아귀에 들어가게 될 것이오.","검은심연에서 심연의 돌을 찾으시오. 아마 물 가까이에 있을 것이오. 제대로 읽어내기가 쉽진 않겠지만 심연의 돌에는 모든 정령들의 역사와 관련된 심연의 핵이 들어 있소. 내게 그걸 가져오면 대지 고리회의 힘을 빌려 황혼의 망치단을 저지할 수 있을게요."}, {"심연의 핵을 잿빛 골짜기의 조람가르 전초기지에 있는 제네우 생크리에게 가져가야 합니다."}},
[909] = {nil, nil, nil},
[910] = {nil, nil, nil},
[911] = {nil, nil, nil},
[913] = {"천둥매의 울음 소리", {"천둥매는 위험한 맹금들이지. 이제 자네가 그들을 상대할 때가 됐군. 그들이 주로 다니는 곳을 찾아가 처치한 다음 내게 사냥의 증거를 가져오게.","내가 부탁하는 마지막 사냥이 될 테니 잘해주게나."}, {"천둥매를 찾아 처치하고 천둥매 날개를 타우라조 야영지에 있는 조른 스카이시어에게 가져가야 합니다."}},
[914] = {"송곳니의 드루이드 우두머리", {"통곡의 동굴에 있는 송곳니의 드루이드들은 미쳐버렸습니다. 처음에는 불모의 땅을 치유하려던 드루이드 귀족이었지만, 지금은 자신들의 비뚤어진 야망을 채우기 위해 이 땅의 판을 새로 짜려하고 있습니다.","송곳니의 드루이드들은 4명의 지도자가 이끌고 있으며, 각기 꿈의 보석을 지니고 있습니다. 꿈에서 본 그들의 얼굴이 지금도 생생합니다! 그들을 처치하고 꿈의 보석을 제게 가져오면 불모의 땅에 다시금 평화를 가져올 수 있을 겁니다.","통곡의 동굴 깊은 곳으로 가세요. 그들을 발견할 수 있을 겁니다. 어서 떠나세요."}, {"코브란, 아나콘드라, 피타스, 서펜티스의 보석을 모아 썬더 블러프에 있는 나라 와일드메인에게 가져가야 합니다."}},
[915] = {nil, nil, nil},
[916] = {"타락한 나무그물거미", {"나는 그늘 거미굴에 서식하는 나무그물거미를 관찰하기 위해 그늘 협곡에 왔소. 최근에 텔드랏실이 축복받았다고 들었는데, 그렇다면 이 거미들은 조금이라도 덜 위협적이어야 하오. 아무래도 내가 잘못들은 것 같군.","최근에 나무그늘거미의 독주머니를 조금 찾아서 조사해 보았는데, 그 안에 거미들을 타락시키는 원인을 발견하고 나서 조금 불안해 졌소. 간단히 말해 이 야수들이 미쳐 날뛰도록 내버려두면 무슨 일이 일어날지 걱정이라오.","해결책을 마련할 시간을 벌 수 있도록 그대가 거미들의 수를 줄여줬으면 좋겠소."}, {"나무그물거미 12마리를 처치해야 합니다."}},
[917] = {"나무그물거미의 알", {"사실 좀 곰곰이 생각해보니 그대가 저를 조금 더 도와주실 수도 있을 것 같습니다. 나무그늘거미들이 알을 가지고 있지 않은 것 같아 동굴 깊숙한 곳에 있는 그 알을 채취하러 왔으니까요. 저 대신 나무그늘거미의 알을 채취해 디라니아에게 가져다주면 정말 고맙겠습니다."}, {"나무그물거미알을 구해야 합니다."}},
[918] = {"나무괴물 씨앗", {"텔드랏실의 나무괴물은 자연의 정령입니다. 어떤 면에서 거대 고목에 사는 동식물에서 비롯되는 자연의 질서를 반영하지요. 이렇게 사나워진 나무괴물을 보면 치유 방법까지 고려해야 하므로 심란하기 짝이 없습니다.","저는 근본적인 원인을 추적하는 데 힘을 보태고 싶습니다. 그동안 식물을 기르는 방법을 여러 가지로 연구했는데, 그 방법을 나무괴물 씨앗에 적용해 보고 싶군요. 그러니 그대가 알아메스 호수 주위의 나무괴물들에게서 씨앗을 모아 제게 좀 가져다주시겠습니까?"}, {"나무괴물 씨앗 6개를 모아야 합니다."}},
[919] = {"나무괴물 씨눈", {"작은 나무괴물이 알아메스 호수 주변에서 자라납니다. 이 씨눈은 손쓸 수 없을 정도 같아 걱정스럽군요. 계속 자라나 문제를 일으키기 전에 땅에서 씨눈을 제거해야 합니다.","호숫가를 지나다가 나무괴물 씨눈을 발견하면 모두 가져다주십시오. 우리의 영토가 깨끗하게 유지될 수 있도록 도와주셔야 합니다!"}, {"나무괴물 씨눈 10개를 모아야 합니다."}},
[920] = {"테나론의 부름", {"그대 이름이 <name> 맞소? 테나론이 찾고 있다고 들었소. 알드랏실 꼭대기, 가장 높은 곳에 있는 방으로 가면 그를 만날 수 있을 거요.","비록 우리의 수명이 길긴 하지만 그를 너무 오래 기다리게 하지 않는 것이 좋을 거요."}, {"그늘 협곡, 알드랏실 꼭대기에 있는 테나론 스톰그립과 대화해야 합니다."}},
[921] = {"텔드랏실: 아제로스의 왕관", {"<name>, 이제 그대도 자신의 운명을 찾아 나설 때가 되었소. 하지만 축복받은 우리의 숲을 넘어 미지의 세계로 떠나기 전에 최근의 역사에 대해 알아 두어야 할 것이 많이 있소.","우선 그대에게 맡길 임무가 하나 있소. 알드랏실 북쪽에 있는 달샘으로 가서 물을 한 병 담아 와 주시오. 그곳에 있는 동안 우리 마법과 과거의 일면을 목격하게 될 것이오.","수정 용기에 물을 채우면 알드랏실 꼭대기에 있는 테나론 스톰그립에게 가져다주시오. 그는 알드랏실 꼭대기의 서쪽 경사로에 있을 것이오."}, {"수정 용기에 물을 채워야 합니다."}},
[922] = {"렐리안 그린스파이어", {"<name>, 그대가 가져온 이 씨앗 중 하나를 렐리안 그린스파이어라는 제 친구에게 좀 전해주실 수 있겠습니까? 그는 생명의 오두막에 사는 드루이드인데, 지난 번에 함께 얘기를 나누었을 때 나무괴물에 대한 제 연구에 관심을 보였습니다. 나름대로 생각이 있을 테니 연구에 필요한 씨앗 표본을 가져다준다면 기뻐할 것입니다.","고맙습니다, <name>. 언젠가 그대도 제 노력의 결실을 보게 되길 바라겠습니다.","생명의 오두막은 돌라나르 밖에 다르나서스로 향하는 서쪽 길을 따라가면 찾을 수 있습니다."}, {"생명의 오두막에 있는 렐리안 그린스파이어에게 나무괴물 씨앗을 가져가야 합니다."}},
[923] = {"이끼 낀 종양", {"나무괴물에게서 사악한 악의 기운이 자라나고 있소. 나는 지금 그 근원을 조사하는 중이오. 이것이 밝혀질 때까지 텔드랏실의 안전을 위해 오염된 나무괴물들을 처치해야 하오. 텔드랏실 북부 지역에 있는 생명의 호수 주위를 어슬렁거리는 놈들이 가장 심각하게 오염되었으니 반드시 제거해야만 하오!","그 지역에서 발견하는 나무괴물을 처치하고 놈들에게서 자라는 이끼 낀 종양을 모아오시오. 내게 가져오면 태워버리겠소."}, {"이끼 낀 종양 5개를 모아야 합니다."}},
[924] = {"악마의 씨앗", {"악마의 씨앗은 불타는 군단이 사용하는 강력한 도구요. 그 씨앗은 공포안개 봉우리의 동굴에 있는 악마의 불제단 위에 놓여 있소.","이 앞 탁자 위에 내가 준비해 놓은 마법석을 가지고 가야 하오. 악마의 씨앗 근처에 가져다 놓으면 불타는 군단과의 연결이 끊어지면서 파괴될 거요.","자, 공포안개 봉우리에 있는 불의 제단으로 가시오. 서쪽으로 가다가 길이 갈라지는 곳에서 북쪽으로 가면 되오. 그러면 왼쪽에 공포안개 봉우리가 보일 것이오. 가장 안전한 북쪽 면을 타고 꼭대기까지 올라가도록 하시오.","시간이 지날수록 마법석의 힘이 점점 약해지니 서두르는 게 좋을거요."}, {"우선 혼란의 마법석을 손에 넣으십시오. 마법석의 힘이 다하기 전에 불의 제단에 가져다 놓은 후 아크젤로스에게 가야 합니다."}},
[925] = {nil, nil, nil},
[926] = {"혼란의 마법석", nil, nil},
[927] = {"이끼로 덮인 심장", {"악취나는 검은이끼괴물의 심장은 끈적끈적한 검은색 이끼로 덮여 있습니다. 느리지만 일정한 박동이 느껴지는 것으로 보아 이것이 심장이라는 것만 간신히 알아볼 수 있을 뿐입니다. 심장의 박동은 지금까지도 멎지 않고 있습니다.","데날란이 이 심장을 보고 싶어할지도 모릅니다."}, {"이끼로 덮인 심장을 알아메스 호수에 있는 데날란에게 가져가야 합니다."}},
[928] = {"텔드랏실: 의식의 통과", {"달샘과 텔드랏실에 대해 들려 줄 이야기가 더 있긴 하지만, 이제 그대를 보내야 합니다. 코리드라스 문레이지가 그대를 기다리고 있을 것입니다. 그대가 채워온 약병에 든 물을 이 유리병에 옮겨 담아놓았습니다.","돌라나르에 있는 달샘 근처에서 코리드라스를 찾도록 하십시오. 그늘 협곡 외곽에 있는 이곳 알드랏실에서 남쪽으로 이어진 조약돌 길을 따라가다가 길이 서쪽으로 향해도 계속해서 숲길을 따라 남쪽으로 가면 돌라나르에 도착할 것입니다.","길을 잘 따라가야 합니다. 근래 들어 숲에 위험한 짐승들이 자주 출몰하고 있습니다."}, {"돌라나르에 있는 코리드라스 문레이지에게 반쯤 찬 유리병을 가져가야 합니다."}},
[929] = {"텔드랏실: 위상들의 거부", {"그대가 먼저 완수해야 할 임무에 대해 더 말씀드리겠습니다. 다르나서스의 드루이드들은 텔드랏실에 있는 달샘의 물을 사용하고 있지요. 그 달샘은 때때로 보충해 주어야 합니다. 특별히 제작된 이 약병을 사용하면 달샘의 물을 채취할 수 있습니다.","이 약병을 가지고 동쪽의 별바람 마을 외곽에 있는 달샘으로 가서 물을 채운 다음 제게 가져오십시오. 우리의 결점이 더 악화되지 않도록 달샘에 대해 들은 바를 유념하시기 바랍니다."}, {"비취 약병에 달샘의 물을 가득 채워야 합니다."}},
[930] = {"빛나는 과일", {"빛나는 식물의 잎 아래에 크고 둥근 과일이 달려 있습니다.","데날란이 이 과일을 연구하고 싶어하리라는 확신이 분명히 듭니다."}, {"알아메스 호수에 있는 데날란에게 빛나는 과일을 가져가야 합니다."}},
[931] = {"희미하게 빛나는 잎", {"이 식물의 잎은 숲 속의 빛을 받아 희미하게 빛나며 고동치는 듯한 기운을 발합니다.","데날란이 이 식물의 표본을 원할 것입니다."}, {"데날란에게 희미하게 빛나는 잎을 가져가야 합니다. 데날란은 보통 알아메스 호수에 있지만, 가끔 생명의 오두막으로 가기도 합니다."}},
[932] = {"뒤틀린 증오", {"<name>, 그대에게 충고해 드릴 게 있습니다. 이 문제는 우리 둘만의 비밀로 해야 합니다. 사티로스는 이미 우리에게 심각한 골칫거리를 안겨줬지만 이번 문제와는 비교도 안 됩니다.","제왕 멜레나스라는 사티로스가 있는데, 상당수의 그렐족 전사를 모아 북쪽에 있는 악마 바위굴 근처에서 지냅니다. 마음은 칠흑같이 시커먼 자로서 가장 음흉하고 더러운 음모만 꾸밉니다.","이곳 북쪽에 있는 악마의 동굴 안에서 그놈을 찾아내어 처치한 다음 머리띠를 제게 가져다 주십시오."}, {"제왕 멜레나스의 머리띠를 회수해야 합니다."}},
[933] = {"텔드랏실: 다가오는 새벽", {"마지막으로 방문해 주셨으면 하는 달샘이 있습니다. 그 달샘은 이곳에서 곧바로 남쪽에 있는 아리스리엔 연못가에 있습니다. 그곳에서 달샘을 찾을 때 무기를 꼭 손에 쥐고 경계를 늦추지 마십시오. 텔드랏실 남쪽의 가지에서 타락의 기운이 점점 커지는 것이 느껴집니다.","달샘에서 물을 채취하여 연못으로 가면 친숙한 얼굴이 보일 테니 그 존재에게 채취한 물을 가져다주십시오. 타락의 기운과 싸우고 싶어서 그대가 직접 가져다주기를 요청했으니까요."}, {"전기석 약병에 달샘의 물을 채워야 합니다."}},
[934] = {"대지의 왕관", {"하지만 텔드랏실은 모든 면에서 문제가 많았소. 세계수에 대한 스태그헬름 님의 신중한 계획은 그분이 바라는 대로 실행됐지만 작은 문제가 있었소. 그리고 그 문제로 인해 텔드랏실에 또 다른 문제들이 꼬리를 물고 발생하게 된 것이오.","지금은 다른 문제들은 제쳐 두겠소. 우선 그대는 북서쪽 신탁의 숲에 위치한 마지막 달샘으로 가도록 하시오. 신탁의 나무 가지 아래 우리의 최초의 달샘이자 가장 강력한 샘이 하나 있소. 그 물을 이 약병에 담아 내게 가져오시오."}, {"자수정 약병을 채운 다음 돌라나르에 있는 코리드라스 문레이지에게 가져가야 합니다."}},
[935] = {"텔드랏실의 물", {"지금 이곳에서 할 일은 다 했군요. 이 물이 아주 중요하다는 사실은 입증되었는데, 원래 제게 올 것이 아니었습니다. 물이 든 유리병을 교차로에 있는 코리드라스 문레이지에게 가져다 주세요. 그대가 이 물을 원래 가야 할 곳에 전달해주기를 바랄 테니까요."}, {"다르나서스 정면의 교차로에 있는 코리드라스 문레이지에게 가득 찬 유리병을 가져가야 합니다."}},
[936] = {"대드루이드 룬토템 돕기", {"손님, 괜찮으시다면 잠시만 시간을 내주시겠습니까?","썬더 블러프의 세나리온 의회에서는 아주 중요한 조사 작업 때문에 당신처럼 노련한 모험가들을 찾고 있다고 합니다. 자세한 내용은 모르지만 이 연구 조사를 다름 아닌 하뮬 룬토템 님께서 직접 지휘하고 있다는 것만은 자신 있게 말씀드릴 수 있습니다.","혹시 관심이 있으시다면 썬더 블러프에 있는 장로의 봉우리에서 그분과 직접 얘기를 나눠 보십시오."}, {"썬더 블러프에 있는 대드루이드 하뮬 룬토템과 대화해야 합니다."}},
[937] = {"마법의 터", {"저는 신탁의 숲 속 주변에 둥지를 튼 하피들로부터 신탁의 나무를 지키고자 파수꾼 몇 명과 함께 이곳으로 파견되었습니다. 현재 조금씩이나마 그들을 몰아내는 중이지요.","그런데 다르나서스로 보고서를 전달하라고 신탁의 나무가 보낸 전령이 하피 무리에게 습격을 받아 전사했습니다.","괜찮다면 둥지로 가서 하피를 처치한 다음 증거물로 허리띠를 가져다 주십시오."}, {"붉은깃털 허리띠 6개를 모아야 합니다."}},
[938] = {"미스트", {"이 퓨마의 털은 특이한 회색빛이라 주변의 숲과 조화를 이룹니다. 누워 있는 퓨마의 등과 배 전체에 심하게 깊이 찢긴 상처가 보입니다.","머리를 들고 당신을 바라보는 퓨마의 푸른 눈에 총명함이 번뜩입니다. 퓨마는 당신을 따라가고 싶어하는 것 같습니다."}, {"신탁의 나무 근처의 달샘에 있는 파수꾼 아리니아 클라우즈브레이크에게 미스트를 데려가야 합니다."}},
[939] = {"사바릭의 플룻", {"사바릭을 퇴치하고 나서 얻은 단순한 모양의 나무 플룻을 관찰합니다.","단단해 보이는 구조로 단면에 그려진 문양들이 돌아가는 것처럼 보이고, 뒤집어 보니 움직이고 있었습니다. 갑자기 오한이 느껴지며 몸이 떨립니다...","기분 탓이라는 생각에 고개를 흔듭니다.","사티로스라면 당신이 느낀 이상한 현상에 대해서 알고 있을 것 같습니다. 악령숲 남쪽에 살고 있는 드루이드, 에리단 블루윈드에게 당신이 찾은 것들을 가져가십시오."}, {"사바릭의 플룻과 비취불꽃 악마매듭 5개를 악령숲 남쪽의 에리단 블루윈드에게 가져가야 합니다."}},
[940] = {"텔드랏실", {"숲에서 그대의 용감한 업적을 숭배하는 속삭임이 바람을 통해 들려오는군요. 하피들이 둥지에서 쫓겨나면서 숲에 조용한 평화가 찾아오고 생물들이 안정을 되찾고 있는 게 느껴집니다. 대드루이드께 또 전갈을 보내기가 두려웠는데, 이제 마음 놓고 보낼 수 있겠습니다.","이걸 그분께 전하도록 하세요. 바위의 숲에 다다를 때까지 숲의 정령들이 그대를 안전하게 지켜줄 겁니다."}, {"다르나서스에 있는 대드루이드 판드랄 스테그헬름에게 글씨가 새겨진 나무껍질을 전해야 합니다."}},
[941] = {"오염된 심장 심기", {"심장에서 해로운 이끼를 제거하기는 했지만, 아직도 오염된 채로 남아 있습니다.","심장을 제 화분에 넣어 주십시오. 이 화분에는 영양분이 풍부해 심장을 정화하고 치유할 수 있는 흙이 담겨 있으니까요."}, {"데날란의 화분에 오염된 심장을 심어야 합니다."}},
[942] = {"정신없는 발굴조사단장", {"이 화석은 내가 전에 아이언포지에서 봤던 것과 상당히 유사하군그래. 플라곤구트라는 고고학자가 탐험가 연맹 연례 총회에 들고 나온 것이었소. 그는 그 화석이 봉인을 해제할 힘을 지니고 있다고 믿었소.","플라곤구트에 대해 마지막으로 들은 소문에 따르면 웰가르의 발굴현장까지 자신을 호위해 줄 이를 기다리며 메네실 항구의 깊은바다 선술집에 숨어 지낸다고 하오. 당신이 그를 찾아 렘트래블의 화석을 보여 주시오. 아마 그가 과거의 비밀을 밝혀줄 수도 있을 거요.","메네실로 가는 배는 스톰윈드에서 출발한다오."}, {"메네실 항구에 있는 고고학자 플라곤구트에게 신비로운 화석을 가져가야 합니다."}},
[943] = {"정신없는 발굴조사단장", {"원본 화석은 지금 발굴조사단장 웰가르가 저습지에 있는 그의 발굴현장에 보관하고 있소. 그의 발견 이후 나는 화석의 신비를 밝혀낼 열쇠로 추정되는 렐루의 돌을 모단 호수에서 찾아냈소.","그런데 웰가르를 만나러 가는 도중 줄무늬랩터의 공격을 받아 그 유물을 잃고 말았다오.","그 얼룩덜룩한 짐승들 중에 어느 녀석이 돌을 삼켜 버렸는지는 모르지만, 당신이 그걸 찾아온다면 화석의 힘을 끌어낼 수 있을 거요.","내게 렐루의 돌과 웰가르가 갖고 있는 원본 화석을 가져다주시오."}, {"메네실 항구에 있는 고고학자 플라곤구트가 렐루의 돌과 플라곤구트의 화석을 가져다 달라고 했습니다."}},
[944] = {"지배자의 무덤", {"이곳 어둠의 해안에 나가가 나타났다는 건 이상한 일이 아닐 수 없군. 그런데 더 이상한 건 바로 황혼의망치단이야. 그 이교도들은 이 땅의 고대 신을 섬기고 있지. 아주 예전에 죽은 지배자를 말이야.","남쪽에 있는 지배자의 무덤은 그 지배자가 전투에서 패배한 장소야. 그곳으로 가서 황혼의망치단 이교도들이 더 있는지 찾아 봐. 참, 이 수정점의 유리병을 가져가야 해. 지배자의 무덤을 찾은 후 이걸 사용하면 수정점 그릇을 만들 수 있을 거야. 그 그릇을 사용하면 나와 말을 할 수 있지."}, {"정보를 수집한 다음 수정점의 유리병을 사용하여 수정점 그릇을 만드십시오. 만들어진 그릇을 사용하여 오누와 대화하십시오."}},
[945] = {"테리루네의 탈출", {"도와주세요!","저는 지배자의 고대검 근처를 산책하고 있었는데 더러운 이교도들에게 포위당하고 말았어요. 그나마 내가 숨는 데 능숙해서 다행이었죠.","이곳을 탈출하도록 도와줄 수 있나요? 그리고 우리 언니 테리실에게 내가 무사하다고 알려야 해요. 그녀는 남쪽의 잿빛 골짜기에 있는 아에시나의 제단에 있어요."}, {"테리루네의 탈출을 도운 다음 아에시나의 제단에 있는 테리실에게 그녀가 무사하다는 사실을 알려야 합니다."}},
[947] = {"동굴 버섯", {"내가 연구 중인 물약에는 특정한 동굴에서만 자라는 희귀한 버섯이 필요하오. 그 동굴은 여기서 동쪽으로 가면 나오는 산맥의 약간 북쪽에 위치한 절벽 폭포 뒤에 있소.","그곳에 직접 가보려 했지만 고대정령의 숲이 그곳에 가까이 가지 말라고 충고하더군. 우리의 오랜 친구인 그들은 느꼈나 보오. 그 동굴이 어둠해안에 새로이 생겨난 악의 세력이 숨어 있는 은신처라는 걸 ...","부디 날 위해 버섯을 좀 모아 주시오. 그리고 동굴을 정찰하여 고대정령이 우려하는 바가 사실인지 확인해주기 바라오."}, {"아우버다인에 있는 바리스라스 문셰이드에게 껄껄이그물버섯 5개와 알광대버섯 1개를 가져가야 합니다."}},
[948] = {"오누", {"고대정령의 숲에 사는 지식의 고대정령 오누가 절벽 폭포에서의 모험에 대해 듣고 당신과 얘기를 나누길 원하고 있소. 고대정령의 숲은 남쪽 산맥 근처에 있소.","고대정령들은 원래 지혜롭고 인내심이 많기로 유명한데, 오누가 당신을 불러 절벽 폭포에서 본 것을 묻고자 한다면 뭔가 급한 일일지도 모르겠소."}, {"고대정령의 숲에서 오누와 대화해야 합니다."}},
[949] = {"황혼의 망치단 야영지", {"지배자의 고대검에서 황혼의 망치단이 무엇을 하려는지는 아직 알 수 없군. 하지만 분명 좋은 일은 아니겠지.","그곳에 있는 야영지를 수색해서 황혼의 망치단의 의도를 짐작할 만한 단서를 찾아야 해. 그들이 계획을 실행에 옮기기 전에 그 의도를 알아내야만 해."}, {"지배자의 고대검에 있는 황혼의 망치단 야영지에서 단서를 찾아야 합니다."}},
[950] = {"오누에게로의 귀환", {"황혼의 마법책을 자세히 읽어본다는 건 참으로 어려운 일입니다. 잠시만 보고 있어도 그 내용이 꿈틀대며 움직이고 아름다운 무늬가 기괴한 모양으로 뒤틀려 정신이 혼미해집니다.","정신을 거의 잃어버리게 만드는 이 광기의 양피지 문서에서 겨우 한 장을 찢어낼 수 있었습니다."}, {"오누에게 광기의 양피지 문서를 가져가야 합니다."}},
[951] = {"마시스트라의 유물", {"자네가 지배자의 고대검에서 발견한 양피지는 고대 문서 중 한 장으로... 엘프들이 작성한 것이지! 그런데 타락한 황혼의 망치단 때문에 심하게 저주받고 오염되어 버렸어. 이 이교도 집단은 고대 엘프의 마법을 사용하여 그들의 옛 주인을 이 세상에 다시 불러오려고 하는 것 같아.","북동쪽에 있는 마시스트라 폐허로 가서 풀과 깨진 돌 사이에서 고대 유물을 찾아봐. 그 유물들을 연구해 보면 황혼의 망치단이 엘프의 마법을 어떻게 이용하려는지 알아낼 수 있을 거야."}, {"고대정령의 숲에 있는 오누에게 마시스트라 유물 6개를 가져가야 합니다."}},
[952] = {"고대정령의 숲", {"그대가 신탁의 나무를 위해 전갈을 전할 시간이 있었다면, 아우버다인의 남쪽, 어둠해안에 있는 고대정령의 숲으로 전갈을 가져가는 임무도 맡아줄 수 있을 거라 생각하네.","히포그리프를 타고 이동하는 중에는 안전에 각별히 유의해야 하네. 하지만 그대라면 잘해낼 거라 믿네. 이걸 지식의 고대정령 오누께 전하게. 내가 신탁의 숲에 대한 소식을 기다린 만큼이나 그분도 내게서 소식이 오기를 기다리고 있다네."}, {"아우버다인의 남쪽, 어둠해안에 있는 고대정령의 숲에 있는 오누에게 판드랄의 전갈을 전해야 합니다."}},
[953] = {"아메스아란의 멸망", {"여기서 동쪽으로 가면 아메스아란의 폐허가 보일 겁니다. 지금 그곳은 성에 살던 명가의 원혼들이 차지하고 있지만, 한때는 아즈샤라의 부하들이 자유롭게 마법을 익히던 곳이었답니다.","저는 멸망한 아메스아란의 역사가 기록된 커다란 서판 두 개를 찾기 위해 폐허를 수색하는 임무를 띠고 파견되었습니다. 그런데 제가 서판의 룬 문자를 읽고 있는 동안 원혼들이 접근해 오는 바람에 거기서 도망칠 수밖에 없었습니다.","괜찮다면 저 대신 폐허로 가서 서판에 있는 내용을 판독해 주시길 부탁 드립니다."}, {"멸망한 아메스아란의 역사가 기록되어 있는 서판의 내용을 알아낸 다음 어둠해안에 있는 파수꾼 티샤 문블레이드에게 가야 합니다."}},
[954] = {"바샬아란", {"동쪽에 있는 바샬아란의 폐허가 악마의 앞잡이들에게 점령당하고 말았소. 마귀들과 사티로스들은 그곳을 장악하고 마법 에너지를 흡수해 나날이 강해지고 있소.","그런데 아직 그들이 접근조차 하지 못한 제단이 하나 있다고 하오. 폐허의 서쪽 가장자리에 작은 절벽이 있는데, 그 꼭대기에서 이상한 기운이 뻗어나온다고 하더군. 그곳으로 가면 이 악마들의 저항에 대해 알아낼 수 있을 거요.","그대가 좀 조사해 주시오."}, {"바샬아란의 폐허에 감도는 이상한 푸른 기운의 근원을 찾아야 합니다."}},
[955] = {"바샬아란", {"내가 살아온 얘기를 한다면 아마 그대는 믿지 못할 걸세. 내 삶은 아주 길고 고통스러운 것이었다네. 그리고 이렇게 실체 없는 모습이 되어버린 건 그중 가장 고통스러운 일이지.","난 마법에 의해 여기에 갇히게 되었다네. 내 얘기가 믿기 어려울지도 모르지만, 그대가 날 구속하고 있는 도구를 찾아 준다면 그 고마움은 말로 다 하지 못할 만큼일 게야. 마귀들과 그렐들을 조사해 보면 날 가두고 있는 이 봉인에 대해 뭔가 단서를 발견할 수 있을 거야."}, {"바샬아란에 있는 아스테리온이 그렐족 귀걸이 8개를 모아 달라고 했습니다."}},
[956] = {"바샬아란", {"그렐이 날 무한정 구속하고 있는 봉인에 접근한 이유를 알 것도 같군그래. 이제 그들을 이끌고 있는 사티로스가 봉인을 가지고 있는 것이 분명해졌네.","그게 사실이 분명하다는 확신이 드네. 저 사티로스 중 누군가 분명히 봉인을 가지고 있을 걸세. 그대가 그 봉인을 찾아 준다면 난 자유를 찾을 수 있을지도 몰라. 내 눈에서 감격의 눈물이 흐를 날이 올지도 모르겠군그래."}, {"고대 월장석 봉인을 찾아 바샬아란에 있는 아스테리온에게 가져가야 합니다."}},
[957] = {"바샬아란", {"이 감옥의 봉인을 만든 건 가장 강력한 명가 중 하나의 솜씨였다네. 이곳과 똑같이 생긴 남쪽의 폐허 아메스아란에는 아직도 녹색 빛이 나는 고대의 불꽃이 남아 있다더군. 그 불꽃으로 이 봉인을 깰 수 있을 걸세.","폐허에 가면 부디 조심하도록 하게."}, {"아메스아란에 있는 고대의 불꽃으로 고대 월장석 봉인을 파괴한 다음 바샬아란에 있는 아스테리온에게 가야 합니다."}},
[958] = {"명가의 도구", {"<class>. 부탁할 일이 하나 있소. 혹시 아메스아란의 폐허를 본 적이 있소? 남쪽으로 가다 보면 나오는 큰길 동쪽편에 있을 거요.","한 때 그곳은 기세등등한 명가들의 고향이었는데, 지금은 과거 그들이 사소한 이유로 벌인 일에서 비롯된 파멸이 흔적처럼 남아 있소.","파수대에서 들은 바로는 명가의 영혼들이 아직 남아 고대의 마법 도구들을 사용하고 있다 하오. 그 유물들을 압수해 파괴해야 하오."}, {"아우버다인에 있는 툰드리스 윈드위버를 위해 명가의 유물 7개를 구해야 합니다."}},
[959] = {"99년 묵은 포도주", {"보시다시피 작은 문제가 발생했소. 아침에 무법항에서 막대한 양의 뱃짐이 배달됐소. 정말 엄청난 규모지! 내 생전에 그렇게 많은 상자들은 본 적이 없단 말이야. 난 일을 신속히 처리하려고 평소보다 조금 무리해서 짐을 부리기 시작했소. 바로 그때 문제가 발생한 거요.","광기의 매글리시라는 악당이 나타나 눈 깜짝할 사이 99년이나 숙성된 포트 포도주병을 들고 달아나고 있었소. 죽기 살기로 통곡의 동굴까지 달아나는 그자를 쫓아갔지. 그런데 그 안으로는 들어갈 수가 없어서 밖에서 발만 동동 굴렀지.","당신이라면 들어갈 만한 용기가 있을 것 같은데 내게 그 포도주를 되찾아 주지 않겠소?"}, {"톱니항에 있는 기중기 기사 비글퍼즈가 통곡의 동굴에 숨어 있는 광기의 매글리시에게서 99년 숙성된 포트 포도주를 되찾아 달라고 했습니다."}},
[960] = {"생각 중인 오누", nil, nil},
[961] = {"생각 중인 오누", nil, nil},
[962] = {"불뱀꽃", {"로데론의 위대한 도시 언더시티에 있는 왕립 연금술 학회에서 매우 특별한 임무를 위해 날 이곳으로 보냈소. 아마 당신은 포세이큰의 발전을 위해 노력 중인 나와 어둠의 여왕님을 도와줄 거라고 생각하오.","최근에 나는 불뱀꽃이라는 희귀한 식물의 표본에 대해 연구했소. 이 향초의 잠재력은 아주 크다고 믿소.","하지만 불행하게도 불뱀꽃은 불모의 땅에 있는 위험한 동굴인 통곡의 동굴의 가장 어둡고 깊은 곳에서만 자라오. 통곡의 동굴로 가서 불뱀꽃을 구해다 주시오."}, {"썬더 블러프에 있는 연금술사 자마가 불뱀꽃 10개를 모아 달라고 했습니다."}},
[963] = {"영원한 사랑", {"영원의 샘에서 벌어진 전투로 인해, 아메스아란이 멸망하고 내 사랑 아나야와 그곳 주민들이 모두 죽었다고 들었소.","수천 년이 지난 지금에도 아나야에 대한 기억이 꿈속에서 날 괴롭힐 줄은 생각지도 못했소. 무의식 중에 어둠해안의 숲을 거닐다가 나도 모르는 사이에 아메스아란의 폐허에 와 있는 나 자신을 발견하게 되고... 거기서 내가 사랑하는 아나야의 유령을 보곤 한다오.","그녀를 자유롭게 해 주어야 하지만 차마 내가 직접 그렇게 할 용기가 나지 않는다오. 부디 그대가 그녀의 영혼을 자유롭게 해주시오."}, {"아나야 돈러너의 영혼을 자유롭게 한 다음 그녀의 펜던트를 아우버다인에 있는 세렐리안 화이트클로에게 가져가야 합니다."}},
[964] = {"뼛조각", {"스칼로맨스로 가는 열쇠를 해골 열쇠라고 부르지. 해골 열쇠는 물론 해골로 만드는 데 가장 단단한 금속을 적당한 거푸집에 넣고 굳혀서 만들지. 사실 해골이 여러 개 필요하다네. 그리고, 스칼로맨스로 가는 차원문을 자유로이 드나들 수 있는 존재에게 힘의 인장을 얻어야 열쇠를 완성할 수 있어.","꽤 복잡하지? 우선 생각나는 것부터 시작하세. 우선 열쇠의 재료가 되는 뼛조각이 필요해. 안돌할 안쪽에 있는 해골들에게서 얻을 수 있을 걸세. 하지만 성벽 밖에 있는 해골도 상관은 없어."}, {"서부 역병지대의 보루에 있는 연금술사 디더스에게 뼛조각 15개를 가져가야 합니다."}},
[965] = {"알살락스의 탑", {"안녕하십니까, 젊은 <race>|1이여;여;... 전 엘리자 스타브리즈라고 합니다. 이곳 아우버다인을 안전하게 지키는 게 제 임무입니다.","얼마 전 발툴 섀도스트라이크를 북동쪽 알살락스의 탑으로 보내 그 주변에서 일어나고 있는 심상치 않은 일들을 조사하도록 했답니다.","그가 돌아와야 할 때가 한참이나 지났는데... 숲에서 불의의 사고라도 당한 것이라도 아닌가 걱정이 됩니다. 그대가 그를 찾아 무사히 임무를 수행했는지 확인해 준다면 정말 고맙겠습니다."}, {"어둠해안에 있는 알살락스의 탑 근처에서 발툴 섀도스트라이크를 찾아야 합니다."}},
[966] = {"알살락스의 탑", {"한 무리의 소환사들이 탑 안팎에 모여 있소. 내가 좀 더 일찍 아우버다인으로 돌아가 엘리자 님께 보고했어야 하지만 자리를 비운 사이 뭔가 중요한 것을 놓치게 될 것만 같아 여기를 떠날 수가 없었소.","델그렌 님께서는 이자들이 탑으로 모여들 것 같다고 하셨지만 그 이유까지는 모르셨소.","소환사들이 가진 양피지 문서를 약간 손에 넣었지만 의문을 완전히 풀기 위해선 몇 개 더 필요한데 그대가 좀 도와주시겠소? 하지만 탑 안으로는 들어가지 않는 게 좋을 거 같소. 탑 안에 있는 소환사들은 훨씬 더 강력하니까 말이오!"}, {"알살락스의 탑 근처에 있는 발툴 섀도스트라이크를 위해 낡은 양피지 문서 4개를 모아야 합니다."}},
[967] = {"알살락스의 탑", {"내 스승이신 수호자 델그렌께서는 숲을 악마와 언데드의 손으로부터 지키기 위해 기꺼이 도움을 주고 계신 성기사이시오. 그분은 내게 성스러운 빛과 전투 기술에 대해 많은 걸 가르쳐 주셨다오. 델그렌이시라면 이 집단이 대체 무엇을 하고 있는 건지 바로 알아내실 것이오.","이곳에서 남쪽으로 멀리 떨어진 잿빛 골짜기 숲의 마에스트라 주둔지로 가면 그분을 만날 수 있을 거요. 아... 암흑이교도의 위협이 시시각각으로 커지고 있는 것 같소. 서두르시오."}, {"잿빛 골짜기에 있는 수호자 델그렌에게 발툴의 편지를 전해야 합니다."}},
[968] = {"힘의 저편", {"워낙 낡은데다 심하게 긁히긴 했지만 이 책의 표지에 적힌 제목은 아직 읽을 수 있습니다.","'힘의 저편'","펼쳐보니 어떻게 읽어야 할지 아리송한 이름들과 함께 예언의 제목, 경배 의식의 절차, 희생 제물들의 순위가 적혀 있습니다. 그림을 보니, 희생 제물의 대부분은 인간과 비슷한 형상을 한 살아있는 생명체들입니다.","책 표지 안쪽에는 '아이언포지의 쓸쓸한 뒷골목, 본그립의 룬과 운명 서점, 소유자: 게릭 본그립'이라는 글귀가 찍혀 있습니다.","본그립의 서점에서 이런 종류의 책을 취급한다면 이 책을 다시 사들일지도 모릅니다."}, {"아이언포지에 있는 게릭 본그립에게 '힘의 저편'이라는 책을 가져가야 합니다."}},
[969] = {"행운이 함께하기를", {"허, 자네 눈을 들여다보기만 해도 뭘 원하는지 알 것 같군. 행운 깃든 부적을 만들어 달라는 게지? 말하지 않아도 이 마우아리는 다 안다네!","남쪽으로 가게. 그곳에 가면 얼음으로 만들어진 거인들을 볼 수 있을 게야. 가끔 녀석들이 지니고 다니기도 하고, 아니면 자네가 직접 산맥을 뒤져 조각들을 찾아올 수도 있지. 이곳에서는 질이 아주 좋은 것들이 나오니 말이야...","서리망치거인조각 4개를 가져오면 내 행운 깃든 부적을 자네에게 줌세."}, {"눈망루 마을의 의술사 마우아리에게 서리망치거인조각 4개를 가져가야 합니다."}},
[970] = {"알살락스의 탑", {"발툴의 편지가 매우 긴급한 내용을 담고 있군. 내게 이 암흑이교도는 반드시 없애버려야 할 눈엣가시와 같다네. 일전에 북동쪽에 있는 오르딜아란에서 암흑이교도 녀석 몇 명을 상대한 적이 있다네. 그런데 놈들 중 하나가 가진 영혼의 보석이란 것이 이 이교도가 가진 불가사의한 힘의 비밀을 밝히는 열쇠가 될 거라 생각되네.","그걸 내게 가져다주게. 그러면 내가 그 안에 숨겨진 비밀을 밝혀내도록 하지."}, {"빛나는 영혼의 보석을 모아 마에스트라 주둔지에 있는 수호자 델그렌에게 가져가야 합니다."}},
[971] = {"심연의 지식", {"저는 고대 지식이나 강력한 힘을 지닌 정보, 새로운 차원문을 여는 지식, 그리고 잘못 잠든 자들을 깨우는 지식을 모은답니다.","소문에 의하면 잿빛 골짜기에 있는 검은심연 나락 깊숙한 곳 서쪽에 로르갈리스 초본이라는 오래된 지식서가 있다고 하던데... 그곳은 이미 오래전에 죽은 엘프들이 점령해 거처로 삼고 있어요. 도시가 파멸되기 전에는 위대한 지식을 가졌던 귀족들이죠.","검은심연 나락에서 그 초본을 찾아오면 은혜는 잊지 않겠어요. 이 세상을 떠나더라도 잊지 않을 거예요."}, {"검은심연 야영지에 있는 파수꾼 훈련생 이사라에게 로르갈리스 초본을 가져가야 합니다."}},
[972] = {"물의 정기", nil, nil},
[973] = {"알살락스의 탑", {"일크루드 마그스룰... 이자에 대해서는 좀 알고 있지. 그는 잿빛 골짜기 남서부 지역에 있는 거대한 악의 장소인 불타버린 신전에 살고 있는 강력한 오크족 흑마법사라네.","지금까지는 조금 성가신 존재에 지나지 않아서 별다른 조치를 취하지 않았는데, 이제 놈을 상대해야 할 시간이 온 것 같군."}, {"마에스트라 주둔지에 있는 수호자 델그렌에게 일크루드 마그스룰의 마법책을 가져가야 합니다."}},
[974] = {"앗! 뜨거!", {"이곳의 온천은 참 수수께끼야. 도대체 열이 어디서 나오는 걸까?","불기둥 마루에 가깝다는 점으로 미루어 보아 화산과 어떤 관련이 있는 게 아닐까... 화산의 온도를 잴 수 있는 온도계측장치를 만들었으니 당신이 가서 가장 뜨거운 곳을 찾게나. 온도는 섭씨도, 화씨도 아니라 크래클씨로 표시되지!","자, 받게나. 불기둥 마루에서 뜨거운 열점을 찾을 때마다 온도를 재주게."}, {"운고로 분화구에 있는 크래클이 불기둥 마루에서 가장 뜨거운 자리를 찾아 달라고 했습니다.","뜨거운 열점을 찾을 때마다 온도계측장치를 오른쪽 클릭해 온도를 재십시오. 가장 뜨거운 열점을 찾을 때까지 계속 재야 합니다."}},
[975] = {"마우아리의 부적", {"이 부적을 지금 자네에게 주기 전에 사용 방법을 먼저 알려 줘야겠지?","이 부적을 가지고 다니면 자네의 사냥감들은 특별한 물건들을 떨어뜨릴 걸세. 부적이 없다면 상상도 못할 얘기지만 말이야.","그 물건들의 이름은 에코라고 한다네. 만약 찾게 되면 이 마우아리를 찾아오게. 내 도와 줄 터이니...","여기서 좀 기다리게. 내 부적을 만들어 주지."}, {"행운의 부적을 얻기 위해 다시 한번 의술사 마우아리에게 말을 걸어야 합니다."}},
[976] = {"아우버다인으로의 보급품", {"델그렌 님께서 아우버다인에 있는 파수꾼 스타브리즈에게 보급품과 서신을 전해 달라고 말씀하셨지만 난 혼자서 길을 떠나고 싶지 않다네. 숲 속엔 위험한 적들이 어슬렁거리는데 이 보급품과 중요한 정보가 혹시라도 나쁜 놈들의 손에 넘어가면 어쩌나?","만약 자네와 동료가 나를 경호해 준다면 지금 당장에라도 출발할 수 있겠네만... 그러면 이 물품을 안전하게 아우버다인으로 수송할 수 있으리라 믿어 의심치 않네."}, {"피이로가 안전하게 잿빛 골짜기를 통과하도록 경호한 뒤 마에스트라 주둔지에 있는 수호자 델그렌과 대화해야 합니다."}},
[977] = {"깜짝 기계설인 대작전", {"움직이는 기계설인의 부품을 덮을 것들이 모두 있는데, 단 한 가지만 없지 뭐예요... 사실은 두 개요. 뿔 두 개!","얼음엉겅퀴 설인은 암컷, 수컷 가릴 것 없이 큰 뿔을 갖고 있어요. 그놈들의 뿔이면 되겠는데... 여기서 남동쪽에 있는 동굴에서 살고 있으니 한번 가보세요. 엄청나게 많이 있으니까 그리 힘들지는 않을 거예요.","제일 좋은 뿔을 가져다주세요. 부러지거나 망가진 뿔은 절대로 싫다고요!"}, {"눈망루 마을에 있는 우미 럼플스니커에게 온전한 설인 뿔 2개를 가져가야 합니다."}},
[978] = {"달빛의 올빼미야수", {"동부 내륙지에 사는 올빼미야수의 깃털에 마법 흔적들이 엿보입니다. 올빼미야수는 선천적으로 마력을 타고난 것 같군요. 그러니까... 태어날 때부터 그러한 성질을 지니고 나왔다는 얘기예요.","풍문에 의하면 여명의 설원에도 올빼미야수들이 많이 서식하고 있다는데, 거기 사는 녀석들도 그런 힘이 있을지 궁금하네요. 그 해답을 찾도록 도와주실 수 없을까요, <name>? 여명의 설원으로 가서 땅에 떨어진 깃털을 모아 제게 가져다주세요."}, {"여명의 설원에서 달빛 깃든 깃털을 10개 모아서 루테란 마을의 에렐라스 엠버스카이에게 가져가야 합니다."}},
[979] = {"란샬라 찾기", {"그래, 그래! 이전에 봤던 것처럼 이 깃털에는 어떤 마법이 걸려있는 듯하오... 그게 무엇인지는 잘 모르겠지만 당신이 계속해서 도와준다면 해답을 찾을 수 있을 거요.","최근에 내 동료 하나가 올빼미야수 서식지를 관찰하러 여명의 설원으로 갔소이다. 그 친구를 찾아 뭘 알아냈는지 한번 알아보지 않겠소?"}, {"여명의 설원 동부에 있는 란샬라를 찾아야 합니다."}},
[980] = {"새로운 온천", {"여기서 북쪽 멀리 여명의 설원이라는 곳에도 온천이 있다고 들었는데, 한가지 이상한 점은 그 근처 어디에도 화산이 없다는 거지. 꽤 재미있지 않나?","더 자세히 알고 싶으면 여명의 설원으로 가서 내 친구와 얘기해 보게나. 도노바 스노우덴이라는 이름의 여자인데, 지금 그 온천 옆에서 지내고 있네.","참, 온도를 재는 걸 도와줘서 고마웠수! 그럼, 또 보세!"}, {"여명의 설원으로 가서 도노바 스노우덴과 대화해야 합니다."}},
[981] = {"알살락스의 탑", {"그대가 행한 모든 일을 칭송하건대 그대는 충분한 보상을 받아 마땅하오. 델그렌님께 돌아가 애스리쿠스 나라신을 그대 손으로 잠재운 것과 암흑이교도들이 공격을 받아 뿔뿔이 흩어진 사실을 전해주시기 바라오."}, {"마에스트라 주둔지에 있는 수호자 델그렌과 대화하십시오."}},
[982] = {"깊고 푸른 바다", {"여기서 북쪽으로, 어둠해안 근처에 난파선이 두 척 있소. 은빛 새벽호와 안개 장막호... 모두 얼마 전 아우버다인으로 들어가는 대해 위에서 몹쓸 멀록 녀석들과 충돌하였다오. 이제는 악마들의 전리품이 되어 바다 밑바닥에 가라앉았지.","그날 밤, 두 배의 선장들은 모두 살아 나오지 못했소. 항해일지와 선장들의 소지품들도 금고에 든 채 가라앉아 있소. 당신이 가서 가져와 준다면... 아직 잔해 부근에 머무르고 있는 선원들에게 큰 위안이 될 거요."}, {"아우버다인에 있는 골볼트 스틸핸드를 위해 은빛 새벽호의 금고와 안개 장막호의 금고를 회수해야 합니다. 모두 마을의 북쪽에 있는 난파선에서 찾을 수 있을 것입니다."}},
[983] = {"윙윙상자 827", {"이봐요! 내가 이번에 발명한 윙윙상자라는 기계가 있는데, 그건 멀리 떨어진 이들과 얘기를 할 수 있게 해주는 장치죠. 레버가 달린 상자 같은 건데 혹시 본 적이 있을지도 모르겠군요. 이 장치의 유일한 문제점은 꾸준히 유지 보수를 해줘야 한다는 거예요.","뭐, 세상에 모든 기계에 문제점이 한두 가지씩은 있겠지만 내가 아주 기발한 해결책을 찾았어요. 문제를 해결해 주는 부품을 가진 짐승 서식지 근처에 각 장치들을 설치하는 거였답니다.","그런데 지금 윙윙상자 827이 고장났어요. 아우버다인 남쪽, 아주 가까운 곳에 있죠. 그걸 고치려면 집게다리 6개가 필요해요. 내 부탁을 들어준다면 답례를 하죠."}, {"집게다리 6개를 모아 윙윙상자 827에 넣어야 합니다."}},
[984] = {"거대한 위협", {"우리 동료 중 일부가 텔드랏실의 오염된 펄볼그에게 잡혀 죽을 뻔한 적이 있소. 그때부터 난 우리 종족이 더는 희생되거나 아니면 그보다 더 끔찍한 일이 발생하기 전에 이 사태를 막겠다고 맹세했소.","이미 어둠해안에서 오염의 단서가 될만한 것을 몇 가지 발견했지만, 광범위한 징후는 아직 찾아내지 못했소. 펄볼그들에 대한 조사를 계속하는 게 좋을 것 같소. 놈들의 야영지를 정찰하다가 오염의 징후를 발견하면 내게 좀 알려 주겠소?"}, {"어둠해안에서 오염된 펄볼그 야영지를 찾은 다음 아우버다인에 있는 테렌디스에게 가야 합니다."}},
[985] = {"거대한 위협", {"그대의 정찰 실력은 충분히 입증되었소. 그렇다면 이제 적들에 맞서 싸우는 능력을 보여주는 건 어떻소? 정면 공격보다는 잠행과 침투가 더 적성에 맞을 수도 있지...","임무를 맡을 자신이 있다면 현재 우리에게 가장 큰 위협이 되고 있는 아우버다인 남쪽 펄볼그 야영지로 가시오. 그곳에는 검은나무 일족들이 있을 것이오. 검은나무 길잡이 8마리와 검은나무 풍수사 5마리를 처치한 다음 내게 돌아오시오."}, {"검은나무 길잡이 8마리와 풍수사 5마리를 처치한 다음 아우버다인에 있는 테렌디스에게 가야 합니다."}},
[986] = {"사라진 주인", {"그대는 이미 내게 큰 도움을 주었소. 미안하지만 한 가지 부탁이 더 있는데... 잔혹발톱과 그의 주인 볼코르를 좀 도와 주시겠소? 그 일을 하려면 그대에게 마법 망토가 필요하오. 볼코르를 찾아다니는 동안 어둠해안에 사는 짐승들에게 방해를 받지 않고 다닐 수 있도록...","망토를 만들려면 덩치 큰 달표범이나 우두머리 달표범을 잡아 고급 달표범 모피 5필을 구해와야 하오. 여기서 남쪽에 있는 성난굽이 강 근처로 가거나 아니면 더 멀리 남쪽으로 잿빛 골짜기 근처에 가면 그 맹수들을 찾을 수 있을 거요."}, {"고급 달표범 모피 5필을 찾아 아우버다인에 있는 테렌디스에게 가져가야 합니다."}},
[990] = {"잿빛 골짜기로의 여정", {"반갑습니다. 레이네 울프러너님께서 잿빛 골짜기 내의 도시 아스트라나르에서 그대를 기다리고 계십니다. 그곳의 상황이 그대의 도움으로 조금은 나아졌으면 합니다.","남쪽으로 길을 잘 따라가시면 문제없이 아스트라나르를 찾아갈 수 있을 것입니다."}, {"잿빛 골짜기에 있는 레이네 울프러너를 찾아가야 합니다."}},
[991] = {"테로니스 찾기", {"아스트라나르에서 온 제 친구인 레이네 울프러너가 서쪽에 있는 팔라딤 호수로 정찰병을 보내달라고 부탁했었어요. 테로니스는 호드가 공격해오기 바로 며칠 전에 그곳으로 떠났답니다.","그는 우리 주민들을 공격하는 펄볼그를 막을 물건을 찾고 있었는데, 그것은 지금은 죽고 없는 한 사악한 마법사가 만든 마법봉이에요.","이곳을 떠나기 전, 그는 마법봉을 사용하는 데 꼭 필요한 보석을 찾아야 한다고 말했어요. 그리고 그 보석이 팔라딤 호수의 제단에 숨겨져 있을지도 모른다고 했다는군요.","우리가 알고 있듯이 호수는 유감스럽게도 악의 무리에게 침략당했어요.","<name>, 제발 제 친구를 찾아 주세요."}, {"잿빛 골짜기에서 테로니스를 찾아야 합니다."}},
[992] = {"가젯잔 수자원 측량", {"가젯잔 수자원개발회사에 임시 후임측량사 자리가 비어 있는데 혹시 관심 있소?","유목민들이 우리 우물을 몽땅 차지하는 바람에 새로운 수원을 찾아야만 살 판이오! 황량한 사막에도 물은 있지만, 머리를 잘 굴려야만 차지할 수 있지.","이 탐사장치를 슬픈모래 감시탑 근처에 있는 물 웅덩이에 갖고 가서 물 견본을 받아 오시오. 아마 그 주변에 가면 트롤들이 있을 거요. 다 끝나거든 추출한 탐사장치를 내게 가져오시오!"}, {"슬픈모래 감시탑의 물 웅덩이 부근에서 추출 전의 탐사장치를 사용하십시오. 추출이 끝나면 가젯잔에 있는 선임측량사 피즐도저에게 추출한 탐사장치를 가져가야 합니다."}},
[993] = {"사라진 주인", {"망토가 완성됐소. 이제 볼코르를 찾으러 떠날 때가 왔소. 부디 너무 늦지 않았기를 바랄 뿐이라오.","망토의 마법은 5분 정도로 오래가지 않는다오. 그러니 신중하게 사용하도록 하시오. 내 생각엔 잔혹발톱을 찾은 후에 사용하는 게 좋을 것 같소만...","잔혹발톱이 말한 게 사실이라면 여기서 남쪽에 있는 동굴에 가면 볼코르를 찾을 수 있을 거요. 잔혹발톱이 길가에서 그대를 기다리고 있을 거요. 그를 만나면 손을 흔들어 신호를 보내시오. 그러면 볼코르가 있는 방향을 알 수 있을 것이오."}, {"어둠해안에 있는 볼코르를 찾아서 마법 달표범 망토를 줘야 합니다."}},
[994] = {"무력 탈출", {"그래요, 그렇게 합시다. 일단 준비가 되는 대로 즉시 이곳을 빠져나간 다음, 적진을 뚫고 그림클로가 기다리고 있는 길까지 가는 거요. 그곳에 이르면 각자 가도록 합시다.","테렌디스님께서 그대의 얘기를 듣고 싶어할 테니 이곳을 빠져나간 다음 아우버다인으로 곧장 가도록 하시오."}, {"길에 이를 때까지 볼코르를 보호한 다음 아우버다인에 있는 테렌디스와 대화하십시오."}},
[995] = {"은밀한 탈출", {"좋소, 그럼 여기서 몰래 빠져나갑시다. 준비가 되면 바로 출발합시다.","탈출한 후에는 아우버다인으로 돌아가 테렌디스님을 만나야 한다는 걸 명심하시오. 나는 따로 그곳으로 가겠소. 아마 당신보다는 빨리 도착할 것이오."}, {"펄볼그 동굴을 탈출한 다음 아우버다인에 있는 테렌디스를 만나십시오."}},
[996] = {"오염된 바람꽃", nil, nil},
[997] = {"데날란의 흙", {"남쪽으로 가는 길이신가요? 알아메스 호수로요? 그렇다면 부탁할 일이 있는데...","제 동료인 데날란은 그 호수의 동쪽 가장자리 부근에 야영지를 설치하고 텔드랏실의 식물 생태를 연구하지요. 다르나서스에서 온 귀한 흙 한 꾸러미를 보내 달라고 요청했는데 물건이 최근에야 이곳 돌라나르에 도착해서 많이 늦어지고 말았죠.","이 꾸러미를 데날란에게 좀 가져다주시겠어요?"}, {"알아메스 호수에 있는 데날란에게 귀한 흙 꾸러미를 가져가야 합니다."}},
[998] = {"오염된 바람꽃", nil, nil},
[999] = {"꿈이 악몽으로 바뀌면", nil, nil},
[1000] = {"새로운 미개척지", {"주목하시오!","세나리온 의회에서는 칼림도어의 광활한 미개척지를 탐험할 건장하고 능력 있는 자들을 찾고 있소! 타나리스와 가젯잔에서 서쪽으로 멀리 떨어진 곳에는 상상도 못할 위험이 도사리는 금단의 황무지가 펼쳐져 있는데, 세나리온 의회에서는 그 일을 대신해줄 명예롭고 뜻있는 지원자들을 찾고 있는 것이오.","관심이 있는 자들은 모두 세나리온 의회에 알현을 요청하도록 하시오. 더 자세한 얘기를 듣고 싶은 자는 썬더 블러프의 장로의 봉우리에 있는 대드루이드 하뮬 룬토템을 찾아가 보시오!"}, {"썬더 블러프의 장로의 봉우리에 있는 대드루이드 하뮬 룬토템을 찾아가 세나리온 의회의 칼림도어 미개척지 탐험 지원자 모집에 대해 대화해야 합니다."}},
[1001] = {"윙윙상자 411", {"기계에서 작은 소리가 들립니다.","\"위즈뱅이에요! 다음 윙윙상자는 아우버다인 북쪽의 해변 어딘가에 있어요.","그 윙윙상자의 번호는 411이고 수리하려면 트레샤돈의 눈 3개가 필요해요. 해안 주변에서 찾을 수 있죠. 어둠해안 트레샤돈들... *딸꾹* 너무 수고스러운 부탁을 하는 것 같군요.","지난번처럼 윙윙상자에 눈 3개를 넣으면 기계에서 뭔가 좋은 게 보상으로 튀어나올 거예요. *딸꾹* 네? 아, 아녜요. 전 괜찮아요!\""}, {"윙윙상자 411 근처 깊은 바다에 있는 어둠해안 트레샤돈에게서 트레샤돈의 눈 3개를 모아야 합니다."}},
[1002] = {"윙윙상자 323", {"윙윙상자에서 들리는 잡음에 위즈뱅의 목소리가 섞여 있습니다.","\"다음 윙윙상자는 323번이고, 아우버다인 북쪽에 있죠. 그런데 내 술병이 어디 갔더라? 이런... 참, 다리 근처에 있으니까 쉽게 찾을 수 있을 거라고요.\"","위즈뱅이 무언가 잘 알아들을 수 없는 말을 한 후에 술 따르는 소리가 크게 들립니다.","\"이번에는 달표범 송곳니...6개...\""}, {"달표범 송곳니 6개를 모아 윙윙상자 323에 넣어야 합니다."}},
[1003] = {"윙윙상자 525", {"기계에서 나는 끓는 듯한 소리와 물 떨어지는 소리 너머 위즈뱅의 혀 풀린 목소리가 들려 옵니다.","\"다음 거는 힉... 길을 따라 딸꾹... 남쪽으로 한참 가야 해요. 찾기가 그리 힘들진 딸꾹... 않을 거예요. 길 옆 딸꾹... 울타리 근처에 있거든요. 내가 덤불 속에 힉... 숨겼던 것 같아요. 덤불 속에... 딸꾹... 헤헤헤... 그래요, 딸꾹... 윙윙상자 525에는 엉겅퀴불곰의 가죽을 딸꾹... 집어넣어야 해요. 딸꾹... 윙윙상자 525는 꽤나 많이 먹어야 한다고요. 힉... 헤헤헤...\"","기계가 멈추었다가 꿀꺽거리는 소리가 더 나더니 갑자기 기계가 다시 돌아가기 시작합니다.","\"4개는 있어야지!\""}, {"아우버다인 남쪽에 있는 엉겅퀴불곰에게서 불곰 가죽 4개를 모아 윙윙상자 525에 집어넣어야 합니다."}},
[1004] = {"새로운 미개척지", {"영웅은 들으십시오!","세나리온 의회에서는 칼림도어의 광활한 땅을 개척할 건장하고 실력있는 모험가들을 찾고 있습니다! 타나리스와 가젯잔에서 서쪽으로 멀리 떨어진 곳에 펼쳐져 있는 금단의 황무지에는 상상도 못할 위험이 도사리고 있습니다. 세나리온 의회를 대신해 개척 임무를 맡아줄 용맹스럽고 뜻있는 지원자들을 찾고 있는 것입니다.","관심이 있는 이들은 모두 세나리온 의회에 알현을 청하십시오. 썬더 블러프, 장로의 봉우리에 있는 대드루이드 하뮬 룬토템을 찾아가면 자세한 얘기를 들을 수 있습니다!"}, {"썬더 블러프, 장로의 봉우리에 있는 대드루이드 하뮬 룬토템을 찾아가 세나리온 의회의 칼림도어 개척 지원자 모집에 대해 대화해야 합니다."}},
[1005] = {"문 너머에 있는 적", nil, nil},
[1006] = {"문밖의 적", nil, nil},
[1007] = {"고대 조각상", {"<class>|1이여;여;, 그대에게 부탁할 일이 있소.","일전에 작은 배를 타고 물속에 가라앉은 조람의 폐허 위를 지나고 있을 때 나가들이 물속에서 튀어나와 발톱으로 날 공격했소! 나는 그들을 피해 도망쳤지만, 지금 중요한 건 그게 아니오.","나는 고고학자라오. 그래서 잃어버린 도시에서 유물을 되찾고 싶소.","서남쪽의 조람 해안가로 향해 고대 조각상을 찾아봐 주겠소? 내가 조람 해안의 위험을 무릅썼던 건 바로 그 조각상 때문이라오."}, {"고대 조각상 10개를 모아야 합니다."}},
[1008] = {"조람 해안", {"조람 해안으로 알려진 북서쪽 바닷가에 악당들이 숨어 있습니다.","그곳은 대륙이 분리되는 동안 파괴되어 바다 밑에 가라앉은 도시, 조람이 잠들어 있는 곳이랍니다. 오랜 세월 동안 나이트 엘프는 조람을 생각하지 못하고 지내 왔습니다. 아니, 조람이라는 도시에 대해 거의 잊어버렸었다고 할 수 있겠군요.","그런데 지금, 무슨 이유에서인지 모르지만 나가들이 다시 돌아왔습니다. 그들이 돌아온 이유는 별로 중요하지 않습니다만 반드시 그들을 섬멸해 버려야 합니다!","임무를 완수한 후 내게 돌아와 주십시오."}, {"아스트라나르에 있는 신드렐 스위프트파이어에게 성난지느러미 머리 20개를 가져가야 합니다."}},
[1009] = {"루젤", {"그대가 이 고대 조각상에 있는 자물쇠의 열쇠를 찾지 못하면 이 조각상의 비밀은 영원히 풀리지 않을 것이오.","사실 그 열쇠는 조람의 반지라고 한다오. 그 반지는 대륙이 분리되서 이 도시가 물속에 수장되기 전, 도시의 지배자가 소유하던 것이오.","최근 나가들이 몇 년 동안이나 행방이 묘연했던 그 반지를 발견했소.","그들의 우두머리인 루젤은 조람의 북쪽 변방에 있는 한 섬에 살고 있소. 날 위해 반지를 꼭 좀 찾아 주시오."}, {"조람 해안 근처에 있는 탈렌에게 조람의 반지를 가져가야 합니다."}},
[1010] = {"배스랜모초", {"길 건너 동쪽에 있는 배스랜 서식지의 오래된 폐허에서 자라는 풀이 있소. 배스랜모초라는 약초인데 영혼의 병을 치료한다고 알려졌다오.","그런데 지금 호드가 그곳에서 약초를 악한 목적으로 사용하려고 마구 캐내고 있소.","마을에 병든 아이가 하나 있는데, 내가 보기엔 단순히 몸에만 병이 든 것은 아닌 것 같소. 스승님께 그 아이를 치료할 방법을 찾아보겠다고 약속드렸지만, 나는 싸우는 방법을 모르오.","그대가 배스랜 서식지로 가서 배스랜모초를 좀 찾아다 주지 않으시겠소?"}, {"배스랜모초 8개를 수집해야 합니다."}},
[1011] = {"포세이큰의 역병", {"언데드 호드 군대인 포세이큰이 잿빛 골짜기로 숨어들어와 이곳 폐허와 언덕을 어슬렁거리고 있소. 우리 생각엔 그들이 나이트 엘프의 땅에 질병을 퍼뜨리려는 것 같소. 이건 꼭 확인해 봐야 할 문제요.","포세이큰들은 남쪽의 도르다닐 지하굴 근처에 야영지를 설치하고 독약과 질병을 만들어 병 안에다 담고 있소. 그 야영지로 들어가 역병 견본을 훔쳐 가져다주시오. 그러면 내가 실험을 통해 그 용도를 밝혀내겠소."}, {"숲의 노래에 있는 케이네스 스틸윈드에게 역병 견본 하나를 가져가야 합니다."}},
[1012] = {"미쳐버린 드루이드", {"숲의 심장을 타락시킨 호드들에 의해 자제력을 상실한 지도자들이 드루이드들을 죽이고 있소!","타락의 기운이 이곳에까지 느껴지니 나는 감히 더 다가갈 수가 없겠소.","나는 잿빛 골짜기와 숲의 심장에 너무 익숙해져 있으니, 당신... 당신이 지하굴로 내려가서 해야 할 일을 처리해 주시오. 당신이 일을 쉽게 처리할 수 있도록 그곳에 준비해 두었소.","가슴이 무너지는듯하지만, 한 때는 위대한 지도자였던 타닐 다크우드, 우실 문콜과 마보리스 클라우즈브레이크를 처치해 주시오."}, {"드루이드 지도자인 타닐 다크우드, 우실 문콜, 마보리스 클라우즈브레이크를 처치해야 합니다."}},
[1013] = {"우르의 책", {"그림자송곳니 성채에 우르의 책이 보관되어 있소. 그 책은 내 소장 목록에 포함할 만한 가치가 충분한 것이지. 우르는 스컬지가 도래하기 전 달라란에서 가장 훌륭한 마법사였소이다. 다른 세계에 대한 그의 연구는 포세이큰의 몇몇 무리에게는 매우 가치가 높지.","그림자송곳니 성채에 들어가 이 책을 찾아 주시오. 그걸 내게 가져오면 학문 연구에 공헌한 당신에 대해 어둠의 여왕님께 보고하도록 하겠소이다."}, {"언더시티의 연금술 실험실에 있는 관리인 벨두거에게 우르의 책을 가져가야 합니다."}},
[1014] = {"아루갈의 최후", {"아루갈이 아직 그림자송곳니 성채에 버젓이 살아 있소.","아루갈을 처치하기 전에는 은빛소나무 숲을 어둠의 여왕님을 위한 전략 요새로 차지할 수 없소. 그의 마법은 내가 완전히 없애겠지만, 아루갈이 한 짓에 마땅한 형벌을 받도록 하는 것은 당신 손에 맡기겠소.","그림자송곳니 성채로 가서 아루갈의 사악한 마법 주문을 영원히 잠재워 버리시오. 그리고 그 사악한 마법사의 머리카락을 내게 가져오도록 하시오."}, {"아루갈을 처치하고 그 증거로 그의 머리카락을 공동묘지에 있는 달라 돈위버에게 가져가야 합니다."}},
[1015] = {"새로운 미개척지", {"영웅은 들으십시오!","세나리온 의회에서는 칼림도어의 광활한 땅을 개척할 건장하고 실력 있는 모험가들을 찾고 있습니다! 타나리스와 가젯잔에서 서쪽으로 멀리 떨어진 곳에 펼쳐져 있는 금단의 황무지에는 상상도 못할 위험이 도사리고 있습니다. 세나리온 의회를 대신해 개척 임무를 맡아줄 용맹스럽고 뜻있는 지원자들을 찾고 있습니다.","관심이 있는 이들은 모두 세나리온 의회에 알현을 청하십시오. 다르나서스에 있는 대드루이드 판드랄 스태그헬름을 찾아가면 자세한 얘기를 들을 수 있습니다!"}, {"다르나서스의 세나리온 자치령에 있는 대드루이드 판드랄 스태그헬름을 찾아가 세나리온 의회의 칼림도어 미개척지 탐험 지원자 모집에 대해 대화해야 합니다."}},
[1016] = {"미스트랄 호수 수복", {"호드가 미스트랄 호수에 있는 아군의 물의 정령 수비대를 차지하지 않았다면 실바람 산장을 쉽게 방어할 수 있었을 것입니다.","호드의 주술사 중 한 명이 타이더레스와 숲 속에 있는 물의 정령들을 소환했습니다. 이 정령들은 우리 정령들을 타락시켜 우리와 맞서게 한 고대의 격노한 존재들이지요. 우리는 미스트랄 호수의 정령들과 호드의 압박 때문에 이곳 별가루 첨탑으로 후퇴할 수밖에 없었습니다.","동쪽으로 가십시오. 호수를 되찾아 언젠가 우리가 실바람 산장을 되찾을 수 있도록 해 주십시오."}, {"타락한 물의 정령 12마리와 타이더레스를 처치해야 합니다."}},
[1017] = {"물의 정령 배후의 마법사", {"불모의 땅에서 이렇게 많은 물의 정령을 조종할 수 있는 언데드 마법사는 한 명밖에 없습니다. 이런 짓을 하려 들 만큼 어리석은 자도 그자뿐이고... 바로 사릴루스 파울본이라는 자인데, 아마 지금 공포안개 봉우리 꼭대기에 앉아 날 비웃고 있을 겁니다.","적진으로 가서 이 악당을 반드시 처치하고, 파수대를 괴롭히면 어떻게 되는지 똑똑히 보여 줘야 합니다. 이미 그자의 바보 같은 만행은 충분히 겪었으니까요.","사릴루스를 처치하고 그 증거를 가지고 오십시오. 하지만 조심하시기 바랍니다. 호드가 그대를 노리고 있을지도 모르니..."}, {"사릴루스 파울본을 처치하고 그 증거로 그의 머리카락을 실바람 산장에 있는 파수꾼 벨레네 스타스트라이크에게 가져가야 합니다."}},
[1018] = {"새로운 미개척지", {"주목하시오!","세나리온 의회에서는 칼림도어의 광활한 미개척지를 탐험할 건장하고 능력 있는 자들을 찾고 있소! 타나리스와 가젯잔에서 서쪽으로 멀리 떨어진 곳에는 상상도 못할 위험이 도사리는 금단의 황무지가 펼쳐져 있는데, 세나리온 의회에서는 그 일을 대신해줄 명예롭고 뜻있는 지원자들을 찾고 있는 것이오.","관심이 있는 자들은 모두 세나리온 의회에 알현을 요청하도록 하시오. 더 자세한 얘기를 듣고 싶은 자는 썬더 블러프의 장로의 봉우리에 있는 대드루이드 하뮬 룬토템을 찾아가 보시오!"}, {"썬더 블러프의 장로의 봉우리에 있는 대드루이드 하뮬 룬토템을 찾아가 세나리온 의회의 칼림도어 미개척지 탐험 지원자 모집에 대해 대화해야 합니다."}},
[1019] = {"새로운 미개척지", {"영웅은 들으십시오!","세나리온 의회에서는 칼림도어의 광활한 땅을 개척할 건장하고 실력 있는 모험가들을 찾고 있습니다! 타나리스와 가젯잔에서 서쪽으로 멀리 떨어진 곳에 펼쳐져 있는 금단의 황무지에는 상상도 못할 위험이 도사리고 있습니다. 세나리온 의회를 대신해 개척 임무를 맡아줄 용맹스럽고 뜻있는 지원자들을 찾고 있습니다.","관심이 있는 이들은 모두 세나리온 의회에 알현을 청하십시오. 다르나서스에 있는 대드루이드 판드랄 스태그헬름을 찾아가면 자세한 얘기를 들을 수 있습니다!"}, {"다르나서스의 세나리온 자치령에 있는 대드루이드 판드랄 스태그헬름을 찾아가 세나리온 의회의 칼림도어 미개척지 탐험 지원자 모집에 대해 대화해야 합니다."}},
[1020] = {"오렌딜의 치료제", {"병든 아이를 위한 치료제를 만들었소. 이걸 아스트라나르로 가져가 그 애 아버지인 펠투러스 화이트문이라는 이에게 전해 주시기 바라오.","아스트라나르로 가려면 남쪽으로 길을 따라 가다가 동쪽으로 가면 되오."}, {"아스트라나르에 있는 펠투러스 화이트문에게 오렌딜의 치료제를 가져가야 합니다."}},
[1021] = {"사악한 사티로스와 위험에 처한 드리아드", {"저기요, 뉘신지 잘 모르겠지만 도움이 필요합니다. 우리 자매들이 사티로스에게서 세나리우스의 가지를 되찾으러 갔어요. 금방 돌아올 거라면서 갔지만 꼬박 이틀이나 지났어요.","<name>, 우리 자매들을 좀 찾아봐 주세요. 그들은 멀리 북서쪽 사비안의 나이트 엘프 신전의 폐허로 갔어요. 무슨 문제가 생겼는지도... 모르겠어요. 아닐리아를 찾아보세요. 그녀가 자매들을 이끌고 갔으니까요."}, {"잿빛 골짜기의 사비안에서 아닐리아를 찾아야 합니다."}},
[1022] = {"울부짖는 골짜기", {"북쪽 악령숲에 남아 있는 악마들을 몰아내려고 많은 자원과 시간을 투자해왔지만 아직 이렇다 할 성과를 거두지 못하고 있습니다. 어쨌든 악마로부터 잿빛 골짜기를 지켜낼 수는 있었지만 말입니다.","북쪽 악령숲 가장자리 부근에 폐허가 된 멜탄드리스의 제단이 있는데 괴이한 늑대 인간들에게 점령당하고 말았습니다. 그들의 무시무시한 울음소리 때문에 그 지역은 울부짖는 골짜기라고도 한답니다. 그 제단에 보관된 멜탄드리스의 마법책을 손에 넣으면 늑대 인간들이 온 이유를 알아내는데 도움이 될 겁니다."}, {"울부짖는 골짜기로 가서 멜탄드리스의 마법책을 찾아 읽어 본 다음 숲의 노래에 있는 파수꾼 멜리리아 프로스트섀도에게 가야 합니다."}},
[1023] = {"잃어버린 보석", {"테로니스의 소지품을 뒤져보니 아직 상태가 양호한 일지가 나왔습니다.","테로니스의 시체는 그대로 두고 일지만 챙깁니다.","이제 테로니스가 찾고 있던 보석만 찾으면 됩니다. 오렌딜은 그 보석이 이 지역에 있다고 말했습니다. 어쩌면 지금은 촉수들이 가지고 있을지도 모릅니다."}, {"빛나는 보석을 찾아야 합니다."}},
[1024] = {"드리아드 찾기", {"아, 당신이군요, <name>. 테로니스의 일지를 살펴보았는데, 아무래도 당신이 다음으로 할 일은 다르톨이 만든 마법봉의 나머지 조각들을 찾아야 할 것 같군요.","우선 레인나무 탑으로 향하는 길을 따라 동쪽으로 가십시오.","그곳에서 셸드린이라는 드리아드를 찾으면 그대에게 큰 도움을 줄 겁니다.","보석을 잘 챙겨가도록 하십시오. 셸드린의 도움으로 마법봉을 다시 만들려면 반드시 필요할 것입니다."}, {"잿빛 골짜기의 레인나무 탑에 있는 셸드린과 대화해야 합니다."}},
[1025] = {"공격적 방어", {"달샘으로 출발하기 전에 잿빛 골짜기 주민들의 안전을 위해 제 부탁을 들어주시지 않겠습니까? 그대의 능력을 시험해 보길 원한다면 말입니다.","동쪽으로 난 길을 따라 가다가 팔파렌 강에 못 미친 곳에서 펄볼그들이 여행자들을 공격하고 있습니다. 그들의 비뚤어진 영혼은 공포를 즐기며 무고한 희생자들의 피로 배를 불리고 있답니다.","그들을 처치해 지금까지 우리에게 해온 죗값을 치르게 해주십시오. 우리 센티널이 이 땅을 지킬 수 있도록 좀 도와주시기 바랍니다.","길 남쪽에 놈들의 본거지가 있을 겁니다. 부디 조심하시길..."}, {"썩은나무 보초 1마리, 썩은나무 펄볼그 2마리, 썩은나무 토템술사 10마리 및 썩은나무 전사 12마리를 처치한 다음 아스트라나르에 있는 레이네 울프러너에게 가야 합니다."}},
[1026] = {"레이네의 정화", {"좀 더 좋은 소식이었으면 좋았을 것을...","레이네의 말이 맞습니다. 전 다르톨이 만든 마법봉과 이 지역 내에 있는 나무정령에 대해 잘 알고 있답니다. 하지만 지금은 그 성질이 변질되고 오염되어 예전에 제가 알고 있던 것과는 많이 다릅니다.","마법봉의 다른 조각을 구하려면 우선 그 조각이 들어 있는 상자의 열쇠를 찾아야 합니다.","나무로 만들어진 그 열쇠는 이곳에서 북동쪽 방향, 울부짖는 골짜기의 악령숲 근처에 있는 나무정령 중 하나가 가지고 있습니다. 열쇠를 구하면 악령숲에 있는 동굴을 지나 작은 빈터를 찾아보시기 바랍니다. 상자는 거기 어딘가에 숨겨져 있을 것입니다."}, {"나무 열쇠를 찾아 다르톨의 마법봉의 한 부분인 철제 자루를 얻는 데 사용해야 합니다."}},
[1027] = {"죽은 척하기", {"마법봉의 마지막 조각은 발톱의 드루이드들이 가지고 있습니다. 그들의 지도자인 오소 브램블스카에게 그것을 달라고 했다가 그와 그의 수호병들에게 거의 죽을뻔 했답니다! 확실이 그들도 호드가 숲의 심장을 더럽힌 것에 영향을 받았을 것입니다.","제게 오소의 동굴로 들어가서 마법봉의 마지막 조각을 가져올 수 있는 좋은 생각이 있습니다.","제가 동물이었을 때부터 죽은척 하기에는 일가견이 있었답니다. 특히 곰을 상대로 말이에요! 그대에게 이 죽은척 기술을 알려드리겟습니다! 그늘 동굴 동쪽으로 가서 드루이드들이 그대를 죽이려고 하면 제 죽은척 기술을 사용하세요."}, {"다르톨의 마법봉을 만들기 위한 마지막 조각인 철제 머리장식을 획득해야 합니다."}},
[1028] = {"레이네의 정화", {"자, 여기 완성된 마법봉을 받으십시오. 마법봉을 복구하는 건 별로 어렵지 않았지만 마법봉에 다시 마력을 불어넣을 수 있는 제단을 찾는 건 쉽지 않을 겁니다.","하지만 이미 많은 일을 해낸 그대와 같은 분이 조금만 더 노력하면 이번 일도 그리 어렵지는 않을 것 같군요.","이곳에서 남동쪽으로 가면서 계속 북쪽을 잘 살펴보도록 하십시오. 길을 찾기 힘들 테니 말입니다. 제단은 빈 터를 둘러싼 산 속에 숨겨져 있답니다.","그곳에 도착한 후에는 마법봉을 제단 안에 놓기만 하면 됩니다. 나머지는 마법에 의해 저절로 이루어질 테니... 후훗."}, {"숨겨진 제단을 찾아 다르톨의 마법봉에 다시 마력을 불어 넣어야 합니다."}},
[1029] = {"레이네의 정화", {"레이네를 찾아 마법봉이 완성되었다고 알려 주시기 바랍니다. 그대가 해야 할 다음 일은 레이네가 알고 있을 겁니다.","행운을 빕니다."}, {"아스트라나르에 있는 레이네 울프러너에게 돌아가십시오."}},
[1030] = {"다르톨의 마법봉", {"테로니스 다음으로 하려던 일이 확실히 무엇이었는지는 모르겠군요. 그렇지만 테로니스가 마법봉의 힘에 대해 했던 말이 사실이라면, 마법봉은 그대를 완전히 다른 모습으로 바꿔 줄 거예요. 추측해보건대 펄볼그로 바뀔지도 모르겠어요.","어쩌면 그는 마법봉의 힘을 빌려 오염된 펄볼그들에게 접근하려 했는지도 모르겠어요. 하지만 아무리 그렇다고 해도 조심하지 않으면 그 짐승들에게 죽임을 당할 수도 있었을 텐데... 다르톨은 마법봉으로 자신의 모습을 바꿔 오염되지 않은 펄볼그와 대화하려고 한 것 같습니다.","크롤그라고 알려진 자부터 알아보는 게 좋겠군요. 미스트랄 호수의 남동쪽에 있는 크롤그의 오두막을 찾아보세요."}, {"다르톨의 마법봉의 힘을 사용해 잿빛 골짜기의 크롤그의 오두막에 있는 크롤그와 대화해야 합니다."}},
[1031] = {"세나리우스의 가지", {"아닐리아는 떨리는 손으로 당신의 어깨를 잡아 끌어당깁니다. 가쁜 숨을 몰아쉬며 북쪽의 아치형 문을 가리킨 후 결국 숨을 거둡니다. \"겔타리스... 그가 갖고 있어요...\" 드리아드에게 세나리우스의 가지를 찾아 주는 것이 아닐리아의 마지막 소원이라는 것을 마음으로 느낄 수 있습니다."}, {"겔타리스를 처치하고 아에시나의 제단에 있는 일리야나에게 세나리우스의 가지를 돌려줘야 합니다."}},
[1032] = {"사티로스 처치", {"아닐리아가 죽었다는 사실이 정말 믿기지 않는군요... 우리는 늘 숲 속에서 함께 뛰놀곤 했죠. 정말 슬퍼요.","이 슬픔을 달랠 수 있는 것이 하나 있다면, 바로 복수랍니다. 부디 제게서 아닐리아를 빼앗아간 그 사티로스들을 처치해 주세요. 복수의 증거로 놈의 뿔을 가져다주신다면 평생 당신의 은혜를 잊지 못할 거예요."}, {"사티로스의 뿔 16개를 획득해야 합니다."}},
[1033] = {"엘룬의 눈물", {"오렌딜의 치료제로 렐라라가 깨끗이 낫기를 바랐지만, 병이 심해지는 것을 늦추기만 할 뿐인 것 같습니다. 어서 더 확실한 효과를 가진 치료제를 찾아야 합니다! 다행스럽게도 오렌딜이 이 치료제가 듣지 않을 때를 대비한 치료법을 이야기해 줬습니다.","엘룬의 눈물은 돌의 일종으로, 아스트라나르 북동쪽에 있는 천둥 봉우리의 폭발로 파괴된 아이리스 호수의 잔해에서만 찾을 수 있습니다. 오렌딜은 그 돌이 렐라라의 병을 치료해 줄 것이라고 했습니다.","제발 엘룬의 눈물을 제게 가져다주십시오. 그리고 그 엘룬의 눈물이라는 돌로 제 딸의 병을 고칠 수 있기를 기도해 주십시오."}, {"엘룬의 눈물을 입수해야 합니다."}},
[1034] = {"별가루의 폐허", {"엘룬의 눈물로 우리 딸의 병이 좀 나았어요. 딸을 괴롭히던 악몽도 사라졌고요. 신께 감사할 따름입니다. 하지만... 아직 열이 내리지 않고 있어요.","오렌딜이 별가루의 폐허에 있는 얼어붙은 별가루에 대한 얘기를 해주었어요. 그걸로 렐라라의 열을 내릴 수 있을 겁니다. 별가루를 좀 모아서 제게 가져다주세요.","별가루의 폐허는 남쪽에 있어요. 엘룬께서 당신이 속히 다녀올 수 있도록 인도해 주시길..."}, {"아스트라나르에 있는 펠투러스에게 별가루 5줌을 가져가야 합니다."}},
[1035] = {"유성 호수", {"열은 내렸지만 렐라라의 몸이 아직 많이 쇠약한 상태예요. 시간이 지난다고 우리 딸의 기력이 회복될 것 같지는 않군요. 딸이 완쾌하려면 월장석의 힘이 필요해요.","예전에는 남동쪽에 있는 유성 호수에서 월장석을 캐곤 했답니다. 그늘덤불일족들이 나타나기 전까지는요.","놈들의 우두머리인 그늘덤불일족 현자가 유성 월장석을 독차지하고 있어요. 그 현자에게서 월장석을 좀 빼앗아다 주세요.","월장석을 얻으려면 그를 처치해야 할 것입니다."}, {"그늘덤불 나무현자에게서 유성 월장석을 빼앗아서 아스트라나르에 있는 펠투러스에게 가져가야 합니다."}},
[1036] = {"붉은해적단과의 만남", {"<name> 님... 네. 당신 얘기를 들은 적이 있습니다. 당신도 우리 붉은해적단처럼 무법항에서 환영을 받지 못한다고 들었죠. 피랠론 함장님은 당신처럼 원기 왕성한 <남자/여자>를 찾아 특별 임무를 맡기고 싶어하십니다.","관심 있거든 주홍빛 장막호로 가서 갑판 아래 있는 피랠론 함장님과 얘기해 보십시오. 주홍빛 장막호는 동쪽 거친 해안에 있는 격정의 파도호와 아가씨의 행운호 옆에 있습니다."}, {"가시덤불 골짜기 해안에 정박해 있는 주홍빛 장막호에서 함장 피랠론과 대화해야 합니다."}},
[1037] = {"벨린데 스타송", {"벨린데 스타송은 이곳 잿빛 골짜기 숲에서 내 전임자로 일했어요. 처음에는 악령숲을 그런대로 잘 수호했던 것 같지만 조금씩 그녀의 노력도 소용없어지게 되었습니다. 그러던 어느 날 그녀가 사라져 버렸고 그녀의 후임자로 내가 여기에 오게 된 것이죠.","하지만 난 그 여사제에 대해서 아는 게 없습니다. 파수대 사령관 중 한 분인 신텔 블레이드위버께서는 그녀의 실종에 대해 내가 모르는 진실을 알고 있을지도 모르겠군요."}, {"다르나서스의 전사의 정원에 있는 신텔 블레이드위버와 대화해야 합니다."}},
[1038] = {"벨린데의 영향", {"멜탄드리스의 마법책을 보았단 말인가요? 그렇다면 그대가 벨린데송의 물건을 조사해도 괜찮을 것 같군요. 그녀의 물건들은 파수꾼 합숙소에 있는 상자 안에 넣어 두었습니다. 여기 그 상자의 열쇠를 드리지요. 그녀는 일을 하는 중에 일지를 계속 적었으니, 그걸 조사해 보면 뭔가 알아낼 수도 있을 것 같군요.","한 가지 말해 둘 건, 센티널에서는 그녀에게 그래야 할 이유가 있었기 때문에 떠났지만 곧 다시 돌아올 거라고 믿고 있습니다. 이 여사제는 오랫동안 많은 업적을 쌓아왔기 때문에 큰 신뢰를 얻고 있어요."}, {"벨린데의 상자에서 일지를 찾은 다음 열쇠와 함께 다르나서스에 있는 신텔 블레이드위버에게 돌려주어야 합니다."}},
[1039] = {"불모의 땅 항구", {"불모의 땅에서 항구는 톱니항 하나뿐이에요. 십중팔구 벨린데는 톱니항에서 상선을 얻어 타고 동부 왕국의 검은바다 만으로 갔을 거예요. 그 항구를 운영하는 고블린들과 우리는 제한적인 정보만 주고 받기 때문에 잘 모르지만, 항구의 주임은 분명 출입했던 승객에 대한 정보를 가지고 있을 거예요.","잿빛 골짜기를 통해 남서쪽으로 길을 따라가면 불모의 땅에 이르게 될 거예요. 그 지역에는 호드의 병사들이 순찰을 돌고 있으니 조심하세요. 하지만 항구에서는 안전할 거예요."}, {"톱니항에 있는 부두주임 디지위그와 대화해야 합니다."}},
[1040] = {"무법항으로의 여정", {"아! 드디어 찾았네. 그녀가 이곳을 지나간 지 이렇게 오래됐다고 말해 줬더라면 더 빨리 찾았을 것 아닌가. 어디 보자...","벨린데라... 무법항행 검은 물수리호를 예약했었구먼. 여기 다른 기록은 없으니 배가 항구에 무사히 도착했을 거라고 생각되네.","큰 도움이 못 되어 미안하지만, 그녀가 그 지역으로의 육로 여행에 대해 물었고 내가 무법항에 주로 다니는 행상 러즈고트에 대해 이야기해 준 게 기억이 나는군. 이 벨린데라는 여자가 그와 함께 여행을 했을지도 모르겠군.","이제 어서 가 보게. 나도 이렇게 하루종일 자네와 노닥거리고 있을 수만은 없으니..."}, {"무법항으로 배를 타고 가서 행상 러즈고트와 대화해야 합니다."}},
[1041] = {"행상길", {"내가 오해를 했네요. 이런 일이 자주 있는 게 아니라서...","당신이 찾는 벨린데라는 여자는 기억하고 있지. 이 짐마차는 더럽고 낡았지만 물건을 실어 나르는 데 더할 나위 없소. 하지만 나이트 엘프 여사제가 이런 짐마차를 타고 여행을 하는 일은 거의 없으니까 말이야.","북쪽 길에서 헤어졌는데 그 여사제는 다크샤이어 쪽으로 갔지. 그 곳에 있는 서기관이 모든 기록을 관리하고 있으니 당신에게 도움이 될 만한 사실을 알고 있을지도 모르겠군.","정글에서는 몸조심하시오. 가장 평화로운 때조차도 아주 위험한 곳이니까."}, {"다크샤이어에 있는 서기관 달트리와 대화하십시오."}},
[1042] = {"카레빈 일가", {"다크샤이어에 머물고 있는 벨린데 스타송이라는 이에 대한 기록은 없습니다. 하지만 어느 날 밤에 엘프 여사제가 여관에 방을 잡았던 일이 어렴풋이 기억나긴 하는데...","그래도 당신이 말한 그 늑대인간에 대해서는 들은 바가 있어요. 요 전날 칼로가 녀석들을 처치한 증거를 들고 마을로 왔었죠. 그는 악마나 언데드 같은 괴물을 사냥하는 카레빈 일가와 함께 일하고 있어요. 카레빈 일가의 수장인 조나단과 얘기해 보시죠."}, {"다크샤이어에 있는 조나단 카레빈과 대화해야 합니다."}},
[1043] = {"엘룬의 낫", {"당신 이야기는 사실인 듯하군. 당신의 목적이 뭔지는 모르지만, 숲에서 늑대인간을 몰아내기 위해 이곳 그늘숲에 왔다면 우리로선 나쁠 것 없소.","남쪽에 늑대인간들이 들끓는 광산이 하나 있소이다. 놈들은 모습을 잘 드러내지 않지만, 우리가 아는 한 그곳이 놈들을 처음 발견한 장소요.","자, 이제 가서 할 일을 하시오. 하지만 한 가지 말해둘 건, 뭔가 중요한 걸 발견하면 내게도 알려줘야 한다는 거요. 악의 무리에 맞서기 위해 어떠한 정보나 도움이든 환영이요."}, {"엘룬의 낫의 흔적을 찾은 다음 어둠골에 있는 조나단 카레빈에게 가야 합니다."}},
[1044] = {"해결된 의문", {"늑대인간 문제는 우리가 해결할 것이니 걱정할 것 없소. 당신 친구가 우리 숲에 몰고 온 악의 세력은 제거될 테고, 그녀의 행동에 대해 반감을 보이지도 않을 거요.","요즘은 이상한 일들이 자주 일어나고 있소이다. 어둠의 세력은 휴식이라는 걸 모르는가 보오. 이제 가 보시오. 당신이 그늘숲의 광산에서 발견한 사실에 대한 얘기를 반드시 들어야 할 이들이 있을 것이오."}, {"다르나서스에 있는 신텔 블레이드위버에게 가야 합니다."}},
[1045] = {"레이네의 정화", {"오래전에 죽은 마법사인 다르톨은 나이트 엘프나 오크가 그랬던 것처럼 마법봉을 이용해 우리를 굴복시켜 파괴하려고 했던 것이 확실하오. 그렇지만 이 마법봉을 의도하지 않던 방도로 사용할 수 있을 것 같소.","랜 블러드투스는 잿빛 골짜기에 사는 모든 펄볼그들의 왕이오. 그의 부족인 붉은송곳니 부족은 가장 강력한 힘을 가지고 있으며, 동쪽에 있는 야영지에 숨어살고 있소.","그들을 처치하고 랜 블러드투스의 해골을 내게 가져다 주시오. 그 해골을 올바른 목적에 사용할 수 있는지 한번 봅시다."}, {"랜 블러드투스의 해골과 다르톨의 마법봉을 획득해야 합니다."}},
[1046] = {"레이네의 정화", {"당신 일족에게로 돌아가 이곳에서 당신이 거둔 승리에 대해 알리시오.","이 전리품을 갖고 돌아가 자랑하면서 마음껏 기쁨을 누리시게..."}, {"아스트라나르에 있는 레이네 울프러너에게 랜 블러드투스의 해골과 다르톨의 변신마법봉을 가져가야 합니다."}},
[1047] = {"새로운 미개척지", {"영웅은 들으십시오!","세나리온 의회에서는 칼림도어의 광활한 땅을 개척할 건장하고 실력 있는 모험가들을 찾고 있습니다! 타나리스와 가젯잔에서 서쪽으로 멀리 떨어진 곳에 펼쳐져 있는 금단의 황무지에는 상상도 못할 위험이 도사리고 있습니다. 세나리온 의회를 대신해 개척 임무를 맡아줄 용맹스럽고 뜻있는 지원자들을 찾고 있습니다.","관심이 있는 이들은 모두 세나리온 의회에 알현을 청하십시오. 다르나서스에 있는 대드루이드 판드랄 스태그헬름을 찾아가면 자세한 얘기를 들을 수 있습니다!"}, {"다르나서스의 세나리온 자치령에 있는 대드루이드 판드랄 스태그헬름을 찾아가 세나리온 의회의 칼림도어 미개척지 탐험 지원자 모집에 대해 대화해야 합니다."}},
[1048] = {"붉은십자군 수도원으로", {"어둠의 여왕님께서는 북쪽으로 전진해 오고 있는 리치 왕의 군대에 대한 급박한 상황에 신경을 쏟고 계시다. 난 그동안 좀 포세이큰 내적인 일들을 돌보고 있지.","두말할 것 없이, 자칭 붉은십자군이라는 말썽꾸러기 인간들이 우리 지역 내에서 가장 큰 골칫거리가 되고 있단 말이야. 티리스팔 숲은 말 그대로 이 조그만 불개미 같은 녀석들로 우글거리고 있어.","수도원으로 가라, <class>|1이여;여;. 가서 종교재판관 화이트메인과 그녀의 고위 부관들을 제거하도록 하라."}, {"종교재판관 화이트메인, 붉은십자군 사령관 모그레인, 붉은십자군 용사 헤로드와 사냥개조련사 록시를 처치한 후 언더시티에 있는 바리마트라스에게 돌아가 보고해야 합니다."}},
[1049] = {"타락의 개요", {"대지 고리회에서는 역병에 걸린 우리의 새로운 동맹에게 순수한 의도를 갖고 있었지. 하지만 장로 회의의 귓전에 대고 속닥거리는 자들의 의도를 어떻게 안단 말인가?","우리가 동맹을 맺은 포세이큰은 기만으로 가득 찬 역사가 있다네. 너무 서둘러서 조약을 맺었지. 케른님께서 오그리마의 경고를 듣는 게 좋았을지도 몰라.","붉은십자군 수도원 깊숙한 곳에 인간 광신도들이 보호하는 타락의 개요라는 책이 있네. 그들의 방식은 광기에서 비롯된 것이지만 그 연구 내용은 쓸모가 있을 것일세."}, {"티리스팔 숲의 붉은십자군 수도원에서 타락의 개요를 입수해 썬더 블러프에 있는 현자 트루스시커에게 가져가야 합니다."}},
[1050] = {"티탄 신화", {"미래는 피할 수 없습니다! 우리는 대해에 떨어지는 빗방울처럼 미래를 향해 힘차게 나아가야 합니다.","하지만 과거를 알기 때문에 앞일을 내다보고 미지의 세계에 좀 더 안전하게 대비할 수 있는 것인지도 모릅니다.","로데론에 비극적인 분란이 일어나기 전 티리스팔 숲에는 지식의 보루가 안치되어 있었습니다. 소문에 의하면 티탄 신화라는 책이 옛 수도원의 도서관에 소장되어 있었다고 합니다.","그 책을 저에게 가져오면 함께 과거의 수수께끼를 풀 수 있을지도 모릅니다!"}, {"수도원에서 티탄 신화라는 책을 찾아 아이언포지에 있는 사서 메이 페일더스트에게 가져가야 합니다."}},
[1051] = {"보렐의 복수", {"비록 내 의지는 자유롭다 하나 더 이상 어둠의 여왕님을 섬길 수가 없소. 아제로스에서의 내 명은 다 끝난 것 같군. 내 생명의 힘도 쇠한 것 같소.","심문관 비샤스가 내게 이런 짓을 했소! 수 주간 고문을 하고 내 결혼 반지를 빼앗아 자기 아내에게 줘 버린 몹쓸 녀석! 복수... 난 복수를 원하오!","그 바보 같은 녀석이 로다미어 호수에 사는 자기 아내 낸시에 대해 자랑을 늘어놓았소. 그 여자를 찾아 처치하고 내 결혼 반지를 타렌 제분소에 사는 아내 모니카에게 되돌려주시오.","내 마지막 소원이오..."}, {"타렌 제분소에 있는 모니카 센구츠에게 보렐의 결혼반지를 가져가야 합니다."}},
[1052] = {"붉은십자군의 길", {"우리 붉은십자군은 하스글렌에서 티리스팔 숲에 이르기까지 여러 요새에 대한 소유권을 주장하고 있습니다. 또한 우리는 로데론 곳곳에 있는 정화의 보루들을 매우 자랑스럽게 생각합니다.","<형제/자매>께서 잊혀진 땅에서 언데드를 상대로 잘 싸워 주었지만 정말 큰 위협은 로데론에 있습니다.","동부 왕국에 있는 남녘해안의 마을로 가십시오. 그리고 경건한 신자 랄레이라는 십자군을 찾도록 하시기 바랍니다. 그에게 내 인장이 있는 이 추천서를 주면 우리 붉은십자군 수도원에 있는 영예로운 장소로 <형제/자매>를 안내해 줄 것입니다."}, {"남녘해안에 있는 경건한 신자 랄레이에게 수사 안톤의 추천서를 가져가야 합니다."}},
[1053] = {"빛의 이름으로!", {"나도 한때는 자부심을 지니고 명예롭게 충성을 바쳐 붉은십자군을 따랐었소. 아제로스에서 언데드를 몰아낸다는 그들의 목표가 고귀한 것이라고 믿었으니까 말이오.","하지만 티리스팔 숲의 수도원에서 지내면서 그들이 갈수록 현실을 제대로 파악하지 못하고 있다는 것을 깨닫게 되었소. 이제 그들은 붉은십자군 외투를 입지 않은 이는 모두 악의 세력에 물든 것이라고 믿고 있지. 무고한 남녀가 악의 세력에 물들었을 거라는 이유로 고문을 당했소.","빛의 이름으로 붉은십자군을 처단해야 하오. 당신이 이 미친 집단을 꼭 처치해 주시오."}, {"종교재판관 화이트메인, 붉은십자군 사령관 모그레인, 붉은십자군 용사 헤로드와 사냥개조련사 록시를 처치한 후 남녘해안에 있는 경건한 신자 랄레이에게 돌아가 보고해야 합니다."}},
[1054] = {"위협 제거", {"아스트라나르 바로 북쪽에 엉겅퀴 펄볼그가 있습니다. 우리 정찰병에 따르면 지난 몇 달 사이 그 수가 크게 늘어났다고 합니다. 너무 많이 늘어나면 아스트라나르를 직접 공격하려 들 수도 있어 위험합니다.","우선 펄볼그들을 가능한 많이 처치하고 그 우두머리의 해골을 가지고 와 주십시오. 진압해주신 대가는 후히 드릴 것입니다.","난 이곳 아스트라나르에 남아 그대의 승전 소식을 기다리도록 하겠습니다. 엘룬께서 그대와 함께하시기를..."}, {"아스트라나르에 있는 레이네 울프러너에게 달 블러드클로의 해골을 가져가야 합니다."}},
[1055] = {"레이네의 정화", {"이제 완성된 마법봉이 당신 손 안에 있습니다. 셸드린이 당신이 성공했다는 것을 듣고 싶어할 것입니다."}, {"달샘에 있는 셸드린에게 돌아가십시오."}},
[1056] = {"돌발톱 봉우리로의 여정", {"숲의 정령들이 그대는 모험을 두려워하지 않을 만큼 용맹스럽다고 말해주더군.","남쪽으로 미스트랄 호수에서 그리 멀지 않은 곳에 갈퀴자국 토굴길이 있소. 이 길을 통과하면 돌발톱 산맥의 칼바람 바위산이라는 곳에 이르게 되오. 그곳에 도착해 남서쪽으로 가다가 바위웅덩이 호수를 지나 북쪽으로 가파른 언덕을 오르시오. 그러면 돌발톱 봉우리에 다다를 거요.","그곳에 수호자 알바고름이 그대를 기다리고 있으니 그의 말을 잘 따르시오. <race>|1이여;여;!","위험이 도사리고 있는 힘든 여행이 될 것이오."}, {"돌발톱 봉우리에 있는 수호자 알바고름을 찾아가야 합니다."}},
[1057] = {"잿더미 계곡 복원", {"그대는 내가 숲의 정령을 지키는 자로서 왜 지금의 사태를 우려하는지 잘 알고 있을 거요.","산맥을 넘어 이 봉우리까지 오는 동안 이 땅에 행해진 가혹한 일들을 보았을 걸로 아오. 이 신성한 언덕까지 탐욕의 손길에 의해 더럽혀져서 파멸의 길로 치닫고 있소. 투자개발회사에서 보낸 기계가 남쪽 잿더미 계곡을 불태운데다가 혈폭풍 일족들이 이 땅을 침범해 숲을 가꾸기 위한 노력을 무참히 짓밟고 있는 실정이오.","이 괘씸한 하피들을 당장 몰아내도록 하오!"}, {"돌발톱 봉우리에 있는 수호자 알바고름이 혈폭풍 하피 7마리, 혈폭풍 복병 7마리, 혈폭풍 암살자 7마리, 그리고 혈폭풍 약탈자 7마리를 처치해 달라고 했습니다."}},
[1058] = {"진질의 숲의 마법", {"정말 흥분되는군!","저기 갇혀 있는 세 마리 귀여운 엘프들 좀 보라고! 음, 저 사랑스러운 친구들로 뭘 할 수 있을까? 흠, 저들이 숲의 마법이라 부르는 것을 조금... 맛보여 주는 건 어떨까...!","그러려면 돌발톱 수액이 꽤 많이 필요하겠군. 그리고 무시무시한 황혼의 흑표범에게서 황혼의 수염을 구해오고... 참! 순록의 눈도 있어야 해. 순록의 눈은 많으면 많을수록 좋지. 그리고 귀한 마룡 비늘도 잊으면 안 돼.","자, 어서 돌발톱 봉우리로 가서 내가 말한 것들을 구해오도록 해. 어서 숲의 마법을 써보고 싶단 말이야!"}, {"말라카진에 있는 의술사 진질이 돌발톱 수액 5개, 황혼의 수염 5개, 순록의 눈 30개와 마룡 비늘 1개를 가져다 달라고 했습니다."}},
[1059] = {"잿더미 계곡 복원", {"일단 혈폭풍 일족을 제압하기는 했지만 다른 조치를 취하지 않으면 곧 다시 규합할 거요.","불모의 땅에서 남쪽으로 멀리 버섯구름 봉우리라는 곳이 있소. 그곳에서 서쪽으로 가다가 페랄라스의 울창한 숲 경계에 이르면 팔핀델 웨이워더라는 드루이드를 만나게 될 거요. 이 현명한 드루이드는 잿더미 계곡의 복원을 도울 위대한 숲의 정령을 소환할 수 있소.","가서 우리가 그의 도움을 필요로 한다는 사실을 전하시오! 자, 어서 그를 찾아 떠나도록 하시오!"}, {"버섯구름 봉우리와 페랄라스의 경계에 있는 팔핀델 웨이워더를 찾아가야 합니다."}},
[1060] = {nil, {"지금까지 임무를 아주 훌륭히 수행해 줬어. 자네는 행운을 부르는 자로군. 자, 이제 먼 길을 가야 할 때일세.","원래 하피는 불모의 땅에 살지 않았어. 멀리 북서쪽에 있는 돌발톱 산맥이라는 곳에서 왔지. 위험한 일이란 건 알지만 이 편지를 거기 사는 내 오랜 벗, 진질이라는 의술사에게 전해주겠나?","붉은깃털 세레나가 죽었다는 소식을 들으면 그도 아주 기뻐할 거야. 그리고 자네도 돌발톱 산맥이 얼마나 흥미로운 곳인지 알게 될 것이고..."}, {"돌발톱 산맥의 동굴에서 야영을 하는 진질에게 달소크의 편지를 전달해야 합니다."}},
[1061] = {"돌발톱 산맥의 정령", {"고블린들과 그 하수인들이 약탈과 방화를 일삼는 것에 대해 돌발톱 산맥의 정령들이 분노하고 있네. 그로 인해 땅은 울부짖고 있고, 이러한 사실은 자연의 소리를 듣는 모든 이들에게 전해졌다네.","그래서 호드의 촉망 받는 주술사, 시리스 스톤브레이크가 돌발톱 산맥으로 파견되었네. 그녀는 고블린들이 저지르고 있는 대규모 삼림 훼손에 대해 보고하면서 도움을 청했지.","지금 돌발톱 산맥으로 가서 시리스를 만나게. 불모의 땅의 십자로에서 서쪽 길을 따라 가면 되지. 큰소나무 계곡 근처에서 야영을 하고 있다네."}, {"불모의 땅과 돌발톱 산맥의 경계에 있는 시리스 스톤브레이크와 대화해야 합니다."}},
[1062] = {"고블린 침략자", {"고블린이 운영하는 투자개발회사가 돌발톱 산맥으로 몰려와 함부로 나무를 베고 숲을 태우고 있어요. 숲의 정령들은 고통과 분노로 눈이 멀 지경이니 투자개발회사를 반드시 막아야 해요!","북서쪽으로 가세요. 큰소나무 계곡 너머 칼바람 바위산까지 말이에요. 고블린과 그 하수인들이 일하고 있을 테니, 녀석들에게 우리 호드가 돌발톱 산맥에서 그들이 행한 악행을 그냥 두고만 보지는 않으리라는 걸 똑똑히 일깨워 줘요. 가장 확실히 깨우칠 수 있는 방법으로...","호드의 힘을 보여줘요!"}, {"투자개발회사 나무꾼 15명을 처치한 다음 돌발톱 산맥과 불모의 땅의 경계에 있는 시리스 스톤브레이크에게 가야 합니다."}},
[1063] = {nil, {"돌발톱 산맥의 정령들을 수호하려면 우리의 동맹인 타우렌에게 조언을 구해야 해요. 원로들이라면 지혜를 나눠줄 거예요.","타우렌 족장들을 많이 알지는 못하지만 나이 많고 지혜로운 마가타라는 족장은 알고 있답니다. 그녀에게서 타우렌 종족의 지혜를 얻을 수 있을 거예요.","마가타는 타우렌의 수도 썬더 블러프의 북동쪽 절벽에 있는 장로의 봉우리에 살고 있어요. 그분을 만나거든 위험에 처한 돌발톱 산맥의 상황에 대해 전해 주세요."}, {"썬더 블러프에 있는 마가타와 대화해야 합니다."}},
[1064] = {"포세이큰의 원조", {"타우렌은 이 땅을 지켜야 할 의무가 있네. 그래서 더더욱 돌발톱 산맥의 상황에 대한 소식은 내겐 고통스럽군그래. 이 땅을 치유하려면 먼저 만연한 질병을 제거해야만 하네.","이러한 상황에 포세이큰이 우리 동맹이란 사실이 다행스럽지 않은가? 그들은 질병에 대한 지식이 해박하니 우릴 도울 수 있을 걸세. 그렇게 함으로써 서로 간의 신뢰를 강화할 수 있게 될 테고 말이야.","연금술사 자마를 만나 보게. 그녀는 학자이자 포세이큰의 밀사이기도 하지. 정기의 봉우리 아래에 있는 예언의 웅덩이에 가면 만날 수 있을 걸세."}, {"썬더 블러프의 예언의 웅덩이에 있는 연금술사 자마와 대화해야 합니다."}},
[1065] = {"타렌 제분소로의 모험", {"돌발톱 산맥에서 투자개발회사를 몰아내려면 극단의 조치를 취해야만 하오. 피를 흘리지 않으면서도 더 없이 강력한 방법을 말이지.","우리 동료 중 하나가 이 일에 꼭 알맞은 도구를 가지고 있소. 그의 이름은 라이던, 먼 로데론 땅의 언덕마루 구릉지에 있는 타렌 제분소 마을에 살고 있소이다.","언덕마루로 가려면 비행선을 타고 우리의 수도인 언더시티로 가시오. 그런 다음 북쪽의 티리스팔 숲으로 가서 남서쪽의 은빛소나무 숲으로 간 후 다시 남쪽의 언덕마루로 가면 되오.","라이던을 찾아 이 쪽지를 전해 주시오. 우리가 필요로 하는 사항에 대해 자세히 적어 두었소이다."}, {"타렌 제분소에 있는 연금술사 라이던에게 자마의 쪽지를 가져가야 합니다."}},
[1066] = {"순결한 피", {"내가 투자개발회사 놈들에게서 폭력적인 성향을 뽑아내 놈들을 약하고 순하게 할 독약을 만들 것이오. 그러면 돌발톱 산맥에서 호드가 당면한 문제를 해결할 수 있지 않겠소?","하지만 독약을 만들려면 순결한 자들의 피가 필요한데 상당히 얻기 힘든 재료지. 다행히도 이 지역에 비밀결사대라는 인간 도둑 무리가 어둠마법사를 고용하고 있는데... 그들은 뭔가 불순한 목적에 사용할 순결한 피를 모으고 있소.","비밀결사대 어둠마법사 녀석들은 남동쪽에 있는 던홀드 요새에서 찾을 수 있을 거요."}, {"타렌 제분소에 있는 연금술사 라이던에게 순결한 피가 든 유리병 5병을 가져가야 합니다."}},
[1067] = {"썬더 블러프로의 귀환", {"자, 여기 독약이 있소. 투자개발회사 놈들에게 이 독약을 사용하면 좋은 결과를 얻게 될 거야. 잘하면 놈들 사이에서 전염될지도 모르고... 내가 조제한 독약이 현장에서 실제로 사용되는 걸 보는 건 정말 신나는 일이지!","하지만 사용하기 전에 일단 연금술사 자마에게 가져가도록 하시오. 그녀가 독약의 효과를 극대화할 수 있는 운반 방법을 마련해 줄 것이오."}, {"썬더 블러프에 있는 연금술사 자마에게 라이던의 독약을 가져가야 합니다."}},
[1068] = {"벌목기", {"주술사의 영적 마법과 드루이드의 자연 마법에는 연관이 있어요. 그래서 우리 주술사들이 돌발톱 산맥의 정령들이 하는 말을 들을 수 있는 거죠. 자연의 정령들이 속삭이는 말들을 다 알아들을 수는 없지만, 한 가지 계시... 그 환영만은 아주 또렷하게 기억해요.","큰소나무 계곡 북서쪽의 칼바람 바위산에서 인간 모양을 한 기계 두 대가 무서운 속도로 나무를 쓰러뜨리는 것을 보았어요.","그리고 기계에는 각각 XT:4와 XT:9라는 식별번호가 표시되어 있었어요. 이 기계들은 반드시 파괴되어야만 해요!"}, {"벌목기 XT:4와 XT:9를 파괴한 다음 돌발톱 산맥과 불모의 땅의 경계에 있는 시리스 스톤브레이크에게 가야 합니다."}},
[1069] = {nil, {"거미 알 오믈렛은 무법항에서 새로 유행하고 있는 음식이지. 그런데 문제는... 거미 알 공급이 시원찮단 거야.","그래서 난 이걸 기회로 삼으려고 해!","여기서 북서쪽으로 돌발톱 산맥의 칼바람 바위산과 시쉬르 협곡에는 동굴이끼거미들이 살고 있어. 내게 그 거미의 알을 가져다준다면 값을 두둑이 쳐 주지.","칼바람 바위산에서 그 거미들은 나무 아래 모여 있을 거야.","시쉬르 협곡에는... 흠, 자네 비위가 무지 좋은 편이 아니라면, 칼바람 바위산에서나 열심히 일하라고."}, {"톱니항에 있는 메보크 미지릭스에게 동굴이끼거미알 15개를 가져가야 합니다."}},
[1070] = {"돌발톱 산맥에서의 임무", {"엘룬께 영광을...","방랑벽이 꽤 있어 보이는데 혹시 돌발톱 산맥에 가본 적이 있습니까? 그곳은 투자개발회사와 다른 적들에 의해 심하게 훼손당한 땅이랍니다.","얼마 전 레이네 울프러너가 제 친구 캘라 섀도스피어를 그리로 보내 그 땅에서 무슨 일이 일어나고 있는지 조사하도록 했지만 아직 그녀로부터 아무 소식이 없습니다. 그녀를 보낼 때 얼라이언스에서 노움 마법사 한 명도 붙여 보냈기 때문에 더 걱정입니다. 노움들은 항상 문제를 일으키니까요.","그녀를 찾은 다음 내게 알려 주시겠습니까?"}, {"돌발톱 산맥에 있는 칼바람 바위산 입구에 계곡이 내려다보이는 곳에 캘라 섀도스피어를 찾아야 합니다."}},
[1071] = {"노움의 부탁", {"보다시피 난 비전술, 연금술 및 기계공학 연구회의 회원이지. 내 목표... 아니, 내 임무는 나이트 엘프에게 마법과 약물, 그리고 과학을 합치면 숲을 더 안전하게 수호할 수 있다는 사실을 보여 주는 것이오.","하지만 이건 매우 버거운 전쟁인데다 고블린은 쥐새끼들처럼 그 수가 불어나고 있소. 그래서 난 요즘 머릿속에서 설계한 물건들을 제작할 시간조차 없지 뭐요!","그래서 내가 발명 계획을 짜고 준비를 좀 할 수 있도록 당신이 좀 도와줬으면 하는데 어떻소? 동쪽에 있는 칼바람 바위산의 고블린 놈들을 꼼짝 못하도록 주시오."}, {"투자개발회사 나무꾼 10명 및 투자개발회사 벌채꾼 10명을 처치하고 돌발톱 산맥에 있는 각심 러스트피즐에게 가야 합니다."}},
[1072] = {"오랜 동료", {"내가 생각하고 있는 건 최신형 폭탄이란 말이오. 하지만 특별한 물약이 필요하지. 이번이 마지막 부탁이 될 거요. 이제 곧 근사한 걸 만들어낼 거란 말이지.","니트로미르글리세로니움... NG-5이라는 폭발성 물질이 필요해...","이 NG-5를 만들 수 있는 유일한 인물은 로막 기어스트립이라는 내 오랜 친구뿐이지. 땜장이 마을에 찾아가 보시오.","그에게 NG-5를 만들어 달라고 해서 가져오면 내 당장에라도 제작을 시작할 수 있지."}, {"아이언포지에 있는 로막 기어스트립과 대화해야 합니다."}},
[1073] = {"어리석음 + 화학 물질 = 재미", {"하하하... 고블린이 숲을 파괴하는 일을 막으려고 니트로미르글리세로니움이 필요하단 말이오?","당신이 각심을 얼마나 잘 아는지 상관없으니... 내 니트로미르글리세로니움을 주지. 폭발로 산산조각나는 게 당신이 될지 녀석들이 될지 갑자기 보고 싶어졌거든.","그런데 문제는 니트로미르글리세로니움이 다 떨어졌다는 거요. 하지만 당신이 적당한 물약들을 가져다준다면 즉시 만들어 주도록 하겠소.","내 발명품 중 가장 훌륭한 물건을 얻으려면 적어도 연금술사로서의 자질을 조금은 보여 줘야 하지 않겠소?"}, {"아이언포지에 있는 로막 기어스트립에게 최하급 마나 물약 4개와 최하급 인내의 비약 2개를 가져가야 합니다."}},
[1074] = {"어리석음 + 화학 물질 = 재미", {"자, 다 됐소. 이방인 친구여, 까딱하면 폭발하는 물질이니까... 오, 조심... 살살 다루라고.","당신이 정말 각심의 친구라면 내가 안부 전하더라고 해주시오. 아니면 폭발 구경이나 잘하시구려. 당신이 날아가 버릴지도 모르지만..."}, {"돌발톱산맥에 있는 각심 러스트피즐에게 니트로미르글리세로니움을 가져가야 합니다."}},
[1075] = {"마우렌의 두루마리", {"자, 이제 계획을 완성하려면 원격 폭파 장치가 남았는데... 아, 아직 그 부분을 설명을 안 해 줬군.","간략하게 이렇다오. 약간의 마법을 사용하면 당신이 몸을 피한 후 폭발이 나도록 할 수 있다고. 심호흡 한번 하고 단추를 누르면 NG-5가 폭발하도록 장치를 만들어 주겠다는 뜻이오.","하지만 그러려면 스톰윈드에 있는 친구의 마법 주문이 필요해. 그 친구의 이름은 콜린 마우렌이라 하오. 스톰윈드 마법사 지구로 가면 그를 만날 수 있지. 그는 인간이니까 쉽게 알아볼 수 있을 거라오."}, {"스톰윈드에 있는 콜린 마우렌을 찾아야 합니다."}},
[1076] = {"서부 몰락지대의 악령", {"아, 내게 그런 주문이 있는 것 같습니다. 교신이라는 주문인데... 그가 찾는 마법 주문이 맞을 겁니다.","두루마리를 만들어 그 주문을 걸어야 할 것 같군요. 그러려면 먼지 악령으로부터 파편을 얻어야 합니다.","내가 다른 재료를 준비하는 동안 서부 몰락지대로 가서 그 사악한 것들을 사냥하고 파편을 구해다 주시면 고맙겠군요. 위험한 녀석들이긴 하지만 각심의 실험에 기꺼이 동참할 정도의 담력을 지녔다면 쉽게 상대하리라 믿습니다.","여기서 다시 만나도록 합시다."}, {"스톰윈드에 있는 콜린 마우렌에게 먼지 악령의 파편을 가져가야 합니다."}},
[1077] = {"각심을 위한 특별한 선물", {"자, 다 됐습니다. 이거면 될 겁니다.","각심을 만나면 안부나 전해 주십시오. 이번 모험의 결과가 어떨지 기대하고 있겠습니다.","부디 몸조심하십시오."}, {"돌발톱 산맥에 있는 각심 러스트피즐에게 교신의 두루마리를 가져가야 합니다."}},
[1078] = {"마우렌 돕기", {"여행자들이 계속 내게 돌발톱 산맥에 대해 묻는 걸 보니 모험가들에게는 유명한 곳인가 본데... 와이번을 찾는 이, 정령을 찾는 이, 아니면 투자개발회사에 볼일이 있는 이, 정말 다양하답니다.","예전에 돌발톱 산맥 깊은 곳, 잿더미 계곡에 바실리스크들이 살았는데 그 비늘을 가루로 만들면 내가 만든 몇 가지 주문에 맞는 훌륭한 시약이 될 겁니다. 아직 그 바실리스크들이 살고 있다면 내게 그 비늘을 좀 구해다 주십시오.","급할 건 없으니 서두르지 않아도 됩니다만 사례는 후하게 해 드리도록 하죠."}, {"스톰윈드에 있는 콜린 마우렌에게 비늘 결정체 8개를 가져가야 합니다."}},
[1079] = {"비밀 작전 - 1단계", {"이제 다 됐소. 사용 방법을 설명할 테니 잘 들으시오.","이 설명서를 받으시오. 폭탄과 기폭 장치뿐만 아니라 설치 장소까지 자세히 나와 있소. 이번 임무의 성패는 폭파량에 의해 결정되는 게 아니란 사실을 명심하시오. 바위웅덩이 호수 남쪽에 있는 제재소에서 투자개발회사의 토목 공사 계획서를 빼내 오느냐 아니냐에 모든 것이 달려 있다오."}, {"칼바람 바위산에 있는 제재소에서 투자개발회사의 토목 공사 계획서를 찾아 와야 합니다."}},
[1080] = {"비밀 작전 - 2단계", {"2단계 작전은 1단계와 비슷하오. 혼란을 야기시킨 다음 녀석들에게서 중요한 편지를 빼내 오는 것이지.","이번 계획의 핵심은 녀석들의 편지를 입수하는 것임을 명심하시오. 이미 당신에게 준 설명서에 폭탄을 설치할 장소가 표시되어 있다오. 편지를 가지고 무사히 돌아오길 바라오."}, {"칼바람 바위산의 동쪽 끝에 있는 칼바람 광산에서 투자개발회사의 편지를 찾아와야 합니다."}},
[1081] = {"티란데의 환대", {"티란데 위스퍼윈드 님께서 그대가 이 임무에 동참해 준 데 대한 감사를 직접 표하고 싶어하십니다.","그대의 도움은 겉으로 확실히 드러나는 것은 아니지만 돌발톱 산맥에서 우리의 발판을 아주 확고히 해 줬습니다.","그대가 두 눈으로 확인한 끔찍한 일들에 맞서 우리와 함께할 생각이라면 다르나서스에 있는 달의 신전에서 티란데 님을 만나 보도록 하세요."}, {"다르나서스에서 티란데 위스퍼윈드를 찾아야 합니다."}},
[1082] = {"파수꾼 테니실", {"그대는 이곳에서 정말 큰 도움이 되었어요.","잿빛 골짜기에 주둔하고 있는 센티널에 각심과 제가 살아 있다는 소식을 전해주세요. 우리가 투자개발회사를 저지하고 이 땅을 치유하는 임무를 잘 수행하고 있다고 전하면 그대에게 충분한 보상을 해 줄 거예요.","아스트라나르에 가서 테니실을 찾아 보세요. 이 소식을 가지고 돌아가면 그녀가 무척 기뻐할 거예요."}, {"잿빛 골짜기에 있는 파수꾼 테니실에게 돌아가십시오."}},
[1083] = {"분노한 정령들", {"투자개발회사가 잿더미 계곡에서 한 짓이 뭔지는 확실치 않지만 그곳에 살고 있는 존재들에게 제압당하고 있는 것이 확실한 것 같아요.","그들은 숲에 불을 지르고 기계를 사용해 닥치는 대로 계곡을 파괴했죠. 이 무지한 놈들이 저질러 놓은 일 때문에 난폭한 불의 정령들과 화염의 파괴자, 화염의 약탈자들이 풀려나고 말았어요.","그 정령들이 활개를 치는 한 이제 그 땅에서 엘룬의 미소를 다시는 볼 수 없을 거예요.","이들을 처치하고 그 증거로 그들의 꺼져 가는 불씨를 가져오도록 하세요."}, {"돌발톱 산맥에 있는 캘라 섀도스피어에게 잿더미 계곡에 있는 불의 정령들로부터 얻은 꺼져 가는 불씨 8개를 가져가야 합니다."}},
[1084] = {"상처 입은 고대정령", {"잿더미 계곡의 수호자 고대정령들이 투자개발회사를 몰아내기 위해 뭔가 행동을 취하려 했던 게 확실합니다. 하지만 불행히도 위대한 고대 수호자였던 그들이 오염되고 말았어요.","각심의 말에 의하면 그들이 오염된 건 고블린이 후퇴하면서 사용한 어떤 무기와 관련이 있다고 해요.","그것이 사실인지 확인하고 싶어요. 고대정령들에게서 무기의 파편을 모아 주세요. 각심에게 연구용으로 보내야 해요.","이렇게 부탁드립니다. 뭔가 알아낼 수 있도록 제게 파편을 좀 가져다주세요."}, {"돌발톱 산맥에 있는 캘라 섀도스피어에게 부식된 파편 1개를 가져가야 합니다."}},
[1085] = {"돌발톱 산맥에서의 임무", {"이 땅의 안전을 위해 함께 일하는 나이트 엘프와 노움에게 있어 지금 돌발톱 산맥의 상황은 걱정스럽기만 해요.","우리는 이 땅을 황폐화시키고 있는 투자개발회사를 막기 위해 이곳에 온 것이랍니다. 우릴 도울 생각이라면 각심과 먼저 얘기해 보세요. 각심의 겉만 보고 너무 나쁘게만 보지 마세요. 그래도 협력의 필요성은 잘 알고 있으니까요.","타라에게 좋은 소식을 전하지 못해 유감이군요. 하지만 곧 그럴 수 있을 거예요."}, {"각심과 대화하십시오."}},
[1086] = {"비행장", {"당신이 가져다준 독약으로 독가스통을 만들었소. 이제 갖다 놓기만 하면 되는데 이 일은 당신이 해 주시오.","투자개발회사에는 분명 본부와 돌발톱 산맥에 있는 작업장 사이에서 전갈이나 수화물을 전달하는 수단이 있을 것이오. 조그마한 비행장 같은 게 아닐까 싶은데... 큰 비행선이 드나들 만큼 크지는 않지만 아마 작은 비행기라면 가능할 것이오. 돌발톱 산맥에서 투자개발회사가 관할하는 지역을 수색해서 이 비행장이 정말로 있는지 찾아내시오.","비행장을 찾으면 이 독가스통을 그곳에 놓아두시오. 독가스통을 놓고 재빨리 빠져나와야 하오."}, {"투자개발회사의 비행장에 독가스통을 갖다 놓고 연금술사 자마에게 돌아와야 합니다."}},
[1087] = {"세나리우스의 유산", {"제가 이 비참한 땅을 얼마나 혐오스러워하는지 모를 거예요. 산봉우리에 있는 세나리우스 일족과 칼도레이 드루이드들이 그 알량한 힘으로 마법의 흐름을 막고 있어요. 그들이 그렇게도 아끼는 저 남쪽 멀리 있는 숲에 이르기까지 말이에요.","직접 제데나르로 돌아가서 그 녀석들을 처리하고 싶지만, 임무를 완수하지 못하면 돌아올 생각은 꿈에도 꾸지 말라는 스승님 말씀 때문에 발길이 떨어지질 않네요.","당신도 나만큼이나 나이트 엘프와 그 동맹을 미워하는 것 같으니, 날 도와서 이 임무를 좀 완수해주세요."}, {"해바위 야영지 근처에 있는 브랠린 파이어핸드를 위해 세나리우스의 후예, 세나리우스의 딸, 세나리온 식물학자들을 각각 4명씩 처치해야 합니다."}},
[1088] = {"오르다누스", {"그들은 마법의 흐름을 막을 수 있다고 자신했지만 오산이었어요. 그리고 오르다누스도 숨길 수 있다고 생각하는 모양이지만 반드시 찾아내고 말 거예요!","마법의 힘으로 제 마음의 눈이 떠졌으니 그 능력을 이용해 보도록 하죠.","어디 보자... 그가 있는 곳이 보이는군요. 교차로가 있고... 잿빛 골짜기 숲에 있는 거대한 나무가 보여요... 아, 그 꼭대기에 있군요. 그 나무의 이름은 라이네라고 해요. 숲의 정령들이 섬기는 신이 낳은 첫 번째 아들이죠. 이런, 벌써 그 꼭대기에서 다른 곳으로 이동했군요. 시간이 얼마 지나지 않았으니 멀리 가지는 못했을 거예요.","제게 오르다누스의 머리 장식을 가져다주면 큰 선물을 드리지요."}, {"오르다누스를 처치하고 그 증거로 그의 머리 장식을 해바위 야영지 근처에 있는 브랠린 파이어핸드에게 가져가야 합니다."}},
[1089] = {"갈퀴발톱굴", {"드루이드들은 돌발톱 봉우리에 갈퀴발톱굴을 건설하고 강력한 마력이 담긴 물건들을 다량으로 보관하고 있어요.","그들은 그 물건이 다른 이들의 손에 들어가는 것을 막기 위해 복잡한 건축기법을 이용해 미로를 설계했죠. 전 운 좋게도 그 건축가 중 한 명에게서 굴의 구조에 대한 정보를 좀 빼냈어요.","갈퀴발톱굴의 입구 바로 옆에 놓인 작은 등불에 문지기의 열쇠가 보관되어 있을 거예요. 그 열쇠만 있으면 동굴 곳곳에 있는 은닉처를 열 수 있답니다. 그 안에 있는 어떤 물건을 사용하면 동굴 중앙에 있는 저장고를 열 수 있어요."}, {"돌발톱 봉우리에 있는 갈퀴발톱굴로 가야 합니다. 문지기의 열쇠를 이용해 드루이드의 숨겨진 물건을 획득해야 합니다. 그 물건을 이용해 갈퀴발톱굴 저장고를 여십시오."}},
[1090] = {"게렌조의 지시", {"이곳에서 한때 난 2인자 위치에까지 올랐지만 게렌조가 부임하면서 모든 게 변했소. 그는 내 상관 지즈 피직스 님을 해고하고 나는 광산에서 광물이나 캐라고 내려보냈지. 지즈 님께서 여기 남아서 정보를 수집하라고 하셨기에 참고 있는 것이야. 새로운 상관은 게렌조를 수상히 여기고 그가 꾸미고 있는 계획이 무엇인지 밝혀낸다면 충분한 보상을 하신다고 했소.","어쨌든 내가 아는 건 게렌조가 황녹색 빛 광석을 다량 채굴하도록 지시했다는 거요. 지즈 님께 보낼 표본을 구해 볼 테니 그동안 순찰하는 녀석들이 오는지 망을 좀 봐 주시오."}, {"피즈닉이 캐낸 미확인 광석을 받아야 합니다."}},
[1091] = {"캘라를 위한 새로운 정보", {"센티널에서는 이번 일에 대해 확실히 알고 싶어할 테니 이 편지를 캘라에게 가지고 가시오.","그녀에게 우리가 얼마나 성공적으로 일을 처리했는지, 그리고 마법의 힘을 조금 빌리지 않았더라면 결코 성공할 수 없었으리란 점도 전하도록 하시오."}, {"캘라 섀도스피어에게 투자개발회사의 편지를 가져가야 합니다."}},
[1092] = {"게렌조의 지시", {"이제 여기서 빠져 나갈 궁리를 하는 게 좋을 것 같군. 이곳에서 일어난 일이 곧 윗선까지 알려질 것이고 다시는 나를 우호적으로 맞아 주지 않을 것이오.","나는 여기서 나가 지즈님을 만날 생각이오. 지즈님이 내게 새로운 일을 맡겨 준다면 좋으련만...","난 이 쓰레기들을 좀 치울 테니 당신은 지금 바로 광석 표본을 지즈님에게 가져가시오. 지즈님은 칼바람 바위산 서쪽 어딘가에 야영지를 세우셨다고 들었소. 행운을 빌어요."}, {"돌발톱 산맥에 있는 지즈 피직스에게 미확인 광석을 가져가야 합니다."}},
[1093] = {"슈퍼 절단기 6000", {"투자개발회사가 이곳에 한 짓은 정말 끔찍하지 않나? 고블린 조합에서 노움을 고용하다니 말이야! 대체 무슨 생각을 하고 있었던 건지...","천재를 알아보는 안목을 갖추신 스퍼터밸브님을 만난 건 정말 행운이었어. 해고된 날 바로 고용해 주셨으니까 말이야!","스퍼터밸브께서 투자개발회사 놈들이 거대한 벌목 기계를 만들었으니 그 설계도를 훔쳐내 달라고 부탁하셨지. 하지만 게렌조, 그 괘씸한 놈이 내 목숨을 노리고 있으니 갈 수가 있어야 말이지... 당신이 날 위해 놈들의 그걸 좀 훔쳐다 주겠나? 아마 운전수 중 누군가 가지고 있을 거야."}, {"돌발톱 산맥에 있는 지즈 피직스를 위해 슈퍼 절단기 6000 설계도를 입수해야 합니다."}},
[1094] = {"또 다른 지시 사항", {"이 봉투에는 내가 해고되기 전에 입수한 투자개발회사의 내부 문서와 몇 가지 기계의 조작설명서, 기술 자료 같은 방대한 정보가 들어 있어. 아, 물론 슈퍼 절단기 6000 설계도도 물론 넣었지.","불모의 땅에 있는 톱니항에 가면 체구가 작으신 녹색 고블린, 스퍼터밸브님의 거처가 있을 거야."}, {"불모의 땅의 톱니항에 있는 스퍼터밸브에게 봉인된 봉투를 전달해야 합니다."}},
[1095] = {nil, {"투자개발회사에 고용된 게렌조라는 노움이 언더마인을 휘젓고 다니면서 문제를 일으키고 있지. 무역왕들은 라즈덩크가 이 게렌조란 자와 손잡은 것을 탐탁지 않게 여기고 있다네.","조합장 리들복스께서는 내게 이 문제를 처리해 달라고 하셨고, 아마 그분께서도 무역왕들에게 지시를 받았을 거야. 물론 감독관이 따로 내게 이런 사실을 말해 주건 아니지만 뭐, 내 육감이라고 해 두지.","어쨌든 게렌조는 반드시 처치해야 하니, 피직스에게 맡기면 그를 시험해 볼 수 있는 좋은 기회도 될테니까..."}, {"돌발톱 산맥에 있는 지즈 피직스에게 새로운 명령을 전해야 합니다."}},
[1096] = {"게렌조 렌치휘슬", {"전에 게렌조를 만난 적이 있는데 솔직히 좀 못생겼더군. 살은 썩어 들어가는 것처럼 보였고, 몸 곳곳에는 금속과 기계 장치가 이식되어 있는데다가 팔다리의 일부가 완전히 기계로 대체되어 있었지.","게렌조는 이곳에서 북동쪽 방향으로 바위웅덩이 호수 너머에 있는 물레방아 근처 지역 전체를 감독하고 있지. 난 게렌조를 상대하기는커녕 그곳까지 갈 엄두조차 내지 못했지만 당신이라면 해낼 수 있을 것 같은데...","날 위해 그를 처치해 준다면 넉넉히 사례하도록 하지!"}, {"돌발톱 산맥에 있는 지즈 피직스에게 게렌조의 기계팔을 가져가야 합니다."}},
[1097] = {"엘모어의 임무", {"스톰윈드에 그리만트 엘모어라는 드워프 무기 대장장이가 있소. 그가 물건을 전달해 줄 이를 구하고 있다고 알려 왔소이다. 아마 북쪽에 있는 고향에 짐을 보낼 모양이오.","보아하니 당신은 단련되어 다리가 튼튼한 것 같군! 다리품 파는 일이라도 관심 있다면 그리만트를 만나 보도록 하시오. 이곳에도 당신 손을 빌릴 일이 있긴 하지만 드워프와의 관계도 돈독히 해야만 하니 말이오.","스톰윈드 북동쪽에 있는 드워프 지구의 무기 상점에 가면 그리만트 엘모어를 만날 수 있을 거요."}, {"스톰윈드에 있는 그리만트 엘모어와 대화해야 합니다."}},
[1098] = {"그림자송곳니 성채의 죽음추적자", {"그림자송곳니 성채에 대한 공격을 준비하면서 첩보 수집을 위해 죽음추적자 2명을 그리로 보냈네. 그들이 떠난 지 여러 날이 지났지만 아직 돌아오지 않고 있어. 우리의 공격이 성공하려면 그들의 보고 내용이 반드시 필요하다네!","그러니 자네가 남쪽에 있는 그림자송곳니 성채로 가서 죽음추적자 아다만트와 빈센트를 찾아봐 주게. 그들을 찾아서 도움이 필요하다면 도와주고 말이야.","중요한 임무이니만큼 위험이 크지만 절대 실패해서는 안 돼."}, {"죽음추적자 아다만트와 죽음추적자 빈센트를 찾아야 합니다."}},
[1099] = {"고블린 승리!", nil, nil},
[1100] = {"론브라우의 일지", {"헨리그 론브라우의 빛 바랜 일지를 천천히 열어 봅니다."}, {"헨리그 론브라우의 일지를 읽어야 합니다."}},
[1101] = {"가시덩굴 우리의 대모", {"불쌍한 헤랄라스! 그리고 이 드워프, 론브라우... 정말 용감했는데...","이들의 용맹스러운 죽음을 헛되이 할 수는 없소. 서슬깃 차를가라는 대모를 처치해야만 하오.","두말할 필요도 없이, 가시덩굴 우리에 침투하는 것은 위험한 일일 것이오. 하지만 다르나서스로 소식을 전할 시간이 없소. 병력을 좀 모아서 대모를 처치해 주시오.","그리고 그 증거로 차를가의 메달을 내게 가져오시오."}, {"탈라나르에 있는 팔핀델 웨이워더에게 차를가의 메달을 가져가야 합니다."}},
[1102] = {"운명의 복수", {"이곳 썬더 블러프에서 우리 종족을 단결시킨 케른님이야말로 훌륭한 지도자시지.","하지만 우리 조상들의 땅에서 우릴 몰아낸 자들은 용서할 수가 없네. 우리 타우렌들은 불모의 땅 남부에서 수십 년 동안 살아왔지. 우리에게는 신성한 땅이었지만 여러 적들에 의해 쫓겨나고 말았다네.","가시덩굴 우리 내에서 사악한 군단의 병력을 모으고 있는 노파, 서슬깃 차를가에게는 반드시 복수를 해주어야만 해.","내가 죽더라도 두 눈을 편히 감을 수 있도록 차를가의 심장을 내게 가져다주게."}, {"썬더 블러프에 있는 아울드 스톤스파이어에게 차를가의 심장을 가져가야 합니다."}},
[1103] = {"물의 부름", {"만나서 반갑네! 그대도 이곳의 물을 정화하여 이슬렌에게 능력을 증명해 보이려고 왔나?","방금 내 손으로 타락한 물의 정령을 물리치고 팔보호구와 순수한 물의 나머지 분량을 화로에 넣으려던 참이었지.","뭐라고? 이 티에브는 뭔가 잘못되면 바로바로 알 수 있지. 기꺼이 도와줌세. 하급 물의 현신을 다시 불러내야 할 거야.","자네는 이슬렌에게서 물의 정기를 다시 받아오게. 그러면 나도 내 몫을 하지."}, {"하급 물의 현신과 대화하지 못했으면 은빛소나무 숲에 있는 티에브 모르둔에게 물의 정기를 가져가야 합니다."}},
[1104] = {"소금기 있는 전갈 독", {"난 다양한 모양의 로켓 자동차를 아주 많이 설계해 놓았소! 내 사랑스러운 차들을 전부 다 경주에 출전시킬 거요!","지금은 새로운 연료를 연구하고 있소이다. 정말 아주 아주 활활 타는 연료 말이오! 아직 완벽한 혼합물을 만들어 내지는 못했지만 뭐가 필요한지는 알고 있소.","소금 평원에 돌아다니는 전갈들은 소금이 아주 많이 함유된 독을 가지고 있다오. 전갈에서만 구할 수 있는 아주 독특한 것이오! 바로 그게 필요하단 말이지!","내가 연구를 계속 할 수 있도록 그 독만 구해다 준다면 우리 차는 날아다닐 정도로 빨라질 거요!"}, {"소금 평원에 있는 피즐 브라스볼츠에게 소금기 있는 전갈 독 6개를 가져가야 합니다."}},
[1105] = {"단단한 등껍질", {"우리 형 피즐은 로켓 자동차에 사용할 새로운 연료를 연구하고 있어요. 형은 정말 똑똑하죠. 너무 똑똑해서 탈이지만... 형이 연구 중인 새 연료는 너무 강력해서 차체가 견뎌 낼 수 있을지 걱정이에요!","폭발이라도 일어나면 큰일이죠. 그래서 연료 탱크를 강화하고 싶어요.","소금 평원에 사는 거북이들의 등껍질은 아주 단단해요. 그걸 이용해서 로켓 자동차에 사용할 새 연료 탱크를 만들어 보고 싶어요. 형이 만들 새 연료를 담을 수 있는 연료 탱크를 말이에요.","보아하니 당신은 믿음이 좀 가는데, 그 등껍질 좀 가져다줄 수 있으세요?"}, {"소금 평원에 있는 위즐 브라스볼츠에게 단단한 거북 등껍질 9개를 가져가야 합니다."}},
[1106] = {"망명자 마르텍", {"지금 엄청난 속도를 낼 수 있는 새로운 자동차 엔진을 개발하는 중이오. 내가 장담하는데 분명히 자동차가 날아다닐 정도로 빨라질 거요! 그러려면 엄청나게 강한 압력을 견딜 수 있는 피스톤을 만들어야 하는데... 지금까지의 피스톤은 강도가 충분하지 않아서 다 실패하고 말았소!","이 문제를 해결할 수 있는 자를 알고 있소.","망명자 마르텍이라는 친구인데, 금속에 대한 지식이라면 누구도 따라올 수 없는 유능한 대장장이라오. 그라면 충분히 우릴 도울 수 있을 테니 그에게 이 편지를 전해 주시오!","그자는 아주 아주 먼 동부 왕국의 황야의 땅이라는 곳에 있는 야영지에서 고블린들과 함께 살고 있다오.","그를 찾아봐 주시오!"}, {"황야의 땅에 있는 망명자 마르텍에게 피즐의 편지를 가져가야 합니다."}},
[1107] = {"딱딱한 꼬리지느러미", {"우리가 만든 로켓 자동차는 소금 평원에서 최고로 빠르죠! 하도 빨라서 마찰 때문에 차체가 산산조각 나지 않을까 걱정될 정도랍니다!","그래서 새로 합성물을 만들어 자동차에서 움직이는 부품의 표면에 입혀야 해요. 그 합성물은 단단하고 안정적이면서도 소금에 부식되지 않아야만 하죠. 안 그러면 소금 평원 같은 곳에서는 차체가 남아나질 않을 거예요.","썩은내 산호초라는 곳에 대해 들어 보셨어요? 가시덤불 골짜기 해안 근처에 있죠. 그곳에는 소금비늘 멀록들이 살고 있는데 놈들의 꼬리 지느러미가 내가 만들려는 이 합성물의 중요한 재료랍니다."}, {"소금 평원에 있는 위즐 브라스볼츠에게 딱딱한 꼬리지느러미 10개를 가져가야 합니다."}},
[1108] = {"인듀리움 광석", {"내가 그저 속세를 떠나려고 이곳으로 온건 아니었다오. 황야의 땅은 금속 자원의 무한한 보고요. 당신도 배짱만 있다면 황야의 땅에서 인듀리움이라는 금속을 얼마든지 가져갈 수 있소.","그 금속은 열과 압력에 아주 강하다고 알려져 있소. 그러니 피즐의 피스톤에 사용하면 아주 그만일 거요.","하지만 확실히 해 두는 게 좋겠지. 내게 인듀리움 견본을 가져다주면 그 특성을 시험해 보겠소.","순수한 성분의 인듀리움 광석은 울다만의 깊숙한 곳에 매장되어 있지만, 황야의 땅의 바위동굴 트로그들이 그 광석의 조각을 지니고 있는 경우도 종종 있다고 하오. 내게 그 조각을 가져다주시오."}, {"황야의 땅에 있는 망명자 마르텍에게 인듀리움 조각 10개를 가져가야 합니다."}},
[1109] = {"조분석을 나에게!", {"자네도 알겠지만 우린 왕립 연금술 학회에서 재미있는 실험을 하고 있다네.","나는 이곳의 수석 연금술사로서 우리가 가장 야심 차게 진행하는 연금술 실험을 감독하고 있지. 일이 많으니 시간이 부족할 수밖에 없다네!","자네는 여행을 자주 하는 것 같으니 나를 좀 도와줄 수 있을 걸세.","내게 필요한 재료가 희귀한 것이라 문제야. 그게 좀 지저분한 물건인데 가시덩굴 우리라는 오물 구덩이에만 사는 희귀한 종류의 박쥐에게서 얻을 수 있지. 실험을 시작할 수 있도록 동굴박쥐의 조분석을 좀 가져다주게나."}, {"언더시티에 있는 수석 연금술사 파라넬에게 가시덩굴 조분석을 가져가야 합니다."}},
[1110] = {"로켓 자동차 부품", {"헤헤, 이곳에서 경주를 구경하다 보면 종종 불행한 사고가 일어나곤 하지. 아마 소금 평원을 돌아다녀 보면 과거에 있었던 충돌 사고의 흔적들을 볼 수 있을 거요. 로켓 자동차 부품의 잔해가 곳곳에 흩어져 있거든.","노움과 고블린에게는 그 부품들이 곧 돈이오. 그들은 늘 자신의 차에 장착할 새로운 장치를 찾느라 혈안이 되어 있소.","그러니 가서 그 부품들을 가져오시오. 많이 가져다 준다면 값을 후하게 쳐주도록 하겠소."}, {"소금 평원의 크레이블 콜비어드에게 로켓 자동차 부품 30개를 가져가야 합니다."}},
[1111] = {"부두주임 디지위그", {"사람들에게 필요한 물건을 구해다 주는 게 내 일인데, 여기서 멀리 떨어진 곳에 있는 노움들이 부품을 주문해 왔소. 그 부품이 일주일 전 톱니항에 도착했는데 바빠서 아직 못 찾아 왔소.","그래서 말인데 발품 파는 일거리를 원한다면 톱니항으로 가서 이 부품주문서를 부두에 있는 부두주임 디지위그에게 보여 주도록 하시오.","이 일을 해 준다면 앞으로 더 많은 일을 맡기도록 하겠소.","꽤 짭짤한 이익을 볼 수 있는 일거리를 말이오."}, {"불모의 땅의 톱니항에 있는 부두주임 디지위그에게 크레이블의 부품주문서를 가져가야 합니다."}},
[1112] = {"크레이블의 부품", {"크레이블 앞으로 도착한 짐은 여기 있소. 그리고 그에게 물건을 전해 줄 때 이 말도 좀 전해 주시오. 특별 주문을 하려면 외상값이나 먼저 갚고 하라고 말이오!"}, {"버섯구름 봉우리의 소금 평원에 있는 크레이블 콜비어드에게 크레이블의 부품을 가져가야 합니다."}},
[1113] = {"열정의 증거", {"왜 희귀한 조분석이 필요한지 궁금하겠지? 나는 이 실험에서 서로 상반되는 두 화학 물질이 어떤 영향을 주고받는지 알고 싶다네. 특히 두 물질을 독극물과 결합하면 어떤 무서운 결과가 나올까...","티리스팔 숲 북동쪽에 붉은십자군 수도원이 있네. 넓은 연회실의 내부와 외부에는 온통 붉은십자군 광신도들이 있다네. 자네가 누구를 죽이냐는 문제가 안 된다네. 붉은십자군 광신도들이라면 모두 그 증거를 지니고 있을 테니..."}, {"언더시티에 있는 수석 연금술사 파라넬이 열정의 증거 20개를 가져와 달라고 했습니다."}},
[1114] = {"노움에게 배달", {"내가 필요한 건 챙겼으니 이제 이 부품들을 피즐 브라스볼츠에게 가져다 주시오. 오랫동안 목 빠지게 기다리고 있었으니까... 아마 주문한 물품 중에서 내가 일부러 하나를 빼서 줬다는 것도 금방 알아차릴 테지.","그래, 그걸 굳이 말할 필요는 없을 거요. 작고 쓸모없는 물건이니까.","날 믿으시오."}, {"소금 평원에 있는 피즐 브라스볼츠에게 정밀한 부품을 가져가야 합니다."}},
[1115] = {"소문을 퍼뜨리는 자", {"우린 지금껏 노움을 위해 일해 왔으니 이제 고블린을 위해 일할 때가 됐소!","내가 듣기로 고블린 조종사 나즈 스팀보일은 다른 이름을 하나 더 갖고 있다고 하오. 난 그가 왜 이름을 바꿨는지 궁금하고 그게 왜 비밀인지도 궁금하오. 또한 그가 그 비밀을 왜 중요하게 생각하는지도 알고 싶소.","진실을 알 만한 이는 가시덤불 골짜기의 남쪽 끝, 무법항에 있는 크라젝뿐이오. 크라젝을 만나 이 고블린 조종사에 대해 물어보도록 하시오.","그에게서 유용한 정보를 얻을 수 있다면 돈을 좀 벌 수도 있을 테니까."}, {"가시덤불 골짜기의 무법항에 있는 크라젝과 대화해야 합니다."}},
[1116] = {"습지의 꿈가루", {"나즈에 대한 정보를 수집하는 건 쉬운 일이 아니죠. 그럼요, 만만히 볼 일은 아니랍니다. 하지만 당신은 저 멀리 소금 평원에서 이곳까지 정보를 얻으러 올 정도의 끈기를 지닌 분이니... 흠, 발품을 조금만 더 팔아 보시겠어요?","내게 뭐라도 좀 구해다 주는 건 어떨까요? 난 그동안 정보원들과 연락을 해야 하니까요.","흠... 뭐가 좋을까? 그래, 꿈가루! 그게 좋겠어요. 슬픔의 늪에 있는 용들에게서 꿈가루를 구해다 주세요. 그걸 코담배 주머니에 몇 줌씩 담아 가지고 다니며 향을 맡으면 정말 환상적이랍니다!","제게 그걸 구해다 주면 당신이 원하는 정보를 드리죠."}, {"무법항에 있는 크라젝에게 꿈가루 10줌을 가져가야 합니다. 꿈가루는 슬픔의 늪에 있는 용들에게서 얻을 수 있습니다."}},
[1117] = {"크레이블을 위한 소문", {"여기저기 물어도 보고, 귀한 엘프 포도주도 한 병 구해서 정보의 대가로 주고, 또 나 스스로도 열심히 추론을 한 끝에, 마침내 당신이 원하는 정보를 입수했어요.","자, 나즈 스팀보일이라는 친구에 관한 아주 재미있는 정보를 이 편지에 적었어요. 내가 써먹기에는 너무 위험하니 당신이 알아서 하세요.","어디서 구했는지는 아무에게도 말하지 말고요!"}, {"버섯구름 봉우리의 소금 평원에 있는 크레이블 콜비어드에게 고블린의 소문을 가져가야 합니다."}},
[1118] = {"무법항으로의 귀환", {"당신은 정말 믿을 수 있는 <class>|1이요;요;. 날 위해 일을 완수해 준 것도 그렇고, 그 일을 하는 동안 입이 무거웠던 것도 그렇고...","난 새로운 계획을 구상하던 참이오. 위험 부담은 있지만, 성공하면 우리 둘 다 큰돈을 만지게 될 거요! 그 일엔 당신이 적격이라고 생각하오.","우리의 계획을 시작하려면 다시 무법항으로 돌아가야 하오. 미안하오, 좋은 친구. 지난번 부탁한 일로 멀리 그곳까지 다녀왔는데 이렇게 고단한 길을 다시 가도록 만들어서 말이오. 이번에는 크랭크 피즐버브를 만나 보시오.","그에게 이 편지를 전하고 그가 도움을 필요로 하면 최선을 다해 그를 도와주길 바라오. 그럴 만한 가치가 있는 일이니까."}, {"피즐버브에게 보내는 편지를 무법항에 있는 크랭크 피즐버브에게 가져가야 합니다."}},
[1119] = {"잔질의 혼합물과 멍텅구리 스타우트 맥주", {"스타우트에 잔질의 혼합물을 섞으면 아주 강력한 효과를 낼 수 있다는 사실을 알아냈습니다. 바로 멍텅구리 스타우트라는 것이 탄생했죠. 바로 이게 크레이블에게 꼭 필요할 겁니다. 써보고 효과가 어땠는지 알려 주세요.","그리고 너무 오래 두지는 마세요. 시간이 지나면 상하니까요. 잔질의 혼합물 때문에 스타우트의 효소가 기형적으로 변했답니다."}, {"소금 평원의 크레이블에게 멍텅구리 스타우트 맥주를 가지고 돌아가야 합니다."}},
[1120] = {"노움 취하게 하기", {"이 멍텅구리 스타우트 맥주를 정비공 하나에게 먹여봐야겠군. 노움에게 먹여볼 생각이라면... 자, 받으시오. 이 스타우트 맥주를 노움 정비주임에게 갖다주시오.","너무 오래 지체하지 않는 게 좋을 거요. 스타우트 맥주의 맛이 변할지도 모르니까 말이오."}, {"노움 정비주임에게 멍텅구리 스타우트 맥주를 가져가야 합니다."}},
[1121] = {"고블린 취하게 하기", {"이 멍텅구리 스타우트 맥주를 정비공 하나에게 먹여봐야겠군. 고블린에게 먹여볼 생각이라면... 자, 받으시오. 이 스타우트 맥주를 고블린 정비주임에게 갖다주시오.","너무 오래 지체하지 않는 게 좋을 거요. 스타우트 맥주의 맛이 변할지도 모르니까 말이오."}, {"고블린 정비주임에게 멍텅구리 스타우트 맥주를 가져가야 합니다."}},
[1122] = {"피즐버브에게 돌아가기", {"멍텅구리 스타우트 맥주는 그 이름처럼 효과도 끝내주는군! 바로 다음 경주에 돈을 걸었네... 하하! 이제 그걸 마신 정비공들이 술기운에 정비를 어떻게 하는지만 앉아서 지켜보면 되겠지.","이제 마지막으로 한 가지 일만 더 해 주시오. 무법항에 있는 크랭크 피즐버브에게 다시 가서 이 쪽지를 전해 주시오. 그자도 이 실험이 성공했는지 궁금할 테니까...","그리고 앞으로 있을 경주를 위해 멍텅구리 스타우트를 좀 더 얻고 싶은데..."}, {"가시덤불 골짜기의 무법항에 있는 크랭크 피즐버브에게 멍텅구리 스타우트 보고서를 가져가야 합니다."}},
[1123] = {"라빈 사투르나", {"이번 임무를 위해 달숲에 있는 수호자 레물로스의 청지기인 라빈 사투르나가 타나리스에서 서쪽으로 멀리 떨어진 광활한 황무지를 탐험하는 데 도움을 줄 자를 찾고 있네. 운고로의 서쪽으로 훨씬 더 멀리 떨어져 있는 곳이지. 라빈은 달숲에서 제일 큰 마을인 밤의 안식처에 살고 있다네.","내 그대에게 그대가 가는 것이 중요하다는 걸 강조하고 싶군. 다르나서스의 나이트 엘프와 달숲의 세나리온 의회 간에 긴장감이 날로 커져가고 있어서 말이야. 그곳의 세나리온 의회와 상대할 때에는 종족 간의 차이를 떠나 그대 선조들의 지혜를 빌리도록 하게나. 행운을 빌겠네."}, {"달숲의 밤의 안식처에 있는 라빈 사투르나와 대화해야 합니다. 달숲은 악령숲과 여명의 설원 사이에 있으며 나무구렁 요새에서 나가는 길을 통해 갈 수 있습니다."}},
[1124] = {"황무지", {"북서쪽 산등성이를 타고 운고로 분화구의 서쪽으로 가면 실리더스라는 거친 황무지가 있소. 먼 옛날 그곳에서 우리 나이트 엘프가 사악한 외계 세력에 맞서 전쟁을 치렀다는 사실을 아는 이는 몇 없지. 그때 사악한 힘은 봉인되었지만 누군가... 말로 다 표현할 수 없을 만큼 커다란 공포를 다시 퍼뜨리려는 것 같소.","실리더스에 우리 동지인 라요 스타스트라이크가 있소. 황무지에서 숨을 거둔 이들의 최종 정착지인 용사의 안식처에 가면 그를 찾을 수 있을 거요. 라요에게 이 편지를 전해주면 그대를 믿을 것이오."}, {"실리더스의 묘지인 용사의 안식처 근처에 있는 라요 스타스트라이크를 찾아가 라빈의 편지를 보여줘야 합니다."}},
[1125] = {"마파람의 영혼", {"<name>! 여기서 남서쪽으로 조금만 가면 마파람 마을이라는 비극의 장소가 있소. 바로 그곳이 우리가 일을 시작해야 할 곳이오...","우리가 알아낸 정보에 따르면 그 마을은 한때 나이트 엘프가 실리더스의 거점으로 삼았었지만 오래전 황무지에 들끓고 있는 실리시드 무리에게 점령당했소. 마을이 처음 산산조각났을 때의 공포가 어땠을지 짐작이 가고도 남소...","죽은 드루이드와 파수꾼의 고통받는 영혼이 그 폐허에서 방황하고 있소. 마을을 조사해 이 비극의 영혼들을 해방해주시오."}, {"마파람 마을에서 고통받는 드루이드 8명과 고통받는 파수꾼 8명의 영혼을 해방한 후, 실리더스, 용사의 안식처 묘지에 있는 라요 스타스트라이크에게 가야 합니다."}},
[1126] = {"탑 속 소굴", {"마파람 마을에 있는 소굴을 처리해야 하는데, 내게 한 가지 방법이 있소. 내 경험에 의하면 실리시드 소굴은 주변 환경에 매우 민감하오. 죽음으로 울부짖는 영혼의 소리가 들리면 공격을 해대는 것도 그 때문이지.","말벌 떼가 둘러싸고 있는 탑에 실리시드가 가장 많소. 탑에 올라가서 소굴을 들쑤셔 놓으시오. 뭐라도 알아내거든 알려주고, 조심하기 바라오! 도움이 될만한 걸 찾거든 내게 가지고 돌아오시오."}, {"마파람 마을에 있는 탑 위로 올라가 실리시드 소굴을 들쑤셔 놓을 방법을 찾아야 합니다. 그러는 가운데 뭔가 찾아낸 것이 있으면 실리더스의 용사의 안식처 묘지에 있는 라요 스타스트라이크에게 가져가야 합니다."}},
[1127] = {"멍텅구리 스타우트 맥주", {"멍텅구리 스타우트 맥주가 더 필요하단 말씀이죠? 다시 경주에 돈을 거시려고...?","좋습니다. 잔질의 혼합물만 더 가져다주면 스타우트 맥주를 얼마든지 더 만들어 드리지요."}, {"무법항에 있는 크랭크 피즐버브에게 잔질의 혼합물 12개를 가져가야 합니다."}},
[1130] = {"멜로르의 전갈", {"멜로르 스톤후프가 자네의 활약상을 듣고서 일을 맡기겠다고 하네.","생각이 있다면 썬더 블러프로 가서 멜로르를 만나 보게나. 수렵의 봉우리에서 그를 찾을 수 있을 게야."}, {"썬더 블러프에 있는 멜로르 스톤후프와 대화해야 합니다."}},
[1131] = {"무쇠이빨 하이에나", {"우리는 때로는 식량을 구하기 위해서나 명예와 대지모신의 가르침을 얻기 위해 사냥을 하오.","하지만, 지금 그대 앞에 놓인 사냥 과제는 그런 것과는 차원이 다르오. 바로 대지모신의 저주를 받은 짐승을 처치하는 것이지.","불모의 땅 남쪽 버섯구름 봉우리에는 무쇠이빨 하이에나라는 놈이 돌아다니고 있소. 이 야수는 주변의 다른 하이에나를 강압적으로 지배하면서 평화로이 지내는 동물들을 살육하고 공포를 퍼뜨리고 있지.","무쇠이빨 하이에나를 찾아 처치해 버리도록 하시오."}, {"썬더 블러프의 멜로르 스톤후프에게 무쇠이빨 하이에나의 갈비뼈를 가져가야 합니다."}},
[1132] = {"피오라 롱이어즈", {"오, 다시 한번 바다에 갈 수 있다면! 신선한 바닷바람을 느끼고 포근한 어머니의 품 같은 파도에 몸을 맡겨 본 게 대체 얼마나 됐는지!","오, 나도 당신처럼 나중에 바다를 볼 수 있었으면 좋겠소이다!","부푼 야망을 품은 이들에게 기회의 땅 칼림도어에 대해 얘기해 주는 게 내 일이라오! 바다에서 자신의 운을 시험해 보고 싶다면 이곳에서 배를 타고 아름다운 항구 아우버다인으로 떠나시오. 거기서 내 동료인 피오라 롱이어즈라는 엘프를 만나 보시오.","그녀가 당신이 칼림도어에서 모험을 시작할 수 있도록 해줄 거요."}, {"어둠해안, 아우버다인의 부두에 있는 피오라 롱이어즈와 대화해야 합니다."}},
[1133] = {"아스트라나르로의 여정", {"<name>, 아스트라나르로 가십시오. 그대처럼 의욕 넘치는 얼라이언스의 일원이라면 그곳에서 훌륭한 일을 해낼 수 있을 겁니다. 신드렐 스위프트파이어를 만나 도움을 주시기 바랍니다.","누가 보냈는지는 그녀에게 말해봐야 소용없을 겁니다. 신드렐은 날 모르니까요..."}, {"아스트라나르에 있는 신드렐 스위프트파이어와 대화해야 합니다."}},
[1134] = {"돌발톱 산맥의 사자날개", {"아직 우리가 호드와 전면전을 벌이고 있진 않지만 일촉즉발의 상황입니다. 호드에서 우리를 상대로 작전을 펴는 걸 알아낼 때도 있지만 워낙 은밀히 진행하는 터라...","얼마 전 테라모어의 비밀 첩보원에 의해서 그들의 기습 계획이 드러났습니다. 그 보고에 따르면 오크들이 남쪽 돌발톱 산맥에 있는 땅거미 호수 근처에서 사자날개의 독을 모으고 있다고 합니다. 그리고 오크 암살자들이 그 독을 이용해 얼라이언스를 급습할 거라고 합니다.","이 정보가 사실이라면 반드시 이를 막아내야 합니다. 그들이 독을 얻지 못하도록 그 원천을 봉쇄해야 합니다."}, {"아스트라나르에 있는 신드렐 스위프트파이어에게 사자날개 와이번 독주머니 12개를 가져가야 합니다."}},
[1135] = {"마루둥지 와이번의 독", {"오크들이 주로 와이번을 탈것으로 길들인다는 것은 알고 계시겠지만, 어떤 계통의 와이번 독은 와이번 자체보다도 더 값지다는 것은 잘 모르실 거예요. 혹시 와이번 사냥을 한번 해보시겠다면... 드릴 만한 일거리가 있습니다.","여기서 서쪽으로 가면 버섯구름 봉우리에 마루둥지라는 장소가 있어요. 마루둥지 와이번은 아주 강한 독을 지니고 있습니다. 아주 강하고 값진 독을..."}, {"테라모어에 있는 피오라 롱이어즈에게 마루둥지 와이번 독주머니 10개를 가져가야 합니다."}},
[1136] = {"서리갈기 설인", {"그대도 이제 머나먼 땅으로 여행을 할 때가 되었군.","알터랙 산맥에 사는 서리갈기 설인을 사냥하도록 하시오. 놈의 털은 흰색이라 산맥에 쌓인 눈과 섞여 알아보기가 힘들 뿐만 아니라 항상 숨어다니는 습성 때문에 찾기가 힘들다오. 좀처럼 모습을 드러내지 않기로 유명하지.","하지만 내 그놈을 꾀어내는 방법을 가르쳐 주지. 이 두루마리를 받으시오. 거기다 서리갈기 설인을 꾀어낼 수 있는 방법을 적어 놨소.","이제 가보시오. 멀고도 험난한 여정이 될 거야. 반드시 서리갈기 설인의 갈기를 가지고 돌아와야만 하오."}, {"썬더 블러프에 있는 멜로르 스톤후프에게 서리갈기 설인의 갈기를 가져가야 합니다."}},
[1137] = {"피즐에게 전할 소식", {"인듀리움은 열에 아주 강하오! 틀림없이 피즐의 엔진 피스톤에 쓸 수 있을 거요. 하지만 이 조각들만으로는 부족하고 알짜배기 인듀리움 광석이 필요할 거요.","그 광석은 황야의 땅의 북쪽 경계에 있는 울다만의 깊숙한 곳에서 캐내야만 하오. 그곳이 인듀리움의 진짜 원산지니까.","피즐 브라스볼츠에게 내 안부를 좀 전해 주시오. 그리고 내가 당신에게 해준 말도 전해주고.","부디 안전한 여행 되길 바라오. 그리고 아마 우리는 다시 만나게 될지도 모르오. 피즐이 인듀리움에 대해 알면 분명히 당신을 울다만으로 보내 그걸 가져오게 할 테니까!"}, {"버섯구름 봉우리의 소금 평원에 있는 피즐 브라스볼츠와 대화해야 합니다."}},
[1138] = {"바다의 열매", {"저는 게가 좋아요. 게는 바다의 열매라고 할 수 있지요! 구워도 되고, 바베큐를 해도 되고, 끓여도 되고, 석쇠 구이를 해도 그만이죠. 팬에 살짝 볶아도 되고, 기름을 잔뜩 넣고 튀겨도 되고, 튀길 때 약간 휘저어 주면 맛이 더 기가 막히지요. 게 샐러드, 게 수프, 게 스튜, 후추 게 요리, 레몬 게 요리, 채찍뿌리 게 요리, 그리고 아이언포지 깜짝 게 요리도 있어요. 뭐, 그 정도랍니다.","지금 낚시 중이라 자리를 비울 수 없지만, 당신이라면 모래톱게나 등껍질 파도게에게서 맛좋은 게살을 구해올 수 있겠지요. 그걸로 다양한 요리를 만들 수 있답니다. 구워도 되고, 바비큐를 해도 되고, 끓여도 되고..."}, {"맛좋은 게살 6개를 모아서 아우버다인에 있는 구버 블럼프에게 가져가야 합니다."}},
[1139] = {"잃어버린 결의의 서판", {"검은바위 첨탑에서 어둠괴철로 드워프의 진로를 끊어 그들의 진군은 늦추었지만, 해머토의 꿈은 아직 이루어지지 못했소. 절대 그의 죽음을 헛되이 해서는 안 되오. 해머토는 늘 결의의 서판을 찾기를 꿈꾸었지. 전설에 의하면, 그것은 티탄들이 자신의 의지를 가진 생명체를 창조하는 데 사용하려고 몸소 마법으로 돌에 새긴 서판이라고 하더군.","아무튼 어둠괴철로 드워프들이 발견하기 전에 우리가 먼저 그걸 되찾아와야 하오.","울다만으로 돌아가서 그 서판을 찾는 즉시 다시 이곳으로 돌아오시오."}, {"결의의 서판을 찾아 아이언포지에 있는 조언자 벨그룸에게 가져가야 합니다."}},
[1140] = {"알살락스의 탑", {"오크 녀석들의 글씨는 정말 아이들의 낙서보다 나을 게 없다니까... 내가 그들의 말을 배우기로 했을 때 많은 동료가 비웃었지. 하지만 적에 대해 많이 알면 알수록 유리하다는 사실을 깨닫게 되었다네.","일크루드가 쓴 글을 읽어보니, 아직 애스리쿠스가 가진 영혼의 보석 두 개가 잿빛 골짜기 북동쪽의 사티로스 점령지인 어둠의 터와 사티르나르에 있다는 사실을 확인했네.","조심하게나. 피로 물든 사티로스의 사원은 자네처럼 노련한 <class>|1이라도;라도; 흔들리게 할 수 있으니까 말이야."}, {"어둠의 터와 사티르나르에 갇혀 있는 명가의 영혼들을 구출하고 마에스트라 주둔지에 있는 수호자 델그렌에게 가야 합니다."}},
[1141] = {"블럼프가의 낚싯대", {"저는 구버 블럼프라고 합니다. 낚시를 무척 좋아하죠. 저희 블럼프가는 타고난 낚시꾼의 명가랍니다.","이 근처에서 꼭 잡고 싶은 물고기가 있어요. 어둠해안 농어라고, 전에는 배를 타고나가서 낚고는 했는데, 저 괘씸한 멀록 녀석들이 들어온 후로는 한 번도 배가 나간 적이 없답니다. 농어도 녀석들이 다 먹어 치우고 있을 게 뻔해요.","혹시 어둠해안 농어 낚시를 도와 주실 생각 없으신가요? 그래 주신다면 진품 블럼프가의 낚싯대를 드리겠어요! 아주 훌륭한 물건이죠."}, {"어둠해안 농어 6마리를 잡아서 아우버다인에 있는 구버 블럼프에게 가져가야 합니다."}},
[1142] = {"꺼져가는 생명의 불씨", {"이렇...게 힘이 없어...서야 오래 살...아 있지 못할 것 같...소.","팔핀델에게... 주의를 주...기 위해 론브라우...라는 용...용감한 드워프를 보냈...소.","하지만 주..죽기 전에... 내... 아내... 트레샬라... 와 연결되어 있는... 펜던트를... 그 비...비열한 녀석이 빼앗...아 가 버렸소...","제...제발... 펜던트를 찾아서 다르나서스에 있는... 트레샬라에게... 돌려... 주시오...","...쓸쓸히... 죽어간 나의... 소...소식...도... 함께..."}, {"트레샬라의 펜던트를 찾아 다르나서스에 있는 트레샬라 팰로우브룩에게 돌려줘야 합니다."}},
[1143] = {"알살락스의 탑", {"흠... 그대의 말이 사실이라면 지금이 최적의 공격 시기요. 난 애스리쿠스가 분명 탑 꼭대기에 있을 것이라 믿지만 조심하시오. 그의 주위에는 강력한 소환사들이 많이 있을 텐데, 지금까지 그대가 상대해 온 적들보다 훨씬 강한 자들이니..."}, {"애스리쿠스 나라신을 처치한 다음, 그 증거로 그의 머리 장식을 알살락스의 탑 근처에 있는 발툴 섀도스트라이크에게 가져가야 합니다."}},
[1144] = {"수입업자 윌릭스", {"아, 드디어 구세주가 왔군!","내가 이곳에 얼마나 오래 갇혀 있었는지 모를 거요. 마지막으로 바깥 세상 구경한 게 언제인지 기억도 나질 않으니...","보다시피, 나는 사업가요. 사람들은 돈벌레라고도 부르지. 메보크 미지릭스가 여기 이 돼지우리 같은 곳에서 나는 청엽수를 후한 값에 사들인다는 소문을 듣고 왔다오. 쉽게 들어왔다 빠져나갈 수 있을 줄 알았는데, 보다시피 잘못 생각했던 모양이오.","어쨌든, 여기서 나갈 수 있도록 도와준다면 후하게 보상하리다."}, {"수입업자 윌릭스를 호위해 가시덩굴 우리에서 나가야 합니다."}},
[1145] = {"곤충 떼의 위협", {"십자로로 오는 많은 여행자들이 내게 거인의 들판에서 찾은 알들을 주면서 하는 말이 그곳 짐승들은 정말 위험한 존재라고 하더군. 얼마나 강조하는지 처음에 우리가 생각했던 것보다 훨씬 위험한 곤충이라는 생각이 든다오.","오그리마로 가서 벨그롬 록마울을 만날 급사가 필요한데... 쉬운 일은 아니지. 이 임무를 확실하게 처리해 줄 자가 필요하오.","벨그롬은 오그리마 명예의 골짜기에 있는 용사의 전당 입구에 있는 것으로 알고 있소."}, {"오그리마에 있는 벨그롬 록마울에게 코란의 봉인된 쪽지를 전해야 합니다."}},
[1146] = {"곤충 떼의 위협", {"음... 그 곤충에 대해서 코란도 나와 같은 생각인 것 같군. 생각보다 훨씬 더 위험한 녀석들인 것 같소.","며칠 전, 한 사냥꾼을 만났소. 버섯구름 봉우리에서 초주검이 되어 왔더군. 그는 소금 평원에서 드워프 발굴단을 몰아내기 위해 파견된 공격대원이었소. 그런데 공격 도중 발굴현장의 동굴 안에서 곤충들이 쏟아져 나오는 바람에 부대가 거의 전멸하고 말았다오.","목타르 크린이 지휘하던 그 부대는 소금 평원 변경으로 대피한 모양이니 그에게 이 쪽지를 좀 전해 주시오."}, {"버섯구름 봉우리에 있는 목타르 크린에게 벨그롬의 봉인된 쪽지를 전해야 합니다."}},
[1147] = {"곤충 떼의 위협", {"흠. 벨그롬은 저 괴물들을 꽤 위협적인 존재로 생각하고 있는 것 같군. 놈들을 다시 땅속으로 되돌려 보내길 원하고 있다는 말인 것 같은데...","탐험가 연맹 소속의 드워프들을 처치하는 것만으로는 성에 안차나 보군. 그렇다면 반쯤 남은 우리 부대를 또 다른 전투에 투입해야만 하는데... 이런 상황에서는 도저히 꿈도 꿀 수 없는 일이야. 발굴현장은 이곳에서 남쪽에 있는데 아마 어떤 드워프도 남아 있지 않을 것이오. 우릴 위해 대신 싸워 줄 수 있겠소?"}, {"탐색꾼 실리시드 5마리, 일꾼 실리시드 5마리, 병정 실리시드 5마리를 처치하고 버섯구름 봉우리에 있는 목타르 크린에게 가야 합니다."}},
[1148] = {"실리시드 등껍질", {"껍질을 손에 들고 살펴 보다가 뒤집어 보니 그물처럼 얽혀 있는 키틴질 표면 때문에 껍질이 놀랄 만큼 단단하다는 사실을 발견합니다. 지금까지 이런 물건은 본 적이 없습니다. 다른 부분을 더 살펴 보면서 이 짐승이 고도로 진화된 종이라는 것을 깨닫게 됩니다.","시체를 다시 내려다보니 문득 코란이 이 짐승에 대해 연구해 보고 싶어할 것 같다는 생각이 듭니다. 자세한 연구를 통해 이 동물의 정체와 출처를 알아낼 수도 있을 것 같습니다."}, {"크로스로드에 있는 코란에게 실리시드의 심장 1개, 실리시드의 갈퀴발톱 5개, 온전한 실리시드의 등껍질 3개를 가져가야 합니다."}},
[1149] = {"믿음의 시험", {"나를 일부러 찾아온 건가, 아니면 그저 운명에 이끌려 나한테까지 오게 된 건가? 뭐, 상관없네. 그대가 지금 여기 있고 이제 새로운 여정이 시작되려 한다는 사실이 중요한 거니까...","육체와 정신, 그리고 영혼이라는 여러 요소가 합쳐져 하나의 존재를 이루지. 이중 일부는 한 문화의 전통에 의해 가늠될 수 있네. 하지만 일부는 그저 우리 안에 존재하길 바라는 수밖에... 만약 내 말이 맞다고 생각한다면 그대의 내면을 시험해 보게.","이 시험에 통과한다면 더욱 강해질 것이고 실패한다면 지옥이 얼마나 광대한지를 깨닫게 될 것이네. 첫 번째는 믿음의 시험일세."}, {"믿음이 있다면 버섯구름 봉우리가 내려다보이는 높은 곳에서 판자 아래로 뛰어내려야 합니다."}},
[1150] = {"인내의 시험", {"시험을 통해 그대의 변치 않는 용기를 확인하니 내 마음이 기쁘군. 그대의 확고한 믿음은 확인했으니 이제 육체의 힘을 시험할 때가 됐네.","첫 번째는 인내의 시험이지. 높새바람 봉우리 서쪽 하피 동굴 깊은 곳까지 들어가서 보급품을 파괴하게. 그러면 놈들의 화를 돋우게 될 테고 살아남는다면 끝으로 그렌카 블러드스크리치가 직접 자네를 상대하러 나타날 걸세. 그녀를 처치하고 그 발톱을 가져오면 이번 시험은 끝나지."}, {"버섯구름 봉우리에 있는 도른 플레인스토커에게 그렌카의 발톱을 가져가야 합니다."}},
[1151] = {"용기의 시험", {"옛날 고대 신들의 시대엔 이 땅에 많은 정령이 살고 있었지. 그들은 고대 신들을 섬겼고, 신들은 정령들을 이 세상에 속박해 두었네. 그런데 고대 신들이 이 땅을 뜨자 정령들은 세상에서 사라졌고, 이제는 강력한 마법사만이 다시 그들을 소환해 불러낼 수 있게 되었지.","하지만 어디에나 예외는 존재하는 법... 일부 정령들은 강한 힘을 갖고 있어서 이 땅에 스스로 남아 있을 수 있었지. 로크알림도 그중 하나야. 자, 그대의 힘을 시험하려면 버섯구름 봉우리에서 로크알림을 찾아 처치하게."}, {"버섯구름 봉우리에 있는 도른 플레인스토커에게 로크알림의 파편을 가져가야 합니다."}},
[1152] = {"지혜의 시험", {"이제 나와 함께할 시간도 거의 끝나 가는군. 다른 이에게 다음으로 그대가 수행할 지혜의 시험을 맡기겠네. 이 세상에는 배울 일도 많고 가봐야 할 곳도 많지.","놀라운 지혜를 가진 주술사 브라우그 딤스피릿을 찾아가도록 하게. 그가 지혜의 시험에 대해 알려줄 테니 그의 말에 귀를 기울이도록...","준비가 되는 대로 돌발톱 산맥과 잿빛 골짜기를 잇는 토굴길을 찾아가게. 브라우그는 그곳에 살고 있지. 나이트 엘프들이 자네의 시험을 방해하려 할 것이니 조심하게."}, {"돌발톱 산맥에 있는 갈퀴자국 토굴길 입구 근처에서 브라우그 딤스피릿을 찾아야 합니다."}},
[1153] = {"새로운 광석 견본", {"<class> 친구, 지금까지 나에게 큰 도움이 되어 주었소. 대족장님께서 내가 한 일들에 대해 매우 기뻐하시면서 앞으로도 새로운 결의로 임무를 완수해 내기를 바라시고 있소. 나를 도와준 당신이 합당한 보상을 받을 수 있도록 대족장님께 말씀을 드리려 하는데, 그 전에 부탁이 하나 더 있소.","최근 남쪽에 있는 버섯구름 봉우리라는 곳에서 여행자들이 왔소. 그곳에서 코볼트를 하나 해치우고 아주 희귀한 광석 견본을 손에 넣었다는데, 절대 팔 수는 없다더군. 그 광석 견본을 조사해보고 싶으니 좀 구해다주시오."}, {"자갈주둥이 채굴꾼이나 흙점쟁이가 가지고 있는 정제하지 않은 광석 견본을 찾아 불모의 땅, 타우라조 야영지에 있는 테터낵 스틸포지에게 가져가야 합니다."}},
[1154] = {"지혜의 시험", {"도른의 시험을 잘 통과했으니 이제 다음 시험을 치를 때가 되었네. 지금까지의 시험은 아이들 장난에 불과하지. 육체의 힘을 시험하는 건 어렵지 않지만 마음을 시험하는 건 차원이 완전히 다른 문제지.","쉽게 설명해주지. 내가 말하는 책을 찾아서 탐독하고 그 내용에 대한 내 질문에 답하도록 하게.","무슨 책인지 궁금하겠지? 고대의 유산이라는 책일세. 잿빛 골짜기 남쪽에 있는 도르다닐의 아래쪽으로 가면 찾을 수 있을 걸세. 그곳은 죽은 드루이들과 그들의 노예가 지키고 있으니 조심해야 하네."}, {"고대의 유산을 찾아 돌발톱 산맥에 있는 갈퀴자국 토굴길 입구 근처에서 브라우그 딤스피릿에게 가져가야 합니다."}},
[1159] = {"지혜의 시험", {"잘했네. 지혜의 시험의 첫 단계를 잘 통과했군. 아직 답해야 할 질문이 남아 있지만 그 질문은 내가 하지 않겠네.","로데론의 폐허에 있는 파쿠알 핀탈라스를 찾아가도록 하게. 그는 역병이 휩쓸고 지나가기 전에는 인간들의 위대한 역사가였지. 지금도 타인들을 교화하기 위해 힘쓰고 있지만 예전에 하던 일과는 성격이 다르지.","아마 그가 문제를 내기 전에 그대에게 무언가를 요구할 걸세. 하지만 지금까지 잘해 왔으니 그의 시험도 무사히 통과하리라 믿네."}, {"언더시티에서 파쿠알 핀탈라스를 찾아야 합니다."}},
[1160] = {"지혜의 시험", {"그럼 자네가 찾아야 할 것을 말해 주지. 질문은 그것을 찾아온 후에 할 거야. 힘보다 지식이 훨씬 중요하지. 세상엔 자네보다 더 뛰어난 능력을 갖춘 이들도 많이 있겠지. 하지만 이 세상에 대한 지식을 쌓아 간다면 그게 훨씬 더 가치 있는 일이야.","티리스팔 숲 북쪽에 있는 수도원으로 가게. 그곳 도서관에는 '언데드 위협의 기원'라는 책이 있지. 그 책은 지금은 타락해 버린 성기사들이 우리 종족과 싸우기 위한 훈련에 사용하던 교본일세.","그걸 내게 가져오도록 하게."}, {"언데드 위협의 기원을 찾아 언더시티에 있는 파쿠알 핀탈라스에게 가져가야 합니다."}},
[1164] = {"도둑에게서 훔쳐라", {"케나타! 그 돼지 같은 계집이 아직도 살아 있다니! 고통 속에 보낸 그 세월 동안 그 계집의 가족은 잘 먹고 잘살고 있다네!","난 역병으로 가족을 잃고 전쟁 중에 재산도 도둑맞거나 불타 버렸지. 그 후 케나타 그 계집과 그 악랄한 자식들은 뻔뻔스럽게도 내게 남은 유일한 귀중품마저 훔쳐갔어.","훔쳐간 물건에는 더는 관심이 없으니 그들을 처치해 주게! 아라시 고원에 있는 임시 주둔지 북동쪽의 다비리 농장으로 가면 될 걸세."}, {"언더시티에 있는 지나비 칼로에게 케나타, 파델, 마르셀 다비리의 머리카락을 가져가야 합니다."}},
[1166] = {nil, {"나, 대군주 모크모로크 앞에 머리 숙여라!","렉사르 님이 나를 돌망치 일족의 강력한 지도자로 임명하셨다!","나는 실력 있는 지도자. 내 부족을 안전하게 해준다.","큰 용들이 와서 옛 고향을 다 태워 버렸다. 하지만 나는 똑똑한 오우거. 우리 부족의 새 집을 여기에 마련한다.","너무 급히 옛 집을 떠나오는 바람에 두고 온 것들이 있다. 가서 내 물건들을 가져와라, 꼬마 <race>. 남쪽으로 가서 내 맛있는 그로그주와 코담배통, 그리고 금고를 찾아오면 너 기특하게 여겨 준다. 화염의 둥지와 돌망치 폐허를 찾아봐라, 날 믿어!"}, {"담쟁이 마을의 대군주 모크모로크가 먼지진흙 습지대에서 자신의 그로그주, 코담배통, 그리고 금고를 찾아와 달라고 했습니다."}},
[1167] = {"알살락스의 탑", {"애스리쿠스에 맞서기 전에 발툴과 상의하는 게 좋을 걸세. 그는 분명 탑을 계속 감시하고 있었을 테니, 그곳으로 가면 분명 최근에 밝혀진 정보를 알 수 있을 걸세."}, {"알살락스의 탑 근처에 있는 발툴 섀도스트라이크와 대화하십시오."}},
[1168] = {"검은용군단", {"렉사르 님이 모크모로크를 대군주로 임명하셨다. 그는 우리 오우거일족을 돌보는 것보다는 힘을 얻는 데 관심이 많지.","검은용들이 오우거들을 많이 죽였다. 타르그의 아내도 죽였다. 모크모로크는 모두에게 도망치라고 해서 이곳으로 오게 했다.","오우거는 싸워야 한다. 오우거는 도망치는 건 잘할 줄 모른다. 모크모로크는 자기 걱정만 한다.","나는 돌망치 폐허를 고향이라 부른다. 난 남쪽이 좋다. 여기는 고향이 아니다.","우리 오우거들이 검은용군단과 싸우는 걸 도와라. 난 복수하고 싶다. 고향으로 가고 싶다."}, {"담쟁이 마을의 타르그가 화염갈기혈족 정찰병 10마리, 화염갈기혈족 잿불꼬리 10마리, 화염갈기혈족 맹독전사 5마리를 해치워 달라고 했습니다."}},
[1169] = {nil, {"그 겁 많은 바보 모크모로크는 권력의 맛에 흥청대고 타르그는 슬픔과 복수의 악마들과 악전고투하며 부족의 지도권을 얻기를 바라지. 우리 옛 고향을 침략한 무리가 어떤 녀석들인지 어디서 온 건지 알아내려고 하는 건 나 혼자뿐인 것 같군.","어째서 어리둥절한 얼굴을 하고 있지? 내가 오우거라고 해서 거친 난봉꾼 같은 말투를 쓸 거로 생각했나?","뭐, 어쨌거나 다시 본론으로 돌아가지. 화염 새끼용류 녀석들의 심장과 혓바닥을 내게 가져와라. 난 이 악의 뿌리를 추적해야 한다..."}, {"담쟁이 마을에 있는 드자리블이 화염 혓바닥 7개와 화염 심장 7개를 가져다 달라고 했습니다."}},
[1170] = {"오닉시아 일당", {"돌망치일족 마을이 오닉시아 일당에게 침략당했다. 하지만 왜 검은용군주 데스윙의 딸이 우리 땅에 내려온 거지?","이건 정말 큰일이다. 분명 오닉시아가 겨우 오우거 몇 마리를 포위 공격하러 여기 온 건 아닐 거고 그 이상의 뭔가가 있다.","즉시 모크모로크에게 알려야 한다! 반드시 조치를 취해야 해."}, {"담쟁이 마을에 있는 대군주 모크모로크와 대화해야 합니다."}},
[1171] = {"오닉시아 일당", {"이제 나 혼자 있게 해 줘. 드라즈질브한테 가서 우린 여기 있을 거라고 말해. 여기에 검은용은 없어."}, {"담쟁이 마을에 있는 드라즈질브와 대화해야 합니다."}},
[1172] = {nil, {"오닉시아 일당은 용의 늪 전역에 흩어져 있다. 더는 새끼를 치도록 그냥 놔둬선 안 된다.","서둘러 먼지진흙 습지대 남쪽에 있는 용의 둥지로 가라. 틀림없이 그곳에 오닉시아의 소굴이 있을 거다. 그 사악한 용의 알을 찾아서 부숴 버려라.","돌망치 폐허는 그 주변이 용의 번식지로 남아 있는 한 되찾을 수 없을 것이다.","모크모로크에 대해서는... 내가 다른 계획을 세워 놓겠다."}, {"담쟁이 마을에 있는 드자리블이 오닉시아의 알 5개를 파괴해 달라고 했습니다."}},
[1173] = {"대군주 모크모로크에게 도전", {"너 감히 이 대군주 모크모로크에게 도전하나?","크하핫!","이 조그만 <race> 녀석, 짓뭉개 준다!","실컷 두들겨 맞고 싶으면 언제라도 와라."}, {"모크모로크에게 이긴 후 담쟁이 마을에 있는 드라즈질브에게 이 소식을 보고해야 합니다."}},
[1175] = {"경주 중의 충돌", {"고블린의 경주용차 붉은 번개호와 바실리스크가 충돌하는 큰 사고가 일어난 후 크론크라이더가 경주로 관리를 위해 날 고용했네. 도대체 소금 평원의 모든 짐승들이 뭘 먹고 그렇게 거대해졌는지 모르지만, 어쨌든 우리는 놈들의 수를 최대한 줄여 경주가 진행되는 동안 경주로에 들어오는 일이 없도록 해야 하네.","돈벌이를 찾고 있다면 이 지역에 있는 바실리스크를 좀 처치해 주시게나."}, {"소금 평원에 있는 경주로관리인 제린을 위해 소금바위 바실리스크 10 마리, 소금바위 수정바실리스크 10 마리 및 소금바위 큰눈바실리스크 6 마리를 처치해야 합니다."}},
[1176] = {"하중 줄이기", {"경주에는 모든 요소가 다 중요하지! 출력을 세부적으로 조정하거나 인장 고리를 조금 느슨하게 하거나 차체 하중을 낮출 때에도 모든 점을 고려해야 한단 말이오.","우리는 경주용차의 세밀한 구조를 변경하는데 독수리 뼈를 많이 사용하고 있지. 독수리 뼈는 강도와 중량의 균형을 완벽하게 맞춰주는 재료라오. 게다가 이곳엔 독수리들이 우글거리니 구하기도 쉽고 말이오.","내게 독수리 뼈를 좀 구해다 주면 값을 후하게 쳐 주도록 하겠소."}, {"소금 평원에 있는 포직을 위해 가벼운 독수리 뼈 10개를 구해야 합니다."}},
[1177] = {"배고픈 오우거", {"머드는 덩치 큰 오우거.","머드 배고프다.","물고기 잡아 줘. 머드 먹을 거 갖다줘.","머드 먹을 거 안 주면 머드가 너 잡아먹을 거야."}, {"먼지진흙 습지대 북쪽에 있는 머드크러쉬 더트피트가 진흙아가미 멀록의 머리 8개를 가져다 달라고 했습니다."}},
[1178] = {"고블린 후원", {"우리 경주팀은 어딜 가나 고블린의 자랑거리지. 다양한 방법으로 우리 팀을 후원하는 사람들의 얘기를 들으면 아마 깜짝 놀랄 것이오.","가즈로를 아시오? 그자는 우리를 위해 곳곳에서 부품을 구해다 주고 있소. 한번은 직접 와서 장치를 손봐 준 적도 있을 정도지.","몇 주 전에 그가 새로운 연료 조절 장치의 원형을 보낸다고 했는데 물건이 아직 도착하지 않고 있소. 절대 늦는 법이 없었는데... 당신이 톱니항으로 가서 그에게 좀 물어봐 주겠소? 뭐, 강요하는 건 아니지만 노움에게는 절대로 지기 싫단 말이오."}, {"불모의 땅의 톱니항에 있는 가즈로와 대화해야 합니다."}},
[1179] = {"브라스볼츠 형제", {"소금 평원에 대해 들어 본 적 있소? 칼림도어의 버섯구름 봉우리 협곡 남쪽에 넓게 펼쳐져 있는 염분이 침전된 평지지. 평탄한 지대라 경주를 하기에는 그만이라오.","그곳에서 내 친구 노움 형제가 새로운 로켓 자동차를 만들고 있소. 내가 들은 바로는 정말 빠르다고 하오. 너무 빨라서 계속 충돌 사고를 일으켜 조종사들이 부상을 입을 정도라지.","그래서 내게 보호용 보호모를 부탁했는데, 난 해야 할 일이 있어서 이곳을 떠날 수 없소.","당신이 나 대신 좀 가 주겠소?"}, {"버섯구름 봉우리의 소금 평원에 있는 위즐 브라스볼츠에게 충격방지 보호모 상자를 가져가야 합니다."}},
[1180] = {"고블린 후원", {"자, 이제 자네가 여기 온 용건에 대해 얘기해보자고. 그래, 내가 포직에게 새로운 연료 조절 장치를 보내 주겠다고 약속한 건 맞아. 그런데 나도 아직 물건을 받아보지 못했다네. 무법항에 있는 레빌가즈가 그걸 배에 실어 보냈다는데 아마 어딘가에 빠져버린 모양이야.","그게 사실이라면 어쩔 수 없지. 우선 물건이 무법항에서 발송된 게 맞는지 확인해 봐야 할 것 같아.","그곳 부두주임 로즈길을 만나 보라고. 그가 뱃짐 기록을 확인해 줄 거야."}, {"무법항에 있는 부두주임 로즈길과 대화해야 합니다."}},
[1181] = {"고블린 후원", {"도움이 되지 못해 죄송합니다. 좀 더 조사해 보려면 남작께 한번 여쭤 보십시오. 레빌가즈님께서 하시는 일은 본인이 가장 잘 아실테니까 말입니다.","지금쯤 부두 건너편 선술집의 맨 꼭대기 층에 계실 겁니다."}, {"무법항에 있는 남작 레빌가즈와 대화하십시오."}},
[1182] = {"고블린 후원", {"가시덤불 골짜기 북쪽 나즈페리티 호수에 있는 투자개발회사의 벌목기에 새로운 모델의 연료조절장치가 장착되어 있네. 포직이 가서 자세한 정보를 캐낼 줄 알았는데, 그곳 감독관 코즐이란 자가 생각보다 영리하단 말이야.","보아하니 당신도 그 설계도를 꼭 찾고 싶어하는 것 같은데... 그 감독관이 집안 어딘가에 설계도를 보관하고 있다는 건 확실하네. 하지만, 아마 자물쇠로 단단히 채워져 있을 거야."}, {"무법항에 있는 남작 레빌가즈에게 연료조절장치 설계도를 가져가야 합니다."}},
[1183] = {"고블린 후원", {"자, 준비가 다 됐으니 어서 떠날 채비를 하게. 포직에게 날 대신해 안부를 전하고 필요한 게 있으면 망설이지 말고 얘기하라고 전해 주라고. 머지않아 그가 경주 기록을 경신했다는 소식을 들을 수 있기를 기대하지."}, {"소금 평원에 있는 포직에게 연료조절장치 설계도를 전해야 합니다."}},
[1184] = {"실리시드 등껍질", {"오그리마에 있는 벨그롬님에게 돌아가서 목타르가 버섯구름 봉우리에서 명령을 잘 수행하고 있더라고 전해 주시오. 그리고 내 연구에도 진척이 있어 곧 더 많은 것을 알게 될 거라고도 전해 주시오. 벨그롬님이 크게 기뻐하실 거요."}, {"오그리마에 있는 벨그롬 록마울에게 가야 합니다."}},
[1185] = {"껍질 속에 담긴 비밀", nil, nil},
[1186] = {"18번째 조종사", {"이제 좀 바빠지겠군. 이 새로운 연료 조절 장치를 우리 경주용차의 설계에 적용하려면... 만약 우리 팀을 도와줄 의향이 있다면 저기 래저릭과 얘기해 보라고. 그는 우리 팀에서 정확하게18번째 조종사라오. 아무튼 그가 경주용차를 조금 개조하고 싶어하지.","그자는 정신이 약간 이상하니까 너무 놀래키지는 말라고."}, {"소금 평원에 있는 래저릭과 대화하십시오."}},
[1187] = {"래저릭의 호기", {"포직은 훌륭한 기술자죠. 저도 그건 인정하지만 원체 겁이 많아서 저 엄청난 출력을 자랑하는 차에 한번 타보지도 못하고 있다니까요. 하지만 난 달라요.","보통 포직이 완성한 경주용차를 제가 약간 손보곤 해요. 제어장치를 좀 느슨하게 풀고, 차체의 군더더기를 제거해서 속력을 빠르게 만들지요!","가젯잔의 기즈모리움에 있는 쉬리브에게 시포리움 부스터를 주문해 두었어요. 그런데 그걸 싣고 오던 비행선이 먼지진흙 습지대에서 추락한 것 같아요. 그게 꼭 필요해요."}, {"소금 평원에 있는 래저릭을 위해 시포리움 부스터를 입수해야 합니다."}},
[1188] = {"안전 제일", {"잠깐, 이게 뭐지? 쉬리브가 안전 장치를 켜 놓았군. 이러면 부스터에서 얻을 수 있는 힘이 줄어들잖아. 분명히 꺼 달라고 말했는데! 흠... 이 상태로는 안 되겠군요.","이걸 타나리스 사막에 있는 가젯잔 기즈모리움으로 가져가서 쉬리브에게 안전 장치를 제거해 달라고 해 주세요."}, {"가젯잔에 있는 쉬리브에게 시포리움 부스터를 가져가야 합니다."}},
[1189] = {"안전 제일", {"자, 래저릭이 부탁한 그대로 고쳤습니다. 시동을 걸 때 조심하라고만 전해 주세요. 작은 실수라도 하면 앞으로 나가기는커녕 경주로 위에서 잿더미가 돼 버릴 테니...","그렇게 돼 버린다면 정말로 부끄러운 일일 거예요. 그나마 그다음 19번째 조종사가 잘해내서 따라잡을 수 있다면 다행이지만... 그건 아무도 장담할 수 없잖아요?"}, {"버섯구름 봉우리의 소금 평원에 있는 래저릭에게 개조된 시포리움 부스터를 다시 가져가야 합니다."}},
[1190] = {"속도 유지", {"브라스볼츠 형제의 차가 훨씬 빨라졌는데 설계나 주행에 관한 모든 자료들은 이전과 동일하단 말이지. 뭔가 새로운 자재를 발견한 게 분명해. 그것밖에는 이 상황을 설명할 길이 없군.","우리가 아는 건, 리즐 브라스볼츠가 지속적으로 새로운 뭔가를 발견하기 위한 계획에 힘써 왔다는 사실이야.","자멕이 놈들의 주의를 다른 곳으로 돌리면 당신이 그의 계획서를 좀 빼내 달라고."}, {"소금 평원에 있는 포직을 위해 자멕이 리즐의 주의를 다른 곳으로 돌리는 동안 그의 설계도를 훔쳐야 합니다."}},
[1191] = {"자멕의 교란", nil, nil},
[1192] = {"인듀리움 광석", nil, nil},
[1193] = {"부서진 함정", nil, nil},
[1194] = {"리즐의 설계도", {"리즐의 설계도에는 인듀리움 광석으로 부품을 강화하는 과정이 설명되어 있습니다. 그가 써 놓은 내용으로 보아 황야의 땅의 울다만에 있는 드워프의 발굴지에서 인듀리움 광석을 구해 온 것 같습니다.","다른 쪽지는 대부분 내화 특성에 관한 것들입니다.","그 쪽지는 설계의 사전 단계에서 사용된 것으로, 제련되지 않은 인듀리움 광석에 대해 상당한 내용이 담겨 있습니다."}, {"소금 평원에 있는 포직에게 인듀리움 광석 견본을 가져가야 합니다."}},
[1195] = {"신성한 불꽃", {"버섯구름 봉우리에 있는 갈라크 켄타우로스들은 켄타우로스가 창조되던 시기의 유물을 보호하고 있네. 우린 그걸 찾고 싶어! 그러려면 나이트 엘프의 달샘에서 떠온 물 한 병이 필요하다네.","샘물을 모으려면 잿빛 골짜기 숲 중심부에 있는 레인나무 은신처 근처의 드리아드들이 가지고 다니는 병 중 하나를 손에 넣어야 하네. 약병을 채울 수 있는 달샘은 대로 남쪽에 있는 팔파렌 강의 서쪽 둑 근처에 있네."}, {"썬더 블러프 상층에 있는 잔겐 스톤후프에게 가득 찬 청옥 약병을 가져가야 합니다."}},
[1196] = {"신성한 불꽃", {"가득 찬 약병을 버섯구름 봉우리 남동쪽 높새바람 봉우리에 있는 라우 클리프러너에게 가져다주게. 불모의 땅 남쪽 끝에 있는 구름 승강기를 타면 버섯구름 봉우리로 갈 수 있을 걸세.","자네가 앞으로 우리에게 큰 도움이 될 것이라는 것은 의심할 여지가 없군."}, {"높새바람 봉우리에 있는 라우 클리프러너에게 가득 찬 청옥 약병을 전해야 합니다."}},
[1197] = {"신성한 불꽃", {"당신이 가져온 물로 켄타우로스 유물을 감싸고 있는 불꽃을 일시적으로 누그러뜨릴 수는 있을 거요. 하지만 영구적인 게 아니니 서둘러야만 하오.","여기서 북쪽으로 가면 갈라크 켄타우로스의 주둔지인 갈래발굽 바윗골을 찾을 수 있을 거요. 불꽃은 바윗골 안에서도 경비가 삼엄한 지역인 갈래발굽 소굴에 있소이다.","조심하시오. 이곳에는 갈라크 켄타우로스가 아주 많은데다 자신들이 소중하게 여기는 것을 그렇게 쉽게 내놓으려고 하지 않을 테니까."}, {"높새바람 봉우리에 있는 라우 클리프러너에게 갈래 발굽을 가져가야 합니다."}},
[1198] = {"타엘리드 찾기", {"요즘 잿빛 골짜기 해안 서쪽의 검은심연 나락에 있는 나가와 결탁한 황혼의 망치단에 대한 많은 소문이 나돌고 있소.","은빛 여명회에서 내 임무는 가능한 많은 정보를 수집하고, 그것이 언제 어디에 필요한지 파악하는 것이오.","검은심연 나락 내부의 동태를 살피려고 정찰병을 보낸 지 며칠이 지났소. 그런데 아직도 정찰병 타엘리드로부터 보고를 받지 못했소. 다른 정찰병들은 이미 긴급 임무에 투입된 상태이니, 당신이 타엘리드를 찾아 그를 좀 도와주시오."}, {"검은심연 나락에서 정찰병 타엘리드를 찾아야 합니다."}},
[1199] = {"황혼의 망치단의 몰락", {"혼돈의 고대 신을 위한 고대 의식으로 힘을 얻은 황혼의 망치단 단원들이 사악한 후원자들을 환생시켜 죽음과 공포를 전 세계에 퍼뜨리려 하고 있습니다.","이들은 나가와 동맹을 맺고 서쪽에 있는 검은심연 나락에 집결하고 있다고 하는군요.","그들이 그 오래된 신전 깊은 곳에서 어떤 사악한 음모를 꾸미고 있든지 이를 반드시 좌절시켜야 합니다.","황혼의 망치단과 결전을 벌이고 승리의 증거물을 가지고 돌아오십시오. 수고에 대한 보상은 후하게 해 드리겠습니다."}, {"검은심연 야영지에 있는 파수꾼 알루윈에게 황혼의 펜던트 10개를 가져가야 합니다."}},
[1200] = {"검은심연의 음모", {"이제 저는 아무 힘도 없습니다. 당신의 도움이 필요해요!","아주 오래전 이곳은 위대한 엘룬의 신전이었습니다. 하지만 불행히도 한 타락한 고대 신의 기운이 땅 밑에서 솟아나와 성스러운 달샘을 오염시키면서 폐허가 되어 버렸지요.","고대 신의 시녀인 아쿠마이가 물 위로 올라왔기 때문이었습니다.","황혼의 망치단 이교도들은 이 땅을 점령하기 위해 나가와 동맹을 맺었는데, 켈리스를 따르는 이 이교도 집단은 죄 없는 자들을 아쿠마이에게 제물로 바쳐 힘을 얻고 있습니다.","켈리스를 해치우고 그의 머리카락을 검은심연 야영지의 애쉬엘란에게 가져다주십시오!"}, {"황혼의 군주 켈리스를 처치하고 그 증거로 머리카락을 검은심연 야영지에 있는 애쉬엘란 노스우드에게 가져가야 합니다."}},
[1201] = {"테라모어 침투요원", {"호드와 얼라이언스는 불안정한 휴전 협정을 맺고 있네. 서로 외진 곳에서 접전을 벌이고 은밀한 공작을 펼치고 있는 이러한 시기에 호드의 승리를 확고히 하는 것이 바로 우리 임무라네. 지금이 바로 그 임무를 위한 절호의 기회라 할 수 있지!","얼라이언스 침투요원들이 담쟁이 마을 동쪽 지역을 장악하고 있네. 녀석들은 마을을 염탐하고 우리의 움직임을 테라모어에 보고하고 있지. 이건 용납할 수 없는 행위야.","놈들을 찾아 처치하도록 하게. 침투요원 따위가 활개치는 것을 그냥 보고만 있지 않을 거란 걸 놈들의 지휘관들에게 똑똑히 보여 주게!"}, {"테라모어 침투요원 9명을 처치한 다음 담쟁이 마을에 있는 나지르 블러드파이크에게 가야 합니다."}},
[1202] = {"테라모어 부두", {"우리 첩보원 중 두 명이 테라모어 부두에 있는 배에서 서류를 훔쳐오는 임무를 띠고 파견되었는데, 서류를 입수했으나 발각되고 말았네. 그래서 서류를 바다에 던질 수밖에 없었지.","한 명은 나중에 도망쳤지만 서류는 되찾지 못했네. 그는 내게 임무 실패 사실을 보고했고 이제 그 임무를 자네에게 맡기려고 하네.","문서는 테라모어 부두 밑의 물속 어딘가에 가라앉아 있는 작은 상자 안에 있네. 그 서류를 찾아서 내게 가져오도록 하게."}, {"담쟁이 마을에 있는 나지르 블러드파이크에게 선장의 서류를 가져가야 합니다."}},
[1203] = {"자를의 검", {"내가 뭘 좀 잘라야 하는데... 뭘 자를 건지는 말해 줄 수 없지만... 아무튼 난 검이 좀 필요합니다. 좀 특별한 검인데...","어떤 이들은 그냥 검이라고 부르지만 난 흔들검이라고 부르지요.","어쨌거나 절 좀 도와주시겠어요? 그 물고기같이 생긴 녀석들, 멀록인지 뭔지 말입니다. 멀록들 사이에서 서슬돌기라고 큰 검을 지니고 다니는 녀석이 있는데 이곳에서 북쪽에 해안 근처에 삽니다. 녀석이 지닌 그 검이 바로 제게 필요한 검입니다!","하지만 시간을 너무 지체하지 마십시오. 제가 자르려는 게 영원히 그대로 있지는 않을 테니까요..."}, {"먼지진흙 습지대에 있는 \"늪지눈알\" 자를에게 서슬돌기의 검을 가져가야 합니다."}},
[1204] = {"진흙바위거북 수프와 벌레", {"지금 전 '진흙바위거북 수프와 벌레'라고 하는 저만의 특별 요리를 만드는 중이지요. 그런데 아직 주재료가 부족하답니다. 그리고... 전 먼지진흙 습지대를 가로지를 만큼 오래 걸을 수 없을 거 같습니다.","그러니 제 대신 사냥을 좀 해주시겠어요? 먼저 진흙바위거북 혓바닥이 필요해요. 대해로 이어지는 만 근처의 해안가에 가시면 진흙바위거북을 찾을 수 있을 거예요.","하지만 아무 혓바닥이나 사용할 수는 없습니다! 일부 진흙바위거북의 혀는 갈래가 져 있다고 들었어요.","그들 말로는 그 혓바닥이 독특한 맛이 난다고 하던데, 제 요리에 그 맛을 내고 싶어요."}, {"테라모어에 있는 모건 스턴에게 진흙바위거북 혓바닥 8개를 가져가야 합니다"}},
[1205] = {"붉은눈늪지악어", {"이제 먼지진흙 습지대에 살고 있는 고대 악어인 붉은눈늪지악어를 처치할 때가 되었소.","녀석이 늪 속에서 거대한 몸을 움직일 때마다 녀석의 늙은 뼈들이 삐걱거리며 갈리고 있소. 녀석은 젊었을 때처럼 여전히 힘이 넘치고 빠르지만, 점점 늙어가는 몸이 그 안에 있는 위대한 영혼을 괴롭히고 있소이다. 영원히 꺼지지 않을 불꽃 같은 영혼을 말이오.","아직도 그는 미칠 듯한 고통 속에 살고 있소. 그대가 붉은눈늪지악어가 안식을 얻을 수 있도록 이끌어 줘야 하오. 이 고귀한 생명의 삶을 끝내고 내게 그의 이빨을 가져 오시오."}, {"썬더 블러프에 있는 멜로르에게 붉은눈늪지악어 이빨을 가져가야 합니다."}},
[1206] = {"자를의 부탁", {"눈알 주스는 좋은 음료랍니다. 대부분의 동물에게 눈이 두 개밖에 없다는 게 참으로 애석할 뿐이지요.","하지만 거미는 눈이 많지요. 엄청나게 많답니다. 그리고 암흑안개거미 눈 주스는 맛이 가장 좋답니다. 부드럽고 짭짤하지요! 터지지 않은 거미의 눈... 전 이미 맛본 적이 있습니다.","암흑안개거미는 서쪽에 있는 암흑안개거미 굴에 서식하고 있습니다. 그 굴에서 거미 이름을 따왔다는 걸 눈치챘겠죠. 아니면... 반대로 거미 이름에서 굴 이름을 따왔나요?"}, {"늪지기 오두막에 있는 \"늪지눈알\" 자를에게 터지지 않은 암흑안개거미의 눈 20개를 가져가야 합니다."}},
[1218] = {"습지 개구리 다리", {"저는 요리하는 걸 아주 좋아합니다. 맛있는 늪지 요리 말입니다. 바로 청개구리 뒷다리 튀김 같은 것이지요.","제가 개구리 요리를 하려는데 재료가 되는 다리가 떨어져 가고 있답니다. 다행스럽게도 이 오두막은 개구리들로 넘쳐나지요, 바로 거대한 습지 개구리들 말입니다. 제 생각엔 당신이 개구리 다리를 좀 가져다줄 수 있을 거 같은데 어떻습니까? 아, 일이야 어려울 것 없습니다. 그저 녀석들의 머리를 딱 쳐서 그 다리를 가져오면 된답니다.","재료를 가져오면 그걸 스튜에 넣어 잘 끓여 나도 맛있게 먹고 또 당신한테도 좀 나누어 드리겠습니다.","개구리 스튜 좋아하십니까?"}, {"먼지진흙 습지대에 있는 \"늪지눈알\" 자를에게 거대한 개구리 다리 10개를 가져가야 합니다."}},
[1219] = {"오크 보고서", {"푸석푸석한 흙 사이에 오크의 손만이 드러나 있습니다. 꽉 쥔 손에는 뼈를 깎아 만든 관이 쥐여 있습니다. 그리고 관 안에는 흙 묻은 양피지 문서가 들어 있습니다.","양피지 문서는 오크어로 쓰여 있으며... 중요한 문서 같아 보입니다."}, {"테라모어에 있는 부관 아덴에게 밀정의 보고서를 가져가야 합니다."}},
[1220] = {"사령관 바임스", {"당신이 발견한 오크 보고서는 반드시 바임스 사령관님께 보여 드려야 하오. 그분은 누구보다도 오크어를 잘 아시는데다가 테라모어 근방에 호드 첩보원이 있다는 사실도 아셔야 하오!","사령관님은 테라모어 섬 북동쪽에 있는 병영에 계시오."}, {"사령관 개런 바임스에게 밀정의 보고서를 가져가야 합니다."}},
[1221] = {"청엽수 줄기", {"청엽수 줄기는 세계적인 별미지! 하지만 희귀해. 아주 희귀해. 청엽수 줄기는 불모의 땅 깊숙한 곳에 있는 가시덩굴 우리에서만 찾을 수 있어.","그곳에서도 청엽수 줄기가 있는 곳을 정확히 알지 못하면 하늘에서 별 따기라니까! 내가 이 땅다람쥐를 조련하는 것도 바로 그 때문이지. 후각이 워낙 뛰어나 오십보 밖에서도 청엽수 줄기 냄새를 맡을 수 있거든.","쉽지는 않을 테지만 내게 청엽수 줄기를 좀 가져다주면 후하게 보답할 거야."}, {"구멍 난 상자를 집어야 합니다.","땅다람쥐 지휘봉도 챙겨야 합니다.","땅다람쥐 설명서를 찾아서 읽어야 합니다.","가시덩굴 우리에서 구멍 난 상자를 사용해 땅다람쥐를 소환하고 지휘봉을 사용해 땅다람쥐에게 청엽수 줄기를 찾아내도록 해야 합니다.","톱니항에 있는 메보크 미지릭스에게 청엽수 줄기 6개와 사용한 땅다람쥐 지휘봉과 구멍 난 상자를 가져가야 합니다."}},
[1222] = {"말썽꾼의 탈출", {"오, 감사합니다. 이제야 날 잡아먹으려 들지 않는 이를 만나다니!","이 주변에 있는 짐승들을 처리하는 일을 도와줄 이가 필요한데, 마침 당신이 이 일에 안성맞춤일 것 같군요.","요리사 모건 스턴이 새로운 조리법을 연구하고 있는데, 그가 진흙콩 잎을 모아오라고 날 여기로 보냈지요. 아마 그 잎이 요리에 안성맞춤이라고 생각하는 것 같아요.","아무튼, 진흙콩 식물을 찾던 중... 그만 포위되어 버리고 말았지 뭡니까! 여기서 탈출하도록 날 좀 도와주겠어요? 그리고 내가 진흙콩을 찾는 동안 내 뒤를 좀 지켜 주세요!"}, {"\"말썽꾼\" 이그나츠를 호위한 다음 테라모어에 있는 모건 스턴과 대화해야 합니다."}},
[1238] = {"사라진 보고서", {"푸석푸석한 흙 사이에 오크의 손만이 드러나 있습니다. 꽉 쥔 손에는 뼈를 깎아 만든 관이 쥐여 있습니다. 그리고 관 안에는 흙 묻은 양피지 문서가 들어 있습니다.","양피지 문서는 몇 주 동안 테라모어 도시를 염탐한 오크에 의해 작성된 보고서입니다.","양피지 문서를 자세히 살펴본 결과, 담쟁이 마을에 있는 오크 나지르 블러드파이크에게 전달해야 할 보고서였던 것이 분명합니다."}, {"담쟁이 마을에 있는 나지르 블러드파이크에게 밀정의 보고서를 가져가야 합니다."}},
[1239] = {"무서운 가면", {"열심히 땅을 파자 거칠게 보이는 남자 오크의 가면이 나왔습니다. 입술은 으르렁거리며 굳은 듯, 다소 반항적인 눈동자로 당신을 노려보는 형상입니다."}, {"담쟁이 마을에 있는 나지르 블러드파이크에게 험상궂은 오크 가면을 가져가야 합니다."}},
[1240] = {"트롤 의술사", {"반드시 마그의 죽음을 둘러싼 사건들에 대해 알아내야 하네! 트롤의 마법을 통해서만 가능할 거야.","마그의 가면을 가시덤불 골짜기의 그롬골 주둔지에 있는 킨윌레이에게 가져가게.","킨윌레이는 검은창 트롤 중 가장 강력한 의술사일세. 마그 나이트아이의 영혼과 이야기할 수 있는 자가 있다면 바로 그 친구일 거야."}, {"가시덤불 골짜기의 그롬골 주둔지에 있는 킨윌레이에게 혼이 깃든 오크의 가면을 가져가야 합니다."}},
[1241] = {"실종된 사신", {"최근 테라모어에 사신을 보내 제이나 프라우드무어 님을 만나 보도록 했었소. 그런데 그 사신이 그곳에 도착하지 않은 모양이오.","나는 데피아즈단이 이 음모와 관련이 있다고 보지만 자세한 내막은 확실하지 않소. 아직은 주민들에게 사신의 행방불명 소식이 알려지지 않았으나, 언제까지나 비밀로 묻어 둘 수는 없는 일이오.","이미 폐하의 신하들이 단서를 찾고자 도시를 조사하고 다니고 있지만, 나는 내 나름의 인맥을 이용해 보려고 하오.","영웅의 계곡에서 낚시를 즐기길 좋아하는 요르겐이라는 내 옛 친구가 있소. 그를 찾아 이 편지를 전하고 그의 지시를 따르도록 하시오."}, {"스톰윈드에서 요르겐을 찾아야 합니다."}},
[1242] = {"실종된 사신", {"이 쪽지를 엘링 트리아스에게 전하시오. 하지만 그에게 그 외의 다른 말은 절대 하지 마시오. 그저 예의바르게 그가 대답할 때까지 기다리기만 하면 되오. 기다리면 대답이 있을 테니 걱정은 하지 말고 말이오.","다시 한번 강조하지만, 이번 일은 진지하게 임해야 하고 반드시 입 조심 해야 하오.","트리아스는 상업 지구에 그가 운영하는 트리아스 치즈 상점에 있을 거요."}, {"스톰윈드 내에 있는 엘링 트리아스에게 엘링에게 보내는 봉인된 쪽지를 전해야 합니다."}},
[1243] = {"실종된 사신", {"요르겐, 이 몹쓸 친구 같으니라고... 그래, 결국 이렇게 되는 것인가?","괜찮습니다. 이 정도는 감당할 수 있습니다. 요르겐 그 친구가 어서 빨리 뒤틀린 황천에나 가 버렸으면 좋겠군요!","앞으로 어떻게 될 것인지 말씀드리죠. 여기서는 내 인맥을 이용해 보겠지만, 발로 뛰어다녀야 할 일의 대부분은 직접 맡아 주셔야겠습니다. 그래야 돈을 좀 절약할 수 있으니까요.","그늘숲의 다크샤이어로 가서 순찰대원 바커스를 찾으세요. 그는 주로 마을 바로 외곽의 북쪽 도로를 순찰한답니다. 날 위해 데피아즈단의 활동에 대해 조사 중이라고만 말하면 그가 아는 모든 정보를 알려 줄 겁니다."}, {"그늘숲에 있는 순찰대원 바커스에게 봉인된 쪽지를 가져가야 합니다."}},
[1244] = {"실종된 사신", {"데피아즈단의 활동? 글쎄요, 그들은 항상 무슨 일을 꾸미고 있지요. 설사 그들이 조용히 지낸다 하더라도 위협으로 간주해야 할 정도니...","아, 이제 생각해 보니 최근에 이상한 일을 목격한 적이 있습니다. 몇 주 전에, 그들 요원 중 몇몇이 애들 농장 근처에 모였던 적이 있었습니다. 이에 대한 보고를 듣고 뭔가 큰일이 아닐까 싶은 생각이 들더군요.","우선 그곳을 확인해 보고 뭔가 발견하면 내게 가져와 보는 게 어떻겠습니까? 그 농장은 당신이 그늘숲으로 들어올 때 지나온, 서부 몰락지대로 이어진 도로의 남쪽에 있습니다."}, {"데피아즈단의 일정표를 찾아 그늘숲에 있는 순찰대원 바커스에게 가져가야 합니다."}},
[1245] = {"실종된 사신", {"이런, 당신을 따돌리기 위한 물건이라고 하기엔 지나치게 복잡하군요. 난 이런 건 제대로 보지도 못하겠습니다. 이걸 트리아스에게 가져가도록 하십시오. 그라면 이해할 수 있을 겁니다. 그리고 그 누구도 당신을 방해하지 못하도록 해야 합니다. 이건 내가 감당할 수 있는 것 이상이군요. 트리아스도 감당할 수 없을지 모르지만 두고 볼 일이죠.","그리고 내 부탁 하나 합시다. 아무에게도 내가 당신을 도왔다는 말은 하지 말아 주시기 바랍니다. 내 상관들이 내가 이 일에 대해 조금이라도 알고 있으면서, 그들에게 알리지 않았다는 걸 알게 되는 날에는 틀림없이 난 교수형을 당하게 될 겁니다."}, {"스톰윈드에 있는 엘링 트리아스에게 데피아즈단의 일정표를 가져가야 합니다."}},
[1246] = {"실종된 사신", {"아, 제 기억으론... 다셸... 다셸 뭔데... 이름이 뭐였더라? 아! 그래요, 피스트! 다셸 스톤피스트. 어떤 이들은 그를 '돌주먹'이라고 부른답니다. 아마 그가 싸움질을 좋아하기 때문일 겁니다.","그는 주로 구 시가지 중앙에서 바로 오른쪽에 위치한 곳에 주로 있답니다. 그를 만나 보시는 게 좋겠군요. 필요하다면 검이나 다른 묵직하고 단단한 것을 사용해 자백을 받아내도록 하십시오. 그리고 주저해서는 안 됩니다. 이 문서에 나와 있는 대로 정말 그가 이 일에 깊이 관여하고 있다면 어떠한 정보도 쉽게 내주진 않을 테니까 말입니다."}, {"스톰윈드에 있는 다셸 스톤피스트와 대화해야 합니다."}},
[1247] = {"실종된 사신", {"좋아, 음, 에이들의 은신처에서 있었던 회의에 대해 좀 알고 있지. 몇 주 전이었던 거 같은데, 난 절대 거기에 참여하진 않았소. 1차 작전이 실패했을 때 내 임무는 이미 끝났다고... 그놈의 모험가들 때문에...","2차 작전을 위해 내부에서 포섭한 홀쭉이라는 자가 메네실에서 왔었소. 2차 작전에 관해서 이게 내가 아는 전부요."}, {"스톰윈드에 있는 엘링 트리아스와 대화해야 합니다."}},
[1248] = {"실종된 사신", {"내가 아는 이 중 하나가 저습지에 메네실 항구 도시에 칩거하고 있습니다. 당신이 날 위해 일하고 있다는 얘기만 하면 기꺼이 도와줄 겁니다. 그의 이름은 미크헤일이라고 합니다. 몇 해 전 스톰윈드를 떠나기 전에는 바텐더가 되겠다고 말했었지요.","그와 얘기한 다음 이 홀쭉이란 자를 찾도록 하세요. 그리고 이 납치된 사신에 대한 다른 정보가 있는지 찾아보시기 바랍니다. 우리는 그를 어디로 데려갔는지, 아니면 최소한 그가 누구인지라도 알아내야 합니다. 이 일정표에는 사신을 납치한 후의 계획에 대해선 어떠한 언급도 없습니다."}, {"저습지에 있는 미크헤일과 대화해야 합니다."}},
[1249] = {"실종된 사신", {"엘링 씨가 여기로 보냈다고 하셨습니까? 흠, 그렇다면 열 배로 더 환영입니다. 필요한 게 있으면 뭐든 말씀만 하세요. 최선을 다해 돕겠습니다.","흠, 홀쭉이라고요? 그 이름을 들어 본 적이 있는지는 확실치 않지만, 힘닿는 대로 그를 찾아보도록 하겠습니다.","지금은 이리저리 둘러보지 마십시오. 출구 옆의 저 신사분이 이쪽을 보고 있습니다. 우리 대화를 좀 지나치게 집중하면서 듣고 있어요. 무슨 말인지 아시죠? 만약 제가 손님이라면 저자가 문으로 도망가기 전에 그를 잡아 버릴 겁니다."}, {"타포케 잔이 도망가기 전에 그를 제압한 다음 메네실에 있는 미크헤일에게 가야 합니다."}},
[1250] = {"실종된 사신", {"데피아즈단이 놈들을 위해 목숨을 걸 만큼 충분히 많은 돈을 내게 지급한 건 아니니까... 놈들은 왕을 납치한 거야. 그래, 하찮은 사신 따위가 아니라 스톰윈드의 왕을 말이야. 왕은 제이나 프라우드무어를 만나기 위해 테라모어로 가는 길이었지. 뭔가 대단히 중요하고도 비밀스러운 목적이 있는 것 같았어. 하지만 이 일에 관련된 건 데피아즈단만이 아니야. 데피아즈단은 다른 누군가와 함께 일하고 있어. 아마 놈들이 혼자 감당하기에는 벅찬 일이라는 걸 알았던 것 같아.","내가 한 일은 그저 배가 출항하기 전 항구에 있을 때 놈들이 보낸 헨델이라는 자를 왕의 배에 몰래 태운 것뿐이야. 맹세코 이게 내가 아는 전부라고!"}, {"저습지에 있는 미크헤일과 대화해야 합니다."}},
[1251] = {"검은 방패", {"그늘 쉼터 여관 건물의 얼마 남지 않은 부분 중 하나인 허물어진 굴뚝에 여관 전체를 휩쓸어 버린 불로 검게 그을린 철제 방패가 붙어 있습니다.","굴뚝의 벽돌에서 방패를 떼어낼 수 있습니다."}, {"담쟁이 마을에 있는 크로그에게 타락의 철제 방패를 가져가야 합니다."}},
[1252] = {"부관 파발 리테", {"잔해 밑에서 뭔가가 반짝이는 것이 눈에 띕니다. 그 물건을 덮고 있는 잔해와 재를 털어내자 표면에 에나멜을 입힌 흰색 바탕에 황금 닻이 그려진 배지가 드러납니다. 바로 테라모어의 상징입니다.","닻 아래에는 '부관 파발 리테'라는 이름이 새겨져 있습니다."}, {"테라모어에 있는 사령관 개런 바임스에게 리테의 배지를 가져가야 합니다."}},
[1253] = {"검은 방패", {"그늘 쉼터 여관 건물의 얼마 남지 않은 부분 중 하나인 허물어진 굴뚝에 여관 전체를 휩쓸어 버린 불로 검게 그을린 철제 방패가 붙어 있습니다.","굴뚝의 벽돌에서 방패를 떼어 낼 수 있습니다."}, {"테라모어에 있는 사령관 개런 바임스에게 검은 철제 방패를 가져가야 합니다."}},
[1258] = {"... 그리고 벌레", {"새로 시도해 보는 이번 요리에 들어갈 또 다른 재료로는 거대한 게의 딱딱한 다리입니다. 먼지진흙 습지대에서 서식하는 가시 바위 게들이 재료로 안성맞춤일 겁니다!","게 다리의 껍질이 이번 요리의 모양을 결정하는 핵심적인 요소니까 긁히지 않은 온전한 다리가 필요합니다. 다 큰 게에는 그런 다리가 한 개 내지는 두 개밖에 없는데 제 요리를 연구하려면 많이 필요하답니다.","테라모어 주위의 바위로 올라가서 게 다리를 제게 가져다주십시오!"}, {"테라모어에 있는 모건 스턴에게 온전한 집게 다리 12개를 가져가야 합니다."}},
[1259] = {"부관 파발 리테", {"부관 파발 리테라는 이름은 처음 들어 보는데... 부관 테소렌을 만나 확인해 보는 게 현재로선 가장 현명한 선택일 듯하네. 테소렌은 개인 신상 정보에서 장비에 이르기까지 테라모어 경비병에 대한 모든 기록을 보관하고 있으니까 말일세.","그라면 이 리테라는 자가 누구인지 자료를 찾아 금방 확인해 줄 수 있을 것일세."}, {"테라모어에 있는 부관 테소렌으로부터 리테에 대해 알아내야 합니다."}},
[1260] = {nil, {"전 식당에서 선보일 새로운 메뉴를 개발하고 있습니다. 호수를 주제로 한 전원풍의 요리들로 구성할 참이지요. 아마 고독한 은둔자 선술집에 있는 우리 단골이 아주 좋아할 거예요!","조카 모건을 먼지진흙 습지대 가장자리에 있는 도시인 테라모어로 보냈지요. 그 지역의 조리법을 수집하고 있는데, 지금까지 얼마나 수집했는지 궁금하네요.","테라모어까지는 아주 먼 길입니다. 제게는 너무나도 먼 길이지요. 하지만 당신처럼 젊은 <class>|1이라면;라면; 이런 여행 기회를 마다하리라 생각되지 않는데... 어떻습니까?"}, {"테라모어에 있는 모건 스턴과 대화해야 합니다."}},
[1261] = {"말하는 마그", {"괘씸한 진흙껍질 녀석들!! 누구도 이 마그를 죽일 순 없어! 아, 그렇지만 난 죽어버렸어. 틀림없이 놈이 나를 죽인 거야.","하지만 내 임무! 내 임무는 어떻게 하지? 당신이 내 임무를 완수해 줬으면 해.","테라모어 근처에 있는 동안 난 해변에 있는 얼라이언스 병사들을 염탐했었지. 그들 중 한 명이 펜던트를 걸고 있었는데... 보통 병사들은 가질 엄두도 못 낼 만큼 많은 보석이 박힌 펜던트였어.","그날 밤 난 놈들의 야영지에 몰래 숨어들어 펜던트를 훔쳤지만 기지로 돌아오는 길에 진흙껍질 게 인간 녀석이... 날 죽이고 말았어!","그 진흙껍질을 찾아 반드시 펜던트를 찾아와야 해!"}, {"담쟁이 마을에 있는 나지르 블러드파이크에게 보석 펜던트를 가져가야 합니다."}},
[1262] = {"조르에게 보고하기", {"마그가 훔친 펜던트가 일개 병사들의 수중에 있다니 이상한 일이군. 어쩌면 그 주인이 변장을 하고 있었던 것일 수도 있고 아니면 그 병사들이 직접 훔친 것일 수도 있네!","어느 쪽이 사실이든지 간에 이것은 귀족의 펜던트이고 이 펜던트의 수수께끼를 풀려면 진짜 주인을 찾아야 하네.","오그리마에 있는 조르 론트리에게 그 펜던트를 가져다주게. 얼라이언스 정치학을 오래 공부한 자라 이 펜던트가 스톰윈드의 귀족들 간에 유서가 깊은 물건인지 알 수 있을 걸세."}, {"오그리마에 있는 조르 론트리에게 보석 펜던트를 가져가야 합니다."}},
[1263] = {"불타는 여관", nil, nil},
[1264] = {"실종된 사신", {"매월 초 모든 쿨 티라스 해병들은 대개 월급을 받고 테라모어를 떠나 다른 곳으로 갑니다. 그들 중 많은 이들은 술과 여자를 찾아 흥청망청 놀기 위해 이곳으로 오곤 하죠. 이 선술집의 단골손님이라 할 수 있는 그런 무리 중 하나에 이 헨델이란 자도 있었던 것 같군요.","헨델이 아직 테라모어에 배치돼 있다면 그곳의 사령관 사마울 님이 그를 찾는 걸 도와줄 수 있을 겁니다. 사마울 님은 프라우드무어 님께 충성심이 아주 대단하지만, 그래도 엘링의 이름을 대면 아마 따로 도와줄 겁니다."}, {"먼지진흙 습지대에서 사령관 사마울을 찾아야 합니다."}},
[1265] = {"실종된 사신", {"헨델? 물론 알고 있소만 내 병사는 왜 찾는 거요?","엘링이 그를 찾기 위해 당신을 보냈다고? 흠, 별로 마음에 들지 않는 소린데... 일이 잘못되지 않았다면 엘링이 먼저 당신을 내게 보냈을 리가 없소. 무슨 일인지 말해줄 수 없다는 건 이해하겠소. 하지만 이건 알아 두시오. 나에게는 이 땅을 보호할 책임이 있소. 그러니 당신이 문제를 일으킬 거라면 내게 미리 알려 주기 바라오.","헨델은 테라모어 외곽의 첫 번째 탑에 있을 거요. 조심하시오."}, {"감시초소에서 병사 헨델에 대한 단서를 찾아야 합니다."}},
[1266] = {"실종된 사신", {"병사 헨델은 이곳에서 북서쪽으로 가면 찾을 수 있습니다. 지금 그는 테라모어의 첩보원들 중 일부가 휴식을 취하는 동안 그들을 보호하는 경비 임무를 수행하고 있답니다. 그들은 호드 기지인 담쟁이 마을을 주시하면서 우리 영토의 경계를 위협할지 모를 다른 적들을 수색하고 있습니다.","도로변에 있는 야영지만 찾으면 그를 쉽게 볼 수 있을 겁니다.","난 처리해야 할 다른 일이 있어서 이만... 하지만 곧 당신을 따라잡을 수 있을 겁니다. 문제가 언제든지 생기면 테라모어에 있는 프라우드무어님의 탑으로 날 찾아오시기 바랍니다."}, {"먼지진흙 습지대에서 병사 헨델을 찾아야 합니다."}},
[1267] = {"실종된 사신", nil, nil},
[1268] = {"수상한 발굽 자국", {"아직도 연기가 피어나는, 잔해만 남은 그늘 쉼터 여관에서 나와 이어져 있는 발굽 자국이 있습니다. 최소한 몇 개는 눈으로 확인할 수 있지만 다른 자국들은 습지의 부드러운 흙에 묻혀 알아보기 어렵습니다."}, {"담쟁이 마을에 있는 크로그에게 그늘 쉼터 여관의 수상한 흔적에 대해 보고해야 합니다."}},
[1269] = {"부관 파발 리테", {"잔해 밑에서 뭔가가 반짝이는 것이 눈에 띕니다. 그 물건을 덮고 있는 잔해와 재를 쓸어내자 표면에 에나멜을 입힌 흰색 바탕에 황금 닻이 그려진 배지가 드러납니다. 바로 테라모어의 상징입니다.","닻 아래에는 '부관 파발 리테'라는 이름이 새겨져 있습니다."}, {"담쟁이 마을에 있는 크로그에게 리테의 배지를 가져가야 합니다."}},
[1270] = {"말썽꾼의 탈출", {"오, 감사합니다. 이제야 날 잡아먹으려 들지 않는 이를 만나다니!","이 주변에 있는 짐승들을 처리하는 일을 도와줄 이가 필요한데, 마침 당신이 이 일에 안성맞춤일 것 같군요.","톱니항에서 온 메보크 미지릭스라는 고블린이 진흙콩 잎이 필요하다더군요. 물약을 만드는 데 쓴다나 뭐라나... 뭐, 솔직히 나야 뭘 위한 거든 상관없고... 그가 꽤 넉넉히 돈을 쳐주는데다가 내가 이 늪지 지리에는 밝거든요. 그래서 여기 온 거랍니다.","그런데 안타깝게도 진흙콩을 찾기도 전에 온통 짐승들에 둘러싸여 버렸답니다! 내가 메보크에게 가져다줄 잎을 찾는 동안 내 뒤를 좀 지켜 주지 않겠어요?"}, {"\"말썽꾼\" 이그나츠를 호위한 다음 톱니항에 있는 메보크 미지릭스와 대화해야 합니다."}},
[1271] = {"고독한 은둔자 선술집에서의 잔치", nil, nil},
[1272] = {nil, nil, nil},
[1273] = {"리테 심문", {"시간은 오래 걸렸지만 리테를 찾아냈다. 인간치고는 잘 숨어 다니더군.","이 오그론은 늪지에 그렇게 오래 머물다 미쳐 버리게 될까 봐 걱정했다고. 당신 나랑 같이 그 녀석을 심문하러 가자."}, {"오그론과 함께 가서 리테를 심문한 다음 담쟁이 마을에 있는 크로그에게 돌아가야 합니다."}},
[1274] = {"실종된 사신", {"실례합니다. 드라베이 주교님께서 제게 까다로운 일을 도와줄 수 있을 만한 모험가를 찾아보라고 말씀하셨어요.","괜찮으시면 가능한 한 빠르고 조용히 스톰윈드 왕궁으로 가셔서 그분을 만나 보세요. 여행자분께서 도와주신다면 주교님이 정말 고마워하실 겁니다. 다시 한번 말씀드리지만 중요한 일이니 신중하셔야 해요."}, {"스톰윈드 왕궁에서 주교 드라베이를 찾아야 합니다."}},
[1275] = {"타락에 대한 연구", {"바보 같은 블러드 엘프들이 악마의 마법을 써 대다니... 칼림도어에서 사티로스와 나가들에게 어떤 일이 일어났는지 보지 못했단 말인가?","블러드 엘프들도 그처럼 기형으로 변해 버릴까 걱정스러워요. 아제로스에 더는 사악한 종족이 태어나선 안 돼요.","신도레이의 운명이 앞으로 어떻게 될지 추론해 볼 수 있도록 사티로스와 나가의 뇌간을 연구해 보고 싶어요. 서쪽에 있는 검은심연 나락에서 사티로스와 나가를 모두 찾을 수 있을 거예요."}, {"검은심연 야영지에 있는 렐윈 쉐이드스타가 변이된 뇌간 8개를 가져다 달라고 했습니다."}},
[1276] = {"검은 방패", {"아, 이제 기억나는군! 모사른은 썬더 블러프에서 존경받는 대장장이요. 전에 어디에선가 그의 이름을 들은 기억이 있다 했어.","영광스럽게도 나는 대지 고리회의 이름으로 모사른이 만든 갑옷을 얻을 수 있었지. 그건 정말 잘 만들어진 갑옷이었소.","그가 만든 방패에 대해서는 그에게 직접 듣는 편이 나을 거요.","큰 단서는 아니지만 리테에게서 별다른 걸 알아내지 못했으니 이제 이것이 우리가 가진 유일한 단서요."}, {"썬더 블러프에 있는 모사른에게 검은 철제 방패를 보여줘야 합니다."}},
[1281] = {"짐의 노래", nil, nil},
[1282] = {"유쾌한 짐이라 불리는 사나이", {"저 늙은 짐만 생각하면 불쌍해서 견딜 수가 없습니다. 예전엔 저런 모습이 아니었는데... 그는 한동안 습지에 있는 여관을 운영했었는데 정말 훌륭한 여관이었습니다.","흠, 그에 대해서는 더는 얘기하고 싶지 않습니다. 정 원한다면 바임스 사령관님께 가 보시기 바랍니다. 사령관님께서는 제이나 군주님의 탑 동쪽에 있는 거대한 병영인 거점 요새의 최상층에 계십니다."}, {"거점 요새의 최상층에 있는 사령관 개런 바임스와 대화해야 합니다."}},
[1284] = {"수상한 발굽 자국", {"아직도 연기가 피어나는, 잔해만 남은 그늘 쉼터 여관에서 나와 이어져 있는 발굽 자국이 있습니다. 최소한 몇 개는 눈으로 확인이 가능하지만 다른 자국들은 습지의 부드러운 흙에 묻혀 알아보기 어렵습니다."}, {"테라모어에 있는 사령관 개런 바임스에게 그늘 쉼터 여관의 수상한 흔적에 대해 보고해야 합니다."}},
[1285] = {"대린의 부하", {"레드돈... 레드패스... 아, 여기 있군! 부관 파발 리테.","프라우드무어님과 함께 바다를 건너온 대부분의 신병들과 함께 테라모어 경비병으로 지원했소이다. 16세 때 쿨 티라스의 해병대에 입대했고... 뛰어난 전과와 진급...","실종된 것으로 기록되어 있소."}, {"테라모어에 있는 사령관 개런 바임스에게 돌아가 리테에 대한 정보를 전해야 합니다."}},
[1286] = {"탈영병", {"이런 습지대에 주둔지를 건설하고 군대를 유지하는 일이 힘들긴 하지만 우리는 불굴의 의지로 지역 내에 방어에 필요한 몇 개의 감시탑을 만들어 치안을 유지해 올 수 있었다네.","그런데 최신 입수한 정보와 정찰 보고에 따르면 탈영병 무리가 여관 남동쪽의 버려진 경비탑에 숨어든 것 같네.","그 지역 부관이 밸로스 제킨이라는 자 같네. 가서 그에게 정보를 캐내 보도록 하게.","그는 만만한 상대가 아닐 테니 조심해야 할 걸세."}, {"먼지진흙 습지대에 있는 밸로스 제킨과 탈영병들에게서 리테에 대한 정보를 알아내야 합니다."}},
[1287] = {nil, {"리테? 그 겁쟁이 말인가?","자꾸 담쟁이 마을에서 오우거의 보급품을 약탈하자고 해서 야영지에서 내쫓아 버렸어. 우리는 그런 생각 없는 망나니들을 상대할 만큼 여유롭지 않으니까 말이야.","넘어서는 안 될 선을 너무 많이 넘은 놈이었지. 그래서 혼자 살아가도록 내버려뒀어.","내가 당신을 도와주었으니 이제 당신이 우릴 도와주면 어떨까? 뭐, 갑자기 당신이 불쑥 들이닥친 건 피차 잊어버리고 말이야..."}, {"테라모어에 있는 사령관 개런 바임스와 리테에 대해 대화해야 합니다."}},
[1288] = {nil, nil, nil},
[1301] = {"제임스 하이얄", {"이곳 고독한 은둔자 선술집에서 일하기 전에 전 지금까지 만나 본 중에 가장 뛰어난 요리사이자 여관 주인이었던 분에게서 교육을 받았죠. 바로 제임스 하이얄이라는 분인데, 일생 제가 가진 것, 지위는 모두 그분 덕분이라 할 수 있습니다.","잘 지내고 계시는지 궁금하네요. 그분의 형제인 빈센트와 함께 선술집에서 일하시려고 메네실 항구로 가셨는데 한동안 소식을 듣지 못했습니다.","혹시 그쪽으로 가시면 그분께 제 안부를 좀 전해 주시겠습니까?"}, {"메네실 항구에 있는 빈센트 하이얄과 대화해야 합니다."}},
[1302] = {"제임스 하이얄", {"내 동생 제임스가 프라우드무어 님과 함께 바다를 건너갔습니다. 내가 마지막으로 들은 건 그곳에서 전투가 끝나 제임스가 어딘가에서 여관을 열었다는 소식이었지요. 그 녀석은 어디에 있든 잘해낼 겁니다. 정말 요리 하나는 끝내주게 잘하거든요.","그러고 보니 소식을 들은 지도 꽤 됐는데... 아마 여관 일이 너무 바쁜가 봅니다. 테라모어의 서기관에게 가면 틀림없이 그가 어디에 있는지 알려줄 겁니다. 테라모어의 중앙 근처에 있는 요새의 최상층에서 서기관을 찾아보십시오. 그 요새 이름이 뭐더라... 아, 맞아요. 거점 요새입니다."}, {"테라모어 거점 요새에 있는 서기관 렌드리와 대화해야 합니다."}},
[1318] = {"고르독 일 마무리", {"이제 당신 새 왕이다, 토르텔드린이라고 하는 기분 나쁜 엘프에게서 소중한 고르독 유물 빼앗아줘! 이전 왕은 실패했고, 토르텔드린이 아주 오래전 왕에게서 유물 훔쳐간 뒤로 다른 임금들 다 실패했다!","그 유물은 권력의 고르독 건틀릿이라고 한다. 옛날 얘기에는 진짜 고르독 왕만이 갖고 올 수 있다고 한다. 옛날 얘기에는 기분 나쁜 엘프가 아직도 이 근처에 있다고 한다. 그 유물 찾으면 평생 왕이 끼고 있게 될 거다! 그래, 내가 보여준다!"}, {"권력의 고르독 건틀릿을 찾아 혈투의 전장에 있는 대장 크롬크러쉬에게 가져가야 합니다.","크롬크러쉬의 \"옛날 얘기\"에 따르면 자칭 왕자라는 토르텔드린이라는 이름의 \"기분 나쁜\" 엘프가 고르독 왕에게서 빼앗아갔다고 합니다."}},
[1319] = {nil, {"크게 도움을 주지 못해 미안하네. 내 도움이 될 만한 이를 알려 주도록 하지. 이곳에서 가장 뛰어난 대장장이 중에 카즈 투스프로켓이라고 하는 자가 있는데, 가열로 근처에서 일하고 있네.","그에게 그 방패를 가져가 보도록 하게. 그 친구라면 뭔가 알고 있을 걸세."}, {"테라모어에 있는 카즈 투스프로켓에게 검은 철제 방패를 보여줘야 합니다."}},
[1320] = {"검은 방패", {"아, 잊어버릴 뻔했는데 말이죠... 분명 뒤에 달린 가죽끈은 불에 타 없어졌겠지만, 몸집이 큰 친구에 맞게 만들어진 것 같네요. 아마 타우렌들이 사용하던 게 아닌가 싶은데...","내가 말해 줄 수 있는 건 그게 다에요. 바임스 사령관님께는 큰 도움이 못 돼 죄송하다고 전해 주세요."}, {"테라모어에 있는 사령관 개런 바임스와 대화해야 합니다."}},
[1321] = {"검은 방패", {"흠... 이 방패에 이상한 점은 없는 것 같은데... 대장장이 도골에게 가져가서 이에 대해 뭔가 아는 바가 있는지 알아봐야 할 것 같군. 특별한 기대는 하지 않는 게 좋겠지만 말이야."}, {"담쟁이 마을에 있는 도골에게 타락의 철제 방패를 보여 주십시오."}},
[1322] = {"검은 방패", {"흐음, 바로 여기 방패 테두리에 글자가 새겨져 있지만 불에 타서 읽기가 어렵다.","방패를 닦아낼 산성 용액이 필요하다. 암흑안개거미의 독이 용액으로 쓰기에 좋을 거다. 그 거미들은 마을의 북서쪽에 있는 암흑안개 굴이라는 오래된 광산을 점령하고 있다. 녀석들의 독주머니를 구해 오면 방패를 깨끗이 닦아낼 수 있을 거다."}, {"담쟁이 마을에 있는 도골이 산성 독주머니 5개를 모아 달라고 했습니다."}},
[1323] = {"검은 방패", {"오, 방패에 산성 독액을 너무 많이 떨어뜨린 것 같은데... 조금 닦아내 봐야겠다...","흠, 읽기 어렵긴 하지만, 모사른이라는 이름 같군. 그런 이름은 들어 본 적이 없는데... 아마 크로그가 알 거다. 크로그는 아는 사람이 많으니까."}, {"담쟁이 마을에 있는 크로그와 대화해야 합니다."}},
[1324] = {"실종된 사신", {"뭐라고?! 내가 데피아즈단과 손잡았다는 걸 밝혀내려고 여기까지 왔다고? 당신은 내가 이런 모욕을 당하고도 가만히 있을 거라 생각하는 거요? 참을 수가 없군! 아무리 생각해도 당신은 진작에 죽어야 했어!"}, {"먼지진흙 습지대에 있는 병사 헨델을 쓰러뜨려야 합니다."}},
[1338] = {"스톰파이크의 주문서", {"내가 아주 존경하는 드워프 방어구 제작자가 한 명 있소. 푸렌 롱비어드라고 하는데, 그의 기술은 타의 추종을 불허하지. 난 그가 만든 새 방패를 원한다오!","그런데 문제는... 그가 남쪽으로 멀리 떨어져 있는 스톰윈드 내의 드워프 지구에 살고 있다는 거요. 인간의 땅에 말이오! 그가 왜 아이언포지에서 그렇게 멀리 떨어진 곳에 사는지 도무지 모르겠소. 필시 그곳 인간들이 그를 잡아두기 위해 거액을 쏟아 붓고 있겠지!","여행도 하고 돈도 벌 생각이 있다면 푸렌 씨를 찾아가 내 방패를 좀 주문해 주겠소?"}, {"스톰윈드에 있는 푸렌 롱비어드에게 스톰파이크의 주문서를 가져가야 합니다."}},
[1339] = {"산악경비대 스톰파이크의 임무", {"산악경비대 스톰파이크가 심부름꾼을 찾고 있다고 들었소. 그를 위해 잠시 여행을 다녀올 자를 말이지. 어떻소? 당신이야말로 이 일에 적격이라고 생각하는데...","북쪽 경비탑 꼭대기로 가서 스톰파이크를 만나 보시오."}, {"산악경비대 스톰파이크와 대화해야 합니다."}},
[1358] = {"헬브림에게 가져갈 견본", {"칼림도어에 있는 동료인 연금술사 헬브림은 로데론 독소가 칼림도어의 야수들에게 미치는 영향을 연구하고 있어요. 렌퍼럴이 내게 보내준 늑대 심장 견본에 큰 관심을 보일 거예요.","헬브림은 불모의 땅의 십자로라는 마을에서 지내고 있답니다. 그에게 가려면 비행선을 타고 오크족 수도인 오그리마까지 간 다음 남쪽으로 듀로타까지 걸어가야 해요. 그곳에서 서쪽으로 가면 불모의 땅인데 곧 십자로에 도착하게 될 거예요."}, {"불모의 땅에 있는 연금술사 헬브림에게 늑대 심장 견본을 가져가야 합니다."}},
[1359] = {"진게의 소포", {"자네가 가져온 이 늑대 심장들은 다른 비슷한 견본들에서는 보지 못한 성질을 갖고 있네. 이 심장에는 심장이 뛰고 있는 늑대 자신에게 치명적인 영향을 미치는 독성이 있어!","정말 놀라운 일이야. 반드시 더 연구해 봐야겠어. 자네가 가져온 심장 중 몇 개는 내가 견본으로 갖고 있네. 그것들을 내 동료인 연금술사 진게에게 가져다주게. 언더시티에 있는 우리 본부에서 진게를 만날 수 있을 걸세."}, {"언더시티에 있는 연금술사 진게에게 늑대 심장 견본을 가져가야 합니다."}},
[1360] = {"보물 찾기", {"한때 나는 황야의 땅에 있는 울다만 발굴현장에서 꽤 이름난 채굴꾼으로 일했지... 그 몹쓸 트로그놈들이 나타나기 전까지는 말이오. 난리통에 겨우 목숨만 건져서 나오는 바람에 가장 아끼는 물건을 두고 왔지 뭐요! 그 물건은 놈들이 우글대는 곳에 있소! 단단히 잠긴 궤짝 안에 있어서 트로그 놈들, 아직 보물 구경도 못했을 거야...","그걸 찾아와 준다면 당신에게 큰 빚을 지는 거지! 북쪽 대전당의 끝에 가면 내 이름이 적힌 궤짝이 있는데 그 속에 보물이 들어 있소."}, {"울다만의 북쪽 대전당에 있는 궤짝에서 크롬 스타우트암의 보물을 찾아서 아이언포지에 있는 크롬 스타우트암에게 가져가야 합니다."}},
[1361] = {"렉타르 데스게이트", {"명예로운 호드의 전사인 렉타르 데스게이트가 잊혀진 땅에서 임무를 수행할 대원을 모집하고 있소. 그는 불모의 땅에 있는 십자로의 서쪽 언덕 감시탑 아래에 있소.","잊혀진 땅은 전쟁으로 고통받고 있는 곳이니 준비를 단단히 해 가도록 하시오."}, {"렉타르 데스게이트와 대화해야 합니다."}},
[1362] = {"잊혀진 땅의 콜카르", {"콜카르 켄타우로스는 잊혀진 땅에 살고 있으며 그들의 군대는 불모의 땅 전역에 퍼져 있네. 이 때문에 우리 호드 대원들이 놈들을 상대하고 있는 것이지.","하지만 잊혀진 땅에서 켄타우로스가 힘을 행사하는 이상, 놈들은 항상 위협적인 존재가 될 것이야. 이제 그 위협을 제거할 때가 되었네.","잊혀진 땅의 침묵의 초소에 있는 펠굴 투커츠를 만나 보게. 그는 켄타우로스에 대한 정보를 수집하기 위해 파견되었으니 잊혀진 땅의 콜카르를 처리할 최선의 방법을 알고 있을 거야."}, {"잊혀진 땅의 침묵의 초소에 있는 펠굴 투커츠와 대화해야 합니다."}},
[1363] = {"마젠의 부탁", {"이곳 비전술 학회의 도서관 관리인으로서, 모든 마법 서적과 두루마리들을 항상 잘 관리하는 것이 내 임무요. 이 마법 문서 중 일부가 나쁜 자들 손에 들어가는 어리석은 일이 일어나지 말란 법도 없잖소?","내 조수 델리스가 지난주에 저지른 끔찍한 실수가 다시 떠오르는군... 그는 도서관 일은 꽤 잘해내고 있지만 스톰윈드 성문 밖으로만 나가면 무능해져 버린다오. 당신이 델리스를 돕는 영웅이 되어 주지 않으시겠소?"}, {"스톰윈드에 있는 수행사제 델리스와 대화해야 합니다."}},
[1364] = {"마젠의 부탁", {"마젠 님의 지시는 아주 분명했습니다. 네더가드 요새에 있는 감시자 마할 바에게 중요한 문서를 전달하라는 것이었지요.","하지만 저는 저주받은 땅에 가 본 적이 한 번도 없었다고요! 여행은 아주 끔찍했죠.","슬픔의 늪에서 길을 잘못 드는 바람에 식물처럼 생긴 무시무시한 괴물들에게 둘러싸였었거든요. 절 공격할 것처럼 다가오기에 도망쳤어요. 그렇게 서두르다가 문서를 떨어뜨렸죠. 분명히 그 괴물 중 하나가 집어 갔을 겁니다.","문서를 되찾아서 마할 바에게 전해 주시지 않겠어요?"}, {"차원의 집합점에 대한 카드가의 평론을 찾아 네더가드 요새에 있는 감시자 마할 바에게 전해야 합니다."}},
[1365] = {"칸 데즈헤파", {"잊혀진 땅엔 콜카르 켄타우로스만 있는 게 아닐세. 수많은 부족이 이 메마른 평원을 누비고 다니며 서로 싸우고 죽이곤 하지. 워낙 강하고 거친 녀석들이라 자기들끼리 싸우는 게 천만다행이야. 만약 녀석들이 손을 잡고 우리를 공격한다면 아주 끔찍한 적이 될 테니까 말일세. 녀석들이 뭉치기 전에 손봐 놓아야만 하지.","당신이 해줘야 할 첫 번째 임무는 콜카르 일족을 분열시키는 걸세. 그러려면 녀석들의 우두머리인 칸 데즈헤파를 처치하면 될 테고.","콜카르 마을은 여기서 북동쪽에 있으니 어서 가게. 행운을 빌겠네."}, {"칸 데즈헤파를 처치하고 그 증거로 그의 머리카락을 잊혀진 땅에 있는 펠굴 투커츠에게 가져가야 합니다."}},
[1366] = {"켄타우로스 현상금", {"켄타우로스 부족들에게 공포감은 충분히 심어 주었으니 이제는 녀석들에게 우리의 힘을 보여 줄 차례군. 잊혀진 땅을 여행하는 동안 켄타우로스를 사냥해 주게. 어느 부족을 사냥하든 상관없으니 녀석들에게서 귀걸이를 가져오게.","녀석들의 수를 줄여서 기를 꺾어 놓으면 영토 확장은 꿈도 꾸지 못하게 될 게야. 그럼 즐거운 사냥이 되길 빌겠네."}, {"잊혀진 땅의 펠굴 투커츠에게 켄타우로스의 귀걸이 15개를 가져가야 합니다."}},
[1367] = {"마그람 동맹", {"켄타우로스를 물리치려면 반드시 서로에 대한 증오를 이용해야 합니다. 우리는 그들 부족 중 하나와 동맹을 맺은 후에 그들을 통해 모든 부족을 물리칠 방법을 알아내려고 합니다.","마그람과 동맹을 얻으려면 그들의 숙적인 겔키스들을 처치해야 합니다. 마그람의 신뢰를 쌓은 후에 잊혀진 땅 남동쪽의 마그람 마을 외곽에 있는 마그람 대변인 와루그를 만나 보도록 하십시오."}, {"마그람에게 우호적인 평판을 얻은 다음 와루그와 대화해야 합니다."}},
[1368] = {"겔키스 동맹", {"켄타우로스를 물리치려면 반드시 서로에 대한 증오를 이용해야 합니다. 우리는 그들 부족 중 하나와 동맹을 맺은 후에 그들을 통해 모든 부족을 물리칠 방법을 알아내려고 합니다.","겔키스와 동맹을 맺으려면 그들의 숙적인 마그람을 처치해야 합니다. 겔키스의 신임을 얻은 후에 잊혀진 땅 남서쪽의 겔키스 마을 외곽에 있는 겔키스 주술사인 현자 우텍을 만나 보도록 하십시오."}, {"겔키스에게 우호적인 평판을 얻은 다음 현자 우텍과 대화해야 합니다."}},
[1369] = {"눈물의 파편", {"겔키스들은 우리 켄타우로스 어머니, 테라드라스 님께서 우릴 지켜 주신다고 생각하지. 놈들은 멍청이들이야. 누구도 켄타우로스를 지켜 주지 않아. 그리고 마그람은 보호 같은 건 필요 없다고! 우린 가장 강한 부족이니까!","겔키스들에게 그 어리석음을 일깨워 주도록 해. 놈들 마을에 있는 동굴로 가. 놈들이 모아 둔 수정, 테라드라스의 눈물이 그 동굴에 있지. 테라드라스의 눈물을 파괴한 다음 부서진 조각들을 내게 가져와."}, {"마그람에게 우호적인 평판을 유지하면서 마그람 마을에 있는 와루그에게 눈물의 파편 3개를 가져가야 합니다."}},
[1370] = {"보급품 훔치기", {"우리가 전쟁을 치르지 않을 때는 고기를 얻기 위해 사냥을 하지. 고기는 우리에게 힘을 주고 배가 든든해야 잘 싸울 수 있는 법이니까 말이야.","마그람도 마찬가지야. 놈들은 사냥하지 않고도 오래 전쟁을 치를 수 있도록 여분의 고기를 저장해 두고 있지. 네가 동쪽에 놈들 마을을 습격해서 저장되어 있는 고기를 빼앗는다면 놈들은 힘을 잃게 될 거야."}, {"겔키스 마을에 있는 현자 우텍에게 말린 고기 6개를 가져가야 합니다."}},
[1371] = {"와루그의 물건", {"오래전에 작은 노움 녀석이 잊혀진 땅으로 왔었지. 그 녀석은 말과 마차가 있었고 마차에는 처음 보는 물건들이 실려 있었어. 괴상한 것들 말이야. 여러 가지 색깔로 불에 탄 물건들, 움직이지만 살아 있지는 않은 것들, 난 그 작은 노움 녀석을 처치해 버렸는데... 그 전에 그 녀석이 나를 웃게 했어.","녀석의 마차에서 가장 마음에 드는 물건 하나를 내가 가졌지. 나무로 만든 인간이었는데 내가 때려 부수기 전까지 계속 나에게 손을 흔들었어.","그게 새로 하나 더 있었으면 좋겠어. 새로 하나 구해다 줘!"}, {"마그람에게 우호적인 평판을 유지하면서 잊혀진 땅의 마그람 마을에 있는 와루그에게 고급 표적 허수아비를 가져가야 합니다."}},
[1372] = {"진실의 물약", {"우리 원정 부대는 바리마트라스 님의 명령으로 아제로스 저지대를 조사하기 위해 파견되었네.","정찰 중에 나는 인간 침투요원이 부랑자 소굴을 염탐하고 있는 것을 발견했지. 내가 녀석을 잡기도 전에 우리 오크 형제 중 한 명이 그를 스토나드의 슬픔의 늪에 있는 요새로 끌고 갔네.","우리의 오크 동맹군을... 돕고... 싶다네. 그들은 이곳에 우리가 야영하고 있는 것을 보지 못했지만 어쨌든 그들을 돕고 싶네.","이곳에 있는 파우스틴을 도와 스토나드의 우리 친구들을 위해... 그... 진실의 물약을 만들도록 하게."}, {"부랑자 소굴에 있는 연금술사 파우스틴과 대화해야 합니다."}},
[1373] = {"온게쿠", {"넌 아직 좀 더 증명해 보일 게 있어. 다음 임무를 수행하려면 먼 곳까지 가게 될 테니 당분간 겔키스는 너의 도움 없이 마그람과 싸우게 되겠지.","땅은 귀를 기울여 듣는 자에게 수많은 비밀을 속삭여 주지. 내가 들은 것 중 하나는 붉은 수정에 대한 것이야. 그것은 드레니시스트라고 불리는데 이 땅에는 처음 발견되었지. 원래는 산들이 날아다니고 악마들이 지배하는 곳에서 나는 수정이지.","드레니시스트 파편을 내게 가져오라고. 그러면 내 마법이 강력해질 테니! 드레니시스트 파편은 잃어버린 드레나이 온게쿠가 차지하고 있는 슬픔의 늪이라는 곳에서 찾을 수 있을 거야."}, {"겔키스에게 우호적인 평판을 유지하면서 잊혀진 땅의 겔키스 마을에 있는 현자 우텍에게 드레니시스트 파편 하나를 가져가야 합니다."}},
[1374] = {"칸 젠", {"땅이 내게 말을 하고 있어. 마그람이 약해져 가고 있다는군. 지금이 바로 그들의 우두머리를 처치할 때야!","마그람의 우두머리 칸 젠의 머리띠를 가져와!"}, {"칸 젠을 처치하고 그 증거로 그의 머리띠를 잊혀진 땅의 겔키스 마을에 있는 현자 우텍에게 가져가야 합니다."}},
[1375] = {"칸 샤카", {"넌 마그람을 돕는 자로군. 난 네가 맘에 들어. 자, 이제 겔키스의 우두머리를 처치해 줘! 칸 샤카의 머리띠를 가져오라고! 녀석의 머리띠를 손에 들고 비웃어 주고 싶단 말이다!"}, {"마그람에게 우호적인 평판을 유지하면서 칸 샤카를 처치하고 그 증거로 그의 머리띠를 잊혀진 땅의 마그람 마을에 있는 와루그에게 가져가야 합니다."}},
[1380] = {"칸 흐라사", {"넌 우리가 마그람 녀석들을 물리치는 것을 도와줬다. 이제 마라우돈 일족을 물리치는 것을 도와줘. 마라우돈 일족의 우두머리 칸 흐라사를 처치해!","그와 맞서려면 마라우돈 전쟁 뿔피리를 불어야 해. 뿔피리는 뾰족바위 골짜기에 있는 높은 절벽 꼭대기에 있다.","하지만 그 전쟁 뿔피리를 불려면 먼저 전쟁 뿔피리 파이프가 필요하지. 그것은 신임받는 마라우돈 켄타우로스들만이 지니고 있는 희귀한 물건이다.","그 전쟁 뿔피리를 불면 켄타우로스들이 올 거다. 흐라사가 나타날 때까지 녀석들을 처치해.","칸 흐라사에게 그... 열쇠 조각이 있어. 그 열쇠 조각을 내게 가져와라..."}, {"겔키스에게 우호적인 평판을 유지하면서 겔키스 마을에 있는 현자 우텍에게 마라우돈 열쇠 파편을 가져가야 합니다."}},
[1381] = {"칸 흐라사", {"겔키스의 우두머리가 죽었으니 이제 마라우돈 일족을 무너뜨릴 차례로군! 마라우돈 일족의 우두머리 칸 흐라사를 처치해!","그와 맞서려면 뾰족바위 골짜기의 높은 절벽 위에 있는 전쟁 뿔피리를 불어야 해. 하지만 그 전쟁 뿔피리를 불려면 파이프가 필요하지. 파이프를 구하려면 마라우돈 켄타우로스를 처치해야 해. 파이프를 발견하게 될 때까지 계속 처치하라고. 찾은 다음에는 그걸로 전쟁 뿔피리를 불어.","전쟁 뿔피리를 불면 탑으로 마라우돈 켄타우로스들이 올 거야. 칸 흐라사가 나타날 때까지 그들을 계속 처치해. 흐라사에게는 열쇠 조각이 있어. 이 열쇠 조각을 나에게 가져와라..."}, {"마그람에게 우호적인 평판을 유지하면서 잊혀진 땅의 마그람 마을에 있는 와루그에게 마라우돈 열쇠 파편을 가져가야 합니다."}},
[1382] = {"이상한 동맹", {"켄타우로스 부족들이 자기들끼리 서로 싸운다는 것이 우리에겐 행운이 아닐 수 없군. 저들이 하나의 군대로 뭉치면 아무도 막을 수 없을 테니 말이오. 그렇소, 정말 운이 좋은 거지. 그러니 반드시 이 사실을 잘 이용해야 하오.","켄타우로스 부족의 하나인 겔키스 일족이 마그람 일족과 격렬한 전쟁을 치르고 있소. 우리가 마그람을 공격한다면 겔키스와 동맹을 맺을 수 있게 될 거요.","잊혀진 땅 남서쪽의 겔키스 야영지 외곽에 있는 현자 우텍을 만나 보시오. 아마 그녀를 통해 겔키스와 동맹을 맺을 수 있을 거요."}, {"겔키스에게 우호적인 평판을 얻은 다음 현자 우텍과 대화해야 합니다."}},
[1383] = {"진실의 물약", {"내가 아주 완벽한 물약을 생각해 냈는데 말이오. 이 물약은 마땅히 이루어져야 할 일을 실현되게 할 거요.","이 물약을 만들려면 늪지에서 그림자표범 심장을 몇 개 가져와야 하고, 또 그곳에 사는 우두머리 늪괴물에게서 마법에 걸린 곰팡이도 가져와야 하는데... 당신처럼 능력 있는 자라면 곰팡이를 얻어올 수 있을 거요.","물약을 만드는 데 필요한 마지막 물건은 바로 꿈의 새끼용의 피라오. 그 새끼용은 습지대에 있는 이타리우스의 동굴 외곽에 사는 녀석들과 같지.","자, 이제 구하러 떠나시오!"}, {"부랑자 소굴에 있는 연금술사 파우스틴에게 그림자표범 심장 5개, 우두머리 늪괴물 곰팡이, 그리고 새끼 녹색용 피를 조금 가져가야 합니다."}},
[1384] = {"콜카르 습격", {"콜카르 켄타우로스는 자신들의 마법이 강하다고 생각하지. 하지만 놈들은 마법에 대해서 하나도 몰라! 마법으로 인해 구속을 당하게 되더라도 아무것도 모를 걸!","네가 놈들에게 자신들의 마법이 얼마나 약한지 가르쳐 주도록 해. 북동쪽으로 가서 콜카르 놈들을 처치해. 그리고 놈들의 부적을 가져와. 내게 부적을 가져오면 불 속에 처넣고 웃어 주도록 하지, 하하!"}, {"겔키스에게서 우호적인 평판을 유지하면서 잊혀진 땅의 겔키스 마을에 있는 현자 우텍에게 조잡한 부적 10개를 가져가야 합니다."}},
[1385] = {"야비한 정책", {"잊혀진 땅은 전쟁에 휩싸여 있소. 켄타우로스들이 서로 전쟁을 벌이고 있고 그들 중 승리하는 쪽이 세를 확장해 우리 본거지를 위협하게 될 거요. 그런 일이 일어나도록 내버려둬서는 안 되오.","우리는 켄타우로스를 격퇴할 방법을 찾아야 하오. 그리고 그러기 위해서는... 놈들 부족 중 하나와 동맹을 맺어야 하오. 마그람 켄타우로스들이 겔키스와 전쟁을 벌이고 있소. 당신이 겔키스에 대항해 싸운다면 마그람이 동맹을 고려할지도 모르오.","마그람의 호감을 산 다음 잊혀진 땅 남동쪽의 마그람 마을에 있는 와루그를 만나 보시오."}, {"마그람에게 우호적인 평판을 얻은 다음 와루그와 대화해야 합니다."}},
[1386] = {"콜카르 공격", {"콜카르 놈들은 자신들이 강하다고 생각하지만, 정말 웃기는 얘기지! 우리는 놈들을 비웃으며 처치해 버릴 거야! 너도 놈들을 처치해야 해. 너도 우리처럼 콜카르를 처치할 능력이 있다는 걸 보여 줘. 북쪽으로 가서 그들을 모두 처치해!"}, {"마그람에게서 우호적인 평판을 유지하면서 콜카르 켄타우로스 12명, 콜카르일족 척후병 12명, 콜카르일족 투사 6명을 처치한 다음 잊혀진 땅의 마그람 마을에 있는 와루그에게 돌아가야 합니다."}},
[1387] = {"켄타우로스 현상금", {"켄타우로스는 여러 부족으로 이루어져 있소. 각 부족마다 우두머리가 있고 자기들만의 방식이 있지만, 그들 모두 잔인하다는 공통점이 한 가지 있지.","우리가 평화를 되찾으려면 반드시 켄타우로스들을 제거해야만 하오. 그리고 바로 이 일을 당신에게 맡기려고 하오.","잊혀진 땅을 두루 돌아다니며 켄타우로스 녀석들을 처치하고 녀석들의 귀걸이를 가져오시오. 많으면 많을수록 좋소.","얼라이언스를 위해 이 일을 해주시기 바라오. 그러면 전쟁의 끝에 한 걸음 더 가까워지게 될 거요."}, {"잊혀진 땅의 나이젤의 야영지에 있는 하사관 멜킨스에게 켄타우로스의 귀걸이 15개를 가져가야 합니다."}},
[1388] = {"진실의 물약", {"모든 것이 계획대로 진행되고 있소. 당신이 죽음의추적자 즈레더스에게 이 진실의 물약을 가져다주길 바라오. 그러면 그가 그걸 어떻게 사용하는지 알려 줄 거요. 제대로... 사용하는 방법 말이지.","하하!"}, {"부랑자 소굴에 있는 죽음의추적자 즈레더스에게 파우스틴의 진실의 물약을 가져가야 합니다."}},
[1389] = {"드레니시스트 수정", {"드레노어 성역에 사는 드레나이들의 겉모습은 우리와 같지만 아제로스에 이르는 고된 여정으로 인해 미쳐 버렸소. 한때는 우리 형제였지만 지금은 동쪽의 드레노어 성역에 살면서 주위에 나타나는 이들은 전부 잡아가거나 죽이고 있다오.","그리고 우리에게 매우 신성한 물건인 드레니시스트 수정을 훔쳐 간 적도 있지.","드레노어 성역으로 들어가 그들의 오두막과 모닥불 사이에서 도난 당한 드레니시스트 수정을 되찾아 오기에는 우리 병력이 부족하오. 하지만 그 수정이 없으면... 우리까지 미쳐 버릴지도 모르오."}, {"슬픔의 늪의 피난처에 있는 막투르에게 드레니시스트 수정 6개를 가져가야 합니다."}},
[1391] = {"진실의 물약", {"이 일은 신중을 기해서 수행해야 하네. 그곳에서 왈츠나 추면서 그 바보에게 파우스틴의 물약을 마시도록 점잖게 설득하려는 건 아니니까 말이야, 그렇지 않나?","하지만 인간들이 약한 게 하나 있다면 바로 술이지. 파우스틴의 물약을 가져가서 이 에일 맥주에 탈 생각이네. 그 불쌍한 바보가 이 맥주통 소리를 들으면 주저 없이 마시리라고 확신하네.","서둘러 슬픔의 늪의 스토나드로 가서 조용하고 깔끔하게 우리의 임무를 처리하도록 하게."}, {"스토나드에 있는 첩보원 마크슨에게 즈레더스의 맥주를 가져가야 합니다."}},
[1392] = {"곤봉잡이 노보루", {"노보루는 피난처의 드레나이를 여러 달 동안 괴롭혀 왔습니다. 그의 곤봉은 잃어버린 드레나이에 의해 그들이 겪어야 했던 야만스러운 고통의 상징입니다."}, {"슬픔의 늪에 있는 피난처의 우두머리에게 노보루의 곤봉을 가져가야 합니다."}},
[1393] = {"갈렌 구출", {"살려 주세요! 이 짐승들이 날 잡아먹으려고 해요!","여기서 빠져나가게 도와주신다면 귀한 보물을 드릴게요! 보물은 스토나드 북쪽 늪지에 있는 제 야영지에 있어요. 금고 안에 들어 있는데, 저만 알고 있는 방법을 통해 열 수 있답니다.","제가 빠져나갈 수 있도록 도와주신다면 여는 방법을 가르쳐 드리겠어요."}, {"갈렌이 위험에서 벗어나도록 호위한 다음 슬픔의 늪에 있는 그의 야영지를 찾아 갈렌의 금고를 열어야 합니다."}},
[1394] = {"최후의 시험", {"잘했네. 지금까지의 시험들을 잘도 치러냈군. 이제 버섯구름 봉우리의 도른에게 돌아가 보게. 그가 자네의 몸과 마음, 그리고 영혼에 일어난 변화를 느끼고, 충분히 강해졌다는 것을 알아볼 거야. 그동안의 수고에 대해 보상을 받아야 할 만큼이라는 것도...","지금까지의 시험에서 보여 준 것처럼 계속 자신에게 충실하도록 하게. 그러면 언젠가 도른이 다시 자네를 시험하게 될 테니까..."}, {"버섯구름 봉우리에 있는 도른 플레인스토커와 대화해야 합니다."}},
[1395] = {"네더가드 요새 보급품", {"마법석이 들어있는 이 상자를 저주받은 땅에 있는 네더가드 요새로 가져가야 하네. 마법석은 오랫동안 그 힘을 유지하지 못할 테니 다음 짐마차가 올 때까지 기다릴 수는 없네.","자네가 마법석을 빨리 가져가야해. 이걸 네더가드 요새에 있는 병참장교 룬겔츠에게 전해주게.","요새에 가려면 동쪽의 저승바람 고개를 통과한 후 또 동쪽으로 슬픔의 늪에서 남쪽에 있는 저주받은 땅으로 들어서야 하네.","자네가 가는 길은 위험이 도사리고 있으니 조심하게나. 그렇지 않으면 실패할 거야."}, {"마법석의 힘이 다하기 전에 병참장교 룬겔츠에게 마법석 상자를 가져가야 합니다."}},
[1396] = {"야생 짐승의 공격", {"내 짐마차는 동쪽 해안으로 갈 예정이었지만 목적지에 다다르지 못했소이다. 잃어버린 드레나이들이 우리를 공격하여 경비병들을 죽이고 보급품을 다 박살 냈소. 나만 놈들의 공격에서 살아남았지.","난 남은 보급품을 지켜내기로 마음먹었지만 주위에 야생 짐승들이 숨어 있어서 쉽지 않소. 부디 짐승들로부터 내 야영지를 지킬 수 있도록 도와주시오!"}, {"새끼 톱니악어 8마리, 늪그물거미 10마리, 늪지재규어 10마리를 처치한 다음 슬픔의 늪에 있는 파수병 비그스에게 가야 합니다."}},
[1398] = {"유목", {"정말 큰 신세를 졌소. 당신 안에는 명예로운 심장이 뛰고 있군. 그래서 말인데 부탁했으면 하는 일이 하나 더 있소.","내가 어떻게든 네더가드로 가려면 이 마차를 수리해야만 하오. 마차를 수리하려면... 습지의 무르고 뒤엉킨 나무 말고 튼튼하고 잘 마른 나무가 필요하오.","동쪽의 해변으로 가서 햇볕에 잘 마른 유목을 좀 찾아서 내게 가져다주시오."}, {"슬픔의 늪에 있는 파수병 비그스에게 볕에 말린 유목 8개를 가져가야 합니다."}},
[1418] = {"니카 블러드스카", {"황야의 땅은 거친 땅이며 얼라이언스의 영토인 모단 호수와 가깝지. 그래서 스토나드와 카르가스의 황야의 땅 전초기지 간의 연락이 종종 두절되곤 하오.","자, 당신의 능력을 한번 증명해 보시오. 황야의 땅으로 가서 카르가스 전초기지의 정찰 대장인 니카 블러드스카를 만나 그녀와 대화한 다음 그녀의 보고 내용을 내게 다시 전하도록 하시오."}, {"니카 블러드스카와 대화해야 합니다."}},
[1419] = {"도둑 코요테", {"황야의 땅에서 지낸 지 오래되었습니까, <class>? 그 열기를 느껴본 적 있나요? 저 먼지 가득한 평원을 어슬렁거리는 짐승들과 마주친 적이 있습니까? 이제 당신이 직접 겪어 봐야 할 때가 됐군요.","바위산코요테는 사나운 종일 뿐 아니라 카르가스로 기어 들어와 우리의 식량을 훔쳐 갈 만큼 대담하기도 하답니다. 이 도둑들의 근거지를 제거하는 일 좀 도와주시기 바랍니다!","바위산코요테를 사냥하여 그 턱뼈를 내게 가져오세요, <name>. 충분한 양을 가져오면 당신이 황야의 땅에 대해서 잘 알고 있다는 걸 믿도록 하겠습니다."}, {"카르가스에 있는 니카 블러드스카에게 코요테 턱뼈 30개를 가져가야 합니다."}},
[1420] = {"헬그럼에게 보고", {"스토나드의 헬그럼에게 보낼 새로운 보고서를 작성했습니다. 이걸 그에게 전해 주시기 바랍니다.","그리고 당신이 스스로 길잡이가 될 생각이라면 방향을 잃지 않도록 조심하세요. 그렇지 않으면 적의 손아귀에 떨어질 수도 있으니까 말입니다."}, {"스토나드에 있는 날쌘돌이 헬그럼에게 니카의 보고서를 가져가야 합니다."}},
[1421] = {"사라진 짐마차", {"나는 드레노어 난민들이 공격하는 동안 기절해 있었지만 정신을 잃기 전에 짐마차를 끌던 말 한 마리가 두려움으로 날뛰며 북쪽에 드레노어 난민의 마을인 드레노어 성역으로 끌려가는 것을 보았소.","그 마차에는 귀중한 재료들이 들어 있는 짐마차 상자가 있었는데, 네더가드 요새의 마법사들의 연구에 중요한 재료들이 들어 있었소. 그 보급품들을 찾아서 내게 가져다주시오."}, {"드레노어 성역에서 마법사들의 재료가 들어 있는 짐마차 상자를 찾아 파수병 비그스에게 가져가야 합니다."}},
[1422] = {"바다의 위협", {"내가 자네 무기를 만드는 일을 시작할 동안 카타르와 얘기해 보는 게 어떤가? 그는 해안 순찰을 도와줄 일손을 찾고 있다고 했었네. 그리고 이곳의 별 볼일 없는 다른 이들과 어울려 술이나 마시는 것보다는 자네의 전투 감각과 무기를 잘 단련해 두는 게 낫지 않겠나?"}, {"슬픔의 늪에 있는 카타르와 대화해야 합니다."}},
[1423] = {"잃어버린 보급품", {"이 맥주통은 작지만 튼튼합니다. 나무가 이어진 부분은 자극적인 냄새가 나는 노란색 왁스로 봉해져 있습니다. 그리고 맥주통 밑바닥에는 다음과 같은 글귀가 찍혀 있습니다.","병참장교 룬겔츠","네더가드 요새"}, {"병참장교 룬겔츠에게 잃어버린 보급품을 가져가야 합니다."}},
[1424] = {"눈물의 연못", {"대족장님께서 아탈학카르 신전에서 일어나고 있는 이상한 일들에 대해 조사하라고 우릴 보내셨소.","아탈라이 트롤은 금지된 마법을 행하는 것으로 알려진, 사악한 마법사들로 이루어진 부족이지. 우리가 이곳에 도착해서 신전이 가라앉았다는 사실을 알고 얼마나 놀랐을지 생각해 보시오.","지금은 녹색 용혈족들이 아제로스의 수호자를 자청하며 그곳을 지키고 있소.","무슨 일이 일어난 건지 반드시 알아내야 하오. 눈물의 연못 주변을 조사하고 아탈라이 유물을 모으도록 하시오. 그러면 이 수수께끼의 조각들을 짜맞춰 보는 작업을 시작할 수 있을 것이오이다."}, {"스토나드에 있는 펠제룰이 아탈라이 유물 5개를 모아 달라고 했습니다."}},
[1425] = {"보급품 전달", {"내가 마차를 수리하는 동안 당신에게 마지막으로 부탁할 일이 있소.","오우거의 공격에서 지켜 낸 매우 중요한 보급품을 네더가드 요새로 가져가 주시오. 네더가드 요새는 어둠의 문이 내려다보이는 저주받은 땅의 북쪽 언덕에 자리 잡고 있소.","네더가드로 가려면 동쪽으로 길을 따라가시오. 길이 갈라지는 곳이 나오면 남쪽을 향해 저주받은 땅으로 가다가 왼쪽으로 가면 되오. 조심해서 길을 잘 따라가야 하오."}, {"네더가드 요새에 있는 병참장교 룬겔츠에게 네더가드 뱃짐을 가져가야 합니다."}},
[1426] = {"바다의 위협", {"난 바다를 전혀 좋아하지 않는데다가 그 속에서 나타난 생물들 역시 좋아하지 않지. 바다는 강력하지만 냉담하고 잔인한 눈이 먼 여왕이라 할 수 있어. 내게 그럴 힘만 있다면 저 물고기 인간들을 수백씩 처치해 버렸을 텐데 말이야. 하지만 호드의 대의를 행하는 이의 손에 놈들이 처치되는 걸 보는 것 또한 기쁜 일이 아닐 수 없지.","우리가 당신에게 바라는 것도 바로 이 때문 아니겠나, <class>? 얼마나 많은 놈을 처치할 수 있을까? 놈들 중 가장 강력한 종에 맞서 자신을 한번 시험해 보겠나?"}, {"늪지 멀록 10마리, 늪지 먹물쟁이 10마리, 늪지 청소부 10마리를 처치한 다음 슬픔의 늪에 있는 카타르에게 가야 합니다."}},
[1427] = {"바다의 위협", {"쉬기 전에 톡카르와 한 번 더 얘기해 보라고. 당신이 없는 동안 무기를 몇 개는 만든 것 같더군. 아마 여기서 도와준 것에 대한 보답으로 하나 줄 거야. 언제든 준비가 되면 받은 무기의 힘을 저 물고기 인간들에게 한번 시험해 보면 좋을 거요. 단 나를 위해서 말이지..."}, {"슬픔의 늪에 있는 톡카르와 대화해야 합니다."}},
[1428] = {"계속되는 위협", {"물고기 인간에 대한 당신의 증오도 나만큼이나 커지고 있다니 기쁘군.","해변에서 놈들을 더 찾아내 원하는 만큼 충분히 전투를 치른 다음 내게 돌아오면 당신에게 줄 보상에 대해 얘기하도록 하지."}, {"늪지 먹물쟁이 10마리, 늪지 청소부 10마리, 늪지 점쟁이 10마리를 처치한 다음 슬픔의 늪에 있는 카타르에게 가야 합니다."}},
[1429] = {"추방된 아탈라이 트롤", {"당신이 눈물의 연못에서 위험에 맞서 용감하게 임무를 수행하는 동안, 우리 정찰병 중 한 명이 말린 고기에 피로 쓰인 두루마리를 가지고 돌아왔소. 아마도 아탈라이 사제에 대한 판결문인 것 같은데...","이 사제는 아탈라이 부족에게 있어 죽음보다 더한 형벌을 선고받았지. 그는 마른나무껍질 트롤과 함께 동부 내륙지 북쪽 먼 곳으로 추방당했소.","이 유물들을 싸 줄 테니 그 추방된 트롤을 찾아 유물의 진정한 용도를 알아내어 여행이 다 끝나면 내게 돌아오도록 하시오."}, {"동부 내륙지에 있는 추방된 아탈라이트롤에게 아탈라이 유물 자루를 가져가야 합니다."}},
[1430] = {"싱싱한 고기", {"내 고객들은 육즙이 많은 게살을 많이 찾는데 특히 괴물게의 살이 인기가 좋소이다.","해안으로 갈 일이 있거든 괴물게 집게발을 좀 구해 와서 다시 오그리마로 운송할 수 있도록 해 주시기 바라오. 보상은 충분히 하겠소.","해안은 이곳에서 동쪽에 있소. 괴물게들은 해변과 바다 아래를 종종걸음으로 돌아다니니 찾기 쉬울 것이오."}, {"슬픔의 늪에 있는 다르에게 괴물게 집게발 10개를 가져가야 합니다."}},
[1431] = {"동맹 관계", {"목소리를 낮추시오. 우리 주민과 관련된 중요한 얘기를 당신과 나누고 싶소. 아직 분명한 계획은 아니지만...","우리는 긍지를 지닌 종족이오. 그런데 인간과의 전쟁이 이제 위대한 스랄 님의 인내심까지 시험에 들게 하고 있소. 당신도 알다시피, 우리 대족장님께서는 현명하고 신중한 분이시오. 항상 자신의 백성들을 먼저 생각하고 계시오. 그분은 외교의 직무도 맡으셨기에 오랜 기간 인간을 대하면서 결국 그들의 방식을 알게 되셨지.","내 이야기에 관심이 있다면 이 도시의 서쪽 출구 근처에 있는 켈드란을 만나 보시오. 그곳에 그의 오두막집이 있소."}, {"오그리마에 있는 켈드란과 대화해야 합니다."}},
[1432] = {"동맹 관계", {"얼라이언스가 칼림도어로 세력을 점점 확장함에 따라 호드가 맺은 불타는 군단과의 조약이 불행한 결과를 낳을 수도 있소. 대족장님께서는 그런 일이 일어나도록 용납하지 않으실 것이오. 그리고 나는 그분의 은혜로 지휘관을 맡아 그들을 제거하는 일을 하게 되었지.","나는 대원들을 잊혀진 땅의 거대한 코도 무덤에서 북쪽으로 가면 있는 침묵의 초소에 배치해 두었소. 돌발톱 산맥의 잿더미 계곡에서 남쪽으로 가면 그곳에 다다르게 될 거요. 도착하면 타카타 스틸블레이드를 찾도록 하시오."}, {"잊혀진 땅에 있는 타카타 스틸블레이드와 대화해야 합니다."}},
[1433] = {"동맹 관계", {"불타는칼날단이 노리고 있는 것은 여러 가지지. 근본적으로 녀석들의 영혼은 악하고 타락했기 때문에 녀석들을 막기 위해서는 파멸시키는 수밖에 없소.","켈드란이 마우린을 나와 함께 이곳으로 파견했소. 마우린은 불타는칼날단과 그들의 흑마법사에 대해 잘 알고 있기 때문이요.","나는 잊혀진 땅에 있는 악의 위협들을 처리하는 동안, 그는 불타는칼날단에 대한 우리 계획을 조정하는 일을 맡아 왔소.","그를 만나 얘기해 보시오. 아마 상세한 계획을 알려 줄 것이오."}, {"잊혀진 땅에 있는 마우린 본스플리터와 대화하십시오."}},
[1434] = {"사티로스의 출몰", {"악마가 만연하는 것은 반드시 무언가 다른 세력이 배후에 있다는 뜻이오. 세상에 악이 움틀 수 있도록 돕는 어떤 존재가 있다는 것이지.","나약해진 영혼, 또는 힘에 대한 부질없는 욕망으로 시작되기도 하오. 어쩌면 그것은 복수에 대한 갈망이거나 궁극적인 정의에 대한 호소일 수도 있소. 우리에게 고통이 닥쳐오면 사티로스 같은 악마의 세력이 우리를 이용하게 될지도 모르지.","사티로스들이 불타는 칼날단 무리와 손을 잡은 이후 스랄께서는 이들의 위협을 걱정하고 계시오. 여기서 북동쪽으로 가 그들을 찾아서 제거한 후에 다시 내게 돌아오시오."}, {"서릿발분노의 암살자 7명, 서릿발분노의 악마추종자 7명, 서릿발분노의 배반자 7명, 그리고 서릿발분노의 지옥소환사 7명을 처치한 후 잊혀진 땅에 있는 타카타 스틸블레이드에게 가야 합니다."}},
[1435] = {"불타는 영혼", {"사사로운 욕심을 위해 자신의 군주를 배반한 천한 짐승들은 죽어 마땅하지. 하지만 스스로 어둠 속에 내던진 자들에게는 죽음만으로는 부족하지.","자, 이 보석을 받으시오. 불타는 칼날단 중 하나가 거의 죽음에 이르렀을 때 그 보석의 마법을 사용하도록 하시오. 그러면 그들의 몸에서 타락한 영혼이 나와 보석 안으로 빨려 들어가게 될 것이오.","영혼이 깃든 불타는 보석을 내게 가져오면 알아서 처리하도록 하지.","이제 가 보도록 하시오. 죽지 않도록 조심하고..."}, {"잊혀진 땅에 있는 마우린 본스플리터에게 빌린 불타는 보석 하나와 영혼이 깃든 불타는 보석 15개를 가져가야 합니다."}},
[1436] = {"동맹 관계", {"전투가 순조롭게 진행되어 가고 있소. 계속해서 악마와 그 졸개들을 상대로 한 전투에서 혁혁한 성과를 거둔다면 계절이 바뀌기 전에 대족장님께서 우리를 고향으로 돌려보내 주실지도 모르겠소.","켈드란님께 돌아가서 우리가 한 일을 보고해 주시오. 틀림없이 당신이 이곳에서 수고한 것에 대해 보상을 해 주실 거요. 대족장님께서 자기를 알아 주신다는 것을 알면 아주 자랑스러워할 거요. 그만큼 모든 일에 동기를 중요하게 여기는 자니까..."}, {"오그리마에 있는 켈드란과 대화해야 합니다."}},
[1437] = {"발라리엘의 수색", {"만나서 반갑소. 난 발라리엘이라고 하오. 난 나이트 엘프 상인 가문인 말렘가의 실종된 두 아이를 찾으러 여기 왔소이다. 아이들 아버지는 최악의 상황이 벌어진 게 아닌가 싶어 걱정이 이만저만이 아니오. 난 모든 수단을 동원해서라도 실종된 아이들을 찾아야 하오. 티라니스나 달린다를 찾는 일을 아직 시작하지 않았지만 다행히 여기 오래 있지는 않을 것 같소.","몇 주 전 아이들이 돌발톱 산맥을 지나갔다는 것이 마지막으로 들은 소식이오.","그대가 아이들의 마차를 찾으면 즉시 내게 돌아와 주시기 바라오."}, {"잊혀진 땅에서 티라니스 말렘과 달린다 말렘의 마차를 찾아 조사해야 합니다."}},
[1438] = {"발라리엘의 수색", {"가능하다면 달린다와 티라니스 둘 다 찾아서 내게 돌아오시오. 이 성채부터 수색해 보도록 하시오. 나는 다르나서스에 있는 상관들께 연락해 상황을 알리겠소.","아, 얘기할 게 더 있소이다. 내 상관께서 티라니스가 이곳에 오게 된 동기가 무엇인지 걱정하고 계시오. 달린다를 찾아 안전하게 데려오는 것이 더 중요하오. 티라니스는 집에 데리고 가기 전에... 잘 판단해야 하오."}, {"잊혀진 땅에서 달린다 말렘을 찾아야 합니다."}},
[1439] = {"티라니스 수색", {"오크들이 경비병이랑 하인들을 모두 죽이고 저희를 여기로 끌고 왔어요. 오, 얼마나 끔찍한 짓을 하던지! 우리도 그 자리에서 죽겠구나 싶었는데 다른 속셈이 있는 것 같았어요.","티라니스 오빠는 오크들과 대화를 하려고 했어요. 왜 그랬는지는 잘 모르겠지만... 자기가 신비술에 대해 얼마나 아는지 보여주려고 했던 거 같아요.","부탁이에요. 오빠가 어떻게 됐는지 알지 못하면 여기를 떠날 수가 없어요. 오빠를 찾아서 무슨 일이 있었는지 알아보고 다시 절 데리러 와주세요."}, {"잊혀진 땅에서 티라니스 말렘을 찾아야 합니다."}},
[1440] = {"발라리엘에게 돌아가기", {"그럼 이제 떠날까요? 수고해 주셔서 정말로 감사해요. 후에 상황이 여의치 않을 때를 대비해서 미리 저와 제 가족에게 해주신 일에 감사드릴게요. 준비가 되셨다면 더 이상 여기에 있을 이유는 없으니 때가 되면 떠나도록 하죠."}, {"천둥도끼 요새에서 달린다 말렘을 호위해 잊혀진 땅에 있는 발라리엘 데몬슬레이어에게 가야 합니다."}},
[1442] = {"코르석을 찾아서", {"안녕하시오, <name>. 거룩한 길을 걷는 그대에게 어떻게 도움을 주면 되겠소? 아, 그렇군. 스틸웰... 오래전부터 알고 지내던 그 대장장이 말이로군. 그 친구의 이름을 들어본 지도 꽤 오래됐지만 아직 기억하고 있소.","절대로 그 사람에게서 경솔하다는 인상을 받은 적이 없었으니, 이번에 그가 코르석을 얻으려는 것도 분명 숭고한 이유 때문일 거라고 생각되는군.","진정으로 그 보석을 원한다면 잿빛 골짜기에서 북서쪽으로 가야 하오. 거기서 어느 고대 폐허 안쪽에 검은심연 나락이 있을 거요. 그 아래 살고 있는 나가들이 아직도 그 보석을 사용하고 있다고 하오. 그곳에 절대로 혼자서는 가지 마시길..."}, {"어둠해안에 있는 툰드리스 윈드위버에게 코르석을 하나 가져가야 합니다."}},
[1444] = {"펠제룰에게 돌아가기", {"예언자 잠말란은 내게 아탈학카르 신전에서 추방한다고 선고했다. 한때 그 대사제를 훌륭한 지도자라고 생각한 적도 있었지.","잠말란 자신의 예언에 대한 단호한 믿음은 아탈라이부족에게 파멸을 가져오게 할 것이다. 아직 아탈라이부족이 파멸을 맞지 않았다면 말이지.","내가 이러한 염려를 표명하자, 나를 이단자로 몰아 신전에서 내쫓아 버리더군.","당신이 내게 가져온 유물들은 해롭지 않은 것이지만 잠말란이 소환 의식을 위해 완벽한 숭배 마법을 발견했다면 세상은 큰 위험에 놓인 것이야..."}, {"스토나드에 있는 펠제룰에게 돌아가야 합니다."}},
[1445] = {"아탈학카르 신전", {"잠말란의 예언에 대해 얘기했다고?","아탈라이 부족의 영적 지도자는 학카르가 황천에서 아제로스로 다시 돌아오면 아탈라이 부족에게 불멸의 생을 선사해 줄 것이라 믿고 있는 모양이군. 내가 보기에는 멸망의 지름길일 뿐이야.","그런데 그 유배자가 마법의 우상에 대해 얘기했다고? 흠, 매우 걱정스럽군. 이것이 정말로 이 난장판을 만든 아탈라이 의식에 대한 해답이라면 내재한 힘을 알아야만 하오.","신전으로 가서 그 우상들을 가져오시오!"}, {"스토나드에 있는 펠제룰에게 학카르의 우상 20개를 가져가야 합니다."}},
[1446] = {"예언자 잠말란", {"우리 마른나무껍질 부족은 자기 집에 이방인이 들락거리는 걸 싫어한다.","나? 난 이제 고향이 없어. 난 우리 부족인 슬픔의 늪의 위대한 아탈라이 부족으로부터 추방당했다.","우리 부족의 영적 지도자 잠말란이 자칭 예언이란 걸 받았다고 주장했는데, 학카르를 소환해 아탈라이 부족을 불사의 존재로 만들 수 있다고 하더군.","하지만 나는 부족 주민들에게 경고했다. 혹시 그 예언이 조작된 것이라면...","내가 왜 추방당해야 했지? 난 복수를 하고 싶다. 잠말란을 처치하고 그의 머리카락을 가져와. 그렇게만 하면 우리 부족은 그의 압제에서 해방될 거야."}, {"동부 내륙지에 있는 추방된 아탈라이트롤이 잠말란의 머리카락을 가져다 달라고 했습니다."}},
[1447] = {"실종된 사신", {"하! 스톰윈드에 올 정도로 어리석은 <class>|1이;가; 틀림없는데다가 이제는 절대 여기서 살아나가지 못한 바보 중의 바보가 되겠군.","우리 애들은 아직 못 만난 모양이지?"}, {"스톰윈드에 있는 다셸 스톤피스트와 싸워 이겨야 합니다."}},
[1448] = {"신전을 찾아서", {"나도 자네처럼 젊고 혈기 왕성했던 시절이 있었지, <class> 친구. 하지만 지금은 나이가 들어 예전처럼 자유롭게 세상을 탐험할 수는 없다네. 아, 정말 많은 것들을 봤었는데 말이야!","그런데 아직도 해결하지 못한 일이 하나 있네. 탐험가 연맹에서는 마지막 임무로 내게 아탈학카르 신전을 찾을 것을 지시했었지. 소문에 의하면 그 신전은 슬픔의 늪에 있다고 전해지고 있네.","내 몸이 허약해졌기에 그리핀을 타고 하늘을 날며 신전을 찾아 다녀봤지만 결국 찾지 못했네.","이 늙은 드워프를 좀 도와주겠나? 직접 걸어다니면서 찾아보면 찾을 수 있을지도 모르니까 말이야."}, {"스톰윈드에 있는 브로한 캐스크벨리가 아탈학카르 신전을 찾아 달라고 했습니다."}},
[1449] = {"동부 내륙지로", {"전에 말했듯이, 나는 직접 하늘에서 그 신전을 찾아보려고 했었네. 흠, 정확히 말해 혼자는 아니었지만...","그때 나는 동부 내륙지의 맹금의 봉우리에 있는 그 유명한 그리핀 출격장까지 갔다네. 거기서 그들 중 가장 뛰어난 조종사 중 한 명의 도움을 얻을 수 있었지. 그 조종사는 자신이 운전하던 그리핀에서 떨어져 늪으로 추락하고 말았지...","난 이제 늙어서 그 불쌍한 친구의 이름조차 기억이 나지 않아. 틀림없이 그 출격장의 조련사가 기억하고 있을 거야.","그곳으로 가서 폴스타트 와일드해머를 찾도록 하게. 그 조종사가 어떻게 됐는지 알아내야 해."}, {"동부 내륙지 맹금의 봉우리에 있는 고위 영주 폴스타트 와일드해머와 대화해야 합니다."}},
[1450] = {"그리핀 조련사 탈론액스", {"그래, 당신은 탐험가 연맹의 작자가 보낸 잘난 심부름꾼이란 말이지?","흠, 내 말 잘 들으시오. 난 당신의 늙은 친구가 아제로스를 누구랑 같이 비행했었는지 모르오. 당신 눈엔 내가 그리핀 조련사 탈론액스로 보이오? 그런 거요?","비행 임무에 그렇게 관심이 많다면 그리핀 조련사에게 물어보지 그러시오? 계속 이 주변에서 얼쩡거리고 있는 게 내 눈에 띄면 정말 힘든 일을 시킬 거요.","어서 가 보시오."}, {"맹금의 봉우리 출격장 안에 있는 그리핀 조련사 탈론액스와 대화해야 합니다."}},
[1451] = {"랩소디 신디거", {"랩소디는 술을 아주 좋아했습니다. 정확히 말해, 이곳 출격장의 우리 드워프들은 누구나 시간만 있으면 에일 맥주 마시길 좋아하는 걸 당신도 알고 있을 겁니다.","하지만 신디거, 그 친구는 그리핀이 하늘을 나는 횟수만큼이나 자주 술을 마셨죠. 정말 부끄러운 일이었지요. 그러나 누구도 랩소디처럼 비행하지는 못할 겁니다.","그가 당신 친구 브로한과 함께 수행하던 임무에서 비틀거리며 돌아오던 날, 처음으로 문제의 조짐이 나타났었습니다. 그 이후로 상황은 빠르게 악화되었지요. 그래서 그를 보낼 수밖에 없었습니다.","마지막으로 들은 바로는, 이곳 동쪽에 양조장을 차렸다고 하던데..."}, {"동부 내륙지에서 랩소디 신디거를 찾아야 합니다."}},
[1452] = {"랩소디의 칼림도어 칵테일", {"딸꾹!","그래, 그 정신 나간 브로한이 당신을 보냈단 말이지? 그럼, 우린 정말 끝내주는 모험을 했었지... 딸꾹!","늪에서 무슨 일이 있었는지 듣고 싶다면 독한 술이 좀 필요한데... 그게 그러니까, 맨정신으로 얘기할 만큼 좋은 기억이 아니거든... 딸꾹!","자, 이렇게 하자고... 내가 칼림도어 칵테일을 만들 수 있도록 재료를 구해 오면 그 얘길 전부 다 해주지.","타나리스의 로크 모래주머니... 그리고, 보자... 페랄라스에 있는 그로독 고릴라 간과 무쇠곰 간이 좀 필요하다고...","제대로 된 칼림도어 칵테일 한 잔이면... 다 얘기해 준다니까!"}, {"동부 내륙지에 있는 랩소디 신디거가 로크 모래주머니 3개, 그로독 고릴라 간 3개, 무쇠곰 간 3개를 가져다 달라고 했습니다."}},
[1453] = {"잊혀진 땅에서의 회수 임무", {"잊혀진 땅에서 할 일이 좀 있다네, <class> 친구. 그곳은 켄타우로스와 다른 나쁜 놈들이 판을 치는 끔찍한 곳이지. 좀 어려운 상황이긴 하지만 자네가 한 일주일쯤 걸릴만한 괜찮은 일거리를 찾고 있다면 크렐디그 운고르를 만나 내가 보냈다고 전하게.","그는 잊혀진 땅 북쪽 산속에 숨겨진 작고 아늑한 야영지에 있다네. 나이젤의 야영지라고 하지. 돌발톱 산맥을 통해 잊혀진 땅으로 들어가서 똑바로 동쪽으로 가면 된다네. 가다가 지나치기 쉬우니 주위를 잘 살피는 게 좋을 거야."}, {"잊혀진 땅에 있는 크렐디그 운고르와 대화해야 합니다."}},
[1454] = {"카니톨 난파선", {"안녕하십니까, 친구여. 여기 앉으십시오...","제가 첫 번째로 처리해야 할 일은 스톰윈드의 귀족 중의 한 명인 마법사 카니톨이 의뢰한 일입니다. 그분의 배가 잊혀진 땅의 북서쪽 바다에 출항 중인 동안 바다 거인의 공격을 받았나 보더군요. 그분과 그의 부하들이 귀중한 궤짝 중 하나를 해변으로 가져오려 했지만 바다 거인이 떼로 공격하는 바람에 궤짝을 포기하고 안전지대로 공간 이동을 할 수밖에 없었다고 합니다.","그분은 그 상자와 내용물을 찾아 스톰윈드로 가져다주길 원하고 있습니다."}, {"잊혀진 땅에서 카니톨의 궤짝을 찾아야 합니다."}},
[1455] = {"카니톨 난파선", {"누가 상자를 열었는지 알아내기 위해 잠시 살피다가 궤짝의 양옆과 위에 길고 깊게 긁힌 자국이 있음을 발견합니다. 이것으로 보아 사람이나 거인이 아닌 다른 누군가가 상자에 관심을 가졌던 것 같습니다. 자국은 검이나 단검에 의해 생긴 자국으로 보이지 않습니다.","궤짝을 자세히 살펴본 후 상자가 완전히 비어 있다는 사실을 알게 됩니다.","더 이상 살펴보지 않고 궤짝을 닫은 후에 떠날 채비를 합니다. 크렐디그가 실망할 것 같습니다."}, {"잊혀진 땅에 있는 크렐디그 운고르에게 가야 합니다."}},
[1456] = {"카니톨 난파선", {"흥미롭군. 흠, 아직 희망이 완전히 사라진 건 아닌 것 같군.","그 쪽 해안을 따라 북쪽으로 라나즈자르 섬 근처로 가면 바다 밑바닥에 나이트 엘프 폐허가 있네. 지금은 나가인 뱀갈퀴 일족이 그곳에 자신들의 터전을 만들었지. 아무래도 놈들의 짓일 가능성이 제일 큰 것 같군그래.","정찰대가 남쪽으로 그렇게 멀리 왔었다면 굉장히 강한 녀석들일 거야. 카니톨 님의 물건들을 찾을 생각이라면 나가들 중에서 특히 강한 바다사냥꾼이나 바다 마녀들부터 조사해 보는 게 좋을 걸세."}, {"카니톨의 가방을 찾아 잊혀진 땅에 있는 크렐디그 운고르에게 가져가야 합니다."}},
[1457] = {"카니톨 난파선", {"카니톨님의 물건들을 아이언포지에 있는 로이튼에게 가지고 돌아가면 대가를 지급해 줄 겁니다.","하하, 자칫하면 로이튼이 당신이 너무 맘에 든 나머지 당신더러 우리 회사에 눌러앉으라고 할지도 모르겠는걸요.","두말하면 잔소리겠지만, 정말 일을 잘 처리해 주었습니다.","돌아오는 길에 조심하도록 하고 너무 오래 지체하지 마세요. 카니톨님은 <race>들을 썩 좋아하지는 않지만 특별히 인색하지도 않으니까요."}, {"아이언포지에 있는 로이튼 스톤해머에게 카니톨의 가방을 가져가야 합니다."}},
[1458] = {"회수전문회사를 위한 재료", {"이곳에서의 내 임무 중 하나는 카라노스에 있는 노움 마법사인 레프트위치를 위해 재료를 모으는 일이네. 로이튼은 이미 우리가 이곳에 온 이상 재료를 구하는 건 식은 죽 먹기라고 생각하는 것 같더군. 그는 보다시피 일손이 부족하다는 사실은 별로 신경 쓰지 않는다니까.","그의 목록에서 처음 몇 가지는 구하기도 쉬울 것이고 나 대신 다른 이가 하더라도 아주 수월하게 일을 마칠 수 있을 걸세.","동쪽에 있는 서릿발분노의 사티로스 뿔과 발톱이 필요하니 좀 가져다주게나. 우선 그 일을 마친 후 나머지 재료를 찾도록 하지."}, {"잊혀진 땅에 있는 크렐디그 운고르에게 서릿발분노의 사티로스 발톱 10개와 서릿발분노의 사티로스 뿔 10개를 가져가야 합니다."}},
[1459] = {"회수전문회사를 위한 재료", {"레프트위치가 필요로 한 목록의 다음 항목은... 어디 보자... 흠. 아, 여기 있군.","간단한 항목이지만 그의 지시 사항이 까다롭네. 그는 스콜파시의 독액과 코도의 통가죽을 필요로 하는군. 그런데 둘 다 특정한 생물에서 취한 것이어야만 한다고 되어 있네. 독액은 아무 스콜파시 전갈에게서나, 통가죽은 죽어가는 코도나 고대 코도의 것이 아니라, 반드시 늙은 코도의 것을 가져와야 하네.","자네니까 하는 얘기지만, 마법사들이 찾는 것들 중에는 별 희한한 게 다 있단 말이지, 쩝..."}, {"잊혀진 땅에 있는 크렐디그 운고르에게 스콜파시 독 7병과 늙은 코도 통가죽 3개를 가져가야 합니다."}},
[1462] = {"대지의 정기", nil, nil},
[1463] = {"대지의 정기", nil, nil},
[1464] = {"불의 정기", nil, nil},
[1465] = {"발라리엘의 수색", {"계속해서 마차를 수색하다가 남쪽으로 향하는 발자국과 팬던트를 발견했습니다. 멀리 큰 성채에서 연기가 피어 오르는 것이 보입니다. 건물이 타면서 나는 것이 아니라 누군가 일부러 피워놓은 불에서 나는 연기로 보입니다. 본능적으로 말렘가의 아이들이 아직 살아있으며 연기가 나는 방향으로 잡혀갔다는 느낌이 듭니다.","나이젤의 야영지에 있는 발라리엘에게 발견한 펜던트와 남쪽으로 향하는 생명의 흔적에 대해서 알려야 할 거 같습니다."}, {"잊혀진 땅에 있는 발라리엘 데몬슬레이어에게 말렘 펜던트를 가져가야 합니다."}},
[1466] = {"회수전문회사를 위한 재료", {"이제 마지막 항목이군. 그리고 솔직히 말해서 이번엔 쉬운 게 아니라네. 아마 다른 것들보다 시간도 더 걸릴 거야.","자, 경고는 충분히 했으니 이제 시작해 보지. 레프트위치가 필요하다고 한 것들은 다음과 같네. 마나먹는 지옥사냥개나 마법사 사냥개, 또는 평원의 지옥사냥개의 뇌 10개, 황천의 요녀나 마녀, 여마법사에게서 황천의 날개 10개, 그리고... 파멸의경비대 부대장이나 우두머리의 혈액 10병이로군.","간단하지 않나? 뭐, 자네가 이번 건 그만두겠다고 해도 내 이해하도록 하지."}, {"잊혀진 땅에 있는 크렐디그 운고르에게 지옥사냥개 뇌 10개, 황천의 날개 10개, 파멸의경비대 혈액 10병을 가져가야 합니다."}},
[1467] = {"회수전문회사를 위한 재료", {"보수를 받으려면 아이언포지로 가서 로이튼에게 직접 말해 보세요. 어쨌든 그는 레프트위치의 소포를 빨리 처리하고 싶어할 테니까요. 이걸 그에게 건네 주기만 하세요. 그럼 반드시 당신 보수를 줄 겁니다.","그리고 내가 돌아가면 당신에 대해 잘 얘기해 둘 테니 걱정 마세요. 난 잊혀진 땅에 그리 오래 머무르진 않을 겁니다."}, {"아이언포지에 있는 로이튼 스톤해머에게 레프트위치의 소포를 가져가야 합니다."}},
[1468] = {"어린이 주간", {"여기 보육원 일을 돕고 싶으시다고요? 하늘의 축복이 있으시길. 어린이 주간 동안 도와주신다면 정말 너무 감사할 거예요.","이 호루라기를 받으세요. 이 호루라기는 어린이 주간에만 사용할 수 있어요. 호루라기를 한 번 불면 당신이 돌봐주기로 한 아이와 얘기를 나눌 수 있고 다시 한 번 더 불면 아이를 보낼 수 있죠.","부디 이 호루라기를 사용해 당신이 돌볼 아이와 만나보세요! 누구보다 당신을 만나고 싶어할 거예요."}, {"인간 고아 호루라기를 사용해 어린이 주간 동안 돌보게 될 아이와 대화해야 합니다."}},
[1469] = {"랩소디의 이야기", {"비행하기 전에 난 스타우트 맥주 한 통을 전부 다 마셨었지. 딸꾹!","정말 대단한 비행이었어. 힘차게 날았지. 브로한은 내 뒤에서 따라오고 있었고... 그런데 신전이라곤 어디에도 보이지도 않는 거야. 딸꾹!","흠, 너무 심하게 취해서 조종석에서 떨어지기 전까지는 신전 코빼기도 못 봤단 말이지. 난 곧장 물속으로 떨어져 가라앉았어. 그리고 섬뜩한 트롤 무리가 날 둘러싸고 있다는 걸 알았지. 물론 재빨리 헤엄쳐 나왔다고! 게다가 이 트롤 기념품까지 가지고 나왔다니까. 자, 이걸 브로한에게 가져다주고 그 깊은 곳에 정말 신전이 있다고 알려 주라고. 딸꾹!"}, {"스톰윈드에 있는 브로한 캐스크벨리에게 아탈라이 서판 조각을 가져가야 합니다."}},
[1470] = {"저 너머의 세계", {"어서 오세요. 흑마법사치고는 아직 미숙해 보이는군요. 아직 흑마법의 힘을 자기 것으로 만들지 못했으니 당연한 겁니다. 배우고자 하는 열의가 대단하군요. 이제 곧 흑마법의 힘을 제대로 배우게 될 겁니다.","무지한 자는 힘을 가질 수 없으며 지식이야말로 우리의 가장 큰 힘입니다. 지식을 사용해 혼란스러운 마법과 허공에 떠도는 존재들을 제어하고 우리 의지대로 조종할 수 있게 되는 것이지요.","자, 그럼 이제 종복을 자신의 의지대로 조종하는 방법을 보여 주겠어요. 하지만 먼저 버려진 대장간 근처에서 덜그럭거리는 해골 세 개를 내게 가져와야 합니다."}, {"벤야 말선드에게 덜그럭거리는 해골 3개를 가져가야 합니다."}},
[1471] = {"결속", {"우리 뒤에 있는 소환의 마법진은 이미 보았겠지? 그곳에서만 구속되지 않은 보이드워커를 끌어낼 수 있네.","이 룬을 사용하여 보이드워커를 끌어낸 다음 자네가 지닌 무기와 마법의 힘으로 제압해야 하네.","그런 후에 자네의 명령을 따르게 되면 내게 다시 오게. 자네가 원하는대로 보이드워커를 부릴 수 있도록 해주는 소환 주문을 알려 주도록 하지."}, {"소환의 룬을 사용하여 보이드워커를 소환하여 제압한 다음 언더시티의 마법 지구에 있는 카렌딘 할가르에게 소환의 룬을 가지고 돌아가야 합니다."}},
[1472] = {"영혼에 굶주린 자", {"서큐버스는 영혼을 사로잡고, 정신을 파괴하며, 남자를 유혹하는 존재지. 깊은 악으로 가득 차 있고 기이한 정신을 소유한 피조물이야.","그들을 우리 세계로 데려오려면 미끼가 필요해. 그들을 상대하는데 그들 자신의 힘을 역이용하는 거지. 서큐버스에게 선량하고 순수하며 사랑에 빠진 남자의 심장을 이용하면 우리 세계로 넘어오지 않고는 못 배길 거야. 그런 다음 자네에게 복종시키면 부릴 수가 있지.","사원 안에 있는 내 조수 고드릭 파선과 얘기해 보게. 그가 미끼 찾는 걸 도와줄 거야."}, {"저주의 사원에 있는 고드릭 파선과 대화해야 합니다."}},
[1473] = {"공허의 피조물", {"여러 면으로 보아 이제 자네도 공허방랑자 소환을 배울 때가 된 것 같네.","공허방랑자는 저 너머의 혼돈에서 만들어진 존재지. 자네가 공허방랑자를 소환하고 나서 제어하지 못한다면 자네는 갈가리 찢기고 말 거야.","최초로 공허방랑자를 소환한 건 에갈린 님이셨는데, 그분의 흑마법서에 소환 과정에 대해서 기술되어 있다네.","안타깝게도, 그 흑마법서는 한 붉은십자군 녀석에게 도둑맞았는데, 놈은 그걸 서쪽 탑에 있는 그의 기지에 숨겨 놓았어. 그걸 내게 가져오면 자네에게 그 방법을 가르쳐 주지."}, {"에갈린의 흑마법서를 되찾아 저주의 사원에 있는 카렌딘 할가르에게 가져가야 합니다."}},
[1474] = {"결속", {"이 심장들을 가지고 소환 마법진으로 가서 서큐버스가 나올 수 있는 입구를 열도록 하게. 이제 자네의 힘은 예전보다 강하니 그 힘을 모두 발휘해서 서큐버스를 이겨 내야 해.","아무리 정신력이 강한 이들도 서큐버스에게 지배되어 버리는 경우가 많았으니 서큐버스의 매력에 현혹되지 않도록 조심하도록 하게. 서큐버스의 유혹을 견뎌내면 허공으로 돌아가게 될 거야. 그러면 자네의 명령에 따르도록 하는 소환 주문을 가르쳐 주겠네."}, {"순수한 심장을 사용하여 서큐버스를 소환하여 제압한 다음, 언더시티에 있는 마법 지구에 있는 카렌딘 할가르에게 순수한 심장을 가져가야 합니다."}},
[1475] = {"아탈학카르 신전으로", {"전설에 의하면 아탈학카르 신전은 고대 신, 학카르를 모시는 신성한 제단이라고 하네. 소문에 듣기로 이 신전은 아탈라이라는 사나운 트롤 부족이 지키는 난공불락의 요새라고 하더군.","이제는 슬픔의 늪 아래로 가라앉아 버렸으니 신전에서 유물을 되찾는 일은 더욱 힘들어졌어. 그러나, 바로 이것이 자네가 해줘야 할 일이야.","나를 도와 손상되지 않은 아탈라이 서판을 가져와 주게. 그걸 가져오면 나는 영웅이 되어 탐험가 연맹으로 돌아갈 수 있을 거라고. 나와 자네가 함께 그 영광을 누릴 수 있을 걸세."}, {"스톰윈드에 있는 브로한 캐스크벨리에게 아탈라이 서판 10개를 가져가야 합니다."}},
[1476] = {"선한 자의 유품", {"명예와 선량한 마음을 모두 가진 자? 과연 그런 자가 존재할까요? 여러 방면에서 알아 봤는데 우연히 듣게 된 얘기에서 그럴 만한 남자를 두 명 찾아냈어요.","첫 번째는 자신들의 일가로부터 버림받은 로데론 난민들을 헌신적으로 보살피고 있는 달린 포지라이트라는 자로 그레이메인 성벽에 있어요.","두 번째는 사라진 아내의 흔적을 찾아 저습지를 헤매며 잃어버린 아내에 대한 사랑을 여전히 간직하고 있는 코마 빌라드라는 자죠.","그들의 유품을 카렌딘님께 가져가세요. 하지만 먼저 이 목표물들을 보여 드리지요."}, {"저주의 사원에 있는 카렌딘 할가르에게 달린의 유품과 코마의 유품을 가져가야 합니다."}},
[1477] = {"중요한 보급품", {"내 밑의 마법사들을 저주받은 땅으로 보냈네. 거기서 네더가드 요새를 탐색하여 어둠의 문에 대한 비밀을 밝히는 데 반드시 필요한 정보를 찾으라고 지시했지. 그들에게 보급품을 지속적으로 보내줘야 하는데... 요즘은 네더가드 요새로 가는 이를 구하기가 하늘의 별 따기란 말이지.","흠, 자네라면 네더가드 요새로 보급품을 전달해줄 수도 있을 듯하네만. 어둠골의 순찰대장 소리갈을 찾아서 보고하게. 아마 그가 직접 자네에게 지시를 내릴 거야. <name>, 그곳에는 적들이 득실거리고 있으니 정신 바짝 차려야 하네."}, {"그늘숲의 어둠골에 있는 순찰대장 소리갈과 대화해야 합니다."}},
[1478] = {"할가르의 부름", {"아! 방금 카렌딘 할가르님께서 앞으로의 훈련을 위해 당신을 만나고 싶다는 소식이 왔습니다.","할가르님께서 개인적으로 직접 당신과 같은 젊은 흑마법사를 훈련하시다니 매우 자랑스럽겠군요.","언더시티의 저주의 사원 근처에 있는 마법 지구에 가면 그분을 만날 수 있을 겁니다."}, {"언더시티에 있는 카렌딘 할가르와 대화해야 합니다."}},
[1479] = {"영원의 줄기", {"나이트 엘프들은 곰처럼 생긴 나무 안에 은행을 두고 있다는 얘기를 들은 적이 있어요! 너무 근사하지 않아요? 나이트 엘프들이 자연을 좋아하는 것도 멋진데, 곰 모양이라니 정말이지 너무 멋져요!","그 곰나무를 볼 수 있게 절 다르나서스에 데려다 주지 않으실래요? 그 근처로 가는 배도 있으니 그리 오래 걸리지는 않을 거예요. 제발, 제발, 제발요!"}, {"랜디스를 다르나서스 은행에 데려가야 합니다. 은행 자체는 영원의 줄기라고 알려진 나무 안에 있습니다."}},
[1480] = {"타락자", {"부드러운 가죽은 찢겨져 있고 썩은 냄새가 진동하고 있습니다.","가죽을 펼치자 한쪽 면에 어떤 언어로 쓰인 붉은 색 표시가 선명히 보입니다. 맨 아래쪽에는 일종의 서명으로 추측되는 기호가 선명히 새겨져 있습니다.","어쩌면 마우린이 이 가죽에 대해 좀 더 자세히 알려줄 수 있을지도 모릅니다."}, {"잊혀진 땅에 있는 마우린 본스플리터에게 악마의 가죽을 가져다주십시오."}},
[1481] = {"타락자", {"적합한 재료만 구할 수 있다면 이 악마 군주를 찾아낼 수도 있을 것 같소.","그 악마가 내 생각만큼 강력하다면 사납고 무서운 짐승들로부터 여러 가지 재료를 구해야지. 당신은 대족장님의 이름을 걸고 활동하는 만큼 기꺼이 이 임무를 맡으리라 믿소.","우선 군주가 사용하는 방벽을 모두 파괴해 수정점을 피해야 하오. 이를 위해서는 사티로스의 가죽이 필요하지. 하지만 아무 사티로스의 가죽이나 사용할 수 있는 건 아니오.","서릿발분노의 그림자추적자에게서만 구할 수 있지. 잊혀진 땅 북동쪽에 있는 고대의 유적지로 가면 찾을 수 있을 거요."}, {"잊혀진 땅에 있는 마우린 본스플리터에게 그림자추적자 머릿가죽을 가져가야 합니다."}},
[1482] = {"타락자", {"예언의 수정구만 있다면 좋을 텐데... 예언의 수정구는 나가들이 계시를 받기 위해 만드는 것이오.","해안을 따라 북서쪽으로 가면 해안 근처에 나가들이 살고 있는 거대한 산호초가 있소. 거기서 수정구를 구할 수 있을 거요. 뱀갈퀴 나가족들은 매우 위험하고 당신이 침입하면 그냥 보고만 있지는 않을 테니 조심해서 일을 처리한 후 신속히 돌아오도록 하시오.","당신이 돌아오면 제왕 아즈레토크를 찾을 수 있을 거요."}, {"잊혀진 땅에 있는 마우린 본스플리터에게 예언의 수정구를 가져가야 합니다."}},
[1483] = {nil, {"투자개발회사에서 최고의 고블린들을 포섭해 다수 빼내 갔지만 우린 그들을 되찾아 올 수 있는 기회를 노리고 있지.","얼마 전 돌발톱 산맥에 있는 투자개발회사 지부에서 게렌조 렌치휘슬이라는 노움을 새로 고용했다네. 이에 위기의식을 느낀 다른 기술자 지즈 피직스가 내게 땜장이 조합을 위해 일하게 해 달라고 간청하지 않았겠나? 그래서 그에게 몇 가지 임무를 맡겼는데, 아마 도움이 필요할 거야.","자네가 날 위해 그를 찾아서 좀 도와주겠나? 그는 칼바람 바위산의 서쪽 경계에 배치되어 있다네."}, {"돌발톱 산맥의 칼바람 바위산에 있는 지즈 피직스와 대화해야 합니다."}},
[1484] = {"타락자", {"이 악마는 내가 생각했던 것보다 훨씬 더 위험하오. 방금 일어났던 일에 대한 소식을 타카타에게 알리지 않으면, 분명 그의 자존심이 다칠 것이오. 듣고 나면 그런 악마를 다룰 능력이 내게 있는지 의심하겠지만...","타카타를 만나서, 만약 그가 허락한다면 당신이 그 악마를 처치하고 싶다고 말하시오. 하지만 너무 무리하게 주장하지는 마시오. 그의 기를 살려 주려면 그가 생각해 낸 계획에 따르는 것처럼 하는 게 더 낫지..."}, {"잊혀진 땅에 있는 타카타 스틸블레이드와 대화하십시오."}},
[1485] = {"악마의 하수인", {"칼림도어의 흑마법사들은 언제나 호드의 의심을 사 왔네. 호드는 흑마법사들을 묵인하고 있지만 결코 우호적이라고는 할 수 없지.","그렇다고 해서 흑마법사들끼리 뭉치게 된 것은 아니라네. 많은 흑마법사들이 홀로 마법을 연구하며 숨어 지내기 때문이지. 자네가 가야 할 길을 일일이 안내해 주지는 못하겠지만 자네를 훈련시켜 줄 수는 있네.","북동쪽 동굴에서 악마의 하수인 머리 6개를 가지고 오게. 그 후에 소환 기술을 가르쳐 주도록 하지."}, {"루잔에게 악마의 하수인 머리 6개를 가져가야 합니다."}},
[1486] = {"돌연변이 통가죽", {"나랄렉스가 악몽에 깊이 빠질수록 불모의 땅 깊은 곳으로부터 솟아나온 온갖 이상한 짐승들이 통곡의 동굴에 출몰하고 있소.","이 기이한 짐승들은 알 수 없는 초자연적 특성을 지니고 있소. 사악한 것들이긴 하지만 내 생각엔 그들이 이곳 칼림도어에 존재하는 한 좋은 방향으로 이용할 수도 있을 것 같소. 그들의 가죽을 잘 가공하면 특별한 용도에 쓸 수 있을지도 모른단 거요.","이 일을 맡을 자신이 있다면 아래에 있는 동굴로 가서 돌연변이 통가죽을 좀 구해다 주시오."}, {"통곡의 동굴에 있는 날팍이 돌연변이 통가죽 20개를 가져다 달라고 했습니다."}},
[1487] = {"돌연변이 짐승 섬멸", {"우리의 위대한 지도자, 나랄렉스께서는 숭고한 목표를 가지고 계셨습니다.","에메랄드의 꿈속으로 들어가 이 황량한 땅을 예전의 풍요로운 숲으로 되살려 놓기를 갈망하셨지요. 하지만 뭔가 크게 잘못된 게 분명합니다.","나랄렉스께서 꿈꾸시던 것들은 악몽으로 바뀌었고 타락한 짐승들이 동굴에 출몰하기 시작했습니다.","나랄렉스의 신도 몇 분들이 지도자를 깨울 방법을 찾기 위해 애쓰는 동안, 나는 이 동굴에서 사악한 짐승들을 몰아내야 합니다.","동굴로 가서 기이한 짐승들을 섬멸해 주시기 바랍니다."}, {"통곡의 동굴에 있는 에브루가 돌연변이 약탈자랩터 7마리, 돌연변이 독사 7마리, 돌연변이 늪괴물 7마리, 돌연변이 송곳니천둥매 7마리를 처치해 달라고 했습니다."}},
[1488] = {"타락자", {"이 제왕 아즈레토크라는 자에 대해 아는 바는 없지만 나 역시 마우린과 같은 생각이오. 그자를 처단해야 하오. 만약 그가 불타는 칼날단을 손아귀에 넣고 주물러 자신의 목표를 달성하려 한다면 더욱더 그렇소.","당신은 대족장님을 돕고자 한다는 것을 이미 우리에게 보여 주었으니, 이제 병력을 모아 저 악마 군주와 그의 흑마법사 부하들을 제거해 줄 것이라 믿겠소.","준비가 끝나면 남쪽으로 곧장 가시오. 그들을 제거한 후에 내게 돌아오시오."}, {"제왕 아즈레토크와 죽카르 그림로드를 처치하고 잊혀진 땅에 있는 타카타 스틸블레이드에게 가야 합니다."}},
[1489] = {"하뮬 룬토템", {"자네가 가져온 등껍질에 한 번도 본 적 없는 오염된 자국이 있었네. 분명 오아시스의 물이 원인일 거야. 그 물이 이 땅에 혼란을 일으키고 있다는 것을 느낄 수 있어. 마치 땅이 깨어날 수 없는 악몽을 꾸고 있는듯해.","그 오염의 근원은 틀림없이 통곡의 동굴일 걸세. 하지만 그곳으로 가기 전에 하뮬 룬토템이라는 드루이드 원로를 만나 보게나. 불모의 땅에서 일어나는 일들에 대한 소식을 전해 들어 알고 있을 거야. 지혜로운 자이니 조언을 구할 수 있을 걸세.","썬더 블러프에 있는 장로의 봉우리로 가면 만날 수 있을 걸세."}, {"하뮬 룬토템과 대화해야 합니다."}},
[1490] = {"나라 와일드메인", {"나는 꿈의 세계로 들어가 통곡의 동굴에 사는 드루이드 이교도 집단에 대한 환상을 보았네. 원래 그들에겐 숭고한 목표가 있었지만, 지금은 완전히 잘못된 길로 접어들고 말았지. 이제 그들은 불모의 땅 전역에서 나타나고 있는 타락한 악의 신하들일 뿐이네.","나는 늙어서 기운이 쇠했어. 그래서 꿈의 세계에 오래 머물 수 없었고, 그 오염된 드루이드들에 대해 많은 것을 알아내지는 못했네. 하지만 나라는 젊고 강한 영혼을 지녔지. 내가 그 꿈속에서 도망쳐 나온 후에도 그녀는 계속 머물러 있었네.","천막 안에 있는 나라에게 가 보게나."}, {"나라 와일드메인과 대화하십시오."}},
[1491] = {"영리해지는 음료", {"흠, 랩터 뿔은 별 소용이 없었어. 하지만 첫술에 배부르란 법 없으니까 괜찮아. 그리고 더 좋은 재료를 찾았거든!","통곡의 정수라는 물질이 있는데 내 생각엔 그것이 불모의 땅에 특이한 동식물이 나타나는 원인인 것 같단 말이야. 내게 이 물질을 구해다 주면 꼭 영리해지는 음료를 만들고야 말겠어.","서쪽 통곡의 동굴에 있는 외형질 생물들의 몸 안에 통곡의 정수가 쌓인다고 하더군. 그 생물들을 사냥해서 정수를 모아 달라고."}, {"통곡의 정수 6병을 수집해야 합니다."}},
[1492] = {nil, {"동부 왕국에 있는 우리 동료에게 여러 재료와 물약과 시약이 담긴 상자를 보급해야 하네. 최대한 빨리 이 상자를 톱니항에 보내야 하는데 마지막 짐마차가 이미 떠나 버려서 곤란한 상황이야. 가능하다면 자네가 이 상자를 부두주임 디지위그에게 전해 주었으면 한다네.","일 처리 솜씨가 재빠른 그라면 이 상자를 검은바다 만으로 떠나는 다음 배에 실어줄 거야."}, {"톱니항에 있는 부두주임 디지위그에게 단단히 잠긴 상자를 가져가야 합니다."}},
[1498] = {"수호자의 길", {"전사로서 우리의 가장 중요한 임무 중 하나는 바로... 보호라네. 우리는 호드의 수호자로서 약한 친구들을 안전하게 보호하고 있지. 진정한 수호자의 대열에 합류하고 싶다면 내게 자네의 기개를 증명해 보여야 하네.","번개도마뱀과 천둥도마뱀이 살고 있는 듀로타의 천둥 마루로 가게. 그리고 그 짐승을 사냥하도록! 자네가 그들보다 강하다는 걸 내게 보여 주게! 그 증거로 놈들의 그슬린 비늘을 몇 개 가져오게.","이 일을 해낸다면 내 자네에게 가르침을 주도록 하지."}, {"불모의 땅의 천리길 경비초소에 있는 우제크에게 그슬린 비늘 5개를 가져가야 합니다."}},
[1499] = {"악마의 하수인", {"자네가 악마의 하수인과 싸웠던 장소 근처의 동굴로 가보게. 자네 같은 풋내기들을 보내 많은 짐승을 몰아내는 일을 하고 있는 주리타가 있을 걸세.","그녀를 도우면서 경험을 쌓는 게 좋을 것 같군. 그녀에게 임프를 다루는 일을 도왔다고 꼭 말하게. 아마 보상을 해줄 테니까."}, {"주리타 파게이즈와 대화하십시오."}},
[1500] = {"깨어나는 나랄렉스", nil, nil},
[1501] = {"공허의 존재", {"남아 있는 흑마법사가 별로 없는 이 상황에 우리는 피할 수 없는 큰 위험에 직면하고 있네. 그러니 호드와 대족장님을 위해 일하려면 한시라도 빨리 기량을 키워 놓아야 하네.","이제 새로운 소환 주문을 배울 때가 되었네.","우선 불타는칼날단의 흑마법사들에게 도난 당한 베르가의 서판을 되찾아 오도록 하게. 지금은 도시 동쪽 해골 바위굴에 있는 놈들의 소굴에 보관돼 있을 거야. 서판을 가져오면 보이드워커를 소환하는데 필요한 룬을 가르쳐 주도록 하지."}, {"오그리마에 있는 간룰 블러드아이가 베르가의 서판을 되찾아 오라고 부탁했습니다."}},
[1502] = {"툰그림 파이어게이즈", {"전사에게는 훌륭한 무기가 필수적이지. 툰그림 파이어게이즈라는 오크가 있는데 이 시대에 보기 드문 장인이지. 그는 십자로 동쪽 언덕 꼭대기에서 은둔 생활을 하고 있어.","쓸만한 무기를 찾고 있다면 툰그림을 만나 보게. 서쪽의 십자로로 간 다음 동쪽 언덕으로 곧장 가게. 언덕 위에 그의 야영지가 있을 거야."}, {"불모의 땅에 있는 툰그림 파이어게이즈와 대화해야 합니다."}},
[1503] = {"단조된 강철", {"먼저 날 좀 도와준다면 내 당신에게 무기를 만들어 주겠어. 며칠 전 서슬갈기 가시멧돼지들이 밤에 내 야영지로 쳐들어왔지. 놈들은 내게 싸움을 걸지도 않았는데 내가 앞을 잘 보지 못한다는 사실을 알고 있었어. 몇 녀석이 킁킁거리며 내 주의를 끄는 동안 나머지 놈들이 내 물건을 훔쳐갔소! 비겁한 놈들!","놈들은 철로 만든 상자를 가져갔소. 바로 그 안에 단조된 강철 주괴가 들어있소. 그걸 되찾아 준다면 당신에게 무기를 주도록 하지.","가시멧돼지들의 야영지로 가서 철 상자를 찾아 단조된 강철 주괴를 가져다주시오."}, {"불모의 땅에 있는 툰그림 파이어게이즈에게 단조된 강철 주괴를 가져가야 합니다."}},
[1504] = {"결속", {"자, 베르가의 서판을 잘 살펴보게. 그 안에 소환에 사용되는 문양이 포함되어 있으니까. 그걸 반대쪽 네루의 막사에 있는 소환 마법진으로 가져가도록 하게. 그곳에서 보이드워커를 소환할 수 있을 거야.","자네가 가진 모든 힘과 마법으로 보이드워커를 물리쳐야 하네. 그런 다음 소환의 문양을 가지고 돌아오면 보이드워커를 자네의 의지에 결속시키는 방법을 알려 주도록 하겠네."}, {"소환의 문양을 사용해 보이드워커를 소환하여 굴복시킨 다음 오그리마에 있는 간룰 블러드아이에게 사용한 소환의 문양을 가지고 돌아가십시오."}},
[1505] = {"노련한 우제크", {"호드의 전사들은 압도적인 힘으로 명성을 얻고 있지. 하지만 진정 위대한 전사라면 힘과 동시에 언제나 겸손하게 배우는 자세를 가져야 하오.","우제크를 만나보도록 하시오. 그는 셀 수 없이 많은 전투를 치른 노련한 전사인데, 지금은 젊은 전사들에게 전투 방식을 가르치고 있지. 불모의 땅과 듀로타 접경 지역에 있는 오크 주둔지, 천리길 경비초소의 남쪽으로 가면 그를 만날 수 있을 것이야."}, {"천리길 경비초소에 있는 우제크과 대화해야 합니다."}},
[1506] = {"간룰의 소환", {"이런, <name> 아닌가?","지금 바쁘지 않았으면 좋겠군. 간룰님께서 지금 당장 당신을 보자고 하시니까 말이네. 어둠의 틈에 있는 상급 흑마법사들에게 가서 간룰님을 찾아 보도록 하게.","이제까지 내 경험에 비추어 보면 간룰님께서 부르시는 건 아주 좋은 일 아니면 아주 나쁜 일 중 하나일 거야."}, {"오그리마에 있는 간룰 블러드아이와 대화하십시오."}},
[1507] = {"영혼을 삼키는 자", {"자, 이제 소환 마법에 대해 한층 더 깊이 공부할 때가 되었네. 정신력이 놀랄 만큼 강한 서큐버스를 소환하는 방법을 배우게 될 거야. 서큐버스는 정신적으로 나약한 이들을 자신의 노예로 삼을 수 있지. 하지만 두려워하지 말라고! 자네라면 충분히 감당할 수 있을 테니까.","서큐버스는 위대한 헌신과 사랑을 상징하는 물건으로 유인해야만 소환할 수 있네. 자네가 직접 찾아보도록 해.","힌트를 조금 주자면... 카줄을 한번 만나 보게. 그 늙은 흑마법사는 모르는 게 없으니까."}, {"어둠의 틈에 있는 카줄과 대화해야 합니다."}},
[1508] = {"눈먼 카줄", {"흠... 어쨌거나, 날 찾아 온 목적은 따로 있겠지.","간룰이 보낸 거라면 서큐버스를 소환하기 위해서일 테고 유인하는 데 쓸 물건이 필요해서 온 거겠지.","흠... 내가 눈이 멀긴 했지만 날 믿고 따를 생각이라면 잘 듣도록. 와이번 횃대가 있는 탑 서쪽으로 오그리마의 절벽에 잔카자라는 여인이 살고 있네. 그녀는 남편이 돌아오기를 기다리고 있지.","가서 그녀를 만나 보게나."}, {"오그리마에 있는 잔카자와 대화해야 합니다."}},
[1509] = {"도그란의 소식", {"제 남편 도그란은 얼마 전 임무를 수행하기 위해 크로스로드로 파견되었어요. 그건 바로 진급을 뜻하는 거였기 때문에 그는 무척 자랑스러워했어요.","물론 전 걱정되고 슬펐지만 내색하지 않았어요. 불모의 땅은 오그리마보다 훨씬 더 위험한 곳이니 무슨 일이라도 일어나 그를 다시 보지 못할까봐 두려웠던 거랍니다.","그가 돌아왔어야 할 날짜가 벌써 일주일이나 지났는데 아직 아무런 기별조차 없으니...","크로스로드로 가서 그의 소식을 좀 알아봐 주시겠어요? 남편은 가즈록이라는 사람과 함께 일했어요. 그는 아마 아직 그곳에 있을 거예요."}, {"크로스로드에 있는 가즈록과 대화하십시오."}},
[1510] = {"도그란의 소식", {"도그란은 가시멧돼지를 섬멸하기 위해 타우라조 야영지에 배치되었소. 몇 명의 그런트들을 이끌고 놈들의 소굴로 들어갔는데 그곳에서 큰 부상을 입고 말았소. 다른 이들이 후퇴하면서 그를 구해 내려고 했지만 부상이 심하고 상처가 곪아 먼저 치료부터 하지 않고는 옮길 수가 없었소.","전령이 가져온 소식에 의하면 돌발톱 산맥 아래 말라카진에 있는 트롤에게서 치유의 술을 구해야 한다더군. 이미 그곳으로 병사 하나를 보냈는데 아직 돌아오지 않고 있소."}, {"말라카진에 있는 켄지글라와 대화해야 합니다."}},
[1511] = {"켄지글라의 술", {"또 다른 누군가 술을 구하러 올 줄 알고 미리 준비해 놓았지.","자, 받으시오. 단 환자에게 절반만 먹여야 한다는 것을 명심하시오! 그러면 움직일 수 있을 만큼의 기력은 회복될 거야. 피도 너무 많이 흘리지는 않게 될 것이고. 하룻밤이 지난 다음에 나머지 반을 먹이도록 하시오.","아무튼, 이걸 타우라조 야영지에 있는 로그마르에게 가져가면 될 거요. 처음 왔던 병사의 말로는 그가 도그란이 있는 곳을 알고 있을 거라더군."}, {"타우라조 야영지에 있는 그런트 로그마르에게 켄지글라의 술을 가져가야 합니다."}},
[1512] = {"사랑의 선물", {"내 아내 잔카자는 아직 젊고 아름답지. 나 같은 불구자에게 얽매이도록 할 수는 없소... 죄 없는 그녀를 불행하게 할 수는 없단 말이오.","차라리 나를 두고 가시면 고맙겠소. 생기를 잃어버린 껍데기만으로 사느니 차라리 명예롭게 전사할 것이오.","그리고 제발... 잔카자에게는 이 사실을 알리지 마시오. 그녀가 부질없는 죄책감으로 괴로워하는 건 원치 않소."}, {"오그리마에 있는 간룰 블러드아이에게 진흙투성이 목걸이를 가져가야 합니다."}},
[1513] = {"결속", {"이 목걸이를 네루의 막사에 있는 소환 마법진으로 가져가게. 사악한 서큐버스를 소환한 다음 처치해야 하네. 자네가 가진 마법의 힘을 믿어 보도록 하지. 장래가 촉망되던 많은 흑마법사들이 과도한 자만으로 인해 무너지고 말았다는 사실을 항상 명심하게.","서큐버스를 물리친 다음 목걸이를 내게 다시 가져오도록 하게. 그러면 내가 잔카자에게 남편의 소식을 전하도록 하지."}, {"도그란의 목걸이를 사용해 서큐버스를 소환하여 처치한 다음 오그리마에 있는 간룰 블러드아이에게 도그란의 목걸이를 가져가야 합니다."}},
[1514] = {"오염된 바람꽃", nil, nil},
[1515] = {"포로가 된 도그란", {"지금까지 일을 진척해 왔으니 당신이 끝까지 임무를 완수해주었으면 좋겠소. 하지만 다른 대원을 차출해 줄 수는 없으니 혼자 가야 하오.","도그란은 북쪽에 있는 가시멧돼지 소굴에 붙잡혀 있소. 두 채의 오두막이 붙어 있는 곳을 찾도록 하시오. 경계가 삼엄할 거요. 놈들이 이미 그를 죽였거나 부상이 심해 죽음을 맞이하지 않았다면 구해낼 수도 있겠지.","행운을 빌겠소."}, {"불모의 땅에 있는 도그란에게 켄지글라의 술을 가져가야 합니다."}},
[1516] = {"대지의 부름", {"젊은 <class>|1이여;여;, 이제 때가 되었네. 자네의 힘은 강해졌고 자네 영혼은 대지와 같은 인내를 갖게 되었으니 말이야. 주술사의 길을 선택했다는 것은 자네가 이미 지혜를 가지고 있다는 것을 나타내는 거야.","준비가 되면 대지의 원소가 자네의 운명을 인도하여 자네의 일부가 될 거야. 하지만 자네가 대지 앞에 직접 나서야만 하지. 때가 되면 주술사만이 알 수 있는 것들을 보게 될 것이야.","북쪽으로 가서 하급 지옥사냥개들을 찾아 처치한 후 놈들의 발굽 두 개를 가져오도록 하게. 그걸 가지고 내게 돌아온 다음 자네의 미래에 대해 좀 더 얘기해 보자고."}, {"시험의 골짜기에 있는 카나가 어스콜러에게 하급 지옥사냥개 발굽 2개를 가져가야 합니다."}},
[1517] = {"대지의 부름", {"정기란 우리의 영혼을 자연의 원소에 결속시키기 위해 만들어진 자연이 주는 음료이지. 거기 담겨 있는 마법은 의지가 강한 이들에게 힘을 준다네. 평범한 이들에게는 보이지 않는 자연의 정령을 볼 수 있는 힘 말이야. 선택된 소수의 주술사들만이 정기를 만드는 법을 알고 있네. 자네의 영혼은 각 정기에 맞는 정령과 연결될 거야.","정기는 반드시 신성한 장소에서 마셔야 해. 다른 장소는 절대로 안 되니 그걸 명심하게.","여기서 남서쪽에 있는 정기 바위를 찾아가게. 비밀의 길을 따라가다 보면 나올 거야. 그곳에서 정기를 마시도록 하게. 그러면... 보게 될 거야."}, {"정기 바위를 찾아 대지의 정기를 마셔야 합니다."}},
[1518] = {"대지의 부름", {"우리가 만났다는 증거로 이 천연 석영을 받아 카나가 어스콜러에게 가져가도록 하라. 자네를 위해 토템을 만들어 줄 것이다. 이 작은 조각이야말로 토템의 가장 중요한 재료이니 신중히 전달하도록 하라. 비록 보기에는 작아도 크기는 문제가 되지 않는다는 걸 깨닫게 될 것이다. 가장 작은 것이 다른 것들보다 더 오래 존속할 수도 있는 법이니까.","인내심을 가져라. 언제나 강한 힘을 소유하고 지혜를 추구하라. 이것이 대지가 너에게 요구하는 전부다. 대지가 너를 존중하듯 너도 대지를 존중하라. 그러면 언젠가 대지와 하나가 될 것이다."}, {"시험의 골짜기에 있는 카나가 어스콜러에게 천연 석영을 가져가야 합니다."}},
[1519] = {"대지의 부름", {"때가 되었습니다, 젊은 <class>|1이여;여;. 그대의 몸은 시간이 지날수록 강해지고 그대의 영혼은 대지와 같이 앞으로 닥칠 시련을 견뎌낼 것입니다. 그대는 이미 지혜롭다는 걸 난 알 수 있답니다.","대지가 우리 종족을 이끌고 우리에게 말을 하고 있습니다. 물론, 때가 되면 그대에게도 말을 할 겁니다. 하지만 시험을 거쳐야만 하지요. 준비가 되었다면 우리 종족만이 알고 있는 일들을 보게 될 겁니다.","동쪽 지역에서 뾰족털 주술사들을 찾아 그들이 의식에 사용하는 물약을 구해오도록 하세요. 그대가 사용하기에 충분한 양을 모은 후에 내게 돌아오십시오."}, {"나라체 야영지에 있는 예언자 레이븐페더에게 의식의 물약 2개를 가져가야 합니다."}},
[1520] = {"대지의 부름", {"정기는 우리 영혼을 자연의 원소에 결속하기 위해 만들어진 것입니다. 열린 마음을 지닌 이들이 이 마법의 물약을 마시면 다른 이들은 볼 수 없는 자연의 원소들을 볼 수 있게 되죠. 그대의 영혼은 각 정기에 해당하는 자연의 원소에 영원히 결속될 겁니다. 우리 종족 중 선택된 소수만이 정기를 만드는 비법을 알고 있지요.","정기는 반드시 신성한 장소에서 마셔야 합니다. 여기서 동쪽에 있는 주술의 제단을 찾아가 그곳에서 정기를 마시도록 하시기 바랍니다."}, {"코도 바위를 찾아 대지의 정기를 마셔야 합니다."}},
[1521] = {"대지의 부름", {"우리가 만났다는 증거로 이 천연 석영을 받아 예언자 레이븐페더에게 가져가도록 하라. 그녀가 너를 위해 토템을 만들어 줄 것이며 이 작은 돌이 토템의 가장 중요한 재료가 될 것이다. 비록 보기에는 작아 보이지만 크기는 문제가 되지 않는다는 걸 알게 될 것이다. 미물이 태산보다 더 오래 존속할 수도 있는 법이니라.","인내심을 가져라. 언제나 강한 힘을 유지하고 지혜를 추구하여라. 이것이 대지가 너에게 요구하는 전부니라. 대지가 너를 존중하듯 대지를 존중하라. 그러면 마침내 대지와 하나가 될 것이다."}, {"나라체 야영지에 있는 예언자 레이븐페더에게 천연 석영을 가져가야 합니다."}},
[1522] = {"불의 부름", {"불의 힘이 함께 하기를... 자네는 땅의 주술에 능통해졌으니 이제 크라날에게 가볼 때가 된 거 같군.","크라날은 불모의 땅의 크로스로드 북쪽에 살고 있네. 그는 자연의 원소들을 다루는 데 있어 강력한 힘을 가졌으니 자네에게 불의 주술을 가르쳐 줄 수 있을 거야. 준비가 되었다면 한번 가보게나.","크라날을 찾는 건 쉬워. 너무 멀리만 가지 않으면 되지. 엘프의 땅에 발을 들여놓지 않도록 조심하라고."}, {"불모의 땅에 있는 크라날 피스를 찾아야 합니다."}},
[1523] = {"불의 부름", {"<name>, 마지막으로 당신의 실력을 시험해 본 지도 오래전 일이군. 지금까지 끈기 있게 잘해왔지만 이제 더 많은 것을 배워야 할 때가 왔소. 이제는 파괴... 그리고 혼돈에 대해 배워야 하오. 배움을 위해서 어떤 대가라도 치를 준비가 되었다면 불이라는 자연의 원소에 대해 배울 때가 된 것이지.","불모의 땅에 있는 크라날 피스를 찾아가 보시오. 크로스로드의 북쪽에 있는 작은 거처가 머물고 있는데, 너무 멀리 가면 나이트 엘프의 땅에 이르게 되니 조심하시오."}, {"불모의 땅에 있는 크라날 피스를 찾아야 합니다."}},
[1524] = {"불의 부름", {"자네는 불의 토템을 사용할 수 있을 정도로 강하지만 아직 준비가 덜 되었네.","듀로타의 성난남녘 강을 따라가다 보면 대해에 못 미쳐서 숨겨진 오솔길이 있는데, 그 길을 따라 올라가면 이 땅에서 가장 높은 봉우리 중 하나에 이르게 된다네. 그 꼭대기까지 올라가게. 그러면 텔프 줄람이 지키는 희미한 불꽃의 제단이라는 성지를 발견하게 될 거야.","자네가 불을 길들이려 한다는 표시로 이 횃불을 그에게 주게. 제단에 있는 화로를 타오르게 하여 내게 가져오면 불의 토템을 주겠네."}, {"듀로타에 있는 텔프 줄람에게 희미한 불씨의 횃불을 가져가야 합니다."}},
[1525] = {"불의 부름", {"자네는 영원한 불꽃의 횃불과 불의 토템을 얻으러 온 것이지, 역사의 비밀을 알아내려는 것은 아니지 않나?","첫 단계는 정기를 만들어 불꽃에 자기 자신을 구속하는 것이야.","내게 두 가지 재료를 가져오면 불의 정기를 만들어 주겠네. 첫 번째 재료를 구하는 일은 간단해. 불붙인 타르라는 것으로 불모의 땅 서슬갈기 일족 중에서 마법을 사용하는 놈들에게서 구할 수 있지.","두 번째 재료는 구하기가 조금 더 힘들 거야. 불타는 칼날단 이교도들 중 한 명에게서 시약 주머니를 빼앗아 오게나. 칼바위 언덕 북동쪽에 있는 협곡 위의 한 동굴로 가면 그들을 만날 수 있을 걸세."}, {"듀로타에 있는 텔프 줄람에게 불붙인 타르 1개와 시약 주머니 1개를 가져가야 합니다."}},
[1526] = {"불의 부름", {"불의 정기가 완성됐네. 이제 불꽃과 맞설 차례일세. 이제 불의 정기를 마시고 이 산의 꼭대기까지 올라가 그곳에 있는 불의 현신에 맞서도록 하게. 녀석이 당장 공격하지는 않겠지만 준비를 서둘러야 할 거야. 곧 자네가 자기를 볼 수 있다는 것을 알아차릴 테니...","재빨리 그를 해치우게. 오래 끌면 끌수록 그의 힘은 더 강해지게 될 테니까. 그를 물리친 후에는 그의 타오르는 불꽃을 화로에 넣고 횃불에 불을 붙이게."}, {"하급 불의 현신을 물리친 다음 희미한 불꽃의 화로에 타오르는 불꽃을 넣어야 합니다."}},
[1527] = {"불의 부름", {"횃불을 손에 꼭 쥔 채 화로에서 등을 돌려 듀로타를 내려다 봅니다. 바람이 일어 불길이 심하게 흔들리면서 말할 수 없는 전율이 느껴집니다.","크라날 피스를 찾으러 다시 불모의 땅으로 길을 내려가기 시작합니다."}, {"불모의 땅에 있는 크라날 피스에게 영원한 불꽃의 횃불을 가져가야 합니다."}},
[1528] = {"물의 부름", {"자네의 미래에 물이 흐르는 것이 보이는군. 이미 불의 도에는 능통해졌으니 이제 이슬렌을 만나러 갈 때가 된 거 같군그래.","이슬렌은 불모의 땅의 해안가에서 살지. 분노의 강을 따라 남쪽으로 가면 틀림없이 만나게 될 거야. 큰 상어를 찾아 보라고! 이슬렌은 자연의 원소들을 다루는 데 있어 강력한 힘을 가졌으니 자네에게 물의 능력을 가르쳐 줄 수 있을 거야... 준비가 됐다고 생각하면 한번 가 보라고.","이슬렌은 쉽게 찾을 수 있을 거야. 톱니항을 지나면 바로 있을 테니까 남쪽으로 너무 멀리 가지만 않으면 돼."}, {"불모의 땅에서 이슬렌 워터시어를 찾아야 합니다."}},
[1529] = {"물의 부름", {"<name>, 마지막으로 당신의 실력을 시험해 본 지도 벌써 오래전 일이군. 지금까지 끈기 있게 잘해왔지만 이제 더 많은 것을 배워야 할 때가 왔소. 이제는 순수한 물에 대해 배워야 하오.","불모의 땅에 있는 이슬렌 워터시어를 찾아가시오. 동쪽 해안을 따라 분노의 강과 톱니항의 남쪽에 있는 오두막에서 물고기 낚을 준비를 하고 있는 그녀를 찾을 수 있을 거요. 행운을 빕니다, <name>."}, {"불모의 땅에서 이슬렌 워터시어를 찾아야 합니다."}},
[1530] = {"물의 부름", {"물에서 배워야 할 가장 중요한 교훈이라면 물은 곧 소생을 의미한다는 것이랍니다.","그 힘으로 만물이 흐르고, 침식되고, 정화되는 것입니다. 물이 지닌 치유력과 재생력은 그 어느 것과도 비교할 수 없다는 걸 알게 될 거예요. 하지만 그것은 물이 순수한 상태일 때만 가능합니다.","오염된 물은 모든 것을 황폐하게 하는 파괴력이 있다는 것을 이해해야 물이 얼마나 중요한 자원인지 느낄 수 있을 겁니다.","물의 정기를 원한다면 불모의 땅 남쪽 깊은 곳에 사는 브린을 찾아가세요. 그녀의 집을 가시멧돼지들이 끊임없이 위협하고 있답니다."}, {"불모의 땅 남부, 검은가시 마루에 있는 브린을 찾아야 합니다."}},
[1531] = {"바람의 부름", {"점점 발전해 나가고 있군. 바람의 부름에 응할 준비가 된 것 같아. 우선 버섯구름 봉우리에 있는 프레이트 클라우드시어를 찾아야 하네. 그녀의 도움을 얻어 바람의 부름을 구할 수 있을 거야.","불모의 땅에서 남쪽으로 가면 버섯구름 봉우리가 나오지... 거기서 동쪽으로 가보게. 아마 지금쯤 애인이랑 굴 속에 숨어 있을 걸세. 은둔 생활로 잔뜩 예민해져 있을 테니... 신경은 건드리지 말고."}, {"버섯구름 봉우리에서 프레이트 클라우드시어를 찾아야 합니다."}},
[1532] = {"바람의 부름", {"칼림도어의 바람이 당신을 향해 불어오고 있소. 당신은 우리 호드의 가장 강력한 일원 중 하나로 여겨질 것이오. 정말 뿌듯하군. 지금껏 잘해왔는데다 많은 이들에게 귀감이 되어 앞날에 대한 희망과 기대까지 심어 주고 있으니...","바람은 제어하기 쉽지 않지만 당신만한 실력이라면 눈앞에 놓인 어떤 장애도 극복할 수 있으리라 믿어 의심치 않소.","불모의 땅 남쪽에 있는 버섯구름 봉우리로 가도록 하시오. 동쪽으로 한참 가다 보면 프레이트 클라우드시어를 만날 수 있을 거요. 나보다 더 많은 것을 가르쳐 줄 테니 가르침을 구하시오."}, {"버섯구름 봉우리에서 프레이트 클라우드시어를 찾아야 합니다."}},
[1534] = {"물의 부름", {"이 물주머니를 가지고 나이트 엘프들이 잿빛 골짜기라고 부르는 울창한 숲으로 가세요. 그 곳으로 가려면 일단 불모의 땅의 북쪽 끝으로 가세요.","그리고 숲에 들어서거든 서쪽으로 가세요. 돌발톱 산맥과 잿빛 골짜기 사이에 있는 산을 따라 가다가 미스트랄 호수를 지나면 성지가 있을 거예요. 별가루의 폐허라고 하는데 호수 가운데 작은 샘이 있죠.","그 샘물을 가져오세요. 이슬렌에게 돌아가기 전에 필요한 마지막 물건이에요."}, {"잿빛 골짜기에 있는 별가루의 폐허에서 빈 파란색 물주머니를 채워 불모의 땅에 있는 브린에게 가져가야 합니다."}},
[1535] = {"물의 부름", {"물에 대해 깨우침과 정기를 받고 싶다면 가장 순수한 형태의 물을 찾아야 해요. 견본을 가져올 때는 주변 환경을 잘 기억해 두세요. 이 땅의 생명체들은 물에 의존하는 동시에 그것을 수호하지요. 또한, 물이 숲과 사막에 어떠한 영향을 미치는지도 잘 보시고요. 그리고 생명의 원천인 물이 어떻게 생명을 앗아가는지도 똑똑히 봐두세요.","그럼, 간단한 임무부터 시작하죠. 이곳 바로 아래, 귀한 물이 고인 연못이 있답니다. 이 주머니에 그 물을 가득 담아 가져오세요."}, {"브린의 거처 아래에 있는 물 구멍으로 가서 빈 갈색 물주머니에 물을 채우십시오. 그런 다음 불모의 땅에 있는 브린에게 가져가야 합니다."}},
[1536] = {"물의 부름", {"오래전 동료 몇을 따라 언덕마루 구릉지에 갔답니다. 그런데 그곳에 사는 끈질긴 인간들의 추격에 쫓기다가 포세이큰이 정복한 작은 마을까지 밀려났어요. 그곳이 바로 타렌 제분소였죠.","그런데 그 제분소에서 아주 특이한 걸 보게 되었답니다. 마을 한복판에 맑은 물이 솟아나오는 조그마한 우물이 있었어요. 마을 주민들은 역병에 걸려서 아마 그 안에 담긴 물이 어떤 가치를 지니고 있는지도 모르고 있을 거예요. 그 물은 지금은 그 신성함을 잃어 버린 바다에서 온 것이라서 이 땅에 흔적처럼 남아 있는 신성하고 순수한 결정체랍니다.","이 물주머니에 그 물을 담아오세요."}, {"타렌 제분소에 있는 우물에서 빈 빨간색 물주머니를 채워 불모의 땅에 있는 브린에게 가져가야 합니다."}},
[1558] = {nil, nil, nil},
[1559] = {"섬광탄 제조법", nil, nil},
[1560] = {"길 잃은 투가", {"길을 잃어버렸어요!","제 아내 토르타가 저녁거리를 구해 오라고 보냈는데 어디에선가 길을 잘못 든 것 같아요.","저를 토르타에게 데려다 주시지 않으시겠어요? 스팀휘들 항구 바로 남쪽에서 저를 기다리고 있답니다."}, {"투가를 아내 토르타에게 데려다 줘야 합니다."}},
[1578] = {"전방 보급품 조달", {"모단 호수의 산악경비대는 트로그와의 싸움으로 항상 바쁘다오. 그들에게 방어구와 무기를 지속적으로 보급해 줘야 하오.","바로 그 때문에 내 동료 토르발트가 거기 상주하면서 산악경비대에 제대로 된 보급품을 전달하도록 힘쓰고 있다오. 구리 도끼와 구리 사슬 허리띠를 만들어 그에게 가져다주면 아주 고마워할 거요. 어쩌면 자기가 알고 있는 기술을 알려줄지도 모르겠군.","토르발트는 모단 호수 남쪽에 있는 방어탑에 가면 찾을 수 있소."}, {"모단 호수 남쪽 방어탑에 있는 토르발트에게 구리 도끼 6개와 구리 사슬 허리띠 6개를 가져가야 합니다."}},
[1579] = {"개퍼 잭", {"전 아우버다인이 조용한 게 마음에 들어 이곳으로 왔답니다. 이렇게 조용한 곳에서 발명에 열중하고 싶어서 말이에요. 아이언포지에서는 특히 술이라도 한잔 걸친 다음 날에는 그 소음을 견디기 힘들거든요!","그래서 아우버다인으로 가는 배를 탔는데 도중에 술을 좀 과하게 마시는 바람에 개퍼 잭이 든 상자를 밖으로 떨어뜨려 버렸어요. 작업에 꼭 필요한 물건인데!","정확히 어디에 떨어뜨렸는지는 모르겠지만, 바다에서 낚시를 하다가 운 좋게 건질 수 있을지도 몰라요."}, {"아우버다인에 있는 위즈뱅 크랭크토글에게 개퍼 잭 8개를 가져가야 합니다."}},
[1580] = {"전력추진기", {"당신은 운이 참 좋은 것 같은데... 어디 한번 더 확인해 볼까요?","어느 날 전 맥주를 좀 마시고서 어둑어둑한 어둠의 해안을 거닐고 있었어요. 자체 추진력이 있는 폭탄형 오리 미끼를 시험할 만한 강이나 호수를 찾고 있었거든요. 그런데 제가 조금 취했었나 봐요. 몇 시간 동안 정신을 잃었다가 아우버다인에서 깨어났죠. 깨어나 보니 머리카락은 온통 새카맣게 그을려 있고 전력추진기 주머니는 온데간데 없지 뭐예요! 분명 물속에 빠뜨린 게 틀림없어요!","그 전력추진기는 정말 중요한 물건이에요. 어둠의 해안에 있는 호수나 시내에서 낚시를 하시다가 행여라도 전력추진기를 건지면 가져다주시겠어요?"}, {"아우버다인에 있는 위즈뱅 크랭크토글에게 전력추진기 12개를 가져가야 합니다."}},
[1581] = {"블레이드리프를 위한 비약", {"요즘 우리 약초 가게와 연금술 가게가 아주 호황이랍니다. 하지만 남편은 물약을 만드느라 너무 바빠서 서로 도와줄 시간이 없어요. 그래서 말인데 좀 도와주시지 않겠어요?","미리 조제된 비약을 좀 가져오시면 기꺼이 거래하겠어요. 요긴하게 쓰실 만한 희귀 약초가 좀 있거든요."}, {"돌라나르에 있는 시랄 블레이드리프에게 사자의 힘 비약 6개와 최하급 방어의 비약 2개를 가져가야 합니다."}},
[1582] = {"달빛 조끼", {"다르나서스에 사는 우리 장인들은 아주 바쁘답니다. 열성이 대단한 젊은 모험가들이 자신의 능력을 시험해보고 싶어 황야로 모여들고 있습니다. 그들은 꾸준히 우리의 보급품을 필요로 하고 있답니다.","이번에 주문받은 가죽 제품을 보충할 수 있도록 도와준다면 고급 가죽세공술을 가르쳐 드리도록 하죠."}, {"다르나서스에 있는 로서리아스에게 새김무늬 가죽 장갑 1개, 새김무늬 가죽 장화 5개, 새김무늬 가죽 망토 5개를 가져가야 합니다."}},
[1598] = {"도난 당한 책", {"거기! <name>|1이;가; 아니세요?","흑마법사가 될 마음의 준비가 단단히 되어 있길 바라요. 스스로 자신의 길을 꿋꿋이 지켜나가지 못한다면 적에게 당할 게 뻔하니까요.","앞으로 제가 당신을 가르쳐 드리게 될 테지만 먼저 실력이 어떤지 시험해 봐야겠네요.","북녘골 수도원에서 공부하던 수련사제 한 명이 도망쳐 데피아즈 결사단에 합류했어요. 저 같은 흑마법사가 왜 이 일에 신경 쓰냐고요? 도망친 녀석이 중요한 책 몇 권을 훔쳐갔거든요.","그중 한 권이 제가 아끼는 공허의 힘이라는 책이에요. 녀석들은 포도밭에 있는 막사에 그 책을 숨겨 놓았으니 그걸 훔쳐오세요."}, {"드루실라 라 살르를 위해 공허의 힘을 입수해야 합니다."}},
[1599] = {"시작", {"사람들이 수군거리던 흑마법사가 자네로군. 날 보고 하던 연구를 미루고 자네를 가르치라는 요청이 들어온 걸 보면, 누군가 자네를 상당히 마음에 들어 하는 모양이야. 걱정 말게, 그렇다고 괴롭히진 않을 테니까.","자, 소환 주문 중 가장 쉬운 것부터 시작하지. 임프 소환 주문을 가르쳐 주겠네. 하지만 그 전에 자네가 흑마법사로서 필요한 마법 능력과 신체적 조건을 갖추고 있다는 것을 보여주게.","남서쪽의 동굴에서 서리갈기 수습마법사 몇 마리를 잡아 깃털 부적 3개를 가져오게."}, {"앤빌마에 있는 알라마 그림에게 깃털 부적 3개를 가져가야 합니다."}},
[1618] = {"붉은마루산맥 무기 조달", {"요새는 우리 드워프들 뿐만 아니라 얼라이언스의 일원인 인간들도 바빠 죽을 지경이지!","붉은마루 산맥은 검은바위 오크들로부터 지속적으로 공격받고 있어 항상 장비가 부족한 형편이오. 당신이 이 물건들을 가져다준다면 붉은마루 산맥에 있는 인간 대장장이 베르너 오스구드가 쓸만한 기술을 몇 가지 가르쳐 줄 거요."}, {"토르무스 딥포지가 붉은마루 산맥에 있는 베르너 오스구드에게 구리 룬새김 허리띠 4개와 무거운 구리 마울 4개를 가져다 달라고 했습니다."}},
[1638] = {"전사 훈련", {"당신은 꽤 솜씨 좋은 전사로군요. 하지만 아직은 젊고 배워야 할 것이 많이 남아 있습니다.","해리 벌가드에게 가서 얘기를 나눠 보시기 바랍니다. 그는 보통 스톰윈드의 구 시가지에 있는 돼지와 휘파람 선술집에 있습니다. 해리는 경험이 풍부한 좋은 스승이니 전장에서 목숨을 구해줄 기술을 몇 가지 가르쳐 줄 겁니다."}, {"스톰윈드에 있는 해리 벌가드와 대화해야 합니다."}},
[1639] = {"주정뱅이 바틀비", {"그럼, 가르쳐 줄 수 있고 말고. 하지만 먼저 지금까지 어떤 기술을 익혔는지 보도록 할까?","저기 끝에 앉아 있는 주정뱅이 바틀비가 보이지? 저 녀석의 술잔을 가져오게나.","하하, 알고 있는지 모르겠지만 바틀비가 순순히 술잔을 넘겨줄 사람은 아니라네."}, {"바틀비와 대화하십시오."}},
[1640] = {"바틀비와의 대결", {"뭐를 달라고?? 자네 미쳤군!","내 눈에 흙이 들어가기 전에는 이 술잔을 넘겨줄 수 없어..."}, {"바틀비와 싸워 이긴 후 그와 대화하십시오."}},
[1641] = {"신앙의 고서", nil, nil},
[1642] = {"신앙의 고서", {"성기사는 모든 일을 행함에 있어 언제나 빛과 함께해야함을 잊지 말아야 한다. 우리가 추구하는 것은 스스로 신성한 존재가 되기 위함이 아니라 모든 일을 행함에 의로워야 하는 것이다.","이 혹독한 시대에 악을 물리치기 위해 부름을 받았다고 해도 성기사로서의 진정한 길은 타인을 돕는 것임을 항상 명심해야 한다. 자비와 인내, 용기와 같은 덕목을 소중히 여기는 것은 전투에서 힘을 발휘하는 것만큼이나 중요하다.","진정한 성기사가 되기 위해서는 이를 명심하고 절대 잊지 않아야 한다."}, {"스톰윈드에 있는 두소리안 랄과 대화하십시오."}},
[1643] = {"신앙의 고서", {"자, 이제 자네가 해야 할 일은 이곳 스톰윈드에서 도움의 손길이 필요한 사람을 찾는 것일세. 순수한 마음과 동기를 가진 이를 말이지. 또 다른 이를 돕는 자이거나, 안내가 필요한 자일 수도 있다네. 누구이든 의도가 숭고하며 자네의 친절한 도움이 반드시 필요한 사람이라야 함을 명심하게.","그런 사람을 찾아 도와준 후에 나에게 돌아오도록 하게나. 그 후에 자네가 앞으로 가야 할 길과 빛의 정의를 실천하는 성기사가 되는 것이 무엇을 말하는 지에 대해서 더 얘기하도록 하지."}, {"스톰윈드의 상업 지구에서 스테파니 터너를 찾아야 합니다."}},
[1644] = {"신앙의 고서", {"보육원에 있는 불쌍한 아이들은 지난 십 년 동안 스컬지의 침공으로 가족을 잃은 아이들이랍니다.","제 남편 존이 저에게 옷감을 갖다주면 제가 아이들이 입을 옷이나 바지를 만들지만 최근에는 그럴 시간도 없었어요. 아이들을 먹이고 입히는 데 온 신경을 다 쏟았었거든요.","네? 저를 도와주시겠다고요? 아, 신이시여, 감사합니다! 정말 고마우신 분이시군요!","옷을 좀 만들 수 있도록 리넨 옷감을 더 구할 수만 있다면 정말 큰 도움이 될 거예요."}, {"스톰윈드에 있는 스테파니 터너에게 리넨 옷감 10개를 가져가야 합니다."}},
[1645] = {"신앙의 고서", nil, nil},
[1646] = {"신앙의 고서", {"성기사는 모든 일을 행함에 있어 언제나 빛과 함께 해야함을 잊지 말아야 한다. 우리가 추구하는 것은 스스로 신성한 존재가 되기 위함이 아니라 모든 일을 행함에 의롭기 위한 것이다.","이 혹독한 시대에 악을 물리치기 위해 부름을 받았다고 해도 성기사로서의 진정한 길은 타인을 돕는 것임을 항상 명심해야 한다. 자비와 인내, 용기와 같은 덕목을 소중히 여기는 것은 전투에서 실력을 발휘하는 것 만큼이나 중요하다.","진정한 성기사가 되기 위해서는 이를 명심하고 절대 잊지 않아야 한다."}, {"아이언포지에 있는 타이자 배틀포지와 대화하십시오."}},
[1647] = {"신앙의 고서", {"자, 이제 해야 할 일은 이곳 아이언포지에서 당신의 손길을 기다리는 자를 찾는 것입니다. 순수한 마음과 동기를 가진 이를 말이에요. 안내자를 필요로 할 수도 있고, 또 당신의 도움으로 다른 누군가를 돕고자 하는 자일 수도 있습니다. 누구든 의도가 숭고하고 당신의 도움이 반드시 필요한 사람이라야 합니다.","이 사람을 도와준 후 나에게 돌아오시기 바랍니다. 그 때 당신이 앞으로 가야 할 길과 빛의 정의를 실천하는 성기사가 되는 것이 무엇인지에 대해 좀 더 얘기하도록 합시다."}, {"아이언포지의 외곽 지역에 있는 존 터너를 찾아야 합니다."}},
[1648] = {"신앙의 고서", {"전 스톰윈드에서 아내와 함께 보육원을 운영하고 있습니다. 저희 보육원 아이들은 지난 십 년 동안 스컬지의 침공으로 부모를 잃은 아이들이 대부분이랍니다. 저는 한 달에 한번 정도 이곳 아이언포지에 와서 후원을 받을 수 있을지 알아보고, 여러 단체에 도움의 손길을 청하고 있습니다.","그런데 이번에는 일이 쉽지 않네요.","제 집사람이 아이들 옷을 만들 리넨을 더 구할 수만 있다면 고향으로 부칠 수 있을 텐데 말입니다."}, {"아이언포지에 있는 존 터너에게 리넨 옷감 10개를 가져가 기부해야 합니다."}},
[1649] = {"용맹의 고서", {"용맹과 그에 수반되는 다른 모든 미덕은 귀한 광물과 같다. 귀한 광물은 각고의 노력을 기울여 캐내 정제하여 아껴두어야 하는 것과 마찬가지이다. 귀한 광물은 다른 재료들과 융합하면 아주 강한 합금이 만들어지며 어떠한 막강한 힘에도 부서지지 않게 된다.","바로 이 힘과 본인의 정의로움과 용기를 시험해야 한다. 어떠한 고난이 닥쳐도 정의로움을 잃지 않는 법을 배우며, 빛의 힘으로 범인들이 극복하지 못하는 것을 극복할 수 있다는 것을 깨달아야만 한다."}, {"스톰윈드에 있는 두소리안 랄과 대화해야 합니다."}},
[1650] = {"용맹의 고서", {"좋아! 주저하지 않는 것을 보니 준비도 되었고 자신을 시험해 보고 싶은 마음이 간절한 모양이군. 그럼 시작하도록 하지.","서부 몰락지대의 기나긴 해안 끝에 해변이 내려다보이는 자그마한 집이 한 채 있네. 그곳에 스틸웰 부부가 살고 있지. 남편인 조던은 아주 유능한 대장장이인데, 드워프들을 만나기 위해 잠시 아이언포지에 가 있다네. 그 바람에 그의 아내 다프네는 대책 없이 혼자 두려움에 떨고 있지.","보고받은 내용에 따르면 데피아즈단이 그 지역을 거의 점령했다고 하네. 그 악당들로부터 다프네를 보호해야 하니 그곳으로 가게."}, {"서부 몰락지대에 있는 다프네 스틸웰을 찾아야 합니다."}},
[1651] = {"용맹의 고서", {"이제, 틀림없이 곧 녀석들이 들이닥칠 테니 단단히 준비하세요.","집에 총이 하나 있으니 그걸로 도와드리긴 하겠지만 아무래도 대부분은 상대해 주셔야 할 거예요.","몇 명이나 공격해올진 저도 모르지만, 아까보다는 더 보내겠죠.","빛의 축복을 구하는 기도를 올리는 게... 방금 들으셨어요? 녀석들이 오고 있는 모양이에요.","집 가까이에서 막아내 보도록 하죠!"}, {"데피아즈단의 공격으로부터 대프니 스틸웰을 보호하십시오.","둘 중 하나라도 죽으면 실패하게 됩니다.","성공한 후에 대프니 스틸웰과 다시 대화해야 합니다."}},
[1652] = {"용맹의 고서", {"두소리안님께 돌아가서 제가 이제 안전하다고 전해 주세요. 당신과 대성당에 감사를 드립니다.","두소리안님께서 아이언포지에 있는 제 남편 조던에게 제 걱정은 하지 않아도 된다고 소식을 전해 주실 거예요.","아, 보고 싶은 조던... 남편이 빨리 돌아왔으면 좋겠네요."}, {"스톰윈드에 있는 두소리안 랄과 대화하십시오."}},
[1653] = {"정의의 시험", {"틀림없이 조던이 직접 자네에게 감사의 말을 전하고 싶어할 걸세. 마침 그 친구에게 아내가 무사하단 소식을 전해줄 사람이 필요한데, 자네가 이 일에 적당하겠군.","조던은 부친의 가업을 이어받아 대장장이가 되기 전까지만 해도 빛의 성기사가 될 사람이었지. 그래서 성기사로서의 삶의 자세와 방식에 대해서 아주 잘 알고 있는데다가 우리 중에서 가장 신앙심이 깊은 친구지.","그 친구는 분명 아직도 아이언포지에 있을 걸세. 차가운 산 공기가 좋다면서 낮에는 성문밖에서 일하기를 좋아한다네."}, {"아이언포지에 있는 조던 스틸웰과 대화해야 합니다."}},
[1654] = {"정의의 시험", {"괜찮으시다면 보상을 해 드리고 싶습니다. 아, 오해는 하지 마세요. 당신이 도움을 주는 만큼 보상을 해 드리는 거니까요. 그렇다고는 해도 이런 협력 관계가 우리 모두에게 유익할 것 같군요.","제가 당신이 해낸 일에 걸맞은, 성기사와 빛을 상징하는 무기를 제작해 드리죠. 하지만 먼저 필요한 재료들을 가져오셔야 합니다. 벌써 다 적어 놓았습니다. 이제 이 물건들을 가져올 만큼 용감하고 솜씨 있는 친구만 있으면 되는데, 한번 해보시겠어요?"}, {"조던의 쪽지를 참고해 흰돌참나무 재목, 조던의 제련된 광석 상자, 조던의 대장장이 망치, 그리고 코르석을 찾아 아이언포지에 있는 조던 스틸웰에게 가져가야 합니다."}},
[1655] = {"조던의 광석 상자", {"으앗! 놀래라! 지금 한참 생각에 몰두 중인 게 안 보이는 거요?","뭐라고? 아이언포지의 조던이 주문한 물건이라... 아아, 맞아! 뭘 얘기하는 건지 알겠소. 모그로쉬 오우거들... 바로 그 녀석들이 조던 그 친구가 주문한 물건을 빼앗아 가 버렸소. 저습지에서 이곳으로 물품을 운송해 오던 짐마차가 습격을 당했다오. 겨우 살아서 빠져나온 경비병은 사제들이 때맞춰 오지 못해 며칠 전에 숨을 거두었소이다.","그 물건들을 찾으려면 대호수 건너편 북동쪽으로 보시오. 그곳 어딘가에 분명히 있을 거요."}, {"모단 호수에 있는 베일러 스톤핸드에게 조던의 광석 상자를 가져가야 합니다."}},
[1656] = {"완수하지 못한 임무", {"여행자분, 절 좀 도와주실 수 있으신가요? 제발, 간곡히 부탁드려요. 지금 힘의 의식을 수행하는 중인데 아직 이곳 고원에서 임무를 마치지 못했답니다. 그런데 아버지께서 제게 이 모피를 블러드후프에 있는 여관에 가져다주라고 하셨지 뭐예요.","지금 이렇게 지친 몸으로는 의식도 마치고 아버지의 심부름까지 하기엔 역부족이랍니다. 제 대신 모피를 전해주시겠어요?","그 여관은 아주 편안한 곳이라서 오랜 여행 중에 휴식을 취하기엔 그만이랍니다. 여관에 가 보시면 분명 그렇게 느끼실 거예요."}, {"블러드후프 마을에 있는 여관주인 카우스에게 모피 묶음을 가져가야 합니다."}},
[1657] = {"남녘해안에 구린내 폭탄 투하", {"남녘해안에 있는 적에게 슬픔을 안겨 준다면 훨씬 더 의미 있는 할로윈 축제가 될 거야!","여기 특수 제작된 구린내 폭탄을 하나 골라 보게. 이 안에는 인간은 물론 의지가 약한 생물은 견딜 수 없을 정도로 고약한 악취가 가득 차 있지. 확실한 효과를 얻으려면 구린내 폭탄을 남녘해안 한가운데 던져야 해. 그러니까 얼라이언스와의 전투에 대비해 두게!","이것은 우리의 해방을 축하하는 좋은 선물이 될 것이네. 일을 마치면 내게 돌아오게나!"}, {"언덕마루에 있는 남녘해안 한가운데에 3개의 포세이큰 구린내 폭탄을 던진 후, 티리스팔 숲의 밀짚인형 축제가 열리는 곳에 있는 암흑의 소환사 얀카에게 가야 합니다."}},
[1658] = {"밀짚인형 축제 정탐", {"티리스팔 숲으로 가서 밀짚인형 축제에 대한 정보를 수집해 올 용감한 정찰 요원이 필요하오. 솔직히 말하는데 그곳으로 가는 길은 무척이나 위험하다오. 그리고 그곳 경비병들은 아주 사악한 놈들이지. 하지만 올해 밀짚인형 축제의 규모가 어느 정도이고, 또 할로윈 축제 동안 포세이큰이 어떤 계략을 꾸미고 있는지도 꼭 알아내야 하오.","티리스팔로 가서 밀짚인형 축제를 정탐해 보겠소? 성공하고 돌아오면 내 후하게 보상하겠소."}, {"티리스팔 숲에서 포세이큰의 밀짚인형 축제가 열리고 있는 곳을 찾아 정탐한 후 남녘해안에 있는 하사관 허트만에게 가야 합니다."}},
[1661] = {"고결함의 고서", {"다시 만나게 되는군. 자네를 <형제/자매>라고 부를 수 있다는 사실이 그 어느 때보다도 자랑스럽네. 자네의 헌신은 우리 모두에게 큰 힘이라네.","이제 자네에게 큰 선물을 줄 때가 되었군. 하지만 이것만은 알아두게나. 지금은 이걸 아무런 대가 없이 주지만 후에 때가 되면 내 임무를 수행하기로 결정한 모든 성기사들에게 훨씬 더 큰 도전이 될 걸세.","다시 날 찾아오게나. 그러면 아주 큰 보답을 해주겠네."}, {"스톰윈드에 있는 두소리안 랄과 대화해야 합니다."}},
[1665] = {"바틀비의 술잔", {"알았다고. 여기 있소. 잠깐! 그 안에 든 술이나 마저 마시고... 자, 이제 가져가쇼."}, {"벌가드에게 바틀비의 술잔을 가져가야 합니다."}},
[1666] = {"치안대장 해거드", {"어디 보자, 기술은 익혔으니 이제 쓸만한 무기가 필요하겠군그래. 치안대장 해거드를 찾아가면 분명히 자네를 도와줄 걸세.","치안대장 해거드는 엘윈 숲에 있는 동쪽계곡 벌목지의 저택에 살고 있다네. 지금은 은퇴했지만 현역에 복무하는 동안 많은 장비를 수집했지.","가서 내가 보내더라고 말하면 자네에게 알맞은 무기를 줄 거야."}, {"치안대장 해거드와 대화해야 합니다."}},
[1667] = {"썩은니 잭", {"벌가드가 보냈단 말이지? 그래, 여분의 무기가 좀 있네만, 먼저 자네에게 맡길 임무가 있다네...","얼마 전에 썩은니 잭이라는 데피아즈단의 악당이 날 속였어. 어두운데다가 스톰윈드 휘장을 걸쳤기에 치안대장 더그한인 줄 알았지. 내 스톰윈드 치안대장 휘장을 빌려달라고 해서 그 녀석에게 줬다네... 그랬더니 녀석이 날 비웃고는 달아나 버렸지 뭔가!","내 휘장을 되찾아 주게! 틀림없이 썩은니 잭이 남쪽에 있는 자신의 야영지에 숨겨 놓았을 걸세."}, {"썩은니 잭의 야영지에서 치안대장 해거드의 휘장을 찾아가야 합니다."}},
[1678] = {"베즈렉", {"전사로서 신분을 높이려면 강해져야 하네. 새로운 기술을 가르쳐 주기 전에 자네가 정말 강한 적들과 싸울 준비가 되어 있다는 것을 보여 주게나.","이 근방에서 제일 강한 트롤인 베즈렉을 꺾고 돌아오게. 그는 서리갈기 소굴의 바로 남쪽 구릉지에 있는 오두막에 있네.","베즈렉을 처치하고 그 증거를 가져오면 자네의 배짱을 알게 되겠지."}, {"베즈렉을 처치하고 그 증거로 그의 머리카락을 아이언포지에 있는 무렌 스톰파이크에게 가져가야 합니다."}},
[1679] = {"무렌 스톰파이크", {"안녕하신가, 전사 친구! 재능이 많아 보이는 전사로군. 하지만 아직 배워야 할 것이 많으니 무렌 스톰파이크라는 드워프에게 배움을 청하는 게 제일 좋을 거야.","무렌은 아이언포지의 군사 지구에 있는 전투의 전당으로 가면 만날 수 있지. 전사로서의 길이 어떤 것인지 배울 준비가 되었다면 그를 만나보도록 하라고."}, {"무렌 스톰파이크와 대화하십시오."}},
[1680] = {"토르무스 딥포지", {"이제 몇 가지 기술을 배웠으니 쓸만한 무기를 한번 찾아보게. 토르무스 딥포지를 찾아가면 분명 자네가 좋아할 만한 무기를 만들어 줄 수 있을 걸세.","토르무스는 이곳 아이언포지의 대용광로에 있네."}, {"토르무스 딥포지와 대화하십시오."}},
[1681] = {"아이언밴드 야영지", {"내 스승이신 스투르기 아이언밴드께서는 던 모로 동부, 투구바닥 호수 남쪽에 야영지를 하나 갖고 계셨지. 스승께선 그곳에서 제철공학을 가르치셨는데 그 분야에선 최고셨소.","그러던 어느 날, 제자들 중 하나가 이상한 광석을 가져왔소. 아이언밴드 선생님께서는 그것을 암영 광석이라 부르셨는데, 그 광석을 무척 연구하고 싶어하셨지. 그런데 그 광석을 가져온 지 얼마되지 않아... 검은무쇠 드워프 패거리들이 나타나 야영지를 공격하여 모두 죽여 버렸소!","아이언밴드 야영지로 가시오. 가서 내 스승님의 복수를 해 주시오. 그리고 이 비극의 원인이었던 암영 광석을 가져와 주시오."}, {"아이언포지에 있는 토르무스 딥포지에게 암영 광석을 가져가야 합니다."}},
[1682] = {"움브랄 광석 무기", nil, nil},
[1683] = {"볼루스 바일후프", {"먼저 내게 당신의 결의를 확인해 보고나서 훈련을 시작하겠어요.","볼루스 바일후프라는 사티로스가 바네실 지하굴에서 남동쪽으로 멀리 떨어진 우리 달샘 하나를 찾았어요. 지금 그 안에서 텀벙거리며 달샘의 순수한 물을 더럽히고 있죠.","달샘에서 볼루스를 찾아 처치하세요. 증거로 그의 뿔을 내게 가져오면 전사 상급 훈련을 시작하도록 하겠어요.","달샘으로 가는 길은 찾기 어렵지만, 한눈 팔지 않고 잘 살피면 찾을 수 있을 거예요."}, {"다르나서스에 있는 엘라나리아에게 볼루스의 뿔을 가져가야 합니다."}},
[1684] = {"엘라나리아", {"전사여, 당신의 기술은 점점 늘어가지만 전사의 길은 생각만큼 단순한 게 아닙니다. 이제 스승을 찾아 기술을 더 향상시켜야 할 때입니다.","다르나서스의 전사의 정원에 있는 엘라나리아라는 전사를 찾아가 보십시오. 당신에게 좋은 스승이 될 테니..."}, {"엘라나리아와 대화하십시오."}},
[1685] = {"게이킨의 부름", {"어떻게 항상 이런 식이지? 내가 하루종일 당신을 찾아 다닐때는 코빼기도 보이지 않더니, 이젠 포기하고 있으니 이렇게 불쑥 나타난단 말이야.","당신 때문에 시간 낭비는 충분히 했으니 바로 본론으로 들어갑시다. 게이킨님께서 추가 훈련을 위해 당신을 찾고 계시오. 내 생각엔 당장 찾아가 뵙는 것이 좋을 것 같소.","그 분은 어둠의 희생양 선술집 지하실에 계실 것이오. 가면서 사람들 눈에 띄지 않도록 언행을 조심하는 게 좋을 거요. 위치가 탄로나거나 소문이 나면 곤란하니까 말이오."}, {"스톰윈드의 마법사 지구에 있는 대흑마법사 게이킨에게 보고해야 합니다."}},
[1686] = {"엘룰라의 유령", {"이제 무기를 하나 만들기로 해요.","전사를 위한 무기를 말이에요. 그러기 위해서는 먼저 어둠해안으로 가야 해요. 오래전, 아우버다인 등대 근처에서 난파된 배 한 척에서 엘루니트 광석이 든 수많은 상자가 바다 밑으로 가라앉았죠.","엘루니트의 수호자인 엘루라의 유령을 물리쳐야 해요. 그는 원래 그 배의 선장이었지만 자신의 뱃짐을 지키기 위해 영원히 바다 깊은 곳을 누비도록 스스로 저주를 걸었죠. 그녀를 물리치고 그녀의 메달을 빼앗은 후 잃어버린 엘루니트 상자를 가져오세요."}, {"다르나서스에 있는 엘라나리아에게 엘루니트 광석 상자 8개와 엘루라의 메달을 가져가야 합니다."}},
[1687] = {nil, nil, nil},
[1688] = {"슈레나 캘리돈", {"내겐 슈레나 캘리돈이라는 이름의 제자가 있었네. 바로 지금 자네가 서 있는 자리에 서 있었지. 흑마법을 간절히 배우고 싶어하는데다가 마법적인 재능도 보통이 아니었어. 게다가 젊고 인물도 아주 빼어난 녀석이었지. 몹쓸 녀석 같으니! 내가 그때 알아봤어야 했는데...","그 도둑 같은 제자 녀석이 엘런 드러지무어라는 녀석과 달아나 버렸네. 그 녀석을 잃은 건 그리 중요하지 않지만 내가 그 녀석에게 소중한 혈석 목장식을 선물로 줘 버렸었네.","그 녀석은 브랙웰 호박밭에 있을 걸세. 그 녀석의 목숨이야 이제는 내 알 바 아니니 내 물건이나 좀 되찾아 주게나."}, {"슈레나의 목장식을 찾아 스톰윈드에 있는 대흑마법사 게이킨에게 가져가야 합니다."}},
[1689] = {"결속", {"슈레나를 물리치고 내 목걸이를 찾아왔으니 자네가 그녀만큼은 재능이 있다는 것을 증명해 보였군.","그렇다고 해도 자네는 아직 많은 수련이 필요해. 마법의 흐름은 다룰 줄 아는 것만으로 진정한 흑마법사가 되는 것은 무리가 있네.","내 보이드워커를 다루는 법을 보여 주도록 하지. 그 전에 소환 마법진에서 보이드워커를 불러내 물리적이든 마법적이든 자네가 갖고 있는 무기를 써서 제압해야만 하네. 아래층에 있는 소환 마법진으로 가서 이 목걸이의 마법을 사용하도록 하게."}, {"혈석 목걸이를 사용하여 보이드워커를 소환해 제압한 후, 스톰윈드에 있는 대흑마법사 게이킨에게 혈석 목걸이를 돌려주어야 합니다."}},
[1690] = {"사막유랑단 처단", {"이보시오, <race>! 당신이 가젯잔 수자원개발회사에 큰 도움을 줄 때가 됐소.","타나리스 사막을 떠돌아다니는 사막유랑단 녀석들이 우물을 거의 다 빼앗아 버렸소! 우물을 사용하지 못한다면 우리의 수자원 공급은 곧 모두 바닥나 버릴 거요. 우리와 돈독한 관계를 맺고 싶다면 그 유목민 녀석들에게 정의의 처단을 내리시오!","여기서 동쪽으로 가서 10명의 사막유랑단 약탈자와 좀도둑을 처치한 다음 즉시 내게 돌아와 보고해 주시오. 이곳 사람들... 아니, 고블린들의 정의를 위해서 어서 서두르시오!"}, {"가젯잔의 동쪽에서 10명의 사막유랑단 약탈자와 좀도둑을 처치한 후, 선임기술자 빌지위즐에게 돌아와 보고해야 합니다."}},
[1691] = {"사막유랑단 추가 처단", {"이제 당신은 그 사막의 떠돌이 중에서도 좀 더 강한 녀석들을 상대해야 하오. 지금까지도 큰 도움이 됐지만 좀 더 합리적인 전략을 세웠다오. 가능한 적은 수를 처치해서 우물을 되찾을 수 있다면 좋지 않겠소!","그러니 이번에는 사막유랑단 도적, 암살자, 어둠마법사를 처치해 주시오. 여기서 동쪽과 남동쪽으로 가면 녀석들을 찾을 수 있을 거요.","녀석들은 아주 지독한 놈들이오. 괜한 허풍이 아니오. 이 일만 잘해주면 애초부터 우리 소유였던 자원을 확실히 되찾을 수 있을 거요!"}, {"가젯잔에 있는 선임기술자 빌지위즐을 위해 사막유랑단 도적 8명, 사막유랑단 암살자 6명, 사막유랑단 어둠마법사 10명을 처치해야 합니다."}},
[1692] = {"대장장이 메시엘", {"당신이 가져온 엘루니트 광석을 상자에 넣어 놓았어요. 이 상자를 갖고 대장장이 메시엘을 찾아가면 엘루니트로 무기를 하나 만들어 줄 거예요.","메시엘은 여기서 남쪽, 이 건물의 반대편에 있어요."}, {"엘루니트 상자를 대장장이 메시엘에게 가져가야 합니다."}},
[1693] = {"엘루니트의 무기", nil, nil},
[1698] = {"요루스 발리브루", {"요루스 발리브루가 얼라이언스의 우수한 전사들에게 도전해 오고 있소. 그는 붉은마루 산맥에 있는 호숫골 여관에서 기다리고 있는데, 자신의 시험을 통과하는 자에게는 후하게 보상할 것이라 공언하고 있소."}, {"요루스 발리브루와 대화해야 합니다."}},
[1699] = {"레스밴 도전", {"이 과제는 레스밴 도전이라고 하네. 도전에 성공하려면 레스밴 동굴의 첫번째 분기점까지 들어갔다가 되돌아오기만 하면 되네. 아주 간단하지 않나! 단, 몇 가지 규칙이 있네.","레스밴 동굴로 가는 길에 널린 붉은마루일족 놀들을 뚫고 여기로 1시간 내에 되돌아와야만 하네. 또 시작하기 전에 우리 가문 특유의 술인 발리브루 스칼더 맥주도 한잔 쭉 들이켜야하지!","어디 한번 해보겠나? 레스밴 동굴은 레이크샤이어 북쪽의 구릉지대에 있네. 자, 준비됐나?"}, {"주어진 시간 내에 레스밴 동굴의 첫번째 분기점까지 들어갔다가 요루스 발리브루에게로 되돌아와야 합니다.","죽어서 영혼이 빠져나가서는 안 됩니다."}},
[1700] = {"그리만트 엘모어", {"자네에게 줄 갑옷을 만들면서 터득한 요령을 적어 놓았는데, 이 쪽지를 그리만트 엘모어에게 갖다주었으면 하네. 그는 이곳 드워프 지구에 있는 무기 상점에 있을 걸세.","이 쪽지를 전해 주고는 재빨리 피하게. 그는 자기 실력에 대한 자부심이 대단해서 나한테서 뭔가를 배운다는 걸 아주 못마땅해 할 거야..."}, {"그리만트 엘모어에게 푸렌의 쪽지를 가져가야 합니다."}},
[1701] = {"불에 달궈 만든 갑옷", {"내 자네에게 좋은 방패를 주었네만 여기서 그칠 수는 없지. 내가 굉장히 단단한 쇠사슬 갑옷을 만드는 비기를 알고 있다네. 아주 희귀한 재료가 필요하고 만들기도 쉽지 않지만 다리품을 좀 팔아도 괜찮다면 내가 하나 만들어 주도록 하지.","어떤가? 생각이 있다면 어디로 가서 뭘 구해 와야 하는지 적은 목록을 주겠네."}, {"스톰윈드에 있는 푸렌 롱비어드에게 그가 필요로 하는 재료들을 모아서 가져가야 합니다."}},
[1702] = {"방패 상인", {"자네는 레스밴 도전을 치르면서 실력을 제대로 보여 주었네. 나는 약속을 어기는 일이 없는 정직한 드워프이니 약속한 대로 보상을 주도록 하지. 그런데 그게... 스톰윈드에 있단 말이야.","스톰윈드의 드워프 지구에 있는 내 친구 푸렌 롱비어드를 찾아가 보시게. 둘째 가라면 서러워할 방어구 제작자인데, 나한테 큰 빚을 진 적이 있지.","자, 푸렌에게 이 발리브루 스칼더 맥주통을 가져가서 방패를 하나 달라고 해보시오. 아주 괜찮은 놈으로 골라 줄 테니까 말이야."}, {"푸렌 롱비어드에게 스칼더 술통을 가져가야 합니다."}},
[1703] = {"메시엘", {"자네에게 줄 갑옷을 만들면서 터득한 요령을 적어 놓았는데 이 쪽지를 대장장이 메시엘에게 갖다주게나. 자네가 멀리 다르나서스까지 돌아가야 하겠지만 내가 터득한 걸 알게 되면 분명히 아주 고마워할 걸세."}, {"메시엘에게 푸렌의 쪽지를 가져가야 합니다."}},
[1704] = {"클락몰트 스패너스판", {"자네에게 줄 갑옷을 만들면서 터득한 요령을 적어 놓았는데, 이 쪽지를 클락몰트 스패너스판에게 갖다 줄 사람이 필요하네. 아이언포지에 사는 노움 대장장이인데 내가 이번에 터득한 요령을 가르쳐 주면... 분명 흥분해서 자기 수염을 쥐어뜯을 거야."}, {"클락몰트 스패너스판에게 푸렌의 쪽지를 가져가야 합니다."}},
[1705] = {"불타는 혈액", {"인정하고 싶진 않지만, 정말로 푸렌이 쓸만한 기술을 발견한 모양이군. 하지만 확실하게 확인해 보기 위해 내 방어구에다가 푸렌이 고안한 기술을 써 보도록 하지. 재료를 모으는 것을 도와주면 내 방어구를 가져가도록 해 주겠소.","갑옷을 만들려면 약간의 불타는 혈액과 불타는 보석이 필요하오.","내가 알고 있기로는, 그것들을 찾을 수 있는 곳은 그늘숲 밖에 없소. 롤랜드 광산에 사는 파멸의어둠일족 늑대인간들이 불타는 혈액을, 그들의 두목인 거트스필이 불타는 보석을 가지고 있을 거요.","행운이 있기를 빌겠소."}, {"스톰윈드에 있는 그리만트 엘모어에게 불타는 혈액 20개와 불타는 보석 1개를 가져가야 합니다."}},
[1706] = {"그리먼트의 갑옷", nil, nil},
[1707] = {"물주머니 현상금", {"반갑소. 내 이름은 루글룬켓이오. 관심이 있다면 중요한 임무를 하나 맡기고 싶소! 하하!","사막에서 사막유랑단 물주머니를 구해준다면 숙달된 양수기기사로서 그보다 더 기쁜 일은 없지. 그 물은 원래 우리 소유였으니까. 그 유목민 녀석들이 훔쳐갔을 뿐이오! 5개씩 모아올 때마다 가젯잔 수자원개발회사가 선물세트를 하나 증정할 거요. 물론 제일 중요한 보상은 당신이 가젯잔 고블린들을 도왔다는 사실 그 자체라오!"}, {"가젯잔에 있는 양수기기사 루글룬켓에게 사막유랑단 물주머니 5개를 가져가야 합니다."}},
[1708] = {"철산호", {"푸렌 롱비어드의 새 기술을 내가 만들 방어구에 써 보고 싶어요. 당신이 푸렌을 도와줘서 푸렌이 새 기술을 알려준 거니까, 방어구가 완성되면 가져가도 좋아요!","작업을 마치려면 철산호라는 특수한 산호가 필요해요. 이 산호는 저습지의 메네실 항구 남쪽 해안가에서만 자란답니다","이 철산호를 충분히 가져오면 즉시 방어구를 완성할 테니 서둘러 주세요. 지금 내 발가락이 다 꼬이고 있는 거 보이세요? 어서 작업에 착수하고 싶어요!"}, {"아이언포지에 있는 클락몰트 스패너스판에게 철산호 20개를 가져가야 합니다."}},
[1709] = {"클락몰트의 창작품", nil, nil},
[1710] = {"바짝 마른 알껍질", {"내 동료 푸렌 롱비어드가 발견한 걸 어서 시험해 보고 싶소. 지금 연구 중인 방어구에 쓰면 좋은 결과가 나올 거요.","좀 도와주지 않겠소? 필요한 재료를 모아 주면 방어구 하나를 만들어 드리리다.","버섯구름 봉우리의 마루둥지에서 서식하는 마루둥지 와이번의 바짝 마른 알껍질이 필요하오. 둥지 아래에서 알껍질을 찾아서 내게 가져다주오."}, {"다르나서스에 있는 메시엘에게 바짝 마른 알껍질 20개를 가져가야 합니다."}},
[1711] = {"메시엘의 방어구", nil, nil},
[1712] = {"사이클로니안", {"나는 이곳에서 일어나는 일들을 지켜보며 오랜 세월을 보냈다네. 이러다 보면 언젠가 거대한 사이클로니안이 쓰러지는 것을 볼 수 있을 테니까 말이야. 하지만 아직도 그 바람의 군주를 쓰러뜨릴 만한 강자를 찾지 못했다네. 그 강자가 내 앞에 서 있는 바로 자네일지도 모르겠군.","나에게 자네의 능력을 보여주게나. 사이클로니안을 소환하는 데 필요한 물건들을 모아 오게. 그러면 자네가 훌륭한 전사라는 것을 알게 되겠지. 그러다가 혹시 사이클로니안을 쓰러뜨리고 그의 소용돌이 심장을 얻을 수 있을지도 모르는 일 아니겠나.","그렇게만 된다면 그 심장의 힘을 불어넣은 무기를 자네에게 주도록 하지.","여기 이 양피지에 필요한 물건의 목록을 적어 두었네."}, {"알터랙에 있는 바람의감시자 바스라에게 바스라의 양피지에 적혀 있는 물건들을 가져가야 합니다."}},
[1713] = {"소환", {"이제 사이클로니안을 소환하는 의식을 행할 준비가 되었네. 소환이 되면 녀석을 물리치고 녀석의 소용돌이의 눈을 꺼내야 하네. 그것으로 무기에 사이클로니안의 힘을 넣을 수 있을 걸세.","바람의 군주에게 맞설 준비가 되었는가?"}, {"바람의감시자 바스라에게 소용돌이의 눈을 가져가야 합니다."}},
[1714] = {"떠도는 정령의 정수", nil, nil},
[1715] = {"어둠의 희생양", {"흠... 그 옛날에 카즈 모단의 모든 흑마법사들은 우리의 놈리건에서 훈련을 받았었죠. 그러나 이젠 아이언포지의 이 쥐구멍 같은 곳으로 밀려나 마그니의 감시를 받는 신세니, 신참들을 훈련시키려면 스톰윈드로 보내는 수밖에 없어요.","다행히도 게이킨님은 유능하신 분이라 당신을 잘 가르쳐 줄 거요. 하지만 생각할수록 억울하군요!","어쨌거나, 스톰윈드로 가서 어둠의 희생양이라는 선술집의 지하실에 가서 게이킨님을 만나보도록 하세요."}, {"스톰윈드의 마법사 지구에 있는 대흑마법사 게이킨에게 보고하십시오."}},
[1716] = {"영혼을 삼키는 자", {"서큐버스는 영혼에 굶주린 자이자 마음의 파괴자이며 정신의 지배자라네. 악으로 가득 찬 피조물이지.","이제 자네가 배우게 될 것이 바로 이 서큐버스를 소환하고 부리는 것일세. 하지만 이 피조물을 불러들이려면 자극제가 필요할게야.","사랑의 징표를 미끼로 사용한다면 분명히 자네의 부름에 응할 걸세. 그러고 나서 녀석을 제압하여 자네의 것으로 만들게나.","하지만 이런 성질을 지닌 자극제는 찾기가 어려울 거야. 칼림도어에 타우라조 야영지 근처에 불모의 땅에 있는 현자 타카르라면 조언을 해줄 수 있을걸세."}, {"불모의 땅에 있는 현자 타카르와 대화해야 합니다."}},
[1717] = {"게이킨의 부름", {"오 <class>|1이로군;로군;! 이곳에서 당신을 보게 될 줄은 정말 몰랐는데... 어쨌거나 난 당신을 찾아오라는 명을 받았소.","수련을 쌓아야 할 때가 지났으니 스톰윈드에 들러 게이킨님을 찾아가 보시오. 당신을 기다리고 계실 거요."}, {"스톰윈드의 마법사 지구에 있는 대흑마법사 게이킨에게 보고하십시오."}},
[1718] = {"섬 사람", {"전사로서의 명성이 점점 커지고 있군요. 이제는 당신의 동료전사들과 실력을 겨루어 볼 때가 되었습니다.","톱니항의 남쪽, 불모의 땅 해안에서 조금 떨어진 곳에 섬이 하나 있습니다. 그곳을 격투의 섬이라고 하는데, 전사들이 만나 서로 실력을 겨루는 곳이지요.","격투의 섬의 교관인 클라녹 맥클라우드와 대화를 나눠 보세요. 클라녹이 당신에게 자격이 있다고 판단하면 더 강한 전사의 길로 인도해 줄 것입니다.","격투의 섬으로 가려면 무역 해안을 따라 얕은 모래 언덕을 찾아 따라가 보도록 하세요."}, {"클라녹 맥클라우드와 대화해야 합니다."}},
[1719] = {"격투장", {"나에게서 광전사로서의 자세를 배우고 싶다면 먼저 격투에서 이겨야만 하오. 이곳 격투의 섬은 자신이 강하다고 생각하는 유망한 전사들이 모여 있는 곳이오. 격투에서 승리하게 되면 만천하에 자신의 강함을 증명해 보이게 되는 것이지.","격투를 시작하려면 저기 모인 관중 중앙의 격투장 위에 서시오. 그러면 트위기 플랫헤드가 당신이 준비된 것으로 알고 도전자들을 내보낼 거요.","도전자들을 모두 이기면 싸움꾼 윌이 나올 테니 그를 꺾고, 주어진 시간 안에 내게 돌아오시오."}, {"싸움꾼 윌을 꺾고 격투의 섬에 있는 클라녹 맥클라우드과 대화하십시오."}},
[1738] = {"사랑나무", {"당신이 올 것을 예감하고 나는 투시력을 사용하여 시간의 안개를 뚫고 세상을 살펴보았소. 그래서 당신이 찾고 있는 게 무엇인지 알아냈지.","고대의 전쟁 당시 서로 사랑하던 두 연인이 전투 중 함께 목숨을 잃고 말았소. 그리고 그들이 숨을 거둔 곳에서 불멸의 사랑을 상징하는 사랑나무가 자라났소.","그거라면 서큐버스 하나 정도는 충분히 꾀어낼 수 있을 거요. 사랑나무는 잿빛 골짜기 숲의 북서쪽에 있는 오르딜아란의 폐허에서 찾을 수 있을 거요."}, {"잿빛 골짜기에서 사랑나무를 찾아 스톰윈드의 마법사 지구에 있는 대흑마법사 게이킨에게 가져가야 합니다."}},
[1739] = {"결속", {"이 사랑나무 줄기를 사용해 아래층에 있는 소환 마법진에서 서큐버스를 불러올 문을 여는 마법을 시전할 수 있을 걸세.","조심하게나. 서큐버스들은 정신력이 아무리 강한 사람이라도 쉽게 압도해 버릴 만한 능력이 있으니 말이야.","서큐버스를 이겨 황천으로 돌려보낸 후 다시 나에게 돌아오면 서큐버스를 부리는 방법을 가르쳐 주도록 하지."}, {"사랑나무 줄기를 사용하여 서큐버스를 소환해 제압한 후, 어둠의 희생양 선술집에 있는 대흑마법사 게이킨에게 사랑나무 줄기를 가져가야 합니다."}},
[1740] = {"소랜루크 수정구", {"전투 중 불타는 군단 악마들은 생명이 소실되었고 쓸모없어진 그들의 영혼은 수정구 안에 갇히게 됐어요. 그리고 군단 내 마법사와 흑마법사들의 손에 넘겨졌지요. 아제로스에 있는 불타는 군단의 수하들이 이 수정구들을 지니고 다녔는데, 수정구가 산산조각으로 깨져도 그 안에 담긴 악마의 힘은 그대로 유지되었답니다.","소랜루크 수정구는 두 조각으로 깨졌는데 그중 한 조각은 더 잘게 부서져 검은심연 나락에 있는 황혼의 망치단 수행사제들이 쓰고 있지요. 그리고 좀 더 큰 나머지 조각은 그림자송곳니 성채에 있는 마술사가 갖고 있습니다. 그 조각들을 찾아서 가져오면 다시 원래 모양으로 만들어 보겠습니다."}, {"소랜루크 조각 3개와 큰 소랜루크 조각 1개를 찾아 불모의 땅에 있는 도안 카르한에게 가져가야 합니다."}},
[1758] = {"비밀의 고서", {"지옥사냥개를 소환하려면 단순한 소환 마법진으로는 어림도 없소.","나와 나의 수행사제 세 명이 함께 마법진 만드는 것을 도와줄 거요. 음... 그러려면 비밀의 고서가 필요한데 잃어버린 지가 꽤 오래된 물건이지. 그 책에는 누구의 도움도 받지 않고 처음으로 지옥사냥개를 소환해낸 흑마법사들의 기록이 들어있소.","크롬 스타우트암이 당신을 도와줄 수 있을지도 모르겠군. 요즘은 여기저기 유랑을 하며 고대 서적들을 찾고 있다던데... 사람들의 눈을 피해 아이언포지의 도서관에서 나타난다고도 하오."}, {"아이언포지에 있는 크롬 스타우트암과 대화해야 합니다."}},
[1778] = {"신앙의 고서", {"어서 고향으로 돌아갈 준비를 해야겠네요. 제 아내 스테파니가 스톰윈드에서 잘 지내고 있었으면 좋겠어요. 아, 어서 빨리 보고 싶어요. 그럼, 다음에 또 만납시다."}, {"아이언포지에 있는 타이자 배틀포지와 대화하십시오."}},
[1779] = {"신앙의 고서", {"자, 받으세요. 생명의 징표라고 하는 물건입니다.","이제까지 당신은 자비로운 마음과 굳센 인내심을 보여주었으니 이제 전투에서 버틸 힘이 얼마나 강해졌는지 증명해 보여야 해요.","제 남편 뮈레돈이 최근 검은무쇠단 드워프들과 마주친 일이 있었는데 겨우 목숨만 건져서 도망쳐 왔답니다. 저쪽에서 사제에게 치료를 받고 있지요.","이 생명의 징표를 가지고 가서 남편과 얘기해 보세요. 남편을 도와줄 수 있다면 곧 다시 얘기하도록 하죠. 빛의 서광이 언제나 당신께 환하게 비치기를..."}, {"생명의 징표를 가지고 가서 아이언포지에 있는 뮈레돈 배틀포지와 대화하십시오."}},
[1780] = {"신앙의 고서", {"오늘 당장 아이들에게 옷을 만들어줘야겠어요. 친절하신 <class>님, 살펴가세요. 이 삭막한 도시에서 당신처럼 인품이 좋은 분을 만났다는 건 행운입니다. 당신은 이곳 스톰윈드의 성기사들이 도움이 필요한 사람들에게 등을 돌리지 않는다는 것을 보여주셨어요.","안녕히 가세요."}, {"스톰윈드에 있는 두소리안 랄과 대화하십시오."}},
[1781] = {"신앙의 고서", {"자, 받게나. 생명의 징표라고 하는 물건이네.","자네가 자비롭고 인내심이 강하다는 것을 증명하였으니, 이제는 자네가 빛의 정의로움이 선사하는 가장 큰 선물 중 하나에 힘을 집중할 수 있는지를 시험해 볼 때가 되었네.","내 친구 가쟁이 최근 수호영웅의 안식처 근방에 있는 엘윈 숲 정찰을 나갔다가 데피아즈단과 맞닥뜨렸다네. 그는 겨우 목숨만 건져서 돌아왔지. 저 위 제단 옆에서 우리 사제가 상처를 치료해 주고 있네.","생명의 징표를 가지고 가서 그와 얘기를 나누어 보게. 그를 도와줄 수 있으면 다시 또 얘기하도록 하지. 빛의 서광이 자네를 비추기를..."}, {"스톰윈드에 있는 가쟁 테놈에게 생명의 징표를 가져가야 합니다."}},
[1782] = {"푸렌의 갑옷", nil, nil},
[1783] = {"신앙의 고서", {"우리는 투구바닥 호수 남쪽에 숨어 있는 검은무쇠단 드워프들을 감시하고 있었네. 아무래도 문제가 있는 듯해 나는 나름과 함께 더 가까이 가보기로 했지. 두목 녀석이 부하들에게 명령을 내리는 것이 보이더군.","우리 둘이서 그 녀석들 몇 명을 해치우려고 했지만 생각보다 강했네. 지원군들이 나타나 우리를 덮치는 게 아닌가. 나름이 나더러 먼저 도망쳐서 지원을 요청하라고 했기에 여기 오게 된 것일세.","난 나름을 도와야 해. 그 친구가 무사히 돌아올 때까지 쉬지 않을 걸세.","날 도와줄 수 있겠나?"}, {"생명의 징표를 가져가서 던 모로에 있는 나름 폴크를 되살려야 합니다."}},
[1784] = {"신앙의 고서", {"전투에서 쓰러지기 전 뮈레돈과 나는 작은 집 근처에 있던 검은무쇠단 비밀 요원을 거의 해치웠었소. 두목이 그 자에게 명령을 내리는 것을 목격한 나는 그를 해치우고 문서를 낚아챌 생각이었지.","아이언포지의 일부 원로들은 아직 던 모로에 검은무쇠단 드워프가 있다는 사실을 믿지 않고 있소. 그 문서 중 하나만이라도 손에 넣을 수만 있다면, 그들의 동태에 대한 증거도 얻고 무슨 음모를 꾸미고 있는지도 알 수 있을 것이오.","문서를 빼내 뮈레돈에게 돌아가시오."}, {"투구바닥 호수 근처에 있는 검은무쇠단 드워프들에게서 검은무쇠단 문서를 빼앗아 아이언포지에 있는 뮈레돈 배틀포지에게 돌아가십시오."}},
[1785] = {"신앙의 고서", {"집사람이 벌써 기쁜 표정을 짓고 있군그래. 당신과 얘기를 나누고 싶어할 테니 얼른 가 보게.","빛의 가호를 비네, 강한 <class>|1이여;여;. 때가 되어 스컬지와 검은무쇠단 드워프들에게 맞서 싸우게 되면 내 꼭 전장에서 당신을 찾아보리다."}, {"아이언포지에 있는 타이자 배틀포지와 대화하십시오."}},
[1786] = {"신앙의 고서", {"나와 헨즈는 엘윈 북동부에 있는 돌무덤 호수의 한가운데에 위치한 섬인 수호영웅의 안식처까지 갔소. 데피아즈단이 우리 경계를 넘었다는 소식을 듣고 폐하의 참모들에게 가져갈 증거를 확보하고 싶었지.","문서를 훑어보고 있는 데피아즈단 놈 두어 명을 발견하고 우린 그들을 공격했소. 곧 데피아즈단의 지원병들과 마법사들이 나타나 우린 꼼짝없이 당하고 말았지.","헨즈는 내가 도망칠 수 있도록 뒤를 맡아 주었소... 무모한 짓을 하다니 바보 같은 친구.","헨즈, 그 친구는 아직 그곳에 있소. 우릴 도와주겠소?"}, {"생명의 징표를 가져가서 엘윈에 있는 헨즈 폴크를 되살려야 합니다."}},
[1787] = {"신앙의 고서", {"징표에 관한 이야기는 나중에 계속하기로 하고 이제 데피아즈단 녀석들이 읽고 있던 문서를 빼내는 것이 좋겠소. 문서를 빼낼 수 있다면 데피아즈단의 엘윈 침략을 증명할 수 있으며, 녀석들의 계략이 어떤 것인지도 파악할 수 있게 될 거요.","지금 내 상태로는 전투에 참여할 순 없을 거 같군. <형제/자매>가 이곳 섬에 있는 데피아즈단에게서 그 문서를 빼앗아 올 수 있겠소?","문서들을 손에 넣으면 가쟁에게 가지고 돌아가시오. 내가 무사하다는 소식과 함께 말이오."}, {"엘윈 숲에 있는 데피아즈단 견습마술사들에게서 데피아즈단 문서를 빼앗아 스톰윈드에 있는 가쟁 테놈에게 가져가야 합니다."}},
[1788] = {"신앙의 고서", {"이 문서는 두소리안에게 보여 준 후 폐하의 참모들에게 가져가겠소. 그동안 두소리안과 대화를 나누는 게 좋겠소. 그 친구는 분명 이번 일을 칭찬하며 자네 등을 두드려줄 거요!","다시 한번 고맙다는 말을 전하오이다. 다시 만날 기회가 있었으면 좋겠군. 당신은 정말 자랑스러운 나의 친구이자 동지요."}, {"스톰윈드에 있는 두소리안 랄과 대화하십시오."}},
[1789] = {"생명의 징표", nil, nil},
[1790] = {"생명의 징표", nil, nil},
[1791] = {"바람의감시자", {"이제 광폭 태세를 배웠으니 이에 걸맞은 괜찮은 무기가 있어야겠지.","알터랙 산맥과 서부 역병지대 사이에서 은둔하여 지내는 트롤인 바람의감시자 바스라를 찾아가시오. 그는 아주 솜씨 좋은 무기 제작자이자 강한 주술사지.","바스라를 찾으려면 타렌 제분소의 동쪽 강둑을 따라 흐르는 강으로 가면 되오. 바스라는 강을 따라 북쪽으로 가면 강의 동쪽 둑에 있는 폐허 근처에서 야영하고 있을 것이오."}, {"바람의감시자 바스라와 대화해야 합니다."}},
[1792] = {"소용돌이 무기", nil, nil},
[1793] = {"용맹의 고서", nil, nil},
[1794] = {"용맹의 고서", nil, nil},
[1795] = {"결속", {"준비되었으면 이제 시작하도록 할 테니 설명을 잘 듣도록 하시오.","먼저 내가 기본적인 소환 마법진을 만들겠소. 마법진이 완성되면 내 수행사제들이 정신 집중의 마법봉을 사용해 더 큰 마법진을 만들 것이오.","마법진이 구성되면 비밀의 고서에 쓰인 주문을 읽으시오. 그러면 지옥사냥개가 소환되어 나올 것이오. 지옥사냥개는 아주 강하니 조심하는 게 좋을 거요. 꼭 살아남길 바라오."}, {"비밀의 고서를 사용해 지옥사냥개를 소환해 제압한 다음 톱니항에 있는 스트라드 파선에게 사용한 고서를 돌려주어야 합니다."}},
[1796] = {"흑마법사의 로브", {"먼저 로브를 만들어 드릴 테니 조금 도움을 주셔야 해요. 당신이 필요한 물건들을 모아 오는 데 성공하면, 전투에서 당신을 더욱 강하게 만들어줄 첫 번째 물건을 얻을 수 있어요.","전에 아카나의 로브를 만들어 보았을 거예요. 재봉사가 대신 만들어 주었을 수도 있고, 다른 이에게서 구입할 수도 있겠지만 당신이 손수 만들 수 있다면 좋겠네요. 뭐, 어쨌든 당신이 그 로브를 하나 가져오면 다음 일을 계속하도록 하죠."}, {"불모의 땅에 있는 미네라 보이드렌더에게 아카나의 로브를 가져가야 합니다."}},
[1798] = {"스트라드를 찾아서", {"이제는 당신도 지옥사냥개 소환을 배울 때가 되었군. 이곳에는 당신을 가르치기 위해 필요한 물품이 없으니 당신이 우리 동지인 스트라드 파선을 찾아가야 할 것이야.","그는 톱니항의 고블린 항구에서 살고 있소. 미리 당신이 찾아갈 것이라고 연락을 취해 두도록 하겠소."}, {"불모의 땅에 있는 스트라드 파선과 대화해야 합니다."}},
[1799] = {"오라힐 보주의 파편", {"좋아요, 그럼 시작하죠.","먼저 저 안으로 들어가 내 조수들과 대화를 해보세요. 그들이 안내하는 길 중 하나를 선택해 따라가세요. 자세한 설명은 그들에게 들으시고요","그 후엔 잊혀진 땅으로 가서 만노로크 소굴의 흑마법사들 중 하나가 지니고 있을 지옥의 보주를 찾으세요. 그 보주는 먼지진흙 습지대 남부에 있는 마법사 타베사의 도움을 받아서 정화해야 합니다. 그녀는 나를 봐서라도 당신을 도와 줄 거예요.","다시 한 번 말하지만 내가 부탁한 일을 모두 처리하기 전엔 돌아올 생각을 하지 마세요."}, {"톱니항이 내려다보이는 탑 안에 살고 있는 미네라의 조수들과 대화하고 그들이 제시하는 길 중 하나를 따라가야 합니다.","그 후엔 지옥의 보주를 구해 먼지진흙 습지대의 타베사에게 가져가야 합니다."}},
[1800] = {nil, nil, nil},
[1801] = {"비밀의 고서", {"당신이 맞설 지옥사냥개를 소환하려면 단순한 소환 마법진으로는 안 되오.","세 명의 수행사제와 내가 마법진 만드는 것을 도와줄 것이오. 음, 비밀의 고서가 필요한데 잃어버린 지가 꽤 된 물건이지. 아무런 도움도 없이 최초로 지옥사냥개를 소환해낸 흑마법사들이 쓴 책이라오.","조라 애니슨이 당신을 도와줄 수 있을지도 모르겠군. 요즘은 언더시티의 지도 제작자로 고용되어 일을 하고 있다지만 고대 서적에 대해서는 나보다 더 잘 안다오."}, {"언더시티에 있는 조라 애니슨과 대화해야 합니다."}},
[1802] = {"비밀의 고서", {"잃어버리긴 했지만 어디서 찾을 수 있을지는 대충 알고 있지.","그 책은 딱 한 권밖에 만들지 않았는데 언젠가 한 부분이 크게 찢겨 나가 버렸네. 소문에 의하면 어쩌다 보니 버섯구름 봉우리에 사는 켄타우로스의 손에 떨어졌다는군.","나머지 부분은 남녘해안의 서부 해안을 떠나 메네실 항구까지 배로 운송하기로 되어 있었는데 아직도 배가 도착하지 않았다고 하네.","알려준 대가로 청이 하나 있는데 그걸 찾게 되거든 내게 가져와서 한 번만 보여주게나."}, {"아이언포지에 있는 크롬 스타우트암을 위해 곰팡이 슨 고서와 너덜너덜한 필사본을 모아야 합니다."}},
[1803] = {"비밀의 고서", {"아! 당신도 나랑 취향이 비슷한 흑마법사인 모양이군. 비밀의 고서를 입수하는 것은 참으로 당찬 야심이오. 그 고서가 한때는 로데론 내에 보관되어 있었다는 건 알고 있소? 하지만 거의 절반이나 찢어져 버렸지. 대체 누가 그런 못된 짓을 했는지...","그 찢어진 반쪽은 잃어버렸소. 하지만 소문을 듣자 하니 그것이 버섯구름 봉우리의 켄타우로스들 손에 들어갔다고 하오.","나머지 부분은 아이언포지로 운송하기로 되어 있었는데 남녘해안의 서부 해안에서 운반 도중 사라졌다고 하더군."}, {"언더시티에 있는 조라 애니슨을 위해 곰팡이 슨 고서와 너덜너덜한 필사본을 모아야 합니다."}},
[1804] = {"비밀의 고서", {"벌써 돌려주려니 아쉽군. 하지만 분명 자네는 더 중요한 일에 써야 할 테니 어쩔 수 없군. 다만 잊지 말고 스트라드에게 내가 사본을 한 권을 꼭 갖고 싶어하더라고 전해주게나.","그나저나 자네가 이곳을 떠나 있는 동안 새로운 전갈이 왔더군. 수행사제들이 소환을 도와주려면 정신 집중의 마법봉 세 개가 필요하다고 했다네.","그는 어디서 마법봉을 찾야아 할지 모를 거야. 하지만 걱정말게. 얼마 전에 내가 최신 정보를 얻었으니까! 저습지에 있는 용아귀부족의 오크들이 마지막으로 정신 집중의 마법봉을 사용했다니 그곳으로 가보게. 그동안 난 용아귀부족에서 마법을 시전하는 자들을 확인해 볼 거야!"}, {"톱니항에 있는 스트라드 파선에게 복원된 고서와 정신 집중의 마법봉 3개를 가져가야 합니다."}},
[1805] = {"비밀의 고서", {"이 책은 최근 몇 년간 내가 읽은 것들 중에 가장 흥미로운 책 중 하나로군. 이곳 책장에는 고리타분한 지리서밖에 없는데다가 난 벌써 다 읽어 봤다오. 스트라드가 기회가 되는 대로 사본을 한 권 보내 줬으면 좋겠군.","아, 그 친구 얘길 하니 생각나는데 당신이 없는 동안 그에게 전갈을 받았소. 당신의 임무에 정신 집중의 마법봉이 필요하다고 하더군.","이 책을 보게 해줬으니 한 가지 비밀을 알려 주리다. 저습지에 용아귀부족의 오크가 마법봉을 가지고 있으니 그리로 가 보시오."}, {"톱니항에 있는 스트라드 파선에게 복원된 고서와 정신 집중의 마법봉 3개를 가져가야 합니다."}},
[1806] = {"정의의 시험", {"여기 대장간에 가서 작업을 시작하도록 하죠. 얼마 전부터 당신이 오기를 기다리며 준비를 하고 있었으니 오래 걸리진 않을 겁니다.","무기를 얻게 되면 그 어느 때보다 더 굳건히 정의를 수호할 수 있을 겁니다. 그것만은 보장해 드리죠.","솔직히 얘기하자면 당신이 세운 공훈의 결과를 보니 좀 부럽네요. 하지만 나도 내 나름대로 당신의 모험에서 큰 역할을 해낼 수 있다는 것이 무척 기쁩니다."}, {"조던 스틸웰이 무기를 다 만들 때까지 기다려야 합니다."}},
[1818] = {"딜링거와의 대화", {"죽음경비병 딜링거가 당신 같은 사람에게 줄 일거리가 있다고 하오. 어둠의 여왕님의 위대한 군대에게 당신의 가치를 증명해 보이려거든 즉시 딜링거에게 보고하도록 하시오.","죽음경비병 딜링거의 초소는 브릴의 서쪽 끝, 공동 묘지 근처에 있소."}, {"브릴에 있는 죽음경비병 딜링거와 대화해야 합니다."}},
[1819] = {"학살자 울라그", {"스컬지가 티리스팔 숲을 휩쓸고 지나갈 때, 우리는 연구 목적으로 한 녀석을 붙잡았었지. 녀석은 자유 의지가 있었고 자신을 학살자 울라그라고 하더군. 자유 의지를 가지긴 했을지 몰라도 여전히 리치 왕의 노예였네.","우린 울라그를 리치 왕의 손아귀에서 빼내려고 노력해봤지만 실패했어. 이제야 울라그를 해방시킬 때가 되었네.","울라그를 해방시키고 처치해버리게.","내 뒤쪽 입구로 들어가 북쪽 묘지로 가서 큰 건물 근처 바닥에 있는 묘지 제동 장치를 사용하게. 그러면 그를 구속하고 있는 마법의 봉인이 풀어질 거야.","그럼... 준비 하라고."}, {"학살자 울라그를 처치한 후 죽음경비병 딜링거와 대화해야 합니다."}},
[1820] = {"콜만과의 대화", {"어둠의 여왕님께 자신의 가치를 증명해 보였으니 이제 그분의 신하에게도 스스로 가치를 증명할 수 있겠군그래.","콜만 파딩이 포세이큰의 적들에게 복수하는데 도움을 줄 전사를 기다리고 있네.","브릴 동쪽에 있는 교수대끝 선술집에 가면 그를 찾을 수 있을 거야."}, {"콜만 파딩과 대화해야 합니다."}},
[1821] = {"아가만드 가문의 가보", {"저는 평생을 아가만드 가문을 위해 일했지요. 하지만 저는 아가만드 가문 때문에 죽임을 당했습니다. 아가만드 가문이 리치 왕의 어리석은 졸개가 되었다는 사실은 그리 놀랄만한 일도 아닙니다.","이제 그들이 가진 마지막 명예의 증거를 빼앗고 싶습니다. 아가만드가 납골당으로 들어가 무기 선반에서 그들의 가보인 검, 도끼, 단검, 철퇴를 훔쳐다 주세요!","아가만드가 납골당은 아가만드 밀농장의 북쪽 끝에 있는데 잠복한 스컬지와 죽은 아가만드 가족들이 지키고 있습니다."}, {"콜만 파딩에게 아가만드 가문의 도끼, 아가만드 가문의 검, 아가만드 가문의 철퇴, 그리고 아가만드 가문의 단검을 가져가야 합니다."}},
[1822] = {"가보 무기", nil, nil},
[1823] = {"루가와의 대화", {"명성이 자자한 실력 있는 전사로군. 루가 레이지토템의 시험에 도전할 준비가 됐는가? 그렇다면 불모의 땅에 있는 타우라조 야영지에 가서 루가를 찾게.","자신의 부름에 답할 전사들을 기다리고 있으니."}, {"루가 레이지토템과 대화해야 합니다."}},
[1824] = {"거인의 들판에서의 시험", {"좋아, 이 시험을 통과하려면 남쪽에 있는 거인의 들판으로 가야 하네. 그곳에는 거대 곤충들이 득실거리고 있다네. 불모의 땅에 새로 온 녀석들인데 하는 짓이 영 마음에 들지가 않는단 말이지.","녀석들의 꿈틀거리는 더듬이를 내게 가져오게. 잘라낸 더듬이는 금세 꿈틀거림을 멈추니 서둘러야 할 거야. 주어진 시간 안에 더듬이를 가져오면 시험을 통과하게 될 걸세."}, {"타우라조 야영지에 있는 루가 레이지토템에게 꿈틀거리는 더듬이 5개를 가져가야 합니다."}},
[1825] = {"툰그림과의 대화", {"유명한 오크 대장장이인 툰그림 파이어게이즈가 자네가 호드를 위해 열심히 싸워준 얘기를 들었다네. 자네에게 갑옷을 하나 만들어주고 싶으니 즉시 자기를 찾아오라더군.","툰그림은 불모의 땅 십자로 동쪽에 위치한 야영지에 있다네."}, {"불모의 땅에 있는 툰그림 파이어게이즈와 대화해야 합니다."}},
[1838] = {"투사의 방어구", {"당신의 힘과 용맹에 대해서는 익히 들은 바가 있어 쓸만한 갑옷을 만들어주고 싶소. 당신이 위대한 업적을 하나씩 이룰 때마다 당신을 도와줄 동반자 같은 갑옷 말이지.","하지만 이 갑옷은 만드는 것 자체만으로도 큰 임무요. 당신의 도움은 물론 호드의 다른 대장장이들의 도움도 필요하지.","자, 이 두루마리를 받으시오. 잘 보이지도 않는 눈으로 며칠 밤을 지새우면서 갑옷을 만드는 데 필요한 재료들을 적어놓았소. 이 두루마리에 적힌 물건들을 가져오면 첫 번째 갑옷을 만들어주도록 하지."}, {"툰그림 파이어게이즈에게 연기나는 철제 주괴 15개, 아주라이트 가루 10개, 철 주괴 10개, 연소 약병 1개를 가져가야 합니다."}},
[1839] = {"울라엘레크와 투사 건틀릿", {"트롤 대장장이 울라엘레크가 당신에게 투사 건틀릿을 만들어 줄 것이오. 그 친구는 듀로타에 있는 센진 마을에서 검은창부족 형제들과 살고 있는데 당신이 오기를 기다리고 있소이다."}, {"듀로타에 있는 울라엘레크와 대화하십시오."}},
[1840] = {"오름 스톤후프와 투사 투구", {"오름 스톤후프는 그가 가진 기술 때문에 많은 존경을 받고 있지. 그를 찾아가면 당신에게 투사 투구를 만들어줄 것이오.","썬더 블러프에 가서 도시의 거대한 중앙 토템 옆에 있는 연못 근처에서 그를 찾도록 하시오."}, {"썬더 블러프에서 오름 스톤후프와 대화하십시오."}},
[1841] = {"벨로라 나이틀리와 투사 다리보호구", {"들리는 바에 의하면 죽기 전 벨로라 나이틀리는 로데론에서 으뜸가는 갑옷 제작자였다는군. 죽음마저도 그녀의 재능을 퇴색시키지는 못한 것 같네. 벨로라를 만나 대화를 해 보도록 하시오. 당신에게 투사 다리보호구를 만들어 줄 것이오.","벨로라는 로데론의 언더시티에 있는 상업 지구에서 부지런히 장사를 하고 있소이다."}, {"언더시티에 있는 벨로라 나이틀리와 대화하십시오."}},
[1842] = {"사티로스 발굽", {"내 자네에게 강력한 마법이 깃든 투사 건틀릿을 한 쌍 만들어주지. 그 전에 먼저 이 마법을 모아서 내게 가져와야 하네.","자네 혹시 알고 있나? 사티로스들이 잿빛 골짜기 북동부에서 사는 건 그 숲의 힘 때문이라네. 녀석들은 숲의 순수한 힘을 거머리처럼 빨아들이면서 숲을 오염시키고 있네. 어느 사티로스가 잿빛 골짜기의 마법을 가장 많이 빨아먹었는지는 다부진 통발굽을 보면 알 수 있지.","그 발굽에는 강한 힘이 깃들어 있는데 자네에게 줄 건틀릿을 만들려면 그 발굽이 필요하네."}, {"듀로타의 센진 마을에 있는 울라엘레크에게 사티로스 통발굽 7개를 가져가야 합니다."}},
[1843] = {"투사 건틀릿", nil, nil},
[1844] = {"키메라의 뿔", {"투구를 만들어주기 전에 그대가 쓸 자격이 있다는 것을 보여주어야만 하오이다.","돌발톱 산맥의 북서부 지역에 잿더미 계곡이 자리 잡고 있소. 한때는 푸르렀던 곳이지만 이제는 오염되어 잿빛으로 변해버렸고 남은 생물들도 고통과 절망에 미쳐버렸지.","바로 그곳으로 그대를 보내려고 하오.","잿더미 계곡으로 가서 우두머리 키메라를 찾으시오. 우두머리 키메라를 처치해 고통에서 해방시키고, 키메라의 번개 뿔을 가져오면 투구를 만들어주도록 하겠소이다."}, {"썬더 블러프에 있는 오름 스톤후프에게 번개 뿔을 가져가야 합니다."}},
[1845] = {"투사 투구", nil, nil},
[1846] = {"용아귀부족 다리뼈", {"투사 다리보호구를 만들려면 단단한 다리뼈로 만든 버팀대가 필요해요. 저습지의 용아귀부족 오크들은 다리뼈가 무척 두텁고 단단하지요. 바로 그 다리뼈가 필요해요.","반드시 단단한 것이라야 되니 다리뼈를 모으면서 확인을 해 보도록 하시기 바랍니다. 맨손으로 우지끈 부러뜨릴 수 있는 것이면 방어구를 만들기에 적합하지 못한 것이랍니다. 호호!"}, {"언더시티에 있는 벨로라 나이틀리에게 단단한 용아귀부족 다리뼈 8개를 가져가야 합니다."}},
[1847] = {"투사 다리보호구", nil, nil},
[1848] = {"투사 갑옷", nil, nil},
[1858] = {"검은손 도적단", {"자네가 타잔의 가방을 열어 그 안에 든 물건들을 나에게 꺼내줘야겠네. 자물쇠를 보아하니 아주 견고하고 복잡해서, 열쇠를 찾는 것이 그나마 가장 쉬운 방법이 아닐까 싶군.","타잔에게 없었다면 다른 사람이 갖고 있겠지... 아, 그래! 타잔에게 가몬이라는 조수가 하나 있었지. 항상 술을 달고 사는 자이니 술집에 가면 찾을 수 있을 거야. 운 좋게도 술에 취해 있다면 열쇠를 몰래 빼내 오기 쉬울 걸세."}, {"타잔의 열쇠를 훔쳐 가방을 열고 그 속에 든 물건들을 오그리마의 어둠의 틈에 있는 테르조크에게 가져가야 합니다."}},
[1859] = {"테르조크", {"쉿, 목소리를 낮추라고! 당신 이름이야 물론 잘 알고 있지. 사실은 당신과 닮은 오크를 찾아오라는 지시를 받았거든.","소문을 듣자 하니 싸움 실력도 좋고 가끔씩 비밀스러운 일도 맡는다던데, 맞나?","어쨌거나 오그리마에 있는 테르조크를 찾아가 보라고. 당신에게 제안할 게 있다고 하니까 말이야."}, {"오그리마의 어둠의 틈에 있는 테르조크와 대화하십시오."}},
[1860] = {"제니아와의 대화", {"우리 마법사들이 꼭 해야 할 임무가 있소. 스톰윈드와 엘윈 숲의 안전을 위해 아주 중요한 임무지.","자세한 내용은 제니아 캐논이 알고 있소. 스톰윈드의 마법사 지구에 있는 마법사의 성소에서 공부를 하고 있으니 서둘러 찾아가 보시오."}, {"스톰윈드에 있는 제니아 캐논과 대화해야 합니다."}},
[1861] = {"거울 호수", {"이 세계는 거대하고 신비로운 힘들간의 전쟁에 피해를 받았습니다. 이 전쟁으로 인해 한때는 안정적이던 마법의 흐름에 커다란 문제가 생겼답니다. 우리는 이러한 변화가 가져온 결과를 알아내기 위해 노력하고 있는데 일부 마법사들은 최악의 결과가 아닐까 우려하고 있습니다!","지금은 정보를 모아야 하니 자세한 얘기는 나중에 하도록 하겠습니다.","자, 이 물통을 받으세요. 스톰윈드 성문의 남서쪽에 거울 호수 폭포 밑으로 가서 물을 담아 오세요. 그 물을 내게 가져오면 나쁜 마법에 오염되지 않았는지 실험해 보도록 하겠습니다."}, {"스톰윈드에 있는 제니아 캐논에게 거울 호수 견본을 가져가야 합니다."}},
[1878] = {"물주머니 현상금", nil, nil},
[1879] = {"빙크와의 대화", {"이보시오! 아이언포지에 있는 빙크라는 노움 친구가 소식을 전해왔소. 해야 할 임무가 있다는데 마법사가 아니면 맡기질 않겠다고 하오! 당신은 마법사지 않소?","그렇다면 빙크와 대화해 보시오. 그녀는 아이언포지의 마법 지구에 있는 신비의 전당에서 주로 시간을 보내니 그쪽으로 가면 만날 수 있을 것이오."}, {"아이언포지에 있는 빙크와 대화해야 합니다."}},
[1880] = {"마법사의 기억장치", {"우리 마법사들은 손가락 하나 까딱거린다고 화려한 마법을 쓸 수 있는 게 아니에요! 이 세계를 이루는 기본 요소인 마법을 제어하는 것이죠! 그러려면 아주 신중해야 하고 굳건한 의지와 방대한 지식이 필요하지요!","내 장치 중 마법사의 기억장치는 이처럼 방대한 양의 지식을 수집하고 보관할 수 있답니다! 그런데 그 장치를 잃어버렸지 뭐예요! 고향인 놈리건에서 도망 나오다 도시 외곽에 있는 숙소의 연장통 속에 숨겨 놨어요. 하지만 지금 그곳엔 오염된 노움들이 득실거리고 있어서 찾기가 어렵답니다!","부탁이에요. 내 기억장치를 찾아주세요!"}, {"빙크에게 마법사의 기억장치를 가져가야 합니다."}},
[1881] = {"아나스타샤와의 대화", {"아나스타샤 하트웰이 당신 같은 재능을 지닌 마법사에게 맡길 임무가 있다고 합니다. 언더시티의 마법 지구에서 그녀와 얘기를 나누고 포세이큰의 마법사들을 위한 첫 임무를 준비하도록 하시기 바랍니다."}, {"아나스타샤 하트웰과 대화해야 합니다."}},
[1882] = {"발니르 농장", {"동쪽에 있는 역병지대는 오염 정도가 심각해요. 리치 왕이 만들어낸 마법이 땅속에 퍼지면서 살아있는 것들을 모조리 오염시키고 죽이고 있어요. 역병의 파괴력을 막아주는 주문이 있긴 하지만 우리 포세이큰의 마법사들도 그 주문을 통제하기가 어려워 주문의 힘을 줄여야 한다고 생각하고 있지요.","하지만 먼저 역병이 티리스팔에 얼마나 깊숙이 침투했는지 알아야 해요. 티리스팔 동쪽에 있는 발니르 농장에 가면 정원에 금어초가 있을 거예요. 그것들을 모아서 가져오세요."}, {"언더시티의 마법 지구에 있는 아나스타샤 하트웰에게 발니르 금어초를 가져가야 합니다."}},
[1883] = {"운투와와의 대화", {"센진 마을의 부족 마법사인 운투와가 듀로타의 마법 흐름을 방해하는 요소가 있다면서 도움을 요청하고 있습니다. 운투와와 대화를 하고 어떻게 해서든 그를 도와주도록 하십시오.","그렇게 하면 머잖아 더 많은 비밀을 알게 될 것입니다."}, {"운투와와 대화해야 합니다."}},
[1884] = {"주주 해골", {"메아리 섬에 사는 흑마법사 잘라제인이 듀로타의 정상적인 마법 흐름을 흐트러뜨려 놓았네. 그는 주주 해골이라는 오염된 우상으로 이런 일을 벌이고 있는데 메아리 섬의 오두막에 그 우상을 두었다네.","그 우상은 야생의 동물과 식물을 타락시키고 어둡고 외진 곳에 사는 흑마법사와 지옥의 생물에게 힘을 부여하고 있지.","그 주주 해골을 파괴해야 하네. 마법의 흐름이 영원히 바뀌어버리기 전에..."}, {"주주 해골 4개를 파괴하고 센진 마을에 있는 운투와에게 가야 합니다."}},
[1885] = {"메넷 칼카드", {"음? 칼카드님께서 당신에게 관심을 가지고 계시기에 어떤 자일까 궁금했는데 생각했던 거랑 좀 다르군요. 하기야 겉모습이 그 사람의 본 모습은 아니니까요, 그렇죠?","어쨌거나 가능한 빨리 그분을 찾아가보세요. 마지막까지 그분의 소환을 거역한 자들이 어떻게 됐는지 알고 싶지 않다면 말이에요."}, {"언더시티의 도적 지구에 있는 메넷 칼카드와 대화해야 합니다."}},
[1886] = {"죽음추적자", {"바리마트라스 님의 최정예 근위대인 죽음추적자에 지원할 생각이라면 먼저 자네의 능력을 증명해야 할 거야.","흠, 솔직히 말하지. 이 임무는 자네의 목숨을 앗아갈 수도 있네.","내가 손을 써야 할 일이 좀 있는데, 은빛소나무 숲과 언더시티를 오가며 소식을 전하는 아스토르 하드렌이라는 전령의 소지품이 필요하네. 소지품을 가져오는 방법은 자네의 판단에 맡기지.","이 일에 대해서는 자세히 알지 않는 편이 좋을 걸세. 성공하고 돌아오면 자세한 얘기를 들려주겠네."}, {"아스토르의 소개장을 구해, 도적 지구에 있는 메넷 칼카드에게 가져가야 합니다."}},
[1898] = {"죽음의추적자", {"소개장만 가지고 있다면 자네가 바로 안드론이 기다리던 그 전령이라고 설득할 수 있을 거야. 전에 안드론을 만나본 적이 있는데 그런 의심을 할 만큼 똑똑한 위인은 아니더군.","바리마트라스님께서는 안드론이 다른 단체를 도와주고 있다고 의심하고 계시네. 그래서 그 단체에 대해 알아보고 안드론이 그 단체를 돕고 있다는 증거를 찾아오라 지시하셨지.","가서 안드론을 만나 증거를 찾아 오게. 연금술 실험실 근처에 있는 상점에 가면 그를 찾을 수 있을 거야."}, {"언더시티에 있는 안드론 건트와 대화해야 합니다."}},
[1899] = {"죽음의추적자", {"당신 고용주가 찾는 정보가 담긴 장부가 내 뒤로 보이는 책장에 있소. 갖다 드리면서 내 안부도 좀 전해 주시오. 그리고 정보가 맘에 들어 앞으로도 계속 거래를 하겠다면 당신을 보내줬으면 한다고 전해주시오."}, {"언더시티에 있는 메넷 칼카드에게 안드론의 장부를 가져가야 합니다."}},
[1918] = {"타락한 정령", {"쓰러진 정령에게서 강렬한 빛을 발하는 물구슬이 나왔습니다. 구슬 안에 든 내용물은 정말이지 역겹습니다. 미스트랄 호수를 오염시킨 성분이 무엇이든 이 구슬 안의 물에도 들어 있을 것이 분명합니다.","이런 물건에 관심을 둘 만한 인물이 정확히 누구인지는 알려지지 않았지만, 토막나무 주둔지의 마스토크 윌리히스라면 무언가 좋은 생각이 있을지도 모릅니다."}, {"잿빛 골짜기 토막나무 주둔지에 있는 마스토크 윌리히스에게 더럽혀진 물구슬을 가져가야 합니다."}},
[1919] = {"제니아에게 보고", {"마법에 대한 지식이 점점 늘어가고 있군, 젊은 마법사여. 이제 진짜 훈련을 시작할 준비가 되었소? 우리가 치르고 있는 비밀 전쟁에 동참할 준비가 되었는가 말이오?","그렇다면 스톰윈드 마법사 지구의 마법사의 성소에 있는 제니아 캐논과 대화하도록 하시오. 그녀가 보이지 않는 세계의 첫 창문을 열어 줄 임무를 알려줄 것이오."}, {"스톰윈드에 있는 제니아 캐논과 대화해야 합니다."}},
[1920] = {"고독한 은둔자 선술집 조사", {"마법사 지구에 있는 고독한 은둔자 선술집에서 소란이 있었다는 보고가 들어왔습니다. 눈에 보이지 않는 괴이한 생물들이 선술집을 어슬렁거린다는데 그곳에 마력의 균열이 생긴 게 아닌지 걱정입니다.","가서 선술집을 조사해보고 그 생물들을 잡아오도록 하세요. 선술집에서 현신의 주문서를 사용해 생물들이 보이게 해서 제압한 다음 속박의 상자를 사용해 붙잡으세요. 충분히 잡았으면 정령이 든 속박의 상자와 빈 상자, 그리고 주문서를 가지고 제게 돌아오도록 하세요."}, {"제니아 캐논의 뒤에 있는 현신의 주문서와 속박의 상자를 획득하십시오. 마법사의 성소에 있는 제니아에게 채워진 속박의 상자 3개를 가져가야 합니다."}},
[1921] = {"재료 수집", {"동료 마법사들을 위해 좋은 일을 해주었으니 이제 마법사의 로브를 입을 때가 되었군요. 위니 라슨이 로브를 만들 수 있지만 그러려면 필요한 재료를 찾아서 그녀에게 가져다줘야 해요.","로브를 만들려면 리넨이 있어야 하는데 리넨은 찾기가 쉬워요. 문제는 충만한 균열석이에요. 지금까지 이 보석이 있다고 알려진 곳은 모단 호수의 은여울 광산뿐이에요. 드워프 광부 조합에서 상자 안에 보관해 두었죠.","리넨과 보석을 모아 마법사 지구의 라슨의 의상실에 있는 위니 라슨에게 가져가세요."}, {"스톰윈드에 있는 위니 라슨에게 리넨 옷감 10장과 충만한 균열석 6개를 가져가야 합니다."}},
[1938] = {"우르의 흑마법 논문", {"오래전 세 명의 마법사가 이 탑에서 공부를 했었는데 그중 하나가 어둠의 마법에 빠져 타락해 버렸다네. 주위에서는 그 사악한 마법에서 손을 떼라고 설득했지만 그는 사악한 힘을 뿌리칠 수가 없었지. 그리고 그는 추방을 당했다네.","타락한 마법사는 붉은마루 산맥에 있는 일갈라의 탑을 새로운 영역으로 삼았는데, 우르의 흑마법 논문이라는 강력한 마법서를 가져가 버렸어.","우리 스톰윈드의 마법사들은 그 책을 되찾길 원하네. 일갈라의 탑으로 들어가서 그의 서재에서 책을 찾아서 내게 가져오게."}, {"스톰윈드에 있는 고위마술사 안드로매스에게 우르의 흑마법 논문을 가져가야 합니다."}},
[1939] = {"고위마술사 안드로매스", {"고위마술사 안드로매스 님께서 당신과 얘기를 나누고 싶어하세요. 그분은 스톰윈드 마법사 지구에 있는 마법사의 성소 최상층에 계시답니다.","그분과 얘기를 나누는 게 좋을 거예요. 안드로매스 님께서는 달라란에 붙잡혀있지 않는 마법사 중 가장 강한 분이시거든요. 그분과의 대화는 말 그대로 고위 회담이랍니다."}, {"고위마술사 안드로매스와 대화해야 합니다."}},
[1940] = {"온전한 거미줄", {"자네는 항상 기대에 어긋나지 않게 임무를 완수하는군. 이제는 숙련된 마법사인 자네의 지위에 어울리는 로브를 하나 더 입어야 할 때가 아닌가 싶군. 필요한 재료를 모아오면 로브를 하나 만들어 주기로 하지.","그늘숲으로 가서 청소부그늘거미를 사냥해 그 거미줄을 뽑아와야 하는데 가장 가늘고 온전한 거미줄이라야만 하네. 거미줄을 충분히 모으거든 이곳 마법사 지구의 라슨의 의상실에 있는 위니 라슨에게 가져가면 로브를 만들어줄 걸세."}, {"스톰윈드에 있는 위니 라슨에게 온전한 거미줄 8개를 가져가야 합니다."}},
[1941] = {"마나매듭 로브", nil, nil},
[1942] = {"별매듭 로브", nil, nil},
[1943] = {"다이노와의 대화", {"트롤 마법사 다이노가 당신의 도움이 필요합니다. 그녀는 오그리마의 검은장미 오두막에 있으니 찾아가 보도록 하세요."}, {"다이노와 대화해야 합니다."}},
[1944] = {"사비안의 폭포수", {"전설적인 사비안 도시는 잿빛 골짜기 동부 깊은 곳에 자리 잡고 있습니다. 학식이 있는 자들 사이에서는 사비안의 고대 지식에 대한 이야기가 잘 알려졌죠. 전해지는 바로는 사비안의 물에는 거대한 마법의 힘이 있다고 합니다.","그 물의 근원지를 찾아 견본을 가져와야 합니다. 이 병을 가지고 사비안으로 가세요. 폭포의 가장 높은 곳에서 가장 낮은 곳까지 물을 담아 내게 가지고 오도록 하세요. 그 고대 도시를 돌아다닐 때는 조심해야 합니다. 현재 폐허가 된 그곳을 점령한 사티로스는 자신들의 일에 간섭하는 마법사를 싫어하니 말입니다."}, {"오그리마에 있는 다이노에게 사비안 폭포수 견본을 가져가야 합니다."}},
[1945] = {"미소띤 자매", {"당신의 공로에 대한 보답으로 필요한 재료를 모아오면 새로운 로브를 만들 수 있도록 준비 해두었습니다.","이 로브를 만들려면 잿빛 골짜기로 돌아가 미소띤 자매의 머리카락을 구해와야 해요. 그들은 드리아드로, 레인나무 은신처 근처에 모여있습니다.","머리카락을 구해 불모의 땅 십자로에 있는 트롤 재봉사 킬할라에게 가져다주면 당신의 로브를 만들어줄 겁니다. 킬할라가 당신을 기다리고 있을 거예요."}, {"십자로에 있는 킬할라에게 미소띤 자매의 머리카락 12개를 가져가야 합니다."}},
[1946] = {"황천의 로브", nil, nil},
[1947] = {"습지대로의 여행", {"이제 마법사의 지팡이를 받을 때가 된 것 같군요. 이 임무를 시작하기 전에 먼저 먼지진흙 습지대에 사는 인간 은둔자 타베사와 얘기를 나누어보도록 하세요. 테라모어 서쪽, 습지대 깊숙한 곳에 있는 오두막집에 있을 겁니다.","그녀는 지식이 아주 풍부한 사람이니 얘기를 나누면 도움이 될 거예요. 돌망치 폐허의 바로 북쪽, 테라모어 서쪽에 타베사의 작은 집을 찾을 수 있을 겁니다."}, {"타베사와 대화해야 합니다."}},
[1948] = {"강력한 물건", {"마법사의 지팡이를 만드는 건 쉬운 일이 아니죠. 다량의 희귀한 재료가 필요하고 많은 양의 마법력을 담을 수 있는 용기도 만들어야 한답니다. 이런 것들을 모아서 내게 가져오도록 하세요.","이 양피지에 필요한 물건의 이름과 구하는 방법을 적어놓았어요. 이 목록에 적은 물건들을 그대로 가져오고 지시사항도 반드시 따르도록 하세요. 그렇지 않으면 지팡이를 만들 때 일이... 잘못될 수도 있으니..."}, {"먼지진흙 습지대에 있는 타베사에게 비취 1개와 벼락 맞은 나뭇가지를 가져가야 합니다."}},
[1949] = {"숨겨진 비밀", {"당신에게 줄 지팡이를 만들려면 어떤 책에서 중요한 구절을 읽어야 합니다. 학자 틸스가 쓴 마력의 의식이라는 책이지요.","이 세계의 거대한 도서관에도 없으며 내게도 이 책의 사본이 없답니다. 그래서 학자 틸스에게 직접 얘기를 하고 받아와야 해요.","행운을 빌겠어요. 틸스는 한때 훌륭한 마법사이자 학자였는데 지금은 소금 평원에 있는 노움과 고블린 경주장에서 술과 노름에 빠져 지내고 있답니다."}, {"소금 평원에 있는 학자 틸스와 대화해야 합니다."}},
[1950] = {"주문 알아오기", {"책 찾는 거야 도와줄 수 있지만 먼저 날 도와줘야 하겠어!","자네가 찾는 그 책은 마법으로 잠긴 금고에 들어있지. 그 금고를 열려면 특별한 주문을 외워야 하는데 도무지 기억이 나질 않는단 말이야! 보통은 내 조수가 주문이 기억나게 와주곤 하는데 어디 있는지 찾을 수가 없군.","내 책을 원하거든 내 조수를 찾아보라고! 운 좋으면 경주장 근처 친구들 중에 아는 사람이 있을지도 모르니 수소문해 보도록 하게. 녀석을 찾으면 여기로 끌고 오던가 아니면 내 금고를 여는 주문을 알아오게!"}, {"틸스의 금고를 여는 주문을 알아서 틸스에게 돌아가야 합니다."}},
[1951] = {"마력의 의식", {"흠... 지금 생각해보니 책이 없군! 그렇지... 전에 노름으로 진 빚이 있어 그걸 갚느라 마지막 마력의 의식 사본을 성직자 친구에게 팔아버렸지...","그 친구 붉은십자군의 일원이었는데 분명히 티리스팔 숲에 있는 붉은십자군 수도원의 도서관으로 가지고 갔을 게야!","그 책을 원한다면 그 수도원으로 가서 찾아야 할 거라고. 수고를 끼쳐 미안하군그래. 요즘 제정신이 아니라서..."}, {"먼지진흙 습지대에 있는 타베사에게 마력의 의식을 가져가야 합니다."}},
[1952] = {"마법사의 마법봉", {"당장 마법봉을 만들어야겠군요."}, {"의식이 끝나면 타베사와 대화하십시오."}},
[1953] = {"다시 습지대로", {"이제 먼지진흙 습지대의 은둔자 타베사와 얘기를 나눌 때가 되었군요. 타베사가 강력한 마법사의 도구를 만드는 법을 가르쳐줄 겁니다.","타베사의 오두막집은 테라모어 서쪽, 먼지진흙 습지대 깊은 곳에 있습니다."}, {"타베사와 대화해야 합니다."}},
[1954] = {"지옥의 보주", {"수정구를 만들기 위한 첫 단계는 힘이 깃든 수정구를 얻는 것입니다. 불타는 칼날단의 흑마법사들이 고급 수정구를 지니고 있답니다. 그런데... 수정구 속에 악마를 묶어두고 있으니 문제가 아닐 수 없습니다! 하지만 우리가 상황을 바로잡을 거예요. 그럼요, 물론이죠.","잊혀진 땅으로 가서 불타는 칼날단 소환사에게서 지옥의 보주 하나를 빼앗아오세요. 소환사들은 만노로크 소굴에 있을 겁니다.","보주를 손에 넣으면 내게 돌아오도록 하세요."}, {"먼지진흙 습지대에 있는 타베사에게 지옥의 보주를 가져가야 합니다."}},
[1955] = {"악마 퇴치", {"소환사의 보주를 손에 넣었으니 이제 보주 안에 있는 악마를 쫓아내야 합니다. 악마를 쫓아내는 건 쉬운 일이니 이것은 내가 하도록 하죠.","당신이 어려운 부분을 맡아줘야 하겠습니다. 악마가 풀려나오면 당신이 물리쳐야 합니다. 시간을 너무 오래 끌면 악마가 다시 보주로 들어가게 되고 처음부터 다시 시작해야 하니까 재빨리 물리쳐야 합니다!","자, 악마와 맞설 준비를 하도록 하세요. 가장 강한 마법을 준비하고 물약도 꺼내 놓도록 하세요. 엄청난 소동이 벌어질 테니 도와줄 친구가 있으면 데려오도록 하세요."}, {"보주의 악마를 물리치고 타베사와 대화해야 합니다."}},
[1956] = {"울다만의 마력원천", {"이제 악마를 제거했으니 마력원천을 찾아야 하겠군요. 유감스럽게도 마력의 원천의 힘이 셀수록 대개 오래되고 얻기가 어렵답니다. 운명의 장난이란 게 때론 참 웃기지, 그렇지 않나요?","울다만으로 가서 흑요석 파수꾼을 물리치도록 하세요. 녀석은 덩치도 크고 힘도 장사인데다가 녀석의 마력원천에는 엄청난 마력이 들어있습니다!","행운을 빌겠어요. 운이 없다면, 뭐... 다시 볼 수 없겠네요. 만나서 반가웠어요."}, {"흑요석 마력원천을 구해서 먼지진흙 습지대에 있는 타베사에게 가져가야 합니다."}},
[1957] = {"마력의 폭풍", {"이제 거의 다 끝났는데 이 마지막 단계가 제일 위험합니다.","비어있는 소환사의 수정구도 있고 힘을 부여할 준비도 되었어요. 이것엔 마력의 균열 에너지를 이용해 힘을 부여할 겁니다. 내가 주문을 걸어 일시적으로 균열을 열면 그곳에서 마력의 폭풍이라는 피조물들이 쏟아져 나올 겁니다. 당신이 마력의 폭풍을 물리치고 그들의 힘이 풀려나오면 내가 그 힘을 모으도록 하지요.","균열이 닫히기 전에 마력의 폭풍을 물리쳐서 그 녀석들의 힘을 모두 수정구에 불어넣으면 됩니다."}, {"주어진 시간 안에 마력의 폭풍 12마리를 물리쳐야 합니다."}},
[1958] = {"천공의 힘", nil, nil},
[1959] = {"아나스타샤에게 보고", {"언더시티의 마법사들이 도시 내에 마법 장애가 발생했다면서 이를 조사해 줄 호드의 마법사를 찾고 있다고 전갈을 보내왔습니다.","자세한 내용은 언더시티의 마법 지구에 있는 아나스타샤 하트웰이 얘기해 줄 겁니다. 그녀를 찾아가 보시기 바랍니다."}, {"아나스타샤 하트웰과 대화해야 합니다."}},
[1960] = {"연금술 상점 조사", {"연금술 실험실 입구에 있는 알게르논이 근무하는 연금술 상점 근처에서 알 수 없는 기운이 감지되고 있습니다. 무슨 이유에서인지 모르겠지만 마법으로 구성된 보이지 않는 생물들이 상점을 돌아다니고 있어요.","이들이 활보하는 데는 분명히 이유가 있을 테니 그 이유를 알아내야 합니다! 가서 이 소란을 조사하고 그 생물들을 잡아오도록 하세요. 내 뒤에 있는 속박의 상자와 주문서를 가져가세요. 연금술 상점에서 현신의 주문서를 사용하면 마법 생물들이 보이게 될 겁니다. 생물들을 제압한 다음 상자를 사용해 붙잡도록 하세요. 그다음 채워진 속박의 상자와 빈 상자, 주문서를 가지고 돌아오세요."}, {"아나스타샤 하트웰의 뒤에 있는 현신의 주문서와 속박의 상자를 획득하십시오. 언더시티에 있는 아나스타샤 하트웰에게 채워진 속박의 상자 3개와, 속박의 상자, 현신의 주문서를 가져가야 합니다."}},
[1961] = {"재료 수집", {"자, 이제 첫 번째 마법사의 로브를 입을 때가 되었군요. 마법사의 로브는 지위의 상징이자 당신의 가치를 입증하는 것이랍니다.","이 로브를 얻으려면 은빛소나무 숲의 달라란 마법사들에게서 마나석을 모아와야 합니다. 우리와 비슷한 믿음을 추구하고 있는 자들이라 죽이는 것이 썩 내키지는 않지만, 우리에게 대항할 힘마저 없다면 살 가치가 없죠.","마나석과 리넨을 구해 마법 지구의 의류점에 있는 재봉사인 조셉 그레고리안에게 가져다주세요."}, {"조셉 그레고리안에게 리넨 옷감 10장과 달라란 마나석 6개를 가져가야 합니다."}},
[1962] = {"마법불꽃 로브", nil, nil},
[1963] = {"으스러진 손", {"대족장님께선 너무 생각이 많으셔서 탈이야. 호드의 세력을 확장하는 문제도 굉장히 조심스러워하시지. 한 때 우리 호드는 모두 강력한 정복자들이었는데 말이야. 과거의 영광을 반드시 되찾아야만 해. 으스러진 손은 반드시 그렇게 하고 말거야.","우리 도적들은 음지에서 힘을 길러 양지의 적을 은밀하게 쓰러뜨리지. 이번 임무를 수행하여 도적으로서 자네의 가치를 증명해야 하네.","타잔이라는 트롤이 톱니항의 해적들과 거래를 하고 있는데 나한테 꼭 필요한 정보를 가지고 있어. 녀석을 해치우고 그의 가방을 가져오게."}, {"타잔을 죽이고 그의 가방을 오그리마의 어둠의 틈에 있는 테르조크에게 가져가야 합니다."}},
[1978] = {"죽음의추적자", {"비밀결사대라고? 생각도 못했던 일이군. 그들의 배반이 있은 후 몇 년 사이 알터랙의 지배자들이 세력을 크게 잃은 모양이야.","바리마트라스님께서 이걸 아시면 아주 흡족해 하실 걸세. 자네의 공으로 이 사실을 알아냈으니 직접 가서 바리마트라스님께 전해드리는 것이 좋겠군.","아, 내 정신 좀 보게. 죽음의추적자에 합류하게 된 걸 축하하네."}, {"언더시티의 실바나스의 전당에 있는 바리마트라스에게 안드론의 장부를 가져가야 합니다."}},
[1998] = {"펜윅 타트로스", {"바리마트라스님께서 방금 포세이큰을 위협하는 자가 있으니 당장 그자를 제거하라고 내게 서한을 보내셨네. 이름은 펜윅 타트로스라고 하는데 바리마트라스님께서는 은빛소나무 숲에 근거지를 두고 있는 언데드의 우두머리로 생각하고 계시네.","이 소식을 들으니 자네 이름이 떠오르더군. 자네 실력이라면 이 임무를 훌륭하게 완수하리라 믿네.","은빛소나무 숲에 있는 죽음의추적자들이 최근에 보고해온 바로는 타트로스가 로다미어의 낡은 부두 근처에 있는 것 같다더군."}, {"펜윅 타트로스를 처치하고 그 증거로 그의 머리카락을 언더시티의 도적 지구에 있는 메넷 칼카드에게 가져가야 합니다."}},
[1999] = {"거래의 도구", {"호박색 농장에 있는 키린 토의 마법사들은 달라란의 현재 상황에 관련된 중요 문서들을 보관하고 있네. 우리 죄수 중 하나가 실토해서 알게 됐지. 바리마트라스 님께서 그 문서를 찾아 죽음추적자들에게 건네주라고 지시하셨네.","이 임무를 자네에게 맡기겠네. 하지만 자물쇠 따는 기술과 약간의 장비가 필요할 거야. 장비를 담당하는 에스텔 젠드리와 얘기를 해 보고 떠나기 전에 도구를 받도록 하게."}, {"달라란 상황 보고서를 찾아 언더시티의 도적 지구에 있는 메넷 칼카드에게 가져가야 합니다."}},
[2000] = {nil, nil, nil},
[2038] = {"사라진 빙글스의 물건들", {"나를 모단 호수에 추락시킨 것으로는 성에 안 찼는지 근처 섬에 살던 트로그들이 나를 덮쳤소. 도구들과 특제 깡그리싸그리 폭탄을 훔쳐가 버렸지 뭐요. 자이로콥터와 폭탄이 없으니 우리 노움에게 가망은 없소!","자이로콥터를 수리할 수 있도록 내 도구들과 깡그리싸그리 폭탄을 찾아주시오. 놈리건을 위해서 말이오."}, {"빙글스의 물건들을 찾아서 돌아와야 합니다.","빙글스의 스패너, 빙글스의 드라이버, 빙글스의 망치, 빙글스의 깡그리싸그리 폭탄을 찾아야 합니다."}},
[2039] = {"빙글스 찾기", {"빙글스에게서 연락이 끊겼소! 놈리건 정찰 임무를 띠고 수색을 나갔는데 일주일이 넘도록 아무런 보고가 없소이다. 어딘가에서 봉변을 당한 게 분명하오.","그가 모단 호수 위를 날아가는 모습이 마지막으로 목격됐다 하는군. 당신이 빙글스를 찾아봐 주면 좋겠소."}, {"모단 호수에서 빙글스 블라스텐하이머를 찾아야 합니다."}},
[2040] = {"지하 공격", {"놈리건이 저 비열한 트로그 녀석들의 손아귀에 들어가다니! 위험한 상황이긴 하지만 어쩌면 당신이 우리를 도와주실 수 있을지도 모르겠군요.","죽음의 폐광 깊은 곳에 아직 쓸 수 있는 고블린 벌목기가 있어요. 그 벌목기를 찾아 구동장치를 그대로 가져다주세요. 벌목기 구동장치의 전력을 우리의 회전천공식 발굴기에 사용해서 놈리건의 바위투성이 지하 경계를 부수고 공격 진입로를 뚫어야겠어요!"}, {"죽음의 폐광에서 노암 톱니구동장치를 입수해 스톰윈드에 있는 쇼니에게 가져가야 합니다."}},
[2041] = {"쇼니와의 대화", {"어쩌면... 어쩌면 말이오. 당신이 놈리건을 되찾기 위한 전투에서 우리를 도울 수 있을 거요.","스톰윈드에 가면 지하 공격조 지휘관인 쇼니가 있소. 쇼니가 회전천공식 발굴기를 사용하도록 도와주시오.","아마 스톰윈드의 드워프들과 함께 있을 거요. 행운을 빌겠소."}, {"스톰윈드의 드워프 지구에 있는 쇼니와 대화해야 합니다."}},
[2078] = {"자이로마스트의 복수", {"애송이 친구, 내 일등항해사를 찾아주면 고맙겠네.","일등항해사가 다시 일을 시작해 멀록 녀석들이랑 그 두목을 붙잡으면 녀석들도 계속해서 그렇게 즐겁게 물장구를 치진 못할 걸!","이 열쇠를 가져 가서 선장을 다시 모시라고 전해주게나. 하지만 말을 듣지 않는다면 그때는 자네의 판단에 모든 걸 맡기겠네."}, {"겔카크의 일등항해사인 망나니탈곡기 4100을 찾아서 겔카크에게 데리고 오거나 처치해야 합니다."}},
[2098] = {"자이로마스트의 수색", {"해적의 삶이란 쉬운 게 아냐, 애송이 친구. 약탈한 돈으로 술을 마시며 흥청대는 게 전부가 아니란 말이지! 아아... 내 배가 트레샤돈 녀석들 때문에 해안 바로 앞에서 침몰됐네. 빨리 다시 일을 시작해야 하는데!","내가 해안으로 밀려왔을 때 일등항해사의 열쇠가 바위에 떨어져 부서졌지. 어둠해안에 사는 짐승들이 그걸 훔쳐간 게 분명하다네. 사나운 모래톱게, 거대한 숲타조, 그리고 멀록들이 난파된 우리 배를 휘젓고 다니는 걸 슬쩍 봤거든."}, {"겔카크의 열쇠 조각 3개를 찾아 겔카크 자이로마스트에게 가져가야 합니다."}},
[2118] = {"역병에 걸린 땅", {"어둠해안 전역에 역병이 퍼지면서 모든 이들의 몸과 마음을 앗아가고 있소.","엉겅퀴곰들이 이 역병으로 가장 큰 타격을 받았다오. 한때는 고귀하던 짐승이 지금은 미친 듯이 날뛰는 파괴의 앞잡이가 되어 버렸소. 내 이 역병을 치료할 수 있을 것도 같소.","이 덫을 숲으로 가지고 가서 땅에 놓으시오. 광포한 엉겅퀴곰이 덫에서 발하는 빛에 닿으면 잠시 동안이나마 온순하게 변할 거요. 곰이 온순해지면 잘 달래어 여기로 데려와 주시오."}, {"광포한 엉겅퀴곰을 생포해 타나리운에게 데려가야 합니다.","광포한 엉겅퀴곰을 생포하는 데 실패하고 덫을 잃으면, 타나리운 트리텐더에게 돌아가 다른 덫을 받아야만 합니다."}},
[2138] = {"병든 엉겅퀴곰 처치", {"우려했던 대로 치료를 해도 소용이 없군.","자연에서 살아가는 짐승들의 목숨을 빼앗는다는 게 너무 마음 아픈 일이지만 병들고 타락한 엉겅퀴곰은 반드시 처치해야 하오.","숲으로 돌아가 광포한 엉겅퀴곰 스무 마리를 처치하시오. 그래도 역병을 막기에는 역부족이겠지만 그 짐승들이 숲과 동식물을 파괴하는 것은 잠시나마 늦출 수 있을 거요."}, {"숲에서 광포한 엉겅퀴곰 20마리를 처치한 후 어둠해안에 있는 타나리운 트리텐더에게 가야 합니다."}},
[2139] = {"타나리운의 희망", {"우리 종족은 이 땅에서 생존을 위해 너무나 큰 희생을 치르고 있소.","자연의 수하들, 수많은 병든 동물들이 그대의 손에 피를 흘리며 죽어갔소. 그래도 아우버다인의 주민들은 그대에게 은혜를 입은 셈이지. 비록 잠시 동안이긴 했지만 숲의 고통을 덜어주었으니 말이오.","그대의 마지막 임무가 하나 남았소만 또 살생을 해야 한다는 것을 미리 말해 두겠소. 이런 일로 그대를 부르는 것이 마지막이었으면 하는 바람이오.","바샬아란 근처의 북동쪽 동굴에 우두머리 엉겅퀴곰이 살고 있소. 그 곰을 처치해주시오."}, {"우두머리 엉겅퀴곰을 찾아서 처치해야 합니다."}},
[2158] = {"평안과 휴식", {"어떤 모험가든 힘이 떨어지면 휴식을 취해야지요. 편안히 쉴 곳이라면 사자무리 여관만한 곳이 없습니다!","내 절친한 친구 팔레이가 사자무리 여관을 운영하고 있습니다. 내가 당신을 보냈다고 말하면 특별히 가격을 깎아 줄지도 모릅니다.","사자무리 여관으로 가려면 여기서 길을 따라 남쪽으로 가면 바로 보일 겁니다."}, {"사자무리 여관에 있는 여관주인 팔레이와 대화해야 합니다."}},
[2159] = {"돌라나르로의 배달", {"안녕하십니까, 젊은 <class>님. 저 좀 도와주지 않으시겠습니까? 돌라나르 마을로 가져다줘야 하는 약초 보따리가 있는데, 그늘 협곡에서 드루이드와 볼일이 남아 아직 떠날 수가 없습니다.","저 대신 이 배달물을 좀 전해주지 않으시겠습니까? 돌라나르 여관주인 켈다미르에게 전달해야 하는 물건입니다. 돌라나르 여관은 남쪽으로 가는 길에 있습니다."}, {"여관주인 켈다미르에게 돌라나르 배달물을 가져가야 합니다."}},
[2160] = {"탄노크에게 보급품 전달", {"안녕하세요! 무척 마음씨 좋은 모험가분이신 거 같군요. 혹시 위험을 무릅쓰고 여행을 하실 계획이시라면 카라노스에 있는 여관으로 이 소포를 좀 전해주지 않겠어요?","도착하면 그 여관에서 좀 머무는 것도 좋을 거예요. 먼 길이라 피곤하실 테니까요.","어쨌든, 이걸 여관 종업원 탄노크 프로스트해머에게 가져다주세요. 저는 이곳에서 멀리 갈 수 없는 몸인데다 며칠 후 도착할 산악경비대 호위병을 기다려야 해서 곤란하거든요."}, {"카라노스에 있는 탄노크 프로스트해머에게 여관 보급품 상자를 전해야 합니다."}},
[2161] = {"일꾼의 짐", {"저기요, 저 좀 도와주시겠어요?","여기 식량 보따리가 하나 있어요. 전 이걸 들고 전갈, 거미, 그보다 더 끔찍한 것들과 싸우면서 걸었어요! 시험의 골짜기에 음식이 필요할 것 같아 돕고 싶은 마음에 가져갔죠. 그런데 음식은 필요하지도 않더라고요! 이제 칼바위 언덕으로 다시 가져가야 하는데 또 짐승들을 만날까봐 무서워서...","저 대신 이 짐을 가져다주실 수 있나요? 저는 일개 짐꾼에 불과하지만 당신은 용감한 영웅이잖아요. 아무것도 두려워하지 않는 영웅!","칼바위 언덕의 여관주인 그로스크에게 가져다주세요. 고맙습니다!"}, {"칼바위 언덕에 있는 여관주인 그로스크에게 우코르의 짐을 가져가야 합니다."}},
[2178] = {"간단한 타조 요리", {"맛있는 냄새 안 나세요? 저만의 특제 요리 신선한 타조 스튜라는 건데 맛이 아주 일품이랍니다!","혹시 조리법을 배워볼 생각이 있으신가요? 그렇다면 요리에 쓸 짐승들을 다루는 방법을 아는지 증명해야 한답니다.","그렇다고 밖에 나가 한 마리를 길들여 타고 오라는 건 아니랍니다. 그냥 몇 마리 잡아서 촉촉한 고깃덩어리를 조금만 가져다주시면 됩니다. 그러면 타조 스튜의 조리법을 가르쳐 드릴게요."}, {"아우버다인에 있는 알란다리안 나이트송에게 타조 고기 5개를 가져가야 합니다."}},
[2198] = {"부서진 목걸이", {"빛바랜 목걸이를 찾았습니다. 비록 겉은 더럽혀져 있지만 정교하고 신비한 기술로 만들어진 것 같습니다. 목걸이는 부서졌는데 모양을 보아하니 보석 세 개가 사라진 듯합니다. 목걸이의 아래 부분에는 아이언포지의 도시 인장이 희미하게 찍혀 있습니다. 인장 밑에는 \"탈바쉬\"라는 작은 글씨가 새겨져 있습니다.","이 목걸이에 대해 알려면 목걸이의 근원에 대해서 알아야 합니다. 인장과 작은 글씨가 그 출발점이 될 것 같습니다."}, {"부서진 목걸이의 잠재적 가치를 알아내기 위해 원 제작자를 찾아야 합니다."}},
[2199] = {"정보의 대가", {"저는 이곳 아이언포지에서 꽤 유명한 보석세공사라고요! 그러니까, 전에 꽤 유명했지요... 지금은 경쟁자의 거짓말 때문에 어렵게 지내고 있지만요! 이 바닥에서 평판이 나빠지면 곧장 돈줄이 끊기게 된답니다. 그러다 보니 한때는 유명했지만 지금은 더럽혀진 명성에 무일푼으로 지내고 있어요, 흑흑.","우선 수주한 주문 하나를 마무리할 수 있게 은괴 다섯 개를 가져다주세요. 이게 얼마 만에 받아보는 주문인지 모르겠다니까요.","당신이 이 은괴 다섯 개를 가져다주면 목걸이에 대한 질문에 답해 드리죠."}, {"아이언포지에 있는 탈바쉬 델 키젤에게 은괴 5개를 가져가야 합니다."}},
[2200] = {"울다만으로 돌아가기", {"제 경쟁자 녀석이 제 목걸이가 그 성기사에게 저주를 내린 거라며 소문을 퍼뜨리기 시작했어요! 저주받은 물건을 사려고 하는 자가 없으리라는 것은 말할 것도 없지요.","제가 제안 하나 할게요. 만약 당신이 잃어버린 보석들을 찾아오면 목걸이를 다시 복원해줄 테니, 대신 저 탈바쉬 델 키젤이 절대로 저주받은 물건을 만들지 않는다고 선전해 주세요!","울다만에서 단서를 찾아보세요. 어쩌면 성기사의 유해를 찾아보는 게 좋을지도 모르겠군요. 그가 마지막으로 그 목걸이를 지니고 있었으니까 말이지요! 보석을 찾으면 이걸 쓰세요. 그러면 이곳 아이언포지에 있는 저와 연락을 할 수 있을 거예요."}, {"울다만 안에서 탈바쉬의 목걸이의 행방에 대한 단서를 찾아야 합니다. 탈바쉬가 말했던 성기사가 그 단서를 가진 최후의 인물일 것입니다."}},
[2201] = {"보석 찾기", {"성기사의 일지에 적힌 목걸이의 보석 위치가 울다만에서 찾을 수 있는 유일한 단서인 듯합니다. 이 성기사가 적어놓은 정보가 정확하다면 발굴현장을 차지하기 위해 싸우고 있는 어둠괴철로 드워프들과 트로그들을 처치해야 합니다.","이 정보를 얻었으니 이제 본격적인 보석 찾기가 시작된 것입니다. 보석을 모두 손에 넣으면 탈바쉬의 지시대로 수정점 유리병을 사용하여 아이언포지에 있는 그에게 알려야 합니다."}, {"울다만에 흩어져 있는 루비, 사파이어, 토파즈를 찾아야 합니다. 모두 찾으면 탈바쉬가 준 수정점 유리병을 사용하여 그에게 연락해야 합니다.","일지에 의하면...","*루비는 어둠괴철로 드워프들이 있는 지역에 숨겨져 있습니다.","*토파즈는 얼라이언스 드워프들이 있는 곳 근처에 트로그 지역에 있는 항아리 중 하나에 숨겨져 있습니다.","*사파이어는 트로그 대장인 그림로크가 가지고 있습니다."}},
[2202] = {"울다만에서 재료 찾기", {"얼마 전에 자홍버섯이라는 새로이 발견된 식물에 대한 얘기를 들었다네. 자홍버섯은 울다만 발굴 현장의 깊숙한 곳에서 군생하지. 다른 곳에서도 군생할 수도 있지만 내가 알고 있는 곳은 거기뿐이라네. 난 그 버섯이 연금술에 사용할 잠재적인 가치가 있는지 연구하고 싶기에 자네의 도움이 필요하다네.","자홍버섯을 찾아서 12개만 나에게 가져다주시게. 버섯송이에 가까이 가면 포자에서 독이 발산되니 조심하도록 하게나.","나를 위해 이 일을 맡아주게. 그리고 내 자네를 격려하는 의미에서 유명한 복원의 비약 한 꾸러미도 줄 것일세!"}, {"카르가스에 있는 자칼 모스멜드에게 자홍버섯 12개를 가져가야 합니다."}},
[2203] = {"황야의 땅에서 재료 찾기", {"자네는 지금까지 내게 아주 큰 도움이 되었네. 내 자네에게 보상으로 전에 자네에게 준 적 있는 비약의 조제법을 알려 주도록 함세. 단, 꽤 위험한 임무 한 가지만 완수해 주면 말이야... 내 얘기를 들어 보게.","이 금고에는 빈 요술 유리병 세 개가 들어 있다네. 화염의 수호비룡의 피를 빨아들일 조화의 오라가 담겨 있는데, 그걸 그 비룡에 사용하면 비룡이 크게 노하게 될 테니 조심하게. 그 유리병 세 개를 다 채우거든 내게 가져오게나.","이 임무를 완수해 주면 조제법은 자네 것이 되는 걸세."}, {"빈 요술 유리병을 황야의 땅에 있는 화염의 수호비룡에게 사용해야 합니다. 세 개를 다 채우면 카르가스에 있는 자칼 모스멜드에게 가져가야 합니다."}},
[2204] = {"목걸이 복원", {"이제 목걸이에 필요한 \"마력\"을 찾아야 해요. 아마도 그 일에는 울다만이 최고의 장소일 걸요. 그곳에 피조물들이 꽤 많이 돌아다닐 텐데, 그렇지 않나요? 그중에서 제일 덩치 크고 악독해 보이는 놈을 잡아서 놈의 마력원천을 빼앗으세요! 제가 그 마력원천을 목걸이에 불어넣을 수 있을 거예요.","그 마력원천이 없이 목걸이를 고친다는 것은 불가능해요. 그러니 마력원천을 찾으면 여기 아이언포지로 다시 돌아오세요. 당신을 위해 모두 고쳐 드릴게요!"}, {"울다만에서 가장 강력한 피조물을 찾아 부서진 목걸이의 마력원천을 획득한 다음, 아이언포지에 있는 탈바쉬 델 키젤에게 목걸이와 함께 가져가야 합니다."}},
[2205] = {"SI: 7 찾기", {"배지요? 오, 아무것도 아니에요. 당신이 처리할 수 있는 건 아니니까 신경 쓰지 마세요. 마티아스 님께서 당신 행색을 보면 진짜 도적보다는 치즈 배달원으로 착각하시겠군요. 그건 그렇고, 내게 일이 하나 주어졌는데 그 일을 끝내겠다고 약속을 했어요.","말이 나와서 말인데 이 소포를 마티아스 님께 전해주세요. 그러면 마티아스 님께서 소일거리 한두 개 정도를 맡길 거예요.","어서 가세요."}, {"스톰윈드 병영에 있는 단장 마티아스 쇼에게 소포를 가져가야 합니다."}},
[2206] = {"선적 일정 입수", {"현장에 있는 우리 요원들로부터 황금골 남쪽에서 좀도둑들이 대거 활동하고 있다는 보고가 들어왔습니다. 보고에 의하면 그 좀도둑들은 제로드 선착장을 손에 넣고는 붉은마루 산맥에서 서부 몰락지대까지 대량의 밀수품을 운반하고 있는 모양이더군요.","그 부두에 침투해서 선적 일정표를 빼내오십시오. 아마 부두주임이 주머니에 단단히 넣어 다닐 겁니다.","그리고... 혹시라도 발각되면 SI:7에서는 당신의 존재를 일체 부인할 것입니다."}, {"부두주임에게서 선적 일정표를 입수해 단장 마티아스 쇼에게 가져가야 합니다."}},
[2218] = {"구원의 길", {"인도받길 바라는 또 한 명의 길 잃은 영혼이시군? 당신은 목표를 찾고 싶은 거겠지. 난 영웅이 되고자 임무를 찾는 젊은이들을 천 명은 더 보았을 거요.","그나저나 오늘 운이 좋군, 젊은 친구. 이 호그럴 님이 당신의 갈증을 해소해 줄 수 있으니 말이오.","자, 나가서 길을 따라 아이언포지로 가서 쓸쓸한 뒷골목으로 가시오. 거기에 도착하면 상급 도적 훌프단 블랙비어드 님과 대화를 나누는 게 당신이 할 일이오. 그분이 당신을 구원의 길로 이끌어 주실 거요."}, {"아이언포지에 있는 훌프단 블랙비어드와 대화해야 합니다."}},
[2238] = {"순진한 핑계", {"알다시피, 마그니 폐하께서 채석장에서 광석과 보석을 죄다 캐내라고 명하셨네. 드워프가 열 번을 살아도 만져보지 못할 엄청난 양이지.","비밀 집회는 대단해. 우리는 귀금속의 가치를 평가한다네. 무슨 말인지 이해하겠나?","우리는 그 고약한 국왕이 우리가 보석을 빼돌린다는 사실을 눈치채지 않았으면 하네. 그 광석과 보석은 모두 나누어 가져야 한다고. 내 말이 무슨 뜻인지 알겠나?","가서 놈리건에 있는 오닌 맥해머와 얘기를 해보고 무슨 소식이든지 가져오게나. 그 드워프는 약삭 빠른 자라서 오염된 노움들 틈에 숨어있으니 조심하게."}, {"놈리건에서 오닌 맥해머를 찾아야 합니다."}},
[2239] = {"오닌의 보고서", {"채석장? 맞아, 그 괴물 같은 증기 전차가 이곳을 거의 매일 지나가지.","동쪽에 있는 내 연락책이 말하길 그 증기 전차는 아이언포지에서 출발해 골볼라 채석장에서 물건을 싣고, 카라노스에 잠깐 들러 연료를 채운 다음 무슨 이유 때문인지 멀리 돌아서 놈리건을 지나 아이언포지에 도착해 물건을 내려놓는다더군.","모두 이 보고서에 기록되어 있으니 훌프단께 가져가면 그분이 다음 조치를 취할 거요."}, {"아이언포지에 있는 훌프단에게 오닌의 보고서를 가져가야 합니다."}},
[2240] = {"비밀 석실", {"자, 그럼 임무 얘기를 하지.","카즈뮬의 석실이라는 비밀 석실이 있는데 그곳을 조사해야 하네. 문제는 이 방으로 들어가는 커다란 문이 잠겨져 있다는 것인데, 다행히 내가 그 문을 여는 방법을 안다네.","내가 이 탁자 위에 있는 일지에 모두 적어뒀지. 지금 나와 내 동료는 이곳에서 꼼짝할 수가 없어서 말이야... 그러니 자네가 우리를 대신해 카즈뮬의 석실을 찾아야 하네!","내 일지를 읽고 그 석실을 조사한 후 아이언포지에 있는 탐험가 연맹으로 가서 발굴조사단장 스톰파이크에게 보고하도록 하게."}, {"밸로그의 일지를 읽고 비밀 석실을 조사한 후 발굴조사단장 스톰파이크에게 보고해야 합니다."}},
[2241] = {"짝사랑", {"무슨 뜻인지는 잘 모르겠지만 주위 친구들이 나더러 '상사병'에 걸렸다고 하더군요.","그래요. 눈만 뜨면 언제나 시우르나 생각뿐이오. 집안 벽은 온통 시우르나의 초상화로 가득 차 있소. 그래요... 며칠씩 뜬 눈으로 밤을 지새우며 먹지도 마시지도 않고 우리가 못다 한 사랑을 슬퍼하지. 그게 그렇게 잘못된 것이오?","이제 그녀는 날 만나려고도 하지 않는단 말이오. 그렇게 사랑했던 나를! 그녀에게 서신을 보내려고도 해봤지만 도적이 아니면 그 누구에게도 말을 하지 않는단 말이오.","이 꽃을 그녀에게 좀 전해주지 않겠소? 이 잔노크가 보냈다고 말하는 것도 잊지 마시오."}, {"다르나서스에 있는 시우르나에게 잔노크의 장미를 가져가야 합니다."}},
[2242] = {"운명의 부름", {"텔드랏실의 숲에 일어나고 있는 일을 이해하는 데 도움을 줄 수 있는 존재가 있다고 합니다. 하지만 유감스럽게도 그 존재의 도움을 받으려다 모두들 엄청난 재앙만 겪었죠.","그는 신탁의 숲 주변에 있는 나뭇가지에 숨어 살고 있어요. 지금까지 온갖 방법을 사용해 봤지만 아무런 소용이 없고 그 사티로스에게 접근한 자는 모두 죽어버리고 말았답니다. 은신한 상태에서 접근하는 것이 해답일지도 모르겠군요.","고대정령 세시르의 가방에 무엇이 있는지 알아내서 내게 알려주세요."}, {"고대정령 세시르를 찾아 시우르나에게 가져갈 만한 어떤 단서든지 찾아와야 합니다."}},
[2258] = {"황야의 땅에서 재료 찾기", {"이보게, 자네만 괜찮다면 도움이 좀 필요하네. 나는 이름이 좀 알려진 연금술사인데 자네가 황야의 땅에서 재료 찾는 일을 도와주었으면 하네.","재료를 넉넉히 비축해 두어야 하는데 몇 가지가 부족해서 말이야. 대머리수리 모래주머니 5개, 바위산코요테 송곳니 10개, 바위 정령 파편 5개가 필요하네. 물론 짐승들에게서 직접 얻어와야 하는 것도 있으니 자네가 싸움에 익숙한 체질이 아니라면 싸울 줄 아는 친구들을 찾아 보도록 하게나.","이 물건들을 가져오면 돈을 좀 주겠네. 해보겠나?"}, {"황야의 땅에서 자칼 모스멜드에게 대머리수리 모래주머니 5개, 바위산코요테 송곳니 10개, 바위 정령 파편 5개를 가져가야 합니다."}},
[2259] = {"에리온 셰이드위스퍼", {"걱정 마시오. 당신을 시우르나에게 보내진 않을 테니까. 에리온이 세나리온 자치령에서 당신을 기다리고 있소. 서둘러 가 보시오!"}, {"다르나서스에 있는 에리온 셰이드위스퍼를 만나야 합니다."}},
[2260] = {"에리온의 부탁", {"그대의 공적에 대한 이야기가 자자하더군요. 사실 너무 멀리까지 퍼져 스톰윈드 첩보기관에서도 그대의 이름을 대며 찾고 있습니다.","고생스럽고 위험한 여행이 되겠지만 SI:7 밑에서 훈련받을 기회를 놓쳐서는 안 됩니다.","스톰윈드의 구 시가지로 가서 SI:7 본부에 있는 칼잡이 렌직과 얘기를 나누십시오. 새로운 세상이 그대를 기다리고 있습니다."}, {"스톰윈드로 가서 칼잡이 렌직에게 조언을 구해야 합니다."}},
[2278] = {"백금 원반", {"이 보물의 방 중앙에는 네 개의 거대한 백금 원반이 놓여 있는데, 각 원반은 근원을 알 수 없는 무수한 인장으로 꾸며져 있습니다. 이 원반을 손으로 건드리면 따끔거리는 느낌이 전해지면서 거대한 바위감시자의 모습이 앞으로 튀어나옵니다.","\"안녕하십니까. 저는 지식의 파수꾼입니다. 창조주의 토석인 창조와 관련된 정보가 이 노르간논의 원반에 기록되고 보관되어 있습니다.\"","당신은 이 세상에서 일찍이 발견된 적 없는 지식을 알게 된 것입니다!"}, {"바위감시자와 대화하고 그가 알고 있는 고대의 지식을 배워야 합니다. 지식을 배운 후에는 노르간논의 원반을 가동해야 합니다."}},
[2279] = {"백금 원반", {"노르간논의 원반을 만지자 다시 손이 따끔거립니다. 하지만 이번에는 원반 내부에서 이상한 마찰음이 들립니다.","가방이 갑자기 무거워져서 안을 보니 소형 원반이 들어있습니다!","분명히 아제로스의 고대 역사에 관심 있는 자들에게 아주 값진 물건이 될 것입니다. 얼라이언스에 널리 알려져 있는 아이언포지의 탐험가 연맹이 그러한 단체 중 하나입니다. 그들이라면 원반을 유용하게 사용할 수 있을 것입니다."}, {"아이언포지에 있는 탐험가 연맹에 노르간논의 소형 백금 원반을 가져가야 합니다."}},
[2280] = {"백금 원반", {"노르간논의 원반을 만지자 다시 손이 따끔거립니다. 하지만 이번에는 원반 내부에서 이상한 마찰음이 들립니다.","가방이 갑자기 무거워져서 안을 보니 소형 원반이 들어있습니다!","분명히 아제로스의 고대 역사에 관심 있는 자들에게 아주 값진 물건이 될 것입니다. 그런 자들이라면 이 원반에 관심이 있을 겁니다!"}, {"원반에 매우 관심이 있는 누군가에게 노르간논의 소형 원반을 가져가야 합니다."}},
[2281] = {"붉은마루 집결", {"지금 당신을 전장에 내보내는 것은 자살 행위요. 이 렌직은 또다시 도적을 잃고 싶지 않거든. 아.. 아, 이 렌직이 당신 실력을 부쩍 키워줄 테니 걱정마시오.","붉은마루 산맥으로 가서 루시우스라는 이름의 SI:7 요원을 찾으시오. 호숫골 부두에서 수상한 다른 녀석들과 함께 있을 거요."}, {"붉은마루 산맥의 호숫골로 가서 루시우스와 대화해야 합니다."}},
[2282] = {"앨더의 제재소", {"우리는 이곳에서 여러 해 동안 도적들을 훈련시켜 오고 있소! 어쨌거나, 당신 같은 악한들이 모두 이 늙은 루시우스 같은 품위와 솜씨를 갖고 태어나는 건 아니니까 말이외다.","여기서 길 위로 좀 떨어진 곳에 앨더의 제재소를 찾을 수 있을 거요. 난 그곳에서 안에 무언가 중요한 게 들어 있는 상자를 얻었지. 가서 그걸 나에게 가져오시오! 그 징표를 가지고 돌아오면 도적 증명서를 받게 될 거요!"}, {"루시우스의 금고를 열고 도적의 징표를 회수한 다음 호숫골에 있는 루시우스에게 가져가야 합니다."}},
[2283] = {"목걸이 회수", {"당신에게 줄 일거리가 있소. 배짱이 있다면 말이오!","황야의 땅 깊은 곳에 울다만 발굴현장이 있소. 최근 그곳에서 죽은 어떤 멍청한 성기사에 대한 얘기를 들었는데... 우리가 그 얘기에 관심을 갖는 이유는 그 성기사가 아주 값진 목걸이를 갖고 있었기 때문이지. 그 목걸이에는 루비, 사파이어, 토파즈 이렇게 세 가지 큰 보석이 박혀 있었다고 하오.","우리는 바로 그것을 원하오. 보수를 최고로 줄 생각이오. 파손됐을지도 모르지만 긁힌 흠집은 우리가 다 고칠 수 있으니까 말이오."}, {"울다만 발굴현장에서 값진 목걸이를 찾아 오그리마에 있는 드란 드로퍼스에게 가져가야 합니다. 목걸이는 파손된 것이라도 괜찮습니다."}},
[2284] = {"목걸이 회수 - 2차 시도", {"그 보석들이 울다만 어딘가에 있는 건 멍청이 말톤도 알고 있을 만큼 확실한 사실이오. 다시 돌아가서 찾아보도록 하시오.","전에도 말했지만 우리가 입수한 정보가 정확하다면 이 목걸이에는 루비, 사파이어, 토파즈가 붙어있었소. 보석들을 찾아서 목걸이에 다시 붙이도록 하시오.","보석들이 어디 있는 지야 누가 알겠소? 운이 좋으면 그 성기사의 유해에서 찾을 수 있을지도 모르지. 벌써 모두 털려버리지 않았다면 시체에 보석들이 남아 있지 않겠소?"}, {"울다만 깊은 곳에서 보석의 행방에 대한 단서를 찾아야 합니다."}},
[2298] = {"왕의 철저한 수색", {"그러니까 거기에서 3단계를 구상하려던 참이었는데 마그니의 얼간이 두 놈이 쳐들어와서는 강도가 어쩌고 하면서 비밀 집회 단원 몇을 잡아가더란 말이지.","하지만 이 노련한 블랙비어드는 아무에게나 잡히는 몸이 아니지! 말싸움이 거세지면서 자네 이름이 튀어나왔는데 말이야.","내가 충고 한 마디 할까? 아이언포지에서 그리핀을 타고 스톰윈드로 가게. 거기서 다시 구 시가지로 가서 칼잡이 렌직이라는 내 오랜 벗과 얘기를 해보게나."}, {"스톰윈드로 가서 칼잡이 렌직을 찾아야 합니다."}},
[2299] = {"훌프단에게로!", {"어디 갔다 온 거요? 훌프단께서 당신을 찾아 온 사방을 뒤지고 다녔단 말이오. 국왕이 당신을 감옥에 잡아넣은 건 아닌가 걱정하고 있었소.","당장 아이언포지로 가서 훌프단님과 얘기해 보시오!"}, {"아이언포지로 가서 훌프단 블랙비어드와 대화하십시오."}},
[2300] = {"SI:7", {"렌직이 당신을 찾고 있습니다.","나라면 마티아스의 부사령관을 기다리게 하지는 않을 겁니다."}, {"스톰윈드로 가서 칼잡이 렌직을 찾아야 합니다."}},
[2318] = {"일지 번역", {"이제는 책을 이해할 수 있는 언어로 번역하는 길밖에 없습니다. 번역을 할 수 있는 이들은 많이 있습니다. 하지만 가장 가까운 곳은 황야의 땅에 있는 카르가스 전초기지입니다. 그곳에서 찾아보기 시작하는 게 좋을 것 같습니다."}, {"성기사의 일지를 번역할 수 있는 이를 찾아야 합니다. 그런 자가 있을만한 가장 가까운 곳은 황야의 땅에 있는 카르가스입니다."}},
[2338] = {"일지 번역", {"드로퍼스 부자는 목걸이의 마법을 복원할 수 있을 만한 지혜가 없지만 나는 달라. 그리고 난 욕심 때문이 아니라 단지 목걸이를 연구해서 직접 만드는 법을 알아내고 싶은 것뿐일세. 그러니 내게 목걸이를 빌려주게. 자네의 목걸이가 필요하오. 그 대가로 내 그 일지를 번역해주겠네. 보석을 찾아 헤매느라 아까운 시간을 낭비하느니 내게 번역을 맡겨서 무엇을 해야 하는지 알아내는 게 어떻나?!","간단한 거래지. 아주 현명한 거래이기도 하고. 어떻게 할 텐가?"}, {"자칼에게 목걸이를 빌려주면 그에 대한 보답으로 일지를 번역해 줄 것입니다."}},
[2339] = {"보석과 마력원천 찾기", {"일지 여기 있네. 번역도 다 됐지.","내용을 보니 루비는 드워프가, 토파즈는 트로그가, 그리고 사파이어는 그림로크라는 트로그가 가지고 있다는군.","목걸이의 효력을 되찾으려면 마력원천이 필요한데, 드란 드로퍼스는 이 사실을 모르고 있지. 마력원천을 찾으려면 이렇게 하게. 울다만 깊숙이 들어가 강력해 보인다 싶은 피조물들은 모조리 물리치게. 그러다 보면 분명히 마력원천을 얻을 수 있을 걸세.","모든 걸 다 찾거든 내게 가지고 오고."}, {"울다만에서 목걸이에 필요한 보석 3개와 마력원천을 회수한 후 카르가스에 있는 자칼 모스멜드에게 가져가야 합니다. 자칼의 말에 의하면 울다만에서 가장 강한 피조물을 처치하면 마력원천을 얻을 수 있다고 합니다.","일지의 내용을 보니 루비는 어둠괴철로 방어진에 숨겨져 있습니다.","토파즈는 얼라이언스 드워프 근처에 있는 트로그 구역 중 하나에 있는 항아리에 숨겨져 있습니다.","사파이어는 트로그의 우두머리인 그림로크가 가지고 있습니다.","마력원천은 울다만에서 가장 강한 피조물을 처치하면 얻을 수 있습니다."}},
[2340] = {"보석 전달", {"재질이 같기만 하면 목걸이에 아무 보석이나 부속품을 써도 상관없지. 목걸이에 마법을 부여하려면 이 마력원천의 힘이 필요하다네. 아마도...","마력원천이 실제로 힘을 발휘한다면 자네에게 목걸이에 필요한 보석들을 기꺼이 내놓겠네.","오그리마로 가서 드로퍼스 부자 고물상과 거래를 끝내게. 우리 거래에 대해서는 아무 말 하지 말고... 자네가 돌아올 때쯤이면 아주 멋진 선물을 받을 수 있도록 준비해놓고 기다리겠네."}, {"오그리마에 있는 드란 드로퍼스에게 회수한 목걸이와 보석을 가져다주어야 합니다."}},
[2341] = {"목걸이 회수 - 3차 시도", {"드란 드로퍼스와 말톤 드로퍼스가 새로 들어온 고물을 두고 다투기 시작합니다. 이제 여기서 볼 일은 끝났습니다. 카르가스의 자칼 모스멜드가 줄지도 모를 보상을 받으러 황야의 땅으로 떠날 만반의 준비가 갖추어졌습니다."}, {"카르가스에 있는 자칼 모스멜드를 만나 그의 실험이 성공했는지 알아보십시오."}},
[2342] = {"보물 되찾기", {"가레트 가문의 사람이 이런 빗자루로 박쥐 우리나 쓸고 있다니 말도 안 돼요! 이 지긋지긋한 일로 모은 몇 푼 안 되는 돈을 당신에게 투자하겠으니 제 신분을 좀 올려 주세요.","우리 가레트 가문이 채굴꾼으로 열심히 일했을 때부터 울다만의 남쪽 대전당에는 가레트 가문의 무기가 든 궤짝이 있어요. 그게 없으면 전 영원히 이곳에서 이 지긋지긋한 박쥐들을 따라다니며 청소나 하게 될 거예요.","우리 가문의 보물을 찾아 제게 가져와 주세요! 제가 타고난 권리를 회복하도록 도와주세요!"}, {"울다만의 남쪽 대전당에 있는 궤짝에서 가레트가의 보물을 찾아 언더시티에 있는 그에게 가져가야 합니다."}},
[2358] = {"네즈라의 뿔", {"몇 년 전, 라벤홀트 장원에서 네즈라의 뿔이 도난당했소. 나는 대륙과 바다를 가로지르며 수색을 했지만 이 보잘것없는 야수들이 계속 갖고 있었다는 사실을 알아내는 데 그쳤소.","거치적대는 검은바위 오크를 모두 해치우고 네즈라의 뿔을 찾아 호숫골로 날 찾아오시오.","일이 잘 되면 당신의 이름이 라벤홀트가에 알려지게 될 거요."}, {"호숫골에 있는 아란티르에게 네즈라의 뿔을 돌려줘야 합니다."}},
[2359] = {"클라벤의 탑", {"저는 이 탑에서 몇 주째 잠복을 하고 있었습니다. 최근 그늘숲에서 많은 짐이 도착했는데 그와 함께 경비병들도 오더군요.","그들이 무슨 종족인지는 모르겠어요. 아마 당신도 마찬가지겠지만 한가지 확실한 것은 그들은 인간이 아니라는 거예요.","어쨌든, 그 짐 중에는 궤짝도 있었어요. 아마 저 흉측한 것들은 그 궤짝을 지키는 것이겠지요. 저들 중 얼굴에 못이 잔뜩 박혀 있는 좀비 녀석이 우리에게 필요한 열쇠를 지니고 있어요.","그 열쇠를 얻어 탑으로 들어간 다음, 궤짝에 들어 있는 것이 무엇인지 알아내어 마티아스 님께 돌아가서 보고하도록 하세요."}, {"데피아즈단 탑 열쇠를 훔쳐 데피아즈단 탑으로 숨어들어 간 후, 그늘숲 궤짝에 무엇이 들어 있는지 밝혀내야 합니다. 알아낸 모든 정보를 스톰윈드에 있는 마티아스 쇼에게 알려줘야 합니다. 켈넌의 보고서을 읽고 탑에 대한 정보를 얻으십시오."}},
[2360] = {"마티아스와 데피아즈단", {"때를 딱 맞춰 왔군. SI:7 정찰대의 보고에 의하면 서부 몰락지대에서 데피아즈단의 활동이 보통 때와 달리 잦아지고 있다고 하네.","감시의 언덕 남동쪽에 있는 버려진 얼라이언스 감시 탑을 점령하여 작전 기지로 사용하면서 붉은마루 산맥 보급로로부터 물자를 밀수하는 모양이야.","서부 몰락지대로 가서 탑 근처에 잠복하고 있는 첩보원 켈넌을 만나 보고를 받도록 하게.","그리고 말일세... 혹시라도 자네가 발각되면 SI:7에서는 자네의 존재를 일체 부인할 것일세."}, {"서부 몰락지대로 가서 첩보원 켈넌을 찾아 보고를 받아야 합니다."}},
[2361] = {"목걸이 복원", nil, nil},
[2378] = {"검은손 도적단을 찾아서", {"지금까지 자네는 죽음의추적자들을 위해 열심히 일해 왔네만 이제는 저 밖으로 모험을 떠날 때가 된 것 같군. 이제까지는 은신과 속임수에 소질을 보여 왔지만 아직 더 배워야 할 게 많다네.","죽음의추적자들에게는 검은손 도적단 내부에 동지들이 있네. 우리를 도와줄 이를 찾게. 바로 오그리마에 있는 셴툴일세."}, {"오그리마로 가서 어둠의 틈에 있는 셴툴과 대화해야 합니다."}},
[2379] = {"잔도잔", {"당신의 결심만으로는 우리 앞에 놓인 난관을 극복하기엔 부족하다.","잔도잔을 만나 검은손 도적단 요원으로서의 훈련을 받도록.","은신술과 도적술을 깨우쳐야 비로소 당신의 운명을 향한 다음 발을 내디딜 수 있다."}, {"어둠의 틈에 있는 잔도잔과 대화하십시오."}},
[2380] = {"오그리마로!", {"당신이 은신술과 속임수의 달인은 아니지만 그런 거야 바뀔 수도 있고 또 분명 바뀌게 될 것입니다. 아직은 덜 익은 솜씨로나마 윗사람들을 상당히 놀라게 한 모양이군요. 위대한 셴툴이 직접 당신의 이름을 대며 찾고 있는 걸 보면 말입니다.","여기서 북쪽으로 나 있는 길을 따라 오그리마로 가서 어둠의 틈에 있는 셴툴과 얘기해 보시기 바랍니다."}, {"오그리마의 어둠의 틈에 있는 셴툴과 대화하십시오."}},
[2381] = {"약탈자 약탈하기", {"어이, 겁 주려는 게 아니야. 하지만 남쪽바다 해적단이 숨겨놓은 보물을 훔쳐 오라고 보낸 다섯 명의 도적이 모두 갈가리 찢겨 되돌아왔단 말이지.","해안을 따라 남쪽으로 가다 보면 해적선 타이드 레이저호가 보일 거야. 바로 그 안에 남쪽바다 해적단의 보물이 들어 있지. 그 배 2층에는 녀석들이 도적들을 길러내는 해적 소굴이 있어.","올라가서 보물을 훔쳐서 가져오라고. 도적 기술은 해적의 소굴에서 배울 수 있을테지만, 조심해야해!"}, {"톱니항에 있는 비운의 레닉스에게 남쪽바다해적단 보물을 가져가야 합니다. 레닉스의 기계장치에서 E.C.A.C.와 도둑 도구를 얻는 것도 잊지 마십시오. 임무를 완수하려면 이 두 가지가 모두 필요합니다.","유별나게 공격적인 앵무새에게 맞서게 되면 E.C.A.C.를 사용하십시오!"}},
[2382] = {"톱니항의 레닉스", {"이 잔도잔은 보았다! 암, 그 날은 온다니까! 스랄이 죽는 날... 등에는 비수가 꽂히고 독이 핏줄을 따라 퍼지면서... 잔도잔이 말을 너무 많이 한 것 같군.","<race>, 잔도잔이 당신에게 줄 임무가 있다! 이제 십자로 동쪽에 있는 톱니항에 가서 비운의 레닉스와 얘기를 하도록!"}, {"불모의 땅에 있는 톱니항으로 가서 비운의 레닉스와 대화해야 합니다."}},
[2383] = {"간결한 양피지", {"자네가 없는 동안 양피지가 하나 도착했네, <name>.","시간이 있다면 읽어보게. 내가 착각한 것이 아니라면 이건 상급 전사, 프랑이 보낸 걸세. 준비되는 대로 그에게 가보게. 이야기를 나누고 싶어할 테니... 동굴 밖에 있는 그늘에서 자네를 기다리고 있을 거야."}, {"간결한 양피지를 읽고 시험의 골짜기에 있는 프랑과 대화해야 합니다."}},
[2398] = {"길 잃은 드워프", {"울다만에 있는 발굴현장이 항상 침략을 당한 건 아니오. 한때는 우리 아이언포지 드워프들이 그 지역을 다스렸었지. 하지만... 그건 트로그 녀석들이 오기 전이었소. 엄청난 숫자의 트로그들이 갑자기 밀어닥치는 바람에 우리 드워프들도 녀석들을 막아내지 못했소이다!","사실 너무나 갑작스러운 공격에 우리 주민 일부가 빠져나오지 못하고 적의 경계선 너머에 갇혀있는 것 같소.","트로그들이 나타나기 바로 전 내 친구 밸로그가 드워프 몇을 데리고 발굴현장 깊이 들어갔다오. 만약 살아있다면 당신이 그들을 찾아주기 바라오."}, {"울다만에서 밸로그를 찾아야 합니다."}},
[2399] = {"싹튼 잎", nil, nil},
[2418] = {"마법석", {"얼마 전 어둠괴철로 드워프들과 만난 일이 있지. 그들은 나를 고통의 요새로 데리고 가더니 울다만에서 캐낸 두 개의 이상한 마법석을 연구하게 하더군. 나는 그 마법석을 잘 다루기만 한다면 지속적이고 안정된 마력의 원천으로 사용할 수 있다는 것을 알게 됐지. 그 오래된 발굴현장에서는 별 걸 다 찾게 된다니까!","그건 그렇고, 내가 그 마법석의 비밀을 말해줬더니 녀석들은 날 고통의 요새에서 쫓아내고 울다만에서 더 많은 마법석을 찾기 시작했지.","나도 그 돌을 갖고 싶다고! 그 두 가지 마법석을 모두 가져오면 후하게 사례하지!"}, {"황야의 땅에 있는 리글퍼즈에게 덴트리움 마법석 8개와 안알레움 마법석 8개를 가져가야 합니다."}},
[2438] = {"에메랄드 꿈흡입기", {"저는 한때 바네실 지하굴에 사는 드루이드의 수호자 게롤라스 탈베드렌에게서 에메랄드 꿈흡입기를 받았었습니다. 이 강력한 아뮬렛은 에메랄드의 꿈이 가진 에너지를 빨아들일 수 있기 때문에 지니고 다니는 이에게는 행운이 따르지요.","그런데 안타깝게도... 그걸 별바람 마을에 있는 옷장에 두고 왔습니다. 별바람 마을도 한때는 평온한 곳이었지만, 지금은 그곳에 사는 펄볼그가 타락하는 바람에 몰락해버렸습니다.","혹시 제 꿈흡입기를 찾아와주시지 않겠습니까?"}, {"에메랄드 꿈흡입기를 회수해야 합니다."}},
[2439] = {"백금 원반", {"이 원반은 엄청난 발견이라네. 이 원반에 든 지식이 정확하다면 고대 티탄, 그러니까 창조주께서 우리의 마법적 창조와 연관이 있다는 말이 되네! 이 원반에 대해 가능한 많이 조사해 봐야겠네.","이렇게 훌륭한 영웅적인 일을 해준 자네에게 보답을 해야겠지. 은행에 있는 디니타 스톤맨틀에게 이 사례 보증 서류를 가져다주면 원하는 보상을 받을 수 있을 걸세!","준비가 되면 나한테 곧장 돌아오게. 탐험가 연맹을 위해 해야 할 일이 잔뜩 있네!"}, {"아이언포지 은행에 있는 디니타 스톤맨틀에게 사례 보증 서류를 가져가야 합니다."}},
[2440] = {"백금 원반", {"우리는 변치 않는 자세로 대지의 비밀을 알아내기 위해 정진해야만 하네. 내 힘을 이 원반에 집중하도록 하겠네. 때가 되면 진정한 원리가 밝혀지겠지.","이 원반을 내게 가져와준 데 대한 사례를 하도록 하지. 썬더 블러프의 숙련 연금술사인 베나 윈터후프에게 이 사례 보증 서류를 가져가게. 그러면 그곳에 있는 내 물건 중에서 하나를 고르도록 해줄 걸세.","그리고는 나한테 곧장 돌아오게. 이번 발견과 관련해서 해야 할 일이 매우 많을 것 같군그래."}, {"썬더 블러프에 있는 베나 윈터후프에게 사례 보증 서류를 가져가야 합니다."}},
[2458] = {"잠입", {"투자개발회사라고... 혹시 들어 본 적 있나?","난 그 회사 내부에 요원 하나를 심어두고자 1년간 공을 들였다네. 신중한 계획을 세워 꾸준히 노력한 덕분에 성공은 했지.","그런데 최근 투자개발회사에서 뭔가 대규모 사업을 계획하고 있다는 정보가 입수됐네. 진흙 늪 북쪽에 있는 투자개발회사로 가서 우리 측 비밀 요원인 감독관 피줄을 만나보게.","탑을 찾으면 이 신호용 권총을 사용한 후 피줄에게 다가가서 으스러진 손 /경례를 하도록."}, {"진흙 늪 북쪽에 있는 투자개발회사 탑으로 가서 으스러진 손의 비밀 요원인 감독관 피줄과 접촉해야 합니다.","탑이 보이면 신호용 권총을 사용해 감독관 피줄에게 당신이 도착했음을 알리십시오. 신호탄은 2번 발사해야 하며, 그 후 감독관 피줄에게 다가가 으스러진 손 /경례를 해야 합니다.","피줄은 자기가 모르는 자는 무조건 처치하려 들기 때문에 정확한 순서대로 수행해야 합니다."}},
[2459] = {"꿈을 먹어치우는 페로시타스", {"에메랄드... 에메랄드가 사라졌습니다! 제 꿈흡입기가 손상되었단 말입니다!","별바람 마을 북쪽에는 나무옹이 비술사 무리가 삽니다. 꿈을 먹어치우는 페로시타스는 우두머리인데 밤에 녹색으로 빛나는 목걸이를 걸고 다닌다더군요. 제 꿈흡입기를 생각해보니 놈이 에메랄드를 훔쳐간 것이 분명합니다... 에메랄드의 힘이 자기에겐 아무짝에도 쓸모가 없다는 사실을 꿈에도 모르겠지요.","잃어버린 보석을 찾아주십시오, <name>. 그리고 간 김에 타락한 비술사도 제거해주시기 바랍니다."}, {"나무옹이 비술사 7명을 처치하고 잃어버린 보석을 찾아야 합니다."}},
[2460] = {"검은손 도적단 경례", {"검은손 도적단의 도적이라면 검은손 도적단 /경례를 반드시 배워두어야 한다. 당신이 도적단의 일원임을 증명할 수 있는 유일한 수단이니까 말이지.","보고 배운 뒤 반복해서 연습하도록."}, {"셴툴에게 검은손 도적단 /경례를 해 보이십시오."}},
[2478] = {"미션 임파서블: 잘하면 가능", {"현장감독 실릭시즈에게서 탑 열쇠를 훔쳐내시오. 나중에 꼭 필요할 테니 열쇠를 손에 넣으면 잘 지니고 있으시오.","이 탑에는 독이 퍼져 모든 것이 약해져 있다는 사실을 잊지 말고 이 탑에 사는 놈들을 인정사정 볼 것도 없이 처치하시오!","반드시 갤리윅스를 암살하고 그 증거로 머리카락을 가져오시오. 그런 다음 그의 금고를 열고 잔질의 혼합물을 훔치시오. 갤리윅스의 머리카락과 혼합물을 오그리마에 있는 셴툴에게 가져가시오."}, {"현장감독 실릭시즈에게서 실릭시즈의 탑 열쇠를 훔쳐낸 다음, 돌연변이 투자개발회사 일꾼 2명과 투자개발회사 순찰대원 2명, 그리고 투자개발회사 망꾼 2명을 처치해야 합니다.","총감독 갤리윅스를 암살하고 그의 머리카락을 손에 넣은 후, 갤리윅스의 금고를 열고 잔질의 변화된 혼합물을 훔쳐야 합니다. 그 혼합물과 갤리윅스의 머리카락을 오그리마에 있는 셴툴에게 가져다주고 다음 지시를 받으십시오."}},
[2479] = {"히노트의 도움", {"혼합물 견본을 타렌 제분소에 있는 서지 히노트에게 가지고 가도록!","당신을 치유할 수 있는 자가 있다면 그건 바로 서지일 것이다. 그곳까지 살아서만 도착할 수 있다면 서지가 독극물 제작 방법과 사용법을 가르쳐 줄 것이다."}, {"언덕마루 구릉지의 타렌 제분소로 가서 서지 히노트에게 잔질의 혼합물 견본을 전해야 합니다.","타렌 제분소로 가려면 먼저 비행선을 타고 언더시티로 가야 합니다. 도착하면 남쪽 길을 따라 은빛소나무 숲을 지나 언덕마루 쪽으로 가야 합니다. 표지판을 따라 가십시오!"}},
[2480] = {"히노트의 도움", {"독에서 이렇게 놀라운 효과가 나오는 건 처음 보는군요. 잔질이라고 했습니까? 들어본 적은 없지만 이 독을 만들어 낸 실력만큼은 칭찬해주고 싶군요.","그러니까 내가 이 독에 걸린 당신을 치료해주고 이 독을 어떻게 사용하는지 가르쳐줘야 한단 말입니까?","이 독을 조사할 동안 잠시만 기다리도록 하세요."}, {"서지 히노트가 해독제를 만드는 동안 기다려야 합니다."}},
[2498] = {"데날란에게 돌아가기", {"알아메스 호수에 있는 데날란이 나무괴물들에게 퍼지고 있는 종양의 원인을 알아냈다는 소식을 방금 들었소. 가서 내가 보내더라고 말하고 얘기를 나눠 보시오.","서두르시오. 그는 우리의 도움을 필요로 하고 있으니까."}, {"렐리안 그린스파이어가 알아메스 호수에 있는 데날란과 대화해 달라고 했습니다."}},
[2499] = {"오크나무괴물", {"이제 알아냈습니다! 그대를 찾으려고 여기까지 급하게 뛰어 왔습니다! 정말 간단한 일이었습니다... 타락이 나무괴물에서 나무괴물로 퍼져 나갔던 이유는 단 하나였습니다.","오크나무괴물 말입니다. 이 괴물은 지금까지 한 번도 본 적 없는 크기의 종양 때문에 중독되었습니다.","오크나무괴물은 호수 근처의 동굴에 살았었지만, 그 후 이곳 북동쪽의 외딴곳으로 갔습니다. 오크나무괴물을 찾아 종양을 모아오십시오. 부디 제 고향에서 악의 근원을 제거해주시기 바랍니다."}, {"오크나무괴물에게서 거대한 종양을 모아야 합니다."}},
[2500] = {"황야의 땅에서 재료 찾기", {"<class>|1이여;여;, 당신의 도움이 필요하오. 나는 이름이 제법 알려진 연금술사인데 당신이 황야의 땅에서 재료 찾는 일을 도와주었으면 하오.","재료를 넉넉히 비축해 두어야 하는데 몇 가지가 부족해서 말이오. 대머리수리 모래주머니 5개, 바위산코요테 송곳니 10개, 바위 정령 파편 5개가 더 필요하다오. 물론 짐승들을 죽여서 직접 얻어와야 하지. 그러니 맞서 싸우는 체질이 아니라면 싸움 꽤나 할 줄 아는 친구들을 찾아보도록 하시오.","재료들을 찾아오면 수고비를 좀 주도록 하지... 어때, 해보겠소?"}, {"황야의 땅에서 필요한 재료를 구한 후 텔사마에 있는 가크 힐터치에게 가야 합니다."}},
[2501] = {"황야의 땅에서 재료 찾기", {"<name>, 당신은 지금까지 내게 아주 큰 도움이 되었소. 그 보상으로 전에도 한번 당신에게 준 적 있는 물약의 조제법을 드리겠소. 단, 위험한 임무 한 가지만 완수해 주면 말이오.","이 금고에는 빈 요술 유리병 3개가 들어 있소. 화염의 수호비룡의 피를 빨아들일 수 있는 조화의 오라가 담겨 있는데, 그걸 비룡에게 사용하면 크게 노할 테니 조심해야 하오. 그 유리병 3개를 다 채우거든 내게 가져오시오.","이 임무를 완수하면 조제법은 당신 것이 되는 거요."}, {"빈 요술 유리병을 황야의 땅에 있는 화염의 수호비룡에게 사용해야 합니다. 3개를 다 채우면 텔사마에 있는 가크 힐터치에게로 가져가야 합니다."}},
[2518] = {"달의 눈물", {"여왕거미 사스라는 한때 엘룬의 총애를 받았었지요. 우아하고 순수하게, 달빛 아래에서 저녁 안개를 머금고 은빛 실을 잣던 거미였어요. 그 은빛 이슬에는 강력한 치유력이 있어 근처 신전에 보관되었답니다.","하지만 최근 사스라는 광적으로 변해버렸는데 후손들마저도 그렇게 될 위험에 놓였습니다.","<name>, 여왕거미 사스라를 찾아 그 고통을 끝내주세요. 그녀는 여기에서 북동쪽에 사니 그리로 가서 처치한 다음 사스라의 은빛 돌기를 제게 가져다 주세요."}, {"여왕거미 사스라의 은빛 돌기를 입수해야 합니다."}},
[2519] = {"달의 신전", {"<race>님, 한때는 위대했던 숲의 생물들은 도움이 필요해요.","꼭 해결해야만 하는 어려운 임무가 있답니다.","아무라 여사제님을 찾아가 보세요. 그분께서 무슨 일인지 설명해주실 거예요. 여기서 남동쪽에 있는 달의 신전 안에서 그분을 찾아보세요."}, {"수녀 아퀴네가 달의 신전에 있는 여사제 아무라와 대화해 달라고 했습니다."}},
[2520] = {"사스라의 제물", {"이제 당신에게 주어진 임무도 완전히 끝나가는군요. 이 사스라의 돌기를 엘룬께 제물로 바치세요. 그 위대한 거미가 편히 쉴 수 있도록...","이 신전 중앙에 분수대가 있는 샘에 제물을 두고 오세요. 그 안의 성수가 사스라를 미치게 만들었던 타락을 정화해 줄 거예요.","이 일을 다 끝내거든 다시 제게 오세요."}, {"여사제 아무라가 달의 신전 중앙에 있는 샘에 사스라의 은빛 돌기를 제물로 바치고 돌아와 달라고 부탁했습니다."}},
[2521] = {"굼이샤의 지시", {"<race>의 그 조그마한 두뇌로 귀하디 귀한 드레니시스트 구슬의 완전무결함을 어찌 이해할 수 있겠어요? 드레나이는 이 결정 하나를 찾아 헤매는 데 평생을 바치다 결국 목적도 이루지 못한 채 허무한 최후를 맞이 했어요.","자, 이제 이 굼이샤가 무얼 원하는지 알고서도 계속 도와주겠다고 나서는 건가요? 그렇다면 좋아요... 이 땅을 돌아다니다가 완전무결한 드레니시스트 구슬을 찾으면 내게로 가져오세요. 그 대가로 꿈도 꾸지 못할 부와 명예를 누리게 해드리죠."}, {"저주받은 땅을 돌아다니다가 완전무결한 드레니시스트 구슬을 발견하면 수집가 굼이샤에게 가져가야 합니다.","이 보석은 아제로스를 통틀어 가장 희귀한 수정 중 하나로써 이곳 생물들이 지니고 있을 가능성도 있습니다.","완전무결한 드레니시스트 구슬을 수집해 갈 때마다 각각에 대해 보상을 받게 됩니다."}},
[2522] = {"굼이샤의 노력", {"비록 차원문을 열어 고향으로 돌아가는 데는 실패했지만 다음번에는 꼭 성공할 거예요.","드레나이의 소망이란 싸구려 보석을 사랑하는 당신들의 욕심만큼이나 강하죠. 이 저주받은 땅에서 다시 한번 완전무결한 드레니시스트 구슬을 찾아오세요.","물론 이 임무를 맡고, 안 맡고는 전적으로 당신에게 달렸죠! 훗..."}, {"완전무결한 드레니시스트 구슬을 찾아서 수집가 굼이샤에게 가져가야 합니다."}},
[2523] = {"오염된 노래꽃", nil, nil},
[2541] = {"잠자는 드루이드", {"이곳에 사는 나무옹이 주술사가 잠자는 드루이드의 영혼과 육체를 분리하는 방법을 발견했소. 그래서 펄볼그들이 내 육체를 마음대로 조종해 바네실 지하굴에 들어오는 자들을 공격한다오. 지금 나는 에메랄드의 꿈에 갇혀 이들을 막을 힘이 없다오.","나를 도와줘야만 하오. 나무옹이 주술사들이 지니고 다니는 기이한 부적이 이 의식에 사용되는데, 그걸 한번 살펴보고 싶소. <name>, 부탁이니 그 부적을 하나 가져다 주시오."}, {"바네실 지하굴에 있는 오벤 레이지클로에게 부두교 주술사 부적을 가져가야 합니다."}},
[2561] = {"발톱의 드루이드", {"<name>, 이 부적을 조사해보고 나니 무엇을 해야 할지 알겠소. 이걸 받고 내 말대로 해주시오.","이제 그대는 안식처 주변의 문으로 가야 하오. 그 안쪽에는 영혼을 잃어버린 내 육체가 있소... 이런 말을 하려니 마음이 아프지만, 나무옹이 일족의 지배에서 나 자신을 해방시킬 방법은 달리 없다오.","나무옹이 일족에게서 벗어나려면 그대가 내 육체를 처치해줘야만 하겠소. 그런 다음 쓰러진 육체에 부두교 부적을 사용하시오. 이 임무를 완수한 다음 다시 돌아와 주시오."}, {"영혼이 없는 오벤 레이지클로의 육체를 처치하고 그 시체에 부두교 부적을 사용해야 합니다."}},
[2581] = {"칼송곳니하이에나 턱", {"하이에나 턱이 엄청나게 강하다는 것은 누구나 알고 있는 사실이지. 그런데 두께가 한 뼘이나 되는 토륨 강철도 물어뜯을 수 있다면? 거짓말처럼 들리겠지만 내가 이 두 눈으로 직접 봤소이다.","저주받은 땅으로 가서 칼송곳니하이에나 턱 3개를 가지고 오시오. 그리고 눈을 크게 뜨고 멧돼지와 스콜포크도 잘 찾아보시오. 첫 가루약에는 손상된 멧돼지 허파 2개와 스콜포크 집게발 1개도 필요하니까 말이오."}, {"혈법사 드라지알에게 칼송곳니하이에나 턱 3개, 손상된 멧돼지 허파 2개, 스콜포크 집게발 1개를 가져가야 합니다."}},
[2582] = {"시대의 분노", {"아, <name>. 잘 돌아왔소. 칼송곳니하이에나에게서 얻은 엄청난 힘이 꽤나 즐거웠나 보군. 당장은 힘이 솟아나는 효과를 볼 수 있지만 장기적으로는 당신의 영혼과 지능에 치명적인 효과를 미칠 수 있소. 그러니 조심하시오.","분명 그 힘이 위험하다고 경고했는데도 또 얻고 싶단 말이오? 어쩔 수 없지... 다시 가서 칼송곳니하이에나 턱 3개와 손상된 멧돼지 허파 2개, 그리고 스콜포크 집게발 1개를 구해 오시오."}, {"혈법사 드라지알에게 칼송곳니하이에나 턱 3개, 손상된 멧돼지 허파 2개, 스콜포크 집게발 1개를 가져가야 합니다."}},
[2583] = {"멧돼지의 생명력", {"이 저주받은 땅에는 두 종류의 멧돼지가 살고 있소. 잿빛갈기멧돼지와 지옥멧돼지...","이 야수들은 체력이 너무 강해 아무리 공격해도 상처 하나 입지 않을 정도요. 그래서 다른 짐승들은 이들을 상대할 수 없다고 느끼고 감히 덤비지조차 못하고 있소.","이 멧돼지들을 더 분석해 봐야겠소. <name>, 당신의 도움이 필요하오. 내게 손상된 멧돼지 허파 3개와 스콜포크 집게발 2개, 바실리스크 뇌 1개를 가져다주시오. 그걸 분석한 다음 남은 걸로 신성한 비약을 만들어주리다. 그러니까, 당신이 나의 첫 실험 대상이 되는 거지!"}, {"혈법사 드라지알에게 손상된 멧돼지 허파 3개, 스콜포크 집게발 2개, 바실리스크 뇌 1개를 가져가야 합니다."}},
[2584] = {"멧돼지의 혼", {"내가 이 비약을 당신에게 처음 줬을 때부터 한두 개로는 당신의 욕심을 채울 수 없다는 것을 느꼈소. 지금도 당신의 두 눈에 굶주림의 빛이 역력하오. 이 약은 중독성이 아주 강해서 아무리 정신력이 강한 자라고 해도 이겨낼 수 없소.","당신에겐 허파즙 칵테일이 더 필요한 것 같군. 그러니 다시 저주받은 땅으로 돌아가 손상된 멧돼지 허파 3개, 스콜포크 집게발 2개, 바실리스크 뇌 1개를 구해 오시오."}, {"혈법사 드라지알에게 손상된 멧돼지 허파 3개, 스콜포크 집게발 2개, 바실리스크 뇌 1개를 가져가야 합니다."}},
[2585] = {"민첩한 스콜포크", {"녀석들이 싸우는 모습을 본 적 있소? 스콜포크 말이오. 나비처럼 우아한 자태로 날렵하게 움직이고 벌처럼 과감하게 공격하는 모습을 본 적이 있느냔 말이오.","그 민첩성을 추출해 알약으로 정제할 수만 있다면 우린 떼돈을... 아! 그러니까... 과학사에 큰 업적을 이룰 거란 말이지.","그러려면 그 약의 견본부터 만들어야 하는데... 용맹스러운 <race>|1이여;여;, 날 좀 도와주시오.","쓸만한 견본을 만들려면 스콜포크 집게발 3개, 독수리 모래주머니 2개, 손상된 멧돼지 허파 1개가 필요하오."}, {"혈법사 드라지알에게 스콜포크 집게발 3개, 독수리 모래주머니 2개, 손상된 멧돼지 허파 1개를 가져가야 합니다."}},
[2586] = {"스콜포크의 가루약", {"살아 있었소?! 이거야말로 경사로군. 스콜포크 가루약을 더 받으러 온 거겠지?","좋소, 저주받은 땅으로 다시 가서 스콜포크 집게발 3개, 독수리 모래주머니 2개, 손상된 멧돼지 허파 1개를 구해 가져오시오."}, {"혈법사 드라지알에게 스콜포크 집게발 3개, 독수리 모래주머니 2개, 손상된 멧돼지 허파 1개를 가져가야 합니다."}},
[2601] = {"바실리스크의 내장", {"이 황무지는 붉은바위 바실리스크 때문에 온통 벌집투성이가 되었습니다. 이 바실리스크는 이 지역에 서식하는 다른 어떤 야생 생물보다 적응력이 강해서 그 수가 많이 늘었어요.","먼 곳에서 녀석들이 사냥하고, 생활하는 모습을 지켜본 적이 있습니다. 놀랍게도 이 짐승들은 일부 인간형 생물보다도 높은 수준의 사고 과정을 보이더군요.","꼭 녀석들의 뇌를 조사해봐야겠습니다!","용맹스러운 <class>|1이여;여;, 녀석들의 뇌 10개를 가져다주세요. 그리고 물약의 기본 재료가 되는 독수리 모래주머니 두 개가 있어야 혼합물을 완성할 수 있으니 잊지 마십시오."}, {"혈법사 린노어에게 바실리스크 뇌 10개와 독수리 모래주머니 2개를 가져가야 합니다."}},
[2602] = {"흔들리지 않는 정신", {"아니, 벌써 집중력이 떨어지셨나요? 아직 연구에 필요한 재료를 더 구해야 해요!","아, 수고하신 대가는 물론 똑같이 드려야죠. 저주받은 땅으로 가서 붉은바위 바실리스크 뇌 열 개와 독수리 모래주머니 두 개를 가지고 오세요."}, {"혈법사 린노어에게 바실리스크 뇌 10개와 독수리 모래주머니 2개를 가져가야 합니다."}},
[2603] = {"독수리의 면역력", {"이 저주받은 땅에서는 정말 죽음이 날개라도 돋친 듯 활개를 치죠. 저는 검은 독수리들이 잔치라도 하듯 죽은 모험가의 시체에 모여 살점을 뜯고 뼈를 쪼아대는 것을 보아왔죠. 때로는 심하게 부패하고 질병으로 뒤덮인 시체를 먹는 것도 보았고요. 그래서 녀석들의 내부 기관이 어떻게 이 땅을 뒤덮은 질병을 견딜 수 있는지 궁금할 수밖에 없답니다.","용맹스러운 <race>|1이여;여;, 절 도와주세요!! 제 연구에는 모래주머니가 필요해요! 물약의 기본 재료인 칼송곳니하이에나 턱도 두 개 있어야 해요.","부탁한 물건을 가져오면 두 배로 보답해 드리겠어요."}, {"혈법사 린노어에게 독수리 모래주머니 10개와 칼송곳니하이에나 턱 2개를 가져가야 합니다."}},
[2604] = {"영적 지배", {"당신의 내면의 열정이 꺼져가는 것 같군요. 정신적인 행복감이 한 시간 정도 지속됐다고 했나요?","모래주머니 껌을 더 갖고 싶으시면 모래주머니를 가져다주셔야 해요. 다시 저주받은 땅으로 가세요. 그리고 모래주머니 열 개와 턱 두 개를 찾아 오세요."}, {"혈법사 린노어에게 독수리 모래주머니 10개와 칼송곳니하이에나 턱 2개를 가져가야 합니다."}},
[2605] = {"목마른 고블린", {"나에게 좋은 생각이 하나 있소. 여기서 남서쪽에 이슬먹는 덤불나무괴물이라는 희한한 녀석들이 산다고 들었는데, 녀석들은 특이하게도 몸 안의 샘에다 물을 저장할 수 있다는구먼.","그 샘 하나만 있으면 이 끔찍한 갈증을 해소할 음료를 만들 수 있을 거요! 나를 도와주지 않겠소?","선인장 주변을 찾아보시오. 그곳에서 이슬먹는 덤불나무괴물들을 찾을 수 있을 거요."}, {"가득 찬 이슬샘을 모아 가젯잔에 있는 마린 노겐포저에게 가져가야 합니다."}},
[2606] = {"맛의 달인", {"우웩! 무슨 맛이 이렇담! 이건 안 되겠는데...","잠깐, 뭐라고 했소? 아, 알고 있소. 포기는 하지 말라는 거지. 그럼 좋소.","보자... 아주 좋은 생각이 있소! 내 친구 스프링클에게 이걸 가져 가시오. 그녀는 아주 훌륭한 요리사인데, 가서 우리 사정을 얘기하고 도움이 필요하다고도 전해 주시오.","그러니까, 맛이 그렇게 이상하면 적어도 뭔가 톡 쏘는 맛이라도 있어야지... 안 그렇소?"}, {"마린 노겐포저가 가젯잔에 있는 스프링클과 대화할 것을 부탁했습니다."}},
[2607] = {"잔질의 손길", {"내가 기대했던 것보다 더 많은 정보를 가져온 것 같군. 분명 일이 이상하게 돌아가고 있네.","아, 자네가 걸린 독의 해독제 말인가? 흠... 지하실로 내려가서 닥 믹실픽실과 이야기해 보게. 그 친구가 도와줄 수 있을지도 몰라."}, {"병영 지하실에 있는 닥 믹실픽실과 대화해야 합니다."}},
[2608] = {"잔질의 손길", {"아, 이런. 당신 안색이 꼭 죽을 상이군. 아니지... 송장은 많이 봐왔지만 당신 얼굴은 상태가 더 안 좋아 보이는데... 그러니까 당신은 귀신보고 놀래 쓰러진 얼굴이라고나 할까...?","하하! 방금 내가 한 말 들었니, 노암? 그거 꼭좀 적어 두라고!","*콜록* 에헴, 그러니까... 내가 당신을 도와주려면 피를 좀 뽑아야 하겠소. 걱정하지 마시오. 아주 쪼오금... 많이 아플 뿐이니까... 치료 받을 준비가 되거든 /누워 주시오."}, {"닥 믹실픽실에게 검사를 받으려면 \"/누워\"야 합니다. 믹실픽실은 원인도 모르는 병의 치료법을 어떻게 찾을 수 있을까요?"}},
[2609] = {"잔질의 손길", {"만장일치로 결론이 났소. 당신은 '그것'에 걸린 것 같소. 그것 말이오.","다행히도, 내가 '그것'을 치료할 수 있소. 그러려면 필요한 게 몇 가지 있소이다. 수수한 야생화 한 묶음, 가연 약병 1개, 청동관 1개, 그리고 마지막으로 중요한 재료인 밝은 황록색 비단 실타래 1개라오. 늙은 엠마가 날 위해 '최고급 실타래' 상점에 비단 실타래 하나를 남겨 뒀을 거요. 운하를 따라가다 보면 그 상점을 찾을 수 있을 거요.","어서 서두르시오. 다 나을 때까지 아무도 건드리지 말도록 하시오. 아, 그리고 몽고메리 박사가 말하길 당신은 샤워를 좀 해야 한답디다. 내가 한 게 아니고 저 사람이 한 말이오."}, {"닥 믹실픽실에게 수수한 야생화, 가연 약병 1개, 청동관 1개, 그리고 밝은 황록색 비단 실타래 1개를 가져가야 합니다. '그것'은 저절로 낫지 않는 병인 것 같습니다."}},
[2621] = {"명예를 잃은 자", {"지금 내게는 후회밖에 남지 않았소. 나의 잘못된 판단이 처참한 결과를 낳고 말았소. 명령을 기다렸어야 했소! 그랬다면 저주받은 땅에서 우리를 기다리고 있던 공포를 미리 알 수도 있었을 텐데! 아니면 다른 곳으로 파견됐을지도 모르지. 결국 내 잘못이었소. 모든 게 내 잘못이야.","그렇기 때문에 나는 눈도 감지 못하고 여기에 서서 오지 않는 명령서를 기다리고 있소. 나는 꼭 알고싶소. 오지 않은 그 명령서도 받아야겠소.","스토나드에 파견사령관 루아그란 자가 살고 있소. 그를 찾아 어떻게 된 일이었는지 알아봐주시오."}, {"스토나드에 있는 파견사령관 루아그와 대화하십시오."}},
[2622] = {"잃어버린 명령서", {"명령서? 어째서 당신이 명령서를 찾는 거요?","지금 뭘 모르고 하는 소리 같은데, 당연히 난 명령서를 보냈다고. 그런데 내가 보낸 전령들이 그만 잔인한 멀록의 습격을 받았단 말이오. 모두 죽고 딱 한 명만이 살아남았지. 그 꼴을 보면 살아남았다고 하기도 어렵긴 하지만... 말도 거의 못하고 걸을 수도 없는 데다, 음식도 관을 통해서 먹는다오. 그 명령서가 어떻게 되었는지 알고 싶으면 벤고르와 얘기해 보시오. 이 근처 어딘가에 있을 거요... 그리고 <name>, 쓸데없는 간섭 같은 건 그만두시오. 그러다 크게 당할 수가 있으니..."}, {"벤고르와 대화하십시오."}},
[2623] = {"수다쟁이 늪지 멀록", {"파견사령관에게서 명령서를 받은 우리는 저주받은 땅...으로 가는 길에 습격을 당했소.","멀록... 수많은 멀록들... 그중 수다쟁이 늪지 멀록이라는 녀석이 내 손에서 명령서를 낚아챘고 내 머리에 창... 동쪽... 동굴...","<기절하는 벤고르>"}, {"대족장의 명령서를 회수해 호드 영웅의 넋에게 가져가야 합니다."}},
[2641] = {"스프링클의 비밀 재료", {"제가 꼬마였을 때는 언니가 요리를 도맡아 했었죠. 그런데 얼마나 맛이 없던지... 아예 먹을 수도 없는 음식도 있었어요. 저는 참다 못해 이른바 저만의 \"비밀\" 재료를 찾게 됐죠! 그걸 집어넣기만 하면 무엇이든지... 음, 정말 기절할 정도로 맛있어진다고요!","그게 뭐냐고요? 바로 제비수염버섯이에요. 이 구역질 나는 음료를 마실 만한 걸로 만들고 싶다면 동부 내륙지로 가서 작은 호수를 찾아보세요.","참! 제비수염버섯은 물속에서만 자란다는 것도 기억하시고요."}, {"가젯잔에 있는 스프링클이 제비수염버섯을 하나 구해 가지고 와 달라고 했습니다."}},
[2661] = {"마린에게 배달", {"여기 제비수염 가루를 받으세요. 마린에게 가져가서 나한테 고마워 하라고 전해 주세요! 이걸로 마린은 내게 큰 빚을 지게 된 거예요."}, {"스프링클이 가젯잔에 있는 마린 노겐포저에게 제비수염 가루를 가져가라고 부탁했습니다."}},
[2662] = {"노겐포저의 비약", {"자, 이제 이 가루를 이슬에 섞겠소.","잠시 후 다시 와주시오... 내가 멋진 노겐포저 비약을 완성할 테니까 말이오!"}, {"마린 노겐포저가 비약이 완성되길 잠시 기다렸다가 다시 말을 걸어 달라고 부탁했습니다."}},
[2681] = {"속박의 돌", {"당신 혼자 힘으로 라젤리크와 그 부하들을 상대한다는 것은 무리요.","실력 있는 모험가들을 모아 우리 부하들을 끝이 없는 고통의 나락에서 해방시켜 주시오! 모두 18개의 돌을 부수어야만 하오.","부디 용맹스럽게 싸우다 명예롭게 죽으시오."}, {"라젤리크의 하수인 9명, 세바인의 하수인 3명, 알리스타즈의 하수인 3명, 그롤의 하수인 3명을 해방시킨 후, 호드 영웅의 넋에게 가야 합니다. 돌 가까이 있지 않으면 임무에 실패하게 됩니다."}},
[2701] = {"과거의 영웅", {"자, 이 유물을 받으시오. 속세에서 멀어진 우리에겐 더 이상 쓸모가 없는 것이라오.","부디 불타는 군단과의 싸움에서 도움이 되기를 바라겠소."}, {"궤짝을 열고 보상을 받으십시오."}},
[2702] = {"과거의 영웅", {"<race> 친구, 당신은 그 숭고함과 영웅심으로 나에게 명예를 다시 안겨 주었소.","그리고 이제 저세상으로 간 그들의 명예도 되찾아 준 거요."}, {"하사관 선드 스플릿후프와 대화해야 합니다."}},
[2721] = {"키리스", {"모험의 장 하나가 막을 내리면서 또 다른 막이 오르고 있소.","내 부하 한 명의 행방이 아직 묘연하다오. 키리스 부관 말이지... 그는 저주받은 땅 어딘가에서 실종됐소.","전에도 얘기했지만 우리 둘은 알리스타즈에게 붙잡혀 감옥에서 고문을 받았었소. 하지만 불쌍한 키리스는 결국 의지력을 잃고 말았지. 내가 갇혀 있던 곳까지 고문하는 소리가 들렸소. 고통스러워하는 비명 소리가 동굴 전체를 울려 퍼졌지. 그것이 내가 본 키리스의 마지막 모습이오."}, {"부관 키리스가 어떻게 되었는지 알아봐야 합니다."}},
[2741] = {"고성능 자동부화기", nil, nil},
[2742] = {"갇혀 있는 린지", {"거기 <race> 친구!","린지가 도끼를 잃어버리고 여기 갇혔소. 제발 이곳을 빠져나가게 도와주시오. 당신 같은 <race>|1이;가; 우리 일족을 좋아하지 않는다는 건 알지만 이 린지가 이렇게 부탁하오! 여길 꼭 나가야 하오!","린지를 도와주는 마음 착한 <race>한테는 꼭 보답하겠소.","참, 그리고 나 린지가 전망대 절벽에 아무도 몰래 숨겨둔 것이 하나 있는데..."}, {"린지를 호위해 쿠엘다닐 오두막을 빠져나간 후 그가 동쪽 전망대 절벽에 숨겨두었다는 비밀 물건을 찾아야 합니다."}},
[2743] = {"어둠의 엄습", {"있는 사실 그대로 다 말하겠소. 시간이 없으니 바로 본론으로 들어가지.","악마의 군주, 라젤리크를 보호하는 마력의 3인조를 물리쳐야 하오. 이건 저주받은 영혼 하나를 해방시키는 일처럼 쉬운 일은 아니오.","<묘한 미소를 지어 보이는 키리스>","라젤리크의 심복들은 라젤리크가 손수 만든 아뮬렛을 삼등분하여 하나씩 목에 두르고 있소. 이것들이 있어야 가장 사악한 악마를 소환할 수 있지.","트레보르에게 돌아가서 라젤리크를 소환하는데 무엇이 필요한 지 말해주면 그가 더 자세히 얘기해줄 거요."}, {"호드 영웅의 넋에게 돌아가서 키리스로부터 알아낸 정보를 보고해야 합니다."}},
[2744] = {"악마사냥꾼", {"그 3인조는 라젤리크의 보호를 받고 있소.","라젤리크는 자신을 섬기는 대가로 그들에게 저주받은 땅에서 무한한 권한을 휘두를 수 있도록 해주었지. 아뮬렛 조각들이 그들에게 불멸의 힘을 주고 있소. 그 조각은 주인의 생명력에 맞게 조절되지. 맨몸으로 처치할 가능성은 거의 없소. 저주받은 땅에 머무르는 한 녀석들은 영원히 죽지 않기 때문이지...","당신을 도와줄 만한 자가 하나 있긴 하오. 아즈샤라로 가서 악마사냥꾼, 로라무스 탈리페데스를 찾으시오. 섬을 뒤져보면 될 거요."}, {"아즈샤라에 있는 로라무스 탈리페데스와 대화해야 합니다."}},
[2745] = {"성 잠입", {"흠. 재미있는 얘기지만 가볍게 여겨서는 안 될 얘기로군요. 좋습니다, 당신을 도와드리도록 하지요. 그런데 당신이 해야 할 일은 정의로운 행동이지만, 그러니까... 합법적이지 못하게 정의를 실현하는 것입니다. 끝까지 해내려고 한다면 말입니다.","라스코발과 마르존을 신속하게 처리해야 합니다. 할 수 있을 것 같습니까?","자, 결정하시기 바랍니다. 이 일을 하시겠다면 서둘러 성으로 가서 내 오랜 노움 친구 티리온을 찾으시기 바랍니다. 그 친구는 지금 2주째 라스코발을 감시하고 있답니다. 그 친구라면 당신을 도와줄 수 있을 겁니다."}, {"스톰윈드 성에 있는 티리온과 대화해야 합니다."}},
[2746] = {"변장에 필요한 물건", {"티리아스가 일을 더 빨리 진행하기를 원하는 거군? 좋아. 그렇게 하도록 하지. 하지만 난 아직 준비가 안 됐고 이건 알아두라고. 어려운 일은 내가 하겠지만 손을 더럽히는 일을 하는 건 당신이오. 알겠소? 좋아.","자, 계획을 실행하기 전에 우선 당신이 가지고 와야 할 물건이 몇 가지 있지. 난 저 첩보로봇을 이용해 근위병을 지나갈 수 있을만한 완벽한 변장술의 대가요.","비단이랑 사과 두 개를 가지고 오시오. 비단을 구하는 건 당신 몫이지만, 도시를 막 벗어나면 클라라의 조그만 농장이 있는데 그곳에 딱 알맞은 용도의 사과가 있다오."}, {"스톰윈드에 있는 티리온에게 비단 옷감 3개와 클라라의 싱싱한 사과 2개를 가져가야 합니다."}},
[2747] = {"비범한 알", nil, nil},
[2748] = {"좋은 알", nil, nil},
[2749] = {"평범한 알", nil, nil},
[2750] = {"상해버린 알", nil, nil},
[2751] = {"야만전사의 갑옷", {"오모쉬 집안은 20대째 망치를 들어 왔소. 그동안 다듬은 금속만 해도 십만 톤이나 되지. 하지만 불행히도 이 오로크가 이 전통의 맥을 끊게 생겼다오. 나는 오모쉬 집안의 수치요. 친척도 없는데다 자식을 낳지도 못했거든.","오모쉬 집안의 비법을 내 죽음과 함께 사라지게 할 수는 없소! 이 유산은 오모쉬 집안 사람이든 아니든 누군가 계속 이어가야 하오!","당신이 할 수 있다는 것을 이 오로크에게 증명해 주시오. 청동 무늬 팔보호구 2개, 청동 대검 2개, 날카로운 발톱 2개를 이 오로크에게 가져오시오. 얘기는 그다음에 계속하겠소."}, {"오그리마에 있는 오로크 오모쉬에게 청동 무늬 팔보호구 2개, 청동 대검 2개, 날카로운 발톱 2개를 가져가야 합니다."}},
[2752] = {"강철 견갑", {"당신 눈에는 열의가 있어 보이는군. 마치 화선지가 먹물을 빨아들이듯이 오모쉬 집안의 지혜를 흡수하고 있어.","앞으로 세 가지를 더 가르쳐줄 것이오!","모루로 돌아가 청동 전투도끼 4개와 청동 전쟁망치 4개를 만들어 이 오로크에게 가져오시오. 가열로의 뜨거운 열기 때문에 살이 익는 것처럼 느껴지겠지만 강철 견갑 만드는 법을 배우려면 꼭 구해와야 하오."}, {"오그리마에 있는 오로크 오모쉬에게 청동 전투도끼 4개와 청동 전쟁망치 4개를 가져가야 합니다."}},
[2753] = {"발 밑의 적", {"이제 두 가지만 더 가르쳐 주면 되오, <name>. 온몸이 쑤시는 것은 알겠지만 이 오모쉬 집안을 위해서 조금만 더 참아주시오!","이 오로크에게 녹색 철제 투구 4개, 녹색 철제 팔보호구 4개, 녹색 철제 다리보호구 2개를 가져오시오. 그러면 당신은 실력이 늘 것이고 당신의 적은 고통받게 될 것이오!"}, {"오그리마에 있는 오로크 오모쉬에게 녹색 철제 투구 4개, 녹색 철제 팔보호구 4개, 녹색 철제 다리보호구 2개를 가져가야 합니다."}},
[2754] = {"광란의 뿔", {"이제 한 가지만 더 가르쳐주면 되겠군. 성스러운 모루와 괴철로로 돌아가는 거요! 그리고 이 오로크에게 튼튼한 철제 마울 2개, 은도금한 청동 장화 2개, 은도금한 청동 건틀릿 2개를 가져오시오.","당신이 접근하면 그들의 눈에 공포가 서릴 것이오!"}, {"오그리마에 있는 오로크 오모쉬에게 튼튼한 철제 마울 2개, 은도금한 청동 장화 2개, 그리고 은도금한 청동 건틀릿 2개를 가져가야 합니다."}},
[2755] = {"오모쉬의 기쁨", {"이 오로크는 우는 게 아니오. 눈에 먼지가 들어갔을 뿐.","<눈물을 훔쳐내는 오로크>","이 오로크가 당신에게 가르쳐줄 오모쉬 집안의 마지막 비밀이 하나 남았소. 덕분에 이 오로크 행복하게 눈을 감을 수 있겠소.","오모쉬 집안에 대대로 내려오는 기쁨의 춤을 따라하면 철제 장갑을 만드는 법을 가르쳐 주리다!"}, {"오모쉬가 추는 기쁨의 춤을 보고 배워야 합니다."}},
[2756] = {"과거의 방식", {"전설의 시대에 이 아투르크는 적의 두개골을 박살 내고 뼈를 잘게 빻아 가는 실을 만드는 데 쓰곤 했지. 이제는 모두 옛날 얘기가 되어 버렸지만 말이야. 대족장님께서는 부족 간의 평화를 천명하시고 이제는 지친 눈으로 오래된 적을 바라보실 뿐이지!","믿기 힘들다는 건 나도 알지만 스랄 님의 뜻이 그런 걸 어쩌겠나.","넋두리는 이제 그만하세. 자네가 여기 온 목적은 뭔가 배우러 온 거겠지? 꼭 그렇게 될 걸세... 그전에 내게 강철 가슴보호갑 4개와 강철 투구 4개를 가져다주게. 자네에게 전쟁을 맛보게 해주지!"}, {"오그리마에 있는 무쇠모루 아투르크에게 강철 가슴보호갑 4개와 강철 판금 투구 4개를 가져가야 합니다."}},
[2757] = {"무법항으로!", {"이봐, 여기서 어슬렁대봤자 얻을 건 아무것도 없다니까.","내가 어떤 녀석을 아는데 발이 꽤나 넓은 놈이지. 지금쯤 무법항의 대장간 주위를 돌아다니고 있을 거야. 가서 이 배지를 보여주면 미스릴에 대해 모두 말해줄 거야.","그런 애들 물건은 기저귀 찬 아가들에게나 줘버리라고. 이제는 더 큰 물에서 놀아야지. 어쩔 텐가?","혹시 생각이 있으면 맥개번을 찾아봐. 그에게 이 표장을 보여주면 누가 보냈는지 알아볼 거야."}, {"무법항에 있는 맥개번과 대화하십시오."}},
[2758] = {"대장기술의 근원", {"내가 당신 나이 정도였을 때 드워프이신 갈반께서 나를 수하에 데리고 있으면서 방어구 제작의 도를 가르쳤지. 그분은 무법항으로 떠나기 전에 날더러 대장기술 솜씨를 향상시키려는 야망과 결단력을 갖춘 모든 이들을 가르치겠다는 약속을 하게 하셨소. 그래서 오랜 세월이 지난 지금도 이러고 있는 것이지.","그래, 이제는 당신이 배워야 할 때로군. 황금 미늘 팔보호구 6개를 만들어 오면 대장기술의 근원에 대해 가르쳐 주겠소."}, {"스톰윈드에 있는 쇠망치 행크에게 황금 미늘 팔보호구 6개를 가져가야 합니다."}},
[2759] = {"갈반을 찾아서", {"갈반님께서 무법항을 떠난 지는 오래되었지만 아직도 그의 동료가 마을에 남아 있소. 언젠가 갈반의 지도를 받을 만한 이가 나타날 것을 기다리면서 말이지.","이 미스릴 표장을 가지고 가서 무법항에 있는 맥개번에게 보여 주시오.","그곳까지 간 것만으로도 당신이 자격이 있다는 걸 알 거요. 난 당신을 믿소!"}, {"무법항에 있는 맥개번과 대화해야 합니다."}},
[2760] = {"미스릴 대장조합", {"무법항까지 오는 데 성공하는 신참들은 그리 많지 않소. 그 표장의 광채를 보면 아무리 착실한 대장장이라도 욕심이 생기게 마련이지. 어리석은지고... 그 표장 따위는 앞으로 보게 될 물건에 비하면 아무것도 아닌데!","전설의 대장장이, 갈반님을 찾아가 이 펜던트를 보여주시오. 그분은 여기서 한참 북쪽으로 줄구룹과 모쉬오그 오우거 소굴 사이에서 지내고 계신다오."}, {"가시덤불 골짜기에 있는 전설의 대장장이 갈반과 대화해야 합니다."}},
[2761] = {"대장기술과 채광", {"많은 대장장이들이 채광과 대장기술은 상관없는 일이라고 생각하지. 하지만 채광 없이 대장기술이 있을 수 있겠는가? 그렇지, 불가능한 일이지! 솜씨 좋은 대장장이는 기술 좋은 광부도 돼야 하는 법...","자네라면 해낼 수 있다는 것을 이 갈반에게 보여주게나. 철 주괴 40개와 미스릴 주괴 40개를 가져오면 이 갈반이 화려한 미스릴 바지의 제작 방법을 가르쳐 주지!"}, {"가시덤불 골짜기에 있는 전설의 대장장이 갈반에게 미스릴 주괴와 철 주괴를 각각 40개씩 가져가야 합니다."}},
[2762] = {"훌륭한 가짜배기 은", {"진은은 미스릴보다도 더 강하고 튼튼하다고들 하지. 이 갈반도 광물에 대한 이러한 분석에는 반대하지 않네. 하지만 진은은 이름과는 달리 진정한 것이 아니라네! 미스릴이 훨씬 더 고귀한 광물이지! 하! 그거야 어쨌든, 이번 수업을 받으려면 그 훌륭한 가짜배기 은을 가져와야 한다네.","내게 미스릴 주괴 40개와 진은 주괴 5개를 가져오게. 그걸 가져오면 화려한 미스릴 장갑의 비밀을 배우게 되거니와 진은이 가짜배기라는 것을 배우게 될 것일세!"}, {"가시덤불 골짜기에 있는 전설의 대장장이 갈반에게 미스릴 주괴 40개와 진은 주괴 5개를 가져가야 합니다."}},
[2763] = {"보석 채취", {"귀한 보석을 채취하는 것은 우리 작업에서 가장 중요한 일이라네. 황수정보다 더 귀한 보석은 몇 되지 않지. 우리가 만든 물건들에 부여하는 마법 속성은 이 황수정이 있을 때 크게 증폭된다네.","이 수업을 위해서는 미스릴 주괴 40개와 황수정 4개가 필요하네. 이 수업을 받고 나면 마법 보석학을 더 잘 이해하고 화려한 미스릴 어깨보호구를 만드는 방법을 알게 될 거야."}, {"가시덤불 골짜기에 있는 전설의 대장장이 갈반에게 미스릴 주괴 40개와 황수정 4개를 가져가야 합니다."}},
[2764] = {"갈반의 수제자", {"이 갈반과의 수업을 마칠 준비가 되었으면 가젯잔으로 가게나.","그곳에서 내 수제자, 트렌튼 라이트해머를 찾을 수 있을 걸세. 그 녀석이 화려한 미스릴의 도에 대해 많은 것을 가르쳐줄 게야. 아니면, 여기 남아서 배우든지!","참, 물론 가젯잔은 타나리스에 있다네!"}, {"가젯잔에 있는 트렌튼 라이트해머와 대화해야 합니다."}},
[2765] = {"고급 대장기술", {"자네가 내게 처음 왔을 때 나는 자네가 내 최고의 제자 중 하나가 될 줄 알아봤다네! 자네는 주저하지 않고 맥개번에게 마법 장신구를 내주었고, 그것도 단지 나를 만나기 위해서 그런 것이니 말일세. 대장기술에 대한 그런 맹목적인 믿음과 헌신은 보상을 받아야 마땅하지.","자네는 고급 대장기술을 터득했네. 자, 보상을 받게나!"}, {"전설의 대장장이 갈반에게서 보상을 받으십시오."}},
[2766] = {"OOX-22/FE 수색", {"금속으로 만들어진 이상한 알 모양의 장치를 찾았습니다. 손잡이 하나를 만지작거리다 보니 알이 작동하면서 노움 로봇 장치로 변합니다! 로봇 알 속에서 소음과 함께 목소리가 들려옵니다.","\"나는 오글소프 오브노티쿠스라고 하오, 내 유도 로봇이 불시착했는데 로봇을 찾아주면 사례를 하겠소. 이 탐지기를 로봇에게 가져가 주시오!\"","\"방금 로봇의 현재 위치를 산출해 봤는데 거친흉터 골짜기 근처의 동굴에 있소!\""}, {"거친흉터 골짜기에 있는 오글소프의 유도 로봇에게 조난 탐지기를 가져가야 합니다."}},
[2767] = {"OOX-22/FE 구출!", {"로봇에서 다시 노움의 목소리가 흘러나옵니다.","\"긴 활주로로 사용할 수 있을 만한 넓고 안전한 장소로 OOX-22/FE를 옮겨야겠어요. 로봇에 위장 기능이 있기는 하지만 다시 작동하려면 시간이 필요해요. 그 로봇을 현재 있는 곳에서 그러니까, 큰 도로로 호위해주세요. 그곳이라면 이륙하기에 충분할 거예요!\"","\"안전하게 호위하여 무법항으로 와 저에게 알려 주세요! 오글소프 오브노티쿠스, 이상!\""}, {"OOX-22/FE를 안전하게 호위하여 무법항에 있는 오글소프 오브노티쿠스에게 가서 보고해야 합니다."}},
[2768] = {"자동 탐사막대", {"하사관 블라이가 내가 아끼는 물건을 훔쳐갔소! 처음엔 빌려간다고 해놓고는 내 자동 탐사막대를 아예 훔쳐가 버린 거요!! 그 막대가 없으면 어떻게 새로운 위치를 찾아서 물웅덩이를 팔 수 있겠소?!","블라이를 찾아서 내 막대를 되찾아 주시오! 듣기로는 평판이 좋지 않은 일단의 모험가들을 이끌고 서쪽에 있는 줄파락으로 보물을 찾으러 갔다고 하오. 분명 거기서 블라이를 찾을 수 있을 거요.","이미 트롤들 녀석을 처치했다면 좋겠군... 막대를 쉽게 되찾을 수 있을 테니... 직접 싸워야 한다면 분명 아주 큰 싸움이 벌어질 거요."}, {"가젯잔에 있는 선임기술자 빌지위즐에게 자동 탐사막대를 가져가야 합니다."}},
[2769] = {"브라스볼츠 형제", {"브라스볼츠 형제를 만나 보셨나요? 칼림도어의 소금 평원에서 고블린들과 로켓 자동차 경주를 하면서 지내는데, 늘 그들 형제가 이긴답니다. 그리 놀랄 만한 일도 아닌 게 고블린 로켓 자동차는 워낙 폭발이 잦아서 결승선까지 가는 일이 드물거든요!","하지만 그렇다 해도 브라스볼츠 형제는 늘 자기들 로켓 자동차를 더 빨리 달리게 할 방법을 연구하고 있죠. 위즐 말로는 아주 희귀한 부품이 필요하다고 하더군요.","틀림없이 당신의 도움이 필요할 거예요. 위즐에게 가서 알아보세요."}, {"소금 평원에 있는 위즐 브라스볼츠와 대화해야 합니다."}},
[2770] = {"가즈릴라", {"타나리스에는 줄파락이라고 하는 트롤들의 모래 도시가 있죠. 깊숙이 들어가면 신성한 웅덩이가 하나 있어요. 트롤 녀석들은 이 웅덩이에서 커다란 괴물을 소환한답니다! 가즈릴라! 이 광포한 녀석은 비늘조차도 힘이 넘쳐 흘러요. 바로 그 힘을 제 차에 이용하고 싶어요!","가즈릴라의 전기 비늘을 가져와 주세요!","가즈릴라를 소환하는 법은 트롤이 철저히 비밀로 지키고 있으니 녀석과 맞서려면 먼저 트롤에게서 비밀을 캐내야 할 거예요."}, {"소금 평원에 있는 위즐 브라스볼츠에게 가즈릴라의 전기 비늘을 가져가야 합니다."}},
[2771] = {"지혜로운 스승", {"마치 스승님께서는 처음부터 계획이 있으셨던 것 같구먼. 자네가 배운 것은 내가 배우지 않았고 내가 배운 것은 자네가 배우지 않았어.","과연 스승님은 지혜로우셔. 우리는 서로 가르칠 수 있다네!","미스릴 코이프와 화려한 미스릴 어깨보호구 만드는 법을 가르쳐 주면 내가 화려한 미스릴 투구 만드는 법을 가르쳐 주지!"}, {"트렌튼 라이트해머에게 미스릴 코이프 2개와 화려한 미스릴 어깨보호구 1개를 가져가야 합니다."}},
[2772] = {"화려한 미스릴 장화", {"작년에 대장장이 선수권 대회를 본 자라면 모두 알고 있을 거야. 투자개발회사 주물 공장에서 대표 선수로 나온 비겁한 녀석이 내 다리를 걸어 넘어뜨리는 걸! 하지만... 후후, 아무도 내가 그 시합에서 우승할 줄은 몰랐겠지. 비밀은 바로 화려한 미스릴 장화라네!","올해에도 우승하려면 반드시 화려한 미스릴 바지를 만드는 법을 알아야 해. 왜냐하면 투자개발회사 벌목기가 출전을 하거든! 내게 바지 만드는 법을 가르쳐 주면 나는 장화 만드는 법을 가르쳐 주겠네!"}, {"트렌튼 라이트해머에게 견고한 미스릴 장화 2개와 화려한 미스릴 바지 1개를 가져가야 합니다."}},
[2773] = {"미스릴 천재", {"이 부근에서는 모두 나를 미스릴 천재라고 부르지. 전 세계에서 온 모험가들에게 화려한 미스릴을 공급해 주니까 말이야.","<목소리를 낮춰 속삭이며...>","하지만 내가 화려한 미스릴 장갑 만드는 법을 모른다는 건 아무도 모른다네. 쉿! 우리끼리 비밀이니까 꼭 지켜주게.","내게 화려한 미스릴 장갑 만드는 법을 가르쳐 주게. 그러면 내가 갈반의 슬하에서 배운 기술의 결정체를 보여줄 테니까. 바로 화려한 미스릴 흉갑의 비밀을 말일세."}, {"트렌튼 라이트해머에게 견고한 미스릴 흉갑 2개와 화려한 미스릴 장갑 1개를 가져가야 합니다."}},
[2781] = {"현상 수배: 칼리프 스콜피드스팅", {"범죄자 칼리프 스콜피드스팅을 현상 수배한다.","타나리스의 가젯잔 수자원개발회사는 사막유랑단 무법자들의 두목인 칼리프 스콜피드스팅을 처치하는 자에게 두둑한 현상금을 지급할 것이다. 범죄자 칼리프 스콜피드스팅을 아래 죄목으로 현상 수배하는 바이다.","1. 가젯잔 수자원개발회사 직원 살해!","2. 가젯잔 수자원개발회사 자원 탈취!","3. 가젯잔 수자원개발회사 자산 불법 점유!","칼리프 스콜피드스팅의 머리카락을 선임기술자 빌지위즐에게 가져오는 자에게는 엄청난 현상금을 지급할 것이다."}, {"칼리프 스콜피드스팅을 처치하고 그 증거로 그의 머리카락을 가젯잔에 있는 선임기술자 빌지위즐에게 가져가야 합니다."}},
[2782] = {"린지의 비밀", {"이 서판은 마른나무껍질 트롤 종족의 독특한 문양으로 온통 뒤덮여 있습니다. 린지의 비밀은 참으로 수수께끼입니다.","여기에서 아주 값진 물건을 당신이 책임지게 될 수도 있고 그렇지 않게 될 수도 있습니다. 궁지에 몰린 마른나무껍질 트롤은 온갖 수단 방법을 가리지 않을 테니까..."}, {"마른나무껍질 트롤에 관해서 누구보다 잘 알고 있는 저명한 학자인 오란 스네이크레이드라면 이 서판을 해독할 수 있을지도 모릅니다. 그녀는 언더시티의 마법 지구에 살고 있다고 합니다."}},
[2783] = {"사소한 말다툼", {"우리는 엉뚱한 적과 싸우고 있소.","우리가 저주받은 땅을 두고 오크족과 옥신각신하는 동안에 이 땅의 진정한 적이 그 힘과 수를 불려 가고 있소.","호드와 전쟁만 치르다가 어느 날 불타는 군단에 참패를 당하는 꼴을 그냥 두고만 봐야겠소? 백해무익이오.","서로 생각 차이는 제쳐 두고 호드 영웅의 넋과 이야기해 보시오. 저주받은 땅에 숨겨져 있는 진실을 알고 있을 테니까..."}, {"저주받은 땅과 늪의 경계에 있는 호드 영웅의 넋과 대화해야 합니다."}},
[2784] = {"명예 실추", {"<race>|1이여;여;, 내 얘기를 좀 들어 보게."}, {"호드 영웅의 넋이 하는 이야기를 들어야 합니다."}},
[2801] = {"슬픈 이야기", {"먼저 내가 지금부터 하는 이야기를 잘 들어 보시오."}, {"호드 영웅의 넋이 하는 이야기를 들어야 합니다."}},
[2821] = {"최고의 품질", {"나의 가죽세공 철학은 가능한 품질이 최상인 제품을 만들어야 한다는 것입니다. 이 점에는 절대 타협이란 있을 수 없지요. 품질은 중요하지 않다고 말하는 자는 던 모로에서 바닷가 별장을 팔려고 하는 자입니다!","얼마 전 고품질 가죽 재료를 찾다가 두꺼운 설인 가죽을 발견하게 되었습니다. 그 설인은 거친흉터 골짜기에 많이 서식하는 페랄라스의 토착민입니다. 그 가죽이 좀 필요한데, 두꺼운 설인 가죽 10개를 가져다주면 내 가죽세공품 중 하나를 주도록 하죠. 조건이 마음에 들면 거래하게 되는 겁니다."}, {"페더문 요새에 있는 프랫 맥그루벤에게 두꺼운 설인 가죽 10개를 가져가야 합니다."}},
[2822] = {"최고의 품질", {"나는 내 가죽세공술에 자부심이 있으며 내 기술이 증명되고 있다고 생각하네. 품질에 타협이란 절대 있을 수 없지. 품질이 중요하지 않다고 말하는 자는 대지가 비밀을 지킬 줄 모른다고 우기는 자나 마찬가지네!","얼마 전 고품질 재료를 찾다가 두꺼운 설인 가죽을 발견하게 되었지. 거친흉터 설인은 페랄라스의 토착민으로 대게 이곳의 서쪽, 거친흉터 골짜기에서 발견할 수 있네. 두꺼운 설인 가죽 10개를 가져다주면 내 가죽세공품 중 하나를 주겠네. 조건이 마음에 든다면 거래는 성립된 걸세!"}, {"모자케 야영지에 있는 장도르 스위프트스트라이더에게 두꺼운 설인 가죽 10개를 가져가야 합니다."}},
[2841] = {"기술 전쟁", {"노움의 기술이라... 2년 전만 해도 농담으로나 하던 소리였는데... 시대가 많이 변했죠. 이제 노움은 우리 벌목기를 다 모은 것보다 더 좋은 장비를 가지고 있어요. 녀석들의 설계도가 필요해요!","유감스럽게 그 설계도는 금고에 보관되어 있는데 기계박사 텔마플러그가 항상 근처를 지키고 있어요. 게다가 그만이 금고 암호를 알고 있답니다.","무슨 수를 써서라도 금고 번호를 알아내세요. 번호를 알아내면 금고를 열고 설계도를 가져오세요!"}, {"놈리건에서 장치 설계도와 텔마플러그의 금고 암호를 찾아서 오그리마에 있는 노그에게 가져가야 합니다."}},
[2842] = {"선임기술자 스쿠티", {"케르노비에게서 새 노움 장치에 대한 소식을 듣자마자 스쿠티와 나는 순간이동기 6000을 만들기 시작했습니다. 순간이동기 6000은 가장 빠르고 안전하게 놈리건에 가는 방법이랍니다.","드디어 승객들을 태울 준비가 됐군요. 한번 타보고 싶으시다면 무법항으로 가서 와이번 횃대에 있는 스쿠티와 얘기를 나눠보시기 바랍니다. 무법항으로 가려면 톱니항에서 배를 타야 한답니다.","물론 드워프가 득실거리는 던 모로의 산맥을 지나서 놈리건까지 뛰어가도 상관없지만요!"}, {"무법항에 있는 스쿠티와 대화해야 합니다."}},
[2843] = {"놈리거어어어언!", {"응답장치를 갖고 발판에 올라서는 즉시 놈리건으로 이동하게 될 거요.","부작용이 좀 생기겠지만 영구적인 것은 아니오.","아, 제일 중요한 걸 잊을 뻔했군. 귀환 말인데 돌아오려면 놈리건에 있는 순간이동기 발판에 올라서면 되지.","이제 응답장치를 당신의 키와 체중에 맞게 조절하겠소."}, {"스쿠티가 고블린 응답장치를 조절하는 동안 기다려야 합니다."}},
[2844] = {"거대한 수호자", {"난 쓸모없다. 내 친구 샤이를 지켜 주기로 했었는데 샤이가 날 떠나버렸다. 무슨 일이 일어난 건지 모르겠다. 샤이를 마지막으로 본 건 우리가 북쪽에 있는 폐허를 지나고 있을 때였다. 모든 게 다 괜찮았는데 뒤돌아보니까... 샤이가 없었다.","이제 뭘 어떡해야 할지 모르겠다. 무슨 좋은 방법 없을까? 샤이가 걱정된다.","샤이를 좀 찾아봐 줬으면 한다. 너는 나보다 훨씬 더 빨리 걸어다닐 수 있을 거다."}, {"길 잃은 나이트 엘프 소녀를 찾아야 합니다."}},
[2845] = {"방황하는 샤이", {"제 친구 무쇠이빨에게 절 다시 데려다 주지 않으시겠어요? 절 지켜 주기로 했었는데... 전 가끔 이리저리 헤매어 엉뚱한 곳으로 가는 경향이 좀 있거든요. 여기저기 볼 것도 너무 많고 해서 쉽게 주의가 산만해진답니다.","단 한 가지 항상 제 주의를 집중시키는 게 있긴 해요.","이 궤짝 안에, 제가 무척 아끼는 종이 있어요. 소리가 얼마나 예쁜지 몰라요. 이걸 드릴 테니 또 엉뚱한 곳으로 가 버리면 종을 울려주세요. 당신이 이 종만 울리면 다시 되돌아오겠다고 약속할게요."}, {"궤짝에서 샤이의 종을 집어야 합니다.","샤이 리프러너를 무쇠이빨의 야영지까지 호휘해야 합니다."}},
[2846] = {"심연의 티아라", {"오래전에 난 아름다운 보물을 하나 갖고 있었어요. 심연의 티아라라는 물건인데, 아주 아름다울 뿐만 아니라 제대로 사용할 줄 아는 이에게는 엄청난 힘을 주었죠.","그 소문이 유체술사 벨라타의 귀에 들어가자 욕심을 낸 거예요. 그래서 내가 집을 비운 사이에 부하들을 보내 훔쳐 가 버렸어요. 망할 놈의 도둑들 같으니!","내 티아라를 되찾고 싶어요! 타나리스에 있는 트롤 도시, 줄파락에 가서 벨라타를 찾아 그 티아라를 빼앗아 주세요. 그걸 갖다주면 보상을 해 드리겠어요."}, {"먼지진흙 습지대에 있는 타베사에게 심연의 티아라를 가져가야 합니다."}},
[2847] = {"야생 가죽 방어구", {"당신, 가죽 세공인으로서 솜씨가 점점 좋아지고 있군요! 당신이 흥미를 가질 만한 도안을 몇 가지 알고 있습니다. 바로 야생 가죽 방어구이지요! 야생 가죽은 풀이 무성하게 자라는 지역과 그곳에 사는 생물들에게서 볼 수 있는 야생덩굴의 강력하고 혼돈스러운 특성을 지니고 있습니다. 그걸로 만든 방어구는 당신 같은 솜씨를 가진 사람들에게는 최고의 것입니다. 내 보증하죠.","당신에게 이걸 가르쳐 주는 대가로 한동안 나와 함께 일을 해야 할 겁니다. 먼저 해야 할 일은 두꺼운 가죽 10개를 가져오는 겁니다. 더 자세한 얘기는 그 후에 하도록 합시다."}, {"페더문 요새에 있는 프랫 맥그루벤에게 두꺼운 가죽 10개를 가져가야 합니다."}},
[2848] = {"야생 가죽 어깨보호구", {"야생 가죽 어깨보호구는 착용하는 자의 골격을 아주 잘 받쳐 주기 때문에 팔 위쪽과 어깨를 보호하는데 최고라고 할 수 있습니다. 다른 야생 가죽 방어구와 마찬가지로 야생덩굴을 바느질해 붙이면 강력한 효과가 무작위로 더해지게 됩니다.","이 도안을 얻으려면 두꺼운 방어구 키트 6개와 야생덩굴 하나가 필요합니다. 당신한테 그리 어려운 임무는 아닐 겁니다."}, {"페더문 요새에 있는 프랫 맥그루벤에게 두꺼운 방어구 키트 6개와 야생덩굴 1개를 가져가야 합니다."}},
[2849] = {"야생 가죽 조끼", {"야생 가죽 조끼는 모든 야생 가죽 방어구 세트의 기본입니다. 조끼의 틀이 야생덩굴과 잘 섞여 조끼 전체에 강력한 마법이 흐르게 되기 때문에 몸통을 최대한으로 보호할 수 있답니다.","이 도안을 얻으려면 거북 껍질 흉갑 2개와 거북 껍질 장갑 2개, 그리고 야생덩굴 1개가 필요합니다. 단순한 임무는 아니지만 그리 어렵진 않을 겁니다."}, {"페더문 요새에 있는 프랫 맥그루벤에게 거북 껍질 흉갑 2개, 거북 껍질 장갑 2개, 야생덩굴 1개를 가져가야 합니다."}},
[2850] = {"야생 가죽 투구", {"야생 가죽 투구는 편안하면서도 머리의 중요한 부위를 아주 우수하게 보호해 줍니다. 모든 야생 가죽 방어구와 마찬가지로 여기에 포함된 야생덩굴이 투구의 기본틀에 강력한 효과를 무작위로 부여하게 되지요. 가죽 방어구를 착용하는 사람들은 틀림없이 이런 투구를 원할 겁니다!","이 도안을 얻으려면 밤하늘 튜닉 2개, 밤하늘 머리띠 2개와 야생덩굴 1개가 필요합니다. 지시대로 완수하면 이 도안은 당신 것이 될 겁니다!"}, {"페더문 요새에 있는 프랫 맥그루벤에게 밤하늘 튜닉 2개, 밤하늘 머리띠 2개, 야생덩굴 1개를 가져가야 합니다."}},
[2851] = {"야생 가죽 장화", {"지금까지 꽤 많은 기술을 배웠지만 아직 더 배울 게 있습니다. 다음은 모든 세트의 기초가 되는 야생 가죽 장화를 만들 차례입니다.","이 장화는 발을 편안하게 해 주고 위험한 것들로부터 발을 보호하도록 고안되었답니다. 부드러운 밑창은 움직일 때 소리가 나지 않는 신발을 찾는 여행자들에게는 아주 딱입니다.","이 도안을 얻으려면 밤하늘 바지 2개와 밤하늘 장화 2개와 야생덩굴 2개가 필요합니다. 이번엔 야생덩굴 1개가 아니라 2개입니다. 모두 다 구하거든 나에게 다시 오도록 하세요."}, {"페더문 요새에 있는 프랫 맥그루벤에게 밤하늘 바지 2개, 밤하늘 장화 2개, 야생덩굴 2개를 가져가야 합니다."}},
[2852] = {"야생 가죽 다리보호구", {"기본을 잘 익혔으니 이제 좀 더 복잡한 것을 배울 때가 되었군요. 야생 가죽 다리보호구는 가죽 장비 중에서도 가장 튼튼하고 강력한 것에 속한답니다. 허리, 허벅지, 종아리에 이르기까지 모두 강력한 보호를 받게 되는 겁니다. 야생덩굴에서 얻을 수 있는 강력한 효과까지 합치면 이걸 가진 모험가들이 당신을 볼 때마다 감사를 표하게 될 겁니다.","이 도안을 얻으려면 거북 껍질 투구 2개, 거북 껍질 팔보호구 2개와 야생덩굴 2개가 필요합니다."}, {"페더문 요새에 있는 프랫 맥그루벤에게 거북 껍질 투구 2개, 거북 껍질 팔보호구 2개, 야생덩굴 2개를 가져가야 합니다."}},
[2853] = {"야생 가죽 세공의 대가", {"음, 당신이야말로 근면 그 자체군요. 나머지는 모두 터득했고 야생 가죽 방어구 도안이 딱 하나 남았습니다. 이 도안은 굳이 고생을 해서 얻을 필요가 없습니다. 당신 스스로 받을 자격을 충분히 증명해 보였고 또... 내가 줄 수는 없습니다. 나를 가죽 세공의 길로 이끌어 주신 내 스승께서 직접 당신에게 보상을 해주셔야만 합니다.","텔로니스님께 이 편지를 가져가십시오. 그분께서는 다르나서스에서 가죽 세공술을 가르치시고 계십니다. 그분께 이 편지를 보여 드리고 나와 마찬가지로 당신도 그분께 동등한 동료로 인정받도록 하시기 바랍니다."}, {"다르나서스에 있는 가죽 세공의 대가인 텔로니스에게 프랫의 편지를 가져가야 합니다."}},
[2854] = {"야생 가죽 방어구", {"당신의 가죽 세공 기술은 빠르게 발전하는군! 당신이 익히면 도움이 될 가죽 세공 기술을 내가 알고 있는데... 바로 야생 가죽 방어구요! 야생 가죽은 풀이 무성하게 자라는 지역과 그곳에 사는 생물들에게서 볼 수 있는 야생덩굴의 강력하고 혼돈스러운 특성을 이끌어내지. 내 보증하건대 그걸로 만든 방어구는 당신 같은 솜씨를 가진 세공인에게는 최고의 것이오.","내 그 기술을 가르쳐 주는 대신 한동안 나와 함께 일을 해야 할 것이오. 먼저 해야 할 일은 두꺼운 가죽 10개를 가져오는 것이니 자세한 얘기는 그 후에 하도록 하겠소."}, {"모자케 야영지에 있는 장도르 스위프트스트라이더에게 두꺼운 가죽 10개를 가져가야 합니다."}},
[2855] = {"야생 가죽 어깨보호구", {"야생 가죽 어깨보호구는 착용하는 자의 골격을 아주 잘 받쳐 주기 때문에 팔 위쪽과 어깨를 보호하는데 최고라고 할 수 있지. 다른 야생 가죽 방어구와 마찬가지로 야생 포도 덩굴을 바느질해 붙이면 강력한 효과가 무작위로 더해지게 되는 것이오.","이 도안을 얻으려면 두꺼운 방어구 키트 6개와 야생덩굴 하나가 필요하오. 당신한테 그리 어려운 임무는 아닐 것이오."}, {"모자케 야영지에 있는 장도르 스위프트스트라이더에게 두꺼운 방어구 키트 6개와 야생덩굴 1개를 가져가야 합니다."}},
[2856] = {"야생 가죽 조끼", {"야생 가죽 조끼는 모든 야생 가죽 방어구 세트의 기본이지. 조끼의 틀과 야생덩굴이 한데 잘 엮여 조끼 전체에 강력한 마법이 흐르게 되기 때문에 몸통을 최대한으로 보호할 수 있소이다.","이 도안을 얻으려면 거북 껍질 흉갑 2개와 거북 껍질 장갑 2개, 그리고 야생덩굴 1개가 필요하오. 쉬운 수업은 아니지만 당신 실력이라면 이 정도는 틀림없이 해낼 수 있을 것이오."}, {"모자케 야영지에 있는 장도르 스위프트스트라이더에게 거북 껍질 흉갑 2개, 거북 껍질 장갑 2개, 야생덩굴 1개를 가져가야 합니다."}},
[2857] = {"야생 가죽 투구", {"야생 가죽 투구는 편안하면서도 머리의 중요한 부위를 아주 우수하게 보호해 주지. 모든 야생 가죽 방어구와 마찬가지로 여기에 포함된 야생덩굴이 투구의 기본틀에 강력한 효과를 무작위로 부여하게 되는 것이오. 가죽 방어구를 착용하는 사람들은 틀림없이 이런 투구를 원할 것이오!","이 도안을 얻으려면 밤하늘 튜닉 2개, 밤하늘 머리띠 2개, 그리고 야생덩굴 1개가 필요하오. 이 수업을 마치면 이 도안은 당신 것이 될 것이오!"}, {"모자케 야영지에 있는 장도르 스위프트스트라이더에게 밤하늘 튜닉 2개, 밤하늘 머리띠 2개, 야생덩굴 1개를 가져가야 합니다."}},
[2858] = {"야생 가죽 장화", {"당신의 야생 가죽 방어구에 대한 지식은 날로 늘어가고는 있지만 아직 배워야 할 게 더 있소.","모든 세트의 기초가 되는 야생 가죽 장화요. 이런 장화는 발을 편안히 하면서도 모험에서의 위험을 견뎌낼 수 있도록 고안되었소. 부드러운 밑창은 움직일 때 소리가 나지 않는 신발을 찾는 사람들에게는 아주 딱이지.","이 도안을 얻으려면 밤하늘 바지 2개와 밤하늘 장화 2개, 야생덩굴도 2개가 필요하오. 이번엔 야생덩굴이 1개가 아니라 2개요. 재료가 다 갖춰지면 수업을 계속 진행하도록 하겠소."}, {"모자케 야영지에 있는 장도르 스위프트스트라이더에게 밤하늘 바지 2개, 밤하늘 장화 2개, 야생덩굴 2개를 가져가야 합니다."}},
[2859] = {"야생 가죽 다리보호구", {"이제 좀 더 복잡한 기술을 익힐 때가 되었군. 야생 가죽 다리보호구는 가죽 장비 중에서도 가장 튼튼하고 강력한 장비에 속하오. 허리, 허벅지, 종아리에 이르기까지 모두 강력한 보호를 받게 되는 것이지. 거기에 야생덩굴에서 얻을 수 있는 강력한 효과까지 합치면 이걸 가진 모험가들이 당신을 볼 때마다 감사를 표하게 될 것이오.","이 도안을 얻으려면 거북 껍질 투구 2개, 거북 껍질 팔보호구 2개, 야생덩굴 2개가 필요하오."}, {"모자케 야영지에 있는 장도르 스위프트스트라이더에게 거북 껍질 투구 2개, 거북 껍질 팔보호구 2개, 야생덩굴 2개를 가져가야 합니다."}},
[2860] = {"야생 가죽 세공의 대가", {"내가 가르칠 수 있는 모든 것을 당신에게 가르쳤지만 아직 야생 가죽 방어구 도안 하나가 남아있소. 이 도안은 특별히 고생을 해서 얻을 필요가 없소. 당신 스스로 이미 받을 자격을 충분히 증명해 보였지만... 내 마음대로 줄 수 있는 게 아니오. 나를 가죽 세공의 길로 이끌어 주신 내 스승께서 직접 보상을 해주셔야 하오.","썬더 블러프에서 가죽세공술을 가르치고 계신 우나님께 이 편지를 가져가도록 하시오. 가서 이 편지를 보여 드리고 나와 마찬가지로 당신도 그분께 동등한 동료로 인정받도록 하시오."}, {"썬더 블러프에 있는 가죽 세공의 대가인 우나에게 장도르의 편지를 가져가야 합니다."}},
[2861] = {"타베사의 임무", {"듣자 하니, 먼지진흙 습지대 깊은 곳에 사는 동료 마법사 타베사가 줄파락에 사는 어떤 트롤과 해결할 문제가 있다고 해요. 타베사에게 가면 더 자세히 얘기해 줄 거예요.","참, 친구도 몇 명 데리고 가세요, <name>. 타베사가 당신을 줄파락으로 보낼지도 몰라요. 그렇다면 혼자 가는 것은 바람직하지 않아요.","타베사의 오두막은 테라모어 서쪽, 돌망치 폐허의 바로 북쪽에 있어요."}, {"먼지진흙 습지대에 있는 타베사와 대화해야 합니다."}},
[2862] = {"덩굴발 일족과의 전투", {"놀 녀석들... 단순히 거슬리기만 하는 게 아닙니다. 바로 이 야영지의 존속 자체에 끊임없는 위협이 되고 있단 말입니다! 녀석들이 페랄라스의 토착민이든 아니든 상관없습니다. 우리가 좀 더 강한 결의로 녀석들과 맞서지 않으면 언젠가 우리는 이 지역에서 완전히 쫓겨나게 될 테니까요.","요즘 들어 놀 녀석들이 야영지를 더 거세게 공격하고 있습니다. 녀석들의 수를 줄이는 데 당신의 도움이 필요합니다. 녀석들의 갈기 10개를 가져오시면 그 수고에 대해 후하게 보상하도록 하겠습니다.","어서 가십시오!"}, {"모자케 야영지에 있는 아도겐 스위프트스트라이더에게 덩굴발 일족 갈기 10개를 가져가야 합니다."}},
[2863] = {"우두머리 공격", {"이번 계획은 우두머리라고 자칭하는 여럿의 놀 지도자 녀석들을 아주 짧은 시간 안에 진압하는 것이오. 우리가 재빠르고 정확하게 공격을 가할 수만 있다면 놈들 간에 혼란을 불러올 수 있을 것이며, 또한 우리가 그들을 짓밟을 수 있다는 걸 멍청한 놈들 머릿속에 확실히 새겨 넣게 될 것이오!","바로 당신이 이러한 내 뜻을 전달하는 사자가 되어 줬으면 하오. 이 일을 제대로 수행하려면 한 시간 안에 우두머리 다섯을 처치해야만 하오. 그렇지 않으면 우린 계속 이 곤경에 빠져 있게 될 것이오!"}, {"1시간 안에 덩굴발일족 우두머리 5마리를 처치하고 아도겐 스위프트스트라이더에게 되돌아와야 합니다."}},
[2864] = {"트란렉", {"내 사촌, 트란렉이 곤경에 빠졌어요. 왕쇠똥구리 껍질을 한 짐 보내겠다고 약속한 지가 벌써 몇 주나 됐는데 아직 도착하지 않았거든요. 사촌이라 가젯잔으로 누구를 보내서 독촉을 하기가 좀 그런데...","나 대신 트란렉과 얘기해 볼래요? 내가 허튼짓을 하기 전에 빨리 보내라고 했다고 말에요.","가젯잔은 칼림도어의 타나리스 북부에 있어요."}, {"가젯잔에 있는 트란렉과 대화해야 합니다."}},
[2865] = {"왕쇠똥구리 껍질", {"타나리스의 왕쇠똥구리들은 단단한 껍질을 갖고 있지요! 건축 자재로 쓸 수 있을 만큼 아주 단단해요. 그 외에도 정말 여러 가지로 쓰이지요!","사실, 그 껍질이 얼마나 유용한지, 왕쇠똥구리가 거의 멸종될 정도로 사냥을 해댔답니다!","저는 그 귀한 왕쇠똥구리의 서식지를 알죠. 껍질을 제게 가져오겠다고만 약속하신다면 어딘지 가르쳐 드리지요. 약속하시겠어요?","그럼, 좋아요. 왕쇠똥구리들은 줄파락에 무리를 지어서 살아요. 다행히 그곳 트롤들이 껍질을 노리고 녀석들을 사냥하지는 않는 것 같아요.","당신이라면 할 수 있죠! 줄파락으로 가서 온전한 껍질을 가져오세요!"}, {"가젯잔에 있는 트란렉에게 온전한 왕쇠똥구리 껍질 5개를 가져가야 합니다."}},
[2866] = {"솔라살 폐허", {"여기서 남쪽으로 가면 현시대가 되기 훨씬 이전부터 있었던 솔라살 폐허가 있습니다. 처음 그 폐허를 조사했을 때는 특별히 눈에 띄는 것이 없기에 건드리지 않고 그대로 두었지요.","그런데 최근 들어 엄청난 수의 증오갈기 나가들이 그 폐허를 점령했습니다. 처음에는 녀석들이 공격 준비를 하는 것으로 생각했지요. 지금도 그럴 가능성이 있긴 하지만 뭔가 다른 일을 벌이는 게 아닌가 싶군요.","가서 그 폐허를 탐사하고 눈에 띄는 것이 있으면 뭐든지 가져 와 주십시오. 엘룬께서 행운을 가져다주시길."}, {"솔라살 폐허를 탐사하고 그곳에 있는 나가의 실태를 조사해야 합니다."}},
[2867] = {"페더문 요새로 돌아가기", {"솔라살 폐허의 중심부에서 기이한 전망대를 찾았으니 페더문 요새로 돌아가 여기서 발견한 것을 샨드리스 페더문 장군에게 보고해야 합니다."}, {"페더문 요새에 있는 샨드리스 페더문에게 돌아가 발견한 것에 대해 보고해야 합니다."}},
[2869] = {"증오갈기 일족과의 전투", {"이 시점에서 가장 좋은 방책은 나가의 속셈이 무엇이든 녀석들이 언젠가는 섬을 차지하기 위해 움직일 것이라 가정하고, 너무 늦기 전에 공격 조치를 취해 위협을 막아야 하는 것이오.","상태가 양호한 증오갈기 나가 비늘 10개를 가져오시오. 비늘을 어떻게 손에 넣는지는 알고 있을 거라 믿소. 그대는 나가와의 전투에서 경험을 쌓고 우리는 살도르 섬의 나가 수를 좀 줄일 수 있으니 모두에게 득이 되는 일일 것이오. 자, 어서 가시오."}, {"페더문 요새에 있는 라트로니쿠스 문스피어에게 증오갈기 나가 비늘 10개를 가져가야 합니다."}},
[2870] = {"군주 샬자루와의 전투", {"새로운 정찰 보고를 받고 나가의 명령 체계를 더 자세히 알 수 있게 되었소. 듣자하니 나가 종족 중에는 왕족이 있는데 이 왕족이 모든 일을 결정하고 있다고 하는군.","그중에서도 유독 잔인한 군주 샬자루라는 자가 있다는 것을 확인하였소. 정찰병의 보고에 의하면 샬자루는 최근 나가들이 발굴하고 있는 공포의 섬에 수로를 건너 주 유적지의 남쪽 동굴에서 특이한 유물을 찾았다고 하오.","그를 처치해 발굴을 저지하고 그 유물을 연구할 수 있게 내게 가져오시오."}, {"군주 샬자루를 처치하고 그가 찾아낸 신비한 유물을 페더문 요새에 있는 라트로니쿠스 문스피어에게 가져가야 합니다."}},
[2871] = {"유물 전달", {"이 유물을 내 아내 베스티아에게 가져다주면 고맙겠소. 내 아내는 우리 종족의 훌륭한 여사제일 뿐만 아니라 가장 박식한 학자 중 한 사람이기도 하오. 이 유물을 여는 방법에 내기가 걸려 있다고 해도 도무지 어떻게 돌아가는 건지 알 수가 없군. 내 아내라면, 그러니까 누군가 이 물건에 대해 아는 사람이 있다면 아마 내 아내일 것이오. 게다가 우리는 이 물건에 대해 꼭 알아내야만 하오!","이 길을 꺾어 돌아가면 틀림없이 아내가 연구를 하고 있을 것이오. 고맙소, 친구."}, {"페더문 요새에 있는 베스티아 문스피어에게 신비한 유물을 가져가야 합니다."}},
[2872] = {"스톨리의 빚", {"내 오랜 친구 스톨리는 나에게 아주 특별한 럼주 한 병을 빚졌다오. 그 친구는 칼림도어의 타나리스에 있는 스팀휘들 항구에서 술집을 운영하는데 그곳의 럼주는 무법항에서 찾을 수 있는 다른 어떤 것보다 더 독하다고 하더군. 얼마나 자신 있어 하는지, 내가 그 술을 마셔 보고 그의 말에 동의하지 않는다면 내게 빚진 돈을 두 배로 갚겠다고 하는 거요!","뭐, 지금은 우리가 지난번 얘기를 나눌 때만큼 그렇게 자신 있어 하는 것 같진 않지만 말이야. 이제는 내게 그 럼주를 주는 것을 꺼려하고 있다니까. 그렇지만 나는 내기에서 물러서는 성격이 못되니 나 대신 그 친구와 얘기를 나누어 보지 않겠소?"}, {"스팀휘들 항구에 있는 스톨리와 대화해야 합니다."}},
[2873] = {"스톨리의 뱃짐", {"지금 럼주가 다 떨어지고 없소! 무법항의 바다 늑대, 맥킨리랑 건 내기에서 이기려면 럼주를 좀 줘야 하는데 말이오. 최근에 쿠에르고 선장이 내 뱃짐을 훔쳐서 해적 마을에 숨겨 두고 있다오. 성벽으로 둘러싸인 그 마을은 여기에서 남쪽에 있소.","럼주가 들어 있는 내 뱃짐을 찾아와 준다면 칼림도어의 술이 무법항 것보다 훨씬 낫다는 걸 맥킨리에게 확실히 보여줄 수 있소이다!"}, {"스팀휘들 항구에 있는 스톨리에게 뱃짐을 가져가야 합니다."}},
[2874] = {"맥킨리에게 배달", {"다시 한번 도와줘서 고맙다는 말을 하고 싶소. 여기 스팀휘들 럼주가 한 병 있소. 이걸 맥킨리에게 갖다주면 그 친구도 마침내 무법항 술에 대한 그 맹목적인 믿음을 버릴 수 있을 거요."}, {"무법항에 있는 \"바다늑대\" 맥킨리에게 칼림도어산 럼주가 든 스톨리의 병을 가져가야 합니다."}},
[2875] = {"현상 수배: 앙드레 파이어비어드", {"스팀휘들 항구와 가젯잔을 휩쓸고 다니는 사악한 해적 앙드레 파이어비어드를 현상 수배한다.","이 글을 읽는 모든 이에게 허가한다! 의회의 판결에 따라 앙드레와 공범들을 체포할 때 죽음을 불러올 정도의 힘을 사용해도 좋다.","앙드레가 죽거나, 심하게 다쳐서 보안과장 빌지위즐에게 호송하지 못할 경우 그의 머리카락을 잘라서 증거로 가져가도 현상금을 받을 수 있다."}, {"앙드레를 처치하고 그 증거로 그의 머리카락을 스팀휘들 항구에 있는 보안과장 빌지위즐에게 가져가야 합니다."}},
[2876] = {"선박 일정표", {"이 두루마리에는 스팀휘들 항구와 무법항 간의 뱃짐 운송 일정이 기록되어 있습니다. 처리를 완료한 일정과 앞으로 예약된 일정이 선박의 이름 옆에 적혀 있습니다. 일부 항해 일정 옆에는 크게 X자로 표시되어 있고 어떤 것에는 \"대단한 전리품\"이나 \"치열한 전투\"와 같은 주석이 달려 있습니다.","스팀휘들 항구의 누군가가 이 문서를 보고 싶어할 것입니다."}, {"스팀휘들 항구의 관계자에게 선적 일정표에 대해 보고해야 합니다."}},
[2877] = {"굼벵이 바위굴 청소", {"우리 와일드해머는 아이언포지의 형제들보다 과거를 더 잘 기억한다네. 여기서 친구를 사귀고 싶다면 우리 대신 지저분한 일을 좀 맡아 줘야겠어!","동부 내륙지에 있는 굼벵이 바위굴에서 호드가 물러났네. 이제 그곳은 고약한 녹괴물과 수액괴물이 득실거리지. 우리에게 그 지역에 대한 계획이 있네. 우리가 그곳으로 가기 전에 일꾼을 시켜 먼저 청소를 해놓는 거지.","바로 자네가 적임자일 것 같군! 그곳으로 가서 녹색 녹괴물 10마리와 비취 수액괴물 10마리를 제거한 후에 돌아와 내게 보고하게. 출발!"}, {"녹색 녹괴물 10마리와 비취 수액괴물 10마리를 처치한 후 맹금의 봉우리에 있는 프라가르 썬더맨틀에게 돌아와 보고해야 합니다."}},
[2878] = {"오염된 노래꽃", nil, nil},
[2879] = {"에퀴넥스의 지팡이", {"이 지팡이는 에퀴넥스의 지팡이일지도 모르겠군. 에퀴넥스의 지팡이는 꿈나무의 중앙 지역 서쪽에 있는 까마귀바람 폐허에 에퀴넥스 비석탑을 열 수 있는 열쇠요.","그 폐허에서 아직도 타오르고 있는 삼하, 임벨, 빌탄, 라하사의 네 가지 불꽃을 찾아 그 정기를 받으시오. 그 후 에퀴넥스 비석탑 곁에 서서 그 정기를 사용하여 이 지팡이에 힘을 불어넣으시오. 이것이 정말 에퀴넥스의 지팡이라면 그 비석탑을 열고 안에 든 성스러운 유물을 얻을 수 있을 것이오."}, {"트로야스의 지팡이에 힘을 불어넣고 에퀴넥스 비석탑을 찾아야 합니다."}},
[2880] = {"트롤 목걸이 현상금", {"이것만은 분명히 알아두게. 우리는 엄밀히 말해서, 얼라이언스의 일원이 아니야! 와일드해머 부족은 독립된 부족으로서 개인의 가치를 행동과 업적으로 평가하지. 실제로는 아무 일도 안 하면서 실속 없이 입만 살아있는 자들은 쳐주지 않는단 말일세!","우리의 친구가 되고 싶다면 먼저 자네의 가치를 증명해 보이게! 동부 내륙지의 언덕 부근에 득실거리는 더러운 트롤 녀석들은 종종 전통 목걸이를 지녔다네. 내게 그 목걸이 5개를 가져오게. 그러면 한 단계 통과한 것으로 인정해 주지."}, {"맹금의 봉우리에 있는 프라가르 썬더맨틀에게 트롤 전통 목걸이 5개를 가져가야 합니다."}},
[2881] = {"트롤 목걸이 현상금", nil, nil},
[2882] = {"쿠에르고 골드", {"이 지도에는 남쪽을 향해 넓게 펼쳐진 해변이 그려져 있습니다. 지도 아래 쪽에는 우아한 필체의 글이 쓰여 있습니다.","스팀휘들 항구의 남쪽...","내 고향의 남쪽...","돛대와 깃발, 그리고 뼈를 찾아라!","용기가 있는 자여, 그곳을 파라.","쿠에르고 골드를 손에 넣어라!","열쇠를 찾아라.","적들의 피를 보리라."}, {"쿠에르고 골드를 찾아야 합니다."}},
[2902] = {"덩굴발 일족 조사", {"놀들은 머리가 썩 좋은 편은 아니지만 적어도 최근 활동에 대한 전략 문서 같은 것을 분명 갖고 있을 것이오. 우리가 그 계획서를 손에 넣는다면 녀석들을 그렇게 흥분하게 만든 원인에 대해 좀 더 확실히 알 수 있을 것이오.","덩굴발 일족 녀석들의 야영지로 되돌아가서 전투 계획서처럼 보이는 것이 있으면 뭐든 가져오시오. 전투 계획서가 없이 감행한 공격이라고 보기에는 무척 계획적이었으니 분명 뭔가 갖고 있을 거야. 가서 찾아보고 계획서를 손에 넣거든 내게 가져오시오."}, {"모자케 야영지 남쪽에 있는 놀 야영지에서 덩굴발 전투 계획서를 찾아야 합니다."}},
[2903] = {"전투 계획서", {"놀의 전투계획서를 찾았으니 이제는 모자케 야영지에 있는 아도겐 스위프트스트라이더에게 가져가는 일만 남았습니다. 아도겐 스위프트스트라이더나 모자케 야영지에 있는 누군가가 놀의 의도를 더 정확히 파악할 수 있을 것입니다."}, {"모자케 야영지에 있는 아도겐 스위프트스트라이더에게 덩굴발일족 전투계획서를 가지고 가야 합니다."}},
[2904] = {"곤경에 빠진 케르노비", {"<신음하는 케르노비> 어... 어떻게 된 거죠?","마지막으로 기억나는 건 검은무쇠 드워프들에게 붙잡혀 거의 죽을 만큼 얻어맞고, 여기 이 더러운 트로그놈들 무리에 내동댕이쳐진 것뿐인데...","제발 여기서 빠져나가게 도와주세요!","이렇게 하죠. 출구로 절 데려다 주세요. 그리고 출구까지 살아나가면 무법항으로 가세요. 거기서 스쿠티를 만나 제가 톱니항의 팅커위즈에게 가더라고 전해주세요. 그 거대한 장치를 만드는 데 검은무쇠 드워프가 관련됐다는 사실을 알려야 하거든요.","어서 가죠!"}, {"태엽장치 통로 입구까지 케르노비를 호위한 후 무법항에 있는 스쿠티에게 가서 보고해야 합니다."}},
[2922] = {"고장난 첨단로봇 수리", {"우리가 사랑했던 도시 놈리건이 무너졌을 때, 불행하게도 내가 만든 첨단로봇이 전기 불꽃에 맞아 충격을 입은 모양이오. 한때 수많은 장치와 무제한의 저장 공간으로 우리를 도왔는데 지금은 녀석의 양전자 두뇌가 분극화된 모양이오. 이제는 음전자 성질을 띠어... 첨단로봇의 행동이 완전히 거꾸로 바뀌어 버린 거요. 녀석은 놈리건에서 광포한 행동을 일삼으며 돌아다니고 있소이다!","하지만 녀석을 구할 수 있을지도 모르오. 당신이 첨단로봇의 기억회로를 내게 가져오면 그 분극 작용의 원인을 알아내서 불쌍한 첨단로봇을 고칠 수 있을지도 모르오."}, {"아이언포지에 있는 수석땜장이 오버스파크에게 첨단로봇의 기억회로를 가져가야 합니다."}},
[2923] = {"수석땜장이 오버스파크", {"수석땜장이 오버스파크가 자신이 만든 첨단로봇 때문에 매우 속상해 하고 있소. 내가 기억하기로 첨단로봇은 노움들에게 아주 쓸모 있는 도구였던 것 같은데 오버스파크 얘기로는 그 로봇이 완전히 고장났다고 하더구먼.","아주 슬픈 일이야. 하지만 재수가 좋으면 고칠 수 있을지도 모르지!","아이언포지의 땜장이 마을에 있는 수석땜장이 오버스파크와 얘기해 보시오. 당신을 아주 반길 거요."}, {"아이언포지에 있는 수석땜장이 오버스파크와 대화해야 합니다."}},
[2924] = {"필수 인공장치", {"필수 인공장치라는 부품은 다양한 곳에 두루 사용되기 때문에 놈리건 땜장이와 대장장이들에게는 없어서는 안 될 물건이죠. 그런데 고향에서 정신없이 탈출해 나올 때 모두 깜박 잊고 그 장치들을 놓고 나온 거예요! 곧 착수해야 하는 일에 그 장치가 많이 필요하답니다.","제발 부탁이에요. 놈리건으로 가서 필수 인공장치를 좀 가져다주세요. 깊숙이 들어가면 인공장치 검출기라는 것에서 꺼낼 수 있을 거예요.","부디 서둘러 주세요. 도움을 주시면 감사하겠어요!"}, {"아이언포지에 있는 클락몰트 스패너스판에게 필수 인공장치 12개를 가져가야 합니다."}},
[2925] = {"클락몰트에게 꼭 필요한 것", {"내 노움 친구, 클락몰트 스패너스판이 아이언포지에서 소식을 전해 왔소. 극히 중요한 임무를 수행하기 위해 놈리건으로 위험을 무릎 쓰고 떠나는데 도움이 필요하다는 거요. 놈리건은 한때 위대한 노움 도시였지만 지금은 오염되어 버렸고 제정신이 아닌 노움들과 여러 가지 기괴한 장치들로 가득 차 있소.","나는 이곳을 떠날 수 없지만 내 친구를 꼭 도와 주고 싶소. 당신이 나 대신 가 준다면 정말로 고맙겠소.","클락몰트는 아이언포지의 땜장이 마을에 있소."}, {"아이언포지에 있는 클락몰트 스패너스판과 대화해야 합니다."}},
[2926] = {"폐기물 수집", {"그 사고가 난 뒤부터 노아른은 놈리건에 남아 있는 트로그 녀석들을 쫓아내고 그곳에 퍼진 질병을 치료할 방법을 찾는 데만 온 신경이 쏠려 있소.","다행히도 라즐과 내가 치료약 비슷한 걸 만들어 냈지. 잘 되면 치료약이 될 테고 못해도 독한 럼주가 되는 액체인데 실험을 해보기 전까지는 아무것도 알 수 없소! 따라서 우리 가설을 확인해보려면 몇 가지 물건이 필요하오. 우선 놈리건에 있는 오염된 트로그들에게서 녹색 방사성 폐기물을 채취해 이 병에 채워다 주시오!"}, {"빈 가연채집병에 오염된 침략꾼이나 오염된 약탈자에게서 얻은 방사성 폐기물을 담아야 합니다. 채집병이 가득 차면 카라노스에 있는 오지 토글볼트에게 가져가야 합니다."}},
[2927] = {"그날 이후", {"내 머리숱이 항상 이렇게 적었다고 생각하는 건 아니겠지? 절대 아니오! 이 비극적인 탈모 증상은 놈리건에서 발생한 방사선 유출 때문이라오. 그전에는 내가 아제로스에서 최고로 풍성한 머리를 한 노움이었는데!","<허공에 대고 주먹을 휘두르는 노아른>","망할 놈의 트로그들, 모두 없어져 버려라!","어쩌면 너무 늦지 않았을지도 몰라. 구할 수 있을지도... 아, 내 머리숱 말고 우리 고향 말이오! 카라노스에 있는 오지 토글볼트와 얘기를 해보시오. 그와 라즐이라는 친구가 치료약을 만들고 있소."}, {"던 모로의 카라노스에 있는 오지 토글볼트와 대화해야 합니다."}},
[2928] = {"회전천공식 발굴기", {"정말 암울하네요. 우리 회전천공식 발굴기 부대를 던 모로로 보낼 수 있을 정도로 부품이 충분하지 못할 것 같답니다.","여분의 부품이 필요합니다. 우리 발굴기를 위한 여분의 부품을 얻기에 놈리건보다 더 좋은 곳은 없죠. 그곳에서는 로봇과 기계들이 넓은 초원에 소떼 돌아다니듯 한다고 들었어요.","놈리건으로 가서 그 로봇들을 분해해 부속품을 가져와 주세요. 24개면 충분할 거예요."}, {"스톰윈드에 있는 쇼니에게 기계장치 부속품 24개를 가져가야 합니다."}},
[2929] = {"대배반", {"나는 텔마플러그를 믿었는데, 설마 노움족 전체를 배반하리라고는 꿈에도 생각하지 못했네. 도대체 이유가 뭐란 말인가? 부? 권력? 시간이 지나면 모두 다 가질 수 있었을 것을... 쯔쯔... 지금 우리는 고향에서 쫓겨난 신세라네. 그 미치광이 독불장군이 그곳을 점령했지.","우리는 놈리건을 되찾을 것이고 그때까지 결코 멈추지 않을 걸세. 우리의 투쟁을 도울 생각이 있다면 자네가 꼭 해줬으면 하는 임무가 있네. 반역자 기계박사 텔마플러그를 처치하게."}, {"놈리건으로 가서 기계박사 텔마플러그를 처치해야 합니다. 임무가 끝나면 땜장이왕 멕카토크에게 돌아와야 합니다."}},
[2930] = {"자료 회수", {"놈리건에서 도망쳐 나올 때 대량의 자료를 두고 나왔다네! 굉장히 중요한 자료인데 말이야!","놈리건 깊숙이 있는 행렬 천공기록기 3005-D의 고급 보안 터미널을 통해 자료를 오색 천공 카드에 담아 와야 해. 터미널에 접근하려면 보안 허가가 필요한데... 보안 허가를 받으려면 하급 보안 터미널인 모델 A, B, C에 먼저 접근해야 하지. 그리고 그 하급 터미널에 접근하려면 보안 천공 카드가 필요하고. 그 카드는 흰색인데 말이야... 놈리건에서 찾을 수 있다네.","어떤가, 정말 철통 같은 보안 아닌가? 도와 줄 수 있겠나?"}, {"아이언포지에 있는 수석수리공 캐스트파이프에게 오색 천공 카드를 가져가야 합니다."}},
[2931] = {"캐스트파이프의 임무", {"아이언포지에 있는 수석수리공 캐스트파이프가 일손을 필요로 하고 있소! 극비 임무로 자료를 회수하기 위해 놈리건으로 다시 들어갈 모험가들을 모집하고 있다고 하더군.","캐스트파이프는 나처럼 비전술, 연금술을 비롯해 기계공학 연구회의 회원은 아니지만, 아주 영리한 노움이라서 그가 중요하다고 하면 정말로 중요한 일일 가능성이 내 계산으로는 84%요!","아주 높은 확률 아니겠소?"}, {"아이언포지에 있는 수석수리공 캐스트파이프와 대화해야 합니다."}},
[2932] = {"준엄한 메시지", {"한때 마른나무껍질 트롤들은 호드의 동지였지만 지금은 스랄 님을 배반했소이다... 반드시 응징해야 하오!","자, 내 창을 줄 테니 멀리 북쪽에 있는 동부 내륙지로 가지고 가시오. 우리 호드의 동지 포세이큰들이 살고 있는 타렌 제분소에서 북동쪽으로 가시오.","동부 내륙지에 들어서면 마른나무껍질 트롤들이 눈에 뜨이는 족족 잡으시오. 녀석들의 해골을 모으고 내 창을 그들의 작은 마을인 히리와타나 준와타 중 한 곳에 박아 놓으시오.","사악한 행동에는 반드시 처벌이 따른다는 것을 보여 주시오!"}, {"동부 내륙지에서 마른나무껍질 해골을 모은 후, 님보야의 창을 동부 내륙지에 있는 마른나무껍질 마을 중 한곳에 박아 놓아야 합니다. 그런 다음 가시덤불 골짜기에 있는 님보야에게 돌아가십시오."}},
[2933] = {"독병", {"이 병 안에는 사악한 녹색 독이 들어 있습니다.","이 마을에 깔려 있는 독병의 수를 감안하면 마른나무껍질 부족이 상당히 중요하게 여기는 것 같습니다. 근처의 타렌 제분소에 있는 연금술사가 견본을 원할지도 모릅니다."}, {"타렌 제분소에 있는 연금술사에게 독병을 가져가야 합니다."}},
[2934] = {"온전한 독주머니", {"당신이 가져온 것보다 신선한 독의 견본이 필요하오. 이 독은 분명히 동부 내륙지에 서식하는 거미에서 추출했을 거요. 녀석들을 잡아서 온전한 독주머니를 찾아서 내게 가져와 주시오. 트롤들의 도시인 샤드라알로에 가면 거미를 많이 볼 수 있을 거요.","늑장 부리지 마시오. 독주머니는 반드시 신선한 상태로 가져와야 하니까."}, {"타렌 제분소에 있는 연금술사 라이던에게 온전한 독주머니를 가져가야 합니다."}},
[2935] = {"가드린 장로와 상의", {"혈거미에게서 얻은 독은 당신이 애초에 가져왔던 것만큼 독성이 강하지 않았소. 아마 처음 것은 다른 거미에게서 얻은 것 같소.","그 독의 신선한 견본이 있었으면 좋겠소. 그 독이 어디서 난 것인지 알려면 마른나무껍질부족을 잘 아는 트롤과 상의를 해야 할 거요. 센진 마을의 가드린 장로라면 그 부족에 대해 많은 걸 알려 줄 거요. 듀로타로 가서 가드린과 얘기해 보시오. 운이 좋으면 마른나무껍질부족이 어디서 그 독을 구하는지 그가 알고 있을지도 모르오."}, {"센진 마을에 있는 가드린 장로와 대화하십시오."}},
[2936] = {"거미 신", {"마른나무껍질 부족은 거미 신을 숭배한다네. 그 거미 신에게서 아주 강한 독을 모으지. 그러니 그 독을 원한다면 그들의 신을 소환해야만 하네!","그렇게 하려면 그 거미 신의 진짜 이름을 알아야 하는데 그 이름은 타나리스의 트롤 도시, 줄파락에 묻혀 있는 서판에 적혀 있어. 이 서판은 순교자 데카가 지키는 무덤 안에 있지.","데카를 물리치고 서판에서 거미 신의 이름을 알아내게. 이름을 알아내거든 내게 돌아오게나."}, {"데카의 서판을 읽고 마른나무껍질 거미 신의 이름을 알아낸 다음, 가드린 장로에게 가야 합니다."}},
[2937] = {"샤드라 소환", {"이 양피지를 받게. 동부 내륙지에 있는 마른나무껍질 영토 깊숙이에 있는 샤드라알로 제단에서 이걸 읽으면 거미 신 샤드라가 소환될 걸세."}, {"샤드라알로 제단에서 샤드라를 물리친 후, 그녀의 독을 타렌 제분소에 있는 연금술사 라이던에게 가져가야 합니다."}},
[2938] = {"언더시티로 독 가져가기", {"마른나무껍질부족의 거미 신에게서 얻은 이 독은 아주 대단한 물건이오! 한 방울로 족히 20마리가 넘는 토끼를 죽일 수 있으니까 말이오. 토끼야 물론 맨주먹으로 잡을 수 있을 만큼 작은 것들이라 분명 독에 대한 저항력도 낮겠지만, 그래도 이 독이 강력하다는 건 부인할 수 없는 사실이오!","언더시티 연금술 실험실에 계시는 내 상관인 파라넬 연금술사께 이 독의 견본을 보내고 싶소. 그 분도 이 독을 직접 시험해 보고 싶어하실 거요."}, {"언더시티에 있는 수석 연금술사 파라넬에게 파라넬의 소포를 가져가야 합니다."}},
[2939] = {"지식을 찾아서", {"엘룬께 영광을. 페랄라스의 폐허는 매우 위험할 수 있지만 이곳에서 일어났던 일들에 대해 많은 것을 알아낼 수 있소...","며칠 전 남쪽에 있는 부스러기 더미를 뒤지다 평범해 보이는 지팡이 하나를 발견했는데 단순히 평범한 지팡이가 아닐지도 모른다는 생각을 떨칠 수가 없소이다. 안젤라스와 내가 여기 있는 책이란 책들은 다 뒤져 봤지만 아무것도 알아낼 수가 없었소.","다르나서스에 있는 내 동료라면 이게 무엇인지 얘기해 줄 수 있을 것이오. 그녀가 우리를 도울 수 있는지 가서 그녀를 만나서 얘기해 보는 게 어떻겠소?"}, {"루테란 마을에 있는 다린 라이트윈드와 대화해야 합니다."}},
[2940] = {"페랄라스의 역사", {"이 책은 오랫동안 아무도 펼쳐 보지 않은 것 같아 보입니다. 표지는 꽤 낡았고 책장은 누렇게 변색하였지만 잘 살펴보니 바로 이것이 트로야스가 찾고 있는 것이라는 걸 알게 됩니다.","책을 집어듭니다. 하지만 빌리기 전에 먼저 주인에게 물어 봐야할 것입니다."}, {"다린 라이트윈드에게 책을 빌려도 좋은지 물어봐야 합니다."}},
[2941] = {"사진기 대여", {"나는 그동안 많은 것들을 연구했답니다. 최근에는 동부 내륙지의 해변을 점령하고 있는 무쇠턱거북에 푹 빠져 있지요. 특히 감메리타라는 거대한 무쇠턱거북을 직접 가서 보고 싶은데 연구해야 할 게 있어 이곳을 떠날 수가 없군요. 그 거북의 사진을 보는 게 제일 좋을 것 같은데 말이죠.","이 쪽지를 타나리스에 있는 커글 크랭크홉에게 가져가도록 하세요. 그녀가 날 위해 단숨에 사진을 찍을 수 있는 발명품을 만들었답니다. \"즉석사진기\"라고 불렀던 것 같은데..."}, {"타나리스에 있는 커글 크랭크홉에게 짧은 쪽지를 가져가야 합니다."}},
[2942] = {"반짝이는 돌", {"비석탑에서 유물을 꺼내자마자 에퀴넥스의 지팡이의 힘이 사라지기 시작하는 것이 느껴집니다."}, {"페더문 요새에 있는 트로야스 문브리즈에게 반짝이는 돌과 에퀴넥스의 지팡이를 가져가야 합니다."}},
[2943] = {"트로야스에게 돌아가기", {"여기 있습니다. 내 책을 조심해서 다루도록 하세요.","자, 트로야스가 당신이 돌아오길 무척 기다리고 있을 테니 서두르도록 하세요."}, {"페더문 요새에 있는 트로야스 문브리즈에게 책을 가져가야 합니다."}},
[2944] = {"슈퍼 찰칵이 FX", {"제가 새로 발명한 물건을 한번 보세요. 사진을 찍고 싶은 대상을 선택하고 단추를 누르기만 하면 돼요.","동부 내륙지에서 찾겠다던 그 생물 이름이 뭐라고 했죠? 감메리타? 뭐, 잘 찾아보세요. 즉석사진을 찍어서 가져가면 다린이 아주 기뻐할 거예요.","어쨌든, 슈퍼 찰칵이 여기 있어요. 잘 쓰세요!"}, {"슈퍼 찰칵이 FX로 감메리타의 즉석사진을 찍은 후, 루테란 마을에 있는 다린 라이트윈드에게 가야 합니다."}},
[2945] = {"꼬질꼬질한 반지", {"검은무쇠 드워프 한 놈의 시체에서 반지처럼 보이는 것을 찾았습니다. 하지만 흙과 단단하게 굳은 찌꺼기가 완전히 뒤덮고 있어 어떤 종류의 반지인지 알아보기 어렵고 끼어 볼 수도 없습니다. 찌꺼기 밑에서 무언가 귀한 금속의 광택이 살짝 보이며 보석이 반짝이는 것 같기도 합니다.","이 반지를 사용하려면 단단하게 굳은 찌꺼기를 제거해야 합니다."}, {"꼬질꼬질한 반지에서 찌꺼기를 제거할 방법을 찾아야 합니다."}},
[2946] = {"위험한 시도", {"당신이 발견한 소형 원반을 갖고 울둠의 봉인된 문으로 찾아가게. 어떤 일이 일어날지는 아무도 모르지...","위험할지 모른다는 건 물론 우리도 인정하지만 아무 일도 일어나지 않고 무사할 수도 있네. 이 원반을 다른 무엇인가에 사용해야 할지도 모르네. 이 원반을 소지하고 있으면 문이 열릴지도 모르고. 뭐든지 시도할 준비를 해두고 어떤 불상사에도 대비할 수 있도록 준비하게.","그리고 무슨 일이 일어나는지 알아 내면 아이언포지로 돌아오게. 일이 다 끝나면 분석할 게 엄청나게 많을 테니까!"}, {"소형 백금 원반을 타나리스에 있는 울둠의 문으로 가져가서 이 둘 사이의 연관성에 대한 단서를 찾아야 합니다."}},
[2947] = {"반지의 귀환", {"빤질빤질세척기 5200에서 나온 상자를 열자 생각했던 대로 반짝이는 반지가 들어 있습니다! 반지를 끼자 마법의 힘이 몸 전체를 휩쓰는 것이 느껴집니다.","반지 안쪽 면에는 아이언포지의 봉인 자국이 작지만 읽을 수 있을 정도로 깨끗이 새겨져 있습니다. 그 옆에는 '탈바쉬'라는 세 글자가 조그맣게 새겨져 있습니다. 아마 예전 주인이나 이 반지를 만든 사람의 이름인 듯합니다. 반지를 간직하거나... 이 사람에게 돌려주어야 합니다. 또다시 결정의 시간이 돌아왔습니다."}, {"반지를 그냥 가지거나, 반지 안쪽 부분에 새겨진 인장 자국의 주인을 찾아야 합니다."}},
[2948] = {"노움의 품질 개선", {"제 말 좀 들어 보세요, <name> 님. 네, 물론 그 반지야 당연히 당신 거지만... 제가 훨씬 더 멋지게 만들어 드릴 수 있답니다! 디자인이 너무 단순하지 않나요? 이젠 저도 솜씨가 많이 늘었으니까, 30실버만 주시면 반지를 더 멋지게 바꿔 드리죠! 아, 사실 30실버가 다는 아니고요...","요즘 사정이 좋질 않아서 재료 남은 게 하나도 없거든요. 제게 은괴 1개와 태마노 1개만 가져다주면 반지에 격자 모양의 은과 태마노를 붙여서 세공을 좀 더 화려하게 해드리죠!","한번 믿어보시라고요!"}, {"아이언포지에 있는 탈바쉬 델 키젤에게 은괴 1개, 태마노 1개와 반짝이는 금반지와 30실버를 가져가야 합니다."}},
[2949] = {"반지의 귀환", {"빤질빤질세척기 5200에서 나온 상자를 열자, 생각했던 대로 반짝이는 반지가 들어 있습니다! 반지를 끼자 마법의 힘이 온몸을 휩쓸고 내려가는 게 느껴집니다.","반지 안쪽에는 오그리마의 봉인 자국이 새겨져 있습니다. 작지만 읽을 수 있을 정도로 깨끗합니다. 그 옆에 새겨진 내용에서는 '노...'라는 맨 앞글자만 알아볼 수 있습니다.","아마 예전 주인이나 이 반지를 만든 사람의 이름인 듯합니다. 반지를 간직하거나... 이름이 '노'로 시작하는 이에게 돌려주어야 합니다. 또다시 결정의 시간이 돌아왔습니다."}, {"반지를 그냥 가지거나, 반지 안쪽 부분에 새겨진 인장 자국의 주인을 찾아야 합니다."}},
[2950] = {"노그의 반지 수리", {"아니, 아니, 아니, 그 반지가 내 거라고 하는 게 아니고... 보통 이런 일은 잘 하지 않지만 당신이 주신 검은무쇠 드워프에 대한 정보가 아주 유용해서 그 보답으로 반지를 더 멋지게 만들어주려는 겁니다. 이 작고 단순한 모양의 반지를 누가 만들었는지는 모르지만 훨씬 멋지게 만들어 줄게요. 아마 노움이 만든 게 아닐까 싶지만...","은괴 1개, 태마노 1개, 그리고 수고비로 30실버를 가져오면 반지에 격자 모양으로 은을 붙여 마법을 더 강화시켜 주도록 하죠. 어때요... 해보고 싶으시죠?"}, {"오그리마에 있는 노그에게 반짝이는 금반지, 은괴 1개, 태마노 1개, 30실버를 가져가야 합니다."}},
[2951] = {"빤질빤질세척기 5200!", {"완강히 저항하고 있는 노움들 사이에서 이 장치가 눈에 띕니다. 장치 앞면에 붙은 작은 판에 \"빤질빤질세척기 5200\"이라는 이름과 함께 다음과 같은 글귀가 적혀 있습니다.","\"귀중품에 찌꺼기가 끼었습니까? 빤질빤질세척기 5200이 말끔히 닦아 드립니다! 세척할 물건을 장치에 넣고 투입구에 3실버를 넣으십시오. 잠시만 기다리시면, 짜잔! 당신의 귀중품이 깨끗한 모습으로 반짝이게 됩니다!\""}, {"빤질빤질세척기 5200에 꼬질꼬질한 물건을 넣으십시오. 3실버를 투입하면 작동할 것입니다."}},
[2952] = {"삐까뻔쩍세척기 5200!", nil, nil},
[2953] = {"삐까뻔쩍세척기 재사용", nil, nil},
[2954] = {"바위감시자", {"\"안녕하십니까. 저는 이 입구를 지키는 문지기입니다. 원반 세트에 울둠의 원반이 결합되어 있지 않으면 울둠으로 들어갈 수 없습니다.\"","이 노르간논의 바위감시자가 출현할 것을 기대하고 당신에게 백금 원반만을 들려 타나리스 사막으로 보낸 것 같습니다. 먼저 지시를 받은 대로 노르간논의 바위감시자에게 숨겨진 이야기가 무엇인지 알아보아야 할 것 같습니다."}, {"타나리스 사막에 있는 노르간논의 바위감시자와 대화한 후 울둠 대좌를 건드리십시오."}},
[2962] = {"유일한 치료법", {"당신이 가져온 견본은 양도 부족한데다 불순물까지 섞여 있었소.","우리는 녹색 폐기물이 더 많이 필요하오. 그 트로그들의 몸에는 우리가 원하는 만큼의 방사선이 없으니 곧장 놈리건으로 가서 방사성 폐기물을 가져와야겠소. 오염된 진흙괴물, 오염된 폐기물을 말이오. 경고해 두겠는데, 이런 종류의 방사성 폐기물은 채취하기가 매우 어렵소. 그러니 쓸만한 견본을 채취할 때까지 이 약병을 여러 번 사용해야 할 거요."}, {"놈리건으로 가서 고농축 방사성 폐기물을 가져가야 합니다. 이 폐기물은 불안정하여 성분이 금방 변할 수 있으니 주의하십시오.","임무를 완수한 후 빈 대형 가연채집병을 오지에게 돌려줘야 합니다."}},
[2963] = {"울둠의 전조", {"또 만나게 되어 반갑네! 자네가 울다만 안에서 발견한 것 때문에 탐험가 연맹 전체가 떠들썩하다네. 탐험가 연맹에서는 자네가 찾아온 소형 백금 원반의 비밀을 캐내려고 안간힘을 쓰고 있지. 자네가 건진 게 뭔가 중요한 것이었다고 알려주고 싶었는데 마침 여기 왔군그래.","역사가 카르닉이 타나리스 사막에 있는 감시자의 골짜기에 얽혀 있는 진정한 근원에 대한 중요한 단서를 발견했다네. 가능한 빨리 자네와 얘기를 나누고 싶어하니 찾아가 보게나."}, {"아이언포지의 탐험가의 전당에 있는 역사가 카르닉과 대화해야 합니다."}},
[2964] = {"미래의 임무", {"음... 지금 우린 잠시 막다른 골목에 부닥친 것 같네. 울둠의 원반이 필요하다는 것은 알았지만 그것들이 어디에 있을지는 아무도 모르니까. 제대로 된 계획을 세우려면 방대한 조사가 필요하겠군. 다행스럽게도 어디부터 시작할지 대충 짐작 가는 곳이 두 군데 있네. 곧 자네더러 가서 그것들을 회수해 오라고 하게 될지도 모르겠네.","이 일을 선임탐험가께 보고하게. 다시 한번 우리 탐험가 연맹을 도와준 데 대한 감사의 표시를 직접 하고 싶어하실 게 분명해."}, {"아이언포지에 있는 선임탐험가 마겔라스와 대화하십시오."}},
[2965] = {"울둠의 전조", {"아, 다시 와주어서 다행이군. 울다만 발굴현장에서 찾은 것 때문에 우리 현자들 간에 많은 논의가 있었다네. 드루이드들 중 한 명인 나라 와일드메인과 바로 그런 논의를 하고 있었지. 그녀의 예전 탐험 경험이 우리가 앞으로 취해야 할 행동에 큰 도움이 될 것이라고 생각해서였네.","자네가 나라를 만나 그녀의 타나리스 사막에 있는 감시자의 골짜기에서의 모험에 대해 얘기를 나눠 봤으면 좋겠군. 그녀는 장로의 봉우리에서 기다리고 있다네."}, {"썬더 블러프의 장로의 봉우리에 있는 나라 와일드메인과 대화해야 합니다."}},
[2966] = {"위험한 시도", {"현자 트루스시커님과 나는 당신이 이 소형 원반을 가지고 울둠의 봉인된 문으로 찾아갔으면 합니다. 당신이 그렇게 하면 어떻게 될지는 아무도 몰라요.","위험할 것 같다는 건 우리도 물론 인정하지만 아무 일도 일어나지 않을 수도 있어요. 당신이 이 원반을 무엇인가에 사용해야 할지도 몰라요. 이 원반을 소지하고 있으면 문이 열릴지도 모르겠어요. 뭐든지 시도할 준비를 해두고 어떤 불상사에도 대비하도록 하세요.","알아낼 수 있는 만큼 알아낸 후에 다시 날 찾아오세요. 다 끝난 뒤에는 의논할 게 많을 거예요."}, {"소형 백금 원반을 타나리스에 있는 울둠의 문으로 가져가서 이 둘 사이의 연관성에 대한 단서를 찾아야 합니다."}},
[2967] = {"썬더 블러프로의 귀환", {"받침대를 다시 건드리자 갑자기 백금 원반이 다시 가방에 들어있는 것을 알게 됩니다.","바위감시자에 대해서 알았고 원반도 다시 얻었으니 썬더 블러프로 돌아가 나라 와일드메인에게 보고해야 할 때입니다."}, {"썬더 블러프에 있는 나라 와일드메인과 대화해야 합니다."}},
[2968] = {"미래의 임무", {"이거... 곤경에 부닥친 것 같네요. 울둠의 원반이 필요하다는 것은 알았지만 아무도 어디에 있는지는 몰라요. 먼저 조사를 한 다음 제대로 된 계획을 세워야겠군요. 어디서부터 시작할지 대충 짐작이 가는 곳이 두 군데 있어요. 곧 그곳으로 가셔야 할지도 모르겠네요.","어서 이 일을 현자 트루스시커님께 보고해야 해요. 분명 당신이 부름에 응해 준 데 대해 감사를 표하고 싶어하실 거예요."}, {"썬더 블러프에 있는 현자 트루스시커와 대화해야 합니다."}},
[2969] = {"모든 생명에 자유를", {"쉿, 조용히 하고 저 밑을 좀 보세요. 저 더러운 그림토템 부족 녀석이 요정용을 잡아들이고 있어요. 저들이 무슨 일을 저지르려는 건지 정확히는 모르겠지만 분명 못된 짓을 벌이려는 속셈일 겁니다.","우리 힘만으로는 야영지로 쳐들어가 붙잡힌 용들을 풀어줄 수가 없지만 그대가 도와준다면 혹시...","친구들 몇 명과 함께 이 일을 맡아볼 생각이 있다면, 이 열쇠로 우리에 갇힌 용들을 풀어주고 살아서 도망칠 수 있도록 도와주세요. 제가 옆에서 지원하죠. 그리고 만약에 대비해 제르카이는 여기 남을 겁니다."}, {"대나무 우리 열쇠를 사용하여 그림토템 야영지의 우리에 갇힌 화살요정용들을 풀어줘야 합니다. 최소 6마리의 화살요정용과 킨달 문위버가 살아남아야 합니다.","6분이 지나면 킨달이 후퇴할 때가 되었다고 할 것이며 임무에 실패하게 됩니다."}},
[2970] = {"정의의 실현", {"엘룬께 영광을. 벌써 얘기를 들으셨겠지만, 킨달과 저는 이제 그림토템부족을 공격할 작정입니다. 힘을 합한다면 큰 위험 없이 타우렌 녀석들을 일부 제거할 수 있을 거예요. 하지만 당신이 도와주실 수 있으시다면 그 도움을 받고 싶군요.","우리 밑에 있는 야영지를 보셨겠지만 동쪽에 야영지가 또 있답니다. 당신만의 전략으로 타우렌 녀석들에게 되도록 많은 피해를 주시기 바랍니다. 킨달의 화살과 당신의 무기로 쓴맛을 좀 보여준 후에 여기서 다시 만나기로 하죠."}, {"그림토템부족 식물학자 12명, 그림토템부족 침략자 10명, 그리고 그림토템부족 주술사 6명을 처치한 후, 페랄라스에 있는 제르카이 문위버에게 돌아가야 합니다."}},
[2972] = {"정의의 실현", {"이런 황폐한 곳에서는 킨달과 제가 제대로 된 보상을 드리기가 어럽군요. 하지만 당신의 수고에 대해 감사의 표시를 꼭 하고 싶으니 제 인장 반지를 받아 두세요. 혹시 텔드랏실에 가시게 되거든 다르나서스 중심부에 있는 달의 신전으로 가서 대여사제이신 티란데 위스퍼윈드님을 찾아뵙도록 하세요. 이 반지를 보여드리면 우리가 무사하다는 것을 알고 당신의 얘기를 믿으실 겁니다.","킨달과 저, 우리 둘의 마음을 담아 감사드려요."}, {"다르나서스에 있는 대여사제 티란데 위스퍼윈드에게 제르카이의 인장 반지를 가져가야 합니다."}},
[2973] = {"화려한 새 망토", {"만나서 반갑네, <class>|1이여;여;. 난 오그리마 지도자 분들의 명을 받아 동료와 함께 이곳 페랄라스까지 왔다네.","내 아내 코린은 새 옷을 정말 좋아한다네... 하늘하늘한 요정용의 날개로 장식한 새 망토를 만들어줄까 하는데 날 좀 도와줄 수 있겠나? 모자케 야영지에서 서쪽에 멋들어진 날개를 단 화살요정용들이 있으니 날개를 좀 구해다 주게."}, {"모자케 야영지에 있는 크루엑 스컬스플리터에게 오색 화살요정용 날개 10개를 가져가야 합니다."}},
[2974] = {"불길한 발견", {"뭐요!? 그 지역에 있는 타우렌들과 마주쳤단 말이오? 어떻게 생겼소?!","음... 그림토템 부족이로군! 드디어 녀석들을 발견했단 말이지... 벨그롬 장로님께서 그 부족에 대해 관심이 있으시오. 현상금까지 걸어놓았지.","<class>|1이여;여;, 다시 그곳으로 가시오. 이번엔 녀석들의 뿔을 되도록 많이 모아오시오. 그림토템 부족은 섣불리 상대하거나 과소평가해서는 안 될 녀석들이오. 놈들을 처치해주시오. 페랄라스의 강이 놈들의 피로 시뻘겋게 물들도록!"}, {"모자케 야영지에 있는 크루엑 스컬스플리터에게 그림토템 뿔 20개를 가져가야 합니다."}},
[2975] = {"페랄라스의 오우거", {"이 지역에서는 골두니 오우거일족의 영향력을 느끼지 않을 수가 없습니다. 주위를 둘러보세요. 녀석들의 존재는 이 땅에 상처를 입히고 있습니다. 녀석들이 페랄라스에서 세력을 더 확장하기 전에 몰아내야만 합니다.","강을 따라 북쪽으로 가면 녀석들의 전초기지가 있으니 녀석들의 수를 줄여주시기 바랍니다. 녀석들에게 이 땅에서 환영받지 못하는 존재임을 알려주도록 하세요."}, {"모자케 야영지에 있는 로크 올한이 골두니 오우거 10명, 골두니 오우거 마법사 10명, 골두니 투사 5명을 처치해 달라고 했습니다."}},
[2976] = {"불길한 발견", {"뭐요?!","그러니까 내가 현상금 얘기를 했었단 말이지. 아차... 앞으로 입 조심해야겠군.","수고를 한 건 당신이니 당신이 가장 큰 사례를 받아야 마땅하지. 뿔을 가져가시오. 내가 상자 안에 넣어두겠소. 그리고 명예의 골짜기로 벨그롬 록마울 님을 찾아가 보시오. 이번 일에 대해서도 알고 싶어하실 테고 당신의 노고에 대해서도 보상해 주실 거요.","건강히 지내시오, <class> 친구. 그리고 당신이 해준 모든 일에 대해 다시 한번 감사를 전하오."}, {"오그리마에 있는 벨그롬 록마울에게 그림토템 뿔 상자를 가져가야 합니다."}},
[2977] = {"아이언포지로의 귀환", {"대좌를 다시 건드리자 갑자기 백금 원반이 가방에 다시 나타납니다.","바위감시자에 대해서 알았고 원반도 다시 갖게 되었으니 아이언포지로 돌아가 탐험가 연맹에 보고해야 할 때입니다."}, {"아이언포지에 있는 역사학자 카르닉과 대화하십시오."}},
[2978] = {"골두니 두루마리", {"더럽혀진 두루마리를 주워 읽어 보니 이것이 어떤 회합이나 의식을 묘사하고 있는 두루마리 같습니다. 로크 올한이 이 두루마리가 골두니 일족에게 어떤 의미가 있는지를 알고 싶어할 것입니다."}, {"모자케 야영지에 있는 로크 올한에게 골두니 두루마리를 가져가야 합니다."}},
[2979] = {"암흑의 의식", {"음... 이 두루마리는 일종의 암흑의 의식을 위한 계획서인 것 같군요. 내가 보기에 이 의식은 골두니 오우거 대마법사들이 행하는 것 같습니다.","이 오우거 대마법사들 중 하나를 찾아 수정구를 빼앗아 오세요.","마법사의 수정구에는 굉장한 마력이 들어 있어 이 암흑의 의식과 골두니 일족이 사용하는 마법에 대해 더 자세히 알아보는 데 도움이 될 겁니다."}, {"모자케 야영지에 있는 로크 올한이 골두니 수정구를 찾아 와 달라고 했습니다."}},
[2980] = {"페랄라스의 오우거", {"이제 당신이 스스로 진정한 투사라는 것을 입증해 낸 만큼 주어진 임무를 계속 수행해 나갈 용기가 있을 거라 믿습니다.","여기서 남서쪽에 있는 높은벌로 가십시오. 한때 나이트 엘프들이 살았던 땅 위에 버려지고 파괴된 구조물들이 널려 있답니다. 이 폐허를 찾아가서 시들어 버린 나무와 더럽혀진 땅이 보이면 곧 골두니 일족을 발견하게 될 겁니다. 녀석들을 몰아내도록 하세요.","이 오우거들은 당신이 전에 상대했던 녀석들보다 훨씬 더 강하니까 조심하세요."}, {"모자케 야영지에 있는 로크 올한이 골두니 주술사 10명, 골두니 흑마법사 10명, 그리고 골두니 싸움꾼 5명을 처치해 달라고 했습니다."}},
[2981] = {"페랄라스의 위협", {"페랄라스는 실력 있는 <class>|1이;가; 그 가치를 증명하기에 딱 좋은 장소지. 그곳은 골두니라는 오우거일족이 막강한 군사력을 발휘하고 있는데 녀석들의 수가 갈수록 늘어나고 있소. 녀석들이 페랄라스에 주둔하고 있는 우리 동맹에게 위협을 가하기 전에 막아야 하오.","페랄라스의 모자케 야영지에 있는 로크 올한이라는 전사를 알고 있는데 그녀에게 가보시오. 당신이 도착하면 그녀가 현재 상황에 대해 자세히 설명해줄 거요."}, {"벨그롬 록마울이 모자케 야영지에 있는 로크 올한과 대화해 달라고 했습니다."}},
[2982] = {"높은벌", {"트로야스와 나는 위험한 줄 알면서도 폐허가 된 페랄라스 숲을 조사해 왔습니다.","그런데 요즘 들어 트로야스가 자꾸 혼자서 높은벌로 가는 게 너무 걱정이 되네요. 그곳은 골두니 오우거들이 판을 치고 있는데다 녀석들은 자기들 영토에 침입하는 자에게는 몹시 사납게 굴거든요.","그대가 섬 너머 남동쪽에 있는 폐허로 가서 그 오우거들을 좀 처치해 줬으면 합니다. 우리 트로야스를 위해서 그곳을 조금 더 안전하게 해 주면 정말 고맙겠어요."}, {"안젤라스 문브리즈가 골두니 흑마법사 8명, 골두니 주술사 8명, 골두니 투사 8명을 퇴치해 달라고 했습니다."}},
[2983] = {"불의 부름", {"마지막으로 자네의 실력을 시험해 본지도 꽤 됐군, <name>. 지금까지도 끈기 있게 잘해왔지만 이제는 더 큰 힘을 배울 때가 왔네... 진정한 힘을 배울 때가 온 것이지. 어떠한 대가라도 치를 준비가 되었다면 불의 원소는 자네의 것이네.","불모의 땅에 있는 크라날 피스를 찾아가 보게. 십자로 북쪽에 있는 작은 거처에 머물고 있네. 하지만 조심하게. 너무 멀리 가면 그 괘씸한 나이트 엘프의 땅에 이르게 되니..."}, {"불모의 땅에 있는 크라날 피스를 찾아야 합니다."}},
[2984] = {"불의 부름", {"마지막으로 자네의 실력을 시험해 본지도 꽤 됐군, <name>. 지금까지 끈기 있게 잘해왔지만 이제는 더 큰 힘을 배울 때가 왔네... 진정한 힘을 배울 때가 온 것이지. 어떠한 대가라도 치를 준비가 되었다면 불의 원소는 자네의 것이네.","불모의 땅에 있는 크라날 피스를 찾아가 보게. 십자로 북쪽에 있는 작은 거처에 머물고 있네. 하지만 조심하게. 너무 멀리 가면 그 괘씸한 나이트 엘프들의 땅에 이르게 되니..."}, {"불모의 땅에 있는 크라날 피스를 찾아야 합니다."}},
[2985] = {"물의 부름", {"이제 다음 시험을 치를 준비가 된 것 같군. 지금까지 끈기 있게 잘해왔지만 이제 더 많은 것을 배워야 할 때가 왔네. 이제는 물의 순수함에 대해 배워야 해.","불모의 땅에 있는 이슬렌 워터시어를 찾아가게. 동쪽 해안을 따라 성난남녘 강과 톱니항의 남쪽으로 가게. 고기잡이 오두막에 가면 그녀를 찾을 수 있을 거야. 행운을 비네."}, {"불모의 땅에서 이슬렌 워터시어를 찾아야 합니다."}},
[2986] = {"물의 부름", {"마지막으로 자네의 실력을 시험해 본 것도 오래전 일이군. 지금까지 끈기 있게 잘해왔지만 이제 더 많은 것을 배워야 할 때가 왔네. 이제는 물의 순수함에 대해 배워야 해.","불모의 땅에 있는 이슬렌 워터시어를 찾아가게. 동쪽 해안을 따라 성난남녘 강과 톱니항의 남쪽으로 가게. 그곳의 고기잡이 오두막에 가면 그녀를 찾을 수 있을 거야. 행운을 비네."}, {"불모의 땅에서 이슬렌 워터시어를 찾아야 합니다."}},
[2987] = {"골두니 코발트", {"난 페랄라스에 온 지 얼마 되지 않았어. 듣자 하니 이곳에 사는 골두니 오우거들이 아주 흥미로운 광석을 갖고 있다 하더군. 녀석들이 무슨 짓을 꾸미는지는 모르겠지만, 그 물건을 끔찍이 챙기는 건 확실해! 녀석들은 그 물건을 그냥 바닥에 버려두지 않고, 아니, 아예 나 같은 자가 손대지 못하게 묻어두고 있다니까.","혹시 생각이 있다면 내 삽을 줄 테니 북쪽에 있는 골두니 전초기지로 가서 골두니 코발트를 좀 파오라고. 삽을 사용하여 파란빛이 나타나면 땅을 파 봐. 그럼 흙고분 안에서 코발트를 캘 수 있을 거야!"}, {"모자케 야영지에 있는 올윈 기즈믹이 골두니 코발트 12개를 가져다 달라고 했습니다."}},
[2988] = {"마른나무껍질 우리", {"가장 강하고 어린 그리핀, 바람날개의 후계자 뾰족부리가 없어졌소! 며칠 전 어미와 함께 사냥을 나갔다가 실종됐다오. 내 생각으로는 트롤들이 잡아간 것 아닌가 싶은데...","우리를 좀 도와주시오! 마른나무껍질 트롤 마을 두 곳에 있는 우리를 뒤져서 뾰족부리가 있는지 알아봐 주시오."}, {"마른나무껍질 마을 두 곳에 있는 우리를 확인한 후, 그리핀 조련사 탈론액스에게 가야 합니다."}},
[2989] = {"줄의 제단", {"마른나무껍질 트롤들이 뾰족부리를 납치한 것이 아니라면 썩은가지 트롤들이 데리고 간 건지도 모르겠군요. 썩은가지 일족 영토의 서쪽 끝에 있는 줄의 제단으로 가서 녀석을 찾아봐 주십시오."}, {"줄의 제단 위를 확인한 후, 그리핀 조련사 탈론액스에게 가야 합니다."}},
[2990] = {"샤디우스 그림셰이드", {"이 깃털을 샤디우스 그림셰이드라는 점쟁이에게 가져가십시오. 그는 암흑의 지식을 찾아 오래전 맹금의 봉우리를 떠났지만 마음만은 아직도 와일드해머에 속해 있지요!","그의 재능이 뾰족부리를 찾는 데 도움이 될 수 있을지도 모릅니다.","이 깃털은 그 어린 그리핀이 잠을 자는 둥지에서 가져온 겁니다. 샤디우스에게 이 깃털을 전해 주면 그가 점치기 마법을 사용해 뾰족부리를 찾을 수도 있을 겁니다.","행운을 빕니다. 저주받은 땅에 있는 네더가드 요새의 마법사 탑에서 샤디우스를 찾을 수 있을 겁니다."}, {"뾰족부리의 깃털을 저주받은 땅에 있는 샤디우스 그림셰이드에게 가져가야 합니다."}},
[2991] = {"네크룸의 메달", {"먼저 자네가 내 부탁을 좀 들어주면 그 그리핀이 있는 곳을 알아봐 주겠어. 줄파락에 네크룸 거트츄어라는 트롤이 사는데 내가 꼭 연구해 보고 싶은 메달을 갖고 있네. 그 메달을 가져오면 점을 쳐서 뾰족부리를 찾아 주겠어.","줄파락은 바다 건너 서쪽으로 있는 타나리스 사막에 있지."}, {"네크룸의 메달을 저주받은 땅에 있는 샤디우스 그림셰이드에게 가져가야 합니다."}},
[2992] = {"점치기", {"점치기 의식을 수행하도록 좀 물러서 있게나. 자네가 가져온 깃털과 그 주인 사이에 연결된 끈을 찾아서 뾰족부리가 있는 곳을 찾을 거야.","그야 물론... 모든 것이 계획대로 잘 풀릴 때의 얘기지. 잘 되지 않는다면... 차라리 눈을 감아 버리는 게 좋을 거야."}, {"그림섀이드가 점치기 의식을 행하는 동안 기다리십시오."}},
[2993] = {"동부 내륙지로 돌아가기", {"뾰족부리는 동부 내륙지, 진타알로 꼭대기에 있는 절벽 위 우리 안에 갇혀 있네. 그런데 그 우리를 열 수 있는 열쇠는 타락한 여사제 헥스라는 썩은가지 트롤 부족의 대사제가 가지고 있지.","자, 이제 약속을 지켰군. 자네가 임무를 완수할 수 있도록 행운을 빌겠네."}, {"그리핀 조련사 탈론액스와 대화해야 합니다."}},
[2994] = {"뾰족부리 구출", {"뾰족부리를 구해야만 합니다!","썩은가지 부족 영토 중심부에 있는 진타알로의 꼭대기까지 올라가서 타락한 여사제 헥스에게서 뾰족부리가 갇혀 있는 우리 열쇠를 빼앗은 다음 뾰족부리를 구해 오십시오!"}, {"진타알로의 꼭대기까지 올라가 타락한 여사제 헥스를 처치하고 뾰족부리가 갇혀 있는 우리의 열쇠를 빼앗은 다음 뾰족부리를 구출해야 합니다."}},
[2995] = {"연락망", {"쿠엘다닐 오두막은 동부 내륙지 하이 엘프들의 연락 장소입니다. 하이 엘프 사절들은 다른 종족과 유대 관계를 쌓기 위해 노력하면서 동부 내륙지 남부에 사는 마른나무껍질 트롤에 대한 정보를 공유하고 있죠. 연락 정보와 관련된 모든 문서들이 그 오두막에 보관되어 있어요.","깊은골짜기 일족이 다른 종족과 동맹을 맺는 걸 그냥 두고 볼 수만은 없습니다. 깊은골짜기 일족의 문서를 모두 없애버리세요. 그들의 연락망을 완전히 끊어놓아야 합니다."}, {"깊은골짜기 기록문, 깊은골짜기 보고서, 깊은골짜기 일지를 태워야 합니다."}},
[2996] = {"스트라드를 찾아서", {"예전에 내가 자네를 가르쳤던 적이 있지만, 이제 자네는 다른 이의 가르침을 받을 때가 되었네. 톱니항에 사는 이인데 이름은 스트라드라고 하네. 비록 인간이긴 하지만 지레 나약할 거라고 생각하진 말게. 덩치는 작아도 무척 강하다네. 그가 더 강한 소환 기술을 가르쳐 줄 수 있을 걸세. 자네는 이미 배울 준비가 된 것 같아. 지금 가 보게. 톱니항... 항구 마을 위에 있는 탑 옆에 있을 걸세."}, {"톱니항에 있는 스트라드 파선과 대화해야 합니다."}},
[2997] = {"신앙의 고서", {"안녕하시오. 당신이 던 모로의 주민들을 돕느라 바쁘다는 것은 알고 있소. 하지만 시간이 나면 아이언포지에 들러 우리 시대 가장 존경받는 성기사 중 하나인 타이자 배틀포지를 찾아가 보시오. 젊은 성기사들이 성스러운 빛과 성기사의 의무에 대해 배울 때가 되면 그녀를 찾아간다오.","신비의 전당 위층으로 가면 그녀를 찾을 수 있을 거요. 신비의 전당에 처음 가본다면 마법 지구를 먼저 찾아가면 될 거요. 그럼 행운을 빌겠소."}, {"아이언포지에 있는 타이자 배틀포지와 대화하십시오."}},
[2998] = {"신앙의 고서", {"안녕하십니까, <형제/자매>님. 우리 성기사 가운데 가장 영향력 있는 두소리안 랄께서 최근 <형제/자매>님 얘기를 하시더군요. 언뜻 들으니 이제 시험을 치를 때가 되었다는 얘기와 지금까지 잘해왔다는 칭찬도 하시더군요.","나라면 시간 있을 때 스톰윈드로 가서 그분을 만나 뵙겠습니다. 그분은 대부분의 시간을 빛의 대성당에서 보내시니 분명 그곳에 계실 겁니다."}, {"스톰윈드에 있는 두소리안 랄과 대화해야 합니다."}},
[2999] = {"신앙의 고서", {"안녕하시오. 당신이 던 모로의 주민들을 돕느라 바쁘다는 것은 알고 있소. 하지만 시간이 나면 아이언포지에 들러 우리가 가장 존경하는 성기사 중 하나인 타이자 배틀포지를 찾아가 보시오. 젊은 성기사들이 성스러운 빛과 성기사의 의무에 대해 배울 때가 되면 그녀를 찾아간다오."}, {"아이언포지에 있는 타이자 배틀포지와 대화하십시오."}},
[3000] = {"신앙의 고서", {"안녕하신가. 자네가 동부 왕국의 주민들을 돕느라 바쁘다는 것은 알고 있네만 시간이 된다면 아이언포지에 들러 당신 고향에서 가장 존경 받는 성기사 중 하나인 타이자 배틀포지를 만나 보도록 하시게나. 젊은 성기사들이 성스러운 빛과 성기사의 의무에 대해 배울 때가 되면 그녀를 찾아간다네.","아이언포지의 마법 지구에 있는 신비의 전당 위층에서 그녀를 찾을 수 있을 걸세."}, {"아이언포지에 있는 타이자 배틀포지와 대화하십시오."}},
[3001] = {"스트라드를 찾아", {"예전엔 자네가 내게서 기술을 배웠지만 이제는 다른 이에게서 배울 때가 되었네. 자네가 만나봐야 할 친구의 이름은 스트라드라고 하고 톱니항에 살고 있다네. 비록 인간이긴 하지만 나약하다거나 적이라고 생각하는 일은 없도록 하게나. 적어도 지금은 말이야. 아주 강한 사람이니 존중해주어야 해. 이제 자네는 준비가 된 것 같으니 그 친구가 더 강한 소환 마법을 가르쳐줄 걸세. 자, 어서 가보게. 톱니항 마을 위, 탑 옆으로 가면 그가 있을 걸세."}, {"톱니항에 있는 스트라드 파선을 찾아가야 합니다."}},
[3002] = {"골두니 수정구", {"비록 적을 잘 안다고는 해도 난 전사일 뿐입니다. 나로서는 이 수정구가 얼마나 강력한 것인지 전혀 말 해줄 수가 없군요.","이 수정구에 대해 더 자세히 말해 줄 수 있는 마법사를 하나 알고 있습니다. 오그리마에 사는 우텔타이에게 이걸 가져가도록 하세요."}, {"오그리마의 검은장미 오두막에 있는 우텔나이에게 골두니 수정구를 가져가야 합니다."}},
[3022] = {"취급 주의", {"<name>, 뜻 깊은 일을 위해 몸과 마음을 아끼지 않으시는군요. 저를 더 돕고 싶으시다고요? 물론 해 주실 일이야 더 있죠!","이 상자를 에렐라스 앰버스카이에게 가져다주세요. 그 드루이드는 이 알을 돌보고 부화시켜 히포그리프로 조련하죠. 텔드랏실 남부에 있는 루테란이라는 작은 마을에서 찾을 수 있을 거예요."}, {"루테란 마을에 있는 에렐라스 앰버스카이에게 조심스럽게 포장된 상자를 가져가야 합니다."}},
[3042] = {"트롤 경화제", {"줄파락의 성난모래 트롤들은 사암에서 담금질용 경화제를 만들고는 하는데, 내 작업에도 그걸 좀 썼으면 하네. 감정가들이 아주 비싼 값을 부르는 물건이니 충분히 가지고 오면 후하게 사례하겠네.","줄파락은 가젯잔의 북서쪽에 있지. 행운을 비네."}, {"가젯잔에 있는 트렌튼 라이트해머에게 트롤 경화제 20병을 가져가야 합니다."}},
[3062] = {"검은 심장", {"에다나 헤이트탈론은 서쪽에 있는 폐허에 사는 바람계곡 하피들의 여왕이라네. 그녀야말로 완전한 악의 세력이지. 소문에 듣자하니 그 하피의 심장은 수정으로 되어 있다고 하더군. 다른 모든 이들에게 느끼는 증오를 빼면 아무런 감정도 없다고 하지. 에다나에게 맞서 싸우고 그녀의 검은 심장을 가지고 내게 돌아와 주게.","전에 하피들이 지니고 다닌다는 뿔에 대해 들은 적이 있는데, 폐허의 북쪽에 있는 헤이트탈론 비석 곁에서 그 뿔을 불면 그 하피 여왕이 나타난다고 하네.","자신의 동족이 아니라 죽음의 사자가 자신을 부른 것이라는 걸 알게 되었을 때 그녀가 얼마나 놀랄지 상상해 보게."}, {"모자케 야영지에 있는 탈로 쏜후프가 에다나의 검은 심장을 가져다 달라고 했습니다."}},
[3063] = {"바람계곡 일족에 대한 복수", {"나는 소싯적에 아주 능숙한 사냥꾼이었지. 어딜 가도 내가 두려워하는 짐승이라고는 없었네. 그 일이 있기 전까지는...","난 힘겨운 전투를 치른 뒤 숲 속에 앉아 칼을 갈고 있다가 습격을 당하고 말았다네. 나를 포위했던 하피들을 피해 가까스로 위기를 모면했지. 지금도 놈들의 울음소리가 메아리치는 게 들려...","그 후로 나는 변했지. 그러나 내 안에서는 증오가 끓어오르고 있네. 이렇게 오랜 세월이 흘렀지만 난 복수를 해야만 하네.","바람계곡 하피들은 여기서 서쪽에 있는 까마귀바람 폐허로 가면 찾을 수 있네. 날 위해 복수를 해 주게."}, {"탈로 쏜후프가 바람계곡 하피 4마리, 바람계곡 약탈자 4마리, 바람계곡 바람소환사 4마리, 그리고 바람계곡 암살자 4마리를 처치해 달라고 했습니다."}},
[3065] = {"간결한 서판", {"당신이 없는 동안 서판이 하나 도착했소, <name>.","시간이 있다면 읽어보시오. 내가 착각한 것이 아니라면 이건 상급 전사, 프랑이 보낸 거요. 준비가 되는 대로 그에게 가보시오. 당신과 이야기를 나누고 싶은 것 같소. 동굴 밖에 있는 그늘에서 당신이 오기를 기다리고 있을 거요."}, {"간결한 서판을 읽어보고 시험의 골짜기에 있는 프랑과 대화하십시오."}},
[3082] = {"글이 새겨진 서판", {"<name>, 당신이 자리를 비운 사이, 서판이 하나 도착했소.","시간이 날 때 한번 읽어보시오. 내 기억이 맞다면 상급 사냥꾼, 젠샨이 보낸 것일 게요. 당신이 준비가 되면 이야기를 나누고 싶은 것 같소."}, {"글이 새겨진 서판을 읽어보고 시험의 골짜기에 있는 젠샨과 대화해야 합니다."}},
[3083] = {"암호화된 서판", {"<name>, 당신이 자리를 비운 사이, 서판이 하나 도착했소.","시간이 날 때 한번 읽어보시오. 내 기억이 맞다면 상급 도적, 르와그가 보낸 것일 게요. 당신이 준비가 되면 이야기를 나누고 싶은 것 같소."}, {"암호화된 서판을 읽어보고 시험의 골짜기에 있는 르와그와 대화하십시오."}},
[3084] = {"룬으로 새겨진 서판", {"<name>, 당신이 자리를 비운 사이, 서판이 하나 도착했소.","시간이 날 때 한번 읽어보시오. 내 기억이 맞다면 상급 주술사, 시크리크가 보낸 것일 게요. 당신이 준비가 되면 이야기를 나누고 싶은 것 같소."}, {"룬으로 새겨진 서판을 읽어보고 시험의 골짜기에 있는 시크리크와 대화하십시오."}},
[3085] = {"성스러운 서판", {"<name>, 당신이 자리를 비운 사이, 서판이 하나 도착했소.","시간이 날 때 한번 읽어보시오. 내 기억이 맞다면 상급 사제, 켄자이가 보낸 것일 게요. 당신이 준비가 되면 이야기를 나누고 싶은 것 같소."}, {"성스러운 서판을 읽어보고 시험의 골짜기의 동굴 막사 밖에 있는 켄자이와 대화하십시오."}},
[3086] = {"상형 문자로 된 서판", {"<name>, 당신이 자리를 비운 사이, 서판이 하나 도착했소.","시간이 날 때 한번 읽어보시오. 내 기억이 맞다면 상급 마법사, 마이아가 보낸 것일 게요. 당신이 준비가 되면 이야기를 나누고 싶은 것 같소."}, {"상형 문자로 된 서판을 읽어보고 시험의 골짜기의 동굴 막사 입구 근처에 있는 마이아와 대화하십시오."}},
[3087] = {"글이 새겨진 양피지", {"<name>, 자네가 자리를 비운 사이, 양피지가 도착했네.","시간이 날 때 한번 읽어보게. 내 기억이 맞다면 상급 사냥꾼, 카라니샤가 보낸 것일 걸세. 자네만 준비되면 이야기를 나누고 싶은 것 같네."}, {"글이 새겨진 양피지를 읽어보고 시험의 골짜기에 있는 카라니샤와 대화해야 합니다."}},
[3088] = {"암호화된 양피지", {"<name>, 자네가 자리를 비운 사이, 양피지가 하나 도착했네.","시간이 날 때 한번 읽어보게. 내 기억이 맞는다면 상급 도적, 르와그가 보냈을 거야. 자네만 준비되면 이야기를 나누고 싶어하는 것 같네."}, {"암호화된 양피지를 읽어보고 시험의 골짜기에 있는 르와그와 대화해야 합니다."}},
[3089] = {"룬으로 새겨진 양피지", {"<name>, 자네가 자리를 비운 사이, 양피지가 하나 도착했네.","시간이 날 때 한번 읽어보게. 내 기억이 맞는다면 상급 주술사, 시크리크가 보냈을 거야. 자네만 준비되면 이야기를 나누고 싶은 것 같네."}, {"룬으로 새겨진 양피지를 읽어보고 시험의 골짜기에 있는 시크리크와 대화해야 합니다."}},
[3090] = {"오래된 양피지", {"<name>, 자네가 자리를 비운 사이, 양피지가 하나 도착했네.","시간이 날 때 한번 읽어보게. 내 기억이 맞는다면 상급 흑마법사, 나르톡이 보냈을 거야. 자네만 준비되면 이야기를 나누고 싶어하는 것 같네."}, {"오래된 양피지를 읽어보고 시험의 골짜기에 있는 나르톡과 대화해야 합니다."}},
[3091] = {"간결한 글쪽지", {"이보시오. 바로 몇 분 전에 전령이 그대를 찾았었소. 내 생각엔 상급 전사인 하루트 썬더혼 님이 보낸 것 같소이다. 내용이 그리 길지 않은 것 같으니 어서 읽어 보는 게 좋겠소."}, {"간결한 글쪽지를 읽고 나라체 야영지에 있는 하루트 썬더혼과 대화해야 합니다."}},
[3092] = {"글이 새겨진 쪽지", {"이보시오. 바로 몇 분 전에 전령이 그대를 찾았소. 내 생각엔 상급 사냥꾼인 랑카 파샷 님이 보낸 것 같소이다. 내용이 그리 길지 않은 것 같으니 어서 읽어 보는 게 좋겠소."}, {"글이 새겨진 쪽지를 읽고 나라체 야영지에 있는 랑카 파샷과 대화해야 합니다."}},
[3093] = {"룬으로 새겨진 쪽지", {"이보시오. 바로 몇 분 전에 전령이 그대를 찾았소. 내 생각엔 상급 주술사인 밀라 던스트라이더 님이 보낸 것 같소이다. 내용이 그리 길지 않은 것 같으니 어서 읽어 보는 게 좋겠소."}, {"룬으로 새겨진 쪽지를 읽고 나라체 야영지에 있는 밀라 돈스트라이더와 대화해야 합니다."}},
[3094] = {"풀빛 쪽지", {"이보시오. 바로 몇 분 전에 전령이 그대를 찾았소. 내 생각엔 상급 드루이드인 가트 미스트러너 님이 보낸 것 같소이다. 내용이 그리 길지 않은 것 같으니 어서 읽어 보는 게 좋겠소."}, {"풀빛 쪽지를 읽고 나라체 야영지에 있는 가트 미스트러너와 대화해야 합니다."}},
[3095] = {"간결한 두루마리", {"아, 자네가 정신 나간 스컬지 놈들을 상대하러 나간 동안 이 간결한 두루마리가 도착했네. 두루마리에 상급 전사인 다날 스턴의 인장이 찍힌 것으로 보아 중요한 일이 있는 것 같네. 다시 여행을 떠나기 전에 시간을 내서 이 두루마리를 읽어보는 게 좋겠네."}, {"간결한 두루마리를 읽고 죽음의 종소리 마을에 있는 다날 스턴과 대화해야 합니다."}},
[3096] = {"암호화된 두루마리", {"아, 자네가 정신 나간 스컬지 놈들을 상대하러 나간 동안 이 암호화된 두루마리가 도착했네. 두루마리에 상급 도적 데이비드 트리아스의 인장이 찍힌 것으로 보아 중요한 일이 있는 것 같네. 다시 여행을 떠나기 전에 시간을 내서 이 두루마리를 읽어보는 게 좋겠네."}, {"암호화된 두루마리를 읽고 죽음의 종소리 마을에 있는 데이비드 트리아스와 대화해야 합니다."}},
[3097] = {"성스러운 두루마리", {"아, 자네가 정신 나간 스컬지 놈들을 상대하러 나간 동안 이 성스러운 두루마리가 도착했네. 두루마리에 상급 사제 어둠의 사제 듀스텐의 인장이 찍힌 것으로 보아 중요한 일이 있는 것 같네. 다시 여행을 떠나기 전에 시간을 내서 이 편지를 읽어보는 게 좋겠네."}, {"성스러운 두루마리를 읽고 죽음의 종소리 마을의 예배당에 있는 어둠의 사제 듀스텐과 대화해야 합니다."}},
[3098] = {"상형 문자로 된 두루마리", {"아, 자네가 정신 나간 스컬지 놈들을 상대하러 나간 동안 이 상형 문자로 된 두루마리가 도착했네. 두루마리에 상급 마법사 이자벨라의 인장이 찍힌 것으로 보아 중요한 일이 있는 것 같네. 다시 여행을 떠나기 전에 시간을 내서 이 두루마리를 읽어보는 게 좋겠네."}, {"상형 문자로 된 두루마리를 읽고 죽음의 종소리 마을 예배당에 있는 이자벨라와 대화해야 합니다."}},
[3099] = {"오래된 두루마리", {"아, 자네가 정신 나간 스컬지 놈들을 상대하러 나간 동안 이 오래된 두루마리가 도착했네. 두루마리에 상급 흑마법사 맥시밀리언의 인장이 찍힌 것으로 보아 중요한 일이 있는 것 같네. 다시 여행을 떠나기 전에 시간을 내서 이 두루마리를 읽어보는 게 좋겠네."}, {"오래된 두루마리를 읽고 죽음의 종소리 마을에 있는 맥시밀리언과 대화해야 합니다."}},
[3100] = {"간결한 편지", {"당신이 검은바위 첩보원들을 처리하고 돌아오자마자 이 상형 문자로 된 편지를 전해 달라는 부탁을 받았습니다. 이 서신은 상급 전사인 레인 비셰어의 인장이 찍힌 것으로 보입니다. 수도원 내에서 다른 볼일을 보러 가기 전에 어서 편지를 읽어 보는 게 좋겠습니다."}, {"간결한 편지를 읽고 북녘골 수도원에 있는 레인 비셰어와 대화해야 합니다."}},
[3101] = {"신성한 편지", {"당신이 검은바위 첩보원들을 처리하고 돌아오자마자 이 신성한 편지를 전해 달라는 부탁을 받았습니다. 이 편지는 상급 성기사인 수사 사무엘의 인장이 찍힌 것으로 보입니다. 수도원 내에서 다른 볼일을 보러 가기 전에 어서 편지를 읽어 보는 게 좋겠습니다."}, {"신성한 편지를 읽고 북녘골 수도원에 있는 수사 사무엘과 대화해야 합니다."}},
[3102] = {"암호화된 편지", {"자네가 코볼트 야영지에서 돌아오자마자 이 상형 문자로 된 편지를 전해 달라는 부탁을 받았네. 이 서신은 상급 도적인 요리크 케리던이 보낸 것일세. 다른 볼일을 보러 가기 전에 어서 편지를 읽어 보는 게 좋겠네."}, {"암호화된 편지를 읽고 노스샤이어 수도원 뒤 마구간에 있는 요리크 케리던과 대화해야 합니다."}},
[3103] = {"성스러운 편지", {"당신이 검은바위 첩보원들을 처리하고 돌아오자마자 이 상형 문자로 된 편지를 전해 달라는 부탁을 받았습니다. 이 편지는 상급 사제인 여사제 아네타의 인장이 찍힌 것으로 보입니다. 수도원 내에서 다른 볼일을 보러 가기 전에 어서 편지를 읽어 보는 게 좋겠습니다."}, {"성스러운 편지를 읽고 북녘골 수도원에 있는 여사제 아네타와 대화해야 합니다."}},
[3104] = {"상형 문자로 된 편지", {"당신이 검은바위 첩보원들을 처리하고 돌아오자마자 이 상형 문자로 된 편지를 전해 달라는 부탁을 받았습니다. 이 편지는 상급 마법사인 켈덴 브레멘의 인장이 찍힌 것으로 보입니다. 수도원 내에서 다른 볼일을 보러 가기 전에 어서 편지를 읽어 보는 게 좋겠습니다."}, {"상형 문자로 된 편지를 읽고 북녘골 수도원 안에 있는 켈덴 브레멘과 대화해야 합니다."}},
[3105] = {"오래된 편지", {"당신이 검은바위 첩보원들을 처리하고 돌아오자마자 이 상형 문자로 된 편지를 전해 달라는 부탁을 받았습니다. 이 편지는 상급 흑마법사인 드루실라 라 살르의 인장이 찍힌 것으로 보입니다. 수도원 내에서 다른 볼일을 보러 가기 전에 어서 편지를 읽어 보는 게 좋겠습니다."}, {"오래된 편지를 읽고 북녘골 수도원 옆에 있는 드루실라 라 살르와 대화해야 합니다."}},
[3106] = {"간결한 룬", {"<name> 님, 누가 당신께 이 룬을 전해달라고 부탁하더군요. 시간을 내서 한번 읽어보세요. 제 생각에는 상급 전사 스란이 보낸 것 같아요. 쭉 훑어 봐뒀다가 짬이 나면 앤빌마로 가서 이야기를 나눠보도록 하세요."}, {"간결한 룬을 읽어보고 눈마루 골짜기에 있는 스란 코르만과 대화해야 합니다."}},
[3107] = {"신성한 룬", {"<name> 님, 누가 당신께 이 룬을 전해달라고 부탁하더군요. 시간을 내서 한번 읽어보세요. 제 생각에는 상급 성기사 브로모스가 보낸 것 같아요. 쭉 훑어 봐뒀다가 짬이 나면 앤빌마로 가서 이야기를 나눠보도록 하세요."}, {"신성한 룬을 읽어보고 앤빌마에서 브로모스 그럼너를 찾아 대화해야 합니다."}},
[3108] = {"글이 새겨진 룬", {"<name> 님, 누가 당신께 이 룬을 전해달라고 부탁하더군요. 시간을 내서 한번 읽어보세요. 제 생각에는 상급 사냥꾼 토르가스가 보낸 것 같아요. 쭉 훑어 봐뒀다가 짬이 나면 앤빌마로 가서 이야기를 나눠보도록 하세요."}, {"글이 새겨진 룬을 읽어보고 앤빌마에 있는 토르가스 그림슨과 대화해야 합니다."}},
[3109] = {"암호화된 룬", {"자네가 날 도우러 간 사이에 자네 앞으로 이 룬이 도착했네. 시간을 내서 한번 읽어보게. 상급 도적, 솔름이 보낸 것 같아. 쭉 훑어 봐뒀다가 짬이 나면 앤빌마로 가서 만나보도록 하게."}, {"암호화된 룬을 읽어보고 눈마루 골짜기에 있는 솔름 하르그린과 대화하십시오."}},
[3110] = {"성스러운 룬", {"자네가 날 도우러 간 사이, 이 룬이 자네 앞으로 도착했네. 시간을 내서 한번 읽어보게. 상급 사제, 브란스토크가 보낸 것 같아. 쭉 훑어 봐뒀다가 짬이 나면 앤빌마로 가서 만나보도록 하게."}, {"성스러운 룬을 읽어보고 앤빌마에 있는 브란스토크 칼더와 대화하십시오."}},
[3112] = {"간결한 쪽지", {"자네가 날 도우러 간 사이, 이 쪽지가 자네 앞으로 도착했네. 시간을 내서 한번 읽어보게. 상급 전사 스란이 보낸 것 같아. 쭉 훑어 봐뒀다가 짬이 나면 앤빌마로 가서 만나보도록 하게."}, {"간결한 쪽지를 읽어보고 눈마루 골짜기에 있는 스란 코르만과 대화해야 합니다."}},
[3113] = {"암호화된 쪽지", {"자네가 날 도우러 간 사이, 이 쪽지가 자네 앞으로 도착했네. 시간을 내서 한번 읽어보게. 상급 도적, 솔름이 보낸 것 같아. 쭉 훑어 봐뒀다가 짬이 나면 앤빌마로 가서 만나보도록 하게."}, {"암호화된 쪽지를 읽어보고 눈마루 골짜기에 있는 솔름 하르그린과 대화하십시오."}},
[3114] = {"상형 문자로 된 쪽지", {"자네가 날 도우러 간 사이 이 쪽지가 자네 앞으로 도착했네. 시간을 내서 한번 읽어보게. 상급 마법사 마릭이 보낸 것 같으니, 쭉 훑어 봐뒀다가 짬이 나면 앤빌마로 가서 그를 만나보도록 하게."}, {"상형 문자로 된 쪽지를 읽어보고 눈마루 골짜기 위 앤빌마에 있는 마릭 너리빗과 대화해야 합니다."}},
[3115] = {"오염된 룬", {"<name> 님, 누가 당신께 이 룬을 전해달라고 부탁하더군요. 시간을 내서 한번 읽어보세요. 제 생각에는 상급 흑마법사 사리팔이 보낸 것 같아요. 쭉 훑어 봐뒀다가 짬이 나면 앤빌마로 가서 이야기를 나눠보도록 하세요.","하지만 조심하는 게 좋아요, <name>. 당신과 같은 사람은 그 부근에서 별로 환영받지 못하니까요."}, {"오염된 룬을 읽고 눈마루 골짜기 위의 앤빌마에 있는 사리팔 스몰더브루와 대화해야 합니다."}},
[3116] = {"간결한 도장", {"이 도장은 상급 전사 알리시아께서 전령을 통해 전달한 것입니다. 시간이 날 때 한번 도장을 읽어보고 그분을 찾아가 보십시오. 알리시아께서 하실 말씀이 있는 것 같습니다."}, {"그늘 협곡의 알드랏실 안에 있는 알리시아와 대화해야 합니다."}},
[3117] = {"글이 새겨진 도장", {"이 도장은 상급 사냥꾼 아얀나께서 전령을 통해 전달한 것입니다. 시간이 날 때 한번 도장을 읽어보고 그분을 찾아가 보십시오. 당신에게 하실 말씀이 있는 것 같습니다."}, {"그늘 협곡의 알드랏실 안에 있는 아얀나 에버스트라이드와 대화해야 합니다."}},
[3118] = {"암호화된 도장", {"이 도장은 상급 도적 프라훈께서 전령을 통해 전달한 것입니다. 시간이 날 때 한번 도장을 읽어보고 그분을 찾아가 보십시오. 프라훈께서 하실 말씀이 있는 것 같습니다."}, {"그늘 협곡의 알드랏실 안에 있는 프라훈 셰이드위스퍼와 대화해야 합니다."}},
[3119] = {"성스러운 도장", {"이 도장은 상급 사제 샨다께서 전령을 통해 전달한 것입니다. 시간이 날 때 한번 도장을 읽어보고 그분을 찾아가 보십시오. 샨다께서 하실 말씀이 있는 것 같습니다."}, {"그늘 협곡의 알드랏실 안에 있는 샨다와 대화해야 합니다."}},
[3120] = {"풀빛 도장", {"이 도장은 상급 드루이드 마르단트께서 전령을 통해 전달하신 것입니다. 시간이 날 때 도장을 읽어보고 그분을 한번 찾아가 보십시오. 마르단트께서 하실 말씀이 있는 것 같습니다."}, {"그늘 협곡의 알드랏실 안에 있는 마르단트 스트롱오크와 대화해야 합니다."}},
[3121] = {"이상한 부탁", {"모자케 야영지의 북쪽, 라리스 정자에 있는 제라쉬 파이어블레이드가 내가 이곳에서 일을 시작하는 데 필요한 물건을 가지고 있다네! 그 친구가 물약을 하나 만들었는데 그걸로 내... 그러니까 그게... 어디에 필요한지는 나중에 알게 될 걸세.","지금 당장 제라쉬에게 떠나게. 자, 내가 제라쉬에게 약초와 맞바꾸기로 약속했던 구겨진 가면을 가져가게. 비록 좀 구겨졌지만 정말 드워프 가면 같아서 그 친구도 놀랄걸세. 그 친구가 기뻐하리라 확신하네."}, {"페랄라스의 라리스 정자에 있는 제라쉬 파이어블레이드에게 구겨진 가면을 가져가야 합니다."}},
[3122] = {"의술사 우제리에게 돌아가기", {"약초 여기 있네. 이걸 의술사 우제리에게 가져가게.","내가 보내준 가면에 만족해 한다는 것도 전해 주시기 바라네."}, {"페랄라스에 있는 의술사 우제리에게 제라쉬의 약초 주머니를 가져가야 합니다."}},
[3123] = {"단지 실험", {"이건 내가 행한 의식과 자네의 능력 모두에 대한 시험이 될 걸세. 이 단지를 받게나. 이게 있으면 생물을 갈무리해서 이 안에 담을 수 있지.","이곳에서 동쪽으로 가서 야수들을 찾아보게나. 포악한 무쇠가죽곰과 긴다리 송곳니늑대들이 자네의 목표라네. 녀석들을 처치하고 녀석들의 영혼이 빠져나가기 전에 뮤세크 단지를 사용해서 갈무리해서 담아 오게나."}, {"무쇠가죽곰이나 긴다리 송곳니늑대 10마리를 처치하고 뮤세크 단지를 사용해 죽은 야수들을 갈무리해서 담아와야 합니다.","페랄라스에 있는 의술사 우제리에게 야수 뮤세크 10개와 야수 뮤세크 단지를 가져가야 합니다."}},
[3124] = {"히포그리프 뮤세크", {"자네는 신뢰할 만한데다가 뮤세크 단지를 쓰는 솜씨도 아주 그만이니 이제 또 다른 생물을 좀 잡아 줬으면 하네.","오랜 세월 동안 나이트 엘프의 충실한 벗이 되어 준 히포그리프의 영혼은 충성스럽고도 굳건하지.","자네의 임무는 공작날개 히포그리프를 10마리 처치해서 재빨리 단지에 그들을 잡아 갈무리해서 담아오는 것이네. 그렇게 해야 뮤세크가 보존될 수 있으니까 말이야. 히포그리프는 여기서 남쪽에 있는 높은벌에서 찾을 수 있다네."}, {"종에 상관없이 공작날개 히포그리프 10마리를 처치한 다음 뮤세크 단지를 사용해 갈무리해서 담아와야 합니다."}},
[3125] = {"요정용 뮤세크", {"이번에는 보기에는 아주 나약해 보여도 아주 강한 영혼을 지니고 있는 생물을 잡아야 하네. 내가 말하는 요정용은 나이트 엘프의 동지라네. 녀석을 한 마리 잡아서 호드도 그 영혼의 힘으로부터 이익을 얻어야만 하네.","자, 뮤세크 단지 여기 있네. 화살요정용이나 요정용을 8마리 처치해서 그들의 뮤세크를 손에 넣게. 요정용들은 여기서 서쪽으로 가면 찾을 수 있다네."}, {"화살요정용이나 요정용 8마리를 처치하여 뮤세크 단지를 사용해 죽은 요정용을 갈무리해서 담아와야 합니다."}},
[3126] = {"나무정령 뮤세크", {"우리의 다음 목표는 숲을 어슬렁거리는 떠도는 숲정령일세. 나이트 엘프들은 호드에 맞서기 위해 나무정령들을 동지로 받아들였지. 나무정령들이 움직이는 속도는 느릴지 몰라도 수호자로서의 역할을 훌륭히 해낸다는 것은 부인할 수가 없는 사실일세. 곧 이 힘을 우리가 사용할 수 있게 될 것이야.","떠도는 나무정령 세 마리를 찾아서 해치우고, 뮤세크 단지를 써서 그 영혼을 갈무리해 담아 오게나."}, {"떠도는 나무정령 3마리를 처치하고 뮤세크 단지를 이용하여 그들을 갈무리해야 합니다."}},
[3127] = {"산악거인 뮤세크", {"마지막으로 자네에게 부탁할 일은 북서쪽으로 가서 마지막 적을 찾아내는 것일세.","산악거인들은 불타는 군단이 이 세계에 나타났을 때 나이트 엘프 녀석들을 돕기 위해 산을 떠나 내려왔지. 자네가 그들의 암석투성이인 땅을 돌아다니다가 땅이 흔들리기 시작하는 걸 보면 거대한 덩치의 산악거인을 알아볼 수 있을 걸세.","그 거대한 덩치의 힘을 손에 넣고 싶다네. 산악거인 7마리를 죽이고 뮤세크 단지를 사용해 그들의 강력한 뮤세크를 담아 오게나."}, {"평지거인이나 절벽거인 7마리를 처치하여 뮤세크 단지를 이용해 죽은 산악거인을 갈무리해서 담아와야 합니다."}},
[3128] = {"천연 재료", {"내가 하던 일을 마치려면 특정 재료가 필요하다네... 내가 자네에게 맡길 다른 일들과 함께해도 좋아.","페랄라스의 나무정령에게서 쪼개진 통나무를 하나 가져와야 하고, 서쪽에 있는 산악거인과 요정용에게서 보석 박힌 광석도 가져와야 하네. 그리고 동부 내륙지의 올빼미야수나 페랄라스의 히포그리프에게서 질긴 힘줄도 구해다 주게. 마지막으로, 내가 언급한 이 생물들 중 어느 것에게서나 얻을 수 있는 금속 파편을 많이 가져오게."}, {"모자케 야영지에 있는 의술사 우제리에게 쪼개진 통나무 2개, 보석 박힌 광석 6개, 질긴 힘줄 20개, 금속 파편 40개를 가져가야 합니다."}},
[3129] = {"영혼의 무기", {"무기에 뮤세크를 불어넣는 것은 아주 까다로운 과정이지. 이제 필요한 재료도 다 있고 자네가 갈무리해 온 여러 생물들의 뮤세크도 있으니 의식을 시작할 수 있네. 곧 돌아올 테니 잠시 기다리게나."}, {"의술사 우제리가 무기를 준비하는 동안 잠시 기다려야 합니다."}},
[3130] = {"증오의갈기일족과의 전투", {"이제 그대를 내 부관, 라트로니쿠스에게 안내하도록 하겠습니다. 그는 나가 군대의 동태를 철저히 감시하는 임무를 수행중에 있답니다. 나는 여기서 나가에 대한 총체적인 상황을 평가하고 있도록 하지요. 전군을 동원해서 녀석들을 치고 싶은 마음이야 굴뚝같지만 요새를 비워 두고 호드나 그보다 더한 적들이 공격할 수 있는 기회를 주는 것은 최선의 방법이 아니니까요.","아무튼, 라트로니쿠스가 그대를 기다리고 있습니다. 지금까지 잘해주었으니 앞으로도 계속 잘해주시기 바랍니다. 자, 이만 가보도록 하세요!"}, {"페더문 요새에 있는 라트로니쿠스 문스피어에게 보고해야 합니다."}},
[3141] = {"로라무스", {"그러니까 악마들과 싸우기로 결심을 굳혔단 말이지? 그럼 내 얘기를 잘 듣는 게 좋을 걸세."}, {"로라무스 탈리뻬데스의 이야기를 들으십시오."}},
[3161] = {"가즈리디안", {"나는 과학자이지만 보물 사냥꾼이기도 하지!","오래전 이 땅을 점령했던 트롤들은 그들이 섬기던 히드라 신, 가즈릴라의 모양을 본 따 많은 장신구를 만들었소. 그 장신구는 그 신의 이름을 딴 \"가즈리디안\"이라는 재료로 만들어졌지.","내가 직접 찾은 게 좀 있긴 하지만 더 모아서 연구를 해봤으면 하오. 당신이 좀 찾아다 주지 않겠소?","가즈리디안을 쉽게 찾도록 탐지기까지 개발해 놓았소! 그냥 걸치기만 하면 곧바로 탐색을 할 수 있지!"}, {"타나리스에 있는 마본 리벳시커가 가즈리디안 장신구 30개를 모아 달라고 했습니다."}},
[3181] = {"야수의 뿔", {"이 거대한 뿔에는 긁힌 자국과 깨진 곳이 수도 없이 많으며 거친 표면에 이상한 금속이 박혀 있습니다.","더 자세히 살펴보니 이 금속은 실제로 도끼날이 부러져 박힌 것임을 알 수 있습니다. 금속 위에는 다음과 같은 글귀가 새겨져 있습니다.","\"산악경비대 페블비티 소유\"","부러진 도끼날을 빼려고 하자 날이 산산조각이 납니다.","아뿔싸!"}, {"산악경비대 페블비티에게 마르골의 거대한 뿔을 가져가야 합니다."}},
[3182] = {"무훈의 증서", {"그런 꾸며낸 얘기를 나보고 믿으라는 건가요? 그 뿔이 진짜라면 표면에 부러진 내 도끼 날이 아직도 박혀 있었을 텐데요.","이봐요, 잘 들으세요. 그런 너무 뻔한 가짜는 아이언포지에 있는 도서관장 소리우스에게나 가져가세요. 그가 당신 얘기가 정말 사실이라고 판단한다면 무훈의 증서를 받게 될 겁니다. 그 증거물을 가지고 내게 돌아오면 이글거리는 협곡으로 통하는 열쇠를 주도록 하죠.","어서 가 보세요!"}, {"아이언포지에 있는 도서관장 소리우스에게 마르골의 거대한 뿔을 가져가야 합니다."}},
[3201] = {"이글거리는 협곡의 열쇠", {"페블비티가 이 뿔이 진품인지 의심했다니 믿어지지 않는군. 우리끼리만 하는 얘기지만, 페블비티가 질투하고 있는 것 같소이다. 내가 알기로 페블비티는 그 야수를 자기가 직접 해치우고 싶어했었소. '사건' 같은 것도 있었고 해서 말이지.","그래, 무훈의 증거가 필요하다고 했소? 우리 박물관에 큰 기여를 했는데 그 정도야 문제없지.","<큰 문서를 작성하기 시작하는 도서관장 도리우스.>","여기 있소! 이걸 가지고 페블비티에게 돌아가시오."}, {"산악경비대 페블비티에게 무훈의 증서를 가져가야 합니다."}},
[3221] = {"렌퍼럴과 대화", {"자네가 우리 죽음추적자들을 도우러 떠난 사이에 렌퍼럴이 자네를 찾았다네. 자세한 얘기는 하지 않았지만 자네가 준 늑대 심장에 대해서 얘기를 나누고 싶어하더군. 가서 그를 만나보도록 하게."}, {"공동묘지에 있는 연금술사 렌퍼럴과 대화해야 합니다."}},
[3261] = {nil, {"이제 나와는 작별이군. 더 이상 가르칠 게 없군. 남쪽에 있는 타우라조 야영지로 가서 조른 스카이시어와 얘기를 나눠보게. 앞으로 어떻게 해야 할지 알려줄 걸세."}, {"타우라조 야영지에 있는 조른 스카이시어와 대화해야 합니다."}},
[3281] = {nil, {"랩터들을 처치했다니 마음이 조금 놓이는군. 하지만 도둑맞은 은화도 반드시 되찾아야 하오! 톱니항 남쪽에 거대한 랩터 소굴들이 여럿 있다는 보고가 들어왔소. 랩터 서식지라는 데 아마도 랩터들이 훔친 은화를 그곳으로 가지고 갔을 거요.","이 랩터 소굴로 쳐들어가서 도둑맞은 은화를 찾아보시오. 은화를 찾거든 내게 다시 돌아오시오."}, {"십자로에 있는 가즈록에게 도둑맞은 은화를 가져가야 합니다."}},
[3301] = {"무라 룬토템", {"내 동료인 무라 룬토템은 우리의 동맹인 언데드를 돕고자 은빛소나무 숲으로 갔소. 그녀는 병들어 죽어가는 은빛소나무 숲을 되살리는 데 도움을 주고자 했다오. 그러니 여기 불모의 땅에 사는 기이한 생명체를 가져가면 무라가 은빛소나무 숲에서 하는 일에 도움이 될 것이오.","그대가 모아온 등껍질의 견본을 은빛소나무 숲에 있는 무라에게 가져다주시오. 그녀는 아마 공동묘지에 있을 것이오. 운이 좋다면 무라가 등껍질을 연구해 죽어가는 숲을 살리는 데 필요한 것을 알아낼 수 있을 것이오."}, {"은빛소나무 숲의 공동묘지에 있는 무라 룬토템과 대화해야 합니다."}},
[3321] = {"잃어버린 물건", {"자네는 대장조합을 위해 많은 일을 해주었네. 보통 사람이 그만큼 하려고 했다면 벌써 포기하고 돌아갔을 걸세. 나도, 그리고 갈반도, 자네가 우리 대장조합의 일원이 될 자격이 있다고 생각한다네.","그래서 조그만 상이라도 주어야겠어. 뭐 그리 대단한 것은 아니고 장신구라네. 그 장신구의 특성은 자네도 익히 알고 있을 걸세. 애초에 이것 때문에 여기를 찾아온 것이 아니었는가?"}, {"트렌튼이 일을 마칠 동안 기다리십시오."}},
[3341] = {"암네나르 처치", {"불모의 땅에는 가시덩굴 구릉이라고 불리는 끔찍한 덩굴 지대가 있소. 그곳을 처음 조사했을 때는 그다지 큰 위협이랄 것이 없었는데, 최근에 조사한 바로는 훨씬 심각한 조짐을 보이고 있소...","가시덩굴 구릉에 사는 가시멧돼지들이 스컬지와 동맹을 맺었소. 혹한의 암네나르라는 리치가 거대한 의식을 이용해 가시멧돼지들의 모든 행동을 지배하고 있는 것이오.","암네나르는 넬쥴과 텔레파시로 직접 연결되어 있는데, 이 연결을 끊어놓아야만 하오. 혹한의 암네나르를 끝장내 주시오."}, {"앤드류 브로넬이 혹한의 암네나르를 처치하고 혹한의 해골을 가져다 달라고 했습니다."}},
[3361] = {"망명자의 곤경", {"놈리건에 있는 동료들을 찾아가는 길이었는데 갑자기 일이 완전히 잘못돼 버렸지 뭐예요!","짐을 꾸리고 허리 높이까지 내린 눈을 헤치며 앤빌마를 출발했을 때까지는 괜찮았는데, 그 말도 안 되는 지진 때문에 세상천지가 뒤집어져 더 갈 수가 없더군요. 결국 발길을 돌려 이곳으로 돌아왔지만, 설상가상으로 트롤들이 내 짐을 모두 훔쳐가 버렸어요!","요 며칠 동안 제가 하는 일은 어째 제대로 풀리는 경우가 없네요. 부디 남서쪽의 트롤 야영지를 침입해서 제 짐을 찾아줄 수 있으신가요?"}, {"잃어버린 펠릭스의 상자, 펠릭스의 궤짝, 펠릭스의 나사통을 획득해야 합니다."}},
[3362] = {"덤불나무 골짜기", {"덤불나무 골짜기에 가본 적 있나요? 그곳은 굉장히 위험한 곳이에요.","그곳에는 괴상한 짐승들이 있어서 잠시도 마음을 놓을 수가 없답니다! 이름이... 옹이진 덤불나무괴물과 억센뿌리 덤불나무괴물이었던 거 같아요...","어쨌든, 그 골짜기로 들어가고 싶은데 덤불나무괴물들이 그렇게 진을 치고 있으니... 바로 이 때문에 당신의 도움이 필요한 거예요. 녀석들을 좀 처치해주면 제가 다시 돌아가기가 훨씬 수월할 거예요. 어때요? 도와주시겠어요?"}, {"가젯잔에 있는 트란렉이 옹이진 덤불나무괴물 8마리와 억센뿌리 덤불나무괴물 8마리를 처치해 달라고 했습니다."}},
[3363] = {"오염된 노래꽃", nil, nil},
[3364] = {"뜨거운 새벽주 배달", {"여보시오. 앤빌마에 있는 두르난 퍼커터가 주문한 이 맛있는 뜨거운 새벽주를 갖다줘야 하는데 지금에야 생각났지 뭐요! 나는 그렐린이 부탁한 술을 먼저 가져다줘야 하는데다 앤빌마까지 배달을 가면, 새벽주가 식어버려서 항상 불평을 듣고는 했소.","당신은 나보다 빨라 보이는데, 이 뜨거운 새벽주를 두르난에게 갖다주겠소? 새벽주는 몇 분 동안만 뜨거운 상태가 유지되니 빨리 가져다줘야 하오. 두르난은 '차가운' 새벽주를 주문한 게 아니니까. 앤빌마는 저 너머 북동쪽 첩첩산중 안에 있는 마을이오.","그럼 부탁하겠소, <name>. 그리고, 빈 잔을 찾아오는 것도 잊지 마시오!"}, {"앤빌마의 두르난 퍼커터에게 뜨거운 새벽주를 식기 전에 전해야 합니다!"}},
[3365] = {"빈 머그잔 가져가기", {"어, 좋다! 이렇게 귀신이라도 얼어 죽을 만한 추운 겨울날에는 소주... 아니, 이 뜨거운 새벽주만한 게 없지. 덕분에 잘 마셨소이다.","<name>, 수고스럽겠지만 여기 이 빈 술잔을 노리에게 되돌려주지 않겠소?"}, {"눈마루 골짜기에 있는 노리 프라이드드리프트에게 노리의 머그잔을 가져가야 합니다."}},
[3366] = {"빛나는 조각", {"이 조각은 밝은 녹색을 띠고 있습니다. 겉은 단단해 보이지만 그 안에는 연기 같은 것이 소용돌이치고 있습니다. 수정 안의 연기는 눈길을 끌며 뭔가 굉장히 불길한 예감이 듭니다.","혹시 이 이상한 조각에 대한 얘기를 해줄 이가 있는지 톱니항에서 찾아보는 게 좋을 것 같습니다."}, {"톱니항으로 가서 악몽의 조각에 숨겨진 의미를 밝혀내야 합니다."}},
[3367] = {"선타라 비석", {"<신음하는 도리우스>","난... 깊은 상처를 입었소, <race> 친구. 현...현기증이 나는군... 세상이 빙글빙글 돌아... 속에서 피가...","아이언포지로 돌아가야만 하오. 날 좀 도와 주시오."}, {"도리우스가 아이언포지로 돌아가는 것을 도와야 합니다."}},
[3368] = {"선타라 비석", {"아우야,","네가 이 편지를 읽고 있다면 나는 이미 이승을 뜬 것이란다. 이 편지가 어떤 경로로 너한테 전달되었는지는 모르겠구나. 하지만 이 편지를 네게 가져다준 사람에게 감사를 표하고 합당한 보상을 해주길 바란다.","선타라 비석 말인데, 그 돌은 정말 존재하며 검은무쇠단 드워프들이 가지고 있단다. 녀석들의 두목인 검은 라소릭이 선타라 비석의 마력을 이용해 내 발굴단을 노예로 만들고는 용광로로 끌고 가 버렸단다. 그들은 정신이 홀린 채 흑요암이라는 것을 완성하기 위해 밤낮없이 일하고 있다.","그들을 도와 다오, 아우야. 내가 남기는 마지막 부탁이다.","- 도리우스"}, {"아이언포지의 탐험가의 전당에 있는 소리우스에게 그을린 편지를 가져가야 합니다."}},
[3369] = {"악몽", {"이 조각에는 엄청난 비밀이 숨겨져 있어요. 이건 바로 에메랄드의 꿈을 이루는 순수한 정수죠. 하지만 내가 이 조각에서 본 것은 꿈이 아니로군요. 이런 환영을 악몽이라고 하지요.","당신을 놀라게 할 생각은 없었어요. 아무래도 가능한 빨리 이 조각을 현명한 드루이드에게 전해주는 것이 좋겠어요.","장로의 봉우리에 있는 하뮬 룬토템을 찾아야 해요. 썬더 블러프로 가세요. 더 이상은 말해줄 사실이 없군요."}, {"장로의 봉우리에 있는 하뮬 룬토템에게 악몽의 조각을 가져가야 합니다."}},
[3370] = {"악몽", {"이 조각에는 엄청난 비밀이 숨겨져 있어요. 이것은 바로 에메랄드의 꿈을 이루는 순수한 정수죠. 하지만 내가 이 조각에서 보는 것은 꿈이 아니로군요. 이런 환영을 악몽이라고 하지요.","아, 당신을 놀라게 할 생각은 없었습니다. 아무래도 가능한 빨리 이 조각을 현명한 드루이드에게 전해주는 것이 좋겠군요...","세나리온 의회의 마스렌길 베어워커를 찾아야 해요. 다르나서스로 가세요. 더 이상은 말씀드릴 게 없군요."}, {"다르나서스에 있는 마스렌길 베어워커에게 악몽의 조각을 가져가야 합니다."}},
[3371] = {"드워프식 정의", {"내 가슴이 텅 비어 버렸소. 형을 잃었다는 사실을 쉽게 극복할 수 없을 것 같군. 반드시 형의 마지막 소원을 이루어 줘야겠소. 이 냉혈한 살인마들에게 정의의 처단을 내려야 하오.","이글거리는 협곡으로 돌아가서 형의 마지막 소원을 들어주시오. 형의 발굴단을 자유롭게 해 주시오. 검은무쇠 드워프는 노예들을 잿가루 채석장이라 불리는 찌는 듯이 더운 광산에서 일하도록 종용한다오. 그곳에서 도리우스의 일행을 찾아보시오."}, {"이글거리는 협곡으로 돌아가서 도리우스의 발굴단 고고학자를 찾아야 합니다."}},
[3372] = {"해방", {"발굴단을 자유롭게 해 주세요. 그들의 몸이 쇠진해 버린 뒤에도 선타라의 마력이 강제로 임무를 완수하게 합니다.","선타라의 제단이 바로 그 열쇠입니다. 북서쪽으로 가세요. 채석장 위 언덕에... 황혼의 망치단이 유물을 지키고 있습니다. 그 유물을 회수해 선타라의 제단 위에 올려놓으시기 바랍니다.","<죽어 가는 듯 쓰러지는 고고학자>"}, {"이글거리는 협곡의 북서쪽에 있는 황혼의 망치단 우상숭배자들이 보호하고 있는 수수께끼의 유물을 찾은 후, 잿가루 채석장에 있는 선타라의 제단으로 가져가야 합니다."}},
[3373] = {"에라니쿠스의 정수", {"보석을 만지자 굵은 목소리가 마음속까지 울려 퍼집니다.","\"필멸의 생명이여, 내 청을 들어다오. 나는 녹색용군단의 에라니쿠스라하며 이 저주받은 사원의 감시인이다. 내 임무는 두 번 다시 트롤이 그들의 흉측한 신을 이곳에 모시지 못하도록 하는 것이었는데 실패하고 말았다. 내 정수를 이 보석에 부여하니 꿈을 지배하는 나의 힘이 혼돈으로 뒤틀리는구나. 부탁이니 이 보석을 내 안식처에 있는 정수의 샘에 넣어주었으면 한다. 그곳의 마력이 내 영혼에서 악을 씻어내 내가 편히 쉴 수 있도록 해다오.\""}, {"가라앉은 사원의 안식처에 있는 정수의 샘에 에라니쿠스의 정수를 놓아야 합니다."}},
[3374] = {"에라니쿠스의 정수", {"서약의 돌을 사용하자 이타리우스가 돌을 통해 마음속으로 말을 전합니다.","\"이 서약의 돌에 담긴 임무를 받아들임으로써 그대는 나의 대의와 이세라 용군단의 대의를 받들어 에라니쿠스의 타락한 영혼을 구제하는데 동의하는 것이니라. 앞으로 그대와 나는 많은 희생을 치르게 될 것이니, 그 첫 번째는 에라니쿠스의 정신에 남아 있는 자유 의지를 구속해온 그 보석을 내게 넘기는 것이니라.\"","\"이를 행하도록 하라. 그리하면 우리가 힘을 모아 구원받아야 할 영혼을 구원할 수 있으리니.\""}, {"슬픔의 늪에 있는 이타리우스에게 이세라 용군단의 서약의 돌과 속박된 에라니쿠스의 정수를 가져가야 합니다. 이세라의 용군단을 도울 것인지 돕지 않을 것인지는 그곳에서 결정하게 됩니다."}},
[3375] = {"수정점의 유리병 다시 받기", {"음... 수정점의 유리병이 또 필요하단 말씀이세요? 농담하시는 거죠? 처음에 드린 건 어떻게 된 건가요?! 가뜩이나 할 일이 많은데, 그 유리병이 하늘에서 뚝 떨어지기라도 하는 줄 아세요?! 정말로 그랬으면 좋겠군요. 지금은 더 이상 없으니까 말이에요!!","다시 만들려면 마나 물약과 석탄, 달콤한 과즙이 필요해요. 왜 그런지 묻지 말고 그냥 제가 부탁한 물건들이나 가져다주세요. 그러면 유리병을 새로 하나 만들어 드리지요."}, {"아이언포지에 있는 탈바쉬 델 키젤에게 마나 물약과 석탄, 달콤한 과즙을 가져가고 새로운 탈바쉬의 수정점 유리병을 얻어야 합니다."}},
[3376] = {"족장 칼날엄니 처치!", {"당신에 대한 얘기를 많이 들었습니다, 젊은 타우렌이여. 다른 이들은 실패했지만 어쩌면 당신이 우리를 도울 수 있을지도 모르겠군요.","우리 타우렌은 이 땅에서 삶의 터전을 개척했지만 대가가 따랐습니다. 칼날가시 협곡의 뾰족털 가시멧돼지들과 계속된 전쟁은 우리 삶을 고통스럽게 하고 있습니다. 녀석들의 두목이라는 족장 칼날엄니를 처치하는 임무를 맡기겠습니다!","녀석은 동쪽에 있는 칼날가시 협곡 안, 뾰족털 가시멧돼지들의 임시 마을에서 찾을 수 있을 겁니다."}, {"족장 칼날엄니를 처치한 후 그 증거로 그의 머리카락을 붉은구름 고원에 있는 용사 윈드페더에게 가져가야 합니다."}},
[3377] = {"엘룬께 올리는 기도문", {"날 도와 주지 않겠소? 내 얘기를 좀 들어 보시오."}, {"잠마엘이 하는 이야기를 들어야 합니다."}},
[3378] = {"엘룬께 올리는 기도문", {"날 도와 엘룬께 올리는 기도문을 찾아 주지 않겠소?","내가 말했듯이, 기도문은 금으로 된 서판에 새겨져 있소. 우상숭배를 하는 이 황혼의 망치단 불한당들 중 하나가 지닌 게 틀림없소.","내 기도문을 찾거든 다르나서스에 있는 아스타리 스타시커에게 가져가 주시오. 내가 이 세계에 수도 없이 악한 짓을 하고 난 후에 믿음을 회복했다는 사실은 중요치 않소. 하지만 내 기도문만큼은 반드시 전달해야 하오. 어차피 사면 같은 건 기대하지도 않소이다."}, {"엘룬께 올리는 기도문을 찾아 다르나서스에 있는 아스타리 스타시커에게 가져가야 합니다."}},
[3379] = {"그림자매듭", {"이글거리는 협곡에 사는 거미들은 귀하디 귀한 그늘 거미줄의 원천이랍니다. 아시다시피, 그늘 거미줄은 우리 같은 직업을 가진 사람들이 많이 찾는답니다.","최근에 밀렵꾼 패거리들이 이 땅을 침략해서 자신들의 행동에 대한 장기적인 영향 같은 건 고려하지도 않고 마구잡이로 거미들을 해치고 있어요. 당신에게 맡길 첫 임무는 간단합니다. 녀석들을 찾아서... 끝장내 주세요.","북쪽에서 그 침략자들을 찾아보도록 하세요. 지원이 필요하면 라제와 얘기해 보세요."}, {"이글거리는 협곡을 돌아다니는 그늘거미 밀렵꾼을 처치해야 합니다. 닐리스는 다섯 명 정도는 처치해야 한다고 생각하고 있습니다. 임무를 완수하면 이글거리는 협곡에 있는 닐리스에게 돌아가십시오."}},
[3380] = {"가라앉은 사원", {"이 세계에는 편히 쉬지 못하는 영혼들이 있다네. 아주 오래전부터 악에 속박되고 고립되어 오염된 영혼들이지. 전에 땅속 깊은 곳에 어떤 사원이 묻혀있다는 얘기를 들은 적이 있네. 지금까지 내가 보지 못한 강력한 존재들에 의해 추방되었다고 하더군.","가라앉은 사원이라고 불리는 이곳에 대해 더 알고 싶으면 마본 리벳시커라는 고블린을 찾아가보게. 그 친구는 이 세계의 수많은 고대 유적지를 조사했지. 최근에는 톱니항에 있는 집을 떠나 타나리스의 트롤 유적지를 조사하러 갔다고 하니 그를 찾아가보게."}, {"타나리스에서 마본 리벳시커를 찾아야 합니다."}},
[3381] = {"사나스의 스승", {"아이구, 이 사나스를 몸소 찾아오시다니, 이렇게 황송할 때가! 직접 예까지 찾아와 주시니 영광입니다. 와 주셔서 정말로 감사합니다!","설마 비꼬는 말을 칭찬으로 알아듣고 거만하게 구는 건 아니겠지요? 한 가지만 분명히 해두지요. 전 당신이 맘에 안 듭니다. 하지만 지금 우리가 얘기하는 건 우리 관계와는 상관없고 우리 대마법사님과 상관이 있지요.","제 화살에 쓸 히포그리프 깃털 두 개만 가져다주면 그분을 뵈러 갈 수 있게 해 드리지요. 대마법사님께서 좀 쓸만한 모험가들을 보내달라고 하셔서 말입니다."}, {"사나스 임요의 주인과 대화하려면 아즈샤라에 있는 사나스 임요에게 온전한 히포그리프 깃털 2개를 가져가야 합니다."}},
[3382] = {"위험에 처한 선원", {"우리는 해안에서 정박할 만한 안전한 곳을 찾고 있었죠. 그런데 해가 떨어진 지 한 시간도 채 안 돼 그 괘씸한... 나가 녀석들이 우리를 공격했어요!","녀석들이 뭘 가지러 온 것인지는 모르겠지만 찾지 못한 건 분명해요. 벌써 한번 되돌아왔었거든요. 또 공격하러 올 경우를 대비해 계곡 입구에 정찰병 두 명을 배치해 뒀어요.","하필 당신이 지금 같은 때에 나타난 건 안타까운 일이지만 목숨을 걸고 싸울 준비를 하시는 게 좋겠네요."}, {"선장 바네사 벨티스와 그녀의 선원들, 지평선 정찰함의 승객들을 나가의 공격으로부터 보호해야 합니다. 적어도 선장 바네사 벨티스는 반드시 살아남아야 합니다."}},
[3385] = {"암거래상단", {"자칭 암거래상단이라고 적당한 가격만 받으면 판매, 이주, 거래 등등 뭐든지 하는 학살 전문 상인 집단이 있습니다.","더 심각한 건, 그들이 이 이글거리는 협곡에 사업 본부를 세웠다는 겁니다. 대상 코빅이 운영을 하면서 그 구역의 검은무쇠단 드워프들과 친밀한 관계를 맺고 있습니다.","우리가 그들에게 본때를 보여 주고 녀석들의 지도부에 메시지를 전달해야 합니다. 대상 코빅과 그 부하 딸깍이를 해치우고 일을 마치면 대상의 가방을 가지고 돌아오세요.","자, 어서 용광로로 출발하세요!"}, {"대상 코빅과 그의 부하 딸깍이를 처치하고 그 시체에서 대상의 가방을 회수하여 닐리스에게 가져가야 합니다."}},
[3402] = {"암거래상단", {"당신이 직접 가젯잔에 있는 비즈클릭에게 코빅을 물리쳤다는 소식을 전해 주었으면 합니다. 비즈클릭이 코빅이 죽었다는 소식을 들으면 굉장히 기뻐하면서 분명 당신에게 크게 보상해 줄 겁니다."}, {"가젯잔에 있는 비즈클릭과 대화해야 합니다."}},
[3421] = {"귀환", nil, nil},
[3441] = {"성스러운 복수", {"모험가여, 내 얘기를 좀 들어 보시오."}, {"칼라란 윈드블레이드가 하는 이야기를 들어야 합니다."}},
[3442] = {"완전무결한 불꽃", {"잘 결정했소.","우린 가장 타격을 크게 입을 만한 곳인 네 개의 감시탑을 노려야 하오. 그 네 개의 탑을 불길에 휩싸이게 하면 녀석들이 응전을 할 준비가 되기 전에 우리가 공격을 펼칠 수 있소이다.","그 탑들을 불태우려면 적당한 도구가 있어야 하는데 그 도구를 만들려면 몇 가지 재료가 필요하오.","화염의 심장 네 개와 골렘 기름 네 개를 내게 가져오면 내가 완전무결한 불꽃으로 타오르는 횃불을 만들어 주지.","먼저 이 땅에 사는 골렘과 정령들을 사냥하도록 하시오."}, {"화염의 심장 4개와 골렘 기름 4개를 모아 칼라란 윈드블레이드에게 가져가야 합니다."}},
[3443] = {"횃불 자루 만들기", {"완전무결한 불꽃을 만들려면 자루와 불꽃을 담을만한 테두리가 필요하오. 우선 적당한 자루를 찾아야 하오.","용광로에 있는 검은무쇠단 감독관과 검은무쇠단 노예상인은 마법이 걸린 토륨으로 만든 무기를 사용하지. 토륨은 대단히 튼튼한 합금인데, 토륨 도금 단검 여덟 개를 가져오면 내가 녹여서 자루로 만들 수 있소."}, {"칼라란 윈드블레이드에게 토륨 도금 단검 8개를 가져가야 합니다."}},
[3444] = {"돌무리", {"난 가라앉은 사원의 기분 나쁜 방에 들어가 본 적이 있소. 그곳의 사악한 기운은 피부를 뚫고 들어오지. 이제는 바닥을 덮어버린 진흙을 헤치며 간신히 빠져나왔지만, 기억나는 것이라곤 뱀 석상들이 즐비한 커다란 원형 방에 들어갔다는 것뿐이오...","그런데 최근에 바로 그 뱀 문양이 새겨진 동그란 돌을 찾았소. 난 그걸 포장해서 톱니항에 있는 내 작업장으로 부쳤는데, 그 돌을 가져오면 더 자세한 얘기를 들려줄 수 있을지 모르겠군."}, {"톱니항에 있는 마본 리벳시커의 작업장에서 마본의 궤짝에 담긴 아탈라이 돌무리를 회수해야 합니다."}},
[3445] = {"가라앉은 사원", {"전에 땅속 깊은 곳에 있는 어떤 장소에 대해 들은 적이 있어요. 지금까지 내가 보지 못했던 강력한 존재들에 의해 버려졌다고 해요.","가라앉은 사원이라고 불리는 이곳에 대해 더 알고 싶으면 마본 리벳시커라는 고블린을 찾아가보세요. 나나 트로야스처럼 그 친구도 이 세계의 수많은 고대 유적지를 조사했답니다. 그 친구가 타나리스의 트롤 유적지를 조사하려고 톱니항의 집을 떠났다는 게 가장 최근에 들은 소식이에요.","그 친구 소식이 끊어진 지 꽤 됐지만 찾을 수 있길 바랄게요."}, {"타나리스에서 마본 리벳시커를 찾아야 합니다."}},
[3446] = {"심연의 늪", {"사원 안에서 난 발코니가 여섯 개 있는 원형 방을 찾았소. 발코니마다 가장자리에 뱀 석상이 하나씩 있었지. 난 호기심에 그중 하나를 밀어보았는데, 그게 함정이었던 것이오! 난 폭발로 떨어져서 죽을 뻔했소이다. 발코니 끄트머리에 쓰러진 채 아래를 내려다보니 방 바닥에 다른 석상이 있었소. 일종의 제단 같은 것이었는데, 이름이 학카르였던 것 같군.","그 제단에 비밀이 숨겨져 있는 것 같으니 이 돌무리를 가라앉은 사원의 제단으로 가져가도록 하시오."}, {"슬픔의 늪에 있는 가라앉은 사원에서 학카르 제단을 찾아야 합니다."}},
[3447] = {"돌무리의 비밀", {"자세히 기억이 나진 않지만 아는 대로 말해주겠소. 가라앉은 사원에 원 모양으로 서 있는 석상들은 위험하기는 하지만 더 큰 보물로 통하는 비밀을 간직하고 있소.","중심 제단에서는 불을 켤 수 있었는데 이 불의 의미가 무엇인지 알아내면 그 방에 숨겨진 비밀도 알아낼 수 있을지 모르오.","하지만 그 사원에는 엄청난 악이 도사리고 있으니 조심하도록 하시오. 아마도 귀중한 보물이 철통 같은 보안 속에 숨겨져 있을 거야..."}, {"가라앉은 사원으로 가서 원 모양으로 서 있는 석상들에 감춰진 비밀을 알아내야 합니다."}},
[3448] = {"티모르의 협조", {"최근에 난 한때 엘프들의 고향이라 불리던 아름다운 곳, 아즈샤라를 조사하는 임무를 맡게 되었네. 엘프들이 마법을 사용하던 때의 얘기지. 이제는 아름답다기보다는 위험한 곳이지만, 그러한 위험에도 굴하지 않고 우리는 과거에 대한 해답을 파헤쳐 나가야해.","이런 일에 특히 관심이 많은 티모르라는 친구가 날 도와 주기로 했지.","그는 이 도시의 신비의 전당 오른편에 있는 작은 집에 살고 있네."}, {"아이언포지에 있는 티모르와 대화해야 합니다."}},
[3449] = {"비전 룬", {"한때 일부 엘프들은 마법을 그들의 여신, 엘룬만큼이나 경외했던 적이 있었소. 그들 중 가장 강한 힘을 지닌 마법사들 중 일부는 룬을 만들고 마법의 규칙과 새로운 발견에 대한 통찰을 기록한 서판을 만들었소이다. 그들은 이 룬을 공공장소에 배치해 모든 이들이 읽을 수 있도록 했었소.","난 꼭 엘다라스 폐허에서 이 룬의 탁본을 떴으면 하오. 탁본을 만드는 데 쓸 도구는 나에게 있지만 룬을 찾는 건 당신 몫이오. 하지만 조심하시오. 엘다라스에 관심을 가지는 것은 탐험가 연맹만이 아니니까 말이오."}, {"탁본 도구를 사용해 베스아마라 룬, 진야엘 룬, 마크리 룬, 샤엘하이 룬의 탁본을 만든 후, 아즈샤라의 남부 반도에서 조금 떨어진 작은 섬으로 가서 신호를 보내 조종사 시그스 퓨즈라이터가 가지러 오도록 해야 합니다."}},
[3450] = {"간편한 배달 방법", {"내 탑으로 탁본을 배달했으면 좋겠는데 당신이 그곳까지 갈 필요는 없소. 아즈샤라에서 항공편을 통해 가져오도록 하면 될 테니까 말이오.","시그스 퓨즈라이터가 나한테 신세를 좀 진 게 있는데 해안 정찰을 맡게 되어 곧 그곳에 간다고 하더군. 떠나기 전에 그와 얘기를 나눠 보시오. 그가 신호용 권총을 줄 거요. 탁본을 다 만들거든 그 친구가 가라고 하는 곳으로 가서 신호를 한 다음 탁본을 건네주시오. 그 친구가 내 탑까지 무사히 배달해 줄 거요. 그는 군사 지구의 격납고에 있을 거요."}, {"아이언포지에 있는 시그스 퓨즈라이터와 대화해야 합니다."}},
[3451] = {"배달 신호", {"아, 그러니까 티모르씨가 보냈단 말이오? 그에게 신세를 좀 진 일이 있지. 그게 뭐냐고? 이런 약삭빠른 친구 같으니. 당연히 도와 줘야지. 그러니까 신호탄을 쏘고 탁본을 건네줄 곳이 있어야 한다는 것이군... 간단하지.","아즈샤라의 남부 반도에서 조금 떨어진 곳에 작은 섬이 있소. 그곳에 착륙장을 만들어 뒀는데, 이 신호탄을 갖고 가서 쏘시오. 당신이 보낸 신호를 보고 내가 그거... 그러니까 그 친구가 원하는 그 물건을 가지러 갈 테니까."}, {"아이언포지에서 시그스 퓨즈라이터와 대화하고 그에게서 기본지급품 신호탄을 받아야 합니다."}},
[3452] = {"불꽃의 테두리", {"횃불을 완성하는데 마지막으로 한 가지가 더 필요하오.","북서쪽의 산으로 가면 황혼의 망치단이 있는 대규모 야영지가 있소. 소문에 의하면, 자신들의 신에게 경의를 표하는 상징을 지니고 다닌다고 하더군. 거기엔 마법 테두리를 만들 수 있을 만큼 충분한 마력이 담겨 있을 수 있소. 라그나로스의 상징 하나를 내게 가져오면 그 마법을 추출해 테두리를 만들 수 있을 거요."}, {"칼라란 윈드블레이드에게 라그나로스의 상징 1개를 찾아 가져가야 합니다."}},
[3453] = {"응보의 횃불", {"이 재료들을 다 결합하고 필요한 주문을 걸 테니 잠시만 기다리시오."}, {"칼라란 윈드블레이드가 응보의 횃불을 완성할 동안 기다려야 합니다."}},
[3454] = {"응보의 횃불", {"이제 이 횃불은 그 주인에게 결속되어야 하오. 횃불을 집어 드시오."}, {"응보의 횃불을 집어 드십시오."}},
[3461] = {"티모르에게 돌아가기", {"티모르로부터 당신에게 보상을 주라는 이야기는 듣지 못했으니 아이언포지로 돌아가거든 그와 얘기를 해보시오. 틀림없이 아직 마법 지구에 있을 거요. 찾아가서 보상을 넉넉히 받아내시오. 이곳에서 한 일이 결코 쉬운 일은 아니었으니까... 암, 그렇고말고.","아무튼 고맙소이다. 행운을 빌겠소.","아, 난 탁본 도구 필요 없소. 티모르를 찾아가거든 돌려주시오."}, {"아이언포지에 있는 티모르에게 탁본 도구를 돌려주어야 합니다."}},
[3462] = {"견습기사 몰트레이크", {"내 대신 견습기사 몰트레이크와 얘기해 보시오. 몰트레이크가 응보의 횃불의 사용 방법에 대해 더 자세히 가르쳐 줄 것이오."}, {"견습기사 몰트레이크와 대화해야 합니다."}},
[3463] = {"불 공격!", {"잘 들으세요. 그 네 개의 감시탑은 절대 파괴할 수 없지만 응보의 횃불을 사용해 아주 오랫동안 불태울 수는 있습니다. 어떤 자연의 힘이나 마법으로도 끌 수 없는 불로 말이죠.","탑이 불타는 동안에는 경비병들이 경비초소에 올라갈 수 없으니 첫 번째 방어선이 무너지게 될 겁니다.","그 때 각 탑 안으로 들어가 녀석들의 요새 내부에서 응보의 횃불을 사용하도록 하십시오. 그리고 검은무쇠단의 정예 경비병들을 조심하시기 바랍니다."}, {"북쪽, 남쪽, 동쪽과 서쪽 감시탑 내부에서 응보의 횃불을 사용해 불을 지르십시오."}},
[3481] = {"만물 상자...", {"궤짝은 잠긴 듯합니다."}, {"궤짝을 여십시오."}},
[3482] = {nil, nil, nil},
[3483] = {"배달 신호", nil, nil},
[3501] = {"불완전한 드레니시스트 조각", {"완전무결한 드레니시스트 구슬을 찾기란 하늘에서 별을 따는 것처럼 힘들겠지만 부서진 조각들이라면 이 땅을 탐험하면서 많이 찾을 수 있을 거예요.","이 굼이샤가 불완전한 드레니시스트 조각을 사용해 고향으로 돌아갈 수 있을지는 솔직히 모르겠어요. 하지만 드레나이에게는 다른 용도라도 유용하게 쓰일 거예요.","그러니 불완전한 드레니시스트 조각을 찾으면 제게 가지고 오세요. 사례를 해드리죠."}, {"굼이샤에게 불완전한 드레니시스트 조각을 가져가고, 각 파편에 대해 보상을 받아야 합니다."}},
[3502] = {"어느 드레나이의 잡동사니...", {"탐욕스러운 모습을 보면 구역질이 나요.","견딜 수가 없어요. 이제부터 물질에 대한 <race>들의 집착에는 신경 안 쓰겠어요.","불완전한 드레니시스트 조각을 가져올 때마다 제 잡동사니를 드리겠어요. 제 잡동사니 더미는 줄어들고 당신 잡동사니 더미는 늘어나니, 이런 걸 보고 드레노어 속담에 '누이 좋고 매부 좋고'라고 하지요."}, {"굼이샤에게 불완전한 드레니시스트 조각을 가져가야 합니다. 저주받은 땅에 있는 야수는 모두 이 조각을 지니고 있을 가능성이 있습니다."}},
[3503] = {"사나스의 스승", nil, nil},
[3504] = {"배신", {"배신자, 도둑! 나는 그녀를 그렇게 부르지. 그 정도밖에 안 되는 여자니까 말이오!","블러드 엘프를 믿은 내 잘못도 크지만 그보다 더한 건 여자를 믿었다는 거겠지!","그녀의 말만 믿은 내가 바보지! 아즈샤라에 엄청난 재물과 귀한 물건들이 있으니 그녀를 보호할 전사 몇을 보내면 그 대가로 보상을 받기로 했었단 말이오. 이런, 그녀가 그 전사들을 모조리 죽여 버렸으니!","복수를 해야겠소. 하지만 병력을 보낼 여력은 없소이다. 할다르 야영지의 북쪽에 있는 발로르모크에서 아그토르를 찾아보시오. 그 친구가 더 자세한 얘기를 해줄 거요."}, {"아즈샤라에 있는 아그토르 블러드피스트에게 아그토르에게 보내는 봉인된 편지를 전해야 합니다."}},
[3505] = {"배신", {"우선 아즈샤라 북부 반도의 남부 절벽을 따라 그녀의 일꾼들을 처치하는 것부터 시작합시다. 피의 분노를 담아 녀석들을 처치하시오. 배신은 옳지 않다는 사실을 뉘우치게 하시오.","림토리는 신경 쓰지 않아도 될 것이오. 자신의 호위병들을 거느리고 아즈샤라를 수색하는데 바쁠 테니 말이오. 그녀의 개인 야영지는 그 일꾼들을 처치한 후에 찾는 게 더 신중한 처사일 거요. 그녀를 어디서부터 공격하는 게 좋을지 미리 파악해두는 것도 현명한 일이겠지. 그리고 그곳엔 아마도 그녀를 자극하는데 쓸 만한 물건이 있을지 모르오."}, {"블러드 엘프 수색병 10명과 블러드 엘프 감정관 10명을 처치한 후, 학자 림토리의 야영지를 찾아내십시오. 야영지를 찾으면 그녀를 싸움으로 유인하는데 사용할 수 있을 만한 것을 찾아보십시오. 림토리가 중요하게 여기는 것이라야 합니다."}},
[3506] = {"배신", {"곰곰이 생각하다 보니 좋은 생각이 떠오릅니다. 어쩌면 앞에 놓인 수정들을 파괴하면 벨그롬을 배신하고 수많은 오크를 학살한 블러드 엘프를 화나게 할지도 모릅니다.","계속 책 안의 글과 그림들을 해독하다 보니 이 수정들 중 하나는 이 원을 만든 자와, 다른 것은 그보다 훨씬 더 사악한 존재와 연결되어 있음을 알 수 있습니다.","책을 덮고 어느 수정을 파괴할지 결정합니다."}, {"학자 림토리와 연결된 비전 집중의 수정을 파괴하고 그녀를 처치하십시오. 그런 다음 그 증거로 아즈샤라에 있는 아그토르 블러드피스트에게 학자 림토리의 머리카락을 가져가야 합니다."}},
[3507] = {"배신", {"오그리마에 있는 벨그롬에게 돌아가시오. 이 머리카락을 직접 보고 싶어할 테니까! 우리도 곧 그곳으로 돌아가겠소.","아, 오늘은 참으로 기쁜 날이군. 도와줘서 고맙소."}, {"오그리마에 있는 벨그롬 록마울과 대화하십시오."}},
[3508] = {"보호막 파괴", {"정말 무모한 <race>|1이로군;로군;. 하지만 그런 점이 바로 자네 같은 종족에게서 본받을 만한 점이야. 성공할 가능성이 턱없이 낮은데도 맹목적으로 뛰어드는 그 무모함 말이네. 라젤리크의 부하를 하나만이라도 쓰러뜨릴 수 있다면 내, 기꺼이 도움을 주지.","라젤리크의 심복들을 감싸고 있는 보호막을 깨뜨리려면 시간도 오래 걸리고 희귀한 유물도 얻어야 한다네.","움직이지 말고 가만히 있게나. 깨우침의 의식부터 수행해야 하네. 먼저 정보를 모아서 엄청난 힘을 지닌 무기를 만들어야 해."}, {"로라무스가 깨우침의 마법을 마칠 때까지 기다려야 합니다."}},
[3509] = {"악마의 이름", {"악마의 군주와 그의 심복들을 해치울 수 있는 방법은 단 한 가지뿐이지. 악마의 진짜 이름이 새겨진 마력의 무기를 만드는 거야.","그 무기만이 악마의 군주와 그를 따르는 모든 부하들을 처치할 수 있지.","아즈샤라에 제왕 아크코로크라는 하급 신이 살고 있네. 그를 만나서 얘기를 들어야 하는데, 그 부하들이 자네를 보면 즉시 해치려고 덤벼들 테니 조심하게.","그에게 정보를 얻으려면 합당한 대가도 치러야 할 거야."}, {"아즈샤라에 있는 제왕 아크코로크와 대화해야 합니다. 아크코란의 신전을 찾으려면 물가부터 조사해 보십시오."}},
[3510] = {"악마의 이름", {"나의 아이들을 죽이는 자가 있소. 스스로 바다의 제왕이라 칭하고 있지. 하지만 그녀는 가짜 신일 뿐 그 이상은 아무것도 아니오. 그녀를 물리치고 머리를 모두 가져오시오.","그리하면 그대가 찾고 있는 이름은 물론이고 그보다 더 중요한 다른 것도 주겠소.","그녀는 자신의 보금자리를 배회하고 다니는데 이름은 헤타에라라고 하오.","<남서쪽을 가리키며>","가시오!"}, {"헤타에라를 물리치고 헤타에라의 훼손된 머리, 헤타에라의 피묻은 머리, 헤타에라의 으깨진 머리를 제왕 아크코로크에게 가져가야 합니다."}},
[3511] = {"악마의 이름", {"약속대로 악마의 군주, 파멸자 라젤리크의 진짜 이름을 가르쳐주겠소. 그 전에 그대의 여정에 없어서는 안 될 것을 준비해주지.","그대에게 무기를 만들어줄 텐데... 담금질할 피가 필요하오. 그대가 가져온 머리에서 피를 짜내는 동안 잠시만 기다리시오.","<거대한 두 손으로 머리를 쥐어짜 피를 빼낸 후>","이 피를 로라무스에게 가져다주면서 악마의 이름도 함께 알려주시오. 라크리크..."}, {"헤타에라의 피를 로라무스 탈리뻬데스에게 가져가야 합니다."}},
[3512] = {"에라니쿠스의 말", {"우리는 보석을 통해 에라니쿠스와 대화하는 방법을 가장 먼저 알아내야 하노라. 그는 지금 악몽과 죽음 사이의 불확실한 상태에서 악몽 속에 갇혀 있느니라. 그대가 준 정수가 심하게 오염되었기는 하나 그에게 다가갈 방법이 있을 것이다.","여명의 설원 동쪽의 눈 덮인 골짜기로 먼 여행을 떠나 우리의 인간 동지인 영매 엄브런스를 찾아라. 그는 죽은 자를 대신하여 말하는 인간 영매이니라. 에라니쿠스가 완전히 죽은 것은 아니지만 영매의 도움을 받을 수 있을 것인즉... 자, 떠나거라."}, {"여명의 설원의 동쪽으로 가서 영매 엄브런스를 만나야 합니다."}},
[3513] = {"룬새김 두루마리", {"공문으로 보이는 두루마리가 은색 리본으로 단단히 감겨있습니다. 이 두루마리에 관심을 보일 만한 자가 분명 있을 텐데, 그게 과연 누구일까요?","잿빛 골짜기의 북쪽 경계 근처에 전초기지가 하나 있습니다. 이 두루마리의 내용이 나이트 엘프와 관련된 것이라면 바로 그곳으로 가져가는 것이 옳을 것입니다."}, {"불모의 땅의 북쪽 방어탑으로 룬새김 두루마리를 가져가야 합니다."}},
[3514] = {"호드의 존재", {"이 두루마리는 돌발톱 봉우리, 갈퀴발톱굴의 열쇠지기 린타리엘의 것이로군.","당신이 두루마리의 전령을 처치했으니 한 시름 덜었소. 흠... 하지만 우리는 이곳에 기반을 세워야 하오. 호드는 린타리엘의 졸개들을 두고 보지만은 않을 것이오.","갈퀴발톱굴로 가서 린타리엘을 처치하고 모든 이들이 볼 수 있도록 증표를 세워두시오. 이 호드 깃발을 가지고 가서 갈퀴발톱굴에 세워두시오. 다시는 우리 영토에 전령을 보내지 못하도록!"}, {"갈퀴발톱굴의 우두머리인 열쇠지기 린타리엘을 처치해야 합니다."}},
[3517] = {"서판 훔치기", {"이 아즈샤라에서 나도 내 나름대로 최선을 다했습니다. 벨그롬의 처벌도 나름대로 도움이 됐지요.","내가 떠나기 전에 몇 가지 조사를 했었는데 주위에 값진 물건들이 얼마나 많이 널려 있는지 알면 놀랄 거예요.","내 제안에 대해 더 얘기하도록 하죠. 그 물건 중 일부를 모아오면 조금 나누어 주도록 하죠.","여기서 동쪽으로 가면 엘다라스 폐허라는 곳이 있는데, 그곳에 내게 필요한 서판들이 있습니다. 네 개 모두 명가의 마법사들의 이름을 딴 서판들이지요. 일단은 그것들을 가지고 온 후에 더 얘기하기로 하죠."}, {"베스아마라 서판, 진야엘 서판, 마크리 서판, 샤엘하이 서판을 찾아 아즈샤라에 있는 제디가에게 가야 합니다."}},
[3518] = {"마가타에게 전달", {"첫 번째 서판은 마가타라는 타우렌에게 가져가야 해요. 마가타는 썬더 블러프 장로 중 한 명이며, 타우렌과 포세이큰 간 동맹을 지지하는 자 중 하나이기도 하죠. 다른 어떤 장로들보다도 그녀는 타우렌과 타우렌의 방식이 포세이큰이 다시 인간으로 돌아가는 길을 찾는 데 도움을 줄 것이라 믿고 있습니다.","그녀에게 베스아마라 서판을 가져다주세요. 이 서판은 그녀가 포세이큰을 도울 방법을 찾는 데 실마리가 될 거예요. 적어도 그녀 말로는 그렇다고 해요."}, {"썬더 블러프에 있는 마가타에게 베스아마라 서판을 가져가야 합니다."}},
[3519] = {"도움이 필요한 친구", {"으으... 여기서 아주 가까이에 있는 거미 굴을 조사하다가 기시이스라는 흉측한 거미에게 물렸어요. 독이 심하게 퍼진 것 같으니 좀 도와주세요.","디라니아 실버샤인에게 제 상황을 좀 전해 주세요. 그녀라면 도울 수 있을 거예요.","서둘러 주세요... 너무 어지러워요..."}, {"그늘 협곡에 있는 디라니아 실버샤인과 대화해야 합니다."}},
[3520] = {"계곡천둥매의 영혼", {"나는 페랄라스에 사는 계곡천둥매의 영혼을 찾고 있소. 계곡천둥매는 고대 신과 혈연이 맺어져 있지... 그들의 영혼은 고대 신과 연결되어 있소.","이 나뭇가지를 들고 페랄라스로 가보시오. 페랄라스 남부와 중부에 살고 있는 계곡천둥매를 쓰러뜨린 후 시체 위에서 이 나뭇가지를 흔드시오. 그러면 그들의 영혼이 소환될 거요. 영혼을 건드려 모아오도록 하시오.","영혼과 함께 사용한 나뭇가지를 갖고 돌아오면 고대 신에 대해 자세히 말해주리다."}, {"페랄라스에 있는 3마리의 계곡천둥매의 영혼을 모은 후 스팀휘들 항구에 있는 예킨야에게 가야 합니다."}},
[3521] = {"이베론을 위한 해독제", {"제 친구 이베론이 정말 걱정돼요. 거의 하루 동안이나 돌아오지 않는 걸 보니 아무래도 독에 중독되었거나... 아니면 그것보다 더 무서운 일을 당한 게 아닌지 모르겠어요.","만약을 위해 거미 독을 치료할 해독제를 만들어야겠는데 당신이 그 재료를 모아와 주었으면 좋겠어요. 우선 히아신스 버섯이 필요해요. 나무 밑에서 자라니까 쉽게 찾을 수 있을 거예요. 그리고 달봉오리 백합도 필요한데 이건 물웅덩이 근처에서만 자라죠. 또 북쪽의 거미들에게서 나무그물거미 영액도 구해와야 해요.","이베론이 독에 중독되었다고 해도 이것들을 섞어서 해독제로 사용하면 충분할 거예요. 그는 북쪽의 그늘 거미굴로 향했으니 거기서 그를 찾았으면 좋겠군요."}, {"히아신스 버섯 7개, 달봉오리 백합 4개, 나무그물거미 영액 1개를 수집해야 합니다."}},
[3522] = {"이베론을 위한 해독제", {"해독제를 다 만들었어요. 이베론에게 가져가 먹이도록 하세요.","한 가지 알아두셔야 할 게 있는데 이 해독제는 5분 동안만 효력을 유지한답니다. 그러니 그 시간 안에 이베론에게 가져가야 해요.","부디 빨리 도착하시길 바랄게요."}, {"제한 시간이 지나기 전에 이베론에게 해독제를 가져가야 합니다. 이베론은 북쪽에 있는 동굴 옆에서 찾을 수 있습니다."}},
[3523] = {"가시덩굴 구릉의 스컬지", {"훌륭합니다! 내 이름은 벨리스트라즈입니다. 구해 줘서 고맙습니다. 필경 죽음을, 아니 그보다 더한 일을 당하게 될 줄 알았답니다. 이 쓰레기 같은 스컬지 노예들의 먹이가 된다든지... 네, 스컬지 말입니다. 녀석들이 이곳 가시멧돼지와 모종의 거래를 맺어 이런 참사가 일어난 것이지요.","들어 보세요... 여기서 그냥 도망칠 수도 있지만, 그보다 모두를 위해 뭔가 보탬이 되는 일을 하고 싶습니다. 당신이 좀 도와 줄 수 없겠습니까? 아마 위험한 일이 될 테지만 당신은 그런 것에 대해 크게 걱정할 것 같지는 않아 보이는군요. 나를 좀 도와주시기 바랍니다."}, {"벨리스트라즈의 계획을 돕는 것에 동의하면 다시 그와 대화하여 그에게 서약의 돌을 돌려줘야 합니다."}},
[3524] = {"해변으로 올라온 바다 생물", {"어둠해안 연안에서 거대한 바다 생물들이 뛰어올라 해변에서 죽는다고 알려져 있죠. 그런데 이렇게 해변으로 밀려와 죽는 생물의 수가 최근 부쩍 늘었어요. 달의 신전에서 조사를 위해 저를 이곳에 파견했지만 물가에 멀록들이 있어서 어려움을 겪고 있답니다.","아우버다인 바로 남쪽에 해변으로 밀려 올라온 거대한 생물이 하나 있는데 잿빛안개 멀록들이 둥글게 둘러싸고 있어요. 그곳으로 가서 그 생물의 뼈를 가져다주실 수 있나요?"}, {"아우버다인 바로 남쪽에 있는 떠내려온 바다 생물에게서 바다 생물 뼈를 회수한 후, 아우버다인에 있는 그웨니스 블리레곤드에게 가져가야 합니다."}},
[3525] = {"우상 진화", {"입구 근처에 가시멧돼지들이 화덕으로 사용하는 무시무시한 우상이 하나 있습니다. 녀석들은 불모의 땅에서 납치한 자들을 모조리 먹어 치우지요. 그 우상의 불을 꺼 다시는 녀석들이 불을 피우지 못하게 해야 하는데 그러려면 당신의 도움이 필요합니다. 제 계획을 말해 드리겠습니다. 저를 우상까지 호위해 주세요. 우상에 도착하면 즉시 진화의 의식을 시작할 겁니다. 의식이 시작되면 당신과 얘기를 나누기는 고사하고 제 몸 하나 보호하는 것도 어려울 테니, 제가 의식을 진행하는 동안, 부디 절 보호해 주십시오. 제가 녀석들에게 당하면 모든 게 끝장입니다.","무슨 말인지 아시겠습니까?!"}, {"가시덩굴 구릉에 있는 가시멧돼지의 우상까지 벨리스트라즈를 호위해야 합니다.","우상을 진화하는 의식을 수행하는 동안 벨리스트라즈를 보호해야 합니다."}},
[3526] = {"고블린 기계공학", {"고급 기계공학은 두 갈래로 분류된다오. 바로 노움 기계공학과 고블린 기계공학이지. 단연코, 뛰어난 기술자라면 누구나 고블린 기계공학을 선택한다오. 엄청난 화력의 폭발물과 뛰어난 장비를 사용해서 세계를 완전히 장악할 수 있으니 말이오!","이 설명서를 읽어 보시오. 고블린 기계공학을 배우고 싶거든 가젯잔에 있는 닉스 스프로켓스프링에게 이 설명서를 가져가시오. 하지만 명심하시오. 회원 가입은 영구적이고, 일단 가입하면 다른 갈래의 기계공학은 배우지 못한다오. 그러니까 그가 주는 임무를 완료하기 전에 확실히 마음의 결정을 내리도록 하시오!"}, {"고블린 기계공학에 대해 더 알고 싶으면 가젯잔에 있는 닉스 스프로켓스프링에게 기계공학 설명서를 가져가야 합니다."}},
[3527] = {"모쉬아루의 예언", {"모쉬아루의 고대 예언은 죽은 신, 학카르의 정수를 담는 방법에 대해 얘기하고 있네. 서판 두 개에 나뉘어 쓰여 있는데, 가젯잔 서쪽에 있는 트롤 도시인 줄파락에 있어.","내게 모쉬아루 서판을 가져오게!","첫번째 서판은 오래전 순교한 데카라는 트롤이 가지고 있지. 일설에 의하면 순교자 데카를 박해한 자들은 저주를 받아 왕쇠똥구리로 변해 데카의 묘지를 떠돌고 있다고 하네.","두번째 서판은 가즈릴라의 신성한 웅덩이 근처에 있는 유체술사 벨라타가 가지고 있네.","서판들을 찾거든 내게 가져오게."}, {"타나리스에 있는 예킨야에게 첫번째 모쉬아루 서판과 두번째 모쉬아루 서판을 가져가야 합니다."}},
[3528] = {"학카르의 화신", {"이제 학카르 화신의 정수를 모을 차례야.","이 학카르의 알을 동부 왕국의 가라앉은 사원에 있는 죽은 신의 성소로 가지고 가게. 그리고 알의 힘으로 죽은 신을 깨우게.","아마 학카르는 졸개들을 보내 당신을 처치하려 할 거야. 그들을 물리치고 학카르의 피를 반드시 손에 넣게. 그 피로 학카르의 영혼이 깃든 영원의 불꽃들을 꺼야 하니까. 불꽃이 모두 꺼지면 학카르의 화신이 나타날 거야.","그를 물리치고 학카르의 알에 그의 정수를 담아오게."}, {"타나리스에 있는 예킨야에게 충만한 학카르의 알을 가져가야 합니다."}},
[3541] = {"제스리몬에게 전달", {"오그리마에 친구가 하나 있는데 골목길에 살고 있어요. 그를 어디 가서 찾아야 하는지 모르면 상당히 애를 먹겠지만 높은 건물들을 뒤지다 보면 별문제 없이 찾을 수 있을 거예요.","한 가지만 당부하죠. 우리 둘 모두에게 안 좋으니까 칼림도어에 사는 자들에게 그 친구가 칼림도어에서 뭘 하는지 말하고 다니지 말아줘요.","진야엘 서판을 가지고 가면 그가 후하게 값을 쳐줄 거예요. 이유는 모르겠지만 그 서판의 행방을 듣고는 무척 흥분하는 기색이었거든요."}, {"오그리마에 있는 제스리몬에게 진야엘 서판을 가져가야 합니다."}},
[3542] = {"안드론 건트에게 전달", {"다음에 만날 사람은 언더시티 연금술 실험실 근처에 있는 안드론 건트라는 언데드예요.","그 친군 이 서판이 왜 필요한지에 대해서는 쉽게 입을 열더군요. 언더시티의 일부 마법사들이 명가에서 사용하던 신비술을 연구하려는데 이 서판이 도움이 될 거라고 생각한다나 봐요. 마크리는 생물, 특히 정령을 소환하는데 능통했죠.","마크리 서판을 가져다주고 내게 돌아와서 사례금을 받도록 하세요."}, {"언더시티에 있는 안드론 건트에게 마크리 서판을 가져가야 합니다."}},
[3561] = {"대마법사 실렘에게 전달", {"이 마지막 서판은 아주 위험한 인물에게 가져다 줘야 해요. 지금까지 당신에게 만나보라고 말했던 자들보다 훨씬 믿지 못할 자라는 사실을 명심하세요.","대마법사 실렘은 아즈샤라를 내려다보는 언덕 위 탑에 있습니다. 인간이긴 하지만 하는 행동을 보면 내가 지금까지 만났거나 처치한 인간들과는 굉장히 달라요. 그의 몸에서는 힘이 배어 나오죠. 이 서판으로 뭘 할 계획인지는 감히 물어보지도 못해요.","그는 나를 찾아와서는, 자기를 만날 준비가 되면 경호원인 사나스가 탑으로 데려다 줄 것이라고 말하더군요. 북쪽으로 가서 사나스를 찾아보세요."}, {"아즈샤라에 있는 대마법사 실렘에게 샤엘하이 서판을 가져가야 합니다."}},
[3562] = {"제디가에 대한 마가타의 답례", {"이 쪽지를 제디가에게 전해 주고 그녀가 이곳에 돌아오면 사례하겠다고도 말해 주시게. 일을 잘 끝마쳐서 기쁘다는 말도 함께 말이야.","어서 가보게나. 가서 내가 여기서 기다리겠다고 전해주게."}, {"아즈샤라에 있는 제디가에게 마가타의 쪽지를 가져가야 합니다."}},
[3563] = {"제디가에 대한 제스리몬의 답례", {"이 쪽지를 제디가에게 가져다주고 우리 거래가 아주 유익했고 내가 그녀의 일에도 만족스러워하더라고 전해 주시게.","오그리마로 빨리 돌아오면 이 제스리몬이 아주 후하게 대접해 줄 것이라고도 전해 주고 말이야.","제디가에게 그렇게 전해주고, 언젠가 내가 다시 자네를 부를 일이 있을지도 모르겠네."}, {"아즈샤라에 있는 제디가에게 제스리몬의 쪽지를 가져가야 합니다."}},
[3564] = {"제디가에 대한 안드론 건트의 보답", {"자... 여기... <딸꾹> 이걸 제디가에게 전해주시오. 행운을 비오!"}, {"아즈샤라에 있는 제디가에게 안드론의 쪽지를 가져가야 합니다."}},
[3565] = {"제디가에 대한 실렘의 보답", {"이게 뭐지? 아, 그렇군. 사례금. 당연히 줘야지.","이걸 가지고 어서 가보게. 제디가가 자네가 돌아오길 애타게 기다리고 있을 테니 말이야."}, {"아즈샤라에 있는 제디가에게 실렘의 쪽지를 가져가야 합니다."}},
[3566] = {"일어나라, 흑요암이여!", {"모... 모든... 모든 것이 함정이었습니다.","날 용서... 해 주십시오.","우리... 우리를 위해... 소리우스를 위해... 배신자를 처치해 주십시오."}, {"검은 라소릭과 흑요암을 처치하고, 그 증거로 검은 라소릭의 머리카락과 흑요암 심장을 아이언포지에 있는 소리우스에게 가져가야 합니다."}},
[3567] = {"높은 곳", nil, nil},
[3568] = {"스며드는 타락", {"내 뒤에 금방이라도 쓰러질 듯한 모습의 타우렌이 보이나? 최근에 아즈샤라에 다녀왔다고 하는데, 엘다라스 폐허를 지나는 길에 나가에게 부상을 당했다고 하는군.","나가를 몇 마리 처치한 후에 해변에 앉아 쉬면서 해일의 웅덩이의 물을 마신 모양인데... 구토가 나고 다른 증상도 보인다는군.","저 타우렌이 뭘 마신 것인지 알고 싶으니 이 상자를 가져가서 약병에 붙여놓은 이름표를 보고 각각 웅덩이에서 물을 채워오게."}, {"아즈샤라에 있는 엘다라스 폐허의 해변을 따라 가면서 4개의 빈 약병에 해일의 구덩이의 물을 모두 채운 후, 연금술사 큘리에게 가야 합니다."}},
[3569] = {"스며드는 타락", {"이걸 테레사에게 줘보시오. 어쩌면 병이 나을 수도 있고... 어쩌면 낫지 않을 수도 있소. 테레사가 이 약을 마셔봐야 알 수 있을 거요."}, {"언더시티에 있는 테레사 윈드송에게 큘리의 비약을 줘야 합니다."}},
[3570] = {"스며드는 타락", nil, nil},
[3601] = {"킴야엘", {"블러드 엘프들이 이 킴야엘을 어떻게 생각하는지 아시오? 겨우 쥐새끼 정도로 생각하지. 덩치만 커다란 깡패들 같으니라고!","놈들은 나를 고용해서 무슨 잃어버린 \"보물\"을 찾아 지역을 정찰하는 일을 도와 달라고 했소. 나야 물론 보물에다 넉넉한 보수 얘기에 귀가 솔깃해 뛰어들었지만, 녀석들은 날 배신하고 죽이려고 했지. 감히 이 킴야엘을!","이건 정말 블러드 엘프란 못 믿을 족속이라는 걸 단적으로 보여 주는 거요.","그런데 문제는, 녀석들이 아직도 내 장비들을 가지고 있는데 나 혼자서는 그걸 되찾아올 수가 없다는 거요. 날 좀 도와주지 않겠소?"}, {"탈라시안 주둔지에서 킴야엘의 나침반, 망원경, 박제한 닭, 땅콩까개를 찾은 후, 아즈샤라에 있는 킴야엘에게 돌려줘야 합니다."}},
[3602] = {"아즈샤리트", {"아즈샤라 남부 지역의 고대 절벽거인은 그 지역의 바위와 건물들을 먹고살지. 그 절벽거인의 배설물을 아즈샤리트라고 하는데, 굉장히 튼튼하면서도 아주 유연한 수정이라네.","남부에 있는 반도에는 아즈샤리트가 널려있지만 절벽거인들이 워낙 난폭한지라 회수하기가 무척 힘들지.","자네가 남쪽으로 가서 무기에 쓸 아즈샤리트를 충분히 구해오게. 내 지옥사냥개들이 수정을 회수하는 걸 도와줄 걸세."}, {"아즈샤라 남부 지역에서 결정화된 아즈샤리트 20개를 회수해야 합니다. 교묘하게 숨겨져 있는 아즈샤리트의 위치는 지옥사냥개에게 냄새를 맡게 해서 찾을 수 있습니다."}},
[3621] = {"퇴마 무기 제작", {"무기를 만들 준비가 다 됐네. 이제 담금질한 아즈샤리트를 가지고 악마의 힘을 다스릴 수 있는 자를 찾아가야 하네.","가시덤불 골짜기에 그런 일을 할 수 있는 대장장이가 하나 살고 있네. 미스릴 대장조합의 지도자인 전설의 대장장이 갈반이 자네의 요구대로 무기를 제작해 줄 것이네. 그자의 야영지는 줄구룹과 모쉬오그 오우거 소굴 사이에 있네.","자네의 결의와 의지로 이 고난을 헤쳐나갈 수 있기를 바라네. 설사 실패한다 해도 여신께서는 미소를 지어줄 것이라는 점을 잊지 말게."}, {"가시덤불 골짜기에 있는 갈반에게 상자를 가져가야 합니다."}},
[3625] = {"마력이 깃든 아즈샤리트 퇴마 무기", {"무기 하나하나를 만드는 데 시간이 좀 걸릴 거야.","게다가 퇴마 저주의 힘은 3분에 한 번씩밖에 사용할 수 없지. 파멸자 라젤리크와 그의 부하들을 약하게 만드는 능력은 지속 시간이 일정하지 않고 말이야. 그렇기 때문에 기회다 싶으면 즉시 사용해야 하네!","무기를 만드는 동안 잠시만 기다려 주게."}, {"갈반이 무기를 다 만들 때까지 기다려야 합니다."}},
[3626] = {"저주받은 땅으로의 귀환", {"구원의 시간이 가까워졌네, <name>. 새 무기를 들고 저주받은 땅으로 돌아가 호드 영웅의 넋에게 최후의 지시를 받도록 하게."}, {"호드 영웅의 넋에게 돌아가야 합니다."}},
[3627] = {"조각난 아뮬렛 재결합", {"파멸자 라젤리크에게 맞서려면 먼저 그의 심복 3인조부터 처치한 다음 그들이 지닌 아뮬렛 조각을 회수해야 하오. 세 개의 조각을 전부 모으면 내게 돌아오시오. 그러면 다음 지시를 주겠소.","가시오, <name>! 저주받은 땅으로! 세바인과 알리스타즈, 그리고 그롤을 모두 쓰러뜨려야 하오!"}, {"파괴자 그롤, 여군주 세바인, 대마법사 알리스타즈를 처치하고 그들의 시체에서 그롤의 아뮬렛, 세바인의 아뮬렛, 알리스타즈의 아뮬렛을 회수해야 합니다.","마력이 깃든 아즈샤리트 퇴마 무기를 사용하여 3인조의 힘을 약화시켜야 그들을 처치할 수 있다는 것을 명심하십시오. 반드시 현명하게 사용해야 합니다."}},
[3628] = {"악마 라크리크와의 대결", {"라젤리크의 소굴은 저주받은 땅에서 가장 높은 산꼭대기에 있소. 그 소굴로 가는 유일한 방법은 산 근처에 있는 순간이동 룬을 이용하는 것이오. 아뮬렛을 지니고 있으면 그 소굴에 자유롭게 드나들 수 있을 거요. 소굴에 들어서면 그 괴물을 불러내야 하오. 이 아뮬렛이 그 열쇠요. 파멸자의 제단에서 사용하도록 하시오.","파멸자 라젤리크를 물리치면 그의 뿔을 가져오시오. 라젤리크의 뿔은 모든 이들에게 희망의 상징은 물론, 전쟁으로 상처받은 이 세계에 아직도 영웅이 존재한다는 증거가 될 것이오."}, {"파멸자 라젤리크를 처치하고 파멸자의 부러진 뿔을 호드 영웅의 넋에게 가져가야 합니다. 파멸자의 수호물도 호드 영웅의 넋에게 돌려주어 파괴하도록 해야 합니다. 그래야 사악한 자들의 손에 들어가는 것을 막을 수 있습니다."}},
[3629] = {"고블린 기계공학", {"고급 기계공학은 노움과 고블린의 두 가지 분야로 명확히 분류된답니다. 별로 그러고 싶진 않지만 고블린 기계공학에 대해서도 알려드리겠어요. 사회적 지위가 있는 전문 기계공학자이니 매사에 공평해야지요... 고블린은 뭐든지 폭발시키기를 즐겨 하는데, 중간에 어떤 장애물이 있든 아랑곳하지 않아요. 설령 그 장애물이 자기 동족이라 하더라도 말이에요!","이 설명서를 한번 읽어보세요. 폭발 기술을 배우고 싶거든 가젯잔에 있는 닉스 스프로켓스프링에게 이 설명서를 가져다주세요. 하지만 명심하세요. 일단 둘 중 하나를 선택해 회원으로 가입하면 다른 학문은 절대로 배우지 못해요. 그러니까 그가 주는 임무를 완료하기 전에 확실히 마음을 결정하라고요!"}, {"고블린 기계공학에 대해 더 알고 싶으면 가젯잔에 있는 닉스 스프로켓스프링에게 기계공학 설명서를 가져가야 합니다."}},
[3630] = {"노움 기계공학", {"고급 기계공학은 두 가지 학문으로 분류된답니다. 바로 노움기계공학과 고블린 기계공학인데, 물론 이 둘 중에서도 노움기계공학이 훨씬 더 우수하죠! 삶을 더 편리하게 하는 멋진 도구와 장치를 만드니까 말이죠!","이 설명서를 읽어 보세요. 노움 기계공학에 대해 더 배우고 싶거든 이 설명서를 아이언포지에 있는 수석땜장이 오버스파크님께 가져다주세요. 아, 회원 가입은 영구적이며 다른 학문을 배우지 못하게 한다는 사실을 명심하세요. 그러니까 그분이 주는 임무를 완료하기 전에 확실한 마음의 결정을 내리도록 하세요!"}, {"노움 기계공학에 대해 더 배우고자 한다면 아이언포지에 있는 수석땜장이 오버스파크에게 기계공학 설명서를 가져가야 합니다."}},
[3631] = {"지옥마 소환", {"흥. 내가 보기에 당신은 아직 준비가 덜 된 것 같은데 그건 내가 판단할 일이 아니지. 스트라드의 생각은 다른가 보니까. 게다가 이건 그가 내릴 결정이고... 적어도 이번에는 말이야.","스트라드는 하루 빨리 당신과 얘기를 나누고 싶어하지. 그는 아직 불모의 땅, 톱니항에 있는 작은 탑에 수행 사제들과 함께 있소.","여기서 지체하지 말고 어서 가보시오."}, {"불모의 땅에 있는 스트라드 파선과 대화하십시오."}},
[3632] = {"노움 기계공학", {"고급 기계공학은 두 가지로 분류되오. 바로 노움기계공학과 고블린 기계공학인데, 물론 이 둘 중에서도 노움기계공학이 훨씬 더 우수하죠! 삶을 더 편리하게 하는 멋진 도구와 장치를 만드니까 말이죠!","이 설명서를 읽어 보세요. 노움 기계공학에 대해 더 배우고 싶거든 이 설명서를 이곳 아이언포지에 있는 수석땜장이 오버스파크님께 가져다주세요. 아, 회원 가입은 영구적이며 다른 학문을 배우지 못하게 한다는 사실을 명심하세요. 그러니까 그분이 주는 임무를 완료하기 전에 확실한 마음의 결정을 내리도록 하세요!"}, {"노움 기계공학에 대해 더 알고 싶으면 아이언포지에 있는 수석땜장이 오버스파크에게 기계공학 설명서를 가져가야 합니다."}},
[3633] = {"고블린 기계공학", {"고급 기계공학은 두 갈래로 분류됩니다. 바로 노움 기계공학과 고블린 기계공학이죠. 단연코, 뛰어난 기술자라면 누구나 고블린 기계공학을 선택합니다. 엄청난 화력의 폭발물과 뛰어난 장비를 사용해서 세계를 완전히 장악할 수 있으니 말이죠!","이 설명서를 읽어 보세요. 고블린 기계공학을 배우고 싶거든 가젯잔에 있는 닉스 스프로켓스프링에게 이 설명서를 가져가시고요. 하지만 명심하세요. 회원 가입은 영구적이고, 일단 가입하면 다른 갈래의 기계공학은 배우지 못합니다. 그러니까 그가 주는 임무를 완료하기 전에 확실히 마음의 결정을 내리도록 하세요!"}, {"고블린 기계공학에 대해 더 알고 싶으면 타나리스의 가젯잔에 있는 닉스 스프로켓스프링에게 기계공학 설명서를 가져가야 합니다."}},
[3634] = {"노움 기계공학", {"고급 기계공학은 두 갈래로 분류됩니다. 바로 노움 기계공학과 고블린 기계공학이지요. 개인적으로 노움 전문 기술은 쓰레기라고 생각합니다만, 그래도 그게 뭔지는 아셔야겠지요. 노움들은 자신들이 발명한 각종 물건과 장치가 세계를 변화시킨다고 주장합니다. 제대로 작동하는 일이 거의 없다는 게 문제죠.","이 설명서를 읽어 보시지요. 금속 잡동사니 만드는 방법을 배우고 싶거든 그 설명서를 아이언포지에 있는 수석땜장이 오버스파크에게 가져가시고요. 하지만 명심하세요. 회원 가입은 영구적이고, 일단 가입하면 다른 갈래의 기계공학은 배우지 못합니다. 그러니까 그가 주는 임무를 완료하기 전에 확실히 마음의 결정을 내리도록 하세요!"}, {"노움 기계공학에 대해 더 알고 싶으면 아이언포지에 있는 수석땜장이 오버스파크에게 기계공학 설명서를 가져가야 합니다."}},
[3635] = {"노움 기계공학", {"고급 기계공학은 두 갈래로 분류된다오. 바로 노움 기계공학과 고블린 기계공학이지. 개인적으로 노움 전문 기술은 쓰레기라고 생각하지만, 그래도 그게 뭔지는 알려 주겠소. 노움들은 자신들이 발명한 각종 물건과 장치가 세계를 변화시킨다고 주장한다오. 제대로 작동하는 일이 거의 없다는 게 문제지.","이 설명서를 읽어 보시오. 금속 잡동사니 만드는 방법을 배우고 싶거든 그 설명서를 무법항에 있는 오글소프 오브노티쿠스에게 가져가시오. 하지만 명심하시오. 회원 가입은 영구적이고, 일단 가입하면 다른 갈래의 기계공학은 배우지 못한다오. 그러니까 그가 주는 임무를 완료하기 전에 확실히 마음의 결정을 내리도록 하시오!"}, {"노움 기계공학에 대해 더 알고 싶으면 무법항에 있는 오글소프 오브노티쿠스에게 기계공학 설명서를 가져가야 합니다."}},
[3636] = {"빛의 힘", {"스컬지가 얼라이언스를 끈질기게 위협하고 있다는 것은 <형제/자매>도 잘 알고 있는 얘기일 거요. 그런데 최근 들어 우린 아주 이상한 것을 발견했다오.","불모의 땅에는 가시덩굴 구릉이라는 무시무시한 가시덤불이 있소. 한때는 그저 가시멧돼지가 살던 곳이었지만 지금은 이 녀석들이 스컬지와 어떤 조약을 맺어선지 훨씬 더 흉측한 생물들을 만들어내고 있소... 녀석들은 이제 혹한의 암네나르라는 리치의 지배를 받고 있소.","빛의 힘으로 이 사악한 무리를 처치해주시오."}, {"대주교 베네딕투스가 가시덩굴 구릉에 있는 혹한의 암네나르를 처치해 달라고 했습니다."}},
[3637] = {"노움 기계공학", {"고급 기계공학은 두 가지 갈래로 분류되오. 바로 노움 기계공학과 고블린 기계공학이지. 노움 전문 기술은 배워 봐야 소용이 있을지는 잘 모르겠지만 그래도 알아 두기는 해야 할 거요. 노움들은 자신들이 발명한 각종 물건과 장치들이 세계를 변화시킨다고 주장하지만, 제대로 작동하는 일은 거의 없지.","이 설명서를 읽어 보시오. 금속 장비들을 만드는 방법을 배우고 싶거든 그 설명서를 무법항에 있는 오글소프 오브노티쿠스에게 가져가시오. 명심하시오. 회원 가입은 영구적이고 일단 가입하면 다른 갈래의 기계공학은 배우지 못하게 된다는 사실을. 그러니까 그가 주는 임무를 완료하기 전에 확실한 마음의 결정을 내리도록 하시오!"}, {"노움 기계공학에 대해 더 알고 싶으면 무법항에 있는 오글소프 오브노티쿠스에게 기계공학 설명서를 가져가야 합니다."}},
[3638] = {"비밀유지서약서", {"고블린 기계공학은 높은 수익과 더 큰 폭발력을 가져올 실용적인 기술이지! 우리 설계도에 나와있는 장치들을 이용하면 강력한 폭탄과 엄청난 무기들을 만들 수 있는데, 이 점에 대해서는 설명서에도 나와 있소.","또한 설명서에는 우리 설계도의 비밀에 대한 아주 오래된 서약에 대해서 나와 있자. 한 번 고블린 기술자가 되면 그걸로 끝이오. 노움 기계공학에는 전혀 손을 댈 수 없게 되는 것이지.","그래도 고블린 기술자가 되기를 원한다면 평생 동안 비밀을 지킬 것을 서약하는 이 문서에 서명한 후에 다시 나와 얘기하도록 합시다. 신중히 생각하도록 하라고."}, {"고블린 기술자가 되는 데 동의하면 닉스의 비밀유지서약서를 마우스 오른쪽 단추로 클릭한 후 가젯잔에 있는 닉스 스프로켓스프링과 대화해야 합니다."}},
[3639] = {"직접 만든 물건", {"눈이 반짝이는 걸 보니 잘 알겠어. 뭔가 날려버리고 싶은 거군. 자네에겐 아직 꽃피지 않은 재능이 있다고, <name>. 하지만 일단 자네 작품을 좀 받아 봤으면 좋겠는데. 그래야만 내가 자네의 작품을 검토하고, 고블린 지향적인 기술을 강조할 방법을 찾아내지 않겠어? 내게 대형 철제 폭탄 20개, 조밀한 다이너마이트 20개, 양 폭탄 5개를 가져오라고. 물론 안전장치는 꼭 잠가야 해!","이 물건들을 내게 제출하면 공식적인 고블린 기술자로 인정해 주지!"}, {"가젯잔에 있는 닉스 스프로켓스프링에게 대형 철제 폭탄 20개, 조밀한 다이너마이트 20개, 그리고 양 폭탄 5개를 가져가야 합니다."}},
[3640] = {"비밀유지 서약서", {"노움 기계공학은 모두의 삶에 필요한 모든 것들을 만들 수 있는 이론이라오! 설계도를 이용해, 세계를 제어할 수 있는 장치를 만들 수도 있는데, 이 점에 대해서는 당신에게 드리는 설명서에도 자세히 나와 있소이다.","또한 그 설명서에는 우리 설계도의 비밀에 대한 아주 오래된 서약에 대해서도 나와 있소. 그러니까 한번 노움 기술자가 되면 그걸로 끝이오. 고블린 기계공학에는 전혀 손을 댈 수 없게 되지.","그래도 노움 기술자가 되기를 원한다면 평생 동안 비밀을 지킬 것을 서약하는 이 문서에 서명한 후 다시 나와 얘기합시다. 신중히 생각하도록 하시오."}, {"노움 기술자가 되는 데 동의하면 비밀유지서약서를 마우스 오른쪽 단추로 클릭해 서명을 한 후 아이언포지의 수석땜장이 오버스파크와 다시 한번 대화하십시오."}},
[3641] = {"직접 만든 물건", {"진짜 기똥찬 장치를 만들고 싶다면 제대로 찾아온 거요. 당신이 노련한 기술자란 건 나도 알지만, 그래도 당신의 작품을 일단 보고 싶소. 당신의 작품을 검토해, 앞으로 당신이 작업할 때 노움 지향적인 기술을 강조할 수 있는 방법을 찾아야 하니까 말이오. 내게 미스릴관 6개, 정밀한 조준경 1개, 고급 표적 허수아비 2개를 가져오시오.","이 물건들을 내게 가져오면 공식적인 노움 기술자로서 모든 설계도를 익히실 수 있다오!"}, {"아이언포지에 있는 수석땜장이 오버스파크에게 미스릴관 6개, 정밀한 조준경 1개, 고급 표적 허수아비 2개를 가져가야 합니다."}},
[3642] = {"비밀유지서약서", {"우리의 기계공학은 노움 종족의 모든 요구를 충족시킬 수 있도록 잘 다듬어져 온 이론이오. 우리 설계도들을 이용하면 세계를 우리의 필요에 따라 제어할 수 있는 장치들을 만들 수 있는데, 이 점에 대해서는 당신이 읽은 설명서에도 나와 있소이다.","그리고 한가지 서약에 대해서도 나와 있소. 설계도의 비밀에 대한 아주 오래된 약속이지. 그러니까... 한번 노움 기술자가 되면 그걸로 끝이오. 고블린 기계공학에는 손도 댈 수 없지.","그래도 노움 기술자가 되기를 원한다면 평생 동안 비밀을 지킬 것을 서약하는 이 문서에 서명한 후에 다시 나와 얘기합시다. 신중히 생각하도록 하시오."}, {"노움 기술자가 되는 데 동의하면 비밀유지서약서를 마우스 오른쪽 단추로 클릭한 후 무법항에 있는 오글소프 오브노티쿠스와 다시 한번 대화하십시오."}},
[3643] = {"직접 만든 물건", {"진짜 기똥찬 장치를 만들고 싶다면 제대로 찾아온 거예요. 당신이 노련한 기술자란 건 저도 알지만, 그래도 당신의 작품을 일단 보고 싶어요. 당신의 작품을 검토해, 앞으로 당신이 작업할 때 노움 지향적인 기술을 강조할 방법을 찾아야 하니까요. 제게 미스릴관 6개, 정밀한 조준경 1개, 고급 표적 허수아비 2개를 가져오세오.","이 물건들을 가져오시면 공식적인 노움 기술자로서 모든 설계도를 익히실 수 있어요!"}, {"무법항에 있는 오글소프 오브노티쿠스에게 미스릴관 6개, 정밀한 조준경 1개, 고급 표적 허수아비 2개를 가져가야 합니다."}},
