[3661] = {"엘룬의 피조물", {"올빼미야수는 정말 예측할 수 없는 생물이오. 한없이 순하다가도 자신들이 중요하게 여기는 것이 위협을 받으면 극도로 포악해지거든.","들리는 소문에 의하면 올빼미야수는 엘룬이 창조했다고 하오. 난 그게 사실인지 정말 궁금하다오.","동부 내륙지에 올빼미야수 무리가 서식한다는 소리를 최근에 들었는데 그들에 대해서 좀 더 알고 싶소. 가서 올빼미야수 깃털 15개를 모아 내게 가져다주지 않겠소?"}, {"에렐라스 앰버스카이에게 동부 내륙지에 있는 올빼미야수 깃털 15개를 가져가야 합니다."}},
[3681] = {"신앙의 고서", {"길 가는데 방해해서 미안하오만, 스톰윈드에서 훈련을 받도록 성기사들을 소환하고 있다고 하던데, 혹시 알고 있어야 하지 않을까 싶어서 불렀소.","내가 보기에 당신의 때가 무르익어 이제 신앙의 고서를 배우기 시작할 때가 된 것 같소이다.","스톰윈드에서 계시는 두소리안 랄님을 찾아가 보시오. 그분은 보통 빛의 대성당에서 성기사들을 훈련하고 계시오."}, {"스톰윈드에 있는 두소리안 랄과 대화하십시오."}},
[3701] = {"불타는 타우릿산의 폐허", {"불타는 평원에 있는 타우릿산의 불타는 폐허로 가서 가능한 많은 정보를 수집해 오십시오.","소문에 의하면 그곳에는 신비한 유물들이 널려 있다고 하니 수색해 보는 게 좋겠습니다. 그 폐허를 지키고 있는 타우릿산의 수호병들을 조심하십시오. 아직 그곳에 묻혀 있는 모든 비밀을 보호하고 있는 암살자들이랍니다."}, {"불타는 평원, 타우릿산의 폐허에 있는 타우릿산 유물에서 정보를 얻어야 합니다. 정보를 발견하면 왕실사학자 아케소누스에게 가야 합니다."}},
[3702] = {"불타는 타우릿산의 폐허", {"검은무쇠 드워프의 역사를 간단히 들어 보지 않으시겠습니까?"}, {"왕실사학자 아케소누스가 들려 주는 타우릿산의 역사를 들어야 합니다."}},
