[7500] = {"신비술사의 요리책", {"이 책에는 도서관의 인장이 찍혀있습니다."}, {"주인에게 책을 돌려줘야 합니다."}},
[7501] = {"빛과 정의에 관하여", {"이 책에는 도서관의 인장이 찍혀있습니다."}, {"주인에게 책을 되돌려주십시오."}},
[7502] = {"지배의 그림자", {"이 책에는 도서관의 인장이 찍혀있습니다."}, {"주인에게 책을 돌려줘야 합니다."}},
[7503] = {"사냥꾼의 위대한 혈통", {"이 책에는 도서관의 인장이 찍혀있습니다."}, {"주인에게 책을 돌려줘야 합니다."}},
[7504] = {"성스러운 볼로냐: 빛이 알려주지 않는 것들", {"이 책에는 도서관의 인장이 찍혀있습니다."}, {"주인에게 책을 되돌려주십시오."}},
[7505] = {"냉기 충격과 주술", {"이 책에는 도서관의 인장이 찍혀있습니다."}, {"주인에게 책을 되돌려주십시오."}},
[7506] = {"에메랄드의 꿈...", {"이 책에는 도서관의 인장이 찍혀있습니다."}, {"주인에게 책을 돌려줘야 합니다."}},
[7507] = {"폴로르의 용사냥 개론", {"매우 중요한 무엇인가를 찾았습니다. 이 책은 지금까지 보아온 것들과는 다릅니다.","누군가가 도서관의 인장을 깨는 데 성공한 듯하며, 책 안에 쓰인 글은 흐릿하고 거의 읽을 수 없을 정도입니다."}, {"폴로르의 용사냥 개론을 도서관에 되돌려주어야 합니다."}},
[7508] = {"쿠엘세라 검 만들기", {"이 임무를 맡으려면 현자 리드로스에게 무딘 칼을 보여주어야 합니다."}, {"납작하고 무딘 엘프 검을 현자 리드로스에게 주어야 합니다."}},
[7509] = {"쿠엘세라 검 만들기", {"이 달궈지지 않은 고대의 검을 오닉시아의 둥지로 가져가 오닉시아를 싸움에 끌어들이도록 하시오.","이 칼을 달구려면 꾀를 써야 하는데, 전투 중에는 오닉시아가 반드시 당신을 불태워 죽이려 할 것이오. 이 달궈지지 않은 고대의 검을 근처에 꽂아 놓고 오닉시아의 화염 숨결로 달구어지기를 기대할 수밖에 없소이다. 그 후에도 살아남았다면 달궈진 고대의 검을 다시 뽑아든 후 그 용을 처치하시오.","달궈진 고대의 검을 불타는 오닉시아의 시신에 꽂으면 가공된 고대의 검이 될 것이오."}, {"오닉시아가 달궈지지 않은 고대의 검에 화염 숨결을 내뿜도록 한 후, 달궈진 고대의 검을 뽑으십시오. 달궈진 고대의 검은 언제까지나 달구어져 있지 않으니 서둘러야 합니다.","마지막 단계로 오닉시아의 시체에 달궈진 고대의 검을 꽂으십시오.","이렇게 하면 쿠엘세라 검을 가질 수 있습니다."}},
[7541] = {"호드를 위한 활약", nil, nil},
[7562] = {"모르줄 블러드브링어", {"당신은 어둠의 기술을 깊이 탐구해왔습니다. 타계의 생물들에 대한 통제력이 아주 강해진 만큼 이제 그 악명 높은 공포마를 당신에게 결속시킬 준비가 되었을지도 모르겠군요.","그 야수를 불러내 오는 의식을 치르려면 상당한 힘과 자원이 필요합니다. 그래도 이 대단한 탈것을 지배하고 싶다면 모르줄 블러드브링어와 얘기해 보십시오. 그는 불타는 평원에 있는 폭풍의 제단 근처에서 야영하고 있습니다.","자, 어서 가십시오. 아주 위험한 임무가 되겠지만 당신 정도의 흑마법사라면 위험을 두려워하지 않는 법이니까요!"}, {"불타는 평원의 폭풍의 제단에 있는 모르줄 블러드브링어와 대화해야 합니다."}},
[7563] = {"피의 광란", {"공포마들은 다른 세계에 서식하는 생물로, 불이 이글거리는 소로스 평원에서 살고 있습니다. 이 야수를 결속시키려면 강력한 상형 문자 주문을 외워야 하는데, 내가 양피지에다 그 상형 문자를 적어줄 수는 있지만 먼저 특별한 잉크가 필요합니다.","머나먼 여명의 설원에는 올빼미야수들이 서식하고 있지요. 그들은 한때 순결한 종족이었지만 이제는 검은속삭임 협곡에서 흘러나오는 마력에 미쳐 광란 상태로 설원을 떠돌고 있습니다.","양피지에 쓸 잉크로 녀석들의 피가 필요하니 가서 그들의 피를 가져오십시오."}, {"불타는 평원에 있는 모르줄 블러드브링어에게 흉포한 올빼미야수의 피 30병을 가져가야 합니다."}},
[7564] = {"와일드아이즈", {"내 하수인인 고르지키라고 실력있는 연금술사가 저기 있습니다. 아마 그 친구가 양피지에 쓸 잉크를 준비해 줄 수 있을 겁니다. 그대가 수집한 피를 모아 두었으니 고르지키에게 가져가면 녀석이 작업을 시작할 수 있을 겁니다."}, {"불타는 평원에 있는 고르지키 와일드아이즈에게 피가 담긴 상자를 가져가야 합니다."}},
[7581] = {"감옥벽의 재료", {"파멸의 수호병을 가둘 감옥의 재료는 하급 악마들에게서 찾아야만 하오.","아마 혈투의 전장을 차지한 야생혈족 사타르의 피가 좋을 거요. 응고된 피가 헤더린 조각들을 결합시키고 수정 감옥을 단단하게 만들어 줄 것이오."}, {"페랄라스에 있는 혈투의 전장으로 간 다음 굽이나무 지구에 서식하는 야생혈족 사티르로부터 사티르의 피 15개를 회수한 후 타락의 흉터에 있는 다이오에게 가야 합니다."}},
[7582] = {"감옥의 외벽", {"하이잘 산의 구릉을 점령한 헤더린 악마들은 강력한 마의 독을 분비하는데, 그 독은 시간이 지나면 딱딱해져서 마법에 내성을 갖는 수정이 된다오. 바로 이 수정을 헤더린의 눈물이라고 부르지.","이 수정으로 감옥의 벽을 만들면 파멸의 수호병이 아무리 용을 쓴다 해도 부서지지 않을 거요.","여명의 설원에 있는 검은속삭임 협곡으로 가서 그 눈물을 구해 오시오."}, {"여명의 설원에 있는 검은속삭임 협곡에서 서식하는 헤더린의 악마들로부터 헤더린의 눈물 5개를 모아야 합니다. 임무를 완수하면 타락의 흉터에 있는 다이오에게 돌아가십시오."}},
[7583] = {"감옥에 가두기", {"감옥은 준비가 되었소. 이제 이 임무에서 가장 어려운 부분만 남았소이다.","타락의 흉터 안에서 파멸의 수호병을 찾아 이 수정 감옥에 가둬야 하오.","조심하시오! 이 수호병은 교활하고 치명적인 적이라 가두려고 의식을 행하려는 즉시 당신을 죽이려고 덤벼들 거요. 사로잡히지 않으려고 아주 거칠게 저항하겠지.","녀석은 당신만 처치하려고 덤벼들 테니 당신을 지켜줄 만한 동료를 데리고 가는 것이 좋을 것이오."}, {"타락의 흉터에서 파멸의 수호병을 찾아내십시오.","빛나는 수정 감옥을 파멸의 수호병에게 사용해야 합니다. 악마는 잡히지 않으려고 맹렬하게 공격할 테니 정신을 바짝 차리십시오.","성공하면 사로잡은 파멸의 수호병을 타락의 흉터에 있는 다이오에게 데려가야 합니다."}},
[7601] = {"나이비께서 명하사", {"지옥불정령을 소환하는 비결을 알고 싶으면 먼저 크로쉬우스를 처치하고 불타버린 그의 잔해에서 지옥핵을 가져 와야 하지. 그게 말처럼 쉬운 일은 아니겠지만... 크로쉬우스는 완전히 타버렸을 테니 잔해에 다시 불을 지펴 잿더미가 그를 다시 살려야 하거든. 그렇게 하려면 다른 악마적 존재로부터 어떤 재료를 모아야 해.","저기 내 부하 임프시가 더 자세히 가르쳐줄 거야."}, {"악령의 숲에 있는 임프시와 대화하십시오."}},
[7602] = {"완전무결한 지옥의 정수", {"크로쉬우스를 되살리려면 지옥의 불꽃이 필요해요. 제가 만들 수 있는데 재료가 좀 필요해요.","이 세계에 있는 세 개의 지역을 여행하고 완전무결한 지옥의 정수를 갖고 오세요.","아즈샤라의 레가쉬 사티로스들이 첫 번째 것을, 제데나르에 있는 제데나르 부대원들이 두 번째 것을, 마지막 것은 저주받은 땅의 지옥수호병 보초들이 갖고 있어요.","이 세 가지 정수를 모아 오면 크로쉬우스를 되살릴 수 있는 지옥의 불꽃을 만들어 드리겠어요."}, {"악령숲에 있는 임프시가 이 세계의 각기 다른 세 개의 지역에서 완전무결한 지옥의 정수 3개를 가져다 달라고 했습니다.","아즈샤라에는 레가쉬 사티로스들, 제데나르에는 제데나르 부대원들, 그리고 저주받은 땅에서는 지옥수호병 보초들이 각기 그 지역의 완전무결한 지옥의 정수를 갖고 있습니다.","완전무결한 지옥의 정수 3개를 다 모은 다음에는 악령숲에 있는 임프시에게 가야 합니다."}},
[7603] = {"크로쉬우스의 지옥핵", {"북동쪽에 있는 불벼락 골짜기로 가면 어딘가에 쓰러져 있는 크로쉬우스가 보일 거예요. 이 지옥의 불꽃을 가지고 가서 그의 잔해에 불씨를 지피세요. 그러면 지옥불정령이 되살아날 겁니다.","분명 녀석은 일어나자마자 당신과 친구들을 모조리 죽이려 들 거예요. 그래서 말인데, 가능한 한 친구들을 많이 데리고 가는 게 좋을 거예요.","그리고 만에 하나 기적이라도 일어나 당신이 크로쉬우스를 처치하게 되는 날에는 녀석의 지옥핵을 나이비에게 가져다주세요."}, {"악령의 숲에 있는 불벼락 골짜기를 수색해 크로쉬우스의 잔해를 찾아야 합니다. 찾으면 부근에서 지옥의 불꽃을 사용해 크로쉬우스를 되살리십시오. 크로쉬우스가 깨어나면 놈을 쓰러뜨린 후 크로쉬우스의 지옥핵을 꺼내야 합니다.","크로쉬우스의 지옥핵을 악령의 숲에 있는 전지전능한 나이비에게 가져다주십시오."}},
[7604] = {"대장조합 계약서", {"이하 서명인은 이에 토륨 대장조합(이하 토대 조합)에 배달되는 설퍼론 주괴가 모조품이 아닌 진품임을 선언하고, 또한 설퍼라스의 제작, 제조, 주조, 수색, 판매, 식음을 시도하다 발생할 수 있는 모든 손해에 대해서 토대 조합의 책임 또는 잘못이 아님을 알립니다.","본 계약에 서명하는 것은 여기 나열된 모든 조항에 동의한다는 것을 의미합니다.","_________________","서명"}, {"설퍼론 도면을 받으려면 설퍼론 주괴와 토륨 대장조합 계약서를 로크토스 다크바게이너에게 가져가야 합니다."}},
[7621] = {"경고", nil, nil},
[7622] = {"빛과 어둠의 균형", {"그대는 내가 해낼 수 없었던 일을 해내야 해요. 스트라솔름에서 탈출하려다 죽임을 당한 인부들을 구해야 합니다.","모두들 빛을 향해 나아갈 것이니, 그대가 그들의 생존을 지켜야 해요. 너무 많은 이들이 쓰러지면 우리들의 저주 받은 존재는 지속되고, 그대는 실패한 것이 됩니다.","그대가 터득한 모든 기술과 주문, 마법에 대한 시험을 받게 될 겁니다. 빛의 가호가 함께하기를."}, {"15명이 살해 당하기 전에 인부 50명을 구해야 합니다. 이 임무를 완수하면 에리스 헤븐파이어와 대화하십시오.","살해 당한 농부의 수를 보려면 죽음의 말뚝을 보십시오."}},
[7623] = {"군주 베인할로우", {"모르줄님이 자네가 소로스로 가는 차원문을 열고 싶어한다고 하시더군. 그곳으로 통하는 차원문을 열려면 소로시안 별가루가 필요할 거야. 그게 웬만해서는 아제로스에서 찾을 수 없는 물건이거든!","그걸 갖고 있을 만한 이가 딱 한 명 있긴 한데, 바로 어둠의 의회가 있는 제데나르의 베인할로우 군주라네!","하지만 제데나르로 가려면 어둠의 의회가 지닌 사악한 기운을 뒤집어써야 하는데, 그렇게 하려면 내 어둠의 물약이 필요하거든. 그건 돈을 내고 사야지..."}, {"불타는 평원의 폭풍의 제단에 있는 고르지키 와일드아이즈에게서 어둠의 물약을 구입해야 합니다.","이 물약을 사용하여 악령숲의 제데나르로 가서 어둠의 요새에 있는 군주 베인할로우와 대화하십시오."}},
[7624] = {"배신자 울라텍", {"네게 은혜를 베풀어 주기를 원한다면 날 위해 처치해 줘야 할 녀석이 있다.","내 하인 울라텍은 내 앞에서는 역겨울 정도로 굽실거리면서 뒷전에서는 소로스의 헬누라스 군주와 음모를 꾸미고 있느니라. 헬누라스는 이곳에서 내 권력을 뒤엎으려 하고 있고 울라텍이 바로 그 첩자인 것이지...","네가 나의 경쟁 상대가 아니라 날 섬기는 자라는 것을 입증해 보이도록 하라. 서쪽에 있는 어둠의 요새로 가서 울라텍의 구역에서 그를 찾도록 해라. 내가 놈의 비밀을 알고 있음을 녀석에게 알리고 녀석이 공포에 떠는 것을 지켜본 후, 그 배신자를 처치하고 그 증거를 내게 가져와야 할 것이다!"}, {"울라텍과 맞서 싸운 후 자에데나르에 있는 베인할로우 군주에게 배신자의 증거를 가져가야 합니다."}},
[7625] = {"소로시안 별가루", {"그래, 소로시안 별가루 때문에 날 찾아온 것이란 말이지? 내 경쟁자 헬누라스의 영토 소로스로 통하는 차원의 문을 열고 싶다고? 녀석이 애지중지하는 공포마라도 한 마리 훔칠 생각인겐가?","좋다. 내 신하 우르단이 소로시안 별가루를 갖고 있으니 녀석에게서 얻을 수 있을 것이다... 그 값을 치를 수 있다면 말이지."}, {"우르단에게서 소로시안 별가루를 구입한 후, 불타는 평원에 있는 고르지키 와일드아이즈에게 가져가야 합니다."}},
[7626] = {"데스무라의 종", {"이미 죽은 흑마법사인 데스무라 다크아이즈의 이름을 딴 데스무라의 종은 대소환진을 만드는 데 필요한 세 가지 도구 중 하나입니다. 물레와 양초가 제대로 조화를 이루면 이 물건들에서 엄청난 양의 마법력이 뿜어져 나오게 되지요.","내 하수인인 고르지키가 그런 종을 만들 줄 알지만, 그렇게 하려면 먼저 암흑 강화의 비약이 많이 필요합니다. 연금술을 배운 친구에게 그 비약을 구해 고르지키에게 가져가도록 하십시오."}, {"불타는 평원에 있는 고르지키 와일드아이즈에게 암흑 강화의 비약 2개를 가져가야 합니다."}},
[7627] = {"어둠의 물레", {"어둠의 물레에는 대소환진에 필요한 극도의 힘이 깃들어 있어 물레가 그 힘을 조종합니다. 마법부여사 친구에게서 눈부신 큰 결정을, 그리고 광부 친구에게서 검은무쇠 광석을 구하면 고르지키가 그 물레를 만들어줄 수 있을 겁니다.","이 재료들을 모아 고르지키에게 가져가도록 하십시오."}, {"불타는 평원에 있는 고르지키 와일드아이즈에게 눈부신 큰 결정 6개와 검은무쇠 광석 25개를 가져가야 합니다."}},
[7628] = {"심판의 양초", {"심판의 양초는 불을 비축하고 있다가 의식을 방해하려는 자가 나타나면 그를 공격하게 되지요. 그대가 대소환진을 만들 때 양초가 켜져 있으면 의식을 방해하려는 악마들이 그 불길에 타 버리게 될 겁니다!","그 불을 억제하는 건 쉬운 일이 아닐 뿐 아니라, 이를 위해서는 검은용 비늘에 깃든 정수도 필요합니다. 무두질이 가능한 친구에게 그 비늘들을 얻어서 고르지키 와일드아이즈에게 가져가도록 하십시오."}, {"불타는 평원에 있는 고르지키 와일드아이즈에게 검은용 비늘 3개를 가져가야 합니다."}},
[7629] = {"임프 배달", {"모르줄님의 양피지를 만드는 데 필요한 한 가지 절차가 더 있네. 바로 종이와 소로시안 별가루를 섞는 일인데... 여기서는 장비가 없어서 할 수가 없단 말이야!","하지만 필요한 장비를 모두 갖춘 연금술 실험대가 있긴 하지. 역병지대의 카엘 다로우의 섬에 위치한 스칼로맨스에 있다네.","나는 거기까지 갈 수 없으니 이 단지를 받게. 이 안에는 머리가 꽤 좋은 임프가 한 마리 들어 있는데, 연금술 실험대에서 풀어 주면 알아서 양피지를 만들어 낼 거야."}, {"단지 안에 든 임프를 스칼로맨스에 있는 연금술 실험대로 가져가야 합니다. 양피지가 완성되면 단지를 고르지키 와일드아이즈에게 되돌려 주십시오."}},
[7630] = {"아케이나이트", {"종, 물레, 그리고 양초를 모두 만들었으니 검은 자철광을 만들어야겠군. 자네의 의식이 진행되는 동안, 의식이 실패하게 된다면 다음 의식을 진행할 물건들을 원상태로 유지하기 위해서 자철광이 필요하게 될 걸세.","자철광을 만들려면 말이야, 아케이나이트가 필요하네. 자네 좀 가지고 있나? 만약 없다면... 연금술사 친구한테서 하나 얻어야 할 것이야.","아케이나이트를 구하거든 내게 다시 오게. 그러면 검은 자철광을 만들어 주지."}, {"불타는 평원에 있는 고르지키에게 아케이나이트 주괴 3개를 가져가야 합니다."}},
[7631] = {"소로스의 공포마", {"준비는 모두 마쳤군요. 이제 그대가 대소환진을 만드는 의식을 치르고 난 후, 소로스로 통하는 차원문을 열기 위해서... 소로시안 별가루를 입힌 양피지를 사용하십시오.","의식에 대한 지침은 두루마리에 적어 놓았습니다. 성공하길 바란다면 각 단계를 정확히 행해야 하니 자세히 읽도록 하십시오.","행운을 빌겠습니다. 그대 적들의 영혼이 그대의 영혼의 주머니로 재빨리 빨려들어가기를..."}, {"모르줄의 지시서를 잘 읽으십시오. 소로시안 공포마를 소환해 물리친 후 그 영혼을 결속시켜야 합니다."}},
[7632] = {"고대의 잎사귀", {"이 잎사귀의 표피는 밝은 녹색 광채를 띠고 있습니다. 표면에는 주름이 매우 많고 잎맥이 몇 개 드러나 있습니다.","이 잎사귀는 어떤 고대의 강력한 존재에게서 나온 것 같습니다.","잎사귀의 원래 주인을 찾아가 보는 것이 좋지 않을까요?"}, {"단단한 고대의 잎사귀의 주인을 찾아야 합니다. 행운을 빕니다, <name>. 세상은 아주 넓으니까요."}},
[7633] = {"고대의 잎사귀", nil, nil},
[7634] = {"힘줄 감긴 고대의 잎주머니", {"어서 오라, <class>|1이여;여;. 나는 지식의 고대정령 하스타트... 아주 오래전에 사라진 지식을 갖고 있느니라.","제대로 묶을 줄만 있으면 내 잎사귀로 1만 년에 한번이나 나올까 말까 한 훌륭한 화살통을 만들 수 있다. 고대정령의 활과 함께 사용하면 어마어마한 힘을 얻을 수 있지.","다 성장한 푸른용의 힘줄을 가지고 이곳으로 돌아오면 화살통을 완성하여 그대에게 주리라."}, {"다 자란 푸른용 힘줄을 찾아서 악령숲에 있는 고대정령 하스타트에게 가져가야 합니다."}},
[7635] = {"최고의 활시위", {"고대정령의 활을 완성하는 데 필요한 지팡이는 바르투스가 줄 수 있지만 활시위에 마법을 부여할 수 있는 건 나뿐이니라.","아주 간단한 일이다. 다 자란 검은용 한 마리를 처치하고 그 뼈에 붙어 있는 힘줄을 가져오기만 하면 되니까. 그 힘줄을 내게 가져오면 마법을 부여해 불멸에 가까운 활시위로 만들어 주리라."}, {"고대정령 스토마가 다 자란 검은용 힘줄을 가져다 달라고 부탁했습니다. 이것을 찾으면 악령의 숲에 있는 스토마에게 가져가십시오."}},
[7636] = {"고대정령의 지팡이", {"아직도 이 세계에는 불타는 군단이 활동하고 있다. 놈들이 나타날 때 녀석들의 손에 닿는 것은 모두 부패하여 자연이 시들고 흐느끼느니라.","<name>, 그 타락하고 부패한 놈들을 찾아 처치하도록! 미리 경고하겠다. 그대가 홀로 맞서지 않고 친구들과 힘을 합쳐 달려들면 녀석들이 도망칠 것이다. 그대가 홀로 맞선다면 녀석들이 단박에 달려들어 그대를 처치하려 들 것이다. 그 오만하고 비겁한 녀석들의 머릿속에 고통이 무엇인지 가르쳐 주어라.","자, 나의 돌껍질 하나를 떼어내 주겠노라. 여기 새겨진 글이 그대를 적에게 인도할 것이다. 모두 처치하고 그 증거를 가지고 나에게 돌아오라."}, {"타락한 악령의 4인조를 찾아 처치해야 합니다.","매혹의 시몬","광기의 클린프랑","학살자 솔레노르","파멸의 인도자 아토리우스","위 넷의 혼을 갈무리하여 악령의 숲에 있는 고대정령 바르투스에게 돌아가십시오.","이들의 위치에 대한 단서는 바르투스가 주는 돌껍질을 참조하십시오.","이 임무는 반드시 혼자서 완수해야 합니다."}},
[7637] = {"희생의 중요성", {"이 일을 맡으려면 희생정신과 판단력이 충분하다는 것을 증명해야 한다네. 특히 희생정신이 강조되지.","대사제이신 로한님의 퇴마 향로를 가지고 온다면 일을 맡기겠네. 비록 정당하고 옳은 일에 퇴마 향로를 사용하려는 것이라 해도 충분한 보상을 치러야만 한다네.","아이언포지로 가서 대사제님과 대화를 나누어 보게. 돈을 넉넉하게 지불한다면 자네의 희생정신을 충분히 증명할 수 있을 것이야.","서두르게, <class>|1이여;여;. 시간은 중요한 것이야."}, {"아이언포지에 가서 대사제 로한의 퇴마 향로를 얻어야 합니다. 퇴마 향로를 얻으려면 150골드를 기부해야 합니다."}},
[7638] = {"그레이슨 섀도브레이커 경", {"자네가 지금까지 무엇을 얻기 위해 기다려 왔는지 알고 있네, <class>|1이여;여;. 이제 어떻게 하면 그것을 얻을 수 있는지 알려 주겠네.","여기 스톰윈드 빛의 대성당에 계시는 그레이슨 섀도브레이커 경과 대화를 나누어 보게. 성기사에게 가장 어울리는 최고의 군마는 어떤 녀석인지, 그 녀석을 얻으려면 어떻게 해야 하는지 알려주실 것일세.","좋은 결과 있기를 빌겠네, 고귀한 <형제/자매여>. 정의를 위해!"}, {"스톰윈드 빛의 대성당에 있는 그레이슨 섀도브레이커 경과 대화해야 합니다."}},
[7639] = {"희생과 심판", {"자, 이 향로를 받으십시오. 이것은 제가 악령 퇴치에 사용하던 것으로 <형제/자매>가 앞으로 겪게 될 험난한 여정에도 많은 도움이 될 것입니다. 축복을 빌겠습니다.","이제 스톰윈드에 있는 그레이슨 섀도브레이커 경에게 돌아가십시오. <형제/자매>의 임무는 아직 끝나지 않았습니다."}, {"퇴마 향로를 가지고 스톰윈드 빛의 대성당에 있는 그레이슨 섀도브레이커 경에게 돌아가야 합니다."}},
[7640] = {"테러데일 퇴마", {"동부 역병지대의 테러데일에 있는 오염된 땅에서 흙을 조금 떠서 향로에 넣었네. 이 향로를 사용하면 영적으로 동요하고 있는 지역을 찾을 수 있을 것이네. 녹색 오라를 찾아야 하네. 녹색 오라는 영혼의 세계가 이 세계를 침범하면서 고통으로 물들이는 지역이라네. 녹색 오라를 찾으면 향로를 사용해서 악마의 영혼을 몰아내도록 하게.","충분한 숫자의 영혼을 몰아내게 되면 다음 단계를 진행할 수 있을 걸세."}, {"퇴마 향로를 사용하여 테러데일에 고통을 주는 영혼을 몰아내야 합니다. 25명의 테러데일 영혼을 해치운 다음 스톰윈드 빛의 대성당에 있는 그레이슨 섀도브레이커 경에게 돌아가야 합니다."}},
[7641] = {"그리만트 엘모어의 마갑", {"적합한 마갑이 없다면 성기사의 군마라고 해도 단지 짐이나 나르는 동물일 뿐이라네. 자네의 군마에 사용할 마갑은 최고급 재료로 만들어야 할 뿐 아니라 주변 환경 때문에 영혼의 세계와 균형을 이루도록 세심한 배려가 필요하네.","일단 마갑을 만들도록 하세나. 스톰윈드의 드워프 지구에 살고 있는 그리만트 엘모어야 말로 이 일에 딱 맞는 최고의 장인이지. 그의 작업비는 꽤 비싸겠지만 실력 하나는 최고라네. 드워프 지구로 가서 그와 대화를 나눠보게나."}, {"스톰윈드의 드워프 지구에 있는 그리만트 엘모어와 대화해야 합니다."}},
[7642] = {"마갑 재료 수집", {"마갑을 만들려면 안감에 사용할 룬무늬 옷감, 도금에 사용할 아케이나이트 주괴, 영적인 균형을 조절하기 위한 아서스의 눈물, 그리고 영혼을 불어넣기 위한 스트라솔름 성수 이렇게 네 종류의 재료가 필요하오. 정확하게 수고비까지 생각한다면 다섯 가지가 되나? 수고비는 손으로 직접 만든 안장, 등자 그리고 인건비까지 포함된 가격이오.","재료와 수고비를 모두 가지고 오면 마갑을 만들어 주겠소. 지금부터 당신이 재료를 모아서 돌아올 때까지 당신에게 줄 선물을 준비하도록 하지."}, {"룬무늬 옷감 40개, 아케이나이트 주괴 6개, 아서스의 눈물 10개, 스트라솔름 성수 물병 5개 그리고 150골드를 스톰윈드의 드워프 지구에 있는 그리만트 엘모어에게 가져가야 합니다."}},
[7643] = {"고대 준마의 영혼", {"자네의 마갑은 특별한 형태의 축복이 필요하다네. 특별한 영혼의 축복이 있어야 한다는 것일세. 혈투의 전장에 있는 고대 준마의 영혼이 바로 그러한 영혼이라 할 수 있지. 그 영혼은 지금 타락한 나무정령인 굽이나무 텐드리스에게 붙잡혀 있네. 이 사악한 악마를 해치우게나.","단, 영혼을 풀어준다고 모두 해결되는 것은 아니라네. 축복을 받으려면 영혼을 진정시켜야만 하는데 그러기 위해서 특별한 말 먹이가 필요하네. 혈투의 전장으로 가기 전에 먼저 사우스쇼어에 있는 메리데스 칼슨을 찾아가 보게나... 그리고 더욱 투철한 희생정신이 필요할 것이네."}, {"사우스쇼어에 있는 메리데스 칼슨에게 말의 영혼에게 먹일 특별한 말 먹이를 얻어야 합니다.","페랄라스에 있는 혈투의 전장 던전으로 가서 굽이나무 텐드리스를 무찔러야 합니다. 굽이나무 텐드리스를 무찌르고 고대 준마의 영혼이 풀려나면 특별한 말 먹이를 먹여 영혼을 진정시키십시오. 마지막으로 아케이나이트 마갑을 주면 영혼이 축복을 내릴 것입니다."}},
[7644] = {"축복받은 아케이나이트 마갑", {"마갑을 고대 준마의 영혼의 등에 올리자 마갑이 약간 커집니다. 짧은 순간 공기가 가득 차면서 강한 기운이 느껴집니다. 이때 말의 등에서 마갑이 사라지더니 소지품 속에 안전하게 보관됩니다.","마갑이 축복받은 것 같습니다. 이제 그레이슨 섀도브레이커 경의 지시에 따라 마갑을 가지고 스톰윈드로 돌아가야 합니다."}, {"축복받은 아케이나이트 마갑을 가지고 스톰윈드의 빛의 대성당에 있는 그레이슨 섀도브레이커 경에게 돌아가야 합니다."}},
[7645] = {"맛좋은 만나 사료", {"영혼에게 먹일 수 있는 특별한 말 먹이를 만들 수 있다고 은빛성기사단에게 말했더니 나를 미쳤다고 생각하더군요. 모두 내가 미쳤다고 생각하고 있어요! 모두에게 보여주겠어요!","필요한 물건을 만들어 주겠어요. 성기사를 용사라고도 하던가요? 어쨌든 공짜는 아니에요. 내가 원하는 것은 특별한 빵이에요. 은빛 여명회에서만 파는 맛좋은 만나빵을 가지고 오세요. 그리고 한 가지 더 요구하자면 내가 겪은 정신적 피해에 대한 보상을 원해요!","당신네 성기사들이 주절거리는 희생정신을 증명해 보세요."}, {"맛좋은 만나빵 20개를 모아서 힐스브래드 구릉지의 사우스쇼어에 있는 메리데스 칼슨에게 가져가야 합니다. 맛좋은 만나빵은 맛좋은 만나 사료에 꼭 필요한 재료입니다. 맛좋은 만나빵은 은빛 여명회에서 독점적으로 파는 것으로 알려져 있습니다.","메리데스 칼슨에게도 50골드를 주어야만 화가 난 마음을 진정시킬 수 있습니다."}},
[7646] = {"예언의 탐지기", {"이제 예언의 탐지기를 만들어야 하네. 이 일은 군마의 영혼을 되찾기 위한 출발점이기도 하지. 사실 자네의 탐지기가 거의 완성되었네. 테러데일에서 사용한 퇴마 향로가 예언의 탐지기의 촉매 역할을 하는 것일세. 장치에 필요한 틀은 내가 만들도록 함세.","마지막으로 다이아몬드 두 개가 필요하네. 아제로스 다이아몬드와 온전한 검은 다이아몬드, 이 두 개의 다이아몬드를 나란히 놓아야 한다네. 이것들을 통해서 탐지기가 어둠을 뚫고 들어갈 수 있게 되는 것일세!"}, {"스톰윈드의 빛의 대성당에 있는 그레이슨 섀도브레이커 경에게 아제로스 다이아몬드와 온전한 검은 다이아몬드를 가져가야 합니다."}},
[7647] = {"심판과 구원의 손길", {"이 가방을 받게. 그 안에는 임무를 위해 필요한 물건이 들어 있네.","스칼로맨스의 대형 납골당 지하의 중심부로 가서 예언의 탐지기를 사용한 다음 저주받은 영혼들이 나타나면 해치우게. 자네의 문장을 잘 사용해서 임무를 완수하게. 그들은 자네 생각보다 강력한 힘을 가지고 있을 것이네. 영혼들을 해치우고 나면 죽음의 기사가 나타날 것이네. 그를 해치우고 군마의 영혼을 되찾아야 하네. 그래야만 말을 구해내고 자네의 새 말로 삼아 마갑을 입힐 수가 있네!","행운을 빌겠네!"}, {"스칼로맨스에 있는 대형 납골당 지하의 중심부로 가서 예언의 탐지기를 사용해야 합니다. 그러면 영혼들이 나타날 것이니 그들을 해치우십시오. 그들을 해치우면 죽음의 기사 다크리버가 나타날 것이니 그를 물리친 다음 군마의 영혼의 잃어버린 영혼을 되찾아야 합니다.","구원된 군마의 영혼과 축복받은 아케이나이트 마갑을 다크리버 군마의 영혼에게 돌려줘야 합니다."}},
[7648] = {"그리만트의 걸작품", {"<자부심 가득한 얼굴로 이마의 땀을 닦는 그리만트 엘모어>","자, 모두 완성됐소, 친구. 내 작품 중에서도 걸작에 속한다고 할 수 있지. 그레이슨 섀도브레이커 경께 만들어 드린 마갑과 비교해도 전혀 떨어지지 않는다오. 나를 믿고 최선을 다해 도와줘서 고맙소이다.","그레이슨 섀도브레이커 경께서도 틀림없이 내 작품을 보고 싶어할 것이오. 내가 만들어 준 마갑을 그분께 가져가시오. 그분의 영예로운 일에 참여할 기회를 주셔서 고맙다는 말도 전해주시오."}, {"스톰윈드 빛의 대성당에 있는 그레이슨 섀도브레이커 경에게 아케이나이트 마갑을 가져가야 합니다."}},
