[8775] = {"목표: 독침 하이브레갈", {"전쟁 준비가 진행되면서 이 지역에 존재하는 실리시드가 우리의 작전에 아주 큰 위협이 되고 있습니다. 당신은 하이브레갈 공격 작전에 선발되었으며 당신의 주요 목표는 날아다니는 독침 하이브레갈입니다.","임무를 완수하면 지휘관 마랄리스에게 보고하십시오."}, {"독침 하이브레갈 30마리를 처치한 후 실리더스의 세나리온 요새에 있는 지휘관 마랄리스에게 전투 임무 지령 IX를 가져가야 합니다."}},
[8776] = {"목표: 암컷 하이브레갈", {"전쟁 준비가 진행되면서 이 지역에 존재하는 실리시드가 우리의 작전에 아주 큰 위협이 되고 있습니다. 당신은 하이브레갈 공격 작전에 선발되었으며 당신의 주요 목표는 일꾼인 암컷 하이브레갈입니다.","임무를 완수하면 지휘관 마랄리스에게 보고하십시오."}, {"암컷 하이브레갈 30마리를 처치한 후 실리더스의 세나리온 요새에 있는 지휘관 마랄리스에게 전투 임무 지령 X을 가져가야 합니다."}},
[8777] = {"목표: 하이브레갈 땅무지", {"전쟁 준비가 진행되면서 이 지역에 존재하는 실리시드가 우리의 작전에 아주 큰 위협이 되고 있습니다. 당신은 하이브레갈 공격 작전에 선발되었으며 당신의 주요 목표는 하이브레갈 땅무지입니다.","임무를 완수하면 지휘관 마랄리스에게 보고하십시오."}, {"하이브레갈 땅무지 30마리를 처치한 후 실리더스의 세나리온 요새에 있는 지휘관 마랄리스에게 전투 임무 지령 XI을 가져가야 합니다."}},
[8778] = {"아이언포지 여단에 폭탄 공급!", {"아이언포지 여단에서 폭발하기 아주 쉬운 재료를 보내 달라는 별난 요청을 해왔습니다. 재료를 모아서 아이언포지 여단에 있는 노즐스프링 씨에게 가져다주세요."}, {"실리더스의 하이브조라 근처에 있는 비전술사 노즐스프링에게 제물의 기름 6개, 고블린 로켓 연료 5개, 강도 높은 화약 10개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 IV도 함께 가져가야 합니다."}},
[8779] = {"수정점 재료", {"지질학자 락스베인의 연구가 실리더스에서 우리가 수행하는 작전에 엄청난 도움이 된다는 것이 입증되었습니다. 최근에 그녀는 수정점 연구에 사용할 재료를 요청했습니다.","그녀가 연구를 계속할 수 있도록 재료를 모으는 즉시 가져다주십시오."}, {"실리더스의 세나리온 요새에 있는 지질학자 락스베인에게 눈부신 큰 결정, 찬란하게 빛나는 큰 결정, 거대 에메랄드를 각각 1개씩 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 V도 함께 가져가야 합니다."}},
[8780] = {"전장에 방어구 강화도구 공급", {"전장에 있는 아이언포지 여단의 방어구 강화도구 보유량이 매우 부족한 상황입니다. 방어구 강화도구를 구해서 하이브조라 외곽의 아이언포지 여단 주둔지에 있는 자넬라 스타우트해머에게 전달해 주십시오."}, {"실리더스의 하이브조라 근처 아이언포지 여단 주둔지에 있는 자넬라 스타우트해머에게 튼튼한 방어구 강화도구와 고급 방방어구 강화도구를 각각 8개씩 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 VII도 함께 가져가야 합니다."}},
[8781] = {"전장에 무기 공급", {"아이언포지 여단은 우리의 실리더스 전투에서 든든한 동맹의 역할을 수행해 왔습니다. 그런데 유감스럽게도 아이언포지 여단이 이곳으로 이동하는 도중 보급품의 상당 부분을 분실했습니다. 넓적검을 구해서 하이브조라 근처에 있는 자넬라 스타우트해머에게 전달해 주십시오."}, {"하이브조라 외곽의 아이언포지 여단 주둔지에 있는 자넬라 스타우트해머에게 달빛강철 넓적검 2개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 VI도 함께 가져가야 합니다."}},
[8782] = {"제복 공급", {"최근에 들어온 지원자들에게 지급할 제복이 부족합니다. 제복 재료를 구해서 바람소환사 프라우드혼에게 가져다주십시오."}, {"실리더스 세나리온 요새에 있는 바람소환사 프라우드혼에게 달빛 옷감 1개, 룬무늬 두루마리 2개, 그리고 강철그물 거미줄 1개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 VIII도 함께 가져가야 합니다."}},
[8783] = {"특별한 재료", {"세나리온 요새의 대장장이 바르구스가 우리 군대가 쓸 강력한 무기에 마력을 부여할 재료를 요청했습니다. 세나리온 요새의 군대가 완전히 무장할 수 있도록 이 재료들을 구하는 즉시 바르구스에게 가져다주십시오."}, {"실리더스의 세나리온 요새에 있는 바르구스에게 마력 깃든 토륨 주괴 2개, 마력 깃든 가죽 2개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 IX도 함께 가져가야 합니다."}},
[8784] = {"퀴라지의 비밀", {"당신이 발견한 것은 고대 퀴라지 유물입니다! 신전의 입구 근처에 있는 용들 중 하나가 이 유물의 사용법을 알고 있을 겁니다."}, {"신전 입구 근처에 숨어 있는 용족에게 고대 퀴라지 유물을 가져가야 합니다."}},
[8785] = {"오그리마 군단에 모조 공급!", {"오그리마 군단에서 몇 가지 특이한 재료를 요청했습니다. 현재 그들이 실리더스에서 수행하는 임무를 고려해 볼 때 최대한 신속하게 요청을 들어주는 것이 좋을 것 같습니다. 재료를 모아서 샤이라는 트롤에게 가져다주십시오."}, {"실리더스의 하이브레갈 근처에 있는 어둠의여사제 샤이에게 제물의 기름 8개, 왕모조 물약 6개, 강력한 모조 6개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 IV도 함께 가져가야 합니다."}},
[8786] = {"전장에 무기 공급", {"오그리마 군단에서 무기를 추가로 공급해 달라고 요청했을 때는 우리가 그럴 수 있는 형편이 아니었습니다. 이제 그들이 요청한 무기를 구하여 하이브레갈 외곽에 있는 메로크 롱스트라이드에게 가져다주십시오."}, {"하이브레갈 외곽 오그리마 군단 주둔지에 있는 메로크 롱스트라이드에게 큰 철제 도끼 3개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 VI도 함께 가져가야 합니다."}},
[8787] = {"전장에 방어구 강화도구 공급", {"오그리마 군단의 용감한 지원자들이 우리를 도와 대업을 달성하기 위해 실리더스에 도착했습니다. 그런데 여행 중에 그만 방어구 강화도구를 분실했다고 합니다. 방어구 강화도구를 모아 오그리마 군단 주둔지에 있는 메로크 롱스트라이드에게 가져다주십시오."}, {"하이브레갈 부근에 있는 메로크 롱스트라이드에게 튼튼한 방어구 강화도구와 고급 방어구 강화도구를 각각 8개씩 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 VII도 함께 가져가야 합니다."}},
[8788] = {"누가 살짝 흔들어 본 선물", nil, nil},
[8789] = {"제국의 퀴라지 무기", {"우리가 천 년 동안 놀고만 있었던 건 아니라네.","우린 이 짐승들은 쓸모가 없다면 자신의 종족까지 잡아먹는다는 것을 목격했다네. 또한 우리는 그들이 이 세계를 파괴하려는 음모를 꾸미는 것을 들었지. 그리고 그들의 내부에 배신이 있었다는 것도 알아냈다네.","그들에게 무슨 약점이 있으며 그 약점을 우리에게 유리한 방향으로 이용하는 방법을 알고 있단 말일세.","그들이 의식에 사용하는 도구를 가지고 오게. 그 도구를 그들이 섬기는 신이 벌인 전쟁으로 인해 만들어진 물질과 합친다면 그들을 심판하는 강력한 무기가 될 것이야."}, {"안퀴라즈 사원에 있는 아리고스에게 제국의 퀴라지 무기와 엘레멘티움 덩어리 3개를 가져가면 엘레멘티움을 주입한 무기를 만들어 줄 것입니다."}},
[8790] = {"제국의 퀴라지 표장", {"아리고스는 고대 신과 그들의 부하들 간에 벌어진 충돌에서 발생한 물질이 그들 자신에게도 매우 치명적일 것이라는 이론을 세웠습니다. 제가 말하는 것은 바로 엘레멘티움입니다, 필멸의 존재여.","제국의 퀴라지 표장과 엘레멘티움을 찾아서 가져오십시오. 내 강력한 힘을 가진 무기를 만들어 주도록 하겠습니다."}, {"안퀴라즈 사원에 있는 꿈의 메리스라에게 제국의 퀴라지 표장과 엘레멘티움 덩어리 3개를 가져가면 엘레멘티움을 주입한 무기를 만들어 줄 것입니다."}},
[8791] = {"오시리안 처치", {"지독하게 위험했고 잔인했던 오시리안이 패하고 그의 영혼은 안식을 찾았습니다.","칼림도어의 모든 이들이 이 승리의 소식을 듣는다면 안도의 한숨을 쉬게 될 것입니다. 세나리온 요새의 지휘관 마랄리스에게 오시리안의 머리를 가지고 가십시오."}, {"실리더스의 세나리온 요새에 있는 지휘관 마랄리스에게 무적의 오시리안의 머리를 가져가야 합니다."}},
[8792] = {"he Horde Needs Your Help!", {"안녕하십니까. 제 부탁을 들어주시겠다니 고맙군요. 호드에서는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황입니다. 그러니까 당신의 도움이 꼭 필요하다는 말입니다! 우리가 대화를 나누고 있는 지금 이 순간에도 공식적으로 수집 임무를 맡은 이들이 다가올 전쟁에 필요한 물자를 모으고 있답니다. 그러나 <name>, 당신의 도움이 없으면 우리는 목표를 달성할 수 없습니다!","전쟁 물자 수집을 책임지고 있는 장군 고르척에게 가서 얘기를 나눠보십시오. 자, 어떻습니까? 호드의 생사가 걸린 전쟁 준비를 돕겠습니까?"}, {"오그리마, 정기의 골짜기에 있는 장군 고르척과 대화해야 합니다."}},
[8793] = {"he Horde Needs Your Help!", {"안녕하신가. 내 말에 귀를 기울여 주겠다니 고맙군. 호드는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황이네. 그러니까 자네가 꼭 필요하다는 거야! 우리가 대화를 나누고 있는 지금 이 순간에도 공식적으로 수집 임무를 맡은 이들이 다가올 전쟁에 필요한 물자를 모으고 있네. 그러나 <name>, 자네가 도와주지 않으면 우린 목표를 달성할 수 없어!","전쟁 물자 수집을 책임지고 있는 장군 고르척에게 가서 얘기를 나눠보게. 자, <class>|1이여;여;, 어떻게 하겠나? 설마 호드의 생사가 걸린 전쟁 준비를 모른 척하지는 않겠지?"}, {"오그리마, 정기의 골짜기에 있는 장군 고르척과 대화해야 합니다."}},
[8794] = {"he Horde Needs Your Help!", {"안녕하십니까. 제 부탁을 들어주시겠다니 고맙군요. 호드에서는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황입니다. 그러니까 당신의 도움이 꼭 필요하다는 말입니다! 우리가 대화를 나누고 있는 지금 이 순간에도 공식적으로 수집 임무를 맡은 이들이 다가올 전쟁에 필요한 물자를 모으고 있답니다. 그러나 <name>, 당신의 도움이 없으면 우리는 목표를 달성할 수 없습니다!","전쟁 물자 수집을 책임지고 있는 장군 고르척에게 가서 얘기를 나눠보십시오. 자, 어떻습니까? 호드의 생사가 걸린 전쟁 준비를 도와주시겠습니까?"}, {"오그리마, 정기의 골짜기에 있는 장군 고르척과 대화해야 합니다."}},
[8795] = {"he Alliance Needs Your Help!", {"안녕하십니까. 제 부탁을 들어주시겠다니 고맙습니다. 얼라이언스 연합에서는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황입니다. 그러니까 당신의 도움이 필요하다는 말입니다! 우리가 대화를 나누고 있는 지금 이 순간에도 정식으로 임무를 맡은 이들이 코앞에 닥친 전쟁에 필요한 물자를 모으고 있습니다. 그러나 당신의 도움이 없으면 우리의 목표를 달성할 수 없습니다!","물자 보급을 책임지고 있는 야전사령관 스노우폴과 얘기해 보십시오. 어떻게 하시겠습니까? 아제로스의 모든 생명체의 생사가 걸린 전쟁 준비를 도와주시겠습니까?"}, {"아이언포지의 군사 지구에 있는 야전사령관 스노우폴과 대화해야 합니다."}},
