[9245] = {"얼어붙은 미늘 건틀릿", {"자네와 오마리온에게 큰 신세를 졌군. 그도 편히 잠들었을 걸세. 난 이제 얼어붙은 미늘 건틀릿을 만들 수 있어. 재료와 함께 약간의 사례비만 지불하면!"}, {"동부 역병지대, 희망의 빛 예배당에 있는 기능공 빌헬름이 얼어붙은 룬 5개, 두꺼운 전갈 딱지 16개, 물의 정수 5개, 거친 경화 가죽 3개와 200골드를 내라고 했습니다."}},
[9246] = {"얼어붙은 미늘 흉갑", {"자네와 오마리온에게 큰 신세를 졌군. 그도 편히 잠들었을 걸세. 난 이제 얼어붙은 미늘 흉갑을 만들 수 있어. 재료와 함께 약간의 사례비만 지불하면!"}, {"동부 역병지대, 희망의 빛 예배당에 있는 기능공 빌헬름이 얼어붙은 룬 8개, 두꺼운 전갈 딱지 24개, 물의 정수 7개, 거친 경화 가죽 5개와 300골드를 내라고 했습니다. 또한, 은빛 여명회로부터 확고한 동맹의 평판을 얻은 상태여야 합니다."}},
[9247] = {"기록 보관원의 부름", {"당신은 한때 은빛 여명회를 위해 봉사했으며 언제나 최선을 다했습니다. 지금 이 세계는 또다시 우리를 간절히 부르고 있습니다.","다시 한 번 우리를 도와주실 생각이 있다면 동부 역병지대의 희망의 빛 예배당에 있는 기록 보관원에게 이 편지를 가져가시기 바랍니다."}, {"동부 역병지대의 희망의 빛 예배당에 있는 기록 보관원에게 기록 보관원의 편지를 가져가야 합니다."}},
[9248] = {"겸손한 제안", {"이시누 알라. 심연의 군주가 죽었다는 증거를 보여 주십시오. 그러면 보상으로 퀴라지와 전투를 치를 때 도움이 될 만한 물건을 드리겠습니다."}, {"실리더스의 세나리온 요새에 있는 오렐 골드리프가 심연의 홀 1개를 가져다 달라고 했습니다. 또한 세나리온 의회로부터 우호적 평판을 얻어야 합니다."}},
[9249] = {"상품권 40매 - 설계도: 통통 전차 조종기", nil, nil},
[9250] = {"아티쉬의 몸통", {"몸체가 구성됐으니 아티쉬는 거의 완성된 것입니다. 이제 지팡이의 윗부분과 아랫부분만 있으면 됩니다. 그리고 지팡이가 살게라스의 악마를 불러내는 통로 역할을 한다는 사소한 문제가 있습니다.","지팡이의 나머지 두 부분을 찾고 지팡이의 악마를 퇴치하도록 도와줄 조력자를 찾아야 합니다."}, {"지팡이의 나머지 두 부분을 되찾고 지팡이의 악마를 퇴치하도록 도와줄 자를 찾아야 합니다."}},
[9251] = {"아티쉬, 저주받은 지팡이", {"그대가 왜 아직도 살아 있는지 궁금할 것이다. 그대가 마침내 손에 넣게 된 것은 지독하게도 사악한 물건이다. 명확하게 대답할 수는 없다. 나는 내 할 일을 할 뿐...","켈투자드는 낙스라마스에 있는 자신의 왕좌에 앉아 음모를 계획하고 있다... 그는 그대가 지니고 있는 것을 찾고 있으며, 그대가 찾고 있는 것을 지니고 있는 장본인이다.","지팡이의 아랫부분은 브란 브론즈비어드라고 하는 드워프가 안퀴라즈로 들어갈 때 지니고 있었느니라. 그는 안퀴라즈를 떠났지만 아티쉬의 나머지 조각들은 그곳에 남아 있다.","아티쉬의 아랫부분을 찾으면 내게 돌아오너라."}, {"타나리스의 시간의 동굴에 있는 아나크로노스가 아티쉬의 지팡이 머리와 아티쉬의 밑동을 가져다 달라고 했습니다."}},
[9252] = {"산들바람 마을 방어", {"확실히 스컬지의 공격 횟수가 늘어났습니다. 가까스로 방어선을 유지하는 상황인데 실버문에서는 지원군이 도착할 기미도 안 보입니다. 마을을 지킬 수 있도록 도와주십시오. 죽음의 흉터 근처를 돌아다니는 언데드의 숫자를 줄이면 됩니다."}, {"썩은다리 약탈자와 어둠의 망령을 각각 4마리씩 해치운 후 영원노래 숲의 산들바람 마을에 있는 순찰자 사레인에게 가야 합니다."}},
[9253] = {"마법석 파수꾼 데리안", {"스컬지의 공격 횟수가 이렇게 늘어나는 것은 방어를 목적으로 유령의 땅 경계를 따라 설치된 마법석 중 하나가 제대로 작동하지 않기 때문일 것입니다","우리는 그 고대의 마법석을 이용해 언데드로부터 우리 땅을 방어했죠. 하지만, 태양샘이 공격당한 후 대부분이 파괴되거나 유실되었습니다.","남쪽으로 길을 따라 유령의 땅을 향해 가다보면 서쪽으로 길이 갈라질 것입니다. 그 주변에 있는 마법석 근처에서 마법석 파수꾼 데리안을 찾을 수 있습니다. 그라면 뭐가 문제인지 알고 있을 것입니다."}, {"영원노래 숲에서 유령의 땅 경계 근처에 있는 마법석 파수꾼 데리안과 대화해야 합니다."}},
[9254] = {"고집불통 수습생", {"도움을 주셔서 감사드리오. 나는 여기 질서를 바로잡을 테니 그대는 내가 부탁하는 임무를 하나 맡아주시오. 내 옛 제자 중 한 명이 아주 위험한 연구에 뛰어들었소. 그녀는 죽음의 흉터 가운데에 있는 동부 성소로 떠났소.","그녀가 몹시 걱정되는군. 그녀를 찾아서 제정신을 차리도록 설득해 주시오."}, {"영원노래 숲의 동부 성소에 있는 수습생 미르베다와 대화해야 합니다."}},
[9255] = {"연구 일지", {"제 연구 결과가 유감스러운 만큼 중요한 정보라는 사실은 인정해야 합니다. 제 연구를 실제로 활용하기 전에 반드시 마법학자께서 검토하셔야 합니다. 이곳에서 서쪽으로 산들바람 마을에 있는 마법학자 란드라 돈스트라이더에게 이 연구 일지를 전해 주세요."}, {"영원노래 숲의 산들바람 마을에 있는 마법학자 란드라 돈스트라이더에게 연구 일지를 전달해야 합니다."}},
[9256] = {"산들바람 마을", {"그대에게 다른 임무를 맡기겠습니다. 순찰자 데골리엔은 인근 해돋이 부두에서 발생한 버림받은 자들의 습격 사건에 대해서 좀 더 자세히 알아보라는 임무를 받고 산들바람 마을로 파견되었습니다. 그가 지원을 요청했지만 아직 상황이 여의치 않습니다. 그를 찾아서 최대한 지원해 주십시오.","매날개 광장 바깥에 있는 길을 따라 남쪽으로 가면 산들바람 마을에 도착할 겁니다. 행운을 빌겠습니다!"}, {"영원노래 숲의 산들바람 마을에 있는 순찰자 데골리엔과 대화해야 합니다."}},
[9257] = {"아티쉬, 수호자의 지팡이", {"이번 마지막 일이 가장 어려울 것이다. 그대는 고대 신과 리치 왕의 사령관과 대적하여 그들을 처치했다. 하지만 이번에 그대가 상대해야 하는 것은 살게라스의 하수인이다.","스트라솔름으로 이 지팡이를 가져가거라. 거기서 정화된 땅을 찾을 수 있을 것이다. 로데론의 가장 위대한 기사가 최후를 맞이한 곳이지. 더럽혀진 지팡이를 성스러운 땅 위에 꽂은 후 그 속에서 튀어나올 엄청난 힘을 가진 존재와 맞설 준비를 하거라... 내 지팡이를 조종하는 사악한 존재를 해치운 그대를 언제나처럼 이곳에서 기다리도록 하마."}, {"타나리스의 시간의 동굴에 있는 아나크로노스가 수호자의 지팡이인 아티쉬를 스트라솔름으로 가지고 간 다음, 정화된 땅에서 사용해 달라고 했습니다. 지팡이에서 쫓겨 나온 존재를 해치운 후 아나크로노스에게 가야 합니다."}},
[9258] = {"불타버린 숲", {"스컬지는 새로운 지역으로 세력을 확장하기에 앞서 토양을 오염시킵니다. 우리는 과거의 오염을 막기 위해 유령의 땅 경계를 따라 보호의 마법석을 설치했습니다.","그러나 스컬지의 공격으로 서쪽 끝에 설치된 마법석이 파괴되는 바람에 우리는 오염의 확산을 막기 위해 땅에 불을 질러야 했습니다.","제 여동생 라리아나 리버윈드가 불타버린 숲을 감시하고 있습니다. 동생을 찾아가십시오. 그대에게 뭔가 부탁드릴 것입니다."}, {"영원노래 숲 남서쪽의 불타버린 숲 근처에 있는 라리아나 리버윈드를 찾아야 합니다."}},
[9259] = {"붉은 해적단의 배신자", nil, nil},
[9260] = {"스톰윈드의 마법진 조사", {"리치 왕이 그의 언데드 군대를 바로 스톰윈드 지척에 보냈소. 절대로 그냥 둬서는 안 될 것이오! 이 저주받을 침략자들로부터 스톰윈드를 지키기 위해 당신이 기꺼이 나서 줄 것이라 믿고 있소.","스톰윈드 외곽으로 나가면 이상한 룬문자 마법진이 있는데 언데드와 죽음의 요새를 둘러싼 힘과 같은 미묘한 기운으로 빛나고 있소이다. 그 룬문자 마법진이 중요한 것임이 틀림없으니 마법진이 어떤 역할을 하는 건지 좀 알아봐 줘야겠소. 조사하는 김에 그 미친 언데드 녀석들도 좀 처리하고 돌아오도록 하시오. 조사 결과와 함께 녀석들을 처치했다는 증거를 가져오면 보상하도록 하겠소이다. 자, 어서 가시오!"}, {"스톰윈드 외곽에 있는 스컬지를 처치하여 희미하게 빛나는 죽음의 돌 3개를 모으고, 스컬지 요새 근처에서 빛나는 룬문자 마법진을 조사해야 합니다."}},
[9261] = {"아이언포지의 마법진 조사", {"리치 왕이 그의 언데드 군대를 바로 아이언포지 지척에 보냈습니다. 절대로 그냥 둬서는 안 될 것입니다! 이 저주받을 침략자들로부터 아이언포지를 지키기 위해 당신이 기꺼이 나서 줄 것이라 믿고 있습니다.","아이언포지 외곽으로 나가면 이상한 룬문자 마법진이 있는데 언데드와 죽음의 요새를 둘러싼 힘과 같은 미묘한 기운으로 빛나고 있습니다. 그 룬문자 마법진이 중요한 것임이 틀림없으니 당신이 마법진이 어떤 역할을 하는 건지 좀 알아봐 주시기 바랍니다. 조사하는 김에 그 미친 언데드 녀석들도 좀 처리하고 돌아오도록 하십시오. 조사 결과와 함께 녀석들을 처치했다는 증거를 가져오면 보상하도록 하겠습니다. 자, 어서 가십시오!"}, {"아이언포지 밖에 있는 스컬지를 처치하여 희미하게 빛나는 죽음의 돌 3개를 모으고, 스컬지 요새 근처에서 빛나는 룬문자 마법진을 조사해야 합니다."}},
[9262] = {"다르나서스의 마법진 조사", {"리치 왕이 그의 군대를 다르나서스 지척에 보냈네. 절대로 그냥 둬서는 안 될 것이야! 이 저주받을 침략자들로부터 다르나서스를 지키기 위해 자네가 기꺼이 나서 줄 것이라 믿고 있네.","다르나서스 외곽으로 나가면 이상한 룬문자 마법진이 있는데 언데드와 죽음의 요새를 둘러싼 힘과 같은 미묘한 기운으로 빛나고 있다네. 그 룬문자 마법진이 중요한 것임이 틀림없으니 자네가 마법진이 어떤 역할을 하는 건지 좀 알아봐 줘야겠군. 조사하는 김에 그 미친 언데드 녀석들도 좀 처리하고 돌아오도록 하게. 조사 결과와 함께 녀석들을 처치했다는 증거를 가져오면 보상하도록 하지. 자, 어서 가게나!"}, {"다르나서스 외곽에 있는 스컬지를 처치하여 희미하게 빛나는 죽음의 돌 3개를 모으고, 스컬지 야영지 근처에서 빛나는 룬문자 마법진을 조사해야 합니다."}},
[9263] = {"오그리마의 스컬지 조사", {"리치 왕이 그의 언데드 군대를 바로 우리 지척에 보냈습니다. 절대로 그냥 둬서는 안 됩니다! 이 저주받을 침략자로부터 오그리마를 지키기 위해 기꺼이 나서 줄 것이라 믿습니다, <name>!","바깥으로 나가면 이상한 마법진이 있을 겁니다. 그 마법진은 공중에 떠있는 죽음의 요새와 스컬지들을 둘러싼 것과 같은 빛으로 반짝이고 있지요. 굉장히 중요한 것이 틀림없습니다. 어떤 역할을 하는지 좀 알아봐 주십시오. 그리고 이왕 가는 김에 그 미친놈들도 좀 처리해 주시고요. 조사 결과와 함께 놈들을 죽였다는 증거를 가져오면 보상을 해드리겠습니다. 자, 출발하십시오!"}, {"오그리마 바깥의 스컬지 요새 근처에서 빛나는 마법진을 조사해야 합니다. 그리고 스컬지를 처치하여 희미하게 빛나는 죽음의 돌 3개를 모아야 합니다."}},
[9264] = {"썬더 블러프의 스컬지 조사", {"리치 왕이 그의 군대를 바로 우리 지척에 보냈어요. 절대로 그냥 둬서는 안 됩니다! 이 저주받을 침략자로부터 썬더 블러프를 지키기 위해 기꺼이 나서 줄 것이라 믿어요, <name>!","좀 더 바깥으로 나가면 이상한 마법진이 있을 겁니다. 공중에 있는 죽음의 요새와 스컬지를 둘러싼 힘과 같은 빛으로 반짝이고 있지요. 굉장히 중요한 것이 틀림없습니다. 어떤 역할을 하는지 좀 알아봐 주세요. 그리고 이왕 가는 김에 그 미친놈들도 좀 처리해 주시고요. 조사 결과와 함께 놈들을 죽였다는 증거를 가져오면 보상을 해드리겠습니다. 자, 출발하세요!"}, {"썬더 블러프 바깥의 스컬지 요새 근처에서 빛나는 마법진을 조사해야 합니다. 그리고 스컬지를 처치하여 희미하게 빛나는 죽음의 돌 3개를 모아야 합니다."}},
[9265] = {"언더시티의 스컬지 조사", {"리치 왕이 그의 스컬지 군대를 바로 우리 지척에 보냈네. 절대로 그냥 둬서는 안 돼! 이 저주받을 침략자로부터 언더시티를 지키기 위해 자네가 기꺼이 나서 줄 것이라 믿네, <name>!","바깥으로 나가면 이상한 마법진이 있을 거야. 그 마법진은 공중에 떠있는 죽음의 요새와 스컬지를 둘러싼 것과 같은 빛으로 반짝이고 있지. 굉장히 중요한 것이 틀림없어. 어떤 역할을 하는지 좀 알아봐 주게나. 그리고 이왕 가는 김에 그 미친놈들도 좀 처리해 주고. 조사 결과와 함께 놈들을 죽였다는 증거를 가져오면 보상을 주지. 자, 출발!"}, {"언더시티 바깥의 스컬지 요새 근처에서 빛나는 마법진을 조사해야 합니다. 그리고 스컬지를 처치하여 희미하게 빛나는 죽음의 돌 3개를 모아야 합니다."}},
