[7905] = {"A Feira de Negraluna", {"A Feira de Negraluna está chegando! Experimente os nossos jogos emocionantes, ganhe prêmios fabulosos e consulte o cartomante!","Corra e troque este vale por cupons premiados e um exemplar do mundialmente famoso Guia do Aventureiro de Negraluna, que vai ajudar você a identificar tesouros nas suas viagens!","Basta entregá-lo para Gelvas Portassuja, dentro da feira.","A feira está montada nos arredores da Vila d'Ouro, na Floresta de Elwynn."}, {"Entregue o Vale-ingresso para Gelvas Portassuja na Feira de Negraluna."}},
[7907] = {"Baralho de Feras de Negraluna", {"Agora que você juntou todas as cartas de Feras em um baralho, uma nona carta surge misteriosamente com o mesmo verso das oito primeiras. Nela, está escrito \"Devolva estas cartas à Feira de Negraluna para receber uma recompensa\". Você ouviu dizer que a entrada da feira aparece perto de Mulgore e da Vila d'Ouro todos os meses."}, {"Devolva o Baralho de Feras à Feira de Negraluna quando ela voltar à cidade."}},
[7908] = {"O mestre da Arena", {"Um mestre de arena deve precisar fazer mais do que isso... Talvez o pirata que coordena os eventos em Gurubashi saiba!"}, {"Fale com João Tampinha Mithril na Arena da Selva do Espinhaço."}},
[7926] = {"A Feira de Negraluna", {"A Feira de Negraluna está chegando! Experimente os nossos jogos emocionantes, ganhe prêmios fabulosos e consulte o cartomante!","Corra e troque este vale por cupons premiados e um exemplar do mundialmente famoso Guia do Aventureiro de Negraluna, que vai ajudar você a identificar tesouros nas suas viagens!","Basta entregá-lo para Gelvas Portassuja, dentro da feira.","A feira está montada nos arredores do Penhasco do Trovão, em Mulgore!"}, {"Entregue o Vale-ingresso para Gelvas Portassuja na Feira de Negraluna."}},
