[8856] = {"Kits de sobrevivência no deserto", {"Com a chegada dos novos recrutas ao Forte Cenariano, vem o problema de como mantê-los vivos no deserto. A desidratação e as diversas criaturas venenosas representam uma séria ameaça a voluntários inexperientes. Reúna os suprimentos necessários para um kit de sobrevivência no deserto e leve-os para Calandrath no Forte Cenariano."}, {"Leve 4 Globos de Água, 4 Antipeçonhas Poderosos e 4 Salmões Assados para Calandrath na estalagem do Forte Cenariano, em Silithus."}},
[8857] = {"Segredos dos colossos: Ashi", {"Eu estou atrás de aventureiros empedernidos e dispostos a levar uma amostra fresca da carcaça do colosso de Ashi para o Olhatorto, lá na Angra do Butim. Não, não, eu não quero que você chegue nem perto do cadáver daquele bicho. É só levar uma das minhas amostras, mas tem que ser rápido. Se apodrecer, não adianta!"}, {"Leve a Carapaça do Colosso de Ashi a Olhatorto Obnótico na Angra do Butim."}},
[8858] = {"Segredos dos colossos: Régia", {"Não tem nada que eu queira fazer mais do que esfregar essa carcaça de inseto bem na cara do Mão-de-graxa... O problema é que tô aqui coletando amostras de barata com complexo de superioridade. Com o tamanho daquela coisa, vou ficar aqui até o dia amanhecer.","Leva essa amostra para o Mão-de-graxa, e rápido, <class>. Ele paga MUITO bem!","O Posto de Tório fica lá na Garganta Abrasadora."}, {"Leve a Carapaça do Colosso de Régia ao Feitor Mão-de-graxa na Garganta Abrasadora."}},
[8859] = {"Segredos dos colossos: Zora", {"Meus servos quase foram mortos na tentativa de recuperar esta amostra. Você precisa se apressar! O Lorde Maximiliano Tyrosus aguarda ansioso por uma amostra fresca de carapaça. Ele crê que os segredos desse Colosso podem nos ajudar na guerra contra o Flagelo!","Por que você ainda está aqui!? Já para Esperança da Luz! Sim, você receberá recompensas incríveis pelos seus esforços."}, {"Leve a Carapaça do Colosso de Zora ao Lorde Maximiliano Tyrosus na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais."}},
[8860] = {"Festa de fim de ano!", {"Ventobravo está dando uma festa de fim de ano! Você vai poder beber de graça o dia inteiro, e à noite vai rolar um show de fogos de artifício! Vai ser uma beleza... O lance é que também vai ter festa lá na Angra do Butim, e eu aposto que vai ser muito melhor!","Mas vamos ao que interessa: a estalajadeira Cristine, de Ventobravo, mandou um pedido de delícias da Fazenda Bosque Defumado para a festa. Você poderia me fazer o favor de entregar a encomenda?","Obrigado, <name>! A estalajadeira Cristine está lá na Rosa Dourada, no Distrito Comercial de Ventobravo."}, {"Entregue o Suprimento do Bosque Defumado para a Estalajadeira Cristine em Ventobravo."}},
[8861] = {"Festa de fim de ano!", {"O Penhasco do Trovão está dando uma festa de fim de ano! Você vai poder beber de graça o dia inteiro, e à noite vai rolar um show de fogos de artifício! Vai ser uma beleza... O lance é que também vai ter festa lá na Angra do Butim, e eu aposto que vai ser muito melhor!","Mas vamos ao que interessa: a estalajadeira Pala, lá no Penhasco do Trovão, mandou um pedido de delícias da Fazenda Bosque Defumado para a festa. Você poderia me fazer o favor de entregar a encomenda?","Obrigado, <name>! A estalajadeira Pala está lá na estalagem perto da ponte que leva ao Platô dos Caçadores."}, {"Entregue o Suprimento do Bosque Defumado para a Estalajadeira Pala no Penhasco do Trovão."}},
